(window.webpackJsonp=window.webpackJsonp||[]).push([[1651],{2005:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-408-hoc-sinh-than-truyen"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-408-hoc-sinh-than-truyen"}},[n._v("#")]),n._v(" Chương 408: Học sinh thân truyền")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Trước mắt cái này một đống, không phải là vừa mới ở trên trời phách lối không dứt đám gia hỏa ư?"),t("br"),t("br"),n._v("Trước đó cái kia thống lĩnh bộ dáng người, đứng tại man thú trên đầu, bàn tay lớn mở ra, lực phá Càn Khôn, khí áp hoàn vũ, khí phách không thể lại khí phách, thế nào nửa người đều cắm vào mặt đất... Cùng cái cây giống giống như?"),t("br"),t("br"),n._v("Nhất là kẻ cầm đầu thái tử phi, tựa như đầu heo, nếu không phải quần áo lộng lẫy, ném tới ăn mày đám bên trong, có lẽ đều nhận không ra."),t("br"),t("br"),n._v("Không chỉ ba người, ngay cả đầu kia hung mãnh man thú, thoạt nhìn cũng rơi thất điên bát đảo, tìm không thấy phương hướng."),t("br"),t("br"),n._v("Cái này, cái này..."),t("br"),t("br"),n._v("Mạc Hoằng Nhất con mắt như là chiêng đồng."),t("br"),t("br"),n._v("Đến cùng xảy ra chuyện gì? Thời gian nháy mắt, liền để trước đó còn phách lối không ai bì nổi đám gia hỏa, biến thành như vậy?"),t("br"),t("br"),n._v("Nghi hoặc trong, lần nữa nghe được hai cái rơi xuống đất thanh âm."),t("br"),t("br"),n._v("Vội vàng nhìn lại, chỉ thấy trước đó nằm tại man thú trên lưng “Bệnh nhân” bị quăng đi ra, trên không trung nhiều bay nửa ngày, lúc này mới rơi xuống đất."),t("br"),t("br"),n._v("Chính là Lâm Nhược Thiên, Lâm Lang."),t("br"),t("br"),n._v("Bọn hắn vốn là trọng thương, lại bay vài trăm mét theo cao mấy chục mét địa phương ngã xuống, toàn thân xương cốt toàn bộ vỡ vụn, hô hấp đoạn tuyệt, trực tiếp đi Địa Phủ báo cáo."),t("br"),t("br"),n._v("“Cha, đệ đệ...”"),t("br"),t("br"),n._v("Thấy cảnh này, trọng thương Lâm Lung vừa vội vừa tức, trực tiếp hôn mê bất tỉnh."),t("br"),t("br"),n._v("Nàng chỉ là ngất đi, Lâm Nhược Thiên thế nhưng là chết không nhắm mắt."),t("br"),t("br"),n._v("Vốn cho rằng nữ nhi tới, nhất định có thể đại triển hùng phong, đem cái kia Trương Huyền gì gì đó toàn bộ chém giết, báo thù rửa hận, mơ cỡ nào đều không có nghĩ đến, mới đi đến người ta trên tòa phủ đệ không, man thú liền rơi xuống, bị ngã đến sắp chết rồi."),t("br"),t("br"),n._v("Ngay sau đó, còn chờ mong có thể chuyển bại thành thắng, kết quả là làm không trung phi nhân... Sau đó, liền không có sau đó."),t("br"),t("br"),n._v("Đến báo thù, cừu nhân đều không có nhìn thấy liền treo..."),t("br"),t("br"),n._v("Trên thế giới còn có ai so với hắn càng khổ cực?"),t("br"),t("br"),n._v("Sớm biết như thế, còn không bằng đừng mấy người nữ nhi, chết sớm một chút, cũng không trở thành thụ nhiều cái này vài ngày tội..."),t("br"),t("br"),n._v("“Lâm gia, lần này xem như đụng phải tấm sắt!”"),t("br"),t("br"),n._v("“Đúng vậy a, Trương sư không chỉ thiên phú cao, còn có cái tốt sư phụ, cùng bọn hắn đấu, không phải muốn chết sao?”"),t("br"),t("br"),n._v("“Không hỏi thăm một chút liền xông lại, đáng đời xui xẻo...”"),t("br"),t("br"),n._v("Nhìn thấy mấy người thảm trạng, chẳng những không có đồng tình, ngược lại đại khoái nhân tâm."),t("br"),t("br"),n._v("Lộ Xung chuyện, cơ hồ tất cả mọi người biết, Lâm gia vốn là danh tiếng không tốt, hiện tại tường đổ mọi người đẩy, thành liệu nguyên xu thế."),t("br"),t("br"),n._v("“Đây là Trương sư vị lão sư kia ra tay?”"),t("br"),t("br"),n._v("“Không phải, vừa rồi có người trong tầm mắt trời lâu nhìn, là một vị danh sư, kêu cái gì... Tiểu Tô, đến cùng là ai, ta cũng không rõ ràng!”"),t("br"),t("br"),n._v("“Bất kể là ai, Chí Tôn cảnh cường giả tiện tay ném ra, phần này thực lực, đáng sợ...”"),t("br"),t("br"),n._v("“Đúng vậy a, về sau cái này Trương phủ liền là cấm địa, hậu bối ai dám xông vào, lập tức trục xuất gia tộc!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghị luận ầm ĩ."),t("br"),t("br"),n._v("“Tiểu Tô? Chẳng lẽ là... Tô sư ra tay?”"),t("br"),t("br"),n._v("Hơi đỏ mặt, Mạc Hoằng Nhất nắm đấm xiết chặt."),t("br"),t("br"),n._v("Tứ tinh danh sư quả nhiên lợi hại, xem ra quyết định của hắn là đúng, bái hai vị này vi sư, về sau nhất định có thể đột nhiên tăng mạnh."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Ngay tại kích động, chờ lấy Tô sư hai người đi ra, tiến đến bái sư, liền nghe đến cách đó không xa đại môn mở ra, hai bóng người đi ra."),t("br"),t("br"),n._v("“Tôn quản gia, vậy chúng ta liền cáo từ, nhìn thấy Trương sư, cũng làm phiền nói cho một tiếng, chúng ta tại vạn quốc liên minh chờ hắn!”"),t("br"),t("br"),n._v("Tô sư ôm quyền nói."),t("br"),t("br"),n._v("“Yên tâm đi, việc nhỏ mà thôi!” Tôn Cường vung tay lên."),t("br"),t("br"),n._v("“Cái này...” Mạc Hoằng Nhất nhoáng một cái."),t("br"),t("br"),n._v("Đây chính là Tô sư, Lăng sư, vạn quốc liên minh tứ tinh danh sư, Dương sư không có tiễn ngược lại cũng thôi, Trương sư thế nào cũng không có đi ra?"),t("br"),t("br"),n._v("Chỉ làm cho một quản gia đưa ra đến, mấu chốt nhất là... Cái này quản gia, cũng quá tùy tiện đi!"),t("br"),t("br"),n._v("Ngươi xem bộ dáng này, rõ ràng mang theo qua loa... Cùng không kiên nhẫn!"),t("br"),t("br"),n._v("Không kiên nhẫn cái quỷ a, nếu như nói trước đó không biết Tô sư hai người thân phận, ngược lại cũng thôi, vừa mới ra tay, long trời lở đất, thế mà còn này tấm thái độ..."),t("br"),t("br"),n._v("Là muốn điên vẫn là có bệnh?"),t("br"),t("br"),n._v("“Đều không phải là... Chỉ sợ là vị này Dương sư thực lực, sâu không lường được, lúc này mới một người đắc đạo gà chó cũng thăng thiên, quản gia cũng như thế diễu võ giương oai...”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất không ngốc, rất nhanh hiểu được, nhịn không được choáng váng."),t("br"),t("br"),n._v("Để tứ tinh danh sư đều kiêng kị... Vậy vị này Dương sư, đến cùng có bao nhiêu lợi hại?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Danh Sư đường."),t("br"),t("br"),n._v("“Ngươi nói... Lão sư xưng hô Tô sư là... Tiểu Tô?”"),t("br"),t("br"),n._v("Tràn đầy khiếp sợ nhìn trước mắt tới hồi báo Chúc trưởng lão, Khương đường chủ không thể tin được."),t("br"),t("br"),n._v("“Vâng! Không chỉ có như thế, Tô sư còn giống như rất nghe Dương sư...”"),t("br"),t("br"),n._v("Chúc trưởng lão nói."),t("br"),t("br"),n._v("“Tô sư là vạn quốc liên minh đường đường tứ tinh danh sư, đối lão sư lễ độ cung kính, thậm chí xưng hô Tiểu Tô cũng không dám phản kháng... Chẳng lẽ, lão sư thực lực, đã vượt qua tứ tinh?”"),t("br"),t("br"),n._v("Sớm biết lão sư thực lực sâu không lường được, nhưng Khương đường chủ nằm mơ đều không có nghĩ đến, lợi hại như vậy."),t("br"),t("br"),n._v("Tứ tinh danh sư lễ độ cung kính, tự xưng vãn bối, nên mạnh bao nhiêu?"),t("br"),t("br"),n._v("“Cũng có lẽ chỉ có lợi hại như vậy danh sư, mới có thể dạy ra Trương Huyền dạng này thiên tài!”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, nhịn không được cảm khái."),t("br"),t("br"),n._v("Trương sư thiên phú, rõ như ban ngày, đổi lại bản thân, khẳng định là không dạy được, cũng chỉ có lão sư, mới có loại năng lực này."),t("br"),t("br"),n._v("“Đúng rồi, đường chủ, Tô sư đám người rời đi Trương sư phủ đệ, liền cưỡi phi hành man thú, quay về vạn quốc liên minh!”"),t("br"),t("br"),n._v("Chúc trưởng lão tiếp tục nói."),t("br"),t("br"),n._v("“Ừm, Tô sư, Lăng sư là vạn quốc liên minh Danh Sư đường trưởng lão, khẳng định còn có rất nhiều chuyện muốn làm, không có khả năng trong này mỏi mòn chờ đợi!”"),t("br"),t("br"),n._v("Khương đường chủ gật gật đầu, chần chờ một chút: “Chuẩn bị một chút, chúng ta đi gặp Trương sư cùng lão sư, đoán chừng, bọn hắn tại Thiên Vũ vương thành cũng đợi không được bao lâu!”"),t("br"),t("br"),n._v("Tô sư, Lăng sư mục đích hắn cũng biết một chút, hiện tại hai người quay người liền đi, không có chút nào dừng lại, nói rõ sự tình đã không sai biệt lắm làm thỏa đáng."),t("br"),t("br"),n._v("Sau ba tháng liền có danh sư thi đấu, chắc hẳn Trương sư đám người chẳng mấy chốc sẽ rời đi."),t("br"),t("br"),n._v("“Đúng vậy a!”"),t("br"),t("br"),n._v("Chúc trưởng lão gật đầu."),t("br"),t("br"),n._v("Trương sư đại tài, Thiên Vũ vương thành loại địa phương nhỏ này, hiển nhiên đã cầm tù không ở, muốn đi huy hoàng hơn địa phương, giương cánh bay lượn."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Khương đường chủ cùng Chúc trưởng lão đều nhìn ra, thời khắc này Trương Huyền, quả thực đang suy nghĩ rời đi vấn đề."),t("br"),n._v(" Hắn tu vi hiện tại đạt đến Tông sư đỉnh phong, không có càng nhiều cấp Chí Tôn đừng công pháp, muốn đột phá, gần như không có khả năng."),t("br"),t("br"),n._v("Mà Thiên Vũ vương quốc loại cấp bậc này địa phương, loại này công pháp là căn bản tìm không thấy."),t("br"),t("br"),n._v("Chỉ có thể đi cấp bậc cao hơn quốc gia."),t("br"),t("br"),n._v("Khôi phục vốn là dung mạo, trở lại phòng tiếp khách."),t("br"),t("br"),n._v("Lúc này, Trịnh Dương đám người còn quỳ gối tại chỗ, giữ vững được hai ngày hai đêm, giọt nước không vào, mặc dù thân thể run rẩy, có chút không kiên trì nổi, ánh mắt nhưng càng ngày càng kiên định, lại không trước đó lỗ mãng."),t("br"),t("br"),n._v("Hai ngày hai đêm rèn luyện, cùng dự đoán đồng dạng, ma luyện tinh thần, rèn luyện ý chí."),t("br"),t("br"),n._v("Biết trừng phạt mục đích của bọn hắn đạt đến, Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("“Đều đứng lên đi!”"),t("br"),t("br"),n._v("Vung tay lên, ngồi tại gian phòng trên chỗ ngồi."),t("br"),t("br"),n._v("Lộ Xung, Triệu Nhã mấy người cũng đi tới trước mặt, lúc này, sáu cái học sinh tề tụ."),t("br"),t("br"),n._v("“Ta muốn rời khỏi Thiên Vũ vương quốc đi vạn quốc liên minh, không nguyện ý đi, sẽ không cưỡng cầu!”"),t("br"),t("br"),n._v("Nhìn về phía mọi người, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Lại đến cảm giác lựa chọn thời điểm."),t("br"),t("br"),n._v("Học sinh không phải nô lệ, nguyện ý đi theo, liền mang theo, không nguyện ý, đại biểu duyên phận lấy hết, không cần thiết cưỡng cầu."),t("br"),t("br"),n._v("“Ta nguyện ý đi theo lão sư!” Triệu Nhã tiến về phía trước một bước."),t("br"),t("br"),n._v("“Ta cũng nguyện ý!” Trịnh Dương mấy người cũng vội vàng mở lời."),t("br"),t("br"),n._v("Không có lão sư, liền không có bọn hắn thực lực bây giờ, đương nhiên muốn cùng sau lưng lão sư, truy đuổi bước chân, càng chạy càng xa."),t("br"),t("br"),n._v("“Lộ Xung, ngươi đây?”"),t("br"),t("br"),n._v("Thiên Huyền theo tới năm cái học sinh rất nhanh đều biểu lộ thái độ, nguyện ý theo ở phía sau, không rời không bỏ, chỉ có Lộ Xung đứng tại một bên, một câu không nói."),t("br"),t("br"),n._v("“Ta trước đó cũng đã nói, cái mạng này đều là lão sư, tự nhiên là lão sư đi đến đâu, theo đến đó! Bất quá... Trước đó, còn có chút chuyện muốn xử lý!” Chần chờ một chút, Lộ Xung nói."),t("br"),t("br"),n._v("“Ừm?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Vạn quốc liên minh cách nơi này không dưới mấy chục vạn cây số, đường xá đằng đẵng, một khi đi, chẳng biết lúc nào mới có thể trở về, ta nghĩ... Tại trước khi đi, tế bái thoáng cái cha mẹ người thân!”"),t("br"),t("br"),n._v("Lộ Xung nói."),t("br"),t("br"),n._v("“Tế bái thân nhân, hẳn là!” Trương Huyền đáp ứng."),t("br"),t("br"),n._v("Lần này đi không biết bao lâu, trước khi đi, tế bái chết đi cha mẹ người thân, nhân chi thường tình."),t("br"),t("br"),n._v("“Nếu có cơ hội... Ta còn muốn để lão sư ra tay giúp đỡ, đem tổ tiên vật lưu lại mang tới, thực lực của ta còn... Hơi có chưa đủ!” Lộ Xung nói tiếp."),t("br"),t("br"),n._v("Trương Huyền đáp ứng."),t("br"),t("br"),n._v("Hắn nói tổ tiên đồ vật, hẳn là có Khổng sư tự tay viết bản chép tay."),t("br"),t("br"),n._v("Thứ này đối với hắn mà nói không có gì dùng, nhưng Lộ Xung bởi vậy gia tộc tan vỡ, lại là Tiên Tổ lưu lại, trước khi đi, nắm bắt tới tay mới rời khỏi không gì đáng trách."),t("br"),t("br"),n._v("“Đa tạ lão sư!”"),t("br"),t("br"),n._v("Lộ Xung trong mắt tràn ngập cảm kích."),t("br"),t("br"),n._v("“Đã các ngươi đều muốn cùng ta, vậy ta cũng nói nói chuyện quy củ!”"),t("br"),t("br"),n._v("Thấy mọi người đều muốn cùng, Trương Huyền nhìn quanh một tuần, thần thái nghiêm túc."),t("br"),t("br"),n._v("“Thầy trò coi trọng duyên phận, học viện phổ thông thầy trò, không muốn lên khóa, có thể rời đi, đổi lão sư, cũng không quan trọng. Nhưng... Các ngươi muốn đi theo ta, liền không đồng dạng.”"),t("br"),t("br"),n._v("Phổ thông thầy trò quan hệ, mặc dù kính trọng, nhưng cũng không có quá lớn trói buộc."),t("br"),t("br"),n._v("Thật giống như trước đó Lộ Xung đám người Bạch lão sư, dạy bọn họ mặc dù thời gian tuy dài, nhưng đổi đi cũng sẽ không có quá lớn cảm giác tội lỗi."),t("br"),t("br"),n._v("Lúc trước tại Hồng Thiên học viện cũng giống vậy, học sinh cho rằng dạy không tốt, lui khóa, đổi lão sư, chuyện thường xảy ra."),t("br"),t("br"),n._v("“Lão sư... Chẳng lẽ muốn thu chúng ta là thân truyền?”"),t("br"),t("br"),n._v("Kịp phản ứng, Trịnh Dương kích động hơi đỏ mặt."),t("br"),t("br"),n._v("Triệu Nhã mấy người cũng mỗi người xiết chặt nắm đấm."),t("br"),t("br"),n._v("Đồng dạng học sinh, thân truyền, giảng bài, địa vị chênh lệch rất lớn, không thể so sánh nổi."),t("br"),t("br"),n._v("Giảng bài học sinh, có thể rời đi, đổi đi lão sư; Thân truyền, nếu như vậy làm, chẳng khác nào phản bội sư môn, bị tru sát cũng không ai dám nói cái gì!"),t("br"),t("br"),n._v("Trở thành thân truyền, đạt được cao thâm nhất pháp quyết tu luyện, cặn kẽ nhất chỉ điểm, gặp phải giảng bài, tự động tấn cấp “Sư huynh”."),t("br"),t("br"),n._v("Đồng dạng, cũng muốn hiệu trung sư môn, lại không hai lòng."),t("br"),t("br"),n._v("Mặt khác, trở thành thân truyền, cũng là lão sư đối học sinh tán thành, không đồng ý, không có khả năng thu lấy."),t("br"),t("br"),n._v("Triệu Nhã đám người trước đó một mực lấy học sinh thân truyền tự cho mình là, nhưng Trương sư chưa hề mở lời, trong lòng vẫn là ít nhiều có chút thất lạc, giờ phút này nghe lời này, tất cả đều hưng phấn khó mà tự chế."),t("br"),t("br"),n._v("Trở thành thân truyền, tương đương với có thân phận thật sự."),t("br"),t("br"),n._v("Lại vì lão sư ra mặt, cũng lẽ thẳng khí hùng."),t("br"),t("br"),n._v("“Không sai, muốn trở thành ta thân truyền, muốn tuân thủ mấy cái quy định: Tôn sư trọng đạo, nghe theo lão sư phân phó, không thể ngỗ nghịch.”"),t("br"),t("br"),n._v("“Sư môn truyền thụ, chưa cho phép không thể truyền ra ngoài.”"),t("br"),t("br"),n._v("“Khoan dung làm người, không thể lấy mạnh hiếp yếu.”"),t("br"),t("br"),n._v("“Lấy sự tin cậy làm gốc, không thể lừa trên gạt dưới.”"),t("br"),t("br"),n._v("“Đoàn kết đồng môn...”"),t("br"),t("br"),n._v("“Nếu như có thể tuân thủ những này điều mục, có thể làm ta thân truyền, làm không được hoặc là về sau vi phạm, ta Trương Huyền sẽ đích thân ra tay đem hắn chém giết, tuyệt không nhân nhượng!”"),t("br"),t("br"),n._v("Đứng dậy, hai tay chắp sau lưng, Trương Huyền mỗi nói một cái, vẻ mặt nghiêm khắc một điểm, nói xong lời cuối cùng, cả phòng không khí tựa hồ cũng ngưng đọng."),t("br"),t("br"),n._v("Đi qua những ngày này khảo hạch, những học sinh này, mặc dù có đôi khi thoạt nhìn rất đục nợ, trên thực tế nội tâm một mực đối với hắn mười phần tôn trọng, không dám mảy may vi phạm, phù hợp học sinh thân truyền tiêu chuẩn."),t("br"),t("br"),n._v("Nhưng... Đã thu làm thân truyền, quy củ tự nhiên muốn lập."),t("br"),t("br"),n._v("“Chúng ta nguyện ý vào Trương sư môn hạ, trở thành thân truyền, phụng lão sư chi mệnh, trung thành không hai, đến chết không hối hận!”"),t("br"),t("br"),n._v("Triệu Nhã đám người không chút do dự, đồng thời quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu: “Từ hôm nay trở đi, Triệu Nhã vì đại sư tỷ, Vương Dĩnh là Nhị sư tỷ, sau đó theo thứ tự là Lưu Dương, Trịnh Dương, Viên Đào, Lộ Xung, đồng môn sáu người, tương thân tương ái, không thể xuất hiện nội chiến!”"),t("br"),t("br"),n._v("Vương Dĩnh mặc dù là cái thứ nhất nhập môn, nhưng tính cách quá yếu, trong mọi người, cũng chỉ có Triệu Nhã biết đại thể, tu vi cũng cao nhất, thích hợp làm đại sư tỷ, để Trịnh Dương đám người dẫn đầu, còn không biết muốn xông ra bao nhiêu tai họa."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("Kết thúc buổi lễ, bối phận xác nhận."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Thiên Vũ lịch 1,736 năm."),t("br"),t("br"),n._v("Mùa thu."),t("br"),t("br"),n._v("Triệu Nhã, Trịnh Dương, Lưu Dương, Vương Dĩnh, Viên Đào, Lộ Xung bái sư, là Trương sư thân truyền."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-416"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-418"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);