(window.webpackJsonp=window.webpackJsonp||[]).push([[1652],{2006:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-409-thai-tu-quyet-đinh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-409-thai-tu-quyet-đinh"}},[n._v("#")]),n._v(" Chương 409: Thái tử quyết định")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Thái tử điện hạ, nhất định phải thay ta làm chủ, giết gia hoả kia, báo thù cho ta rửa hận!”"),t("br"),t("br"),n._v("Phòng kín mít bên trong, một cái hoàn toàn thay đổi, toàn thân sưng tấy nữ nhân, quỳ trên mặt đất, lên tiếng gào thét."),t("br"),t("br"),n._v("Chính là bị cuồng đánh một trận về sau, lại bị rơi nửa chết nửa sống thái tử phi Lâm Lung."),t("br"),t("br"),n._v("Thời khắc này nàng, phục dụng thuốc chữa thương, khôi phục một chút, nhưng vẫn như cũ đầy người sưng vù, bẩn xấu chịu không nổi."),t("br"),t("br"),n._v("Nàng phía trước, ngồi một thanh niên, ba mươi bốn, năm tuổi bộ dáng, trên mặt góc cạnh rõ ràng, cương nghị như đao, theo thỉnh thoảng lộ ra uy nghiêm đó có thể thấy được, thân cư cao vị."),t("br"),t("br"),n._v("Chính là Hiên Viên vương quốc thái tử điện hạ, Đinh Mục!"),t("br"),t("br"),n._v("“Báo thù?”"),t("br"),t("br"),n._v("Ba!"),t("br"),t("br"),n._v("Một cước đạp tới, Đinh Mục tức giận sắc mặt đỏ lên, thân thể run rẩy: “Ngươi cũng đã biết đến cùng đắc tội người nào? Còn dám đề cập với ta báo thù?”"),t("br"),t("br"),n._v("Lâm Lung rời đi thái tử hành cung thời điểm, hắn vừa vặn không tại, trở về nghe được tin tức liền đuổi đi theo, muốn nhìn một chút đến cùng chuyện gì xảy ra, không nghĩ tới, vẫn là tới chậm!"),t("br"),t("br"),n._v("Biết mình nữ nhân bị đánh nửa chết nửa sống, cũng không lỗ mãng tìm vị kia Trương sư phiền phức, mà là phái người hỏi thăm một chút, không nghe ngóng còn tốt, sau khi nghe ngóng, kém chút không có tại chỗ hù chết."),t("br"),t("br"),n._v("Liên phá vô số ghi chép thiên tài danh sư, còn có một cái sâu không lường được lão sư..."),t("br"),t("br"),n._v("Các ngươi Lâm gia đắc tội người nào không được? Nhất định phải đắc tội lợi hại như vậy!"),t("br"),t("br"),n._v("Báo thù? Thế nào báo?"),t("br"),t("br"),n._v("Tô sư, Lăng sư đến Danh Sư đường chuyện, người biết không nhiều, cũng đều là chính thức danh sư, nghe ngóng không ra, nhưng khi đó Lương Thanh Mệnh không phải là đối thủ sự thật còn tại đó."),t("br"),t("br"),n._v("Một tiếng hét vang để Chí Tôn hậu kỳ cường giả cùng Thiểm Điện Kim Lân Thú mất đi năng lực phản kháng, đem mọi người cầm bốc lên ném đi, thực lực mạnh, thủ đoạn chi hung ác, coi như đem Hiên Viên vương quốc toàn bộ cộng lại, cũng không chống đỡ được!"),t("br"),t("br"),n._v("Thật muốn báo thù cho ngươi, không phải là đem toàn bộ vương quốc đều chôn vùi đi vào?"),t("br"),t("br"),n._v("Nói đùa cái gì!"),t("br"),t("br"),n._v("“Ta đương nhiên biết hắn là ai, Danh Sư đường nhị tinh danh sư, phía sau còn có một vị hư hư thực thực tứ tinh danh sư sư phụ!” Bị đạp lăn trên mặt đất, Lâm Lung cũng không sốt ruột, mà là cười lạnh nhìn qua."),t("br"),t("br"),n._v("Thái tử đều biết, nàng khôi phục lại, đương nhiên cũng nghe ngóng."),t("br"),t("br"),n._v("“Biết vẫn phí lời? Tứ tinh danh sư, kém cỏi nhất đều muốn đạt tới Hóa Phàm cảnh, loại này cường giả, đừng nói Hiên Viên vương quốc, coi như tại vạn quốc liên minh, đều là đại nhân vật, không phải ngươi ta có thể đắc tội!”"),t("br"),t("br"),n._v("Bàn tay lớn vẫy một cái, Đinh Mục làm ra quyết định: “Được rồi, chuyện này như vậy coi như không có gì, về sau đừng vội nhắc lại!”"),t("br"),t("br"),n._v("“Đừng sớm như vậy làm quyết định!”"),t("br"),t("br"),n._v("Lâm Lung đứng dậy: “Điện hạ có biết ta Lâm gia vì cái gì cùng vị này Trương Huyền chọc thù hận?”"),t("br"),t("br"),n._v("“Không phải ngươi giết hắn học sinh cả nhà, mới dẫn tới phiền phức?” Đinh Mục nhướng mày."),t("br"),t("br"),n._v("“Là ta phái người giết cái kia Cù Xung cả nhà, nhưng điện hạ có biết vì cái gì làm như vậy?” Lâm Lung nói."),t("br"),t("br"),n._v("Đinh Mục nhướng mày."),t("br"),t("br"),n._v("Chuyện này, thật sự là hắn không biết."),t("br"),t("br"),n._v("Ngoại giới truyền ngôn là bởi vì Cù Xung tỷ tỷ, cùng Lâm Lang náo ra mâu thuẫn, lấy hắn xem, có lẽ không có đơn giản như vậy."),t("br"),t("br"),n._v("Lâm Lung mặc dù bao che khuyết điểm, cũng không phải cái không biết đại cục người, nếu không, làm Hiên Viên vương quốc thái tử, cũng không có khả năng để ý."),t("br"),t("br"),n._v("Bình thường tiếp xúc, không tính lỗ mãng, thế nào lần này liền không sợ phiền phức tình khó mà xử lý, trực tiếp động thủ?"),t("br"),t("br"),n._v("Chẳng lẽ có cái gì ẩn tình?"),t("br"),t("br"),n._v("Nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Điện hạ một lòng muốn bái nhập Lưu Vân tông, ta một mực ghi ở trong lòng... Kỳ thật, thật muốn thành công, vị này Cù Xung, liền là mấu chốt trong mấu chốt!” Lâm Lung khẽ nói."),t("br"),t("br"),n._v("“Ồ?” Đinh Mục sững sờ."),t("br"),t("br"),n._v("Hiên Viên vương quốc tuy thuộc tại vạn quốc liên minh, nhưng muốn nói xung quanh mạnh nhất thế lực, không ai qua được cái này Lưu Vân tông!"),t("br"),t("br"),n._v("Duy trì liên tục bảy mươi bảy lần danh sư thi đấu, ba mươi sáu lần quán quân đều bị ôm vào lòng, số lượng vượt qua một nửa, đủ thấy mạnh mẽ."),t("br"),t("br"),n._v("Vạn quốc liên minh thành tích tốt nhất, cũng mới hai mươi ba tên, đếm ngược thứ năm mà thôi."),t("br"),t("br"),n._v("Đinh Mục là Hiên Viên vương quốc thái tử, nhất định kế thừa quốc vương chi vị, nhưng hắn dã tâm không giới hạn trong đây, mà là rộng lớn hơn thiên địa, đã sớm muốn bái nhập Lưu Vân tông môn hạ, chỉ là một mực không có cơ hội!"),t("br"),t("br"),n._v("Một khi thành công, đạt được bồi dưỡng, nhất định có thể nhất phi trùng thiên, thành tựu Hóa Phàm, thậm chí đạt tới cấp bậc cao hơn, cũng không phải ước muốn."),t("br"),t("br"),n._v("Chỉ bất quá Lưu Vân tông điểm xuất phát quá cao, muốn đi vào, nào có dễ dàng như vậy, lại thêm hắn thuộc về liền nhau thế lực vương quốc thái tử, không có người to lớn đề cử, gần như là không thể nào."),t("br"),t("br"),n._v("“Cái này Cù gia, thoạt nhìn là cái không ra hồn rách nát gia tộc, nhưng mấy vạn năm trước, nhưng phi thường lợi hại! Nhất là bọn hắn Tiên Tổ, từng tại trong lúc vô tình từng cứu Mẫn Thánh một cái mạng!”"),t("br"),t("br"),n._v("Lâm Lung nói."),t("br"),t("br"),n._v("“Mẫn Thánh? Ngươi nói thế nhưng là... Khổng môn bảy mươi hai Thánh một trong... Mẫn Thánh Mẫn sư?” Con ngươi co rụt lại, Đinh Mục hô hấp dồn dập."),t("br"),t("br"),n._v("Khổng sư nổi danh đệ tử ba ngàn, lợi hại nhất, mệnh danh bảy mươi hai Thánh, Mẫn Thánh chính là một trong số đó, tham dự qua Danh Sư đường sáng tạo, thuộc về người sáng lập một trong."),t("br"),t("br"),n._v("Trong lúc vô tình cứu hắn một mạng?"),t("br"),t("br"),n._v("Cái này Cù gia cũng quá lợi hại chứ?"),t("br"),n._v("“Không tệ! Chính vì vậy, bọn hắn đạt được Khổng sư tự tay viết trả lời thư, từ đó tôn làm chí bảo, nhiều đời lưu truyền tới nay.”"),t("br"),t("br"),n._v("Lâm Lung nhìn qua: “Khổng sư tự tay viết lưu lại giấy viết thư trả lời, dù là chỉ có không nhiều chữ viết, giá trị bao lớn, không cần ta nói, điện hạ chắc hẳn cũng có thể rõ ràng! Nếu như đem thứ này chiếm được, đưa cho Lưu Vân tông tông chủ, đừng nói một cái nội môn đệ tử, coi như chân truyền, thậm chí đổi lấy một trưởng lão chi vị, chỉ sợ cũng không là vấn đề đi!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Chấn động toàn thân, Đinh Mục toàn thân cứng ngắc."),t("br"),t("br"),n._v("Khổng sư, thiên hạ sư."),t("br"),t("br"),n._v("Vạn cổ đến nay đệ nhất nhân."),t("br"),t("br"),n._v("Của hắn tự tay viết chữ viết, chứa thiên địa đạo lý, sớm đã bị học sinh trân quý, chưa có lộ ra bên ngoài."),t("br"),t("br"),n._v("Một bộ Khổng sư bút tích thực, lấy ra đưa cho lục tinh danh sư, đối phương có lẽ đều sẽ cao hứng đem hắn thu làm chân truyền..."),t("br"),t("br"),n._v("Quá trân quý!"),t("br"),t("br"),n._v("Đừng nói vạn quốc liên minh, coi như tăng thêm xung quanh hai mươi sáu chỗ tương đồng thế lực, có thể hay không có một bộ cũng còn khó nói... Cù gia lại có!"),t("br"),t("br"),n._v("Cái này..."),t("br"),t("br"),n._v("Hô hấp dồn dập, Đinh Mục cả người đều có chút miệng đắng lưỡi khô."),t("br"),t("br"),n._v("“Hai năm trước, giết chết Cù gia, đã ra tay, đương nhiên muốn Lôi Đình Vạn Quân, nhất cử tiêu diệt, làm sao có thể còn cố ý lưu cái cái đuôi không giết chết?”"),t("br"),t("br"),n._v("Lâm Lung hừ lạnh: “Gia hỏa này cho rằng trốn ở Thiên Vũ học viện, đổi tên Lộ Xung liền không có người biết, nói đùa cái gì! Thật muốn tìm tới, có Tầm Tích thử tại, đừng nói không có thay hình đổi dạng, coi như ngụy trang thân phận, cũng có thể tra rõ rõ ràng ràng!”"),t("br"),t("br"),n._v("Lộ Xung trốn ở Thiên Vũ học viện, thâm cư không ra ngoài, vốn cho rằng là Lâm gia không có phát hiện, nguyên lai đã sớm biết, chỉ là không có động thủ mà thôi."),t("br"),t("br"),n._v("Dù sao, có Tầm Tích thử cái thứ đáng sợ này, biết mùi, liền có thể tìm tới."),t("br"),t("br"),n._v("Nếu như ngay cả một cái Bì Cốt cảnh cũng chưa tới mười mấy tuổi hài tử đều bắt không được, Lâm gia cũng không có khả năng xưng bá vương thành đã nhiều năm như vậy."),t("br"),t("br"),n._v("“Cù gia người, xương cốt rất cứng, thế nào thẩm vấn đều không thể biết được thứ này tung tích, cho nên, liền cố ý lưu lại người sống, chờ hắn chính mình đi tìm.”"),t("br"),t("br"),n._v("Mắt sáng lên, Lâm Lung nói: “Ai ngờ gia hỏa này, cũng nhịn được, hơn hai năm qua, một mực không nhúc nhích. Vốn là, lại không động, chúng ta cũng muốn động thủ, bắt tới thật tốt điều tra, ai ngờ gia hỏa này nhận một cái lão sư tốt, nguyện ý thay hắn ra mặt...”"),t("br"),t("br"),n._v("Nói đến đây, Lâm Lung nắm đấm xiết chặt, khắp khuôn mặt là hận ý."),t("br"),t("br"),n._v("Mặc dù lấy chồng ở xa Hiên Viên vương quốc, không thường thường trở về, Lâm gia chuyện phát sinh, vẫn là rất rõ ràng."),t("br"),t("br"),n._v("Chính vì vậy, mới chuyên môn tốn hao đại đại giới xây dựng đưa tin tường, chỉ là không nghĩ tới, tường này phát huy tác dụng, không phải là bởi vì tìm được cái này lưu truyền xuống bản chép tay, mà là Lâm gia bị người tan vỡ tin tức."),t("br"),t("br"),n._v("“Lão sư tốt? Ta xem không nhất định đi! Có lẽ vị này Trương Huyền, sớm biết Khổng sư tự tay viết chuyện, nếu không, một cái học sinh mà thôi, về phần để hắn một cái Tông sư đỉnh phong nhị tinh danh sư, đắc tội Hiên Viên vương quốc cùng như mặt trời ban trưa Lâm gia?”"),t("br"),t("br"),n._v("Một bên Lương Thanh Mệnh chen vào nói."),t("br"),t("br"),n._v("Hắn là thái tử điện hạ thân tín, tin tưởng không nghi ngờ, bởi vậy, như thế cơ mật lời nói, cũng không có giấu diếm."),t("br"),t("br"),n._v("“Không sai, nhất định là nguyên nhân này, nếu không, coi như hắn có Dương sư làm chỗ dựa, cũng không có khả năng vô duyên vô cớ đắc tội chúng ta, không nhìn Hiên Viên vương quốc uy nghiêm!” Lâm Lung gào thét, răng cắn đến “Khanh khách!” Rung động."),t("br"),t("br"),n._v("Không phải nguyên nhân này, nàng thật nghĩ không thông, một cái tiền đồ vô lượng nhị tinh danh sư, làm gì vì một cái mới nhận biết mấy ngày học sinh, không tiếc cùng Lâm gia khai chiến, thậm chí đưa nàng vị này Hiên Viên vương quốc thái tử phi, đều đánh đấm một trận túi bụi."),t("br"),t("br"),n._v("“Được rồi, bây giờ không phải là thảo luận Trương Huyền động cơ thời điểm!”"),t("br"),t("br"),n._v("Đinh Mục khoát tay đánh gãy hai người thảo luận, con mắt cũng hơi hơi ửng hồng: “Nếu như tin tức là thật, Khổng sư tự tay viết thư, đừng nói đắc tội một thiên tài danh sư, coi như đắc tội vạn quốc liên minh cũng đáng giá!”"),t("br"),t("br"),n._v("Vạn quốc liên minh thống ngự vạn nước, là rất cường đại, nhưng ở xung quanh hai mươi sáu thế lực lớn trong, chỉ có thể sắp xếp thượng cuối cùng."),t("br"),t("br"),n._v("Tin tức là thật, chỉ cần đem đồ vật cướp đến tay, liền có thể thành công bợ đỡ được Lưu Vân tông, thậm chí cao hơn thế lực, bởi vậy đắc tội vạn quốc liên minh, cũng tuyệt đối kiếm lợi lớn."),t("br"),t("br"),n._v("“Đúng vậy a, điện hạ, ngươi phải nhanh lên một chút làm ra quyết đoán, ta đã phái người nghe ngóng, cái này Trương Huyền quyết định rời đi Thiên Vũ vương thành, đã cùng Danh Sư đường mọi người cáo từ. Sáng sớm hôm nay liền hướng Thiên Vũ sơn mạch đi. Vạn quốc liên minh tại phương nam, mà hắn đi Tây Nam, đoán không sai, hẳn là đi tìm món bảo vật này, một khi để bọn hắn đạt được, thuận lợi đưa đến liên minh, còn muốn đoạt lại, liền không dễ dàng như vậy!”"),t("br"),t("br"),n._v("Lâm Lung vội nói."),t("br"),t("br"),n._v("Nàng cũng không phải là hạng người lỗ mãng, trước đó chỉ vì nhìn thấy cha, đệ đệ tại trước mặt thảm trạng, từ đó đã mất đi đúng mực, bị cuồng đánh một trận, tỉnh ngộ lại, sớm đã làm nhiều lần chuẩn bị."),t("br"),t("br"),n._v("“Đến liên minh, còn muốn cướp đến tay, quả thực không thể nào, chỉ là...”"),t("br"),t("br"),n._v("Đinh Mục mày nhăn lại: “Cái kia vị lão sư, cùng bên người hai cái lão giả...”"),t("br"),t("br"),n._v("Hắn lo lắng không phải Trương Huyền, mà là vị lão sư kia, cùng kích thương Lương Thanh Mệnh “Tiểu Tô”."),t("br"),t("br"),n._v("Hóa Phàm cảnh cường giả tọa trấn, đừng nói cướp đoạt, có lẽ vừa mới tới gần liền sẽ bị tại chỗ đánh chết."),t("br"),t("br"),n._v("“Yên tâm đi, điện hạ, người của ta chuyên môn nhìn, cái kia gọi Tiểu Tô cùng một cái khác lão giả, hôm qua đã rời đi. Buổi sáng Trương Huyền thời điểm ra đi, Dương sư cũng không có đi theo, chỉ dẫn theo mấy cái học sinh cùng tùy tùng!”"),t("br"),t("br"),n._v("Lâm Lung nói."),t("br"),t("br"),n._v("Tô sư, Lăng sư rời đi không có giấu diếm, không ít người đều biết, không khó nghe ngóng."),t("br"),t("br"),n._v("Trương Huyền đám người sáng nay cưỡi một đầu to lớn Thiết Xỉ Khiếu Thiên thú rời đi, Khương đường chủ tự mình đưa tiễn, Dương sư không ở tại trong, cũng không ít người nhìn thấy."),t("br"),t("br"),n._v("“Không có đi theo? Cái kia tốt... Thiên Vũ sơn mạch man thú đông đảo, một đám liền Chí Tôn đều không có đạt tới người, chết ở bên trong, chỉ cần xử lý thoả đáng, chắc hẳn... Không có người sẽ biết nguyên nhân!”"),t("br"),t("br"),n._v("Đinh Mục đứng dậy, trong mắt lóe lên một đạo tàn nhẫn."),t("br"),t("br"),n._v("“Quyết định như vậy đi, giết!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-417"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-419"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);