(window.webpackJsonp=window.webpackJsonp||[]).push([[1653],{2007:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-410-tim-toi-cho-o-cu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-410-tim-toi-cho-o-cu"}},[n._v("#")]),n._v(" Chương 410: Tìm tới chỗ ở cũ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Sao ngươi lại tới đây?”"),t("br"),t("br"),n._v("Nhìn trước mắt nữ tử, Trương Huyền không còn gì để nói."),t("br"),t("br"),n._v("Nhận lấy thân truyền, Thiên Vũ vương quốc sự tình cũng xử lý không sai biệt lắm, đem Thiết Xỉ Khiếu Thiên thú triệu hoán tới, không có quá nhiều dừng lại, trực tiếp rời đi."),t("br"),t("br"),n._v("Lưu sư, Lục Tầm biết bản thân bản lĩnh, tại Thiên Vũ vương quốc, có vương thất, Danh Sư đường chiếu khán, còn có thể tiến bộ mau mau, đến vạn quốc liên minh, bị người ăn cũng không biết chuyện gì xảy ra, chủ động lưu lại."),t("br"),t("br"),n._v("Chỉ có Tôn Cường cãi nhau muốn đi theo Dương sư sau lưng, sợ lộ tẩy, đành phải cùng trước đó đồng dạng, mang tại bên người."),t("br"),t("br"),n._v("Theo đến Thiên Vũ vương thành lại đến rời đi, vừa vặn mười ngày, tu vi theo Thông Huyền cảnh đỉnh phong thuận lợi đạt đến Tông sư đỉnh phong, thân thể càng là nắm giữ Chí Tôn sơ kỳ lực lượng."),t("br"),t("br"),n._v("Học sinh cũng nhiều một cái Lộ Xung."),t("br"),t("br"),n._v("Về phần đặc thù chức nghiệp, khảo hạch nhị tinh danh sư thành công, tam tinh thư họa sư huy chương cũng tại hôm qua đưa tới."),t("br"),t("br"),n._v("Bởi vì tu vi không đủ, y sư, luyện đan sư tạm thời không cách nào xứng đôi cấp bậc cao hơn, nhưng cũng lấy được hai cái tam tinh huy chương."),t("br"),t("br"),n._v("Nghề nghiệp của hắn cũng là biến thành: Nhị tinh danh sư, tam tinh luyện đan sư, tam tinh y sư, tam tinh thư họa sư, nhị tinh tuần thú sư."),t("br"),t("br"),n._v("Thu hoạch tương đối khá."),t("br"),t("br"),n._v("Người khác cả một đời đều không lấy được chức nghiệp cùng vinh dự, trong mười ngày liền lấy đến, lưu lại vô số sợ hãi thán phục cùng bội phục."),t("br"),t("br"),n._v("“Ta cũng muốn nhìn xem vạn quốc liên minh danh sư thi đấu, không biết không muốn mang ta đi?” Mạc Vũ thở phì phò nhìn trước mắt thanh niên."),t("br"),t("br"),n._v("Người nào đây!"),t("br"),t("br"),n._v("Coi như phát triển không được càng sâu quan hệ, chí ít cũng là bằng hữu đi!"),t("br"),t("br"),n._v("Muốn đi vạn quốc liên minh tham gia danh sư thi đấu, không nói một tiếng thì cũng thôi đi."),t("br"),t("br"),n._v("Có thể xoay người rời đi, báo tin đều không có báo tin, quá không đủ ý tứ đi!"),t("br"),t("br"),n._v("May mắn sớm có chuẩn bị, cưỡi Thanh Ưng Thú đuổi theo, nếu không, khẳng định đã sớm không thấy."),t("br"),t("br"),n._v("(Mạc Vũ là danh sư, nghe ngóng danh sư thi đấu, cùng Tô sư đám người chuyện, không tính quá khó.)"),t("br"),t("br"),n._v("“Tốt a, mang...”"),t("br"),t("br"),n._v("Trương Huyền bất đắc dĩ."),t("br"),t("br"),n._v("Dù sao Thiết Xỉ Khiếu Thiên thú trên lưng gian phòng cũng đủ lớn, sáu cái học sinh, Tôn Cường, lại thêm Mạc Vũ đều ngồi vào đến, cũng không hiện chen chúc."),t("br"),t("br"),n._v("Không thèm để ý thở phì phò Mạc Vũ, Trương Huyền đứng tại bên cửa sổ bên trên, hướng ra phía ngoài nhìn lại, mây trắng nối liền cùng một chỗ, tựa như mênh mông vô bờ cánh đồng tuyết, xanh tươi sơn mạch khảm nạm trong đó, để cho người ta tầm mắt bao la, thần thanh khí sảng."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Lộ Xung đi tới trước mặt: “Chúng ta Tiên Tổ, lúc trước vì phòng ngừa hậu bối bất tài, bản chép tay rơi vào tay người khác, chuyên môn đem hắn đặt ở Thiên Vũ sơn mạch chỗ ở cũ. Ta cũng là nghe cha thuận miệng đề cập qua một câu, biết đại khái phạm vi, cụ thể ở đâu, cũng không rõ lắm!”"),t("br"),t("br"),n._v("“Tìm xem xem đi!”"),t("br"),t("br"),n._v("Đây là Lộ Xung gia tộc gặp tai hoạ căn nguyên, có thể tìm tới đương nhiên tốt nhất."),t("br"),t("br"),n._v("“Lão sư, đại khái ngay tại cái phạm vi này!”"),t("br"),t("br"),n._v("Nửa ngày về sau, Lộ Xung một chỉ."),t("br"),t("br"),n._v("“Phạm vi này thật lớn, đi nơi nào tìm?”"),t("br"),t("br"),n._v("Theo Khiếu Thiên thú trên lưng xuống, nhìn quanh một tuần, Triệu Nhã nhịn không được nói."),t("br"),t("br"),n._v("Trước mắt là cái cự đại sơn cốc, chung quanh thế núi nhấp nhô, liên miên bất tuyệt, bốn phía đều là cao lớn xanh ngắt thảm thực vật, đừng nói tìm cái gì chỗ ở cũ, liền cái Quỷ Ảnh đều không nhìn thấy."),t("br"),t("br"),n._v("Chủ yếu hơn chính là, nơi này man thú hoành hành, tìm không thấy phương vị chính xác, vạn nhất xông đến man thú sào huyệt, chết cũng không biết chết như thế nào."),t("br"),t("br"),n._v("“Ta... Cũng không biết!”"),t("br"),t("br"),n._v("Lộ Xung gãi gãi đầu, một mặt xấu hổ."),t("br"),t("br"),n._v("Hắn chỉ là nghe trưởng bối nói qua đại khái phạm vi, chưa từng tới bao giờ, cũng không rõ ràng."),t("br"),t("br"),n._v("“Bát ngát như vậy sơn mạch, tìm cái gì đều không rõ ràng chỗ ở cũ... Một năm cũng tìm không thấy!”"),t("br"),t("br"),n._v("Mạc Vũ cũng lắc đầu."),t("br"),t("br"),n._v("Nàng chỉ biết là Lộ Xung trước khi đi vạn quốc liên minh trước, muốn về chỗ ở cũ tìm một vật, cũng không biết vật gì."),t("br"),t("br"),n._v("Nhưng trước mắt cái phạm vi này cũng thực sự quá lớn."),t("br"),t("br"),n._v("Sơn mạch bao la, cây cối thảm thực vật bao phủ, lại nói, khả năng còn có trận pháp che giấu... Làm sao tìm được?"),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Mọi người nhìn một vòng đều không có biện pháp gì, đành phải đem ánh mắt tập trung trên người Trương Huyền."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền cũng gãi gãi đầu: “Nhìn ta cũng vô dụng, ta chưa hề tới qua gia tộc bọn họ chỗ ở cũ, càng không biết ở đâu...”"),t("br"),t("br"),n._v("Nói được nửa câu, đột nhiên ánh mắt sáng lên: “Đúng rồi, ta nghĩ đến một cái phương pháp, tuyệt đối đáng tin cậy, không có bất cứ vấn đề gì!”"),t("br"),t("br"),n._v("“Có biện pháp?”"),t("br"),t("br"),n._v("Mọi người tất cả đều sững sờ, hai mắt sáng lên nhìn qua."),t("br"),t("br"),n._v("Mạc Vũ cũng rất là hiếu kỳ."),t("br"),t("br"),n._v("Lộ Xung gia tộc chỗ ở cũ, liền người trong cuộc cũng không biết, hắn có biện pháp nào?"),t("br"),t("br"),n._v("Ngay tại kỳ quái, chỉ thấy thanh niên trước mắt, xuất ra một bản trống không thư tịch, tiện tay vẽ lên cái phương hướng sơ đồ, không ngừng chuyển động thân thể, trong miệng nói lẩm bẩm: “Lộ Xung gia tộc chỗ ở cũ tại cái phương hướng này... Tại cái phương hướng này... Cái phương hướng này...”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Mạc Vũ, Triệu Nhã đám người một cái lảo đảo."),t("br"),t("br"),n._v("Vốn cho rằng sẽ có cái gì đáng tin cậy phương pháp, nhìn thấy cái này, đều kém chút không có tại chỗ ngất đi."),t("br"),t("br"),n._v("Cầm cái sách nát, tùy tiện vẽ cái hình, liền xác định phương hướng, còn đáng tin cậy, nhất định có thể tìm tới... Ở đâu ra tự tin?"),t("br"),t("br"),n._v("Lừa gạt người, cũng muốn chuẩn bị cho tốt điểm, đây cũng quá qua loa đi!"),t("br"),t("br"),n._v("“Được rồi, liền cái phương hướng này!”"),t("br"),t("br"),n._v("Dạo qua một vòng, Trương Huyền xác định được, chỉ về phía trước."),t("br"),t("br"),n._v("“...” Mạc Vũ lần nữa nhoáng một cái."),t("br"),t("br"),n._v("Có đôi khi cảm thấy vị này Trương sư thiên tư vô song, thế nào lúc này lại cảm thấy như thế làm ẩu đâu?"),t("br"),t("br"),n._v("“Ngươi cái này... Phương pháp thật có thể xác nhận phương hướng?” Thực sự nhịn không được, hỏi lên."),t("br"),t("br"),n._v("“Đương nhiên!” Trương Huyền gật gật đầu, một mặt nghiêm mặt: “Ta phương pháp này liên quan thiên địa tinh thần, sơn xuyên đại trạch, kết hợp với trong lòng đối với thiên địa lĩnh ngộ, bảo tàng đều có thể tìm tới, đừng nói phương hướng!”"),t("br"),t("br"),n._v("Thiên Đạo thư viện có thể xác định sai lầm, tìm không thấy phương hướng không sao, chỉ cần viết ra, trong lòng mặc niệm, liền có thể phán đoán thiếu hụt, tìm tới chính xác nhất vị trí."),t("br"),t("br"),n._v("Bất quá, việc này không thể nói ra được."),t("br"),t("br"),n._v("“Thiên địa tinh thần? Sơn xuyên đại trạch?”"),t("br"),t("br"),n._v("“Đối với thiên địa cảm ngộ?”"),t("br"),n._v("Ngươi nhìn ta ta nhìn ngươi."),t("br"),t("br"),n._v("Cầm cuốn sách bại hoại, tùy tiện vẽ lên cái phương hướng, lượn một vòng liền liên quan những này? Ngươi dám lại kéo một chút ư?"),t("br"),t("br"),n._v("“Được rồi, dù sao cũng tìm không thấy phương hướng, nghe hắn a!”"),t("br"),t("br"),n._v("Mạc Vũ một hồi táo bón cảm giác."),t("br"),t("br"),n._v("Làm sao lại đi theo đến đây, thật hoài nghi như vậy đi xuống, có thể hay không làm mất, rốt cuộc tìm không quay về."),t("br"),t("br"),n._v("Dọc theo chỉ ra phương hướng tiến lên, một khi đi đến cuối cùng, hoặc là phân biệt không ra phương hướng mới, Trương Huyền liền lấy ra sách xoay quanh vòng, một canh giờ sau, đi tới một cái không lớn khe núi."),t("br"),t("br"),n._v("“Chính là chỗ này!”"),t("br"),t("br"),n._v("Chỉ về phía trước."),t("br"),t("br"),n._v("Theo nhìn lại, mọi người tất cả đều sững sờ."),t("br"),t("br"),n._v("Chỉ gặp cách đó không xa xanh ngắt bên trong, quả nhiên lộ ra mấy gian nhà đá nóc phòng, tựa hồ giấu ở một loại nào đó trong trận pháp, như ẩn như hiện, có chút mơ hồ."),t("br"),t("br"),n._v("“Liền là cái này, cùng cha nói với ta giống nhau như đúc...”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, Lộ Xung thốt ra."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Thấy hắn xác nhận, mọi người tất cả đều con mắt trợn tròn nhìn qua, nhất là Mạc Vũ, xem quái vật nhìn về phía Trương Huyền, gần điên rồi."),t("br"),t("br"),n._v("Liền Lộ Xung người biết chuyện này, cũng không tìm tới phương hướng, hắn tùy tiện xuất ra quyển sách, lượn một vòng, vốn cho rằng làm loạn, kết quả... Lại là đúng!"),t("br"),t("br"),n._v("Có lầm hay không?"),t("br"),t("br"),n._v("Thật chẳng lẽ cùng hắn nói đồng dạng, liên quan cái gì thiên địa tinh thần, sơn xuyên đại trạch?"),t("br"),t("br"),n._v("Bằng không, sao có thể thật đi tìm đến?"),t("br"),t("br"),n._v("Hơn nữa cẩn thận nhớ lại thoáng cái, từ đối phương theo xuất ra sách xoay quanh bắt đầu, bọn hắn đi phương hướng toàn bộ đều là đường thẳng, vẫn là an toàn nhất, không có man thú ẩn hiện địa phương, coi như sớm biết vị trí, cũng không thể đi tinh như vậy xác thực chứ?"),t("br"),t("br"),n._v("Cực kỳ mấu chốt chính là, mọi người vị trí, chính là trong trận pháp cái gọi là trận nhãn, đứng ở chỗ này, có thể nhìn thấy phòng ốc, đổi một góc độ vị trí, những này liền sẽ bị trận pháp bao phủ, rốt cuộc không nhìn thấy."),t("br"),t("br"),n._v("Nói cách khác, nếu như không biết vị trí, coi như đi tới trước mặt, cũng sẽ bị trận pháp mê hoặc, gặp thoáng qua."),t("br"),t("br"),n._v("Không biết ở nơi nào, tùy tiện tìm, không chỉ phương hướng chuẩn xác, còn có thể thoáng cái tìm tới địa phương..."),t("br"),t("br"),n._v("Gia hỏa này... Làm sao làm được?"),t("br"),t("br"),n._v("“Được rồi, hắn cũng không phải là người...”"),t("br"),t("br"),n._v("Trong lòng khiếp sợ, thực sự không nghĩ ra, Mạc Vũ đành phải cười khổ lắc đầu."),t("br"),t("br"),n._v("Gia hỏa này từ khi vừa thấy được, liền biểu hiện ra nghịch thiên năng lực, không ít chuyện, dùng lẽ thường căn bản phỏng đoán không thông."),t("br"),t("br"),n._v("Thật muốn phỏng đoán, sớm muộn gì đều sẽ bị tươi sống mệt chết."),t("br"),t("br"),n._v("“Đi qua đi!”"),t("br"),t("br"),n._v("Không để ý tới ngẩn người mọi người, Trương Huyền đi đầu hướng khe núi đi đến."),t("br"),t("br"),n._v("“Lão sư, trước không vội, trận pháp này mười phần nguy hiểm!”"),t("br"),t("br"),n._v("Lộ Xung vội vàng hướng về phía trước: “Do ta Tiên Tổ lưu lại, nghe nói chỉ có thể có hậu bối huyết dịch mới có thể đình chỉ...”"),t("br"),t("br"),n._v("“Hậu bối huyết dịch?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Ừm, thật giống muốn đem máu của ta, nhỏ tại chủ điều khiển trận pháp nham thạch bên trên, trận pháp liền sẽ lập tức đình chỉ, xuất hiện một cái có thể dung người tiến vào thông đạo. Nếu như không có thứ này thủ hộ, lưu tại sơn mạch nhiều năm như vậy, chỉ sợ sớm đã bị người hoặc là man thú vào xem!”"),t("br"),t("br"),n._v("Lộ Xung giải thích nói."),t("br"),t("br"),n._v("Mạc Vũ đám người gật đầu."),t("br"),t("br"),n._v("Cái này chỗ ở cũ, không biết bao nhiêu năm không người đến qua, còn có thể duy trì nguyên dạng, không có bị hủy hoại, đủ thấy trận pháp mạnh mẽ."),t("br"),t("br"),n._v("Không chỉ người vào không được, coi như man thú, muốn đi vào, cũng gần như không có khả năng."),t("br"),t("br"),n._v("Lợi hại như thế trận pháp, không nói trước đình chỉ, tùy tiện tiến lên, sẽ rất phiền phức."),t("br"),t("br"),n._v("“Vậy liền nhanh điểm thử một chút!”"),t("br"),t("br"),n._v("Mạc Vũ mở lời."),t("br"),t("br"),n._v("Cũng không nói nhảm, Lộ Xung hướng bốn phía nhìn lại, nhìn một hồi, lộ ra mướp đắng chi sắc."),t("br"),t("br"),n._v("Cha ban đầu là nói qua, đem máu tươi nhỏ tại chủ điều khiển trận pháp nham thạch bên trên, có thể để cho trận pháp đình chỉ, có thể... Cái này không phải chủ điều khiển trận pháp nham thạch?"),t("br"),t("br"),n._v("Quái thạch đứng vững, mấy bước một cái, toàn bộ trong khe núi, không có hơn vạn cũng chí ít có mấy ngàn, nếu như mỗi một cái đều giọt một giọt máu tươi thử một chút, đoán chừng trận pháp còn không có đình chỉ, hắn trước hết treo."),t("br"),t("br"),n._v("“Cái này... Lão sư, ta tìm không thấy chủ điều khiển trận pháp nham thạch...”"),t("br"),t("br"),n._v("Một lát sau, một mặt xấu hổ."),t("br"),t("br"),n._v("Tỷ tỷ của hắn là trận pháp sư thiên tài, hắn không phải."),t("br"),t("br"),n._v("Từ nhỏ đến lớn, gia tộc mặc dù có rất nhiều trận pháp bí tịch, nhưng hắn cũng không thích, chưa hề nhìn qua, cái này được rồi, đến Tiên Tổ chỗ ở cũ trước mặt, lại ngay cả chủ điều khiển trận pháp nham thạch cũng không tìm tới..."),t("br"),t("br"),n._v("“Tìm không thấy?”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt một chút, nhìn quanh một tuần, cũng vuốt vuốt lông mày: “Ta cũng tìm không thấy...”"),t("br"),t("br"),n._v("Mặc dù nhìn không ít liên quan tới trận pháp sách, nhưng trận pháp này rõ ràng cao hơn tại nhị phẩm, tam phẩm, siêu việt Thiên Vũ vương quốc cấp độ."),t("br"),t("br"),n._v("Lấy nhãn lực của hắn, muốn tìm được, cũng rất phiền phức."),t("br"),t("br"),n._v("“Được rồi, cũng đừng tìm cái gì chủ điều khiển nham thạch...”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền nói."),t("br"),t("br"),n._v("“Cái kia... Chúng ta thế nào đi vào?”"),t("br"),t("br"),n._v("Lộ Xung sững sờ."),t("br"),t("br"),n._v("“Đơn giản...”"),t("br"),t("br"),n._v("Trương Huyền tiến về phía trước một bước."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một bước vào trận pháp phạm vi, toàn bộ đại trận lập tức đung đưa, tiếp lấy Trương Huyền giơ chân lên chưởng, thẳng tắp đạp tới."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Trận pháp đình chỉ."),t("br"),t("br"),n._v("Trương Huyền khoát tay: “Được rồi, có thể đi!”"),t("br"),t("br"),n._v("“...” Lộ Xung, Mạc Vũ đám người."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-418"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-420"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);