(window.webpackJsonp=window.webpackJsonp||[]).push([[1654],{2017:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-33-gia-bo-hao-phong"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-33-gia-bo-hao-phong"}},[n._v("#")]),n._v(" Chương 33: Giả bộ hào phóng")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v(" “Thượng công tử, ngươi đã đến, mau mời tiến vào!” "),g("br"),g("br"),n._v(" Hai người một thú còn chưa đi tiến vào tửu lâu, một người trung niên cười tiến lên đón."),g("br"),g("br"),n._v(" Hồng Thiên Lâu chấp sự, Ngô Sầu!"),g("br"),g("br"),n._v(" “Cho chúng ta tìm một một chỗ yên tĩnh, ta cùng Tào Hùng lão sư phải cố gắng uống một chén!” Thượng Bân nói."),g("br"),g("br"),n._v(" “Mời tới bên này!” "),g("br"),g("br"),n._v(" Ngô Sầu rất nhanh giúp bọn họ sắp xếp xong xuôi chỗ ngồi."),g("br"),g("br"),n._v(" “Thượng thiếu, cái này Ngô chấp sự ta mỗi lần tới đều lỗ mũi Triêu Thiên, lạnh nhạt, lần này làm sao...” Tào Hùng không nhịn được hỏi."),g("br"),g("br"),n._v(" Hồng Thiên Lâu mặc dù là cái tửu lâu, nhưng nghe nói thế lực sau lưng rất lớn, bằng không cũng không thể mở ở Hồng Thiên học viện nội bộ, đồng thời như vậy quy mô!"),g("br"),g("br"),n._v(" Hắn dĩ vãng tới dùng cơm, cái này Ngô chấp sự đều chưa bao giờ phản ứng, bây giờ lại cúi đầu khom lưng, cực điểm cung kính khả năng, để hắn đều có chút không dám tin tưởng."),g("br"),g("br"),n._v(" “Học viện trước đây có cái Hồng Hạo trưởng lão ngươi hẳn nghe nói qua đi!” Thượng Bân nói."),g("br"),g("br"),n._v(" Tào Hùng gật đầu."),g("br"),g("br"),n._v(" Hồng Thiên học viện là Hồng Thiên sáng chế, sau khi hắn chết, hậu bối tiếp tục kéo dài, bởi vậy, trong học viện họ Hồng trưởng lão địa vị đều tương đối tôn sùng."),g("br"),g("br"),n._v(" Cái này Hồng Hạo trưởng lão trước xác thực nghe nói qua, thực lực siêu cường, có người nói còn từng tranh cướp quá Viện trưởng vị trí, bất quá về sau không biết nguyên nhân gì, đột nhiên từ đi tới trưởng lão vị trí, sau đó liền không có tin tức."),g("br"),g("br"),n._v(" “Từ đi trưởng lão vị trí về sau, mở ra ngôi tửu lâu này, hắn cùng ông nội ta từng là bằng hữu tốt nhất, vì lẽ đó, ta mỗi lần lại đây, vị này Ngô chấp sự, đều sẽ cố gắng tiếp đón!” Thượng Bân không khỏi đắc ý nói."),g("br"),g("br"),n._v(" “Thì ra là như vậy!” Tào Hùng gật đầu."),g("br"),g("br"),n._v(" Chẳng trách cái này Hồng Thiên Lâu như vậy quy mô, hóa ra là đã từng Hồng Hạo trưởng lão mở."),g("br"),g("br"),n._v(" Lớn như thế tửu lâu, bị đường đường chấp sự tự mình tiếp đón, tuyệt đối là rất chuyện vinh hạnh."),g("br"),g("br"),n._v(" “Ừm?” "),g("br"),g("br"),n._v(" Chính đang hưởng thụ chưa bao giờ có đãi ngộ, Tào Hùng đột nhiên ngừng lại, sắc mặt âm trầm."),g("br"),g("br"),n._v(" “Thế nào?” Thượng Bân nghi ngờ nhìn sang."),g("br"),g("br"),n._v(" “Cái kia là Trương Huyền đi, hắn làm sao có tư cách trong này ăn cơm?” Tào Hùng chỉ tay."),g("br"),g("br"),n._v(" “Trương Huyền?” Thượng Bân nhìn lại, nhìn thấy thiếu niên đồng thời, cũng nhìn thấy cùng hắn ngồi cùng một chỗ bóng người, lông mày đột nhiên nhảy một cái, lửa giận vọt lên: “Nàng như thế nào cùng tên rác rưởi này đồng thời? Ta mời nàng nhiều lần, một mực từ chối, dĩ nhiên cùng người khác cùng nhau ăn cơm! Đáng ghét, đáng ghét!” "),g("br"),g("br"),n._v(" Hắn đường đường trưởng lão con trai, học viện cao cấp giáo sư, võ giả năm tầng Đỉnh Lực cảnh cường giả, mời cái này Trầm Bích Như thật nhiều lần, đối phương chưa bao giờ đáp ứng, vốn tưởng rằng nàng chưa bao giờ cùng người khác ăn cơm, nằm mơ đều không có nghĩ đến, giờ khắc này đang cùng học viện có tiếng rác rưởi ngồi cùng một chỗ!"),g("br"),g("br"),n._v(" Kém chút không tại chỗ tức đến ngất đi."),g("br"),g("br"),n._v(" Vỗ bàn một cái đứng dậy liền muốn xông qua chất vấn, bất quá vừa nghĩ tới Trầm Bích Như thái độ, lập tức ỉu xìu hạ xuống."),g("br"),g("br"),n._v(" Nàng đáng ghét nhất người khác quấy rối, cứ như vậy xông tới chất vấn, một khi trêu chọc nổi giận, lại nghĩ theo đuổi, khẳng định đừng đùa!"),g("br"),g("br"),n._v(" “Thượng thiếu không cần tức giận, ta có cái biện pháp, có thể để cho Trầm lão sư nhìn rõ ràng cái này Trương Huyền chân diện mục! Còn có thể nhân cơ hội lộ ra Thượng thiếu uy vũ anh tư!” Tào Hùng là cái nhân tinh, làm sao không nhìn ra tình huống, chớp mắt một cái, cười nói."),g("br"),g("br"),n._v(" “Biện pháp gì?” "),g("br"),g("br"),n._v(" “Cái này Trương Huyền khẳng định là nịnh bợ Trầm lão sư, mời nàng ăn cơm! Hắn một cái cấp thấp nhất lão sư, có thể có bao nhiêu tiền lương? Đến thời điểm chúng ta chỉ cần làm một hồi, để hắn trả tiền không nổi, tự nhiên sẽ mất mặt xấu hổ! Đến thời điểm Thượng thiếu sẽ đi qua anh hùng cứu mỹ nhân... Trầm lão sư nhất định sẽ đối với Thượng thiếu nhìn với cặp mắt khác xưa, làm không cẩn thận liền sẽ phương tâm ngầm hứa, ôm ấp yêu thương...” Tào Hùng đem kế hoạch của chính mình nói ra."),g("br"),g("br"),n._v(" “Tốt, cứ làm như thế!” Thượng Bân ánh mắt sáng lên, thoả mãn gật đầu."),g("br"),g("br"),n._v(" Đã có thể tổn hại đối thủ, có thể nâng lên thân phận của chính mình, ý đồ này quả thực quá tốt rồi!"),g("br"),g("br"),n._v(" Xem ra cái này Tào Hùng, không riêng khóa dạy tốt, nhân cũng có một tay, xem ra sau này phải cố gắng bồi dưỡng một hồi..."),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v(" Trương Huyền cũng không biết đã bị người mưu hại, ăn trên bàn mỹ vị, gật đầu liên tục."),g("br"),g("br"),n._v(" Tuy rằng nấu nướng kỹ thuật không bằng Địa cầu, nhưng thế giới này linh khí dồi dào,"),g("br"),g("br"),n._v(" Nguyên liệu nấu ăn mới mẻ, mỹ vị, ăn ở trong miệng có một phong vị khác."),g("br"),g("br"),n._v(" Rất nhanh, đầy bàn rượu và thức ăn liền bị ăn thất thất bát bát, cơ hồ phần lớn đều bị hắn tiêu diệt, Trầm Bích Như mỗi một dạng chỉ tính chất tượng trưng ăn một chút."),g("br"),n._v(" Thấy cái tên này vào xem ăn, ngay cả mình không thèm để ý, Trầm Bích Như tức giận miệng nhô lên."),g("br"),g("br"),n._v(" Nguyên bản nàng còn tưởng rằng đối phương là giả vờ, dùng để hấp dẫn nàng chú ý, giờ khắc này mới biết, vẫn đúng là không coi nàng là thành một chuyện..."),g("br"),g("br"),n._v(" Dù cho học viện thiên tài giáo sư, minh tinh giáo sư, nhìn thấy nàng đều hết sức lấy lòng, khúc ý nịnh hót, vị này ngược lại tốt, rõ ràng học viện thứ nhất đếm ngược lại vẫn cứ coi nàng không có gì, quả thực tức giận răng bạc cắn loạn, hận không thể hai cước đạp tới."),g("br"),g("br"),n._v(" Càng nghĩ càng giận, biết tiếp tục ăn xuống, không phải tức chết không thể, xoay người khẽ kêu: “Tính sổ!” "),g("br"),g("br"),n._v(" “Tổng cộng 1280 kim tệ!” "),g("br"),g("br"),n._v(" Một cái người phục vụ đi lên."),g("br"),g("br"),n._v(" “1280?” Trầm Bích Như sững sờ, sợ hết hồn: “Làm sao đắt như thế?” "),g("br"),g("br"),n._v(" Giống nàng cao cấp như thế giáo sư, một tháng cũng là một ngàn kim tệ tiền lương, một trận liền ăn một tháng tiền lương?"),g("br"),g("br"),n._v(" Làm sao sẽ nhiều như thế?"),g("br"),g("br"),n._v(" Vừa nãy chọn món ăn thời điểm nàng chuyên môn được rồi, liền một trăm cũng chưa tới, làm sao một hồi đạt đến hơn một ngàn?"),g("br"),g("br"),n._v(" “Xin lỗi, này một bình rượu liền 1,200 kim tệ!” Phục vụ viên nói."),g("br"),g("br"),n._v(" Vừa nãy hai người đang dùng cơm, một cái người phục vụ hỏi bọn họ có muốn hay không rượu, Trầm Bích Như cảm thấy uống chút rượu cũng không có gì, liền gật đầu đáp ứng, nằm mơ đều không có nghĩ tới đây sao quý!"),g("br"),g("br"),n._v(" “Bình rượu này là các ngươi đưa tới, chúng ta cũng không biết giá cả...” "),g("br"),g("br"),n._v(" Trầm Bích Như sắc mặt khó coi."),g("br"),g("br"),n._v(" Có ngốc nàng cũng biết, bị người hãm hại."),g("br"),g("br"),n._v(" “Nếu như ngươi thật không muốn, có thể sớm hỏi dò một câu, ngươi không nói, chúng ta cho là ngươi biết giá cả, cũng sẽ không lắm miệng!” Người phục vụ lạnh lùng nhìn sang."),g("br"),g("br"),n._v(" “Hừ!” Thấy đối phương thái độ, Trầm Bích Như biết nói nhiều hơn nữa vô dụng, làm lớn trái lại ảnh hưởng không được, cũng là không nói thêm lời, mở ra túi áo, muốn trả tiền, đột nhiên mặt cười nhất bạch, tràn đầy lúng túng: “Trên người ta không nhiều tiền như vậy, ngươi nhìn nếu không trước tiên nợ, ta trở lại lấy tiền đưa tới cho ngươi...” "),g("br"),g("br"),n._v(" Nàng hôm nay là đi Tàng Thư Các đọc sách, không mang tiền gì, sau đó gặp phải Trương Huyền, hướng về hắn thỉnh giáo, quá mức khiếp sợ liền đem chuyện này đã quên, hiện tại mới nhớ tới, trên thân tổng cộng cũng là chừng một trăm cái kim tệ, cách hơn một ngàn còn kém xa lắm."),g("br"),g("br"),n._v(" Mời người ăn cơm, lại không tiền trả tiền..."),g("br"),g("br"),n._v(" Nàng chỉ cảm thấy trên mặt rát, hận không thể có một cái lỗ chui vào."),g("br"),g("br"),n._v(" “Không trả nổi cũng đừng chọn món ăn, nếu điểm, liền trả thù lao, muốn ăn cơm chùa, nơi này không được!” Người phục vụ hừ nói."),g("br"),g("br"),n._v(" “Ngươi...” "),g("br"),g("br"),n._v(" Trầm Bích Như tức giận mặt cười thấu hồng."),g("br"),g("br"),n._v(" “Đây là thế nào?” "),g("br"),g("br"),n._v(" Nhưng vào lúc này, một cái quát lớn vang lên, lập tức Thượng Bân mang theo Bạo Thiên Sư, Tào Hùng đi nhanh tới."),g("br"),g("br"),n._v(" Hắn toàn thân áo trắng, hai tay chắp ở sau lưng, ngẩng đầu mà bước, trên thân tỏa ra cao cao tại thượng khí chất, đổi lại trước đây, dựa vào khí chất của hắn cùng dung mạo, lại phối hợp đầy người hung hãn Bạo Thiên Sư, xác thực được cho ngọc thụ lâm phong, phiên phiên giai công tử, bất quá bây giờ, một mặt sưng đỏ, hai mắt ô mắt thanh, từ xa nhìn lại, không nói ra được buồn cười."),g("br"),g("br"),n._v(" Bất quá, hắn không hề có cái cảm giác này, trái lại tự mình cảm giác hài lòng, ánh mắt thâm thúy, mang theo xa xưa khí tức, đi tới trước mặt, con mắt lạc trên người Trầm Bích Như, giả ra kinh ngạc dáng dấp: “Trầm lão sư, trùng hợp như vậy, ngươi cũng ở nơi đây?” "),g("br"),g("br"),n._v(" Quay đầu nhìn về phía người phục vụ: “Xảy ra chuyện gì? Sảo sảo nháo nháo còn thể thống gì?” "),g("br"),g("br"),n._v(" “A, hóa ra là Thượng thiếu!” Người phục vụ sợ hết hồn, lại không còn vừa nãy hùng hổ doạ người, trái lại lộ ra cẩn thận từng li từng tí một thái độ: “Là như vậy, bọn họ ăn cơm, không có tiền trả tiền...” "),g("br"),g("br"),n._v(" “Không có tiền trả tiền?” "),g("br"),g("br"),n._v(" Thượng Bân lắc đầu một cái, giả ra một mặt thất vọng dáng dấp, nhìn về phía Trương Huyền: “Trương lão sư, không phải ta nói ngươi, ngươi nếu như không có tiền, cũng không cần giả bộ hào phóng, mời người tới dùng cơm! Lần này mất mặt đi! Ngươi học viện thứ nhất đếm ngược, từ lâu mất mặt ném quen rồi, không có việc gì, để cho chúng ta Trầm lão sư cũng thật mất mặt, cũng có chút quá mức đi!” "),g("br"),g("br"),n._v(" “...” "),g("br"),g("br"),n._v(" Thấy cái tên này một mặt tự cho là dáng dấp, Trương Huyền sửng sốt một chút, quay đầu nhìn về phía Trầm Bích Như: “Ai, hắn thật giống đang nói ngươi, nếu như không có tiền, cũng đừng giả bộ hào phóng, ngươi nhìn lần này mất mặt đi...” "),g("br"),g("br"),n._v(" “Ngươi...” "),g("br"),g("br"),n._v(" Nghe được lời của đối phương, Trầm Bích Như vốn là có khí, lại nghe được Trương Huyền lặp lại, kém chút không tại chỗ tức điên, ngẩng đầu đột nhiên nhìn về phía chính một mặt đắc ý, dự định tinh tướng thu được mỹ nữ ưu ái Thượng Bân, răng ngà cắn “Khanh khách!” Vang vọng: “Thượng Bân, ngươi nói một chút ai giả bộ hào phóng?” "),g("br"),g("br"),n._v("Convert by: Hoang Chau"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-41"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-43"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);