(window.webpackJsonp=window.webpackJsonp||[]).push([[1655],{2008:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-411-han-la-thay-ta"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-411-han-la-thay-ta"}},[n._v("#")]),n._v(" Chương 411: Hắn là thầy ta")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Trận pháp kém một cái tiểu đẳng cấp, uy lực còn kém không chỉ gấp đôi, tìm đầu mối then chốt chỗ cũng là càng khó."),t("br"),t("br"),n._v("Mọi người vốn cho rằng Trương sư có thể một cước đá ngừng tam phẩm sơ kỳ trận pháp, liền rất nghịch thiên, rất để cho người ta khiếp sợ, nhìn thấy thế mới biết..."),t("br"),t("br"),n._v("Cái gì nhị phẩm, tam phẩm, tứ phẩm, căn bản không phải chuyện gì!"),t("br"),t("br"),n._v("Chỉ cần hắn tại, lợi hại hơn nữa, một cước cũng muốn xong đời."),t("br"),t("br"),n._v("Khóe miệng giật một cái, Lộ Xung dùng sức lắc đầu."),t("br"),t("br"),n._v("Lúc trước cha nói với hắn, trận pháp này vô cùng lợi hại, là các tiền bối một lần lại một lần gia cố mới hình thành, ẩn chứa đại trí tuệ... Hiện tại xem ra, trí tuệ cọng lông ah, lão sư tới, hết thảy đều là gà đất chó sành."),t("br"),t("br"),n._v("Trận pháp đình chỉ, không có nguy hiểm, mọi người nối đuôi nhau mà vào."),t("br"),t("br"),n._v("Mấy gian đơn giản nhà đá, không biết tồn tại bao lâu, cho dù có hậu bối thường xuyên sửa chữa lại, cũng lộ ra rách nát không chịu nổi."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Đi tới trước nhà đá, Trương Huyền ngừng lại."),t("br"),t("br"),n._v("Đây là Lộ Xung Tiên Tổ vật lưu lại, cùng hắn tới, chỉ vì phá trận, mục đích đạt tới không có nguy hiểm, qua không đi qua cũng là không trọng yếu."),t("br"),t("br"),n._v("“Được, lão sư!”"),t("br"),t("br"),n._v("Gật gật đầu, Lộ Xung đi vào, thời gian không dài, đi ra, hốc mắt ửng đỏ."),t("br"),t("br"),n._v("Cha mẹ, thân nhân năm đó cả nhà bị diệt, hắn bị ép chạy trốn, thi cốt đều không có thu tồn, hôm qua trong đêm đã làm nhiều lần bài vị, cùng các tiền bối đặt ở cùng một chỗ, cũng coi như lá rụng về cội."),t("br"),t("br"),n._v("“Thế gian không như ý chuyện thường tám chín, đi thôi!”"),t("br"),t("br"),n._v("Biết tâm tình của đối phương, biết không có gì tốt khuyên can, Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("Vội vàng tiến về phía trước một bước, Lộ Xung đem trong tay một cái quyển trục đưa tới."),t("br"),t("br"),n._v("Là bản thẻ tre loại thư tịch, vừa nhìn liền biết niên đại xa xưa, cũ kỹ chịu không nổi, có điều, tựa hồ dùng đặc thù nào đó phương pháp luyện chế qua, mặc dù cũ nát, nhưng cứng cỏi không gì sánh được, sẽ không dễ dàng hư mất."),t("br"),t("br"),n._v("Biết đây chính là quyển kia Khổng sư tự tay viết lưu lại bản chép tay, Trương Huyền nhận lấy, tiện tay kéo ra."),t("br"),t("br"),n._v("Nói thật, đối với Khổng sư vị này thầy thiên hạ, hắn vẫn là thật tò mò, mặc dù không ham học sinh đồ vật, nhưng nhìn một chút đối phương chữ viết, hẳn là không cái gì trở ngại!"),t("br"),t("br"),n._v("Bản chép tay mở ra, một cỗ tang thương chi ý, sôi trào mà lên."),t("br"),t("br"),n._v("Thư nửa trước đoạn, chữ viết mặc dù không tệ, cũng không thể cho người ta mang đến khiếp sợ, nửa đoạn sau cứ việc chỉ có vẻn vẹn mấy chữ, nhưng có loại rồng bay phượng múa, cự sơn đè xuống cảm giác."),t("br"),t("br"),n._v("Nội dung rất đơn giản, đích thật là phổ thông thư, hẳn là Lộ Xung Tiên Tổ gửi ra giấy viết thư, Mẫn Thánh vì báo đáp ý cảm kích, xin lão sư tự mình trả lời vài câu, cùng nhau đưa trở về."),t("br"),t("br"),n._v("Cúi đầu nhìn về phía Khổng sư lưu lại chữ viết, Trương Huyền liền cảm thấy một cỗ mênh mông khí tức bay thẳng trong óc, để tâm hắn như mặt nước phẳng lặng tâm cảnh, lập tức mở ra, trở nên như là gương sáng."),t("br"),t("br"),n._v("Sư chữ luyện tâm!"),t("br"),t("br"),n._v("Truyền thuyết, lợi hại danh sư lưu lại chữ viết, ẩn chứa tinh thần của hắn cùng đối với thiên địa cảm ngộ, thường xuyên quan sát, đối với người tâm cảnh tăng lên có cực lớn trợ giúp, cũng chính là cái gọi là sư chữ luyện tâm."),t("br"),t("br"),n._v("Chỉ bất quá, loại này rèn luyện là một loại cộng minh, trừ phi sư mạch truyền thừa, thậm chí huyết mạch quan hệ các loại, người bình thường xem tiền bối lưu lại chữ viết, muốn dẫn phát, tỷ lệ rất thấp, một vạn lần, đều rất khó dẫn động một lần."),t("br"),t("br"),n._v("Trương Huyền cùng Khổng sư, không có sư mạch truyền thừa cùng liên hệ máu mủ, nhưng có thể trực tiếp dẫn động, không thể không nói, vận khí cực giai."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Cầm trong tay giấy viết thư, Trương Huyền chỉ cảm thấy tâm cảnh của mình, càng ngày càng vững chắc, tâm cảnh khắc độ, cũng chậm chạp gia tăng."),t("br"),t("br"),n._v("Cả người giống như là lâm vào đặc thù ý cảnh, ngây người tại chỗ."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Điện hạ, nương nương, bọn hắn thật giống đã cầm tới đồ vật!”"),t("br"),t("br"),n._v("Khe núi phía trên một cái nham thạch to lớn đằng sau, Lương Thanh Mệnh ôm quyền."),t("br"),t("br"),n._v("Làm ra quyết định, Đinh Mục, Lâm Lung đám người liền đuổi đi theo."),t("br"),t("br"),n._v("Mặc dù đánh lén thiên tài nhị tinh danh sư, một khi bị tra ra, có rất lớn nguy hiểm, nhưng chỉ cần thành công, hồi báo lớn hơn."),t("br"),t("br"),n._v("“Làm nhị tinh danh sư, cái này Trương Huyền trên người khẳng định có không ít lão sư hắn ban cho bảo mệnh đồ vật, nhất định phải một kích tất trúng! Không thể cho bất luận cái gì cơ hội thở dốc, nếu không, một khi phát ra cầu cứu tin tức, hoặc là bắt đầu dùng bảo mệnh đồ vật, còn muốn thành công liền khó khăn!”"),t("br"),t("br"),n._v("Đinh Mục sắc mặt nghiêm túc an bài: “Đương nhiên, càng quan trọng hơn là, quyển kia bản chép tay nhất định phải tới tay.”"),t("br"),t("br"),n._v("Đã Dương sư yên tâm để Trương Huyền một mình đi ra, trên người khẳng định có không thiếu bảo mạng đồ vật, muốn động thủ cũng nhanh, không thể có mảy may chần chờ."),t("br"),t("br"),n._v("“Điện hạ yên tâm!” Lương Thanh Mệnh gật đầu."),t("br"),t("br"),n._v("“Động thủ đi!”"),t("br"),t("br"),n._v("Vẻ mặt ngưng tụ, Đinh Mục gật đầu."),t("br"),t("br"),n._v("Nếu như không hạ sát thủ, chỉ đơn thuần cướp đoạt bản chép tay, che giấu cho dù tốt, khẳng định cũng sẽ bị phát hiện, đến lúc đó, toàn bộ Hiên Viên vương quốc, làm không cẩn thận đều sẽ bởi vậy tan vỡ."),t("br"),t("br"),n._v("Đem người chém giết, chỉ cần xử lý thoả đáng, ngụy trang ra bọn hắn bị man thú đánh lén giết chết giả tượng, nghe nhìn lẫn lộn, đợi chân chính phát hiện thời điểm, có lẽ đã nhờ vào Khổng sư tự tay viết bản chép tay tìm được chỗ dựa, không cần lo lắng."),t("br"),t("br"),n._v("Danh Sư đường, mặc dù công chính, cùng chịu tổng bộ khống chế, nhưng vẫn là có phân chia thế lực."),t("br"),t("br"),n._v("Thật giống như Đại tướng nơi biên cương, chịu triều đình khống chế, tỉnh địa vực trong lúc đó, lẫn nhau so sánh, tranh đấu chỗ nào cũng có."),t("br"),t("br"),n._v("Cùng thế lực trong lúc đó Danh Sư đường tranh đấu, chỉ cần không xuất hiện vi phạm nhân loại vấn đề, tổng bộ rất ít hỏi đến."),t("br"),n._v("Có cạnh tranh mới có tiến bộ, mới có thể để cho nhân loại càng thêm vui vẻ phồn vinh, đối Danh Sư đường tới nói, cạnh tranh... Cũng là một loại thúc giục."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nghe được động thủ mệnh lệnh, Lương Thanh Mệnh ánh mắt ngưng trọng, chân khí trong cơ thể vận chuyển, cổ tay khẽ đảo, một thanh trường kiếm xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Thân ảnh chợt lóe, liền theo nham thạch đằng sau xông ra, hóa thành một đạo thiểm điện, thẳng tắp hướng trong đám người Trương Huyền đâm tới."),t("br"),t("br"),n._v("Chí Tôn hậu kỳ thực lực hoàn toàn thi triển ra, thời gian nháy mắt liền thoát ra khoảng cách mấy trăm mét, đi tới trước mặt."),t("br"),t("br"),n._v("Trận pháp bị đình chỉ, khe núi bên trong không có bất kỳ cái gì phòng ngự, bởi vậy, tốc độ rất nhanh, không bị đến mảy may ràng buộc."),t("br"),t("br"),n._v("Trương Huyền lúc này đang rơi vào sư chữ luyện tâm cảnh giới trong, căn bản không có phát hiện."),t("br"),t("br"),n._v("Mạc Vũ, Lộ Xung càng không có nghĩ tới sẽ có người tại lúc này đánh lén."),t("br"),t("br"),n._v("Nhìn thấy huy hoàng không gì sánh được kiếm mang, nghe được trong không khí phá âm thanh âm, tất cả mọi người đều con ngươi co rụt lại, vẻ mặt biến đổi lớn."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("“Trương Huyền!”"),t("br"),t("br"),n._v("“Thiếu gia!”"),t("br"),t("br"),n._v("Tiếng la vang lên, từng cái thân thể run rẩy."),t("br"),t("br"),n._v("“Lão sư, cẩn thận!”"),t("br"),t("br"),n._v("Khoảng cách Trương Huyền gần nhất Lộ Xung nhìn thấy huy hoàng như vậy kiếm mang, biết một khi bị đâm trúng, hẳn phải chết không nghi ngờ, hốc mắt đỏ lên."),t("br"),t("br"),n._v("Vì giúp hắn báo thù, lẻ loi một mình đối kháng Lâm gia, lão sư chưa nói qua nửa câu trách cứ ngôn ngữ; Vì cho hắn tăng thực lực lên, tốn hao vô số đại giới, lão sư không muốn qua bất luận cái gì hồi báo..."),t("br"),t("br"),n._v("Truyền thụ võ kỹ, dạy bảo công pháp!"),t("br"),t("br"),n._v("Chuẩn mực đạo lý làm người, giảng giải tu luyện bản chất!"),t("br"),t("br"),n._v("Không có lão sư, chỉ có thể bình thường tu luyện, cả một đời không có khả năng báo thù!"),t("br"),t("br"),n._v("Không có lão sư, khả năng sớm muộn gì bị Lâm gia phát hiện, sống chết không khỏi bản thân."),t("br"),t("br"),n._v("Là lão sư, để hắn đi ra vũng bùn, là lão sư để hắn có mặt mũi đối chết đi thân nhân..."),t("br"),t("br"),n._v("Đây là hắn kính trọng nhất, kính yêu nhất người."),t("br"),t("br"),n._v("Tuyệt không thể có chuyện xảy ra!"),t("br"),t("br"),n._v("“Dựa theo ngươi dạng này tu luyện, trong vòng mười năm đều không đột phá nổi Ích Huyệt cảnh, đời này có lẽ đều không thể... Báo thù!”"),t("br"),t("br"),n._v("“Ngươi không cần phải để ý đến ta làm sao mà biết được! Cừu hận nén ở trong lòng, có thể là động lực, cũng có thể là ngăn cản. Ngươi bây giờ đem tâm sự ép quá lợi hại, sẽ chỉ làm tu vi đã bị ràng buộc, khó mà đột phá! Ta biết ngươi không nói lời nào, là sợ nhịn không được, nói ra cừu nhân tên, lọt vào trả thù.”"),t("br"),t("br"),n._v("“Yên tâm, chỉ cần ngươi tin tưởng ta, bảo đảm ngươi trong mười ngày, liền có thể báo thù!”"),t("br"),t("br"),n._v("“Có thể tin, cũng có thể không tin, lựa chọn do ngươi! Bất quá... Cơ hội chỉ có một lần!”"),t("br"),t("br"),n._v("“Trong miệng các ngươi thích khách, vừa vặn ta biết, là đệ tử của ta, mà ta... Là lão sư của hắn!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Oanh!"),t("br"),t("br"),n._v("Cùng Trương lão sư tiếp xúc từng màn, trong đầu chảy xuôi, từng câu lời nói trong đầu tiếng vọng, trong nháy mắt trong cơ thể sinh ra vô cùng lực lượng, cả người giống như là thuấn di, thời gian nháy mắt thoát ra mấy mét khoảng cách, ngăn tại Trương Huyền phía trước."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Lương Thanh Mệnh trường kiếm, trùng hợp vào lúc này, đi tới trước mặt, thẳng tắp đâm vào lồng ngực của hắn, Chí Tôn cường giả chân khí, dọc theo thân kiếm, điên cuồng tràn vào thân thể, trong chốc lát, liền đem sinh cơ phá hư."),t("br"),t("br"),n._v("“Ah!”"),t("br"),t("br"),n._v("Cảm nhận được đối phương kiếm khí ở trong cơ thể mình khuấy động, cả người bất cứ lúc nào cũng sẽ bị xé nứt, Lộ Xung gầm lên giận dữ, hai tay hướng về phía trước duỗi ra, đem đối phương trường kiếm gắt gao bóp tại lòng bàn tay."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới tất sát nhất kích, không có gia hỏa này ngăn trở, Lương Thanh Mệnh sầm mặt lại, muốn đâm xuyên đối phương, lại đem Trương Huyền đánh giết, lại phát hiện bị trường kiếm bị hắn hung hăng bắt lấy, giống như là khảm nạm tại trong nham thạch, vô luận đâm vẫn là bạt, đều không nhúc nhích tí nào."),t("br"),t("br"),n._v("“Buông tay...”"),t("br"),t("br"),n._v("Thế nào đều không có nghĩ đến, bị một cái Tông Sư cảnh gia hỏa ngăn trở, Lương Thanh Mệnh vừa vội vừa tức, rít lên một tiếng, chân khí trong cơ thể dọc theo trường kiếm không ngừng hướng đối phương truyền vào, như muốn đi trước chém giết, lại đối Trương Huyền động thủ."),t("br"),t("br"),n._v("“Ngươi không thể thương tổn hắn, bởi vì...”"),t("br"),t("br"),n._v("Cảm nhận được trên người sinh cơ tại đối phương chân khí cuồng lao xuống, dần dần tiêu tán, Lộ Xung không có chút nào hối hận, ngược lại ánh mắt ngưng trọng, mang theo ai cũng không cách nào dao động kiên định."),t("br"),t("br"),n._v("“Hắn là thầy ta!”"),t("br"),t("br"),n._v("(Quyển thứ tư,  sắp mở ra. Hắn là thầy ta, cái này Quyển lão nhai muốn giảng giải một đoạn thầy trò tình, một đoạn lão sư cùng học sinh tôn trọng lẫn nhau, lẫn nhau khâm phục cố sự, may mắn, không có viết lại."),t("br"),t("br"),n._v("Đương nhiên, quyển này viết đến cuối cùng, bởi vì chính mình nguyên nhân, trong nhà cùng đơn vị sự tình tương đối nhiều, khả năng viết không hài lòng lắm, chỉnh thể tới nói, vẫn là đem ý cảnh ý vị viết ra. Bởi vì lão nhai là lão sư, cảm xúc khá nhiều, lão sư không phải một mực trả giá, học sinh cũng không phải một mực tìm lấy, song phương muốn lẫn nhau tôn trọng, tín nhiệm lẫn nhau, lẫn nhau là đối phương cân nhắc, mới có thể đi càng xa."),t("br"),t("br"),n._v("Về phần Lộ Xung nhân vật này, theo quyển này tên định ra, liền đã định trước thành sau cùng trở nên gay gắt điểm. Đương nhiên, mọi người yên tâm, quyển sách này không phải bi kịch, theo qua vô tận đan điền người biết, lão nhai không thích bi kịch, cũng sẽ không viết. Còn quyển kế tiếp vô địch, là Trương Huyền tâm tính sẽ triệt để thuế biến, sớm báo trước thoáng cái, đại náo Thú đường, xông Danh Sư đường, diệt Hiên Viên vương quốc, một tay ép hai mươi bảy nước... Cố sự sẽ càng thêm đặc sắc, hi vọng mọi người có thể chính bản đặt mua ủng hộ, thuận tiện ném nguyệt phiếu, để quyển sách thành tích càng ngày càng tốt, lão nhai cảm tạ!!!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-419"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-421"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);