(window.webpackJsonp=window.webpackJsonp||[]).push([[1656],{2009:function(n,h,t){"use strict";t.r(h);var c=t(44),g=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-412-bao-thu-cho-han-rua-han"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-412-bao-thu-cho-han-rua-han"}},[n._v("#")]),n._v(" Chương 412: Báo thù cho hắn rửa hận!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Lộ Xung!”"),t("br"),t("br"),n._v("Đang chìm ngâm ở sư chữ luyện tâm bên trong, nghe được một tiếng nóng nảy la lên, ngay sau đó liền thấy Lộ Xung bị một kiếm đâm xuyên, Trương Huyền đầu óc thoáng cái nổ tung, hốc mắt nhịn không được đỏ lên."),t("br"),t("br"),n._v("Người học sinh này, mặc dù nhận biết trễ nhất, nhưng nắm giữ đối với hắn cao nhất tín nhiệm, chân thật nhất tình cảm."),t("br"),t("br"),n._v("“Chết đi cho ta!”"),t("br"),t("br"),n._v("Rít lên một tiếng, trong đầu chấn động, một bản màu vàng kim thư tịch đột ngột xuất hiện."),t("br"),t("br"),n._v("Tựa như để thế giới sụp đổ lực lượng đột nhiên sinh ra, mây gió đất trời tụ hội, thư tịch dày nặng như là một tòa núi lớn, trực tiếp rơi vào Lương thống lĩnh trên đầu."),t("br"),t("br"),n._v("Choảng!"),t("br"),t("br"),n._v("Không có chút nào đình trệ, đầu liền biến thành thiết chùy ở dưới dưa hấu, nổ vỡ nát, kịch liệt trùng kích vào, toàn bộ khe núi xuất hiện một cái sâu vài trăm mét, chiều rộng vài trăm mét hố to."),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("Trốn ở một bên, chuẩn bị xuất thủ lần nữa đánh lén Đinh Mục, Lâm Lung đám người, không nghĩ tới đường đường Chí Tôn hậu kỳ Lương thống lĩnh, bị trên người đối phương toát ra đồ vật, thoáng cái đập cho hôi phi yên diệt, dọa đến vẻ mặt đồng thời tái đi, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Sớm đoán ra vị này Trương Huyền trong tay tất nhiên có Dương sư ban cho hộ thân pháp bảo, không nghĩ tới đáng sợ như thế!"),t("br"),t("br"),n._v("Thoáng cái đập ra lớn như thế hố, liền xem như Hóa Phàm cảnh cường giả, cũng làm không được."),t("br"),t("br"),n._v("“Trốn...”"),t("br"),t("br"),n._v("Biết ngăn cản được không ở, không dám dừng lại nghỉ, Đinh Mục ai cũng không để ý tới, xoay người bỏ chạy."),t("br"),t("br"),n._v("Tuần thú sư Chu Tiến, cũng theo sát lấy đuổi theo."),t("br"),t("br"),n._v("Hai người đều đạt đến cấp Chí Tôn đừng, tốc độ cực nhanh, hai cái hô hấp công phu liền biến thành một tia trắng, chạy ra hơn trăm mét khoảng cách."),t("br"),t("br"),n._v("“Điện hạ chờ ta...”"),t("br"),t("br"),n._v("Không nghĩ tới phu quân của mình, phía sau lớn nhất dựa dẫm, xoay người chạy, nói liên tục đều không có nói, Lâm Lung vẻ mặt trắng bệch, vội vàng đuổi theo."),t("br"),t("br"),n._v("“Chết...”"),t("br"),t("br"),n._v("Ánh mắt đỏ như máu, xem có người theo nham thạch phía sau chạy ra, chỗ nào không biết là bọn hắn quỷ kế, Trương Huyền thật dài gào thét, xoay quanh không trung màu vàng kim thư tịch, lần nữa đập tới."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Biến cố lớn, phương viên vài trăm mét lần nữa bị san bằng, Lâm Lung bị đập ở phía dưới, biến thành thịt vụn, Đinh Mục, Chu Tiến trốn tương đối nhanh, lúc này mới may mắn thoát khỏi tại khó."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("“Nhanh lên man thú, nhanh!”"),t("br"),t("br"),n._v("Nhìn thấy sau lưng cảnh tượng, hai người dọa đến toàn thân run rẩy, cái gì thái tử phi, cái gì người yêu, lại không quản không để ý, thả người nhảy một cái, rơi vào Thiểm Điện Kim Lân Thú lưng, thẳng tắp bay tới đằng trước."),t("br"),t("br"),n._v("Trương Huyền món pháp bảo này quá mạnh, Hóa Phàm cảnh cường giả, đều có thể nhẹ nhõm đập chết, chớ nói chi là bọn hắn."),t("br"),t("br"),n._v("Không trốn nữa đi, chỉ có một con đường chết."),t("br"),t("br"),n._v("“Chết!”"),t("br"),t("br"),n._v("Tiếng gầm gừ trong, Trương Huyền đang muốn khống chế màu vàng kim thư tịch, đem hai người này một thú cũng tại chỗ giết chết, liền nghe “Ông!” Một tiếng, thư tịch biến mất không thấy gì nữa, ngay sau đó một cỗ to lớn rã rời chi ý dâng lên, để đầu óc hắn u ám, bất cứ lúc nào cũng sẽ ngất đi."),t("br"),t("br"),n._v("“Lộ Xung...”"),t("br"),t("br"),n._v("Biết Chí Tôn trung kỳ man thú phi hành, rốt cuộc đuổi không kịp, Trương Huyền cố nén trong đầu mê muội, nhìn về phía trước mắt người học sinh này."),t("br"),t("br"),n._v("Chỉ gặp này vị diện cho kiên nghị, vì báo thù, cố nén hai năm không nói lời nào, vì tăng thực lực lên, phục dụng độc dược cửu tử nhất sinh đều không oán không hối học sinh, con mắt đóng chặt, đã không còn tri giác."),t("br"),t("br"),n._v("Bất quá, tựa hồ thấy được lão sư an toàn, cứ việc lại không cách nào mở to mắt, khóe miệng nhưng mang theo mỉm cười."),t("br"),t("br"),n._v("Hai tay vẫn như cũ gắt gao nắm đâm vào ngực trường kiếm, chết sống đều không buông tay."),t("br"),t("br"),n._v("Tựa như là đang nói, dù là chết..."),t("br"),t("br"),n._v("Lão sư chỉ cần không có việc gì... Liền tốt!"),t("br"),t("br"),n._v("“Thiên Đạo chân khí có thể trị hết thảy thương thế, ngươi không có việc gì...”"),t("br"),t("br"),n._v("Nước mắt chảy ra đến, nắm chặt bàn tay của đối phương, Trương Huyền trong cơ thể Thiên Đạo chân khí, điên cuồng tràn vào trong đó."),t("br"),t("br"),n._v("“Lão, lão sư làm sao biết ta muốn báo thù?”"),t("br"),t("br"),n._v("“Còn xin Liễu lão sư dạy ta...”"),t("br"),t("br"),n._v("“Ngươi không thể gây tổn thương cho hắn, bởi vì... Hắn là thầy ta!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hồi ức cùng thiếu niên từng li từng tí, Trương Huyền theo đáy lòng run rẩy."),t("br"),t("br"),n._v("Hai năm “Câm điếc” trải qua, Lộ Xung chưa từng nhiều lời, thời gian chung đụng bên trong không có vài câu ngôn ngữ, có chỉ là ánh mắt kiên định, trầm ổn bóng lưng..."),t("br"),t("br"),n._v("Vì có thể báo thù, quỳ gối học viện bảy ngày bảy đêm, không có đôi câu vài lời; Rèn luyện độc thể, lại thống khổ cũng không gọi khổ một tiếng, không chút nào lui; Biết rõ Lâm gia cường thế, vẫn như cũ tiến lên..."),t("br"),t("br"),n._v("Cái này không muốn nói lời nói, nhưng kiên trì không ngừng thiếu niên, cho người ta cảm giác băng lãnh, kỳ thật nội tâm, lại là yếu đuối nhất."),t("br"),t("br"),n._v("Nếu không, cũng không có khả năng ngăn tại trước mặt, tình nguyện bản thân chết đi, cũng không muốn lão sư đã bị bất cứ thương tổn gì!"),t("br"),t("br"),n._v("Lộ Xung, học sinh của ta, là lão sư không có bảo vệ tốt ngươi!"),t("br"),t("br"),n._v("Ngươi mau tỉnh lại, cho lão sư một cái lấy cơ hội..."),t("br"),t("br"),n._v("Thiên Đạo chân khí không cần tiền điên cuồng truyền vào, nhưng mà Lộ Xung gầy yếu cương nghị thân ảnh, nhưng không nhúc nhích, không có phản ứng chút nào."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nhìn thấy thanh niên trước mắt, như là điên, Mạc Vũ sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Tình cảnh vừa nãy, quá mức cấp tốc, mặc dù nhìn ở trong mắt, nhưng không có biện pháp gì, vốn cho rằng, lần này khẳng định dữ nhiều lành ít."),t("br"),t("br"),n._v("Thế nào cũng không nghĩ đến, một cái học sinh lại vì hắn, cam nguyện đánh đổi mạng sống."),t("br"),t("br"),n._v("Tú quyền xiết chặt, vội vàng hướng về phía trước."),t("br"),t("br"),n._v("“Trương Huyền!”"),t("br"),t("br"),n._v("Hô một tiếng, không có phản ứng chút nào."),t("br"),t("br"),n._v("“Trương Huyền...”"),t("br"),t("br"),n._v("Lần nữa hô một tiếng, mới nhìn đến Thiên Vũ vương thành chói mắt nhất thiên tài, ánh mắt mờ mịt ngẩng đầu, tựa như mất hồn phách."),t("br"),t("br"),n._v("“Đừng có gấp, y thuật của ngươi vô song, có lẽ... Lộ Xung sẽ không có việc gì!”"),t("br"),t("br"),n._v("Trong lòng một hồi quặn đau, Mạc Vũ nhịn không được nói."),t("br"),n._v("“Đúng, ta có y thuật...”"),t("br"),t("br"),n._v("Đối phương nhắc nhở, Trương Huyền lúc này mới kịp phản ứng, mờ mịt ánh mắt một lần nữa tập trung."),t("br"),t("br"),n._v("Không tệ!"),t("br"),t("br"),n._v("Hắn không chỉ có y thuật, còn có Thiên Đạo thư viện!"),t("br"),t("br"),n._v("Thiên Đạo bên dưới, không chỗ che thân, chỉ cần không phải phấn thân toái cốt, hẳn là đều có biện pháp!"),t("br"),t("br"),n._v("Ánh mắt sáng lên, một lần nữa hội tụ thần thái."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Trong lòng thấp giọng hô, trong đầu xuất hiện một quyển sách, vội vàng lật ra."),t("br"),t("br"),n._v("“Còn có thể cứu...”"),t("br"),t("br"),n._v("Nhìn thoáng qua nội dung phía trên, toàn thân run lên, Trương Huyền vẻ mặt thoáng cái trở nên thấu đỏ."),t("br"),t("br"),n._v("Vốn cho rằng bị Chí Tôn cường giả trường kiếm xuyên ngực, hẳn phải chết không nghi ngờ, không nghĩ tới, thế mà còn có một chút khí tức."),t("br"),t("br"),n._v("Thư tịch phía trên lít nha lít nhít viết đầy các loại thiếu hụt, Lộ Xung kinh mạch trong cơ thể, cơ hồ bị Chí Tôn cường giả chân khí, toàn bộ làm vỡ nát, nội tạng cũng nhận trọng thương, đổi lại bất luận kẻ nào, loại tình huống này, khẳng định đã chết."),t("br"),t("br"),n._v("Bất quá, tu luyện qua Thiên Đạo Kim Thân cùng độc thể, thân thể hơn xa thường nhân mạnh mẽ mấy lần, lại thêm Thiên Đạo chân khí truyền vào kịp thời, lúc này mới bảo lưu lại một tia sinh cơ."),t("br"),t("br"),n._v("“Trước đem kinh mạch, nội tạng chữa trị!”"),t("br"),t("br"),n._v("Đem Y Sư công hội tất cả thư tịch dung nhập trong óc, lĩnh ngộ lý giải, coi như không cần thư viện, Trương Huyền y thuật cũng không dưới tại tam tinh y sư, biết tình huống hiện tại, là trước chữa trị thân thể đối phương thương thế."),t("br"),t("br"),n._v("Ngay sau đó không tại nói nhảm, Thiên Đạo chân khí chậm rãi tại đối phương trong cơ thể chảy xuôi, tu bổ trong đó tổn hại kinh mạch cùng thân thể."),t("br"),t("br"),n._v("Đồng thời lấy ra một viên chữa thương đan dược cho ăn đi xuống."),t("br"),t("br"),n._v("Vơ vét xong Lâm gia, loại này chữa thương dược vật, trữ vật giới chỉ bên trong có rất nhiều, đừng nói một viên, coi như toàn bộ lấy ra, chỉ cần có thể đánh thức đối phương, cũng không tính là cái gì."),t("br"),t("br"),n._v("“Trương Huyền...”"),t("br"),t("br"),n._v("Gặp thanh niên lần nữa khôi phục thần thái, bắt đầu cứu chữa, Mạc Vũ lúc này mới thở phào nhẹ nhõm, cúi đầu nhìn lại, con ngươi co rụt lại."),t("br"),t("br"),n._v("Chỉ gặp Lộ Xung vết thương, tại đối phương chân khí truyền vào xuống, mắt trần có thể thấy khôi phục, trước đó mất máu quá nhiều sắc mặt, cũng hồng nhuận, mặc dù vẫn như cũ ngủ mê mệt, ở bề ngoài xem, đã theo không bị tổn thương trước đó, không có khác nhau quá nhiều."),t("br"),t("br"),n._v("“Chỉ bằng vào chân khí, triệt để chữa trị thân thể?”"),t("br"),t("br"),n._v("Thân thể run rẩy, Mạc Vũ khó mà tin được."),t("br"),t("br"),n._v("Trước đó mặc dù đã gặp trước mắt vị này thi triển qua y thuật, nhưng cơ hồ đều là ngân châm đâm huyệt, sau đó mở ra đan phương, phương thuốc."),t("br"),t("br"),n._v("Như vậy liền có thể chữa trị thương thế, tu bổ kinh mạch... Chân khí nên tinh khiết đến mức nào?"),t("br"),t("br"),n._v("“Chẳng lẽ là... Thượng tam phẩm chân khí?”"),t("br"),t("br"),n._v("Con ngươi co rụt lại."),t("br"),t("br"),n._v("Bên trên tam phẩm chân khí, tinh thuần đến cực điểm, có thể chữa thương, khơi thông trong kinh mạch ràng buộc... Vốn cho rằng chỉ là trong truyền thuyết, không nghĩ tới thế mà có thể tận mắt nhìn đến."),t("br"),t("br"),n._v("Có thể truyền thụ tu luyện ra loại này chân khí công pháp, Dương sư lại nên mạnh cỡ nào?"),t("br"),t("br"),n._v("Khó trách có thể để cho Tô sư, Lăng sư đều cam tâm tình nguyện vì hắn ra tay!"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Đem Lộ Xung trên người cuối cùng một đạo kinh mạch chữa trị hoàn chỉnh, Trương Huyền lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Trong óc lần nữa hình thành mới thư tịch, nhìn thoáng qua, sắc mặt tái xanh."),t("br"),t("br"),n._v("“Lão sư, Lộ Xung thế nào?” Triệu Nhã nhịn không được nhìn qua."),t("br"),t("br"),n._v("“Thân thể không có vấn đề quá lớn, liền là ý thức lâm vào ngủ say, không biết lúc nào có thể tỉnh lại!”"),t("br"),t("br"),n._v("Lộ Xung đã bị tổn thương thực sự quá lợi hại, coi như Thiên Đạo chân khí uy lực vô song, có thể đem trên thân thể thương thế, hoàn toàn khôi phục, nhưng ý thức bên trên nhưng bất lực."),t("br"),t("br"),n._v("Nói cách khác, hắn hiện tại, cùng người thực vật đồng dạng, có khả năng ngày mai liền tỉnh, cũng có khả năng mãi mãi cũng vẫn chưa tỉnh lại."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ?” Triệu Nhã hỏi tiếp."),t("br"),t("br"),n._v("“Ta cũng không biết!”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền lần nữa lộ ra vẻ cô đơn."),t("br"),t("br"),n._v("Mặc dù đem Y Sư công hội tất cả thư tịch, đều biến thành kiến thức của mình, nhưng loại tình huống này, cũng nghĩ không ra tốt phương pháp giải quyết."),t("br"),t("br"),n._v("Kinh mạch tắc nghẽn, thân hoạn tàn tật, không cách nào đột phá... Thiên Đạo chân khí toàn năng trị liệu, vừa ý biết, thuộc về linh hồn, huyền chi lại huyền, Thiên Đạo chân khí lại nghịch thiên, lại có thể giúp đỡ được gì?"),t("br"),t("br"),n._v("“Chúng ta hết cách rồi, có lẽ Dương sư có thể trị!” Mạc Vũ nhịn không được chen vào nói."),t("br"),t("br"),n._v("“Dương sư?”"),t("br"),t("br"),n._v("Trương Huyền vẻ mặt khó coi."),t("br"),t("br"),n._v("Dương sư... Là giả, không tồn tại."),t("br"),t("br"),n._v("Hắn liền là vị này “Cao nhân”, bản thân hết cách rồi, chẳng khác nào đối phương cũng không có cách nào."),t("br"),t("br"),n._v("“Ta nghe nói thất phẩm trở lên danh sư, không chỉ có thể dùng ngôn ngữ cải biến người hành vi cử chỉ, còn có thể ảnh hưởng ý thức, lục soát linh hồn. Lộ Xung chỉ là rơi vào trạng thái ngủ say, nếu có loại cấp bậc này danh sư, tiến vào thức hải của hắn, có lẽ liền có thể đem hắn đánh thức!”"),t("br"),t("br"),n._v("Mạc Vũ nói tiếp."),t("br"),t("br"),n._v("“Ảnh hưởng ý thức, lục soát linh hồn?” Trương Huyền nắm đấm xiết chặt."),t("br"),t("br"),n._v("Cùng đối phương nói đồng dạng, Lộ Xung chỉ là ý thức ngủ say, thực sự có người có thể đi vào thức hải của hắn, có lẽ thật có thể đánh thức."),t("br"),t("br"),n._v("“Ừm, mặc dù đây là nghe người khác nói, nhưng danh sư cấp bậc càng cao, thủ đoạn càng nhiều, chúng ta nghĩ không ra biện pháp, chưa hẳn cấp bậc cao hơn không có...”"),t("br"),t("br"),n._v("Mạc Vũ nói."),t("br"),t("br"),n._v("“Không tệ!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Thật giống như của hắn Tiên Thiên thai độc, Thiên Huyền vương thành cùng Độc điện không có phương pháp giải quyết, Danh Sư đường liền có, mặc dù cũng rất khó, ít nhất chờ tại có phương hướng."),t("br"),t("br"),n._v("Lộ Xung tình huống hiện tại, bất lực, nhưng nương theo cấp bậc cao, xem sách càng nhiều, có lẽ liền có tốt hơn phương pháp."),t("br"),t("br"),n._v("“Ta sẽ đi tìm lợi hại danh sư, có điều, trước đó...”"),t("br"),t("br"),n._v("Nghĩ đến cái này, an tâm không ít, Trương Huyền híp mắt lại, một cỗ sát ý theo trong cơ thể sôi trào mà lên, tựa như sóng lớn, đem thiên địa đều bao phủ."),t("br"),t("br"),n._v("“Muốn trước thay hắn... Báo thù rửa hận!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-420"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-422"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);