(window.webpackJsonp=window.webpackJsonp||[]).push([[166],{519:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1126-đai-tinh-la-chi-luc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1126-đai-tinh-la-chi-luc"}},[n._v("#")]),n._v(" Chương 1126: Đại Tinh La Chỉ Lực")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1126: Đại Tinh La Chỉ Lực"),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Ngụy điện chủ cùng Tiêu Bột ngốc tại chỗ, hai người đồng thời sửng sốt."),t("br"),t("br"),n._v("Liền đi nội tức thông đạo một hồi, làm sao... Nội Tức điện nhiều như vậy Chiến sư, đều thành đối phương học sinh?"),t("br"),t("br"),n._v("Cái này sư bái quá nhanh đi!"),t("br"),t("br"),n._v("Nhất là Ngụy điện chủ, không hiểu ra sao, mặt mũi không rõ ràng cho lắm."),t("br"),t("br"),n._v("Chiến sư đường mệnh lệnh, mặc dù nhất định phải tuân thủ, không thể vi phạm, nhưng điện chủ, đường chủ cùng bình thường Chiến sư tầm đó, thân như huynh đệ, không có quá tận lực sâm nghiêm đẳng cấp."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, bất kể đá hạo vẫn là Tiêu Bột, nói với hắn lời nói, đều rất tự do, không có bởi vì hắn là điện chủ, liền tận lực phụ họa."),t("br"),t("br"),n._v("Tất cả mọi người là huynh đệ... Đột nhiên toát ra một cái lão sư..."),t("br"),t("br"),n._v("Mới vừa còn muốn lấy muốn đem đối phương thu đến Nội Tức điện, kết quả nhân gia liền thành mọi người lão sư... Cái này gọi chuyện gì!"),t("br"),t("br"),n._v("Đang muốn có phải hay không vị này Tôn sư, cho mọi người xuống cái gì mê hồn dược, liền nghe đến trạm tại phía trước thanh niên, lắc đầu."),t("br"),t("br"),n._v("“Đứng lên đi! Con đường tu luyện, cũng không phải là xa rời thực tế, quý ở trao đổi lẫn nhau, về sau mọi người giao lưu là được, không cần xưng hô lão sư!”"),t("br"),t("br"),n._v("Nhưng mà tùy tiện giảng giải, không nghĩ tới những người này lại đột nhiên bái sư, Trương Huyền tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v("“Tôn sư giảng giải đâu ra đó, nhắm thẳng vào tu luyện hạch tâm, chúng ta được lợi rất nhiều, xưng hô một tiếng lão sư, cũng không đủ, trái lại còn kiếm lời đại tiện nghi!” Đá hạo nói."),t("br"),t("br"),n._v("Hắn là Chiến sư đường, nổi danh thiên tài, kiêu ngạo đến cực điểm."),t("br"),t("br"),n._v("Dựa theo tình huống bình thường, liền xem như điện chủ, đều chưa hẳn có thể để cho hắn vui lòng phục tùng, có thể đối trước mắt vị này, nhưng từ đáy lòng bái phục, không có chút nào bất kính."),t("br"),t("br"),n._v("Không riêng gì đối phương chỉ ra hắn trong vấn đề tu luyện cùng mấu chốt, càng quan trọng hơn là, vừa rồi giảng giải, tựa như tiếng trời, chỉ phải thật tốt tiêu hóa, chân khí trong cơ thể số lượng dự trữ, tất nhiên có thể tiến thêm một bước, đi đến không thể tưởng tượng tình trạng."),t("br"),t("br"),n._v("Cao thâm như vậy phương pháp tu luyện, không giữ lại chút nào giảng giải đi ra, không có chút nào che lấp cùng bảo lưu..."),t("br"),t("br"),n._v("Chỉ phần này tâm cảnh, đã làm cho xưng hô một tiếng lão sư, chân tâm thật ý, không có nửa phần trái lương tâm."),t("br"),t("br"),n._v("“Đúng vậy a, Tôn sư, chúng ta chân tâm thật ý gọi ngươi là lão sư, còn xin đừng nên chối từ!”"),t("br"),t("br"),n._v("“Có lão sư giảng giải phương pháp, thực lực chúng ta tuyệt đối sẽ trong thời gian ngắn tiến nhanh, thông qua khảo hạch, dễ như trở bàn tay!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Những người khác cũng vội vàng nói."),t("br"),t("br"),n._v("“Cái này...” Ngụy điện chủ lần nữa ngây người."),t("br"),t("br"),n._v("Mới vừa tưởng rằng không phải Tôn sư, dùng cái gì người không nhận ra thủ đoạn, mời chào Nội Tức điện Chiến sư, liền thấy cảnh này, hiện tại xem ra, là bọn họ Chiến sư thật tâm thật ý học tập, hơn nữa còn chiếm được chỗ tốt."),t("br"),t("br"),n._v("Liền đi thông đạo một hồi... Đến cùng giảng giải cái gì, để nhiều người như vậy như thế bội phục?"),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Thấy mọi người thái độ không thể nghi ngờ, Trương Huyền mặc dù không nguyện ý, vẫn gật đầu."),t("br"),t("br"),n._v("Cùng hắn nói đồng dạng, giảng giải nhiều như vậy, chỉ là vì giao lưu, cũng không phải là muốn nhận đồ, không nghĩ tới nhiều người như vậy ngang xưng lão sư, đến để hắn không biết như thế nào cho phải."),t("br"),t("br"),n._v("Quá ưu tú, kỳ thật cũng thật phiền toái, đi đến cái nào người khác đều bái sư, cũng là một loại phiền não ah."),t("br"),t("br"),n._v("“Ngụy điện chủ, ngươi trở về... Chúng ta đi Võ Kỹ điện đi!”"),t("br"),t("br"),n._v("Chính không biết như thế nào cho phải, nhìn thấy Ngụy điện chủ đi tới, vội vàng hô một tiếng."),t("br"),t("br"),n._v("“Được...”"),t("br"),t("br"),n._v("Biết đối phương bắt hắn chống lại rất nhiều Chiến sư niềm nở, Ngụy điện chủ chỉ thật nhẹ gật đầu, không nói thêm lời, dẫn đường hướng Võ Kỹ điện phương hướng đi đến."),t("br"),t("br"),n._v("Võ Kỹ điện, cùng Nội Tức điện cách nhau cũng không xa, đi một hồi, liền đến đến trong đó."),t("br"),t("br"),n._v("“Phong điện chủ là hảo hữu của ta, hai ta giao nhau vượt qua hơn ba trăm năm, nhưng mà nhìn một chút tu luyện công pháp, ta chỉ cần mở miệng, hắn khẳng định sẽ đáp ứng!”"),t("br"),t("br"),n._v("Đi ở đại sảnh, Ngụy điện chủ cười nói."),t("br"),t("br"),n._v("Phong điện chủ từng là Thanh Nguyên đế quốc Danh Sư đường rất danh khí rất lớn danh sư, tinh thông đủ loại võ kỹ, chiến lực kinh người, lúc này mới bị hình đường chủ chuyên môn mời mời tới, chỉ điểm Võ Kỹ điện rất nhiều Chiến sư, thành đường chủ."),t("br"),t("br"),n._v("Hai người rất nhiều năm trước liền quen thuộc, đồng thời thành bằng hữu, hết sức quen thuộc."),t("br"),t("br"),n._v("“Bẩm báo Ngụy điện chủ, chúng ta điện chủ ra ngoài đến bây giờ cũng chưa trở lại...”"),t("br"),t("br"),n._v("Một cái Chiến sư tiến lên đón."),t("br"),t("br"),n._v("“Chưa về?” Ngụy điện chủ nhíu nhíu mày."),t("br"),t("br"),n._v("Chiến sư mặt mũi áy náy."),t("br"),t("br"),n._v("“Chờ hắn trở về, cùng nói một tiếng, ta tới đi tìm...” Đối phương vắng mặt, cũng hết cách rồi, Ngụy điện chủ đành phải lắc đầu."),t("br"),t("br"),n._v("“Vâng!” Chiến sư gật đầu, cười cười: “Kỳ thật, Ngụy điện chủ không cần tìm tới Phong điện chủ, điện chủ quả thực sưu tập không ít võ kỹ cùng công pháp, có điều, cũng không để ở trên người, mà là đặt ở võ kỹ thông đạo, chỉ cần có thể khiêu chiến thành công, liền có thể tiến vào bên trong, muốn nhìn bao lâu liền nhìn bao lâu, tùy tiện sao chép đều vô sự!”"),t("br"),t("br"),n._v("“Khiêu chiến?” Ngụy điện chủ sững sờ."),t("br"),t("br"),n._v("Hắn rất ít đến Võ Kỹ điện, rất nhiều chuyện cũng không biết rõ tình hình."),t("br"),t("br"),n._v("“Đúng, Võ Kỹ điện có mấy trăm loại võ kỹ công pháp, có thể tùy tiện tuyển lấy học tập, chỉ cần có thể dùng tương đồng võ kỹ, vượt qua điện chủ lưu lại đến khôi lỗi, coi như thành công!” Chiến sư giải thích."),t("br"),t("br"),n._v("Ngụy điện chủ hiểu được."),t("br"),t("br"),n._v("Kỳ thật cũng là đối chiến sư một loại khảo hạch."),t("br"),t("br"),n._v("Cùng bọn hắn Nội Tức điện nội tức thông đạo có chút tương tự."),t("br"),t("br"),n._v("Chỉ có một loại võ kỹ, tu luyện tới cảnh giới nhất định, mới có thể học tập tân tuyệt chiêu, nếu không, tham thì thâm, học nhiều hơn nữa, cũng vô dụng."),t("br"),t("br"),n._v("“Tôn sư...” Rõ ràng chuyện gì xảy ra, nhìn về phía cách đó không xa Trương Huyền."),t("br"),n._v("“Ta có thể qua đi thử xem...” Trương Huyền cười cười."),t("br"),t("br"),n._v("Đã thông qua khảo hạch liền có thể nhìn thấy thư tịch, cũng không cần phải rơi một cái nhân tình."),t("br"),t("br"),n._v("“Vậy thì tốt, ta mang các ngươi đi qua đi!”"),t("br"),t("br"),n._v("Chiến sư cười cười, đi đầu dẫn đường."),t("br"),t("br"),n._v("Không lâu sau, mọi người đi tới một cái rộng lớn đại sảnh, cùng trước đó nội tức thông đạo đồng dạng, chất đầy Chiến sư, nguyên một đám mang trên mặt vẻ mặt ngưng trọng."),t("br"),t("br"),n._v("“Đây chính là võ kỹ thông đạo, phía trước trên vách tường, là võ kỹ danh xưng, chỉ cần đem bên trong bất kỳ một cái nào, tu luyện tới đại thành tình trạng, liền có thể đi vào khiêu chiến Phong điện chủ lưu lại khôi lỗi! Sau đó tiến vào Tàng Thư khố, lựa chọn càng nhiều bí tịch... Tôn sư, ngươi xem một chút nhưng có quen thuộc võ kỹ? Nếu như mà có, ta có thể hiện tại cho ngươi báo danh!”"),t("br"),t("br"),n._v("Chiến sư nhìn qua."),t("br"),t("br"),n._v("Thông qua trò chuyện, hắn cũng biết, cũng không phải là Ngụy điện chủ muốn nhìn thư, mà là trước mắt vị này."),t("br"),t("br"),n._v("Có thể để cho một cái điện chủ, tự mình đi cùng, hắn đối trước mắt vị này, tràn ngập tò mò."),t("br"),t("br"),n._v("“Ta nhìn một chút!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu, hướng cách đó không xa mặt tường nhìn sang."),t("br"),t("br"),n._v("Phía trên lít nha lít nhít viết đầy võ kỹ danh xưng, 《 Thiên Diệp chưởng 》, 《 Hồng Trần Nhất Tuyến quyền 》, 《 Tinh Thần chỉ 》..."),t("br"),t("br"),n._v("Chủng loại phong phú, cân nhắc một chút, chừng hai trăm loại nhiều!"),t("br"),t("br"),n._v("“Cái này... Nhiều như vậy?”"),t("br"),t("br"),n._v("Trương Huyền khiếp sợ."),t("br"),t("br"),n._v("“Chúng ta điện chủ, thích nhất sưu tập những này, cái này bên trong bao hàm, Thanh Nguyên đế quốc Thánh phẩm cấp bậc tất cả võ kỹ, Tôn sư nhưng có am hiểu?” Chiến sư cười nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền thoáng có chút xấu hổ: “Những vũ kỹ này, ta đều là lần đầu tiên nghe được... Còn không có tu luyện qua bất kỳ một cái nào...”"),t("br"),t("br"),n._v("Những vũ kỹ này, đều là Chiến sư đường, thậm chí Thanh Nguyên đế quốc đặc hữu, hắn mới đến đây bên trong, không đến một ngày thời gian, nói thật, cái này hơn hai trăm tên, đều là lần đầu tiên nghe nói, đừng nói tu luyện."),t("br"),t("br"),n._v("Chiến sư sửng sốt một chút: “Những này đại bộ phận đều là bình thường nhất thánh phẩm võ kỹ, Tôn sư... Thân là Thánh vực nhất trọng đỉnh phong, một loại đều... Chưa từng luyện?”"),t("br"),t("br"),n._v("Hai trăm loại võ kỹ, có một trăm tám mươi loại là hết sức bình thường, muốn học được không khó, một cái đều không có học qua... Cái này điểm xuất phát phải có bao nhiêu thấp ah!"),t("br"),t("br"),n._v("“Ta có học qua một loại Thánh phẩm cấp bậc võ kỹ, có điều, không tại đây hai trăm cái bên trong...”"),t("br"),t("br"),n._v("Trương Huyền cười khổ."),t("br"),t("br"),n._v("Hắn biết võ kỹ, đều là lúc trước học, hiện nay còn không có đi đến Thánh phẩm cấp bậc, một cái duy nhất đại bi Thiên Ma chưởng, cũng chỉ học được thời gian rất ngắn, về sau cảm thấy thiếu hụt quá nhiều, liền không tu luyện thế nào."),t("br"),t("br"),n._v("“Chỉ học được một loại? Còn không tại đây bên trong?”"),t("br"),t("br"),n._v("Chiến sư một mặt đồng tình nhìn qua."),t("br"),t("br"),n._v("Còn tưởng rằng là đại gia tộc nào tới thiếu niên thiên tài, không nghĩ tới là cái không thấy qua việc đời gia hỏa."),t("br"),t("br"),n._v("Võ Kỹ điện người, không tu luyện qua ba năm bộ võ kỹ, đều không có ý tứ cùng người khác mở miệng nói mình biết võ kỹ..."),t("br"),t("br"),n._v("“Đúng vậy a!” Trương Huyền suy nghĩ một chút, nói: “Nếu không như vậy đi, ngươi nơi này nhưng có Thánh phẩm cấp bậc võ kỹ? Cho ta xem một chút, ta học một chút, thử một chút có thể hay không xông một chút thông đạo...”"),t("br"),t("br"),n._v("Không học qua phía trên ghi chú võ kỹ, chỉ có thể tùy tiện tìm một bản, vừa học vừa làm."),t("br"),t("br"),n._v("“Học một chút? Xông thông đạo?”"),t("br"),t("br"),n._v("Chiến sư sửng sốt một chút, suýt chút nữa không bật cười."),t("br"),t("br"),n._v("Nói thật nếu không phải Ngụy điện chủ mang tới, chỉ sợ hắn đã sớm mở miệng chửi má nó."),t("br"),t("br"),n._v("Thánh phẩm cấp bậc võ kỹ, tại Võ Kỹ điện, rất là bình thường, nhưng ở Thanh Nguyên đế quốc, cũng được cho tuyệt học, ngươi còn thật sự cho rằng là ven đường cỏ đuôi chó, nói nhặt được liền nhặt được? Nói học liền có thể học được?"),t("br"),t("br"),n._v("Đến đây khảo hạch xông thông đạo, cái nào không phải tại cùng một loại võ kỹ bên trên, nghiên cứu không dưới mấy năm?"),t("br"),t("br"),n._v("Coi như thiên tài chân chính, không có hai năm học tập, cũng tuyệt không dám qua tới khiêu chiến... Cái tên này đến tốt, nghe khẩu khí, bây giờ lập tức học, học xong lập tức đi xông..."),t("br"),t("br"),n._v("Thật là vô tri đến đáng sợ!"),t("br"),t("br"),n._v("“Võ kỹ học tập không dễ dàng như vậy, mỗi một loại đều cần thiên chuy bách luyện, không biết bao nhiêu năm tháng ma luyện...” Cố nén không vui, vẫn kiên nhẫn giới thiệu."),t("br"),t("br"),n._v("“Ta biết... Ngươi nhưng có bí kỹ? Mượn cho ta xem một chút là được, có thể luyện thành, ta tự nhiên sẽ luyện, không luyện được, liền đành phải thôi!” Trương Huyền nói."),t("br"),t("br"),n._v("Đối phương võ kỹ, nếu là cùng hắn sưu tập những cái kia, có thể hình thành Thiên Đạo công pháp, luyện một chút cũng không sao, không cách nào hình thành, cho không hắn đều không luyện, uổng phí hết công phu."),t("br"),t("br"),n._v("“Cái này... Cũng được!”"),t("br"),t("br"),n._v("Nhìn cách đó không xa Ngụy điện chủ một cái, vị này Chiến sư cố nén muốn bộc phát xúc động, cổ tay khẽ đảo, lấy ra một quyển sách."),t("br"),t("br"),n._v("“Đây là ta tu luyện , chính là cái này 200 cái võ kỹ bên trong một cái, có chút khó khăn, ta ở phía trên đắm chìm tròn bảy năm, mới có thể tiểu thành...”"),t("br"),t("br"),n._v("Chiến sư giới thiệu một chút."),t("br"),t("br"),n._v("Ngoài miệng nói bảy năm, trên mặt nhưng mang theo vẻ kiêu ngạo."),t("br"),t("br"),n._v("“Bảy năm liền đem Đại Tinh La Chỉ Lực tu luyện tới tiểu thành... Ngươi chẳng lẽ chính là Phong đường chủ thường xuyên nhấc lên Võ Kỹ điện siêu cấp thiên tài, Tiêu Đàm?”"),t("br"),t("br"),n._v("Ngụy điện chủ nhớ tới cái gì, lông mày giương lên."),t("br"),t("br"),n._v("Bọn họ vừa rồi tìm Phong điện chủ thời điểm, chỉ có vị này Chiến sư, cũng liền tùy tiện hỏi thăm, cũng không biết họ tên, giờ phút này nghe được võ kỹ danh xưng cùng thời gian tu luyện, lúc này mới nhớ tới."),t("br"),t("br"),n._v("Đại Tinh La Chỉ Lực, tại đây 200 cái võ kỹ bên trong, tuyệt đối được cho khó khăn nhất, uy lực cũng lớn nhất, coi như lúc trước Phong điện chủ, tu luyện tới tiểu thành cũng tròn hao tốn 23 năm, trước mắt vị này bảy năm liền tiểu thành, thiên phú cao, có thể nói kinh khủng."),t("br"),t("br"),n._v("“Không sai, chính là tại hạ!”"),t("br"),t("br"),n._v("Chiến sư Tiêu Đàm nhẹ gật đầu, một cỗ khí ngạo nghễ, tự nhiên sinh ra."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1134"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1136"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);