(window.webpackJsonp=window.webpackJsonp||[]).push([[1665],{2023:function(n,h,g){"use strict";g.r(h);var t=g(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,g=n._self._c||h;return g("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[g("h1",{attrs:{id:"chuong-34-lua-bip"}},[g("a",{staticClass:"header-anchor",attrs:{href:"#chuong-34-lua-bip"}},[n._v("#")]),n._v(" Chương 34: Lừa bịp")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("br"),g("br"),n._v(" “A?” Không nghĩ tới mỹ nữ sẽ tức giận, Thượng Bân sợ hết hồn, vội vã giải thích: “Trầm lão sư, ta không nói ngươi, ta nói chính là cái này Trương Huyền, không có tiền cũng đừng mời người ăn cơm, để người ngăn cản, quả thực mất mặt xấu hổ, tổn hại học viện chúng ta uy phong...” "),g("br"),g("br"),n._v(" Lời còn chưa nói hết, trước mắt nữ thần sắc mặt càng ngày càng trầm thấp, bất cứ lúc nào cũng sẽ nổ tung, Thượng Bân chính đang lòng tràn đầy nghi hoặc, chỉ thấy người phục vụ một mặt lúng túng đem đánh gãy: “Thượng thiếu...” "),g("br"),g("br"),n._v(" “Thế nào?” Thượng Bân hơi nhướng mày nhìn sang."),g("br"),g("br"),n._v(" “Khụ khụ, mời khách chính là... Vị này Trầm lão sư!” Phục vụ viên nói."),g("br"),g("br"),n._v(" Hắn nhận được mệnh lệnh, phối hợp Thượng Bân diễn kịch, cũng không nghĩ tới một nam một nữ ăn cơm, là nữ trả tiền, còn chưa kịp báo cáo, Thượng Bân liền vọt tới, lần này càng không thời gian giải thích."),g("br"),g("br"),n._v(" “Trầm lão sư mời khách?” "),g("br"),g("br"),n._v(" Thượng Bân sợ hết hồn, con mắt trợn tròn, tràn đầy không thể tin được."),g("br"),g("br"),n._v(" Thật hay giả?"),g("br"),g("br"),n._v(" Tự mình mỗi ngày mời nữ thần ăn cơm, nàng không thèm để ý, cái này học viện thứ nhất đếm ngược gia hỏa, có tư cách gì cùng nàng cùng nhau ăn cơm, hơn nữa... Còn làm cho nàng mời khách?"),g("br"),g("br"),n._v(" “Làm sao? Ta không có tiền trả tiền, chính là giả bộ hào phóng, mất mặt xấu hổ đúng không!” Trầm Bích Như lạnh lùng nhìn sang, nàng ngày hôm nay ở Trương Huyền trước mặt liên tục ăn quả đắng, liền rất phiền muộn, không nghĩ tới còn bị cái tên này cười nhạo, tức giận đến sắp nổ tung."),g("br"),g("br"),n._v(" “Không phải, không phải...” Thượng Bân sắc mặt trắng nhợt, sắp khóc."),g("br"),g("br"),n._v(" Giờ mới hiểu được, muốn nịnh hót, kết quả vỗ tới vó ngựa bên trên, vội vã xua tay: “Ta không phải ý này, là... Là...” "),g("br"),g("br"),n._v(" Nói liên tục nhiều lần, không biết nên trả lời như thế nào."),g("br"),g("br"),n._v(" Bất quá, hắn không hổ là trưởng lão cháu, học viện lão sư, kiến thức rộng rãi, rất nhanh phản ứng lại, quay đầu nhìn về phía người phục vụ, bàn tay lớn vẫy một cái: “Tốt, Trầm lão sư món nợ liền là của ta, bao nhiêu tiền, ta giúp đỡ thanh toán!” "),g("br"),g("br"),n._v(" “Thượng thiếu chuyện này, ngươi là chúng ta Hồng Thiên Lâu quý khách, chúng ta làm sao có khả năng muốn tiền của ngươi!” Người phục vụ liền vội vàng gật đầu."),g("br"),g("br"),n._v(" Đây là bọn hắn đã sớm đối với tốt lời kịch, chính là muốn ở nữ thần trước mặt biểu hiện thân phận của hắn cùng năng lực."),g("br"),g("br"),n._v(" “Vậy thì tốt...” Thượng Bân đang muốn nói tiếp, biểu lộ ra một hồi khí phách của mình, liền nghe đến Trầm Bích Như âm thanh lại vang lên: “Không cần, chính ta tiêu tốn ta chính mình sẽ giao, miễn cho bị người khác nói thành giả bộ hào phóng!” "),g("br"),g("br"),n._v(" Nói đem cắm ở trên đầu cây trâm lấy xuống: “Căn này ngọc trâm, giá trị chí ít năm ngàn kim tệ, ngươi có thể tìm cái người rõ ràng nghiệm chứng, ta trước tiên áp ở đây, chờ trở lại lấy tiền, lại trở về chuộc!” "),g("br"),g("br"),n._v(" “Chuyện này...” "),g("br"),g("br"),n._v(" Cầm ngọc trâm, người phục vụ không biết nên làm gì bây giờ."),g("br"),g("br"),n._v(" Trước đã nói xong kịch bản bên trong không những này a..."),g("br"),g("br"),n._v(" “Trầm lão sư, không cần như vậy, Hồng Thiên Lâu Ngô chấp sự cùng ta là bằng hữu, ta chỉ cần nói một tiếng, ngươi này điểm tiền không tính là gì...” "),g("br"),g("br"),n._v(" Thượng Bân vội vàng hướng trước."),g("br"),g("br"),n._v(" “Đó là ngươi sự tình, cùng ta có quan hệ gì?” Trầm Bích Như đánh gãy hắn, lần thứ hai nhìn về phía người phục vụ: “Cũng chưa tìm người nghiệm chứng, ta liền đi!” "),g("br"),g("br"),n._v(" “Ta...” Người phục vụ vội vã nhìn về phía Thượng Bân, tràn đầy ánh mắt cầu trợ."),g("br"),g("br"),n._v(" “Nhìn ta làm gì?” Thượng Bân nóng nảy liên tục xua tay, một mặt phẫn nộ: “Ta lại không quen biết ngươi!” "),g("br"),g("br"),n._v(" “Tốt!” "),g("br"),g("br"),n._v(" Không nghĩ tới ăn một bữa cơm náo thành như vậy, Trương Huyền lắc đầu một cái đi lên, đem ngọc trâm từ trong tay người bán hàng lấy tới, một lần nữa đưa cho Trầm Bích Như: “Ngươi cho hắn cái này làm gì? Bữa cơm này không đáng 1280 kim tệ, cho hắn tiền, chẳng lẽ muốn để hắn lừa bịp?” "),g("br"),g("br"),n._v(" “Lừa bịp? Hồng Thiên Lâu thành lập tiếp cận mười năm, giá cả vừa phải, không dối trên lừa dưới, còn chưa bao giờ có người đi tìm phiền phức, ngươi có ý gì?” "),g("br"),g("br"),n._v(" Trương Huyền vừa dứt lời, một người trung niên sải bước đi tới."),g("br"),g("br"),n._v(" Hồng Thiên Lâu chấp sự, Ngô Sầu."),g("br"),g("br"),n._v(" Hắn cũng cùng Thượng Bân nói xong rồi, nguyên bản không cần hiện thân, giờ khắc này nghe được Trương Huyền nói ra lừa bịp, cũng không nhịn được nữa."),g("br"),g("br"),n._v(" Chuyện bên này, dĩ nhiên đã kinh động không ít thực khách, thật sự nếu không xuất hiện, mặc cho đối phương nói ra, tửu lâu khẳng định danh tiếng tổn thất lớn, đến thời điểm ông chủ Hồng Hạo trưởng lão trở về,"),g("br"),g("br"),n._v(" Còn không giết hắn?"),g("br"),n._v(" “Có ý gì? Nhất định phải ta nói ra?” "),g("br"),g("br"),n._v(" Trương Huyền mí mắt vừa nhấc, tràn đầy lười biếng mùi vị."),g("br"),g("br"),n._v(" “Hừ, chúng ta Hồng Thiên Lâu, mỗi một phần món ăn, đều chọn dùng trân quý nhất vật liệu, mới mẻ ngon miệng, giá cả cũng thông qua Hồng Thiên học viện xét duyệt, nếu như ngươi không nói ra được như thế về sau, coi như ngươi là học viện lão sư, ngày hôm nay cũng đừng hòng đi!” "),g("br"),g("br"),n._v(" Ngô chấp sự sầm mặt lại."),g("br"),g("br"),n._v(" Đối với một cái tửu lâu tới nói, lừa bịp khách hàng là rất lớn tội danh, không làm rõ, truyền đi, Hồng Thiên Lâu cũng không cần mở ra."),g("br"),g("br"),n._v(" “Thực sự là tự mình muốn chết!” "),g("br"),g("br"),n._v(" Vốn tưởng rằng lần này không thu thập được Trương Huyền, nhìn thấy hắn lại còn nói Hồng Thiên Lâu lừa bịp, đắc tội rồi Ngô chấp sự, Thượng Bân hưng phấn con mắt ánh sáng lóe lên."),g("br"),g("br"),n._v(" Ngô chấp sự bình thường xem ra rất dễ nói chuyện, tính khí cũng không tệ, nhưng liên lụy đến Hồng Thiên Lâu danh tiếng bên trên, chút nào cũng sẽ không thoái nhượng!"),g("br"),g("br"),n._v(" Cái này học viện thứ nhất đếm ngược gia hỏa, xem ra xui xẻo rồi!"),g("br"),g("br"),n._v(" “Cái tên này nói linh tinh gì vậy đây...” "),g("br"),g("br"),n._v(" Trầm Bích Như thì lại một mặt sốt ruột."),g("br"),g("br"),n._v(" Hồng Thiên Lâu có thể ở Hồng Thiên học viện mở, đồng thời xây lớn như vậy, thế lực sau lưng có thể tưởng tượng được, ngươi công nhiên nói người ta lừa bịp khách hàng, đây không phải để người ta tìm ngươi phiền phức sao?"),g("br"),g("br"),n._v(" Này loại đại tửu lâu, nhưng là rất chú trọng danh tiếng."),g("br"),g("br"),n._v(" “Đã ngươi nếu ta nói, vậy ta liền không khách khí!” "),g("br"),g("br"),n._v(" Đối mặt với đối phương uy hiếp, Trương Huyền không để ý lắm, đi tới bàn trước mặt."),g("br"),g("br"),n._v(" “Phần này Tử Hương Bạo Hùng thịt, dựa theo các ngươi thuyết pháp là đem Bạo Hùng chém giết về sau, dùng Tử Hương Hoa ngâm ba ngày, như vậy mới có thể làm cho thịt kinh ngạc, tràn đầy mùi thơm, đáng tiếc, các ngươi chỉ ngâm một ngày rưỡi liền lấy ra đến, lật xào vào bàn, tuy rằng vị bên trên kém không nhiều, Tử Hương Hoa dược tính nhưng nửa điểm đều không có thấm vào, ăn nhiều hơn nữa đều vô dụng!” "),g("br"),g("br"),n._v(" “Cho tới phần này Hồng Hồ Quế Ngư, các ngươi tuyên truyền là vớt Hồng Hồ hoang dại cá quế, trên thực tế nhưng là Diệp La vịnh nuôi nhốt cá quế, hai người khác nhau rất đơn giản, hoang dại cá quế, bởi vì phải đối mặt thiên địch Thanh Hà Quy, vây đuôi trên so với bình thường nuôi nhốt cá quế càng cứng rắn hơn, đuôi cũng phải thô to không ít, chỉ cần hơi hơi hỏi thăm, liền có thể biết!” "),g("br"),g("br"),n._v(" “Cái này rau xanh xào Oa Tâm Thảo, liền càng không cần phải nói, căn bản cũng không phải là Oa Tâm Thảo, mà là Khổ Tâm Thái! Hai loại thực vật nhưng thật ra là cùng một loại, bất quá sinh trưởng hoàn cảnh không giống, tên cũng khác biệt, Oa Tâm Thảo sinh trưởng rơi vào Man Thú ổ một bên, chịu đến Man Thú chăm sóc, rễ cây bình thường đều rất lớn, dinh dưỡng giá trị cũng cao hơn nhiều!” "),g("br"),g("br"),n._v(" “Khổ Tâm Thái thì lại sinh trưởng ở hoang dã, mười phần tiện nghi, rễ cây bé nhỏ, dinh dưỡng giá trị cũng phải thấp một đoạn dài! Đồng dạng một bàn món ăn, hai người giá tiền ít nhất phải kém hơn gấp mười lần! Không thể giống nhau! Đánh dấu Oa Tâm Thảo, nhưng dùng Khổ Tâm Thái, đây không phải lừa bịp là cái gì?” "),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v(" Trương Huyền tiện tay chỉ về một cái món ăn, liền rõ ràng nói ra món ăn lai lịch cùng khuyết điểm, nguyên bản khí thế hung hăng Ngô chấp sự nghe đến mấy câu này sắc mặt trở nên trắng bệch."),g("br"),g("br"),n._v(" Hắn thân là Hồng Thiên Lâu chấp sự, không riêng trước sân khấu khách hàng, mặt sau nhà bếp sự tình, cũng về chỗ hắn lý, tự nhiên biết đối phương nói thật giả."),g("br"),g("br"),n._v(" Không phải tin khẩu nói bậy, mà là... Quá đúng rồi!"),g("br"),g("br"),n._v(" Những việc này, ông chủ không hề cảm kích, mà là hắn vì kiếm chác lãi kếch sù, tìm người lặng lẽ sửa đổi, đặc biệt là Hồng Hồ Quế Ngư cùng Oa Tâm Thảo, hai cái vật thay thế, quả thực giống như đúc, liền ngay cả chuyên môn mỹ thực sư, cũng không phát hiện dị thường."),g("br"),g("br"),n._v(" Thiếu niên này làm sao nhìn ra được? Mà lại nói được không kém chút nào?"),g("br"),g("br"),n._v(" “Đáng ghét!” "),g("br"),g("br"),n._v(" “Chúng ta chuyên môn từ đằng xa lại đây ăn chính tông Oa Tâm Thảo, không nghĩ tới lại là giả!” "),g("br"),g("br"),n._v(" “Ta cũng vậy, nghe nói nơi này Hồng Hồ Quế Ngư thơm nhất, khẩu vị tốt, chính tông, chuyên tới được, dĩ nhiên như vậy lừa gạt chúng ta!” "),g("br"),g("br"),n._v(" “Trả lại tiền, trả lại tiền! Đường đường Hồng Thiên học viện đệ nhất tửu lâu, dùng giả mạo ngụy liệt, thiệt thòi ta vẫn là khách quen cũ, lại bị như vậy lừa dối!” "),g("br"),g("br"),n._v("..."),g("br"),g("br"),n._v(" Trương Huyền mở miệng, ung dung không vội, có lý có chứng cứ, xung quanh xem náo nhiệt mọi người, lập tức vỡ tổ."),g("br"),g("br"),n._v(" Hồng Thiên Lâu ở toàn bộ Thiên Huyền vương quốc đều được cho có tiếng đại tửu lâu, trong đó không ít bảng hiệu món ăn, hấp dẫn vô số cường giả đến đây, bọn họ nằm mơ đều không có nghĩ đến, những này rõ ràng đều là giả!"),g("br"),g("br"),n._v("Convert by: Hoang Chau"),g("br"),g("br"),g("br"),g("br")]),n._v(" "),g("hr"),n._v(" "),g("p",[g("a",{attrs:{href:"./chap-42"}},[n._v("Chương trước")]),n._v("  |  "),g("a",{attrs:{href:"./chap-44"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);