(window.webpackJsonp=window.webpackJsonp||[]).push([[167],{520:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1127-muoi-mot-cai-ho-hap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1127-muoi-mot-cai-ho-hap"}},[n._v("#")]),n._v(" Chương 1127: Mười một cái hô hấp")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1127: Mười một cái hô hấp"),t("br"),t("br"),n._v("“Thì ra là thế!”"),t("br"),t("br"),n._v("Biết đối phương lại là võ kỹ điện siêu cấp thiên tài, thiên phú mức độ, không tại Thạch Hạo phía dưới, Ngụy điện chủ nhẹ gật đầu, nhìn về phía Trương Huyền: “Tôn sư, ta lần nữa cho ngươi tìm một bộ võ kỹ đi, Đại Tinh La Chỉ Lực, độ khó cực lớn, tại đây hai trăm bộ bên trong, đều sắp xếp bên trên gần phía trước...”"),t("br"),t("br"),n._v("Phong điện chủ, bởi vì lúc trước không phải Chiến sư, không được đến nhất hệ thống huấn luyện, tu luyện chậm một chút ngược lại cũng thôi, nhưng trước mắt này vị thiên tài, cũng hao tốn ròng rã bảy năm mới tiểu thành, Đại Tinh La Chỉ Lực, có thể nói là toàn bộ võ kỹ điện đều công nhận khó học."),t("br"),t("br"),n._v("“Không cần phiền toái như vậy...”"),t("br"),t("br"),n._v("Nhận lấy bí tịch tiện tay mở ra, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Đối với hắn mà nói, chỉ cần có thể tiếp cận thành Thiên Đạo công pháp, cái này 200 bộ võ kỹ, cái nào đều như thế, không có ưu khuyết."),t("br"),t("br"),n._v("“Vậy ngươi xem trước một chút, nếu như khó mà tu luyện, chúng ta lại làm cái khác lựa chọn...”"),t("br"),t("br"),n._v("Biết nói nhiều rồi, sẽ cho người chán ghét, Ngụy điện chủ nói."),t("br"),t("br"),n._v("Thứ này, nói vô dụng, chỉ có bản thân tự mình tu luyện, mới hiểu được độ khó."),t("br"),t("br"),n._v("Chờ cái tên này bại té ngã, ngay cả nhập môn đều làm không được, khả năng liền hiểu cái này Đại Tinh La Chỉ Lực kinh khủng."),t("br"),t("br"),n._v("Rất hiển nhiên Tiêu Đàm cũng ôm ý nghĩ này, rất hứng thú nhìn về phía trước mắt vị này không biết trời cao đất rộng thanh niên, muốn nhìn một chút, đối phương nhìn xong bí tịch về sau, không cách nào lĩnh ngộ sau biểu lộ."),t("br"),t("br"),n._v("Bất quá, loại vẻ mặt này không đợi được, lập tức nhìn thấy đối phương nhíu nhíu mày, một bộ đổ khẩu vị bộ dáng."),t("br"),t("br"),n._v("“Ngươi có ý tứ gì?”"),t("br"),t("br"),n._v("Tiêu Đàm mặt mũi không vui."),t("br"),t("br"),n._v("Xem không hiểu liền xem không hiểu, biết độ khó liền biết độ khó, bộ dáng này, có ý tứ gì?"),t("br"),t("br"),n._v("“Không có gì!” Trương Huyền sửng sốt một chút, lần nữa cúi đầu nhìn lại, một lát sau lần nữa lắc đầu: “Quên đi, cứ như vậy đi!”"),t("br"),t("br"),n._v("Nói hết, một bộ coi thường cái chết bộ dáng, đứng tại chỗ, chậm rãi nhắm mắt lại."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Thấy hắn tràn đầy không tình nguyện, biểu lộ so vừa rồi còn muốn khoa trương, Tiêu Đàm tức giận sắp nổ tung."),t("br"),t("br"),n._v("Đại Tinh La Chỉ Lực, mặc dù không phải cái gì đứng đầu nhất võ kỹ, nhưng ở võ kỹ điện cũng tuyệt đối được cho gần phía trước, bình thường tu luyện, cảm thấy khó khăn, có thể lý giải, nhưng... Vẻ mặt ghét bỏ, cảm thấy tu luyện bộ võ kỹ này, cùng ăn chuột chết, liền có chút quá đáng đi!"),t("br"),t("br"),n._v("“Cho dù rất khó luyện thành, xem không hiểu, cũng không cần loại thái độ này a? Quả thực vô lễ đến cực điểm...”"),t("br"),t("br"),n._v("Hừ lạnh một tiếng, đang nghĩ nói chuyện, chỉ thấy thanh niên trước mắt, bàn tay đột nhiên dựng thẳng lên, tay phải ngón trỏ, hướng về phía trước nhẹ nhàng điểm một cái."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Chân khí đâm rách trên không, giống như là xé rách giấy, phát ra cắt chém giống như thanh âm, đầu ngón tay dường như có tinh thần toàn chuyển, cho người ta một loại mênh mông xa xăm cảm giác."),t("br"),t("br"),n._v("Đại Tinh La Chỉ Lực, là hàng vạn năm trước, một vị Chiến sư đường tiền bối phận, quan sát nhật nguyệt tinh thần sáng lập ra, một chỉ điểm ra, chân khí mượn nhờ tinh thần sắp xếp lực lượng, cho người ta một loại xuyên qua vũ trụ Thương Mang, không chỉ chân khí công kích, trên linh hồn cũng mang theo cực mạnh áp bức."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, bình thường tu luyện giả rất khó lý giải, thậm chí cuối cùng cả đời, đều không hiểu loại này tinh thần lực lượng, hẳn là mượn thế nào dùng."),t("br"),t("br"),n._v("Vốn, cảm thấy vị này Tôn sư, phô trương quá mức, tính cách không đòi thích, muốn quát tháo vài câu, nhìn thấy đối phương điểm ra ngón tay, con ngươi kìm lòng không được thu hẹp, Tiêu Đàm thân thể run lên: “Đây là... Nhập môn?”"),t("br"),t("br"),n._v("Tưởng là đối phương, căn bản nhìn không hiểu võ kỹ là như thế nào tu luyện, ở nơi đó giả vờ giả vịt, nằm mơ đều không có nghĩ đến, liền không vừa mắt con ngươi một chút, đã nhập môn!"),t("br"),t("br"),n._v("Lúc trước hắn tu luyện tới nhập môn, thế nhưng là hao tốn ròng rã thời gian một năm!"),t("br"),t("br"),n._v("Xì xì xì xì...!"),t("br"),t("br"),n._v("Chính khiếp sợ không rõ ràng cho lắm, thanh niên đầu ngón tay chân khí lực lượng càng ngày càng mạnh, phát ra nghẹn ngào thanh âm, tựa như đem người đưa vào trong bóng đêm, bốn phía tinh thần buông xuống, bất cứ lúc nào cũng sẽ hạ xuống."),t("br"),t("br"),n._v("“Đây là... Tinh thông?”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, Tiêu Đàm vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Mặc dù bộ võ kỹ này ấy ư, nhập môn rất khó, một khi sau khi thành công mặt tiến bộ sẽ mau mau, có thể lúc này mới bao lâu thời gian?"),t("br"),t("br"),n._v("Mười cái hô hấp?"),t("br"),t("br"),n._v("Năm cái hô hấp?"),t("br"),t("br"),n._v("Vẫn là hai cái hô hấp?"),t("br"),t("br"),n._v("Ngón tay duỗi ra thời điểm, là nhập môn, cánh tay vẫn chưa hoàn toàn duỗi thẳng, cũng đã tinh thông..."),t("br"),t("br"),n._v("Ngươi đây không phải tu luyện, mà là đùa giỡn a?"),t("br"),t("br"),n._v("Yết hầu phát khô, quay đầu nhìn về phía cách đó không xa Ngụy điện chủ, muốn hỏi một chút, cái tên này đến cùng lai lịch gì, có thể học nhanh như vậy, chỉ thấy vị điện chủ này cũng mở to hai mắt nhìn, một bộ vẻ mặt như gặp phải quỷ."),t("br"),t("br"),n._v("Rất rõ ràng, hắn cũng không nghĩ tới, đối phương có thể nhanh như vậy liền theo nhập môn, đạt tới tinh thông tình trạng."),t("br"),t("br"),n._v("Cái này đã vượt ra khỏi kiến thức bình thường có thể lý giải phạm trù."),t("br"),t("br"),n._v("“Ngụy điện chủ...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, sợ quấy rầy cả hạ tu luyện Tôn sư, truyền âm qua."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Ngụy điện chủ theo trong lúc khiếp sợ tỉnh táo lại."),t("br"),t("br"),n._v("“Vị này Tôn sư... Trước đó, thật không có học qua Đại Tinh La Chỉ Lực?”"),t("br"),t("br"),n._v("Nhịn không được nói."),t("br"),t("br"),n._v("“Sẽ không có học qua... Loại chuyện nhỏ nhặt này, còn không đến mức nói dối, tiến hành lừa gạt!” Nhớ tới thanh niên đủ loại thần kỳ, dừng lại một chút, Ngụy điện chủ nói."),t("br"),t("br"),n._v("Bọn họ một vị đường chủ, hai vị điện chủ liên thủ công kích, đều không có làm bị thương người ta chút nào, ngược lại bị đối phương mượn nhờ trận pháp đánh một trận... Càng là một chưởng quét ngang nội tức thông đạo mười khối bia đá..."),t("br"),t("br"),n._v("Nhân vật như vậy, sẽ không có cần thiết nói dối, nhìn tới... Cái này Đại Tinh La Chỉ Lực, đối bọn hắn tới nói khó mà hoàn thành, đối vị này tới nói, không khó."),t("br"),t("br"),n._v("“Không học qua, thời gian nháy mắt nhập môn, mấy cái vù vù ** thông... Ta hao tốn bảy năm, mới tiểu thành, không biết trong một ngày là có thể đuổi kịp a?” Tiêu Đàm bờ môi không ngừng run rẩy."),t("br"),t("br"),n._v("“Hẳn là sẽ không... Theo tinh thông đến tiểu thành có cái to lớn đường ranh giới, sẽ không có người nhanh như vậy... A?”"),t("br"),t("br"),n._v("Nói một tiếng, Ngụy điện chủ lời còn chưa dứt, rít lên một tiếng, hô lên."),t("br"),t("br"),n._v("Chỉ thấy trước mắt nhắm hai mắt thanh niên, cánh tay rốt cục duỗi thẳng, một cơn lốc xoáy xuất hiện, tựa như tinh hà xoay tròn, còn không có công kích, chỉ nhìn bên trên một cái, tựu khiến người mê muội, bất cứ lúc nào cũng sẽ hôn mê."),t("br"),t("br"),n._v("“Tiểu thành...”"),t("br"),n._v("Hai người đồng thời lui về sau hai bước."),t("br"),t("br"),n._v("Mới vừa nói hết ngày hôm nay không thể nhỏ thành, đối phương liền đạt đến..."),t("br"),t("br"),n._v("Vù vù!"),t("br"),t("br"),n._v("Đạt tới tiểu thành, Trương Huyền mở mắt, nhìn tay mình chỉ một cái, than thở một tiếng, ngẩng đầu nhìn về phía cách đó không xa Tiêu Đàm."),t("br"),t("br"),n._v("“Võ kỹ này thông đạo, là đem một bộ võ kỹ tu luyện tới đại thành tình trạng, mới có thể đi vào vượt quan đúng không?”"),t("br"),t("br"),n._v("“Đúng...”"),t("br"),t("br"),n._v("Không biết hắn hỏi như vậy có ý tứ gì, Tiêu Đàm nhẹ gật đầu."),t("br"),t("br"),n._v("Võ kỹ thông đạo, cần đem cái này 200 võ kỹ bên trong bất kỳ một cái nào tu luyện tới đại thành, mới có cơ hội thông qua, nếu không, căn bản không có khả năng thành công."),t("br"),t("br"),n._v("Trong lòng khó hiểu, đang nghĩ giải thích một chút, chỉ thấy thanh niên trước mắt, đáp một tiếng: “Ta biết rồi...”"),t("br"),t("br"),n._v("Lần nữa đem con mắt đóng lại."),t("br"),t("br"),n._v("“Ừm?” Da mặt run lên: “Ngươi không phải là muốn... Hiện tại liền xung kích đại thành? Đại Tinh La Chỉ Lực, đối chân khí yêu cầu cực cao, không có nói chuẩn bị trước, cưỡng ép xung kích, sẽ chỉ xuất hiện sai lầm, người bị nặng...”"),t("br"),t("br"),n._v("Lời còn chưa dứt, chỉ thấy thanh niên ngón tay lần nữa điểm ra."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Trước mắt không khí, giống như là trong nháy mắt bị áp súc, trong nháy mắt hắn liền cảm thấy cả người, rơi vào đầy Thiên Tinh Đấu bên trong, ngay sau đó từng khỏa tinh thần hạ xuống từ trên trời, dường như tuỳ tiện có thể đem hắn nghiền ép."),t("br"),t("br"),n._v("“Chu thiên tinh thần...”"),t("br"),t("br"),n._v("Tiêu Đàm thân thể đung đưa kịch liệt: “Đây là... Vượt qua đại thành, viên mãn như ý?”"),t("br"),t("br"),n._v("Võ kỹ tu luyện chia làm năm cái cảnh giới: Nhập môn, tinh thông, tiểu thành, đại thành, viên mãn như ý."),t("br"),t("br"),n._v("Mới vừa nói đối phương, tu luyện tới đại thành rất khó, kết quả đối phương, tùy tiện đạt đến viên mãn như ý..."),t("br"),t("br"),n._v("Theo nhận lấy bí tịch, đến tu luyện tới loại tình trạng này, tổng cộng dùng... Mười cái hô hấp!"),t("br"),t("br"),n._v("Mà hắn vị này võ kỹ điện siêu cấp thiên tài, dùng bảy năm, vẫn chỉ là tiểu thành..."),t("br"),t("br"),n._v("Một hồi kịch liệt cảm giác bị thất bại, để hắn sắp nổ tung."),t("br"),t("br"),n._v("Người... Thật sự là không thể so được."),t("br"),t("br"),n._v("Không biết tùy tiện tu luyện một chút, sợ tới mức Ngụy điện chủ, Tiêu Đàm muốn hộc máu, Trương Huyền mở mắt."),t("br"),t("br"),n._v("Nói thật, nếu không phải vì vượt quan, tuyệt đối sẽ không học chút bộ này Đại Tinh La Chỉ Lực."),t("br"),t("br"),n._v("Dung hợp rất nhiều võ kỹ về sau, mặc dù thiếu sót ít đi rất nhiều, vẫn như trước có năm cái nhiều."),t("br"),t("br"),n._v("Nhiều như vậy thiếu hụt công pháp, nói thật, chưa hề luyện qua... Nguyên nhân chính là như vậy, mới xoắn xuýt rất lâu, cuối cùng mặt mũi coi thường cái chết."),t("br"),t("br"),n._v("Quá kém!"),t("br"),t("br"),n._v("Luyện thật sự là mất mặt xấu hổ."),t("br"),t("br"),n._v("Bất quá, cũng hết cách rồi, muốn thông qua khảo hạch, đi tìm càng nhiều Thánh vực nhất trọng công pháp, đây coi như là nhất bớt sức lực biện pháp."),t("br"),t("br"),n._v("May mắn những này thiếu hụt, chỉ là đối thân thể có chút tổn thương, không cần bảo vật gì loại hình, nếu không, khẳng định sẽ phiền phức không ít."),t("br"),t("br"),n._v("“Ta tu luyện xong, khả năng cần phiền phức Tiêu Đàm Chiến sư, hỗ trợ báo danh...”"),t("br"),t("br"),n._v("Lắc đầu đem buồn bực trong lòng dứt bỏ, Trương Huyền nói."),t("br"),t("br"),n._v("Những này thiếu hụt, chỉ là tổn hại trong cơ thể một chút kinh mạch, chỉ cần sử dụng, liền sẽ xé rách, để cho người ta đau đến không muốn sống, nhưng hắn nắm giữ Thiên Đạo chân khí, khôi phục thương thế cực nhanh, vấn đề cũng không lớn."),t("br"),t("br"),n._v("Dù sao, chỉ là vì khảo hạch mà thôi, về sau tận lực ít dùng là được."),t("br"),t("br"),n._v("“Là, là...”"),t("br"),t("br"),n._v("Tiêu Đàm lúc này mới theo trong lúc khiếp sợ kịp phản ứng, rụt cổ một cái, vội vã đi thẳng về phía trước, thời gian không dài trở lại trước mặt: “Tôn sư, ta đã nói qua, để ngươi tới trước!”"),t("br"),t("br"),n._v("Võ kỹ thông đạo, mỗi ngày tới khảo hạch không ít người, có điều, có thể chân chính thông qua không nhiều."),t("br"),t("br"),n._v("Trước mắt vị này là Ngụy điện chủ tự mình mang đến, lại tu luyện võ kỹ như thế nghịch thiên, để hắn trước tiến hành khảo hạch, cũng không có gì."),t("br"),t("br"),n._v("“Làm phiền...”"),t("br"),t("br"),n._v("Ôm quyền, Trương Huyền theo sau lưng, chỉ chốc lát đi tới trước mặt."),t("br"),t("br"),n._v("Vừa vặn cửa mở ra, một thanh niên vẻ mặt chán nản đi ra, vừa nhìn liền biết khảo hạch thất bại."),t("br"),t("br"),n._v("“Tôn sư, mời...”"),t("br"),t("br"),n._v("Tiêu Đàm đưa tay."),t("br"),t("br"),n._v("Trương Huyền nhấc chân đi vào, thời gian nháy mắt biến mất ở trước mặt mọi người."),t("br"),t("br"),n._v("“Họ Tiêu, cái tên này đến cùng lai lịch gì, để ngươi tự mình giúp hắn chen ngang?” Trong đám người một cái Chiến sư hô lên."),t("br"),t("br"),n._v("“Ta cũng không biết...” Tiêu Đàm lắc đầu."),t("br"),t("br"),n._v("“Không biết? Hắn báo cái nào võ kỹ, dù sao cũng nên biết a?”"),t("br"),t("br"),n._v("Mọi người một hồi khó hiểu, lời mới vừa nói Chiến sư, hỏi lần nữa."),t("br"),t("br"),n._v("Căn cứ tu luyện võ kỹ, cũng có thể suy đoán ra một bộ phận lai lịch."),t("br"),t("br"),n._v("“Đại Tinh La Chỉ Lực!” Tiêu Đàm nói."),t("br"),t("br"),n._v("“Đại tinh la... Đây không phải ngươi tu luyện sao?”"),t("br"),t("br"),n._v("“Bộ võ kỹ này rất khó luyện thành, cho dù như ngươi loại này thiên phú, luyện bảy năm, đều chỉ là tiểu thành, cái tên này dám đến khiêu chiến, chẳng lẽ đã đại thành? Ta nhìn hắn tuổi tác cũng không lớn đi!”"),t("br"),t("br"),n._v("Vị này Chiến sư cau mày, nhịn không được hỏi: “Lớn tuổi như vậy, có thể luyện bao lâu?”"),t("br"),t("br"),n._v("“Đại khái...”"),t("br"),t("br"),n._v("Tiêu Đàm suy nghĩ một chút, mặt mũi cười khổ: “Mười hai cái hô hấp, a, không, là tròn mười một cái hô hấp!”"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1135"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1137"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);