(window.webpackJsonp=window.webpackJsonp||[]).push([[168],{521:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1128-buon-phong-đien-chu"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1128-buon-phong-đien-chu"}},[n._v("#")]),n._v(" Chương 1128: Buồn Phong điện chủ")]),n._v(" "),i("hr"),n._v(" "),i("p",[n._v("Chương 1128: Buồn Phong điện chủ"),i("br"),i("br"),n._v("Vù vù!"),i("br"),i("br"),n._v("Vừa đi vào rộng lớn thông đạo, một cái khôi lỗi liền đứng lên."),i("br"),i("br"),n._v("Thoạt nhìn giống như người thật không hai, đại khái sáu mươi tuổi bộ dáng, râu tóc trắng tinh."),i("br"),i("br"),n._v("“Hẳn là Phong điện chủ lấy bản thân bộ dáng luyện chế.”"),i("br"),i("br"),n._v("Trương Huyền trong lòng suy đoán."),i("br"),i("br"),n._v("Vị này Phong điện chủ, theo Danh Sư đường mà đến, hẳn là tinh thông thiên công chức nghiệp, nếu không không có khả năng luyện chế ra như vậy giống như đúc khôi lỗi."),i("br"),i("br"),n._v("Nếu như không phải trước thời hạn biết, chỉ sợ đều tưởng rằng người thật."),i("br"),i("br"),n._v("“Ngươi lựa chọn là Đại Tinh La Chỉ Lực, lấy chỉ phá chỉ, có thể nhận lấy ta công kích, qua ải! Không tiếp nổi, rời khỏi!”"),i("br"),i("br"),n._v("Khôi lỗi không có bất kỳ cái gì biểu lộ."),i("br"),i("br"),n._v("“Tốt!”"),i("br"),i("br"),n._v("Trương Huyền nhẹ gật đầu, tiến về phía trước một bước."),i("br"),i("br"),n._v("Cùng cấp bậc thi triển tương đồng võ kỹ, hắn có tuyệt đối tự tin, Minh Lý Chi Nhãn, Thiên Đạo thư viện không cần, cũng có thể nhẹ nhõm chiến thắng."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("“Bẩm báo đường chủ, Ngụy điện chủ mang theo Tôn lão sư đi võ kỹ điện?”"),i("br"),i("br"),n._v("Hình đường chủ đám người mang theo Tề lão đi tới Nội Tức điện, cũng không phát hiện Trương Huyền đám người, nhịn không được hỏi hướng cách đó không xa Thạch Hạo, rất nhanh đến mức đến trả lời."),i("br"),i("br"),n._v("“Đi võ kỹ điện làm gì?”"),i("br"),i("br"),n._v("Hình đường chủ cau mày."),i("br"),i("br"),n._v("“Không biết!” Thạch Hạo lắc đầu."),i("br"),i("br"),n._v("“Chờ một chút, ngươi mới vừa nói là... Tôn lão sư?” Một bên Liêu điện chủ nghe ra trong lời nói không đúng."),i("br"),i("br"),n._v("Xưng hô Tôn sư, là đối danh sư tôn kính, mà Tôn lão sư, quan hệ thì tiến thêm một bước, có thụ nghiệp chi ân."),i("br"),i("br"),n._v("“Vâng, Tôn lão sư, chỉ điểm chúng ta tu luyện, chúng ta vô cùng cảm kích!” Thạch Hạo gật đầu."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Hình đường chủ đám người liếc mắt nhìn nhau, mỗi người cau mày, một cái Khải Linh sư, chỉ điểm Chiến sư... Hơn nữa để nhiều như vậy Chiến sư đồng thời bái sư... Xảy ra chuyện gì?"),i("br"),i("br"),n._v("Rất nhanh, hỏi rõ ràng tình huống, ba người trợn mắt hốc mồm."),i("br"),i("br"),n._v("Một chưởng đánh tan nội tức thông đạo, một ngón tay để Thạch Hạo nhận thua... Vị này Tôn sư, đến cùng cỡ nào hùng hậu tu vi chân khí?"),i("br"),i("br"),n._v("“Đi võ kỹ điện nhìn một chút...”"),i("br"),i("br"),n._v("Tràn đầy tò mò, vội vã Hướng Vũ kỹ điện chạy tới."),i("br"),i("br"),n._v("Rất mau tới đến võ kỹ ngoài thông đạo."),i("br"),i("br"),n._v("“Các ngươi sao lại tới đây?”"),i("br"),i("br"),n._v("Ngụy điện chủ sửng sốt một chút, vội vàng ôm quyền, ánh mắt sáng lên: “Tề lão... Ngươi không có việc gì?”"),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Tề lão nhẹ gật đầu, nói: “Tôn Cường Tôn sư đâu?”"),i("br"),i("br"),n._v("“A, vừa mới tiến thông đạo khảo hạch đi!” Ngụy điện chủ cười cười."),i("br"),i("br"),n._v("“Khảo hạch?” Hình đường chủ cau mày."),i("br"),i("br"),n._v("“Tôn sư muốn tìm kiếm Thánh vực nhất trọng công pháp bí tịch, trong chúng ta hơi thở điện không phát hiện thích hợp, liền đến tìm Phong điện chủ, vừa vặn không tại, Tôn sư quyết định thông qua võ kỹ thông đạo khảo hạch, sau đó đi Tàng Thư khố quan sát!”"),i("br"),i("br"),n._v("Ngụy điện chủ đem sự tình vừa rồi, giải thích một chút."),i("br"),i("br"),n._v("“Lấy thực lực của hắn, thông qua khảo hạch hẳn là... Không khó!”"),i("br"),i("br"),n._v("Nhớ tới thanh niên cùng bọn hắn đối chiến, cùng vừa rồi tại Nội Tức điện nhìn thấy tình cảnh, Hình đường chủ vuốt chòm râu."),i("br"),i("br"),n._v("Nhân vật thiên tài như vậy, thông qua võ kỹ thông đạo khảo hạch, sẽ không có vấn đề quá lớn, không cần thiết lo lắng."),i("br"),i("br"),n._v("“Trước đó hội nghị kết thúc, Phong điện chủ liền trở lại, không cùng những người khác đồng dạng đi ra ngoài, cái tên này từ trước tới nay chân không bước ra khỏi nhà, làm sao ngày hôm nay ngược lại không tại?”"),i("br"),i("br"),n._v("Không tại lo lắng, Hình đường chủ cười hỏi."),i("br"),i("br"),n._v("Vị này Phong điện chủ, thích nhất liền là trốn ở trong nhà nghiên cứu võ kỹ, cho dù là hắn, mời nhiều lần, cũng sẽ không đi ra ngoài, lần này không cùng Chu điện chủ, Ngô điện chủ đồng dạng, chạy tới Huyền Huyền hội, làm sao sẽ không tại trong điện?"),i("br"),i("br"),n._v("“Ta cũng có chút khó hiểu...” Ngụy điện chủ lắc đầu."),i("br"),i("br"),n._v("Hắn vừa tới thời điểm cũng có chút kỳ quái, có điều, đối phương bên này người đều nói, hơn nữa nói như vậy chính là Tiêu Đàm, cũng không dễ xem kỹ."),i("br"),i("br"),n._v("“Lão Phong cũng có thể cam lòng ra ngoài? Ta thế nào cảm giác không tin đâu? Hả? Không đúng, chẳng lẽ...”"),i("br"),i("br"),n._v("Một bên Liêu điện chủ, cảm khái một tiếng, đột nhiên nhớ tới cái gì, sửng sốt một cái."),i("br"),i("br"),n._v("“Làm sao vậy?”"),i("br"),i("br"),n._v("Thấy hắn bộ dáng này, Hình đường chủ đám người đồng loạt nhìn qua."),i("br"),i("br"),n._v("“Ta cùng Lão Phong... Quan hệ rất tốt, hắn thường xuyên đến ta chỗ này, để cho ta hỗ trợ hóa giải tâm ma... Từng cùng ta nói qua, hắn có cái đặc thù đam mê, sẽ trốn ở võ kỹ thông đạo, ngụy trang thành khôi lỗi, khảo hạch Chiến sư...”"),i("br"),i("br"),n._v("Khóe miệng giật một cái, Liêu điện chủ chậm rãi nói."),i("br"),i("br"),n._v("“Ngụy trang khôi lỗi?” Mọi người sửng sốt."),i("br"),i("br"),n._v("“Đúng vậy a, võ kỹ thông đạo, là khôi lỗi thi triển võ kỹ, tiến hành khảo hạch! Nhưng có thời điểm, liên tiếp vận chuyển, khôi lỗi sẽ xuất hiện bị hao tổn tình huống... Lúc này, hắn bình thường sẽ đè thấp tu vi, ngụy trang thành bộ dáng của đối phương, cùng Chiến sư chiến đấu...”"),i("br"),i("br"),n._v("Nuốt ngụm nước bọt, Liêu điện chủ nói: “Chuyện này, hắn cùng ta nói qua, ta chỉ coi thành trò đùa nghe một chút, sẽ không... Tới thật sao?”"),i("br"),i("br"),n._v("Thân là điện chủ, ngụy trang thành khôi lỗi đi tiến hành khảo hạch, không phải cái gì hào quang sự tình, người biết không nhiều, khả năng toàn bộ Chiến sư đường, cũng chỉ hắn vị này Tâm điện điện chủ biết một ít..."),i("br"),i("br"),n._v("Phong điện chủ đột nhiên không tại, lại không ra ngoài... Sẽ không phải, thật chạy đến thông đạo, hóa trang làm khôi lỗi, đi khảo nghiệm Chiến sư đi!"),i("br"),i("br"),n._v("“Tôn sư chọn cái gì võ kỹ?”"),i("br"),i("br"),n._v("Ngây ngốc một chút, Hình đường chủ vội vàng nhìn về phía một bên."),i("br"),i("br"),n._v("Lấy cái tên này đối võ kỹ cố chấp... Làm không cẩn thận thật đúng là làm được."),i("br"),i("br"),n._v("“Hắn chọn là Đại Tinh La Chỉ Lực...” Ngụy điện chủ nói."),i("br"),n._v("“Bộ võ kỹ này uy lực cực mạnh, chỉ là khó mà tu luyện, Tôn sư tuổi còn trẻ, vậy mà học được?” Hình đường chủ sửng sốt."),i("br"),i("br"),n._v("“Cái khác võ kỹ không biết, Đại Tinh La Chỉ Lực là mới vừa học...” Ngụy điện chủ nói."),i("br"),i("br"),n._v("“Mới vừa học liền muốn cùng Phong điện chủ đối kháng? Lão Phong là thằng điên, khảo hạch nghiêm túc, nếu là biết mới vừa học liền đến vượt quan, tuyệt đối giận dữ, không biết lưu tình, nguy rồi, Tôn sư muốn gặp được phiền phức... Nhanh đi ngăn cản!”"),i("br"),i("br"),n._v("Sửng sốt một chút, Liêu điện chủ xù lông."),i("br"),i("br"),n._v("Phong điện chủ, người nếu như họ, cùng người điên, khảo hạch cực kỳ nghiêm túc, Tôn sư vừa mới học Đại Tinh La Chỉ Lực, có thể cao bao nhiêu trình độ? Gặp được Nguyên Thần cảnh điện chủ, làm không cẩn thận, sẽ bị điên cuồng đánh một trận!"),i("br"),i("br"),n._v("“Kẹt kẹt!”"),i("br"),i("br"),n._v("Tràn đầy cuống cuồng, đang định vọt tới thông đạo cứu người, liền nghe đến tiếng mở cửa vang lên, lập tức liền thấy một thanh niên chậm rãi đi ra."),i("br"),i("br"),n._v("Vừa đi, còn một bên sửa sang lại quần áo."),i("br"),i("br"),n._v("“Tôn sư... Ngươi không sao chứ?”"),i("br"),i("br"),n._v("Thấy hắn hoàn hảo không chút tổn hại đi ra, Hình đường chủ đám người vội vàng đi tới trước mặt."),i("br"),i("br"),n._v("“Không có việc gì!”"),i("br"),i("br"),n._v("Thấy rõ ràng là bọn họ, Trương Huyền mỉm cười."),i("br"),i("br"),n._v("“Ngươi... Thông qua khiêu chiến?”"),i("br"),i("br"),n._v("Ngụy điện chủ nhìn qua, có chút lo lắng."),i("br"),i("br"),n._v("“Ừm, bên trong những khôi lỗi kia, thật đúng là giống... Bị ta đánh một trận, thế mà lại còn hộc máu, nếu không phải trước thời hạn biết, đều cảm thấy là người thật!” Trương Huyền khen ngợi."),i("br"),i("br"),n._v("“Hộc máu?”"),i("br"),i("br"),n._v("Mọi người khóe miệng co giật."),i("br"),i("br"),n._v("“Đúng vậy a, cái tên này, cùng cấp bậc thi triển tương đồng võ kỹ, không vượt qua ta, hỏi hắn qua ải không có, vậy mà không trả lời ta! Đành phải tiếp tục chiến đấu, nhìn hắn thực lực càng tăng lên càng cao, sợ chiến thắng có điều, liền tiên hạ thủ vi cường, trước đem hắn đánh ngất xỉu đi qua... Đánh ngất xỉu khôi lỗi, hẳn là tính vượt qua kiểm tra đi!”"),i("br"),i("br"),n._v("Trương Huyền hỏi."),i("br"),i("br"),n._v("“Đánh ngất xỉu...”"),i("br"),i("br"),n._v("“Thực lực càng tăng lên càng cao?”"),i("br"),i("br"),n._v("Nếu như nói mới vừa rồi còn không xác thực nhận, là khôi lỗi vẫn là Phong điện chủ, nghe nói như thế, đã trăm phần trăm xác nhận, khẳng định là người sau không thể nghi ngờ."),i("br"),i("br"),n._v("Võ kỹ điện khôi lỗi mặc dù thực lực không yếu, có thể... Cấp bậc là cố định, không có khả năng vừa đánh vừa tăng thực lực lên... Trừ phi, là cường giả ngụy trang."),i("br"),i("br"),n._v("“Đi qua nhìn một chút...”"),i("br"),i("br"),n._v("Lại không chú ý, chiến thắng khôi lỗi vẻ mặt vẫn chưa thỏa mãn Trương Huyền, Hình đường chủ đám người vội vã hướng thông đạo đi đến, tiến vào bên trong, chỉ nhìn một cái, tất cả đều da mặt run mạnh."),i("br"),i("br"),n._v("Chỉ thấy Phong điện chủ máu me khắp người cắm ở trên vách tường, quần áo trên người cũng bị lực lượng cuồng bạo, xé rách tựa như vải, nói cỡ nào chật vật, liền có bao nhiêu chật vật."),i("br"),i("br"),n._v("“Quả nhiên là Lão Phong...”"),i("br"),i("br"),n._v("Đi tới trước mặt, Ngụy điện chủ nối một chút, lập tức xác nhận đi ra, cổ tay khẽ đảo, lấy ra một cái đan dược cho ăn đi xuống, đồng thời thôi thúc chân khí, trợ giúp luyện hóa."),i("br"),i("br"),n._v("Thời gian không dài, hôn mê Phong điện chủ tỉnh lại."),i("br"),i("br"),n._v("“Các ngươi làm sao tại đây?”"),i("br"),i("br"),n._v("Vẻ mặt mê man, Phong điện chủ nói."),i("br"),i("br"),n._v("“Ngươi mới vừa rồi là không phải khảo hạch một thanh niên, sau đó... Bị đánh?”"),i("br"),i("br"),n._v("Hình đường chủ nói."),i("br"),i("br"),n._v("“Đúng vậy a, cái này tới khảo hạch người, đối Đại Tinh La Chỉ Lực nắm giữ cực kỳ kiên cố, cùng cấp bậc ta, sử dụng tương đồng võ kỹ, chiến thắng chẳng qua! Tò mò, lặng lẽ tăng lên lực lượng... Ai biết cái tên này, thấy ta thay đổi tu vi, đột nhiên xông lại, một ngón tay liền đem ta đả thương, sau đó, sau đó... Ta liền cái gì đều không nhớ rõ...”"),i("br"),i("br"),n._v("Phong điện chủ hơi đỏ mặt."),i("br"),i("br"),n._v("Thấy có người có thể đem Đại Tinh La Chỉ Lực tu luyện tới cảnh giới như thế, thấy cái mình thích là thèm, muốn cùng đối phương cỡ nào đọ sức một phen, phát huy ra chỉ lực lớn nhất sức chiến đấu, kết quả... Còn không có đem thực lực của đối phương bức đi ra, người ta liền xông lại, trước đem hắn đánh ngất xỉu..."),i("br"),i("br"),n._v("Suy nghĩ một chút đều cảm thấy hổ thẹn."),i("br"),i("br"),n._v("“Ngươi nha...”"),i("br"),i("br"),n._v("Nghe được giải thích, Hình đường chủ mặt mũi bất đắc dĩ."),i("br"),i("br"),n._v("Đường đường Chiến sư đường điện chủ, bị một cái Thánh vực nhất trọng gia hỏa đánh thành như vậy... Thật là khổ cực."),i("br"),i("br"),n._v("Bất quá, vừa nghĩ tới ba người bọn họ trong lòng điện, đồng dạng bị điên cuồng đánh... Lập tức cân bằng xuống."),i("br"),i("br"),n._v("“Đúng rồi, các ngươi làm sao biết ta hôn mê? Người thanh niên kia là ai? Hình như không phải chúng ta Chiến sư đường người a?”"),i("br"),i("br"),n._v("Thấy đường chủ cùng hai vị điện chủ tự mình tới, Phong điện chủ có chút hiếu kỳ."),i("br"),i("br"),n._v("Chiến sư đường người, mặc dù không nói biết hết, nhưng cũng có thể nhận ra bảy tám phần, vị thanh niên này rất xa lạ, hẳn không phải là trong đường."),i("br"),i("br"),n._v("“Hắn là đường chủ mời đến hỗ trợ củng cố nguyên thần Khải Linh sư...”"),i("br"),i("br"),n._v("Liêu điện chủ giải thích."),i("br"),i("br"),n._v("“Khải Linh sư?”"),i("br"),i("br"),n._v("Phong điện chủ ngây người: “Khải Linh sư, đối võ kỹ lĩnh ngộ cao thâm như vậy? Cùng cấp bậc ta không phải là đối thủ?”"),i("br"),i("br"),n._v("Hắn nhưng là Chiến sư đường võ kỹ điện điện chủ, một thân tu vi, toàn bộ Thanh Nguyên đế quốc đều sắp xếp bên trên gần phía trước."),i("br"),i("br"),n._v("Thực lực như thế, cho dù đè thấp tu vi, tất nhiên cũng sẽ không quá yếu... Đối phương một cái Khải Linh sư, không chỉ cùng cấp bậc nghiền ép hắn, tăng cao tu vi, đều không có chặn lại, bị tuỳ tiện đánh bất tỉnh..."),i("br"),i("br"),n._v("“Là thật... Vị này Tôn sư, không riêng gì Khải Linh sư, còn là một vị danh sư, tình huống là như vậy...” Thấy hắn mặt mũi không hiểu, Hình đường chủ đành phải đem trước chuyện phát sinh, kỹ càng giới thiệu một lần."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Phong điện chủ thân thể cứng ngắc, khó tin: “Học mười cái hô hấp Đại Tinh La Chỉ Lực, so ta cái này nghiên cứu mấy trăm năm đều muốn khắc sâu?”"),i("br"),i("br"),n._v("“Thiên tài chân chính, là không thể dùng chúng ta tư duy để cân nhắc, đi, ta mang ngươi nhận thức một chút cái này Tôn sư, các ngươi cũng coi như không đánh nhau thì không quen biết!”"),i("br"),i("br"),n._v("Thấy hắn sắp bị đả kích đánh mất lòng tin, Hình đường chủ vội nói."),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Khôi phục một chút, Phong điện chủ lúc này mới gật gật đầu, mấy người đi ra thông đạo, vừa rời đi gian phòng, liền thấy trước mắt tất cả võ kỹ điện Chiến sư, đồng loạt quỳ mọp xuống đất, nguyên một đám nhìn về phía cách đó không xa thanh niên, tràn đầy thành kính cùng hưng phấn."),i("br"),i("br"),n._v("“Cảm tạ lão sư giúp chúng ta giải thích nghi hoặc...”"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1136"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1138"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);