(window.webpackJsonp=window.webpackJsonp||[]).push([[1684],{2038:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-438-hien-vien-đai-tran"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-438-hien-vien-đai-tran"}},[n._v("#")]),n._v(" Chương 438: Hiên Viên đại trận")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 438: Hiên Viên đại trận"),t("br"),t("br"),n._v("Đinh Mục mới vừa tuyên bố bản thân trở thành quốc vương nắm giữ chí cao vô thượng quyền lợi, liền đến cái này, toàn bộ tế thiên đài tất cả mọi người choáng váng."),t("br"),t("br"),n._v("Vạn quốc liên minh cách nơi này chừng mấy chục vạn dặm, coi như mới cưỡi Chí Tôn đỉnh phong man thú, cũng cần hơn mười ngày mới có thể đi tới, trực tiếp truyền đến đặc sứ thủ lệnh... Không khỏi quá nhanh đi!"),t("br"),t("br"),n._v("“Cái này... Không có khả năng! Không có khả năng...”"),t("br"),t("br"),n._v("Tròng mắt gần rơi trên mặt đất, Đinh Mục đều muốn điên rồi."),t("br"),t("br"),n._v("Chơi ta có phải không?"),t("br"),t("br"),n._v("Lúc này mới mới vừa lên làm quốc vương, làm cho đối phương không dám động thủ, mắt thấy thắng lợi trong tầm mắt, đến như vậy một màn, còn thế nào hiệu lệnh thiên hạ? Làm cho người tin phục?"),t("br"),t("br"),n._v("Phong hào vương quốc, là vạn quốc liên minh phong, người sau không cho thừa nhận, nói nhiều hơn nữa cũng vô ích ah!"),t("br"),t("br"),n._v("“Bệ hạ, cái này... Là thật đặc sứ thủ lệnh, ta đã từng thấy qua...”"),t("br"),t("br"),n._v("Thấy hắn hoài nghi, một cái đại thần một mặt khẩn trương đi tới."),t("br"),t("br"),n._v("Đặc sứ thủ lệnh, thay mặt trời tuần sát, vạn quốc liên minh bốn chữ, mang theo lấy đặc thù uy nghiêm, bất kỳ người nào đều bắt chước không ra được."),t("br"),t("br"),n._v("Chỉ nhìn liếc mắt liền biết, tuyệt đối là thật."),t("br"),t("br"),n._v("“Người tới, tả tướng hồ ngôn loạn ngữ, ảnh hưởng nền tảng lập quốc, ngay tại chỗ trượng giết!”"),t("br"),t("br"),n._v("Vốn là phát điên, thấy cái tên này như thế không có mắt sắc, Đinh Mục bỗng nhiên quay đầu, lớn tiếng quát lớn."),t("br"),t("br"),n._v("“Điện hạ...”"),t("br"),t("br"),n._v("Mọi người ngươi nhìn ta, ta nhìn ngươi, cũng không biết nên tuân thủ vẫn là cự tuyệt."),t("br"),t("br"),n._v("“Các ngươi...”"),t("br"),t("br"),n._v("Thấy hắn vị này quốc vương ra lệnh, không có người để ý tới, Đinh Mục khí hai mắt đỏ thẫm, đang muốn tiếp tục, liền nghe đến một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Vạn quốc liên minh không cho thừa nhận, ngươi cái này quốc vương liền là giả, không có sẽ nghe theo ngươi được mệnh lệnh!”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy Trương Huyền đã đi tới cách đó không xa, lạnh lùng nhìn qua."),t("br"),t("br"),n._v("Kỳ thật đến bây giờ hắn cũng có chút nghi hoặc."),t("br"),t("br"),n._v("Vốn là đã làm dự tính xấu nhất, mặc kệ đối phương có phải hay không quốc vương, đều sẽ ra tay, không nghĩ tới sẽ xuất hiện vạn quốc liên minh không thừa nhận một màn này."),t("br"),t("br"),n._v("Thứ này xuất hiện, đại biểu đối phương quốc vương thân phận danh bất chính, ngôn bất thuận, coi như giết, cũng không ai dám nói cái gì."),t("br"),t("br"),n._v("“Không nghe ta mệnh lệnh lại như thế nào? Ngươi chẳng lẽ cho là ta không có bọn hắn, ta sẽ sợ ngươi? Thực lực của ngươi mặc dù rất lợi hại, nhưng là muốn giết ta, cũng không dễ dàng như vậy!”"),t("br"),t("br"),n._v("Không có quốc vương vị trí, thuộc hạ lại mệnh lệnh không động đậy, Danh Sư đường giờ phút này lại không đứng ở bên phía hắn, Đinh Mục biết, loại trừ liều mạng, đã không có con đường."),t("br"),t("br"),n._v("Mặt mũi dữ tợn như là sói đói, gắt gao nhìn chằm chằm thanh niên trước mắt, đột nhiên đưa tay về phía trước, đem vương vị trước ngọc tỉ, cầm tại lòng bàn tay."),t("br"),t("br"),n._v("Ngọc tỉ nơi tay, cả người khí chất thay đổi, phảng phất thành một tòa núi cao, trấn áp tứ phương."),t("br"),t("br"),n._v("Khí tức mạnh, ngay cả nửa bước Hóa Phàm đều muốn tạm lánh hắn phong."),t("br"),t("br"),n._v("“Ta Đinh thị một môn, có thể có được phương viên mấy chục vạn cây số đất màu mỡ thành lập vương quốc, được tứ phong số, truyền thừa ngàn năm không ngã, những vương quốc khác không dám tới tập, cũng không phải là dựa dẫm đơn giản man lực, mà là... Trận pháp!”"),t("br"),t("br"),n._v("“Hiên Viên vương thành, trải qua nhiều năm giữ gìn, các loại kiến trúc, dung hợp nhật nguyệt tinh thần, sớm đã tạo thành một cái tứ tinh sơ kỳ đại trận! Muốn giết ta, ngươi trước hết chết đi! Coi như làm không được quốc vương, cũng sẽ kéo ngươi chôn cùng!”"),t("br"),t("br"),n._v("Rít lên một tiếng, ngọc trong tay tỉ bay lên, trong nháy mắt dẫn động trận pháp, toàn bộ vương thành, giống như là bị kích hoạt, một đạo cường đại đến cực điểm uy áp, từ không trung hạ xuống tới."),t("br"),t("br"),n._v("Tất cả mọi người ở đây, tất cả đều cảm thấy trên người căng thẳng, tựa như bị cái nào đó trận pháp cầm giữ thân hình, chỉ cần loạn động, liền sẽ bị tuỳ tiện chém giết."),t("br"),t("br"),n._v("“Là... Hiên Viên đại trận... Hắn thế mà khởi động Hiên Viên đại trận!”"),t("br"),t("br"),n._v("Trong đám người, một cái đại thần sắc mặt trắng nhợt, toàn thân mềm nhũn, ngồi ngay đó."),t("br"),t("br"),n._v("“Hiên Viên đại trận, đó là cái gì?”"),t("br"),t("br"),n._v("Không ít người cũng không cảm kích, một cái nhịn không được hỏi."),t("br"),t("br"),n._v("“Truyền thuyết Hiên Viên vương quốc kiến quốc lão tổ, là một vị trận pháp cường đại sư. Nhất thống vương quốc về sau, thành lập quốc gia, chọn trúng nơi này, lấy sơn mạch, dòng sông, các loại kiến trúc làm trận cơ, kiến tạo một cái nghịch thiên đại trận. Coi như Hóa Phàm cảnh sơ kỳ cường giả, rơi vào trong đó, đều có thể nhẹ nhõm chém giết!”"),t("br"),t("br"),n._v("“Trận pháp này, do ngọc tỉ kích hoạt... Là toàn bộ vương quốc sau cùng thủ hộ. Một khi sử dụng, tất cả mọi người ở đây đều chống cự không được...”"),t("br"),t("br"),n._v("Vị này đại thần không ngừng run rẩy."),t("br"),t("br"),n._v("Vừa rồi Đinh Mục phân phó mọi người chém giết vị danh sư này, ai cũng không nghe lời, hiện tại tốt, khởi động đại trận, đem tất cả mọi người giam ở trong đó, thật muốn tính nợ, như thế nào ngăn cản?"),t("br"),t("br"),n._v("Nghe nói như thế, cảm nhận được chung quanh cuồng bạo tàn phá bừa bãi trận pháp, mọi người cũng tất cả đều hoảng sợ biến sắc."),t("br"),t("br"),n._v("Vốn cho rằng không có người nghe theo mệnh lệnh, bản thân thực lực lại cùng đối phương kém quá xa, đã không đáng để lo, thế nào đều không có nghĩ đến, phía sau còn ẩn tàng như vậy một tay."),t("br"),t("br"),n._v("Liền Hóa Phàm cảnh sơ kỳ đều có thể chém giết trận pháp, có lẽ đã đạt đến tứ phẩm!"),t("br"),t("br"),n._v("Thực lực không đạt tới, là tuyệt đối không trốn thoát được."),t("br"),t("br"),n._v("Nhìn tới... Lần này thật hết rồi!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Ngươi là... Vạn quốc liên minh đặc sứ?”"),t("br"),t("br"),n._v("Phía dưới hoảng thành một đoàn, không trung man thú trên lưng, Ngụy Dư Thanh nhìn về phía thiếu niên ở trước mắt, nắm đấm xiết chặt."),t("br"),t("br"),n._v("Theo Độc điện liền biết vị này “Công tử” thân phận rất cao, địa vị tôn sùng, nếu không cũng không có khả năng để Cổ Mục lão sư, đường đường tứ tinh độc sư, cung kính tòng mệnh."),t("br"),t("br"),n._v("Nhưng dù cho như thế... Thế nào đều cũng không nghĩ tới, lại là liên minh đặc sứ."),t("br"),t("br"),n._v("Cầm trong tay lệnh bài đặc sứ, như là liên minh quốc vương bệ hạ đích thân tới, bất kỳ cái gì chư hầu vương quốc dám can đảm chất vấn, trước tiên có thể giảo sát lại đến báo!"),t("br"),n._v(" Có thể nói, chỉ cần tại liên minh cảnh nội, có được vô thượng quyền lợi."),t("br"),t("br"),n._v("Mà cầm trong tay như vậy đại quyền người, cũng chỉ là cái mười mấy tuổi thiếu niên..."),t("br"),t("br"),n._v("Nếu không phải thấy tận mắt hắn ném ra lệnh bài, xuất hiện vừa rồi một màn kia, cũng không dám tin tưởng là thật."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("“Công tử” gật đầu, cũng không giải thích, mà là cúi đầu nhìn về phía phía dưới đã vận chuyển trận pháp, lông mày nhăn lại."),t("br"),t("br"),n._v("Một lát sau nhìn về phía bên người lão giả."),t("br"),t("br"),n._v("“Nhưng có biện pháp phá giải?”"),t("br"),t("br"),n._v("“Công tử biết, ta cũng không am hiểu trận pháp!”"),t("br"),t("br"),n._v("Kim Tòng Hải hơi đỏ mặt."),t("br"),t("br"),n._v("Mặc dù là Hóa Phàm cảnh cường giả, có thể chỉ tinh thông dùng độc, đối với trận pháp cũng không am hiểu, phía dưới trận pháp này, khổng lồ bao la, coi như chân chính tứ tinh sơ kỳ trận pháp sư tới, đều rất khó giải quyết, huống chi hắn."),t("br"),t("br"),n._v("“Trận pháp này, mượn nhờ sông núi địa thế, lại thêm bố trí thời gian lâu dài, chưa hề dùng qua, sớm đã hội tụ năng lượng cường đại, đừng nói ta, ta sợ... Coi như Khang đường chủ tới, cũng không có cái gì biện pháp!”"),t("br"),t("br"),n._v("Chần chờ một chút, vị này Cổ Mục lão sư, tiếp tục nói."),t("br"),t("br"),n._v("Coi như không am hiểu trận pháp, thân là vạn quốc liên minh Hóa Phàm cảnh cường giả, nhãn lực cùng kiến thức vẫn phải có, có thể tuỳ tiện nhìn ra trận pháp này đáng sợ."),t("br"),t("br"),n._v("Đừng nói hắn, e là cho dù Khang đường chủ, cũng không cách nào giải quyết."),t("br"),t("br"),n._v("Khang đường chủ không riêng gì Danh Sư đường đường chủ, cũng là toàn bộ vạn quốc liên minh lợi hại nhất trận pháp đại tông sư."),t("br"),t("br"),n._v("Hắn đều phá giải không được, đủ thấy đáng sợ."),t("br"),t("br"),n._v("“Ta biết trận pháp này lợi hại, cái kia... Ngươi nhưng có biện pháp cứu hắn?” “Công tử” nhịn không được nói."),t("br"),t("br"),n._v("Phá giải trận pháp cùng tại trong trận pháp cứu một người hoàn toàn khác biệt, người sau muốn đơn giản nhiều."),t("br"),t("br"),n._v("“Không có cách nào!”"),t("br"),t("br"),n._v("Kim Tòng Hải lần nữa lắc đầu, nói: “Trận pháp này, tích súc đại thế, uy lực vô cùng, sinh sát cướp đoạt, tất cả trận pháp sư một ý niệm! Muốn phá vỡ, chí ít cần mười sáu vị Hóa Phàm cường giả, ở riêng tại mười sáu cái phương vị... Coi như đơn thuần cứu người, cũng ít nhất phải năm cái ta loại thực lực này người, đồng thời ra tay, mới có thể ngăn cản...”"),t("br"),t("br"),n._v("Trận pháp uy lực lớn nhỏ, cấp bậc là trọng yếu nhất, tiếp theo, liền là trận pháp phạm vi cùng lớn nhỏ!"),t("br"),t("br"),n._v("Bố trí phạm vi càng lớn, cùng cấp bậc cũng là càng mạnh."),t("br"),t("br"),n._v("Đây cũng là vì cái gì trận bàn uy lực nhỏ nguyên nhân."),t("br"),t("br"),n._v("Phía dưới đại trận này, y theo toàn bộ Hiên Viên vương thành địa thế xây lên, chiếm diện tích không xuống mấy trăm cây số, lại tích súc hơn ngàn năm năng lượng, chưa hề dùng qua, một khi sử xuất tất nhiên long trời lở đất, để cho người ta ghé mắt."),t("br"),t("br"),n._v("Cho dù hắn là Hóa Phàm cường giả, gặp phải cường đại như thế sát trận, có thể tự vệ là tốt lắm rồi, cứu người... Căn bản không có khả năng!"),t("br"),t("br"),n._v("“Cái kia... Làm sao bây giờ? Ngươi biết, hắn liền là Liễu Trình!”"),t("br"),t("br"),n._v("Nghe bản thân vị này hộ vệ đều cứu không được, “Công tử” có chút lo lắng."),t("br"),t("br"),n._v("“Đừng có gấp, thực sự không được, cùng lắm thì ta liền xông thoáng cái thử một chút...” Kim Tòng Hải cắn răng."),t("br"),t("br"),n._v("Hắn biết “Liễu Trình” y sư đối “Công tử” ân tình, lần này chuyên môn theo vạn quốc liên minh đi ra, chính là vì tìm hắn, nếu là tận mắt thấy chết tại trước mặt, còn không hối hận chết?"),t("br"),t("br"),n._v("“Xông cái gì?”"),t("br"),t("br"),n._v("Hai người tiếng nói không rơi, trong phòng vang lên một cái giọng nghi ngờ, lập tức mọi người liền thấy Tôn Cường đã tu luyện xong, một lần nữa đứng dậy."),t("br"),t("br"),n._v("Lúc này Tôn Cường, chẳng những củng cố Ích Huyệt cảnh tu vi, thậm chí còn mở ra trọn vẹn ba chỗ huyệt đạo, khí tức mười phần, tinh thần sung mãn."),t("br"),t("br"),n._v("Trương Huyền vừa rồi sư ngôn thiên bẩm, đối với những khác cấp bậc người hiệu quả không lớn, nhưng đối đồng dạng Đỉnh Lực cảnh bất cứ lúc nào cũng sẽ đột phá hắn tới nói, có cực lớn trợ giúp."),t("br"),t("br"),n._v("“Nhà các ngươi thiếu gia bị Đinh Mục đại trận khốn trụ, vô cùng nguy hiểm, ngươi nếu có thể liên hệ Dương sư, nắm chặt thời gian, ta sợ... Hắn khả năng không kiên trì nổi!”"),t("br"),t("br"),n._v("Nhìn hắn tu luyện xong, “Công tử” vội nói."),t("br"),t("br"),n._v("Nói thật, vừa rồi ném ra đặc sứ thủ lệnh thời điểm, nên trực tiếp đem cái này Đinh Mục bắt lại."),t("br"),t("br"),n._v("Thật muốn làm như vậy, cũng không trở thành xuất hiện loại tình huống này."),t("br"),t("br"),n._v("Tới báo ân, cái gì ân đều không có báo, ngược lại hại ân nhân chết tại trước mặt, để hắn có gì mặt mũi trở về? (Hắn thấy, nếu như không phải hắn ném ra thủ lệnh, cũng sẽ không ép phải Đinh Mục cùng đường mạt lộ, trực tiếp kích hoạt đại trận)"),t("br"),t("br"),n._v("“Trận pháp?”"),t("br"),t("br"),n._v("Xuyên thấu qua cửa sổ nhìn xuống phía dưới liếc mắt, Tôn Cường một mặt không quan tâm: “Còn tưởng rằng cái đại sự gì, liền cái đồ chơi này, yên tâm đi, không có việc gì!”"),t("br"),t("br"),n._v("Không nghe lời này còn tốt, nghe nói như thế, một cái lảo đảo, Kim Tòng Hải thiếu chút nữa ngất đi: “Ngươi cũng đã biết trận pháp này là cái gì? Lại có bao nhiêu lợi hại?”"),t("br"),t("br"),n._v("Phía dưới thế nhưng là ngươi thiếu gia, bị Hóa Phàm cảnh cường giả đều không thể bài trừ trận pháp vây khốn, thế mà không có chút nào lo lắng, còn một mặt không quan trọng... Là trái tim lớn, vẫn là đầu óc có bệnh?"),t("br"),t("br"),n._v("“Không biết!”"),t("br"),t("br"),n._v("Tôn Cường bàn tay lớn vẫy một cái: “Cũng không cần biết, nho nhỏ trận pháp mà thôi, không tính là gì!”"),t("br"),t("br"),n._v("Thiên Huyền vương quốc liền trận pháp sư đều không có, hắn chỗ nào biết cái gì trận pháp, nói thật coi như đơn giản nhất, đặt ở trước mặt, đều xem không hiểu."),t("br"),t("br"),n._v("Mặc dù không biết, nhưng đại náo Lâm gia cảnh tượng, đã sớm nghe Triệu Nhã bọn người nói mất trăm lần, lại thêm tận mắt thấy thiếu gia một cước đạp ngừng Lộ Xung tổ trạch trận pháp."),t("br"),t("br"),n._v("Biết thiếu gia đối với trận pháp lý giải, không ai bằng."),t("br"),t("br"),n._v("Nói cách khác, thứ này đối với người khác tới nói kinh khủng đến cực điểm, nhưng đối với thiếu gia tới nói... Vô hiệu."),t("br"),t("br"),n._v("“Không tính là gì? Nho nhỏ trận pháp mà thôi? Ngươi chính là như vậy làm hạ nhân?”"),t("br"),t("br"),n._v("Sầm mặt lại, “Công tử” ngực nhấp nhô, cả người kém chút nổ tung."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-446"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-448"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);