(window.webpackJsonp=window.webpackJsonp||[]).push([[1693],{2048:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-446-phat-ro-mac-thien-tuyet"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-446-phat-ro-mac-thien-tuyet"}},[n._v("#")]),n._v(" Chương 446: Phát rồ Mạc Thiên Tuyết")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Thiên Vũ vương quốc."),t("br"),t("br"),n._v("“Bệ hạ, công chúa điện hạ theo Trương sư, đã đến Hiên Viên vương quốc!” Ám Ảnh vệ thống lĩnh ôm quyền."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Gật gật đầu, Mạc Thiên Tuyết nghĩ đến cái gì, nhịn không được than thở một tiếng: “Nha đầu này cái gì cũng tốt, liền là quá nặng tình, xem như đem của ta tất cả tâm huyết đều uổng phí!”"),t("br"),t("br"),n._v("Mạc Hoằng Nhất quật khởi mạnh mẽ, hắn lo lắng vương vị không gánh nổi, chuyên tâm muốn bồi dưỡng con cái bên trong thiên tài nhất Mạc Vũ, kế thừa chính mình vị trí, kết quả nha đầu, lại vì nhi nữ tư tình, quay người theo Trương sư đi, để hắn lại sốt ruột vừa bất đắc dĩ."),t("br"),t("br"),n._v("Thật đúng là con gái lớn không dùng được."),t("br"),t("br"),n._v("Trương sư thiên tài, độc nhất vô nhị, loại người này Thiên Vũ vương quốc khẳng định là không giữ được, đã không có hi vọng, làm gì lại đi cưỡng cầu?"),t("br"),t("br"),n._v("Còn không bằng an tâm đợi tại vương quốc, thật tốt tu luyện, mau chóng tăng thực lực lên, tốt kế thừa vương vị."),t("br"),t("br"),n._v("Hiện tại, Mạc Hoằng Nhất trở thành nhị tinh danh sư về sau, uy danh càng đậm, ngươi nhưng quay người rời đi, về sau người trong nước đều quên đi ngươi vị thiên tài này, còn thế nào kế vị?"),t("br"),t("br"),n._v("Thật không biết nha đầu này nghĩ như thế nào!"),t("br"),t("br"),n._v("“Công chúa mặc dù tùy hứng, nhưng đối bệ hạ mà nói nói gì nghe nấy, đưa Trương sư đến vạn quốc liên minh, hẳn là liền sẽ trở về!”"),t("br"),t("br"),n._v("Ám Ảnh vệ thống lĩnh nói."),t("br"),t("br"),n._v("Làm quốc vương bệ hạ tư nhân vệ đội, Mạc Thiên Tuyết tâm tư, hắn biết đến rõ rõ ràng ràng."),t("br"),t("br"),n._v("Muốn thuận lợi thành chương kế thừa quốc vương chi vị, không chỉ muốn thực lực mạnh, còn muốn có uy vọng."),t("br"),t("br"),n._v("Bệ hạ không nguyện ý công chúa ra ngoài, liền là muốn cho nàng dần dần tiếp nhận chính vụ, làm nhiều ra chút công tích, sau đó lại đem vị trí yên tâm truyền thụ, nàng ngược lại tốt, xoay người rời đi, nói liên tục đều không có nói, cũng khó trách tức giận."),t("br"),t("br"),n._v("“Chỉ mong đi!”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết cười khổ: “Nếu như nàng thật có thể bắt lấy Trương sư tâm, đi đưa tiễn cũng không quan trọng, đáng tiếc, Trương sư rồng phượng trong loài người, đối nàng không có nửa điểm ý tứ, liền xem như vạn dặm đưa tiễn, cũng là lãng phí thời gian!”"),t("br"),t("br"),n._v("Ám Ảnh vệ thống lĩnh không biết trả lời như thế nào."),t("br"),t("br"),n._v("Mặc dù không cùng vị này Trương sư chính diện tiếp xúc qua, nhưng thầm gặp qua mấy lần, rất nhiều sự tích cũng nghe không ít, biết loại siêu cấp thiên tài này chí hướng, không thể là vì nhi nữ tình trường vây khốn."),t("br"),t("br"),n._v("“Tốt, không nói cái này...”"),t("br"),t("br"),n._v("Đem buồn bực trong lòng dứt bỏ, Mạc Thiên Tuyết đang muốn tiếp tục nói chuyện, chỉ thấy một cái lão giả vội vã đi tới."),t("br"),t("br"),n._v("“Bệ hạ, bệ hạ...”"),t("br"),t("br"),n._v("Còn không có đi vào phòng, lão giả liền mặt mũi tràn đầy thấu đỏ, cả người kích động không ngừng run rẩy."),t("br"),t("br"),n._v("“Cừ lão, không phải để ngươi trông coi đưa tin tường ư? Gấp gáp như vậy tới đây làm gì?”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết nhướng mày."),t("br"),t("br"),n._v("Cừ lão, Mạc Cừ, vương thất lão nhân."),t("br"),t("br"),n._v("Lâm gia có có thể câu thông Hiên Viên vương quốc đưa tin tường, làm chờ chút vương quốc Thiên Vũ vương thất, cũng có một mặt, có thể theo Hiên Viên vương thành truyền lại tin tức tới, sớm biết thượng cấp quốc gia biến động, từ đó làm ra ứng đối."),t("br"),t("br"),n._v("Vị này Cừ lão, chính phụ trách đưa tin tường câu thông cùng liên hệ. Không chỉ có như thế, còn cùng Thiên Huyền vương quốc Tiền Các Lão đồng dạng, chưởng quản lấy toàn bộ vương quốc tin tức truyền lại cùng lưu thông."),t("br"),t("br"),n._v("Chức vụ này, có trách nhiệm sưu tập tin tức, bình thường phái một chút vãn bối đem tin tức đưa tới là được rồi, trừ phi gặp phải đại sự, nếu không không có khả năng tự mình tới."),t("br"),t("br"),n._v("“Bệ hạ, ra... Đại sự!”"),t("br"),t("br"),n._v("Dừng bước, Cừ lão mặt mũi tràn đầy kích động."),t("br"),t("br"),n._v("“Đại sự?” Mạc Thiên Tuyết nhìn qua: “Chuyện gì?”"),t("br"),t("br"),n._v("“Trương sư đến Hiên Viên vương thành, ngay hôm nay, đại náo vương thành, đem thái tử Đinh Mục cho... Giết!”"),t("br"),t("br"),n._v("Cừ lão nói."),t("br"),t("br"),n._v("“Giết thái tử Đinh Mục?” Sắc mặt trắng nhợt, Mạc Thiên Tuyết thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Phong hào vương quốc thái tử, địa vị tôn sùng, tuyệt không phải hắn loại này chờ chút vương quốc có thể so sánh được... Trực tiếp chém giết, cái này Trương sư sắp điên vẫn là chán sống rồi?"),t("br"),n._v(" “Vâng, không chỉ có như thế, vẫn là tại Đinh Mục thái tử kế vị đại điển bên trên động đắc thủ!”"),t("br"),t("br"),n._v("Không thấy được bệ hạ có chút đen kịt sắc mặt, Cừ lão tự mình trả lời: “Kết quả dẫn xuất lão tổ Đinh Hồng...”"),t("br"),t("br"),n._v("“Ngươi nói thế nhưng là một trăm năm trước liền danh chấn Hiên Viên Đinh Hồng bệ hạ? Hắn... Còn sống?” Mạc Thiên Tuyết nhoáng một cái."),t("br"),t("br"),n._v("Vị này Đinh Hồng bệ hạ, hơn một trăm năm trước liền đạt tới nửa bước Hóa Phàm, danh chấn thiên hạ, vốn cho rằng sớm đã qua đời, không nghĩ tới thế mà còn sống."),t("br"),t("br"),n._v("“Vâng, chẳng những còn sống, còn thành Hóa Phàm cảnh cao thủ...” Cừ lão nói."),t("br"),t("br"),n._v("“Hóa Phàm?” Mạc Thiên Tuyết thanh âm phát run: “Cái kia Trương sư...”"),t("br"),t("br"),n._v("Hắn chưa nói xong, nhưng ngụ ý rất đơn giản, Trương sư có lẽ đã dữ nhiều lành ít."),t("br"),t("br"),n._v("Ngươi chạy nhân gia trong nhà, đem người ta sắp kế vị quốc vương bệ hạ giết, nhân gia lão tổ làm sao có thể tha cho ngươi?"),t("br"),t("br"),n._v("Đối với vị này Trương sư, hắn vẫn là hiểu rất rõ, Tông sư đỉnh phong thực lực, mặc dù có thể chém giết nửa bước Chí Tôn, có thể cùng Hóa Phàm... Chênh lệch quá xa, quả thực liền là Thần Long cùng sâu kiến khác nhau."),t("br"),t("br"),n._v("Có lẽ vị này trăm năm trước liền danh chấn thiên hạ lão tổ vừa ra tay, ngay cả chạy trốn đi đều trốn không thoát, liền sẽ bị tại chỗ đánh chết."),t("br"),t("br"),n._v("“A, Trương sư không có việc gì, tại chỗ đột phá đến nửa bước Hóa Phàm, đem Đinh Hồng bệ hạ cho đánh cho tàn phế...”"),t("br"),t("br"),n._v("Cừ lão nói."),t("br"),t("br"),n._v("“Cái gì?” Mạc Thiên Tuyết thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Tông sư đỉnh phong thực lực, đột phá đến nửa bước Hóa Phàm? Ngươi đây không phải đang nói đùa?"),t("br"),t("br"),n._v("Hơn nữa, liền xem như nửa bước Hóa Phàm cùng chân chính Hóa Phàm cảnh, cũng chênh lệch rất tốt đẹp không tốt, làm sao có thể đem hắn đánh cho tàn phế?"),t("br"),t("br"),n._v("“Đối diện truyền lại tin tức như thế, ta đã xác nhận qua, không có sai lầm chút nào!” Cừ lão gật đầu: “Hơn nữa... Hơn nữa...”"),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("Cừ lão chần chờ một chút, nhớ lại trước đó đưa tin trên tường nội dung, dường như đến bây giờ cũng không dám tin tưởng: “Ta tới, chính yếu nhất liền là hướng ngươi bẩm báo chuyện này, Trương sư đánh bại Đinh Hồng bệ hạ về sau, đã để Mạc Vũ tiểu thư đăng cơ làm Hiên Viên vương quốc mới quốc vương bệ hạ, thành một đời Nữ Đế...”"),t("br"),t("br"),n._v("“Đăng cơ làm Đế? Hiên Viên vương quốc quốc vương?”"),t("br"),t("br"),n._v("Mạc Thiên Tuyết kém chút không có theo vương tọa bên trên ngã xuống."),t("br"),t("br"),n._v("Mới vừa nói xong Mạc Vũ trọng cảm tình, theo vị này Trương sư đi ra ngoài, làm trễ nải lôi kéo lòng người, gia tăng uy vọng, kết quả... Nhân gia chạy đến Hiên Viên vương quốc làm quốc vương!"),t("br"),t("br"),n._v("Muốn hay không ác như vậy?"),t("br"),t("br"),n._v("Hiên Viên vương quốc thế nhưng là thượng cấp của hắn quốc gia, cấp dưới chừng Thiên Vũ dạng này vương quốc hơn hai mươi cái, ở đây làm quốc vương..."),t("br"),t("br"),n._v("Thế nào như là đang nằm mơ?"),t("br"),t("br"),n._v("“Vũ nhi chỉ có Thông Huyền cảnh đỉnh phong tu vi, sao có thể làm phong hào vương quốc quốc vương?”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, Mạc Thiên Tuyết vội vàng nói."),t("br"),t("br"),n._v("Mạc Vũ rời đi thời điểm, mới Thông Huyền cảnh đỉnh phong, Tông sư đều không có đạt tới, thấp như vậy thực lực, tại Thiên Vũ vương quốc, đều rất khó trở thành quốc vương, làm Hiên Viên vương quốc, đây không phải muốn chết sao?"),t("br"),t("br"),n._v("“Cái này... Trương sư cũng cảm thấy thực lực của nàng thấp, tại chỗ quán đỉnh để nàng đột phá, Mạc Vũ công chúa hiện tại đã là... Chí Tôn đỉnh phong cường giả!”"),t("br"),t("br"),n._v("Cừ lão nói."),t("br"),t("br"),n._v("“Tại chỗ đột phá? Chí Tôn đỉnh phong?”"),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Cũng không ngồi yên được nữa, Mạc Thiên Tuyết rơi trên mặt đất, hai mắt si ngốc, tóc bị thu hạ đến đều không phát giác gì."),t("br"),t("br"),n._v("Thông Huyền cảnh đỉnh phong, tại chỗ đột phá đến... Chí Tôn đỉnh phong, Trương sư, ngươi có thể nói cho ta biết, đến cùng làm sao làm được ư?"),t("br"),t("br"),n._v("“Vũ nhi... Là thật có ánh mắt...”"),t("br"),t("br"),n._v("Qua không biết bao lâu, lúc này mới nuốt ngụm nước bọt, Mạc Thiên Tuyết vẻ mặt trắng bệch tự lẩm bẩm."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-454"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-456"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);