(window.webpackJsonp=window.webpackJsonp||[]).push([[1695],{2050:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-448-hoa-pham-canh-đang-cap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-448-hoa-pham-canh-đang-cap"}},[n._v("#")]),n._v(" Chương 448: Hóa Phàm cảnh đẳng cấp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Mây trắng như biển, thú đánh trời cao."),t("br"),t("br"),n._v("Cực lớn linh thú trên lưng, Trương Huyền đứng tại bên cửa sổ, ngắm nhìn nơi xa."),t("br"),t("br"),n._v("Đây là một đầu Hóa Phàm tam trọng linh thú, “Công tử” Triệu Phi Vũ tọa kỵ."),t("br"),t("br"),n._v("Biết vu hồn sư cùng Hồng Hải thành, Trương Huyền không có quá nhiều dừng lại, mang theo Triệu Nhã đám người, trực tiếp rời khỏi."),t("br"),t("br"),n._v("Mạc Vũ hiện tại là Hiên Viên vương quốc quốc vương, có vô số đại sự xử lý, tự nhiên lại không có thể cùng hắn cùng một chỗ đi xa, liền lưu tại vương thành, nhờ cậy Lạc Thiên Hồng giúp đỡ chiếu cố."),t("br"),t("br"),n._v("Vốn là muốn cưỡi Thiết Xỉ Khiếu Thiên thú, nghe được Hồng Hải thành cách nơi này không dưới trăm vạn dặm, lập tức coi như không có gì."),t("br"),t("br"),n._v("Khoảng cách xa như vậy, Khiếu Thiên thú chẳng qua Chí Tôn cảnh mà thôi, coi như ngày đêm không ngớt, cũng cần mấy tháng, thực sự quá lãng phí thời gian."),t("br"),t("br"),n._v("Mà Triệu Phi Vũ tọa kỵ, sớm đã đạt tới Hóa Phàm tam trọng, lưng cũng rộng rãi, mọi người ngồi ở phía trên, không hiện chen chúc, lại thêm nhất định phải tới đồng hành, Trương Huyền cũng là không có từ chối nữa."),t("br"),t("br"),n._v("Một đường tiến lên, đây đã là ngày thứ năm."),t("br"),t("br"),n._v("“Lão sư, ngươi không phải độc sư ư? Thế nào thành đặc sứ thuộc hạ...”"),t("br"),t("br"),n._v("Thật vất vả lần nữa gặp được sư phụ, Cổ Mục cũng theo sau, nghi ngờ nhìn về phía cách đó không xa Kim Tòng Hải."),t("br"),t("br"),n._v("Độc sư, người người nghe đến đã biến sắc, bình thường đều sẽ rời xa triều đình, không người hỏi thăm, chính mình vị lão sư này, thế nào ngược lại thành vị này “Công tử” mộ phủ khách, trung thành tuyệt đối?"),t("br"),t("br"),n._v("“Năm đó ta rời khỏi Độc điện, muốn đi báo thù, kết quả bị đối thủ ám toán, mặc dù đem hắn giết chết, nhưng cũng bản thân bị trọng thương, là công tử cha, đem ta cứu... Những năm này vì phòng ngừa người khác biết ta độc sư thân phận, đã rất ít khi dùng độc!” Kim Tòng Hải nói."),t("br"),t("br"),n._v("Cùng Cổ Mục nghĩ tới đồng dạng, độc sư bình thường rất ít hiệu trung người khác, không phải không nguyện ý, mà là ai thu loại người này, sẽ gặp phải kiêng kị, bị người cô lập... Thử nghĩ một chút, ai còn dám muốn?"),t("br"),t("br"),n._v("Kim Tòng Hải bị công tử cha cứu, một mực đi theo, vì phòng ngừa tiết lộ thân phận, đã không biết bao nhiêu năm chưa bao giờ dùng qua độc. Trong mắt người khác, hắn chỉ là cái cần cù chăm chỉ hạ nhân mà thôi, cũng không biết còn là một vị để cho người ta hoảng sợ tứ tinh độc sư."),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("Không để ý tới bọn hắn sư đồ trò chuyện, Viên Đào đám người đi tới."),t("br"),t("br"),n._v("Lúc này Viên Đào, mượn nhờ máu linh thú, Long tê huyết mạch lần nữa kích hoạt, thực lực có trên phạm vi lớn gia tăng, đã đạt tới Tông sư đỉnh phong, so với Triệu Nhã đều không hề yếu."),t("br"),t("br"),n._v("Về phần Trịnh Dương, Vương Dĩnh, Lưu Dương, Tôn Cường bốn người, Trương Huyền thì mỗi người ban cho một khỏa hạt sen."),t("br"),t("br"),n._v("Lần này chỉ là truyền thụ bản rút gọn Thiên Đạo công pháp, cũng không quán đỉnh."),t("br"),t("br"),n._v("Dù vậy, có Xích Diễm hạt sen loại này nghịch thiên bảo vật, lại thêm bản rút gọn Thiên Đạo công pháp, tiến bộ của bọn hắn cũng cùng uống nước bình thường dễ dàng, ngắn ngủi năm ngày thời gian, liền liên tục đột phá mấy cái cấp bậc, hiện tại cũng tất cả đều đạt đến Tông sư sơ kỳ."),t("br"),t("br"),n._v("Mặc dù cùng Triệu Nhã hai người so, hơi có vẻ không bằng, thực lực lại hết sức củng cố, không có chút nào phù phiếm cảm giác, càng quan trọng hơn là, hạt sen đến bây giờ liền một phần trăm năng lượng đều không có tiêu hao đến."),t("br"),t("br"),n._v("Có thể đoán được, thật đem bên trong năng lượng hấp thu sạch, coi như xung kích không được Chí Tôn đỉnh phong, cũng không kém nhiều."),t("br"),t("br"),n._v("Nhìn thấy trân quý như thế Xích Diễm hạt sen, tiện tay đưa người, càng làm cho học sinh, hạ nhân cấp tốc đột phá, Triệu Phi Vũ đám người nội tâm là sụp đổ, có điều, nương theo sụp đổ số lần càng ngày càng nhiều, cũng là dần dần quen thuộc."),t("br"),t("br"),n._v("Trước mắt vị này Trương Huyền, đối bọn hắn tới nói, đã không phải là có thể dùng lẽ thường để cân nhắc người."),t("br"),t("br"),n._v("Cùng hắn càng nghĩ càng mệt mỏi còn không bằng thản nhiên tiếp nhận."),t("br"),t("br"),n._v("“Phía trước đây chính là Hồng Hải thành!”"),t("br"),t("br"),n._v("Linh thú tiếp tục tiến lên, rất nhanh một cái thành phố khổng lồ xuất hiện tại mọi người tầm mắt."),t("br"),t("br"),n._v("Dán chặt lấy bình địa mặt, vô số kiến trúc giống như là khảm nạm ở trên mặt đất nhỏ bé hạt tròn, phản xạ mặt trời ánh sáng."),t("br"),t("br"),n._v("Hiên Viên vương thành coi như rất lớn, nhưng cùng cái này Hồng Hải thành so sánh, cái gì cũng không tính, như là vắng vẻ nông thôn thôn xá đồng dạng."),t("br"),t("br"),n._v("Không hổ là vạn quốc liên minh một trong thành thị lớn nhất, không nói cái khác, chỉ nói chiếm diện tích cùng lối kiến trúc, cũng không phải là phong hào vương quốc, chờ chút vương quốc có thể so sánh được."),t("br"),t("br"),n._v("“Hồng Hải thành hội tụ các phương thương nhân, không chỉ Hiên Viên vương quốc, Xích Hà vương quốc, Lưu Tô vương quốc... Rất nhiều phong hào vương quốc, đều ở nơi này tiến hành mậu dịch, từ đó kiếm lấy chênh lệch giá, thu hoạch kinh người lợi nhuận. Nơi này có thể nói là toàn bộ vương quốc liên minh, thương nghiệp phồn hoa nhất địa phương!”"),t("br"),t("br"),n._v("Triệu Phi Vũ giải thích."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Thiên Vũ vương quốc Tàng Thư khố thư tịch, có giới thiệu cái này Hồng Hải thành, mệnh danh thương nghiệp chi thành, vạn quốc liên minh cùng cấp dưới rất nhiều vương quốc ở giữa mậu dịch cùng câu thông, cơ bản đều ở nơi này."),t("br"),t("br"),n._v("Muốn nói lên phồn hoa trình độ, thậm chí còn vượt qua vương đô, Vạn Quốc thành."),t("br"),t("br"),n._v("“Vạn quốc liên minh không phải Hiên Viên vương quốc loại kia địa phương nhỏ, thực lực của ngươi đạt tới nửa bước Hóa Phàm, mặc dù cùng tuổi bên trong xem như không yếu, có thể ở đây, vẫn là muốn cẩn thận một chút cho thỏa đáng.”"),t("br"),t("br"),n._v("Triệu Phi Vũ vẻ mặt nghiêm túc dặn dò: “Hồng Hải thành Danh Sư đường, có tứ tinh danh sư tọa trấn, mặc dù chỉ là tứ tinh trung kỳ, nhưng chân chính thực lực, đã đạt tới Hóa Phàm tam trọng Âm Dương Kính, sâu không lường được!”"),t("br"),t("br"),n._v("“Âm Dương Kính?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Đã sớm biết võ giả cửu trọng phía trên liền là Hóa Phàm, Hóa Phàm đồng dạng chia làm cửu trọng, nhưng lại không biết cụ thể phân chia tỉ mỉ."),t("br"),n._v("“Hóa Phàm tổng cộng có cửu trọng, theo thứ tự là nhất trọng tăng thọ, nhị trọng nguyên khí, tam trọng Âm Dương, tứ trọng trọc thanh, ngũ trọng hợp linh, lục trọng cầu trời, thất trọng quy nhất, bát trọng đạp hư, cửu trọng tằm phong!”"),t("br"),t("br"),n._v("Biết hắn ý tứ, Triệu Phi Vũ giải thích: “Tăng Thọ cảnh, ngươi cũng đã biết, đạt tới cấp bậc này, lực lượng mặc dù duy trì tại mười vạn, cũng không gia tăng, tuổi thọ lại đột phá một trăm số lượng. Mỗi gia tăng một cái tiểu cấp bậc, liền sẽ tăng thêm 50 năm tuổi thọ, đạt tới đỉnh phong, có thể đủ đạt tới 350 tuổi nhiều.”"),t("br"),t("br"),n._v("“Về phần nhị trọng Nguyên Khí cảnh, là đem chân khí trong cơ thể thêm gần từng bước, chuyển hóa thành có thể điều động thiên địa tự nhiên nguyên khí. Nhất nguyên phục thủy, Vạn Tượng đổi mới... Nguyên khí cùng chân khí hoàn toàn khác biệt, cái trước đại biểu cho lực lượng cùng tiềm lực sinh mệnh, mà cái sau có thể dung nhập tự nhiên, cũng có thể theo trong tự nhiên thu hoạch, đã có thể thể hiện ra vô số bí truyền!”"),t("br"),t("br"),n._v("“Thật giống như một cái bình thường trang giấy, dung nhập nguyên khí, liền vô cùng có khả năng trở nên có linh tính, chính mình lơ lửng, bốn phía bay lượn. Bởi vậy, Nguyên Khí cảnh, cũng chính là chúng ta thường nói Pháp Lực cảnh, nguyên khí liền là pháp lực.”"),t("br"),t("br"),n._v("“Về phần tam trọng Âm Dương Kính, Âm Dương tương sinh, lực lượng hỗ trợ lẫn nhau, thực lực càng thêm cường đại. Đạt tới loại tu vi này, to lớn đã không thể giới hạn, đã có năng lực hủy thiên diệt địa! Tựa như xa xa ngọn núi, cũng có thể một chút đánh nát.”"),t("br"),t("br"),n._v("Triệu Phi Vũ đối tu luyện đẳng cấp, hiểu rõ rất nhiều, êm tai nói."),t("br"),t("br"),n._v("“Ngọn núi cũng có thể một kích mà nát?”"),t("br"),t("br"),n._v("Trịnh Dương đám người líu lưỡi."),t("br"),t("br"),n._v("Đạt tới Tông sư, bọn hắn đã cảm thấy rất lợi hại, mấy ngàn kí lô nham thạch, có thể nhẹ nhõm ném đi, một quyền đánh ra, sắt thép đều có thể vặn vẹo."),t("br"),t("br"),n._v("Có thể cái này mạnh hơn, cũng chỉ là thân thể, thân thể cực hạn, muốn một quyền hủy diệt ngọn núi, vẫn là xa xa làm không được, cái này đã cùng Thần Linh không sai biệt lắm."),t("br"),t("br"),n._v("Khó trách Hóa Phàm cảnh cũng gọi linh cảnh, loại năng lực này, quả thực không phải võ giả có thể chống lại, coi như nửa bước Hóa Phàm, cũng còn thiếu rất nhiều."),t("br"),t("br"),n._v("“Về phần Hóa Phàm tứ trọng Trọc Thanh cảnh, nguyên khí phân chia thanh khí trọc khí, linh hồn phân nặng nhẹ, thật tốt tu luyện, hoàn toàn có thể để cho nguyên khí phẩm chất tiến thêm một bậc. Nói cách khác, đây là người tu luyện, một lần có thể tấn cấp nguyên khí (chân khí) đẳng cấp cơ hội, nếu như có thể theo hạ tam phẩm tấn thăng đến trung tam phẩm, không cần ta nói, tác dụng bao lớn có thể nghĩ!”"),t("br"),t("br"),n._v("Triệu Phi Vũ cười một tiếng, nói tiếp: “Vạn Quốc thành tứ tinh đỉnh phong danh sư Khang đường chủ, chính là cái này cấp bậc. Đạt tới loại tu vi này, tại vạn quốc liên minh mới có thể được cho hoành hành, ai cũng không dám đắc tội. Còn, ngũ trọng Hợp Linh cảnh, ta cũng chỉ là nghe nói, đã vượt qua vạn quốc liên minh phạm vi, tình huống cụ thể, cũng không rõ lắm.”"),t("br"),t("br"),n._v("“Thì ra là thế!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hóa Phàm cửu trọng, cùng võ giả cửu trọng đồng dạng, đồng dạng nhất trọng nhất trọng thiên, tu luyện, cần không ngừng cố gắng cùng giữ vững."),t("br"),t("br"),n._v("Đương nhiên, đối với hắn mà nói, vẫn là cần tìm kiếm vô số bí tịch, hình thành Thiên Đạo công pháp."),t("br"),t("br"),n._v("“Đến, đi xuống đi!”"),t("br"),t("br"),n._v("Nói chuyện công phu, Hồng Hải thành đã đi tới trước mặt, man thú cánh vung vẩy, thẳng tắp hạ xuống tới."),t("br"),t("br"),n._v("Cùng cái khác vương thành đồng dạng, vào thành cần giao nộp nhất định phí tổn, Trương Huyền lấy ra danh sư huy chương, loại này tiêu xài liền phải để tránh trừ."),t("br"),t("br"),n._v("Đi vào Hồng Hải thành đường phố rộng rãi, chung quanh phi thường náo nhiệt, khắp nơi đều là bán ra các loại vật phẩm."),t("br"),t("br"),n._v("Trước đó tại Lâm gia trân quý nhất Quỷ cấp đỉnh phong vũ khí, trong này tùy tiện một cái cửa hàng đều có thể mua sắm."),t("br"),t("br"),n._v("“Tại Hồng Hải thành, kim tệ cơ bản liền vô tác dụng, dùng để giao dịch nhiều nhất là chương trình học ngọc phù cùng linh thạch.”"),t("br"),t("br"),n._v("Triệu Phi Vũ nói: “Danh sư chương trình học ngọc phù, theo đẳng cấp phân, tứ tinh bậc, bài học lúc có thể hối đoái một trăm giờ dạy học tam tinh cấp, tam tinh cấp có thể hối đoái một trăm giờ dạy học nhị tinh bậc... Cứ thế mà suy ra. Còn linh thạch, mười cái khoảng chừng, có thể mua sắm tam tinh cấp chương trình học ngọc phù một khối!”"),t("br"),t("br"),n._v("“Dùng chương trình học ngọc phù làm giao dịch tiền tệ?”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Khó trách danh sư địa vị tôn sùng, bị người kính trọng, giờ dạy học của bọn họ tương đương với tiền tệ, coi như không muốn người tu luyện, cũng không có khả năng cùng tiền tài băn khoăn đi."),t("br"),t("br"),n._v("Bất quá, suy nghĩ kỹ một chút cũng là hiểu được."),t("br"),t("br"),n._v("Danh sư giờ dạy học, có thể để cho người tu luyện giải quyết vô số trong vấn đề tu luyện cùng chướng ngại, đừng nói một canh giờ, coi như vài phút, thường thường đều có thể đối đột phá, đưa đến tác dụng mang tính chất quyết định."),t("br"),t("br"),n._v("Lấy thứ này khi tiền tệ giao dịch, đích xác là công bình nhất, nhất vô tư."),t("br"),t("br"),n._v("“Nghe nói nơi này đào ra một đầu Vô Hồn Kim Nhân, nếu như ta muốn mua lời nói, hẳn là đi nơi nào?”"),t("br"),t("br"),n._v("Lại nghe Triệu Phi Vũ giảng giải một phen, đối cái này Hồng Hải thành có nhất định biết, Trương Huyền mở miệng hỏi."),t("br"),t("br"),n._v("“Vô Hồn Kim Nhân loại bảo vật này, vô cùng trân quý, bình thường đều sẽ do Giám Bảo các có trách nhiệm đấu giá, nếu như đoán không sai, hẳn là ngay tại Giám Bảo các bên trong!” Triệu Phi Vũ suy nghĩ một chút nói."),t("br"),t("br"),n._v("“Giám Bảo các?” Trương Huyền nhìn qua: “Thế nhưng là giám bảo sư hội tụ địa phương?”"),t("br"),t("br"),n._v("Lúc trước tại Thiên Huyền vương quốc thời điểm, từng gặp được một cái “Giám bảo đại sư”... Mặc Dương."),t("br"),t("br"),n._v("Cái tên này chỉ là cái học đồ mà thôi, nhưng giả thần giả quỷ, dùng các loại thủ đoạn lừa gạt tiền."),t("br"),t("br"),n._v("Bất quá, hết lần này tới lần khác có không ít người tin tưởng."),t("br"),t("br"),n._v("Giám bảo sư mặc dù chỉ là trong đó cửu lưu chức nghiệp, lại bởi vì có thể xác định bảo vật trân quý trình độ, mà chạm tay có thể bỏng."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-456"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-458"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);