(window.webpackJsonp=window.webpackJsonp||[]).push([[1696],{2051:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-449-giam-bao-cac"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-449-giam-bao-cac"}},[n._v("#")]),n._v(" Chương 449: Giám Bảo các")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Cùng nhau đi tới, đối với loại nghề nghiệp này người, thấy tương đối ít, cũng là không có coi ra gì, không có nghĩ đến cái này Hồng Hải thành, lại có Giám Bảo các, cái kia nên có chính thức giám bảo sư cùng khảo hạch giám bảo sư phương pháp."),t("br"),t("br"),n._v("Thực sự không được, ngã là có thể kiểm tra một cái chơi đùa, kiếm chút thu nhập thêm."),t("br"),t("br"),n._v("Nếu không, coi như biết Vô Hồn Kim Nhân, chỉ sợ cũng không có tiền mua sắm."),t("br"),t("br"),n._v("Nếu như cho những người khác biết ý nghĩ này, khẳng định sẽ nói không ra lời."),t("br"),t("br"),n._v("Giám bảo sư, mặc dù không tại thượng cửu lưu, nghĩ muốn khảo hạch, nhưng cực kỳ khó khăn, cần đối bảo vật có cực sâu hiểu rõ mới được, mỗi một cái chính thức giám bảo sư, không có đối bảo vật mấy chục năm nghiên cứu, đều khó có khả năng khảo hạch thành công."),t("br"),t("br"),n._v("Cái tên này lại bởi vì không có tiền liền muốn kiểm tra một cái thử một chút, nói cho bất luận kẻ nào nghe, đều sẽ im lặng tới cực điểm."),t("br"),t("br"),n._v("“Đi Giám Bảo các!”"),t("br"),t("br"),n._v("Có ý nghĩ, không chần chờ chút nào, tìm người hỏi thăm một chút vị trí, Trương Huyền cùng mọi người cùng một chỗ, đi tới."),t("br"),t("br"),n._v("Giám Bảo các vàng son lộng lẫy, không chỉ giám bảo càng là một cái cự đại thị trường giao dịch, các loại vật phẩm san sát, dòng người cuồn cuộn, còn chưa tới đến trước mặt, liền bị trước mắt huy hoàng cảnh tượng rung động."),t("br"),t("br"),n._v("“Đây là Giám Bảo các kinh doanh thương hội, có thể vào ở nơi này, rất nhiều đều là đồ thật. Bởi vậy, cái này cũng thành toàn bộ vạn quốc liên minh to lớn nhất thị trường giao dịch!”"),t("br"),t("br"),n._v("Nhìn thấy trước mắt vị này siêu cấp thiên tài, mặt mũi tràn đầy kinh ngạc, Triệu Phi Vũ có chút đắc ý."),t("br"),t("br"),n._v("Giám Bảo các, bởi vì hắn đặc biệt chức nghiệp đặc tính, để cho người ta theo ở sâu trong nội tâm sinh ra tin cậy, thương hội tự nhiên cũng là mở phong sinh thủy khởi, càng nhiều người nguyện ý tốn giá cao tiền mua sắm, chỉ vì an tâm."),t("br"),t("br"),n._v("Chính vì vậy, giám bảo sư mặc dù không phải lên cửu lưu, nhưng thật là kiếm lợi nhiều nhất mấy cái chức nghiệp một trong."),t("br"),t("br"),n._v("Không ít danh sư, lựa chọn phụ trợ chức nghiệp, bình thường ưa thích cửa này."),t("br"),t("br"),n._v("Bất quá, muốn giám bảo, nhất định phải đối lịch sử, điển cố, đồ vật, chất liệu, lai lịch... Biết đến rất nhiều, vô số tri thức hòa hợp một lò mới có thể có thành tựu, khảo hạch nhất tinh dễ dàng, khảo hạch cao cấp hơn khó càng thêm khó."),t("br"),t("br"),n._v("Cho nên, muốn khảo hạch giám bảo sư rất nhiều người, nhưng đạt tới tam tinh lấy trở lên nhưng ít càng thêm ít, thậm chí tại về số lượng liền danh sư đều kém xa tít tắp."),t("br"),t("br"),n._v("Nguyên nhân chính là nguyên nhân này, Giám Bảo các số lượng cũng không nhiều, coi như Thiên Vũ vương thành loại địa phương này, đều không có một tòa, một cái giả mạo đại sư Mặc Dương cũng có thể để nhiều người như vậy vây đỡ."),t("br"),t("br"),n._v("“Giám bảo sư, liền có thể bảo chứng nhất định có thể phán đoán chính xác?”"),t("br"),t("br"),n._v("Một bên Tôn Cường nhịn không được nói."),t("br"),t("br"),n._v("Hắn tại cửa hàng khai qua cửa hàng, biết rất nhiều chuyện ẩn ở bên trong, coi như giám bảo sư lại như thế nào? Cũng có nhìn nhầm thời điểm, không có thể bảo chứng mỗi một cái đều phán đoán chính xác đi!"),t("br"),t("br"),n._v("Dù sao, người không phải máy móc, chung quy có bị lừa thời điểm."),t("br"),t("br"),n._v("Thường tại bờ sông đi đâu có không ướt giày, lợi hại hơn nữa giám bảo sư, cũng không thể cam đoan cả một đời không xuất hiện một lần sai lầm."),t("br"),t("br"),n._v("“Giám bảo sư, nhận hạn chế cấp bậc cùng nhãn lực, quả thực không có thể bảo chứng mỗi một kiện vật phẩm đều phán định chính xác! Bất quá, gặp được loại tình huống này, bình thường đều sẽ phong tồn, sau đó mời cái khác giám bảo sư cùng một chỗ giám định, nếu như được mời mời mấy người, cũng cho không ra xác định đáp án, cũng chỉ có thể cho ra tổng hợp ý kiến, làm làm tham khảo.”"),t("br"),t("br"),n._v("Triệu Phi Vũ nói."),t("br"),t("br"),n._v("“Này bằng với không cho đáp án ah!” Tôn Cường lắc đầu."),t("br"),t("br"),n._v("“Giám bảo sư một đời chỉ có ba lần phán đoán sai lầm cơ hội, một khi vượt qua, liền sẽ tự động hủy bỏ cái nghề nghiệp này, không gặp được có thể xác định đồ vật, là không dám tùy tiện cho ra kết luận!” Kim Tòng Hải chen vào nói."),t("br"),t("br"),n._v("“Ba lần?”"),t("br"),t("br"),n._v("“Ừm, theo trở thành chính thức giám bảo sư bắt đầu, chỗ giám định mỗi một dạng bảo vật, đều muốn ghi chép đệ đơn, một khi thứ này xuất hiện vấn đề, chẳng khác nào xuất hiện sai lầm. Chỉ cần vượt qua ba lần, mặc kệ về sau đạt đến mấy sao, đều sẽ trực tiếp hủy bỏ tư cách!”"),t("br"),t("br"),n._v("Kim Tòng Hải nói tiếp."),t("br"),t("br"),n._v("Những năm này hắn gặp qua không ít giám bảo sư, bởi vì bị lừa mà bị thủ tiêu tư cách, trong lòng có chút buồn bã."),t("br"),t("br"),n._v("Rất nhiều kiếm lời cả đời thanh danh, đến già xuất hiện vấn đề."),t("br"),t("br"),n._v("Mọi người yên lặng."),t("br"),t("br"),n._v("Xem ra người người đều hâm mộ giám bảo sư, mà cái nghề nghiệp này, cũng không có tốt như vậy làm."),t("br"),t("br"),n._v("Vượt qua ba lần sai lầm, liền hủy bỏ tư cách, có thể... Ai có thể bảo chứng, mỗi lần giám bảo, cũng có thể làm không sai chút nào?"),t("br"),t("br"),n._v("“Kỳ thật mỗi cái chức nghiệp đều như thế, danh sư càng lớn, chỉ điểm sai lầm một chỗ, liền vô cùng có khả năng tuyên bố mất sạch, lại không người tin tưởng!” Thấy mọi người đồng tình giám bảo sư, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn không đến đều thế giới này trước, tiền thân cũng là bởi vì chỉ điểm sai lầm, để học sinh tẩu hỏa nhập ma, lúc này mới huyên náo dư luận xôn xao, liền cái học sinh đều chiêu không đến."),t("br"),t("br"),n._v("“Đúng vậy a, y sư cũng thế, tính sai lầm một chỗ, liền vô cùng có khả năng hại chết một vị sinh mệnh...” Triệu Phi Vũ đi theo gật đầu, đồng thời nhìn về phía thiếu niên ở trước mắt."),t("br"),t("br"),n._v("Cái tên này thoạt nhìn cùng nàng không chênh lệch nhiều, sao có thể nhẹ nhõm giải quyết liền tứ tinh, ngũ tinh y sư đều không giải quyết được Tiên Thiên bắp thịt không có lực lượng? Từ đó chữa khỏi bệnh của nàng chứng?"),t("br"),t("br"),n._v("“Thật nhiều đồ vật...”"),t("br"),t("br"),n._v("“Thật xinh đẹp...”"),t("br"),t("br"),n._v("Ngay tại hiếu kỳ, đến cùng thế nào học tập y thuật, lại có gì loại dựa dẫm, liền nghe đến Viên Đào đám người thanh âm hưng phấn vang lên."),t("br"),t("br"),n._v("Nói chuyện công phu, mấy người đã dọc theo trang trí huy hoàng đại sảnh đi vào, trước mắt khắp nơi đều là bán ra vật phẩm tiểu thương, bao gồm mấy chục loại chức nghiệp."),t("br"),t("br"),n._v("Dược vật, đan dược, lô đỉnh, vũ khí, trận bàn... Tinh la mật bố, rực rỡ muôn màu, để cho người ta không kịp nhìn."),t("br"),t("br"),n._v("Vô số bình thường muốn gặp cũng không gặp được trân bảo, lóe ra ánh sáng, kích thích thần kinh người."),t("br"),t("br"),n._v("“Đi qua, đi ngang qua, đều tới xem một chút, mới nhất , có thể tự động phán định bảo vật giá trị, cho ngươi cái mua sắm căn cứ cùng dựa vào, chỉ cần mười cái linh thạch, không mua được ăn thiệt thòi, cũng không mua được mắc lừa!”"),t("br"),t("br"),n._v("Một thanh niên la lớn."),t("br"),t("br"),n._v("“Tầm bảo kính?”"),t("br"),t("br"),n._v("Triệu Nhã chờ mắt người nháy, liền nghĩ qua đi."),t("br"),t("br"),n._v("“Phổ thông toái thiết kính mà thôi, gạt người, mười cái linh thạch nếu như có thể mua được tầm bảo kính, cũng sẽ không cần ở đây bán cái gương!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Coi như không cần thư viện, Minh Lý Chi Nhãn cũng có thể nhìn ra đơn giản một chút bảo vật lai lịch cùng giá trị."),t("br"),t("br"),n._v("Người thanh niên này trong tay cái gọi là tầm bảo kính, vừa nhìn liền biết là giả."),t("br"),t("br"),n._v("“Gạt người? Không phải nói... Nơi này đều là hàng thật ư?” Trịnh Dương nhịn không được nhìn về phía Triệu Phi Vũ."),t("br"),t("br"),n._v("Hắn mới vừa nói xong, có Giám Bảo các làm cam đoan, nơi này vật phẩm hàng thật chiếm đa số, thế nào một chút liền xuất hiện cái giả?"),t("br"),t("br"),n._v("“Nơi này có bao nhiêu bảo bối?” Không có trả lời vấn đề, Triệu Phi Vũ liếc một cái, khoảng chừng một chỉ."),t("br"),t("br"),n._v("“Ít nhất cũng phải có mấy ngàn vạn, thậm chí hơn trăm triệu kiện đi!”"),t("br"),t("br"),n._v("Trịnh Dương nhìn thoáng qua: “Đây là ta có thể nhìn thấy, bên trong còn có không thấy được, hẳn là... Càng nhiều!”"),t("br"),t("br"),n._v("Toàn bộ thương hội, bày không dưới mấy ngàn quầy hàng, mỗi cái quầy hàng bên trên liền có không biết bao nhiêu bảo vật, toàn bộ cộng lại, số lượng nhiều, có thể nói kinh khủng."),t("br"),t("br"),n._v("“Đúng vậy a, nhiều như vậy kiện, nếu như toàn bộ để giám bảo sư phân biệt, mệt chết cũng làm không được! Lại thêm, mỗi ngày ra ra vào vào, quang lưu nước liền có mấy vạn kiện, mấy chục vạn kiện, từng cái giám định, làm sao có thể?”"),t("br"),t("br"),n._v("Triệu Phi Vũ cười cười: “Ta nói chính phẩm, là nắm giữ giám bảo sư giám định tiêu ký, không có thứ này, tốt nhất đừng mua, nếu không, bị lừa cũng không biết!”"),t("br"),t("br"),n._v("“Ah!”"),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("Giám Bảo các thương hội, làm vạn quốc liên minh to lớn nhất thị trường giao dịch, trong đó buôn bán, mua sắm người thực sự nhiều lắm, nếu như mỗi một kiện bảo vật, đều cần giám định, lợi hại hơn nữa giám bảo sư, đoán chừng cũng mệt chết."),t("br"),t("br"),n._v("“Đương nhiên, nhìn thấy thích hợp, mua cũng được, nếu như không yên lòng, có thể ra giá cao, đi trên lầu tìm chân chính giám bảo sư giám định, chính phẩm, liền kiếm lời, hàng giả, liền bồi thường!” Triệu Phi Vũ nói tiếp."),t("br"),t("br"),n._v("Cùng giám bảo đánh bạc vận đồng dạng, mua sắm không có giám định qua đồ vật, khảo hạch nhãn lực cùng vận khí, không có vận may này, chỉ có thể nắm lỗ mũi tự nhận xui xẻo."),t("br"),t("br"),n._v("“Giám bảo sư thật đúng là kiếm tiền...”"),t("br"),t("br"),n._v("Mở thương hội kiếm lấy tiền thuê, liền không nói, mua đồ vật lại đi tìm bọn họ, cam tâm tình nguyện để hắn bóc lột, không hổ là có thể nhất kiếm tiền chức nghiệp một trong."),t("br"),t("br"),n._v("“Mau đến xem nhìn, ta chỗ này có . Chỉ cần đem loại cá này ngậm vào trong miệng, liền có thể tại dưới nước tự do hô hấp, ba ngày ba đêm cũng sẽ không ngạt thở. Tuyệt đối đồ tốt!”"),t("br"),t("br"),n._v("“Đi một chút nhìn một chút, chính tông , có thể giải trừ thường gặp mười bảy loại độc chướng, giá tiền không quý, tác dụng vô cùng!”"),t("br"),t("br"),n._v("“Ta chỗ này có tinh thần thạch, ban đêm xuất ra như là tinh thần, chiếu sáng tứ phương, đi qua đi ngang qua, đừng bỏ qua...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Các loại thanh âm lộn xộn điệt mà tới, khắp nơi đều là bảo vật, không có người dẫn đầu, một thân một mình vào tới, thật đúng là phân biệt không ra tốt xấu, khó mà lấy hay bỏ!"),t("br"),t("br"),n._v("Dọc theo rất nhiều quầy hàng, chậm rãi tiến lên, đột nhiên nhướng mày, Trương Huyền ngừng lại."),t("br"),t("br"),n._v("Cách đó không xa một cái quầy hàng bên trong, một cái lớn chừng bàn tay màu xám vật phẩm xuất hiện tại ánh mắt."),t("br"),t("br"),n._v("Hắn Minh Lý Chi Nhãn mặc dù không có đạt tới cấp bậc cao nhất, cùng cấp cao danh sư có được, cũng kém một mảng lớn, nhưng bảo vật bình thường vẫn có thể tuỳ tiện nhìn ra trải qua, giá trị."),t("br"),t("br"),n._v("Bất quá, chẳng biết tại sao, cái này lân phiến, nhưng chết sống đều nhìn không thấu."),t("br"),t("br"),n._v("“Kỳ quái!”"),t("br"),t("br"),n._v("Bước chân nhất chuyển, Trương Huyền mày nhăn lại."),t("br"),t("br"),n._v("Nhìn không thấu, chẳng lẽ có giá trị không nhỏ? Vượt ra khỏi hắn thực lực bây giờ có thể phân tích trình độ?"),t("br"),t("br"),n._v("“Vị bằng hữu này, có vừa ý đồ vật không? Ta chỗ này có thể cam đoan đều là đồ thật...”"),t("br"),t("br"),n._v("Thấy có người tới, chủ quán vội vàng chào đón, cười rạng rỡ."),t("br"),t("br"),n._v("“Chính phẩm?”"),t("br"),t("br"),n._v("Cười cười, Trương Huyền nhìn quanh một vòng."),t("br"),t("br"),n._v("Cái này quầy hàng bên trên đồ vật quả thực không ít, không dưới mấy ngàn, vũ khí, đan dược, hộ giáp... Cái gì cần có đều có, thật rất nhiều, có thể giả đồng dạng không ít."),t("br"),t("br"),n._v("Đều là đồ thật, thương gia lời nói, quả nhiên không thể tin tưởng."),t("br"),t("br"),n._v("Hoa văn tại trong mắt ba động, rất nhanh lại nhìn một lần."),t("br"),t("br"),n._v("Những vật này cơ bản đều có thể liếc mắt nhìn thấu, không có quá nhiều trân quý bảo bối, cũng không có quá nhiều chần chờ, chỉ hướng cách đó không xa màu xám vật phẩm, trực tiếp mở lời: “Đây là vật gì?”"),t("br"),t("br"),n._v("“Cái này?”"),t("br"),n._v("Chủ quán sửng sốt một chút, lập tức nở nụ cười: “Vị thiếu gia này thật là có ánh mắt, đây chính là ta cái này quầy hàng thứ đáng tiền nhất, là ta theo một cái đánh trộm mộ tay ở bên trong lấy được, cụ thể ra sao bảo bối, cũng không rõ ràng. Bất quá, đã từng dùng hỏa luyện qua, không chút nào tổn hại, dùng nước thấm qua, cũng không có bất kỳ cái gì vết thương, cứng rắn không gì sánh được, tuyệt đối là nhất đẳng bảo bối...”"),t("br"),t("br"),n._v("“Trân quý nhất? Ngươi lừa gạt ai?” Nghe hắn tự biên tự diễn, không dứt, Triệu Phi Vũ hừ một tiếng."),t("br"),t("br"),n._v("Những này quầy hàng chủ quán, chỉ cần có người nhìn bên trong đồ vật, đều có thể như vậy nói, ý đồ bán cái giá cao."),t("br"),t("br"),n._v("Cái gì đáng tiền nhất, trân quý nhất, nếu như tin tưởng, vậy liền thật ngốc."),t("br"),t("br"),n._v("“Ta làm sao dám lừa gạt các ngươi, thứ này thật sự rất trân quý, ta từng để một vị nhị tinh giám bảo sư giám định, đều không có kết quả!” Chủ quán lời thề son sắt."),t("br"),t("br"),n._v("“Tốt, thứ này bán thế nào?” Chẳng muốn cùng đối phương nét bút, Trương Huyền nhìn sang."),t("br"),t("br"),n._v("“Thiếu gia nếu như vừa ý, nói rõ cùng thứ này hữu duyên, liền tiện nghi bán cho ngươi, một trăm linh thạch!”"),t("br"),t("br"),n._v("Nghe được hỏi giá, chủ quán ánh mắt sáng lên, cười nói."),t("br"),t("br"),n._v("“Ngươi nói cái gì?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, kém chút không có máu tươi phun ra."),t("br"),t("br"),n._v("Còn tưởng rằng loại này quầy hàng hàng, có thể tiện nghi mua sắm, nhiều nhất một, hai khối linh thạch, hoặc là bao nhiêu kim tệ... Không nghĩ tới như thế đáng tiền."),t("br"),t("br"),n._v("Một trăm linh thạch..."),t("br"),t("br"),n._v("Hắn chỉ có mấy khối, mấy ngày nay trên đường cũng dùng hết, nhiều tiền như vậy, bán hắn đi cũng không đủ ah!"),t("br"),t("br"),n._v("Không hổ là vạn quốc liên minh, mới mở miệng liền giá cả nhiều dọa người."),t("br"),t("br"),n._v("“Loại này hàng vỉa hè hàng, đều là dùng giá cao dọa người, tuyệt đối đừng mắc lừa!”"),t("br"),t("br"),n._v("Thấy hắn trợn mắt hốc mồm bộ dáng, Triệu Phi Vũ cười một tiếng, truyền âm tới."),t("br"),t("br"),n._v("“Dọa người?”"),t("br"),t("br"),n._v("“Ngươi nhìn xem, ta giúp ngươi mặc cả, một lát nữa, nhất định có thể tiện nghi mua được!” Triệu Phi Vũ một mặt tự tin."),t("br"),t("br"),n._v("“Tốt!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Một trăm linh thạch, đối với hắn mà nói, là giá trên trời, căn bản không bỏ ra nổi đến, đã trước mắt vị này chủ động muốn giúp đỡ hạ giá, tự nhiên vui hưởng hắn thành."),t("br"),t("br"),n._v("“Cũng không cần ở đây tín khẩu khai hà, Giám Bảo các đồ vật chúng ta cũng không phải không có mua qua, nơi này cũng không phải lần đầu tiên tới. Còn một trăm linh thạch, đều bắt kịp mười giờ dạy học tam tinh ngọc phù, liền thứ hư này, cũng đáng cái giá này? Ngươi muốn cướp chứ?”"),t("br"),t("br"),n._v("Bàn tay lớn vẫy một cái, Triệu Phi Vũ tử làm ra một cái khách quen bộ dạng, trừng mắt: “Nói thực giá đi!”"),t("br"),t("br"),n._v("“Vị công tử này nói đùa, ta nói liền là thực giá, thứ này, mặc dù không đáng chú ý, lại là ta giá cao thu mua, luyện khí sư cũng rèn đúc qua, chỉ là không thành công mà thôi. Coi như ta không biết cụ thể là cái gì, nhưng cũng biết, khẳng định là kiện bảo bối...”"),t("br"),t("br"),n._v("Cười khan một tiếng, chủ quán cắn răng: “Bất quá... Đã ngươi là khách quen, cũng thường xuyên đến nơi này, ta liền không nét bút. Như vậy, ngươi nói giá, chúng ta thương nghị một chút, phù hợp liền lấy đi, không thích hợp, coi như xong!”"),t("br"),t("br"),n._v("“Cái này còn tạm được, như vậy đi, ta cho ngươi 15 viên linh thạch!”"),t("br"),t("br"),n._v("Thấy khách quen phương pháp được như ý, Triệu Phi Vũ một mặt đắc ý khoát khoát tay."),t("br"),t("br"),n._v("“Mười lăm? Tốt, coi như ta lỗ vốn, mười lăm liền mười lăm!” Chủ quán một mặt đau lòng bộ dáng."),t("br"),t("br"),n._v("“Như vậy là được rồi?”"),t("br"),t("br"),n._v("Triệu Nhã đám người đồng thời lảo đảo, từng cái con mắt trợn tròn."),t("br"),t("br"),n._v("Chào giá 100, trực tiếp còn 15, vốn cho rằng sẽ bị tại chỗ đánh chết, kết quả nhưng đồng ý..."),t("br"),t("br"),n._v("Thật là quá tàn nhẫn đi!"),t("br"),t("br"),n._v("Không phải tự mình trải qua, thật không thể tin được."),t("br"),t("br"),n._v("“Ừm!” Còn xong giá thấy đối phương đáp ứng, Triệu Phi Vũ giống như là đánh thắng trận, một mặt đắc ý nhìn về phía Trương Huyền: “Tốt, xuất tiền đi!”"),t("br"),t("br"),n._v("“Xuất tiền?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hắn là rất muốn ra tiền, có thể... Mười lăm cái linh thạch, đối với hắn mà nói, cũng là một khoản tiền lớn, tìm khắp toàn thân cũng không có."),t("br"),t("br"),n._v("“Mười lăm linh thạch, ta cảm thấy vẫn là quá mắc!” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Mười lăm linh thạch còn đắt hơn? Thiếu gia nói đùa đi, đây đã là giá thấp nhất...” Chủ quán cười khổ."),t("br"),t("br"),n._v("“Cái này... Quả thực không quý, nếu quả thật coi trọng, mua xuống là được!” Thấy mình đều nói đến mười lăm, cái tên này thế mà còn chối từ, Triệu Phi Vũ lông mày giương lên."),t("br"),t("br"),n._v("Ngươi lần này tới, nhưng là muốn mua sắm Vô Hồn Kim Nhân, loại vật này, không có có mấy ngàn, hơn vạn viên linh thạch, không có khả năng cầm xuống."),t("br"),t("br"),n._v("Mười lăm cái linh thạch mua cái nhìn trúng đồ vật, thế nào ngược lại chê đắt?"),t("br"),t("br"),n._v("“Như vậy đi, ngươi lấy trước cho ta nhìn một chút, đến cùng có thể đáng bao nhiêu tiền, sau đó lại thương nghị!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Tốt!” Chủ quán đưa tay đem màu xám vật phẩm đưa tới."),t("br"),t("br"),n._v("Tiện tay tiếp nhận, cầm tại lòng bàn tay, Trương Huyền cái này mới nhìn rõ ràng đến cùng là cái gì."),t("br"),t("br"),n._v("Lớn chừng bàn tay, mặt ngoài hiện lên màu xám tro, một bên hiện lên hình cung, mặt khác một bên, thì lõm đi vào, cùng một cái khối lớn vảy cá giống."),t("br"),t("br"),n._v("Lõm địa phương, khắc hoạ một chút đặc thù hoa văn, lít nha lít nhít, còn có nhiều chỗ mơ hồ không rõ, nhìn không ra đến cùng là cái gì."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Nhìn lướt qua bên ngoài, không có quá nhiều do dự, trong lòng thấp giọng hô."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn nhìn không ra đây là cái gì, Thiên Đạo thư viện tuyệt đối có thể rõ ràng nhìn ra."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thư tịch xuất hiện tại trong óc, tiện tay lật ra, chỉ nhìn một chút, con mắt lập tức trợn tròn, hô hấp trở nên gấp rút."),t("br"),t("br"),n._v("“Đồ tốt ah...”"),t("br"),t("br"),n._v("Chỉ thấy thư tịch bên trên viết một hàng chữ, kỹ càng giới thiệu thứ này tên cùng thiếu hụt."),t("br"),t("br"),n._v("“Long lân hộ thân phù, hai ngàn năm trước, Bạch Tích lục tinh luyện khí sư luyện chế, có thể thay thế chặn ba lần Hóa Phàm cửu trọng cường giả công kích. Khuyết điểm:”"),t("br"),t("br"),n._v("Nắm đấm căng thẳng."),t("br"),t("br"),n._v("Không nói cái khác, ánh sáng ngăn cản Hóa Phàm cửu trọng cường giả công kích, liền rất đáng sợ."),t("br"),t("br"),n._v("Có thể nói trước kia có thứ này, Lộ Xung tuyệt sẽ không thụ thương, càng sẽ không rơi vào trạng thái ngủ say, đến bây giờ đều không thể tỉnh lại."),t("br"),t("br"),n._v("“Nhất định phải mua được!”"),t("br"),t("br"),n._v("Vốn là chỉ là hiếu kỳ, không nghĩ tới quý giá như thế, đè nén trên mặt kích động, đang muốn mở miệng nói mua, liền nghe đến cách đó không xa một thanh âm vang lên."),t("br"),t("br"),n._v("“Mười lăm linh thạch đúng không, ta muốn!”"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy một người trung niên, ánh mắt lấp lánh nhìn qua, nhìn chằm chằm hắn lòng bàn tay Long lân hộ thân phù."),t("br"),t("br"),n._v("“Nguyên lai là Lưu Xương giám bảo Tông sư!”"),t("br"),t("br"),n._v("Nhìn người nọ, chủ quán thật giống nhận ra được, gấp vội mở miệng: “Ngươi muốn mua, ta hẳn là trực tiếp cho ngươi, có điều, vị thiếu gia này nhìn thấy trước, không bằng... Các ngươi thương nghị?”"),t("br"),t("br"),n._v("“Giám bảo Tông sư?”"),t("br"),t("br"),n._v("Nghe được xưng hô, Trương Huyền lúc này mới chú ý trước mắt vị này."),t("br"),t("br"),n._v("Ngực mang theo một cái huy chương, mặt ngoài hai ngôi sao chói lóa mắt."),t("br"),t("br"),n._v("Lại là vị nhị tinh giám bảo sư."),t("br"),t("br"),n._v("Giám bảo sư có đôi khi cũng lại ở chỗ này đi dạo, thuận tiện mua mua vài món đồ, vạn nhất đụng phải giá trị cao, cũng có thể kiếm một món tiền."),t("br"),t("br"),n._v("“Thứ này ta nhìn trúng, chuyển cho ta đi!”"),t("br"),t("br"),n._v("Lưu Xương giám bảo sư nhướng mày, nhìn về phía Trương Huyền."),t("br"),t("br"),n._v("“Thật không tiện, ta cũng coi trọng, không muốn chuyển cho người khác!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Nói đùa, nếu như không biết là cái gì, cho liền cho, biết là vật gì, đánh chết cũng không thể buông tay ah!"),t("br"),t("br"),n._v("Thứ này tương đương với ba lần bảo mệnh cơ hội, đồ đần mới có thể bán đi."),t("br"),t("br"),n._v("“Không chuyển?”"),t("br"),t("br"),n._v("Lưu Xương giám bảo sư lạnh hừ một tiếng, không tiếp tục để ý Trương Huyền, mà là nhìn về phía chủ quán: “Hắn mặc dù trước coi trọng, còn không có trả tiền ư? Mua đồ, người trả giá cao được, như vậy đi, ta cho ngươi 50 viên linh thạch, ngươi bán cho ta!”"),t("br"),t("br"),n._v("“50?”"),t("br"),t("br"),n._v("Chủ quán ánh mắt sáng lên."),t("br"),t("br"),n._v("Thứ này hắn cũng như thế trong lúc vô tình lấy được, cụ thể có thể đáng bao nhiêu, quả thực không biết, có thể bán năm mươi, hắn thấy, tuyệt đối là vật siêu chỗ đáng giá."),t("br"),t("br"),n._v("Trong lòng kích động, vội vàng nhìn về phía thanh niên: “Vị thiếu gia này, lưu Tông sư đã ra giá, ngươi nhìn... Ngươi muốn không bỏ ra nổi giá cao hơn cách, liền đem thứ này trả lại cho ta đi...”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua: “Như vậy đi, ta cho ngươi cái giá cả, nếu như phù hợp liền bán cho ta, nếu như không thích hợp, liền cho hắn!”"),t("br"),t("br"),n._v("“Tốt!” Chủ quán gật đầu."),t("br"),t("br"),n._v("“Năm mươi cái kim tệ!” Trương Huyền nói."),t("br"),t("br"),n._v("“Năm mươi cái... Kim tệ?”"),t("br"),t("br"),n._v("Chủ quán sững sờ, kém chút không có thổ huyết."),t("br"),t("br"),n._v("Nhân gia ra năm mươi linh thạch, ngươi ra năm mươi cái kim tệ, đại ca, ngươi là đến đùa nghịch ta chơi chứ?"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-457"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-459"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);