(window.webpackJsonp=window.webpackJsonp||[]).push([[1697],{2052:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-450-trom-mo"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-450-trom-mo"}},[n._v("#")]),n._v(" Chương 450: Trộm mộ")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Kim tệ, tại vạn quốc liên minh phạm vi bên trong, cơ bản thông dụng, chỉ bất quá, nương theo vương quốc cấp bậc gia tăng, ảnh hưởng càng ngày càng nhỏ mà thôi."),t("br"),t("br"),n._v("Một khối linh thạch, hơn triệu kim tệ cũng mua không được, chỉ cho năm mươi cái kim tệ, liền muốn mua giá trị năm mươi linh thạch đồ vật, ngươi mở cái gì trò đùa?"),t("br"),t("br"),n._v("Một bên Triệu Phi Vũ mí mắt khẽ đảo, cũng thiếu chút không có thổ huyết ngất đi."),t("br"),t("br"),n._v("Hắn nhọc nhằn khổ sở theo 100 linh thạch nói đến 15, vốn cho rằng rất lớn đột phá, cái tên này ngược lại tốt, mới mở miệng 50 kim tệ?"),t("br"),t("br"),n._v("Mới vừa rồi còn nói đối phương cướp... Hiện tại, thế nào cảm giác, nhân gia chủ quán coi như thành thật, vị này Trương sư, ngã cùng cướp bóc đồng dạng?"),t("br"),t("br"),n._v("Chút tiền ấy liền muốn mua nhân gia đồ vật, mặc kệ là cái gì, cũng căn ăn cướp trắng trợn không có gì khác biệt."),t("br"),t("br"),n._v("“50... Kim tệ?”"),t("br"),t("br"),n._v("Cũng cho rằng cái tên này có thể đưa ra cao bao nhiêu giá cả, nghe nói như thế, Lưu Xương giám bảo sư kém chút không có bật cười."),t("br"),t("br"),n._v("Cái này từ nơi nào xuất hiện nhà quê?"),t("br"),t("br"),n._v("Linh thạch cùng kim tệ là hai loại hoàn toàn khác biệt giao dịch tiền tệ, căn bản không có khả năng so sánh, ta cho ra 50 linh thạch, ngươi nhưng cho ra 50 kim tệ, ngươi thế nào không nói thẳng, tặng cho ngươi được..."),t("br"),t("br"),n._v("Mặt thật là lớn!"),t("br"),t("br"),n._v("Liếc qua, đầy vẻ khinh bỉ."),t("br"),t("br"),n._v("Năm mươi kim tệ nói như thế lẽ thẳng khí hùng, toàn bộ thương hội cũng là độc ngươi một phần."),t("br"),t("br"),n._v("“Thật không tiện, ta không bán cho ngươi...”"),t("br"),t("br"),n._v("Khôi phục lại, chủ quán vội vàng vẫy tay."),t("br"),t("br"),n._v("Còn tưởng rằng thật sự là cái gì công tử, thiếu gia, náo loạn nửa ngày, nguyên lai chỉ là cái quỷ nghèo."),t("br"),t("br"),n._v("Làm hại lãng phí thời gian dài như vậy."),t("br"),t("br"),n._v("“Ngươi cảm thấy ta cho giá cả không đủ cao?” Trương Huyền cũng không tức giận, mà là mỉm cười."),t("br"),t("br"),n._v("Chủ quán im lặng."),t("br"),t("br"),n._v("Đó căn bản không phải không đủ cao vấn đề, mà là tương đương với căn bản không có giá cả!"),t("br"),t("br"),n._v("Ngươi gặp qua cầm giấy nháp đi mua đồ vật sao?"),t("br"),t("br"),n._v("Giấy nháp coi như cũng đáng ít tiền, có thể cùng chân chính tiền so, kém thực sự quá lớn."),t("br"),t("br"),n._v("“Lưu Xương giám bảo sư, thứ này liền bán cho ngươi, không biết còn có hay không vừa ý, ta cùng nhau cho ngươi đánh cái bao...”"),t("br"),t("br"),n._v("Chẳng muốn lại để ý tới cái tên này, chủ quán quay đầu."),t("br"),t("br"),n._v("“Nói như vậy, ngươi là không bán cho ta?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu, lộ ra một tia tiếc hận: “Ai, vốn còn nghĩ chỉ cần bán cho ta, tiện tay giải quyết ngươi đêm không thể say giấc, ác mộng quấn thân phiền phức, đã không cần, quên đi!”"),t("br"),t("br"),n._v("Nói xong, tiện tay đem vật cầm trong tay ném ra ngoài, quay người liền muốn rời khỏi."),t("br"),t("br"),n._v("“Ngươi nói cái gì?”"),t("br"),t("br"),n._v("Vốn là chủ quán không thèm để ý chút nào, nghe nói như thế, thân thể cứng đờ, lập tức vẻ mặt trắng bệch: “Vị thiếu gia này xin dừng bước?”"),t("br"),t("br"),n._v("Người tu luyện, cùng trời chống lại, hấp thu linh khí tẩm bổ thân thể, dựa theo tình huống bình thường, sẽ rất ít xuất hiện đêm không thể say giấc, ác mộng quấn thân tình huống, cũng không biết vì cái gì, hắn gần nhất nửa năm qua, cơ hồ hàng đêm ngủ không được, thậm chí một ngủ, liền sẽ bừng tỉnh."),t("br"),t("br"),n._v("Một hai ngày ngược lại cũng thôi, liên tục nửa năm, cho dù hắn thực lực không yếu, cũng cảm thấy thần kinh suy nhược, bất cứ lúc nào cũng sẽ sụp đổ."),t("br"),t("br"),n._v("Thầm tìm không ít y sư, kết quả cũng không có cách nào, trước mắt vị này đột nhiên nói ra, làm sao không kích động?"),t("br"),t("br"),n._v("“Ngươi gần nhất có phải hay không giật mình mồ hôi trộm, nửa đêm ngủ về sau, như là người chết, thân thể nhẹ run rẩy?” Biết đối phương khẳng định sẽ gọi hắn, Trương Huyền mỉm cười, xoay đầu lại."),t("br"),t("br"),n._v("“Ngươi... Làm sao biết?” Chủ quán lần nữa chấn động."),t("br"),t("br"),n._v("Hắn đêm không thể say giấc, coi như ngày đêm tu luyện, có thể bù đắp thâm hụt, vẻ mặt cũng không quá bình thường, hơi có vẻ trắng xanh."),t("br"),t("br"),n._v("Điểm ấy không ít y sư đều có thể liếc mắt nhìn ra, có thể... Giật mình mồ hôi trộm, ngủ như là người chết, nương theo run rẩy, chỉ có hắn chính mình biết, bất kỳ người nào đều không có nói qua, thậm chí thê tử đều chưa từng nói."),t("br"),t("br"),n._v("Đối phương như thế nào biết được? Hơn nữa nói thẳng ra?"),t("br"),t("br"),n._v("“Ta có thể giúp ngươi giải quyết!” Trương Huyền thản nhiên."),t("br"),t("br"),n._v("“Giúp ta giải quyết?”"),t("br"),t("br"),n._v("Thân thể nhoáng một cái, chủ quán không thể tin được."),t("br"),t("br"),n._v("“Có thể tin, có thể không tin, bất quá... Đối với ngươi mà nói, là một cơ hội. Nếu như không giải quyết, có lẽ tính mệnh đều sẽ bị hao tổn, sống không được bao lâu!” Trương Huyền hai tay chắp sau lưng, thần sắc thản nhiên."),t("br"),t("br"),n._v("Mặc dù rất muốn cái này Long lân hộ thân phù, nhưng cũng muốn đối phương tin tưởng mới được, không tin, nói nhiều hơn nữa cũng vô ích."),t("br"),t("br"),n._v("“Ta...” Chủ quán chần chờ."),t("br"),t("br"),n._v("Một lát sau một cắn răng, ngẩng đầu nhìn về phía trước mắt trung niên nhân: “Lưu Xương giám bảo sư thực sự thật không tiện, thứ này xem ra không thể bán cho ngươi...”"),t("br"),t("br"),n._v("Tiền cố nhiên quan trọng, nhưng thân thể cùng sinh mệnh so liền cái gì cũng không tính."),t("br"),t("br"),n._v("Nửa năm qua, bị loại tình huống này dây dưa, hắn tiêu vào y sư tiền trên người, cũng không ít hơn 50 linh thạch, đáng tiếc một mực không có tiến triển."),t("br"),t("br"),n._v("Nếu như trước mắt vị này có thể giải quyết, thứ này coi như đưa cho đối phương, cũng không tính là gì."),t("br"),t("br"),n._v("Về phần có tin tưởng hay không... Đối phương liếc mắt có thể nhìn ra chỗ mấu chốt, đã vượt qua không ít y sư nhãn lực, có lẽ như hắn nói tới... Thật có thể giải quyết."),t("br"),t("br"),n._v("“Hừ, nơi này ăn nói bừa bãi hạng người chiếm đa số, ngươi phải cẩn thận!”"),t("br"),t("br"),n._v("Không nghĩ tới tốn hao 50 linh thạch, còn không sánh bằng đối phương 50 kim tệ, Lưu Xương giám bảo sư hừ một tiếng, quay đầu nhìn về phía Trương Huyền, híp mắt lại."),t("br"),t("br"),n._v("“Có phải hay không ăn nói bừa bãi, cũng không nhọc đến phiền Lưu giám bảo sư phí tâm!” Trương Huyền khoát tay chặn lại."),t("br"),t("br"),n._v("“Hừ, rất tốt...”"),t("br"),t("br"),n._v("Vẻ mặt khó coi, Lưu Xương giám bảo sư, hất lên ống tay áo, quay người rời khỏi."),t("br"),t("br"),n._v("Hắn đường đường giám bảo sư, chịu không biết bao nhiêu người kính ngưỡng, mua không lên lại trong này dây dưa, vậy liền thật mất mặt."),t("br"),t("br"),n._v("Đương nhiên, chủ yếu nhất điều kiện tiên quyết là, hắn không biết thứ này giá trị."),t("br"),n._v(" Vừa rồi nhìn thấy, chẳng qua là cảm thấy kỳ quái, cũng không nhận ra cụ thể là vật gì, muốn mua xuống, cũng chỉ là hiếu kỳ."),t("br"),t("br"),n._v("Về phần mua không được, cũng không thấy đến cái gì."),t("br"),t("br"),n._v("Bất quá, bị một cái không biết từ nơi nào xuất hiện nhà quê đánh mặt, nhiều tiền như vậy, không có mua lấy đồ vật, vẫn còn có chút nổi giận."),t("br"),t("br"),n._v("“Vị thiếu gia này...”"),t("br"),t("br"),n._v("Thấy Lưu giám bảo sư quay người rời khỏi, trên mặt không vui, chủ quán nhìn về phía Trương Huyền."),t("br"),t("br"),n._v("Nếu như cái tên này thật sự là tin miệng nói nói, không có năng lực giải quyết trên người hắn vấn đề, thật sự khóc."),t("br"),t("br"),n._v("Tại thương hội bày quầy bán hàng, đắc tội một vị nhị tinh giám bảo sư, tuyệt đối là không sáng suốt cử động."),t("br"),t("br"),n._v("“Yên tâm đi, đã có thể nhìn ra, liền có thể giúp ngươi giải quyết! Đến, đánh một quyền cho ta xem một chút!” Trương Huyền nói."),t("br"),t("br"),n._v("“Vâng!” Không có quá nhiều do dự, chủ quán một quyền đánh ra, phong thanh hô hô."),t("br"),t("br"),n._v("Không nghĩ tới chỉ là cái phổ thông quầy hàng chủ quán, thực lực chân thật thế mà đã đạt đến nửa bước Tông sư, khoảng cách chân chính Tông sư, cũng chỉ có một cước chi kém."),t("br"),t("br"),n._v("Không hổ là vạn quốc liên minh, tài nguyên, danh sư đầy đủ tình huống dưới, thực lực tổng hợp, so Thiên Huyền vương quốc cao không biết gấp bao nhiêu lần."),t("br"),t("br"),n._v("“Tốt!” Trương Huyền vẫy tay."),t("br"),t("br"),n._v("Thực lực đối phương không bằng hắn, Minh Lý Chi Nhãn đã đó có thể thấy được không ít vấn đề, có điều, nếu muốn triệt để trị tận gốc, còn cần mượn nhờ thư viện, tìm tới mấu chốt căn nguyên."),t("br"),t("br"),n._v("“Trên tay ngươi rất nhiều thứ đáng giá, cũng không phải là mua được, mà là... Tiền tài bất nghĩa. Vật phẩm mang theo cũ kỹ chi sắc, phía trên sinh đầy thai lục, đây là không khí không lưu thông sinh ra, lại thêm trên người ngươi, tản mát ra một loại tĩnh mịch chi khí... Nếu như ta không nhìn lầm, ngươi am hiểu móc mộ đào mộ!”"),t("br"),t("br"),n._v("Đi tới chủ quán trước mặt, Trương Huyền nhìn quanh một vòng, thản nhiên nói."),t("br"),t("br"),n._v("“Ngươi, ngươi...” Sắc mặt trắng nhợt, chủ quán dọa đến kém chút không có xụi lơ trên mặt đất."),t("br"),t("br"),n._v("Móc mộ đào mộ vẫn là dễ nghe, nói thật, liền là cái trộm mộ."),t("br"),t("br"),n._v("Vô luận bao nhiêu lợi hại cường giả, dù là Chí Tôn vẫn là Hóa Phàm, đều chạy không khỏi thời gian trừng phạt, cuối cùng biến mất tại dòng sông lịch sử. Những người này sau khi chết, liền sẽ lưu lại di tích cùng mộ huyệt, trong đó chôn cùng lấy một chút vật có giá trị."),t("br"),t("br"),n._v("Mà hắn, chính là dựa vào đào móc những địa phương này, dựng nghiệp bằng hai bàn tay trắng, thông qua bán ra người chết đồ vật, mới có hiện tại quy mô."),t("br"),t("br"),n._v("Bất quá, phần này trải qua, nấp rất kỹ, không ít quen thuộc người, đều không rõ ràng, bị trước mắt vị này, một hơi nói thẳng ra, dọa đến không có trực tiếp ngất đi."),t("br"),t("br"),n._v("“Chớ khẩn trương, ngươi làm gì không có quan hệ gì với ta!”"),t("br"),t("br"),n._v("Nhẹ nhàng nở nụ cười, Trương Huyền nói tiếp: “Chỉ nói ngươi bệnh chứng lai lịch.”"),t("br"),t("br"),n._v("“Nếu như chỉ đào một chút phổ thông mộ huyệt, không có vấn đề, có thể tám tháng trước, đào móc một cái Hóa Phàm cảnh cường giả mộ huyệt, ta nói không sai chứ!” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Cái này... Đúng!” Chủ quán cắn răng."),t("br"),t("br"),n._v("“Vị này Hóa Phàm cường giả, nếu là bình thường chết già, ngược lại cũng thôi, đáng tiếc bị thê tử phản bội, thảm tao bạn tốt chém giết, trong lòng một cỗ oán khí bất diệt... Nếu như không nhìn lầm, ngươi có phải hay không đem hắn mang theo người một kiện vật phẩm lưu lại, cũng thiếp thân đeo.” Trương Huyền tiếp tục xem tới."),t("br"),t("br"),n._v("“... Đúng!” Chủ quán lần nữa gật đầu, gặp quỷ đồng dạng nhìn về phía tên trước mắt này."),t("br"),t("br"),n._v("Đối phương mộ huyệt phi thường đơn sơ không có gì thứ đáng giá, chỉ có tiến áp sát người ngọc phù, có giá trị không nhỏ, hắn liền lấy lên... Như thế cơ mật sự tình, cái tên này làm sao mà biết được?"),t("br"),t("br"),n._v("Hơn nữa còn và tận mắt nhìn thấy đồng dạng?"),t("br"),t("br"),n._v("“Liền là thứ này ẩn chứa oán khí, để ngươi đêm không thể say giấc, một khi ngủ, cùng người chết đồng dạng không ngừng run rẩy... Muốn giải quyết, cũng rất đơn giản, đem ngọc phù một lần nữa thả lại chỗ cũ, tìm tới mộ huyệt chủ nhân, thiện đãi hắn hậu nhân, hảo hảo tế bái, tai hoạ ngầm liền sẽ diệt hết!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Kỳ thật vị này tình huống, cùng lúc trước biện đan lúc gặp phải Trần Tiêu đan sư có chút giống."),t("br"),t("br"),n._v("Người sau cưỡng ép đoạt lấy một vị luyện đan sư đan lô, mà không giúp hắn báo thù, nhận lấy nguyền rủa."),t("br"),t("br"),n._v("Vị này thời gian dài bị Hóa Phàm cường giả oán khí xâm nhập, không xuất hiện tình huống mới là lạ!"),t("br"),t("br"),n._v("Không chiếm được giải quyết, tuổi thọ tất nhiên thật to bị hao tổn, có lẽ không kiên trì được mấy năm, đồng dạng sẽ bước Trần Tiêu đan sư theo gót, tử khí nồng đậm, mệnh không lâu dài."),t("br"),t("br"),n._v("Bởi vậy, trước đó nói hắn sống không được bao lâu, cũng không phải là nói chuyện giật gân."),t("br"),t("br"),n._v("“Đa tạ Thiếu gia... Chỉ điểm!”"),t("br"),t("br"),n._v("Đối phương nói rất nhiều chuyện, đều không có sai lầm, đối với Trương Huyền lời nói, chủ quán đã tin tưởng bảy tám phần, tràn đầy cảm kích."),t("br"),t("br"),n._v("Vốn cho rằng là thân thể vấn đề xuất hiện, thế nào đều không có nghĩ đến, nguyên do lại là cái này."),t("br"),t("br"),n._v("“Thứ này... Thiếu gia, ngươi cầm đi!”"),t("br"),t("br"),n._v("Biết giải quyết phương pháp, chủ quán đem vừa rồi Long lân hộ thân phù đưa tới."),t("br"),t("br"),n._v("Thứ này không biết là vật gì, có điều, coi như đắt đi nữa, có thể đổi lấy tính mệnh, cũng giá trị tuyệt đối."),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, đem đồ vật cất kỹ, cổ tay khẽ đảo, lấy ra một đống kim tệ, không nhiều không ít, vừa vặn 50 viên: “Đây là thù lao!”"),t("br"),t("br"),n._v("“Không cần...”"),t("br"),t("br"),n._v("Chủ quán vội vàng vẫy tay."),t("br"),t("br"),n._v("Cùng hắn cầm, còn không bằng đưa ra ngoài, chí ít coi như một cái nhân tình."),t("br"),t("br"),n._v("“Sao có thể không cần? Nói mua sắm liền là mua sắm, nếu như không trả tiền, chẳng phải cùng cướp đồng dạng?” Trương Huyền nghĩa chính ngôn từ."),t("br"),t("br"),n._v("“Cái này... Được rồi!” Chủ quán đành phải đón lấy."),t("br"),t("br"),n._v("“Như vậy đều được?”"),t("br"),t("br"),n._v("Đem một màn này toàn bộ nhìn ở trong mắt, Triệu Phi Vũ chỉ cảm thấy tất cả tri thức đều bị phá vỡ."),t("br"),t("br"),n._v("50 kim tệ, mua giá trị 50 linh thạch bảo bối, đối phương còn mang ơn, hận không thể đem gia cúng bái..."),t("br"),t("br"),n._v("Quá mạnh đi!"),t("br"),t("br"),n._v("Làm vạn quốc liên minh minh chủ nữ nhi, đường đường công chúa... Lợi hại y sư, gặp qua không biết bao nhiêu, cũng thấy liếc mắt, liền nhìn ra người khác có bệnh, liền trộm mộ cái nghề nghiệp này đều có thể biết..."),t("br"),t("br"),n._v("Ngươi không phải y sư..."),t("br"),t("br"),n._v("Ngươi nha coi bói đi..."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-458"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-460"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);