(window.webpackJsonp=window.webpackJsonp||[]).push([[1701],{2055:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-453-gia-re-mua-thuoc-trung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-453-gia-re-mua-thuoc-trung"}},[n._v("#")]),n._v(" Chương 453: Giá rẻ mua thuốc (trung)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Hắn mặc dù có tiền, thế nhưng không phải gió lớn thổi tới."),t("br"),t("br"),n._v("Tự nhiên cũng không có khả năng ngu xuẩn 500 linh thạch mua sắm, 500 kim tệ bán đi."),t("br"),t("br"),n._v("Tiểu tử này đến cùng là đầu óc có bệnh, vẫn là thần kinh mắc đánh? Nếu không làm sao sẽ nói ra như thế ngu xuẩn lời nói?"),t("br"),t("br"),n._v("Quay đầu nhìn lại, chỉ thấy cách đó không xa thanh niên, mỉm cười, nhẹ gật đầu: “Không sai!”"),t("br"),t("br"),n._v("“Náo đủ chưa? Ngươi cũng đã biết nhà ta lão sư là ai, ngay ở chỗ này ăn nói bừa bãi, phát ngôn bừa bãi?”"),t("br"),t("br"),n._v("Thấy hắn càng ngày càng quá trớn, liền lão sư cũng dám khiêu khích, Lưu Xương giám bảo sư cũng nhịn không được nữa, tiến về phía trước một bước."),t("br"),t("br"),n._v("“Người nào? Ánh mắt sắc bén, nhìn bảo vật lần đầu tiên, liền có thể tìm tới nơi mấu chốt, nếu như không có đoán sai... Hẳn là giám bảo sư đi!”"),t("br"),t("br"),n._v("Trương Huyền con mắt thoáng nhìn."),t("br"),t("br"),n._v("“Biết liền tốt, nhà chúng ta lão sư, chính là...”"),t("br"),t("br"),n._v("Lưu Xương đang muốn giới thiệu thân phận, liền bị lão giả cắt ngang: “Tốt, nếu biết ta là giám bảo sư, liền hẳn phải biết, ta không thiếu tiền, cũng sẽ không xảy ra bán đã mua sắm đồ vật!”"),t("br"),t("br"),n._v("Hắn xem như đã nhìn ra, cái tên này liền là cái đầu óc có vấn đề, chẳng muốn tiếp tục so đo, nhìn về phía trước mắt gã sai vặt: “Đem thứ này đều cho ta nhận lấy đi, còn có trước đó muốn cái kia mấy thứ, một khối lấy tới, đưa đến ta phủ thượng!”"),t("br"),t("br"),n._v("“Vâng!” Gã sai vặt gật đầu."),t("br"),t("br"),n._v("Tiếng nói kết thúc, mấy bụi dược liệu, tính cả mấy món bảo vật, liền chở tới."),t("br"),t("br"),n._v("Triệu Nhã đám người lặng lẽ nhìn lại, không chịu được đồng thời tắc lưỡi."),t("br"),t("br"),n._v("Mặc dù số lượng không nhiều, nhưng mỗi một dạng, đều trân quý dị thường, yết giá đều không ít hơn 300 linh thạch."),t("br"),t("br"),n._v("Mấy thứ chung vào một chỗ, chỉ sợ sớm đã vượt qua hai ngàn linh thạch nhiều, người đứng đầu xuất ra nhiều như vậy, còn khuôn mặt không thay đổi, lão giả này giá trị bản thân cao, quả thực làm cho người hoảng sợ."),t("br"),t("br"),n._v("Đối mặt nhân vật như vậy, lão sư 500 kim tệ liền muốn mua nhân gia 500 linh thạch mua sắm đồ vật..."),t("br"),t("br"),n._v("Có phải hay không có chút quá phận?"),t("br"),t("br"),n._v("Nghi ngờ nhìn về phía lão sư, muốn biết hắn sẽ làm thế nào, chỉ thấy thanh niên, hai tay chắp sau lưng, một mặt tiếc hận lắc đầu."),t("br"),t("br"),n._v("“Ai, vốn cho rằng đại nhân vật, giám bảo sư, mua đồ có thể thêm chút mắt, không nghĩ tới dùng nhiều tiền cầm hàng giả liền đi, cái này phẩm vị... Thật đúng là để cho người ta khó có thể lý giải được!”"),t("br"),t("br"),n._v("Thanh âm mang theo than thở, thật giống chỉ tiếc rèn sắt không thành thép."),t("br"),t("br"),n._v("“Hàng giả? Ngươi cũng đã biết lão sư ta là ai? Ngay ở chỗ này tùy ý miệng nói bậy...”"),t("br"),t("br"),n._v("Lão sư tha hắn một lần, còn tưởng rằng cái tên này sẽ hành quân lặng lẽ, không nghĩ tới nói ra lời này, Lưu Xương khuôn mặt trầm xuống, gần nổ tung."),t("br"),t("br"),n._v("Xong chưa?"),t("br"),t("br"),n._v("Lão sư không so đo với ngươi coi như xong, thế mà còn dám nói mua hàng giả, có phải hay không tự tìm cái chết?"),t("br"),t("br"),n._v("“Ai!” Đối mặt quát lớn, Trương Huyền không thèm quan tâm, lần nữa than thở, kìm lòng không được lắc đầu: “Mắt vụng về ngược lại cũng thôi, còn không thừa nhận...”"),t("br"),t("br"),n._v("“Mấu chốt nhất còn tự xưng cái gì còn lớn hơn nhân vật, mất mặt. Được rồi, đã không nghe người ta khuyên, muốn mua giả đồ vật, ta cũng là không nhiều chuyện, đi thôi!”"),t("br"),t("br"),n._v("Nói xong vẫy tay một cái, mang theo Triệu Nhã đám người liền muốn rời khỏi."),t("br"),t("br"),n._v("“Chậm rãi, ngươi nói ai mua là hàng giả? Không đem nói chuyện rõ ràng, đừng nghĩ đi...” Thấy cái tên này muốn nói lại thôi, rất rõ ràng nói lão sư mua giả đồ vật mà không thừa nhận, Lưu Xương cũng nhịn không được nữa, ngăn tại trước mặt."),t("br"),t("br"),n._v("Cùng thời khắc đó, khí tức trên thân phát ra, chân khí không cố định, lực lượng mười phần, lại là một vị Hóa Phàm nhất trọng Tăng Thọ cảnh cường giả."),t("br"),t("br"),n._v("Đồng dạng là Tăng Thọ cảnh, hắn loại này có danh sư, lại đang vạn quốc liên minh tu luyện võ giả, tự nhiên muốn so với chính mình đột phá Đinh Hồng mạnh hơn nhiều."),t("br"),t("br"),n._v("Thật muốn ra tay, coi như Trương Huyền gần nhất tiến bộ rất lớn, chỉ sợ cũng khó mà chống chọi."),t("br"),t("br"),n._v("Bất quá, đối mặt với đối phương trên người phát ra khí tức, Trương Huyền không thèm quan tâm, ngược lại mí mắt cong lên: “Thế nào? Con mắt không tốt không tìm được hàng thật, còn không cho người nói? Hảo tâm nhắc nhở, cảm thấy thật mất mặt, muốn lưu lại chúng ta?”"),t("br"),t("br"),n._v("Ngụy trang Dương Huyền, loại tình huống này thấy cũng nhiều, không có cái này tự tin, còn làm ra vẻ trang?"),t("br"),t("br"),n._v("“Bị lừa? Ngươi cũng đã biết nhà ta lão sư là ai? Toàn bộ Hồng Hải thành bao nhiêu người xin lão sư giúp hắn chưởng nhãn...”"),t("br"),t("br"),n._v("đọc truＹện với http://ngantruyen.com/"),t("br"),n._v("Khí sắc mặt trắng bệch, Lưu Xương đang muốn nói tiếp, chỉ thấy lão giả chậm rãi đi tới."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Khoát khoát tay, lão giả nhìn qua: “Ngươi nói ta mua đến hàng giả, không biết cái nào là giả? Có gì bằng chứng?”"),t("br"),t("br"),n._v("Mặc dù chỉ là phổ thông tra hỏi, nhưng mang theo tràn đầy tự tin, cho người ta một loại mãnh liệt áp bách, thật giống chỉ cần nói sai một câu, liền sẽ lọt vào giáo huấn."),t("br"),t("br"),n._v("“Bằng chứng? Ngươi thật muốn biết?”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("“Nếu như ngươi nói có lý có căn cứ, chứng minh ta đích xác nhìn lầm, đương nhiên sẽ thừa nhận! Bất quá, nếu là ăn nói bừa bãi, nói càn, chỉ vì lòe người, ta có thể không ngại, chỉ sợ... Lưu Xương cũng sẽ không đồng ý!”"),t("br"),t("br"),n._v("Lão giả trong giọng nói mang theo uy nghiêm."),t("br"),t("br"),n._v("Nói thẳng hắn mắt mù không biết hàng, nếu thật là nói càn, coi như không cần ra tay, làm học sinh Lưu Xương, cũng sẽ không bỏ qua đối phương."),t("br"),t("br"),n._v("“Cái tên này thật làm cho người đau đầu...”"),t("br"),n._v("Đem những này nhìn ở trong mắt, Triệu Phi Vũ con mắt tái đi."),t("br"),t("br"),n._v("Hắn thấy, thật muốn mua An Thần thảo, xuất ra tiền là được, cùng một đại nhân vật gạch bên trên, quả thực liền là ngu xuẩn nhất làm pháp."),t("br"),t("br"),n._v("Thật đúng là cái không đèn đã cạn dầu, đi đến đâu, nháo đến đâu, liền không thể sống yên ổn điểm?"),t("br"),t("br"),n._v("Ngoan ngoãn lấy tiền giải quyết nhiều đơn giản?"),t("br"),t("br"),n._v("Nếu như cho Trương Huyền biết ý nghĩ của hắn, khẳng định sẽ khóc ròng ròng."),t("br"),t("br"),n._v("Ngươi là liên minh minh chủ nữ nhi, đường đường công chúa, không thiếu tiền, đứng đấy nói chuyện không đau eo, nếu là có tiền, ai bốc lên bị đánh chết nguy hiểm trang bức ah..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Lòe người? Ngươi suy nghĩ nhiều!”"),t("br"),t("br"),n._v("Lắc đầu, Trương Huyền tiến về phía trước một bước, nhìn về phía lão giả mới vừa mua một đống vật phẩm bên trong: “Căn này Phỉ Thúy trúc là ngươi vừa mới mua sắm a?”"),t("br"),t("br"),n._v("“Không sai, căn này Phỉ Thúy trúc là lão sư hao tốn 300 linh thạch mua, toàn bộ Hồng Hải thành liền cái này một cái!” Lưu Xương gật đầu, cười lạnh một tiếng: “Thế nào, ngươi cảm thấy nó có vấn đề?”"),t("br"),t("br"),n._v("Đồ vật ngay tại trước mặt, phía dưới cũng có yết giá, không có gì có thể phủ nhận."),t("br"),t("br"),n._v("Lại nói, hắn cũng không thấy đến thứ này là giả, không riêng gì lão sư tự mình chưởng nhãn, hắn cũng cẩn thận quan sát, không có sai lầm chút nào."),t("br"),t("br"),n._v("“Không biết Phỉ Thúy trúc công hiệu là cái gì? Các ngươi mua sắm thứ này lại dùng ở nơi nào?”"),t("br"),t("br"),n._v("Không để ý tới đối phương, Trương Huyền dọc theo cây trúc dạo qua một vòng, ngón tay nhẹ nhàng đụng vào, giống như là xác định cái gì, mỉm cười, hỏi tiếp."),t("br"),t("br"),n._v("“Phỉ Thúy trúc là một loại đặc thù linh dược, lá cây có thể làm thuốc, là luyện chế  chủ yếu tài liệu. Loại đan dược này có thể khiến người ta sinh ra Linh Lung chi tâm, tiêu trừ tâm ma, tâm cảnh tự nhiên an tường, đối Hóa Phàm cảnh tam trọng, âm dương cảnh cường giả đều có cực lớn trợ giúp.”"),t("br"),t("br"),n._v("“Về phần... Cành cây, mười phần cứng rắn, có thể luyện chế linh binh . Phỉ Thúy cung, linh phẩm sơ cấp vũ khí, thích hợp vô hại tiễn, bắn thủng thân thể, bởi vì tốc độ quá nhanh, thậm chí liền vết thương đều không có lưu, liền có thể đem người chém giết, là không ít Hóa Phàm cường giả, đều nguyện ý ra giá tiền rất lớn mua sắm vũ khí một trong.”"),t("br"),t("br"),n._v("“Đương nhiên, còn có một cái khác hiệu quả, cái kia chính là có thể làm thành trận cơ, hình thành đặc thù trận pháp, chỉ bất quá cái hiệu quả này so trước hai cái đều muốn yếu, không đáng giá nhắc tới!”"),t("br"),t("br"),n._v("Vuốt râu, lão giả nhàn nhạt mở lời."),t("br"),t("br"),n._v("Trong lời nói bình thản tự tin, cho thấy uyên bác tri thức cùng ngạo nhân hiểu biết."),t("br"),t("br"),n._v("Phỉ Thúy trúc, người khác đều biết trân quý, lại không biết hắn dùng, hắn thân là giám bảo sư mới có biết một hai, nếu không, cũng không có khả năng tốn hao ròng rã 300 linh thạch mua sắm."),t("br"),t("br"),n._v("“Không sai, người bình thường chỉ biết là Phỉ Thúy trúc lá cây có thể luyện thành đan dược, đối luyện chế đại cung, hình thành trận pháp, biết rất ít, có thể biết điểm ấy, xem như không tệ!”"),t("br"),t("br"),n._v("Tán dương một tiếng, Trương Huyền tiếp lấy nhìn qua, khóe miệng giương lên: “Đã có loại này công hiệu, không biết ngươi mua về, là bố trí trận cơ, rèn đúc cung tiễn đâu, vẫn là luyện chế đan dược?”"),t("br"),t("br"),n._v("Luyện chế đan dược, không chỉ muốn lá cây, còn muốn sử dụng cành cây làm lửa dẫn, nấu đậu đốt cành đậu, mới có thể phát huy hiệu quả tốt nhất."),t("br"),t("br"),n._v("Rèn đúc cung tiễn, thì cần muốn tại tươi mới thời điểm, dùng đặc thù dược vật ôn dưỡng, để hắn cứng cáp hơn, chẳng qua như vậy một làm, lá cây chẳng khác nào hỗn tạp vật phẩm khác, không thể dùng."),t("br"),t("br"),n._v("Về phần làm thành trận cơ, cũng cùng hai cái này công hiệu tương đương, cần khắc hoạ trận văn, luyện chế lại một lần, lá cây đồng dạng chịu ảnh hưởng, không cách nào lại dùng."),t("br"),t("br"),n._v("Mặc dù cái này Phỉ Thúy trúc có ba cái ảnh hưởng, nhưng một cái cây trúc, chỉ có thể chọn một dạng, nếu không, vẽ hổ hay sao phản loại chó, ngược lại sẽ đem hắn triệt để lãng phí."),t("br"),t("br"),n._v("“Đương nhiên là luyện đan! Những công hiệu khác mặc dù cũng không yếu, nhưng so với cái trước, vẫn là kém không ít!”"),t("br"),t("br"),n._v("Nhướng mày, lão giả không biết hỏi cái này chút lời nói đến cùng có ý tứ gì, có điều, vẫn như cũ nhẫn nại tính tình giải thích."),t("br"),t("br"),n._v("Phỉ Thúy Linh Lung đan, đây chính là đối Hóa Phàm cảnh tam trọng cường giả đều hữu dụng đồ vật, vô cùng trân quý, mỗi một viên đều có thể bán được giá trên trời, hoàn toàn không phải Phỉ Thúy cung, đặc thù trận pháp có thể so sánh."),t("br"),t("br"),n._v("300 linh thạch Phỉ Thúy trúc, muốn hồi vốn, hơn nữa lợi nhuận càng nhiều, tự nhiên muốn dùng to lớn nhất công hiệu, cái khác chỉ là phụ trợ tiểu đạo mà thôi."),t("br"),t("br"),n._v("“Đã ngươi muốn luyện chế Phỉ Thúy Linh Lung đan, vậy ta liền nói ngươi mua hàng giả!”"),t("br"),t("br"),n._v("Đem muốn biết hỏi rõ ràng, Trương Huyền nói."),t("br"),t("br"),n._v("“Hàng giả? Phỉ Thúy trúc toàn thân xanh tươi, tựa như bích ngọc, cành lá trong lúc đó mang theo khe lớn khe hở, đao vạch không thương tổn; Mỗi một cái chi nhánh bên trên, nắm giữ ba cây phiến lá, phiến lá lại có bảy cái gân lá, không nhiều không ít...”"),t("br"),t("br"),n._v("Lão giả không có trả lời, Lưu Xương nhanh chóng đem Phỉ Thúy trúc phân biệt phương pháp nói ra, khuôn sáo chừng mấy chục cái nhiều, mỗi một dạng đều cùng trong tay hắn cầm cây trúc giống nhau như đúc, không có hai gây nên: “Đây là Phỉ Thúy trúc phân biệt phương pháp, không cần phải sư xác nhận, ngay cả ta đều biết, đây tuyệt đối là chính phẩm, không thể giả được. Ngươi lại còn nói là giả? Vậy thì tốt, ví như đây không phải, đó là cái gì?”"),t("br"),t("br"),n._v("“Những này đích xác là phân biệt Phỉ Thúy trúc phương pháp!” Trương Huyền cười khanh khách gật đầu, cũng không phủ nhận."),t("br"),t("br"),n._v("“Ách? Ngươi biết, cái kia còn nói là giả?” Lưu Xương sững sờ."),t("br"),t("br"),n._v("“Kém không tệ, phân biệt phương pháp cũng đúng, căn này cũng đích xác là Phỉ Thúy trúc, không thể giả được!” Trương Huyền nhẹ nhàng nở nụ cười."),t("br"),t("br"),n._v("“Tiền nhân đều là như vậy xác định, ngươi có dám nhận... Hả? Ngươi nói cái gì... Là? Vậy ngươi còn nói giả...”"),t("br"),t("br"),n._v("“Ngươi đang đùa chúng ta?”"),t("br"),t("br"),n._v("Vốn cho là hắn liệu sẽ nhận, không nghĩ tới trực tiếp thừa nhận là Phỉ Thúy trúc, tất cả mọi người đều sửng sốt."),t("br"),t("br"),n._v("Ngay cả Triệu Phi Vũ, Kim Tòng Hải, cũng lông mày nhảy một cái."),t("br"),t("br"),n._v("Mới vừa nói như thế lời thề son sắt, phi thường náo nhiệt, đã cái đồ chơi này chính là, náo cái quỷ a!"),t("br"),t("br"),n._v("Lời thề son sắt nói người ta mua hàng giả, kết quả, lại thừa nhận mua đồ vật thật sự... Coi như tinh thần phân liệt, cũng không trở thành như thế quá trớn đi!"),t("br"),t("br"),n._v("(Mọi người biết điểm xuất phát lưu truyền ba đại bức vương ư? Thứ nhất, Đế bá nhân vật chính Lý Thất Dạ, trang bức như gió. Thứ hai, trùng sinh chi đô thị tu tiên trần bắc huyền, một đời làm việc không cần cùng nhiều người nói. Cái cuối cùng, liền là chúng ta trương điếu bột tử Trương lão sư, thoạt nhìn ngốc ngốc, nhưng có thể trang bức đem người bức điên.. Mọi người xem xong quyển sách, cũng có thể nhìn xem cái khác hai quyển, tuyệt đối thoải mái đến bay lên.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-461"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-463"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);