(window.webpackJsonp=window.webpackJsonp||[]).push([[1702],{2057:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-454-gia-re-mua-thuoc-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-454-gia-re-mua-thuoc-ha"}},[n._v("#")]),n._v(" Chương 454: Giá rẻ mua thuốc (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 454: Giá rẻ mua thuốc (hạ)"),t("br"),t("br"),n._v("“Đùa nghịch? Các ngươi suy nghĩ nhiều!”"),t("br"),t("br"),n._v("Nhìn thấy đối phương vẻ mặt đều tái rồi, Trương Huyền không chút hoang mang, mà là nhẹ nhàng nở nụ cười: “Ta mới vừa nói là ‘Đã ngươi muốn luyện chế Phỉ Thúy Linh Lung đan, vậy ta liền nói ngươi mua hàng giả’, cũng không phải là nói đây không phải Phỉ Thúy trúc!”"),t("br"),t("br"),n._v("Nghe được hắn như thế quấn miệng lời nói, lão giả cùng Lưu Xương liếc mắt nhìn nhau, người sau thực sự nhịn không được: “Ngươi có ý tứ gì?”"),t("br"),t("br"),n._v("Gã sai vặt, Triệu Phi Vũ mấy người cũng một mặt ngây thơ, không biết rõ cái tên này trong hồ lô đến cùng bán là thuốc gì."),t("br"),t("br"),n._v("“Rất đơn giản, đây là Phỉ Thúy trúc không giả, nhưng thật muốn dùng nó lá cây luyện chế Phỉ Thúy Linh Lung đan, có thể cam đoan, ăn chẳng những không có hiệu quả, sẽ còn bởi vậy tử vong!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Tử vong? Nói đùa cái gì. Hóa Phàm cảnh, câu thông thiên địa, linh hồn cảm ngộ tự nhiên, dễ dàng nhất sinh sôi tâm ma, Phỉ Thúy Linh Lung đan có thể khiến người ta nắm giữ Linh Lung chi tâm, cho dù nắm giữ tâm ma, trên phạm vi lớn tiêu trừ tâm ma ảnh hưởng. Chỉ có có ích, làm sao có thể để cho người ta tử vong?”"),t("br"),t("br"),n._v("Lưu Xương khoát tay chặn lại, mặt mũi tràn đầy không tin."),t("br"),t("br"),n._v("Ngay cả lão giả cũng không nhịn được lắc đầu."),t("br"),t("br"),n._v("Hóa Phàm cảnh câu thông tự nhiên, hoà vào thiên địa, tâm cảnh vô hạn tốc độ khuếch trương, lúc này tâm ma rất dễ dàng sinh sôi. Phỉ Thúy Linh Lung đan, chính là tiêu trừ tâm ma tốt nhất dược vật, hắn thậm chí từng dùng qua một viên, hiệu quả chuyện tốt, khó mà nói nên lời, làm sao lại để cho người ta tử vong?"),t("br"),t("br"),n._v("Không phải nói càn là cái gì?"),t("br"),t("br"),n._v("Hơn nữa, làm cao minh giám bảo sư, Linh Lung đan không chỉ ăn qua, còn giúp người khác giám định qua, cũng làm hắn mặt dùng qua, tuyệt không vấn đề."),t("br"),t("br"),n._v("Phục dụng tử vong... Quả thực chưa từng nghe thấy."),t("br"),t("br"),n._v("“Ngươi khả năng chứng minh?”"),t("br"),t("br"),n._v("Hừ một tiếng, lão giả nhìn qua."),t("br"),t("br"),n._v("Không thể chứng minh, liền là ăn nói lung tung."),t("br"),t("br"),n._v("“Đổi lại người khác, loại trừ luyện chế đan dược, để cho người ta phục dụng, thật đúng là không có biện pháp nào khác, bất quá... Ta còn thực sự có phương pháp!”"),t("br"),t("br"),n._v("Dường như sớm biết đối phương có thể như vậy hỏi, Trương Huyền khóe miệng vung lên, nhìn về phía Lưu Xương: “Còn muốn làm phiền ngươi, đi lấy bên dưới một chiếc lá!”"),t("br"),t("br"),n._v("Lưu Xương nhìn thoáng qua lão giả, thấy người sau gật đầu, lúc này mới đi tới, gỡ xuống một mảnh lá non."),t("br"),t("br"),n._v("“Cầm lửa đốt một cái nhìn xem!” Trương Huyền cũng không nhúng tay vào, tiếp tục nói."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("“Theo hắn làm!” Lão giả vẫy tay."),t("br"),t("br"),n._v("Mặc dù không quá tin tưởng cái tên này lời nói, nhưng đối phương thực sự quá bình tĩnh."),t("br"),t("br"),n._v("Nếu thật là nói bậy, đối mặt hắn loại thực lực này cùng uy nghiêm, khẳng định đã sớm luống cuống, cái tên này không những không hoảng hốt, còn khí độ dạt dào, mang theo tràn đầy tự tin, ngay cả hắn, đều cảm thấy hơi nghi hoặc một chút."),t("br"),t("br"),n._v("Chẳng lẽ... Căn này Phỉ Thúy trúc thật hay giả?"),t("br"),t("br"),n._v("Không có khả năng ah!"),t("br"),t("br"),n._v("Hắn nhưng là chuyên môn nhìn nhiều lần, vô luận thân cành, phiến lá, phát ra khí tức, trình độ cứng cáp... Đều không có mảy may vấn đề."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Nghe được lão sư trả lời, Lưu Xương gật gật đầu, lấy ra cây châm lửa, nhìn thoáng qua cách đó không xa thanh niên, hừ lạnh một tiếng: “Cái này một mảnh lá trúc giá trị ít nhất mấy chục linh thạch, nếu như ngươi ăn nói bừa bãi, tổn thất nhưng muốn đền...”"),t("br"),t("br"),n._v("Cái này gốc Phỉ Thúy trúc lá cây tổng cộng cũng là mười mấy mảnh, giá trị 300 linh thạch, chuyển đổi đến mỗi một cái lá cây đỉnh đầu, từng cái có giá trị không nhỏ."),t("br"),t("br"),n._v("Một chiếc lá tại Thiên Vũ vương quốc mua một tòa thành trì, đều tuyệt đối đầy đủ."),t("br"),t("br"),n._v("Nói giá trị liên thành mảy may đều không đủ."),t("br"),t("br"),n._v("Hiện tại cũng bởi vì cái tên này nói là giả, không muốn thiêu hủy một mảnh, tổn thất bao lớn, liền xem như hắn nhị tinh giám bảo sư, cũng nhịn không được cảm thấy thịt đau."),t("br"),t("br"),n._v("“Yên tâm đi, nếu có vấn đề, đền ngươi cũng không có gì. Bất quá... Chỉ sợ vượt qua một hồi, ngươi liền nói không ra lời này...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Ta ngược lại muốn xem xem ngươi làm cái quỷ gì!”"),t("br"),t("br"),n._v("Thấy hắn như vậy tự tin, Lưu Xương hừ một tiếng, cây châm lửa tại phiến lá phía dưới không ngừng thiêu đốt."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Đốt đi đại khái hai phút đồng hồ, đang muốn nói cái gì sự tình đều không có phát sinh, đã cảm thấy bàn tay chấn động, nguyên bản xanh biêng biếc phiến lá, đột nhiên trở nên biến thành màu đen, thuý ngọc gân lá càng là tanh hôi không gì sánh được, tựa như dữ tợn ma quỷ, âm khí âm u."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Giật nảy mình, Lưu Xương một cái không có bắt lấy, cây châm lửa cùng lá trúc đều rơi trên mặt đất."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Một bên Kim Tòng Hải, Cổ Mục liếc mắt nhìn nhau, mỗi người tràn đầy khiếp sợ."),t("br"),t("br"),n._v("“Ừm?”"),t("br"),t("br"),n._v("Lão giả vốn là mặt không biểu tình,"),t("br"),t("br"),n._v("Một mực nhìn xem Trương Huyền, giờ phút này trên mặt cũng thay đổi nhan sắc, thân thể khẽ động cấp tốc đem rơi xuống lá trúc chộp vào trong lòng bàn tay, trong mắt âm tình bất định, dường như khó mà tin được: “Thật là lợi hại kịch độc, nếu như đem thứ này, luyện chế vào vào Phỉ Thúy Linh Lung đan, không những không thể khu trừ tâm ma, sẽ còn dung nhập chân khí, xâm nhập tim phổi, để cho người ta tại chỗ chết bất đắc kỳ tử...”"),t("br"),t("br"),n._v("Nói, trên đầu của hắn đã toát ra mồ hôi lạnh."),t("br"),t("br"),n._v("Nếu như đối phương không nói, trực tiếp đem đồ vật mang về, một khi luyện chế thành đan dược, tất nhiên sẽ hại chết không ít người, danh dự của hắn cũng sẽ bởi vậy hủy hoại chỉ trong chốc lát!"),t("br"),t("br"),n._v("Nhất là chính hắn, lúc đầu cũng nghĩ luyện chế tốt, phục dụng một viên, xung kích sửa đổi cảnh giới..."),t("br"),t("br"),n._v("Nếu là thật ăn... Có thể tưởng tượng, đừng nói danh dự, địa vị, khả năng mạng cũng bị mất."),t("br"),t("br"),n._v("“Đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Khuôn mặt khó coi, lão giả lại không trầm ổn như trước, quay đầu nhìn về phía cách đó không xa bán ra đồ vật gã sai vặt."),t("br"),t("br"),n._v("Leng keng!"),t("br"),t("br"),n._v("Lưu Xương cũng vẻ mặt trầm thấp, cổ tay rung lên, rút kiếm ra khỏi vỏ, đối với gã sai vặt, dường như chỉ cần hắn nói bậy, liền trực tiếp động thủ giết chết."),t("br"),n._v(" Nếu thật là hàng giả ngược lại cũng thôi, nói rõ bọn hắn nhãn lực không tốt, không thể oán trời trách đất. Rất rõ ràng đây là có người tại trên phiến lá xuống kịch độc, trước mắt thanh niên này chỉ nói, cũng không đụng tới, từ đầu đến cuối chỉ có gã sai vặt tiếp xúc qua, không phải hắn là ai?"),t("br"),t("br"),n._v("“Ah... Các chủ tha mạng, ta chỉ là cái nhân viên tiếp đãi, không biết chuyện gì xảy ra, lại nói, loại độc này, liền lão nhân gia người cũng nhìn không ra, ta cũng không có bản sự này ah...”"),t("br"),t("br"),n._v("Gã sai vặt dọa đến đầu gối mềm nhũn quỳ rạp xuống đất, không ngừng dập đầu."),t("br"),t("br"),n._v("Người khác không biết vị lão giả này thân phận cùng nắm giữ lực lượng, hắn nhưng là biết đến rất rõ ràng!"),t("br"),t("br"),n._v("Thật muốn tức giận giết hắn, cũng là chết vô ích."),t("br"),t("br"),n._v("Loại tình huống này nào dám do dự, lập tức quỳ xuống nhận sai."),t("br"),t("br"),n._v("“Tốt, thật sự là hắn không biết!”"),t("br"),t("br"),n._v("Lão giả theo trong lúc khiếp sợ kịp phản ứng, rõ ràng trách lầm đối phương, lúc này khoát khoát tay, để Lưu Xương thu hồi vũ khí."),t("br"),t("br"),n._v("Loại kịch độc này giấu ở Phỉ Thúy trúc phiến lá bên trong, căn bản nhìn không ra, cấp bậc chí cao, chỉ sợ chí ít đạt đến tứ phẩm."),t("br"),t("br"),n._v("Nói cách khác, có thể hạ độc ít nhất là vị tứ tinh độc sư, loại nhân vật này, lại thế nào khả năng cam tâm tại Nhất Phẩm các làm gã sai vặt? Hơn nữa, loại người này thật muốn đối với mình dùng tay, cũng không có bất kỳ phòng bị nào, tuyệt sẽ không khiến cho phức tạp như vậy."),t("br"),t("br"),n._v("Dù sao, Phỉ Thúy trúc chính mình cũng không nhất định sẽ mua, sớm ở bên trong hạ độc, là yếu hại chính mình, rõ ràng không thể nào nói nổi."),t("br"),t("br"),n._v("“Hừ!”"),t("br"),t("br"),n._v("Thu hồi vũ khí, Lưu Xương vẫn như cũ nổi giận đùng đùng."),t("br"),t("br"),n._v("Lão sư mua sắm dược liệu bên trong, thế mà ẩn chứa kịch độc, tin tức này một khi truyền đi, phiền phức bao lớn, có thể tưởng tượng."),t("br"),t("br"),n._v("Đường đường Nhất Phẩm các, lại có vật như vậy tồn tại, đến cùng là ai?"),t("br"),t("br"),n._v("Lại muốn làm cái gì?"),t("br"),t("br"),n._v("“Tiểu huynh đệ, cảm tạ trợ giúp của ngươi!”"),t("br"),t("br"),n._v("Lão giả khôi phục lại, lần nữa nhìn về phía Trương Huyền, không còn là khinh thường, xem kỹ, mà là tán dương, khâm phục."),t("br"),t("br"),n._v("Căn này Phỉ Thúy trúc, ngay cả hắn đều không có nhìn ra vấn đề, đối phương chỉ nhìn lướt qua, liền tới gần đều không có tới gần, liền có thể xác định, phần này nhãn lực, không khỏi thật là đáng sợ!"),t("br"),t("br"),n._v("Chỉ sợ tại giám bảo bên trên cấp bậc, so với hắn cao hơn mấy phần."),t("br"),t("br"),n._v("Đáng sợ!"),t("br"),t("br"),n._v("“Hiện tại xác định là giả?” Trương Huyền mí mắt vừa nhấc."),t("br"),t("br"),n._v("“Là... Giả!” Lão giả chần chờ một chút, gật đầu."),t("br"),t("br"),n._v("Phỉ Thúy trúc, có thể luyện chế đan dược, để cho người ta an thần tĩnh hơi thở tu vi tăng nhiều, hiện tại cái này gốc, nhưng có thể đem người giết chết, nói là giả, cũng không thể quở trách nhiều."),t("br"),t("br"),n._v("“Kỳ thật, ngươi không cần trách hắn, cũng không cần trách Nhất Phẩm các, cái này gốc Phỉ Thúy trúc, cũng không phải là bọn hắn hạ độc, thậm chí, đều không có người hạ độc!” Hai tay chắp sau lưng, Trương Huyền đầu lâu có chút vung lên, thản nhiên nói."),t("br"),t("br"),n._v("“Không có người hạ độc?”"),t("br"),t("br"),n._v("Lão giả nghi hoặc, ngay cả Kim Tòng Hải, Cổ Mục cũng không nhịn được nhìn qua."),t("br"),t("br"),n._v("Những người khác đối độc hiểu được không nhiều, hai người nhưng là chân chính độc sư, từ nhỏ tiếp xúc, biết đến rõ rõ ràng ràng."),t("br"),t("br"),n._v("Vừa rồi cái kia độc, quỷ dị không gì sánh được, dung nhập kinh mạch đều khó mà phát hiện, độc tính chi mãnh liệt, hiệu quả mạnh Hóa Phàm tam trọng đều có thể tuỳ tiện độc chết. Nếu như không phải có một vị tứ tinh độc sư chuyên môn phối trí, thế nào đều nói không thông."),t("br"),t("br"),n._v("Nếu như không có người hạ độc, cái kia... Độc này từ đâu tới?"),t("br"),t("br"),n._v("“Ngươi đi đem Phỉ Thúy trúc nơi sản sinh, thu mua tư liệu lấy tới!”"),t("br"),t("br"),n._v("Không có giải đáp mọi người nghi hoặc, Trương Huyền mà là nhìn về phía vừa rồi gã sai vặt."),t("br"),t("br"),n._v("“Là...”"),t("br"),t("br"),n._v("Nghe thanh niên vì hắn cùng Nhất Phẩm các nói chuyện, gã sai vặt cảm kích nhìn thoáng qua, vội vàng lui ra ngoài, thời gian không dài, lấy ra một cái vở."),t("br"),t("br"),n._v("Một chút lớn cửa hàng, đều biết đem mua sắm bảo vật trải qua, cùng bảo vật lai lịch, kỹ càng dò nghe, ghi chép thành sách, phòng ngừa mua sắm người hỏi thăm."),t("br"),t("br"),n._v("Vừa đến, có thể lộ ra bảo bối giá trị, thứ hai, xảy ra vấn đề cũng có thể thuận tiện thẩm tra."),t("br"),t("br"),n._v("“Đọc ra nghe một chút!” Trương Huyền phân phó."),t("br"),t("br"),n._v("“Ừm!” Gã sai vặt gật gật đầu, lật ra tư liệu: “Phỉ Thúy trúc, nơi sản sinh Hồng húc đầm lầy, do thương khách đoàn phát hiện, cùng bốn tháng trước, dùng phương pháp đặc thù hái xuống, đưa đến nơi đây...”"),t("br"),t("br"),n._v("Nhất Phẩm các bên trong, liên quan tới Phỉ Thúy trúc ghi chép mười phần kỹ càng, bao quát ai đào ra, ai đưa tới, ở nơi đó giao dịch, lại dùng phương pháp gì để hắn duy trì mới mẻ..."),t("br"),t("br"),n._v("Một cái giới thiệu chừng mấy trăm chữ nhiều."),t("br"),t("br"),n._v("Bất quá, nghe xong những này giới thiệu, mọi người không những không có cảm thấy rõ ràng, càng thêm mê hoặc."),t("br"),t("br"),n._v("Bởi vì, những này ghi chép, cùng thanh niên nói đồng dạng, cơ hồ toàn bộ do người qua tay, không có cơ hội đụng phải độc sư, cũng là tự nhiên không có người hạ độc."),t("br"),t("br"),n._v("Thật là muốn không có hạ độc, vừa rồi đó là cái gì?"),t("br"),t("br"),n._v("“Vừa rồi cái kia kịch độc, thâm nhập cành lá, nhưng không thương tổn cây cối, thậm chí bề ngoài đều mảy may nhìn không ra, liền xem như tứ tinh độc sư, sợ rằng cũng phải tốn hao không ít thời gian, mới có thể hoàn thành!”"),t("br"),t("br"),n._v("Kim Tòng Hải vẻ mặt nghiêm túc, cũng đồng thời kỳ quái."),t("br"),t("br"),n._v("Nếu như những này ghi chép thật sự, quả thực không có hạ độc cơ hội."),t("br"),t("br"),n._v("Dù sao, coi như tứ tinh danh sư, muốn đem độc hạ như thế bí ẩn, cũng tuyệt không phải chuyện dễ dàng."),t("br"),t("br"),n._v("Độc không chỉ có thể giết người, lợi hại kịch độc, liền cây cũng có thể tuỳ tiện hạ độc chết."),t("br"),t("br"),n._v("“Đây rốt cuộc chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Càng nghe càng mê hoặc, thực sự nghĩ mãi mà không rõ, Lưu Xương giám bảo sư nhịn không được nhìn về phía Trương Huyền."),t("br"),t("br"),n._v("“Không rõ?”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng nở nụ cười: “Vậy thì tốt, ta liền cho các ngươi giải thích một chút! Hồng húc đầm lầy thừa thãi cái gì, chắc hẳn các vị đều rất rõ ràng đi!”"),t("br"),t("br"),n._v("“Thừa thãi? Nơi này thừa thãi ... Chẳng lẽ cùng thứ này có quan hệ?”"),t("br"),t("br"),n._v("Lão giả nhịn không được nhìn qua."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-462"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-464"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);