(window.webpackJsonp=window.webpackJsonp||[]).push([[1703],{2058:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-455-vo-hon-kim-nhan-chuan-xac-tin-tuc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-455-vo-hon-kim-nhan-chuan-xac-tin-tuc"}},[n._v("#")]),n._v(" Chương 455: Vô Hồn Kim Nhân chuẩn xác tin tức")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 455: Vô Hồn Kim Nhân chuẩn xác tin tức"),i("br"),i("br"),n._v("Khô Diệp trà, lại gọi Khô Vinh trà, là vạn quốc liên minh Hồng húc đầm lầy to lớn nhất đặc sản."),i("br"),i("br"),n._v("Trà sinh trưởng tại đầm lầy bên trong, trong vòng một năm, chỉ có một tháng thời gian, là sống lấy, thời gian khác đều cùng cây khô không khác."),i("br"),i("br"),n._v("Lá như sinh, nhánh như chết, nhất sinh nhất tử, một khô một vinh, cho nên phải tên này."),i("br"),i("br"),n._v("Loại trà này xào kỹ về sau, ngâm chế thành nước, có thể cho người trải qua bốn mùa cảm giác, sảng khoái đến cực điểm, là toàn bộ vạn quốc liên minh nổi danh nhất danh trà một trong."),i("br"),i("br"),n._v("Chẳng qua Hồng húc đầm lầy, mười phần nguy hiểm, Hóa Phàm cảnh cường giả bị thôn phệ, đều vô cùng có khả năng không cách nào đi ra, lại thêm lá trà chỉ có một tháng thời gian, sản lượng rất thấp, có thể chân chính nếm đến người, cũng không nhiều."),i("br"),i("br"),n._v("“Không sai, nơi này thừa thãi Khô Diệp trà, còn có... Khô Diệp xà!” Trương Huyền nói."),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Mọi người gật đầu."),i("br"),i("br"),n._v("Khô Diệp xà là một loại chuyên môn lấy Khô Diệp trà lá trà làm thức ăn linh thú, trong cơ thể ẩn chứa kịch độc, Hóa Phàm tam trọng cường giả bị cắn trúng một cái, cũng sẽ tuỳ tiện tử vong."),i("br"),i("br"),n._v("“Chẳng lẽ... Là Khô Diệp xà độc rắn? Không đúng, Khô Diệp xà mặc dù độc tính cương mãnh, nhưng nó độc, mùi mười phần, hơn nữa mang theo đặc thù xà tiên, để cho người ta liếc mắt liền có thể nhìn thấy. Căn này Phỉ Thúy trúc phía trên, bóng loáng như, không có bất kỳ cái gì dấu vết...”"),i("br"),i("br"),n._v("Lão giả nhịn không được mở lời."),i("br"),i("br"),n._v("Khô Diệp xà, mười phần đáng sợ, Hóa Phàm cảnh cường giả đều muốn chú ý, nếu như có lưu lại nó xà tiên, hẳn là có thể đủ phân biệt ra được."),i("br"),i("br"),n._v("“Không phải xà tiên, mà là... Căn này Phỉ Thúy trúc, là Khô Diệp xà ở lại sào huyệt. Nói cách khác, thoạt nhìn bóng loáng chỉnh tề thân trúc, nhưng ngày ngày có Khô Diệp xà bò qua bò lại.”"),i("br"),i("br"),n._v("Trương Huyền vẻ mặt nghiêm túc."),i("br"),i("br"),n._v("“Ah...”"),i("br"),i("br"),n._v("Nghe nói như thế, cầm sách gã sai vặt, lập tức dọa đến sắc mặt tái nhợt, kém chút không có ngã sấp xuống."),i("br"),i("br"),n._v("Những ngày này một mực chiếu cố cái này có thể cây trúc, thỉnh thoảng lau, vừa nghĩ tới có rắn thường xuyên chiếm cứ ở phía trên, lập tức một hồi hoảng hốt, tràn đầy chán ghét."),i("br"),i("br"),n._v("“Như vậy trải qua, Khô Diệp xà ăn còn lại bài tiết vật, liền sẽ hoặc nhiều hoặc ít đính vào Phỉ Thúy trúc bên trên, trải qua nhiều năm nhật nguyệt bị cây cối hấp thu vào gân lá chỗ sâu!”"),i("br"),i("br"),n._v("Không để ý mọi người không thoải mái, Trương Huyền tiếp tục nói: “Những vật này, bình thường cũng không hiển lộ, thậm chí căn bản nhìn không ra, đối với người cũng không có cái gì ảnh hưởng, chỉ khi nào đụng phải minh hỏa cùng nhiệt lượng, liền sẽ hiển lộ ra, để đụng phải sinh mệnh, ăn được thiệt thòi lớn!”"),i("br"),i("br"),n._v("“Nếu như chỉ đem cây trúc, xem như trận cơ bày trận, ảnh hưởng không lớn, có thể tiếp tục sử dụng. Có thể luyện chế đan dược đan, lại không được. Độc tố hiển lộ, bị lầm phục coi như Hóa Phàm tam trọng, cũng sẽ tại chỗ tử vong, Thần Tiên khó cứu! Bởi vậy, mới có ‘Muốn luyện chế Phỉ Thúy Linh Lung đan, liền nói ngươi mua hàng giả’ như vậy lời nói!”"),i("br"),i("br"),n._v("Trương Huyền giải thích."),i("br"),i("br"),n._v("“Thì ra là thế!”"),i("br"),i("br"),n._v("Mọi người giờ mới hiểu được tới, lần nữa nhìn về phía thanh niên, tràn đầy bội phục."),i("br"),i("br"),n._v("Liếc mắt nhìn ra viên này Phỉ Thúy trúc bên trong che giấu độc tính, cái này muốn đối thực vật thuộc tính, sinh tồn địa điểm cùng lai lịch, hiểu rõ cỡ nào khắc sâu, mới có thể làm đến!"),i("br"),i("br"),n._v("“Lợi hại!”"),i("br"),i("br"),n._v("Lão giả cũng là nắm đấm căng thẳng."),i("br"),i("br"),n._v("Không nói cái khác, đơn phần này nhãn lực, liền để hắn mặc cảm."),i("br"),i("br"),n._v("“Công tử đại tài, để Trại mỗ mở rộng tầm mắt, đã ngươi đối An Thần thảo ưa thích không rời (*), liền tặng cho ngươi, xem như tạ lễ!”"),i("br"),i("br"),n._v("Bàn tay lớn vẫy một cái, lão giả nói."),i("br"),i("br"),n._v("“Lão sư...”"),i("br"),i("br"),n._v("Lưu Xương ngẩn ngơ."),i("br"),i("br"),n._v("Vừa rồi cái tên này muốn năm trăm kim tệ mua sắm An Thần thảo, hắn còn cảm thấy đối phương đầu óc có phải hay không có vấn đề, bây giờ mới biết, nhân gia đã sớm đoán được một màn này, đoán được lão sư sẽ đem thứ này ra tay đưa tiễn."),i("br"),i("br"),n._v("Đây chính là năm trăm linh thạch mới vừa mua đồ vật, chuyển tay đưa tiễn... Có phải hay không quá hào phóng chút?"),i("br"),i("br"),n._v("Coi như chỉ điểm ra Phỉ Thúy trúc là giả, cũng không trở thành như thế bàn tay lớn ah."),i("br"),i("br"),n._v("“Chẳng lẽ... Điều đó không có khả năng! Sao có thể liền cái này cũng đã nhìn ra?”"),i("br"),i("br"),n._v("Đột nhiên, nhớ ra cái gì đó, toàn thân cứng ngắc, không ngừng run rẩy."),i("br"),i("br"),n._v("Hắn biết lão sư vì cái gì hào phóng như vậy."),i("br"),i("br"),n._v("Giám bảo sư một đời chỉ có ba lần phạm sai lầm cơ hội, qua ba lần, liền sẽ bị thủ tiêu tư cách, rơi xuống phàm trần."),i("br"),i("br"),n._v("Lão sư giám bảo vô số, ba lần quy củ, đã đạt tới, nếu như lại sai, liền sẽ gặp được loại cục diện này!"),i("br"),i("br"),n._v("Nói cách khác, nếu là đối phương không ngăn trở, tùy ý cầm cái này gốc Phỉ Thúy trúc,"),i("br"),i("br"),n._v("Đi tìm người luyện dược, có thể đoán được, coi như không trúng độc, cũng sẽ mất đi giám bảo sư tư chất."),i("br"),i("br"),n._v("Những năm này thân cư cao vị, đắc tội qua, có cừu oán không biết bao nhiêu, thật muốn không có thân phận, vô cùng có khả năng bị người đuổi giết, nói cách khác... Đối phương là cứu lão sư một mạng, đừng nói năm trăm linh thạch mua sắm An Thần thảo, coi như giá trị lại lớn, cũng giống vậy cam lòng trả giá."),i("br"),i("br"),n._v("Có thể... Lão sư phá vỡ ba lần quy củ, chỉ ở giám bảo sư nội bộ lưu truyền, trừ phi thân mật người, không người có thể biết, cái tên này làm sao mà biết được? Hơn nữa như thế chắc chắn, sẽ ở cảm kích phía dưới, đem thứ này đưa tiễn?"),i("br"),i("br"),n._v("Kết hợp trước đó lầu một mua đồ trải qua..."),i("br"),i("br"),n._v("Chẳng lẽ cái tên này thoạt nhìn tuổi trẻ, nhưng thật là cái giám bảo vô thượng cao nhân?"),i("br"),i("br"),n._v("Trong lòng phỏng đoán, vội vàng hướng cách đó không xa thanh niên nhìn lại, muốn xem ra có phải hay không cùng phỏng đoán đồng dạng, chỉ thấy thanh niên một mặt chính khí: “Lễ vật quý giá như vậy, trực tiếp đưa tặng, Trương mỗ thực sự không dám nhận. Tôn Cường, cho vị tiền bối này năm trăm kim tệ, chúng ta đem đồ vật mua xuống, không thể để cho tiền bối ăn thiệt thòi!”"),i("br"),i("br"),n._v("“Phốc!”"),i("br"),i("br"),n._v("Mới vừa còn cảm thấy có phải hay không cái cao nhân, nghe nói như thế, thân thể nhoáng một cái, Lưu Xương kém chút thổ huyết."),i("br"),i("br"),n._v("Em gái ngươi!"),i("br"),i("br"),n._v("“Không dám nhận, không thể để cho tiền bối ăn thiệt thòi”... Nói như thế lẽ thẳng khí hùng, thế nào cái này không biết xấu hổ đây?"),i("br"),i("br"),n._v("Năm trăm kim tệ, cùng không có xuất tiền có cái gì khác nhau?"),i("br"),i("br"),n._v("Đây thật là cái cao nhân nên làm?"),i("br"),n._v(" Vội vàng nhìn về phía lão sư, chỉ thấy hắn cũng quơ quơ, dường như có một ngụm máu tươi giấu ở ngực, cả người buồn bực sắc mặt tái xanh: “Vị công tử này, ta là thật tâm thực lòng cảm kích, tiền... Thì không cần!”"),i("br"),i("br"),n._v("“Tiền bối thật không cần khách khí với ta!” Thanh niên một mặt giữ vững: “Năm trăm kim tệ... Ta vẫn là xuất ra nổi!”"),i("br"),i("br"),n._v("“...” Lưu Xương."),i("br"),i("br"),n._v("Chút tiền ấy, tùy tiện đi đó cái chiến trường nhặt một kiện rác rưởi, cũng đáng nhiều như vậy, đương nhiên xuất ra nổi..."),i("br"),i("br"),n._v("Biết tiếp tục dây dưa tiếp, khẳng định sẽ trực tiếp buồn bực mà chết, lão giả đành phải đón lấy kim tệ, thu vào trữ vật giới chỉ, nói: “Ta nhìn vị công tử này đối giám bảo một nhóm biết quá tường tận, không biết... Là sư thừa người nào, chẳng lẽ cũng là một vị giám bảo sư?”"),i("br"),i("br"),n._v("Lưu Xương cũng vội vàng vểnh tai."),i("br"),i("br"),n._v("Liếc mắt nhìn ra lão sư hắn, đều không thể nhìn ra vấn đề, người này đối giám bảo đã đạt đến cao thâm mạt trắc cảnh giới."),i("br"),i("br"),n._v("Có thể nuôi dưỡng được còn trẻ như vậy giám bảo sư, hắn cũng muốn biết, đến cùng là thần thánh phương nào."),i("br"),i("br"),n._v("“Nhà chúng ta thiếu gia, là danh sư Trương Huyền, lão sư chính là danh sư Dương Huyền.” Tôn Cường tiến về phía trước một bước, mặt mũi tràn đầy tự hào giới thiệu."),i("br"),i("br"),n._v("“Danh sư?”"),i("br"),i("br"),n._v("Lão giả nghiêm sắc mặt, lúc này mới kịp phản ứng, nổi lòng tôn kính."),i("br"),i("br"),n._v("Lưu Xương càng là giật nảy mình."),i("br"),i("br"),n._v("Trước đó hắn còn đối với người này ôm lấy rất bất cẩn gặp, sớm biết loại thân phận này, tuyệt đối không dám."),i("br"),i("br"),n._v("Giám bảo sư mặc dù kiếm tiền nhiều, nhưng cùng danh sư, vẫn là khác biệt rất lớn."),i("br"),i("br"),n._v("Thật giống như trúng thưởng bạo hộ cùng quan viên chính phủ khác nhau, có tiền nữa, tại loại này đệ nhất chức nghiệp trước mặt, cũng không tính được cái gì."),i("br"),i("br"),n._v("Huống chi, trước mắt vị danh sư này, giám bảo trình độ, đừng nói so với hắn, so với lão sư đều chỉ cao không thấp."),i("br"),i("br"),n._v("“Nguyên lai là danh sư, khó trách có như thế nhãn lực, ngược lại là lão hủ thất kính, vừa rồi có bao nhiêu thờ ơ, xin hãy tha lỗi!”"),i("br"),i("br"),n._v("Khôi phục lại, lão giả ôm quyền: “Tại hạ Trại Tiêu Vũ, ngốc già này mấy tuổi, về sau gọi ta Trại huynh là được, tiền bối xưng hô, thực sự không dám nhận!”"),i("br"),i("br"),n._v("Đối phương giám bảo trình độ không kém hắn lại là danh sư, vị lão giả này, lập tức có chấm dứt giao chi tâm."),i("br"),i("br"),n._v("“Trại Tiêu Vũ? Chẳng lẽ là... Giám bảo Trại chủ?”"),i("br"),i("br"),n._v("Nghe được cái tên này, Trương Huyền không có cảm giác gì, Triệu Phi Vũ sững sờ, nhịn không được mở lời."),i("br"),i("br"),n._v("Làm vạn quốc liên minh công chúa, từ nhỏ vừa vui thích đọc sách, đối với cảnh nội rất nhiều công hội người mạnh nhất, vẫn là biết một chút."),i("br"),i("br"),n._v("Nghe nói, giám bảo chủ liền gọi Trại Tiêu Vũ, là vị tứ tinh đỉnh phong giám bảo sư, một đời giám bảo vô số, thậm chí liên minh minh chủ phủ, cũng chuyên môn xin qua, tôn sùng là khách quý."),i("br"),i("br"),n._v("Vốn cho rằng loại người này cao thâm mạt trắc, muốn gặp đều không gặp được, thế nào đều không có nghĩ đến, mới vừa bị Trương Huyền giáo huấn một trận, còn tiện thể gài bẫy một gốc An Thần thảo..."),i("br"),i("br"),n._v("“Chủ chi vị chỉ là rất nhiều bạn cũ nâng đỡ, không đáng giá nhắc tới!” Thấy bị nhận ra thân phận, Trại Tiêu Vũ cười lắc đầu."),i("br"),i("br"),n._v("“Nguyên lai là chủ, vậy liền quá tốt rồi”"),i("br"),i("br"),n._v("Rõ ràng thân phận đối phương, Trương Huyền ánh mắt sáng lên, nhịn không được mở lời: “Nghe... Hồng Hải thành lấy được một đầu Vô Hồn Kim Nhân, không biết tin tức là thật hay không, thứ này lại đang nơi nào?”"),i("br"),i("br"),n._v("Đến Hồng Hải thành chính là vì thứ này."),i("br"),i("br"),n._v("Đã người này là giám bảo chủ, tin tức là nói thật, khẳng định biết."),i("br"),i("br"),n._v("“Vô Hồn Kim Nhân? Nguyên lai Trương huynh đệ là vì cái này mà đến!”"),i("br"),i("br"),n._v("Trại chủ hiểu được, cười cười: “Quả thực có cái này đồ vật, hơn nữa ngay tại giám bảo, đêm nay tiến hành đấu giá.”"),i("br"),i("br"),n._v("“Đêm nay?”"),i("br"),i("br"),n._v("Trương Huyền nắm đấm xiết chặt, lập tức một mặt xấu hổ."),i("br"),i("br"),n._v("Bọn hắn đi tới Hồng Hải thành đã là xế chiều, lại chơi đùa lâu như vậy, trời đang chuẩn bị âm u, đêm nay liền đấu giá, chẳng phải bày tỏ, đấu giá hội lập tức liền muốn bắt đầu?"),i("br"),i("br"),n._v("Trên người hắn... Thế nhưng là liền một khối linh thạch đều không có."),i("br"),i("br"),n._v("Không có tiền liền muốn tham gia đấu giá hội, đoán chừng liền cửa còn không thể nào vào được, liền sẽ bị người trực tiếp đuổi đi ra."),i("br"),i("br"),n._v("Phải biết một chút cỡ lớn phòng đấu giá, trước muốn tiến hành nghiệm chi phí, mới có thể tiến nhập, nếu không người gì đều có thể đi vào, cố ý cố tình nâng giá ép giá lời nói, thị trường đều biết làm hỗn loạn chịu không nổi."),i("br"),i("br"),n._v("“Không sai, ngay tại một canh giờ sau, Trương huynh đệ nếu như đối cái này có hứng thú, ta ngược lại thật ra có thể cho ngươi lưu căn phòng nhỏ!”"),i("br"),i("br"),n._v("Trại Tiêu Vũ nói."),i("br"),i("br"),n._v("“Vậy liền đa tạ Trại huynh!”"),i("br"),i("br"),n._v("Cảm kích nhẹ gật đầu, Trương Huyền hỏi tiếp: “Cái này Vô Hồn Kim Nhân đại khái giá bao nhiêu vị, nếu quả thật muốn mua lại, cần tốn hao bao nhiêu linh thạch?”"),i("br"),i("br"),n._v("Đánh trước nghe rõ ràng, mới tốt sớm làm chuẩn bị."),i("br"),i("br"),n._v("“Vô Hồn Kim Nhân, là vu hồn sư vật lưu lại, cụ thể giá cả cũng không tốt nói. Chúng ta giám bảo cho ra định giá 1500 linh thạch, có điều, phòng đấu giá đấu giá, thế nào đều muốn gấp bội, chỉ sợ cần phải có 3000 linh thạch mới có thể cầm xuống!”"),i("br"),i("br"),n._v("Giải thích một câu, Trại Tiêu Vũ cười nhìn qua: “Thế nào, Trương huynh đệ muốn ra tay?”"),i("br"),i("br"),n._v("“Là có chút ý tứ, chỉ là... Tiền trên người không quá đủ...”"),i("br"),i("br"),n._v("Trương Huyền lúng túng vò đầu."),i("br"),i("br"),n._v("“Không quá đủ?” Trại Tiêu Vũ cười một tiếng: “Cái này không sao, vi huynh ngược lại là có chút tích súc, có thể cho ngươi mượn một chút làm bổ khuyết. Không biết lão đệ còn thiếu bao nhiêu? Cứ mở miệng chính là.”"),i("br"),i("br"),n._v("“Mượn một chút? Vậy thì tốt quá!”"),i("br"),i("br"),n._v("Trương Huyền mặt mũi tràn đầy cảm kích: “Ta còn thiếu 3000 linh thạch, ngươi có thể... Mượn ư?”"),i("br"),i("br"),n._v("“...” Trại Tiêu Vũ."),i("br"),i("br"),n._v("“...” Lưu Xương."),i("br"),i("br"),n._v("“...” Mọi người."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-463"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-465"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);