(window.webpackJsonp=window.webpackJsonp||[]).push([[1704],{2059:function(n,h,i){"use strict";i.r(h);var c=i(44),t=Object(c.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-456-luu-thuong-khuc-thuy"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-456-luu-thuong-khuc-thuy"}},[n._v("#")]),n._v(" Chương 456: Lưu Thương Khúc Thủy")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Ba ngàn linh thạch hẳn là liền đủ mua xuống Vô Hồn Kim Nhân, ngươi muốn mượn nhiều như vậy, tình cảm... Ngươi một khối linh thạch cũng không có..."),i("br"),i("br"),n._v("Triệu Phi Vũ, Kim Tòng Hải xạm mặt lại."),i("br"),i("br"),n._v("Khó trách cái tên này mỗi lần đều dùng kim tệ đi mua đồ vật, cũng không sợ bị người đánh chết, trước đó còn tưởng rằng là kẻ tài cao gan cũng lớn, muốn chứng minh cái gì... Nguyên lai chuyện như vậy!"),i("br"),i("br"),n._v("Trên tay một phân tiền đều không có, liền dám đến mua sắm Vô Hồn Kim Nhân..."),i("br"),i("br"),n._v("Bọn hắn đều cảm thấy suýt ngất."),i("br"),i("br"),n._v("“Khụ khụ!” Trại Tiêu Vũ ho khan một tiếng, thiếu chút nữa cũng bị kẻ trước mắt này tươi sống nghẹn chết: “Trương Huyền huynh đệ, cái này... Ngươi một khối linh thạch cũng không có?”"),i("br"),i("br"),n._v("“Không có!” Không có cái gì chần chờ, Trương Huyền nhẹ gật đầu."),i("br"),i("br"),n._v("Trên người tổng cộng có tầm mười khối linh thạch, trên đường đều dùng, hiện tại một nghèo hai trắng."),i("br"),i("br"),n._v("Kim tệ mặc dù có mấy chục triệu hơn trăm triệu, có thể ở đây, căn bản không tính là cái gì, nói là nghèo chút xu bạc không có, cũng là lời thật."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Không nghĩ tới trả lời như thế dứt khoát, Trại Tiêu Vũ vẻ mặt đỏ lên kém chút biệt xuất nội thương."),i("br"),i("br"),n._v("Hắn nói vay tiền, là vì hòa hoãn không khí, có ý định cùng đối phương kết giao, không nghĩ tới tưởng thật, mới mở miệng liền là ba ngàn..."),i("br"),i("br"),n._v("Ta X!"),i("br"),i("br"),n._v("Liền xem như Giám Bảo các Các chủ, trên người cũng không có nhiều linh thạch như vậy ah, huống chi còn mới vừa mua hai ngàn linh thạch đồ vật, đưa ngươi một gốc An Thần thảo..."),i("br"),i("br"),n._v("Ăn hôi, cũng không cần ăn như vậy đi!"),i("br"),i("br"),n._v("“Không có? Vậy quên đi, các ngươi Giám Bảo các, có cái gì có thể nhanh chóng kiếm tiền hoạt động không?”"),i("br"),i("br"),n._v("Thấy đối phương bộ dáng này, biết chắc không bỏ ra nổi đến, Trương Huyền nhịn không được lắc đầu."),i("br"),i("br"),n._v("Kỳ thật hắn cũng chỉ là nói một chút, cũng không có hi vọng vay tiền liền có thể mua được Vô Hồn Kim Nhân."),i("br"),i("br"),n._v("Huống chi, cùng đối phương chỉ là bèo nước gặp nhau, kết giao không sâu."),i("br"),i("br"),n._v("“Nhanh chóng kiếm tiền? Ngươi muốn hiện tại kiếm tiền, sau đó... Tham gia một canh giờ sau đấu giá hội, còn muốn mua xuống Vô Hồn Kim Nhân?” Trại Tiêu Vũ cùng mọi người kém chút thổ huyết."),i("br"),i("br"),n._v("Cái này não động cũng quá lớn đi!"),i("br"),i("br"),n._v("Linh thạch nếu là tốt như vậy lợi nhuận, bọn hắn còn về phần chạy tới tìm cái gì bảo vật, kiếm lấy chênh lệch giá?"),i("br"),i("br"),n._v("Thật có loại này tốt hoạt động, khẳng định chính mình đã sớm xông tới, còn có thể chờ ngươi tới?"),i("br"),i("br"),n._v("“Ừm, nhất định phải đuổi tại đấu giá hội trước, kiếm được ba ngàn linh thạch!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("Hắn nhất định phải đuổi tại trong vòng một canh giờ, kiếm lấy nhiều tiền như vậy, mới có thể mua xuống Vô Hồn Kim Nhân, nếu không, chẳng khác nào đến không, đánh thức Lộ Xung hi vọng, cũng đem bỏ lỡ cơ hội."),i("br"),i("br"),n._v("“Cái này bây giờ không có biện pháp, ba ngàn linh thạch, thật sự là cái số lượng lớn...”"),i("br"),i("br"),n._v("Thấy hắn xác nhận, thật có loại này ngây thơ tới cực điểm ý nghĩ, Trại Tiêu Vũ bất đắc dĩ lắc đầu."),i("br"),i("br"),n._v("Bọn hắn lựa chọn sử dụng bảo bối mua sắm vật phẩm, cũng chỉ kiếm lấy ít ỏi chênh lệch giá, gom góp tích luỹ ba ngàn linh thạch, liền xem như hắn, cũng muốn tốn hao chí ít mấy năm công phu, tiền đề vẫn là bảo vật đầy đủ, có thể tìm tới người mua."),i("br"),i("br"),n._v("Hai cái điều kiện không vừa lòng, muốn kiếm tiền, không có cửa đâu, có đôi khi rất nhiều bảo bối, thậm chí sẽ đập ở trong tay chính mình, bán đều bán không được."),i("br"),i("br"),n._v("Một canh giờ tựa như lợi nhuận ba ngàn... Coi như nằm mơ cũng không phải làm như vậy phải đi!"),i("br"),i("br"),n._v("“Lão sư...”"),i("br"),i("br"),n._v("Nói còn chưa dứt lời, liền nghe đến Lưu Xương thanh âm vang lên."),i("br"),i("br"),n._v("Quay đầu nhìn qua, chỉ thấy người học sinh này một mặt chần chờ: “Cái kia... Có lẽ có thể khiến người ta trong thời gian ngắn kiếm lấy nhiều như vậy!”"),i("br"),i("br"),n._v("“Cái kia? Ngươi nói chẳng lẽ là... ?”"),i("br"),i("br"),n._v("Chấn động toàn thân, Trại Tiêu Vũ ngay sau đó cười khổ lắc đầu: “Điều đó không có khả năng có người có thể hoàn thành! Giám Bảo các thành lập nhiều năm như vậy, đều chưa hề có người từng thành công...”"),i("br"),i("br"),n._v("“Nhưng muốn một canh giờ kiếm lấy ba ngàn linh thạch, cũng chỉ có cái này một cái biện pháp, cái khác... Ta thực sự không nghĩ ra được!” Lưu Xương vò đầu."),i("br"),i("br"),n._v("Giám bảo sư cái nghề nghiệp này, mặc dù kiếm tiền, thế nhưng không phải cướp bóc, một giám bảo liền lượng lớn lượng lớn linh thạch đến đây."),i("br"),i("br"),n._v("Giúp người giám bảo, chỉ lấy lấy nhất định tỉ lệ lợi nhuận, bình thường tới giám định, giá trị cơ bản đều tại mấy chục linh thạch, hơn trăm đều rất ít, lợi nhuận cực kỳ yếu kém. Lại nói, một ngày tổng cộng cũng không có nhiều người tới, dù sao ai cũng không muốn tốn nhiều tiền."),i("br"),i("br"),n._v("Bởi vậy, dùng loại phương pháp này, một canh giờ tích lũy ba ngàn linh thạch, đích xác là nằm mơ, hoàn toàn không có khả năng."),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Trại Tiêu Vũ chần chờ."),i("br"),i("br"),n._v("Hắn biết học sinh nói rất đúng."),i("br"),i("br"),n._v("Coi như giám bảo sư chức nghiệp rất kiếm tiền, ba ngàn linh thạch cũng tuyệt đối là cái số lượng lớn, không phải một sớm một chiều, có thể lấy được."),i("br"),i("br"),n._v("“Thế nào? Chẳng lẽ có biện pháp? Lưu Thương Khúc Thủy có ý tứ gì?” Đem hai người đối thoại nghe vào trong tai, Trương Huyền nhìn qua."),i("br"),i("br"),n._v("“Lưu Thương Khúc Thủy, là chúng ta Giám Bảo các, khảo hạch ngũ tinh giám bảo sư một cái cửa ải, chỉ cần thông qua, có thể lấy được Giám Bảo các gom góp tích luỹ nhiều năm như vậy ban thưởng, không sai biệt lắm... Đã có năm ngàn linh thạch nhiều...”"),i("br"),i("br"),n._v("Thấy đối phương nghe được, Trại Tiêu Vũ đành phải giải thích."),i("br"),i("br"),n._v("“Cửa ải?” Trương Huyền nghi hoặc: “Khảo hạch cấp bậc cao giám bảo sư, còn có ban thưởng?”"),i("br"),n._v(" Hắn khảo hạch qua danh sư, tuần thú sư, thư họa sư, y sư... Còn chưa từng nghe qua, khảo hạch cấp bậc cao hơn chức nghiệp, có thể thu hoạch được ban thưởng."),i("br"),i("br"),n._v("Khảo hạch chức nghiệp có thể kiếm tiền, lần đầu tiên nghe nói, nếu là như thế, giám bảo sư thật đúng là hẳn là kiểm tra một chút."),i("br"),i("br"),n._v("“Là có ban thưởng, chẳng qua đây đều là... Nhiều năm để dành tới treo giải thưởng.” Nhìn ra ý nghĩ của hắn, Trại Tiêu Vũ có chút bất đắc dĩ, đành phải tiếp tục nói."),i("br"),i("br"),n._v("“Treo giải thưởng?” Trương Huyền nháy con mắt, mọi người khác cũng nhìn qua."),i("br"),i("br"),n._v("Triệu Phi Vũ mặc dù nhìn qua không ít sách vở, có thể đây là Giám Bảo các nội bộ quy củ, cũng không biết được."),i("br"),i("br"),n._v("“Ừm, Giám Bảo các mặc dù có rất nhiều giám bảo sư, có thể... Nhân lực có hạn, bị giới hạn cấp bậc cùng ánh mắt, cũng không ít bảo vật, là nhận không ra. Cái này đều chút bảo bối, trải qua một đời lại một đời giám bảo sư thay nhau giám định, lại không người có thể cho ra chuẩn xác nhất kết quả.”"),i("br"),i("br"),n._v("“Một chút tiền bối, vì giải trừ nghi hoặc, tự móc tiền túi, cho ra treo giải thưởng, để chân chính hiểu bảo vật người, đến đây quan sát, cũng cổ vũ hậu bối, hắn cố gắng học tập, tranh thủ sớm ngày giải quyết. Đáng tiếc... Từ đầu đến cuối không có người thành công, nương theo thời gian trì hoãn, treo giải thưởng càng ngày càng nhiều, tích lũy tháng ngày phía dưới, đã đạt đến năm ngàn linh thạch nhiều.”"),i("br"),i("br"),n._v("Trại Tiêu Vũ nói."),i("br"),i("br"),n._v("Giám bảo sư cùng những nghề nghiệp khác không giống, phân biệt đi ra bảo vật, liền muốn lấy tiền, ngay sau đó làm ra treo giải thưởng, nương theo tích lũy càng ngày càng nhiều, ban thưởng cũng là càng lúc càng lớn, đáng tiếc... Rất nhiều tiền bối đều không thể nhận ra bảo vật, muốn nhận ra nào có dễ dàng như vậy!"),i("br"),i("br"),n._v("“Còn có loại sự tình này?” Trương Huyền trừng to mắt."),i("br"),i("br"),n._v("Cái này cùng y sư nghi vấn khó xử lý tường có chút giống, một cái công hội coi như mạnh hơn, lại nhiều đặc thù chức nghiệp giả, cũng hầu như sẽ có không giải được nan đề."),i("br"),i("br"),n._v("“Đúng vậy a! Giám Bảo các cái này nhiều năm, không biết có bao nhiêu thiên tài giám bảo sư, đều thử qua, đáng tiếc, đều không có thành công!” Trại Tiêu Vũ lắc đầu."),i("br"),i("br"),n._v("Vừa rồi hắn cũng không phải là không nghĩ tới cái này, mà là cái này quá khó khăn, hắn vị này vạn quốc liên minh lợi hại nhất giám bảo sư, đều bất lực, huống chi người khác."),i("br"),i("br"),n._v("“Không thành công? Đã ai cũng không biết là bảo bối gì, lại như thế nào xác nhận thành công không thành công?” Trương Huyền kỳ quái."),i("br"),i("br"),n._v("Đã những bảo bối này, tất cả tiền bối đều nhận không ra, lại thế nào xác nhận bọn hậu bối trả lời có phải hay không chính xác, coi như nhận không ra, tùy tiện viết cái tên, thuộc tính, cũng không thể nào phán đoán đi."),i("br"),i("br"),n._v("“Bảo vật đạt tới Linh cấp, liền có linh tính, chỉ cần nói không có vấn đề, nhất định sẽ lấy được tán thành. Lại nói, chúng ta đều là chính thức giám bảo sư, coi như bảo vật nhận không ra, nhưng đối phương nói ra đặc tính, muốn biết chính xác hay không, vẫn là rất dễ dàng kiểm nghiệm!”"),i("br"),i("br"),n._v("Trại Tiêu Vũ bất đắc dĩ giải thích."),i("br"),i("br"),n._v("Chỉ cần là giám bảo sư đều hiểu những này, trước mắt vị này thế mà còn kỳ quái, thật là một cái so với chính mình giám bảo còn lợi hại hơn gia hỏa?"),i("br"),i("br"),n._v("“Như thế...” Trương Huyền lần này hiểu được."),i("br"),i("br"),n._v("Làm lợi hại giám bảo sư, coi như phân biệt không ra bảo vật cụ thể là cái gì, chỉ cần có người nói đặc thù, có chính xác không, vẫn là có biện pháp nghiệm chứng."),i("br"),i("br"),n._v("Liền chút năng lực nhỏ nhoi ấy không có, còn làm cái gì giám bảo sư?"),i("br"),i("br"),n._v("“Được rồi, mang ta đi nhìn xem, ta nghĩ thử một chút!” Biết phương pháp kia có thể rất mau tới tiền, Trương Huyền xác định được."),i("br"),i("br"),n._v("“Trương huynh đệ trước không vội, nghe ta nói hết lời, mới quyết định!” Thấy hắn gấp gáp như vậy, Trại Tiêu Vũ nhìn qua."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("“Tham gia cái này khiêu chiến, có ban thưởng, tự nhiên có tiền đặt cược, mỗi lần khiêu chiến, đều cần nộp lên một trăm linh thạch, làm tiền thế chấp! Đương nhiên, cái này tiền thế chấp ta có thể thay ngươi giao, không cần lại đi chuẩn bị.”"),i("br"),i("br"),n._v("Trại Tiêu Vũ nói."),i("br"),i("br"),n._v("“100 linh thạch?” Trương Huyền sững sờ."),i("br"),i("br"),n._v("Thế giới không uổng công cơm trưa, quả là thế."),i("br"),i("br"),n._v("Còn tưởng rằng có thể miễn phí tham gia, hiện tại xem ra, cũng không phải là như thế."),i("br"),i("br"),n._v("Cũng đúng, nếu là không có cái giới hạn, chỉ cần là giám bảo sư đều muốn đi khiêu chiến một cái, toàn bộ Giám Bảo các cũng không cần làm chuyện khác."),i("br"),i("br"),n._v("“Không chỉ có như thế, cái này khiêu chiến đối giám bảo thời gian giới hạn, cũng cực kỳ hà khắc!”"),i("br"),i("br"),n._v("Trại Tiêu Vũ nói tiếp: “Lưu Thương Khúc Thủy, tên như ý nghĩa, liền là tại uốn lượn dòng nước trước mặt, đem bảo vật dùng dòng nước đưa xuống đến, quan sát cộng thêm nghiệm chứng, chỉ có mười mấy hô hấp công phu, về sau liền muốn nói ra bảo vật danh xưng lai lịch!”"),i("br"),i("br"),n._v("“Mười mấy hô hấp, liền muốn nói ra lai lịch? Cái này sao có thể?” Trương Huyền không nói chuyện, một bên Triệu Phi Vũ mở to hai mắt nhìn."),i("br"),i("br"),n._v("Giám bảo sư phân biệt, cần các loại quan sát, trong đó không thiếu sử dụng phóng đại thủy tinh, nghiên cứu hoa văn, cân nhắc toàn thân."),i("br"),i("br"),n._v("Mỗi một kiện bảo vật, đều cần tốn hao hồi lâu mới có thể hoàn thành, coi như quen thuộc, cũng muốn xác nhận nhiều lần mới có thể đáp lại."),i("br"),i("br"),n._v("Mười mấy hô hấp, chỉ là bảo vật thông qua dòng nước chảy xuôi đến bên người công phu, liền muốn xác định đáp án, không khỏi quá kinh khủng đi!"),i("br"),i("br"),n._v("Hơn nữa còn là quan sát, chưa hề có người có thể phân biệt ra được bảo bối?"),i("br"),i("br"),n._v("“Đang quan sát trước đó, có cái gì nhắc nhở, hoặc là tư liệu?” Triệu Phi Vũ hỏi tiếp."),i("br"),i("br"),n._v("Nếu có tư liệu, sớm có chuẩn bị tâm lý, vừa đến liền có thể tìm tới thiên về điểm, có lẽ sẽ đơn giản chút."),i("br"),i("br"),n._v("“Không có! Sở dĩ không cho tư liệu, chính là sợ lừa dối phân biệt!” Trại Tiêu Vũ nói."),i("br"),i("br"),n._v("Nếu như biết là cái gì, cái này cửa ải sớm đã có người thông qua được, không biết cụ thể là loại nào bảo vật, coi như cho tư liệu, cũng là không hoàn toàn, còn vô cùng có khả năng lừa dối phán đoán, cuối cùng không có cách nào làm ra chính xác giải đáp."),i("br"),i("br"),n._v("“Cũng thế...”"),i("br"),i("br"),n._v("Triệu Phi Vũ mày nhíu lại thành u cục."),i("br"),i("br"),n._v("Lưu Thương Khúc Thủy nghe có thể nhanh chóng kiếm tiền, có thể... Thực sự quá khó khăn, đừng nói tứ tinh giám bảo sư, liền xem như ngũ tinh, muốn làm đến, chỉ sợ cũng không dễ dàng như vậy."),i("br"),i("br"),n._v("Đang muốn khuyên can người thiếu niên trước mắt này, đừng lại hy vọng xa vời, chỉ thấy Trương Huyền cười nhạt một tiếng: “Tốt, ta muốn tham gia cái này Lưu Thương Khúc Thủy khiêu chiến, mượn trước ngươi một trăm linh thạch, chờ ta thông qua được, gấp bội hoàn trả!”"),i("br"),i("br"),n._v("PS: Thứ hai cầu phiếu đề cử cùng nguyệt phiếu. Mặt khác, gửi cho bạn bè quyển sách, đại thần thịt hầm nồi lớn món ăn sách mới 《 vô địch thức tỉnh 》, mầm non, có thể nuôi thành."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-464"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-466"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=t.exports}}]);