(window.webpackJsonp=window.webpackJsonp||[]).push([[1706],{2061:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-458-thanh-đong-chuy"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-458-thanh-đong-chuy"}},[n._v("#")]),n._v(" Chương 458: Thanh Đồng chùy")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 458: Thanh Đồng chùy"),t("br"),t("br"),n._v("“Ba ngàn linh thạch? Còn chưa tới lớn?”"),t("br"),t("br"),n._v("Trần trưởng lão kém chút không có đã hôn mê."),t("br"),t("br"),n._v("Nhiều linh thạch như vậy, hắn dốc hết tất cả, là có thể lấy ra, thật là muốn như vậy làm, cũng gần như muốn phá sản."),t("br"),t("br"),n._v("Một cái đánh cược cứ như vậy nhiều, ngươi đây là muốn điên ah!"),t("br"),t("br"),n._v("“Ba ngàn linh thạch... Ngươi có nhiều như vậy tiền đặt cược ư?” Thấy bạn cũ xấu hổ, Lộc trưởng lão tiến lên một bước, thay hắn giải vây."),t("br"),t("br"),n._v("“Ta đương nhiên không có!” Mập mạp lắc đầu."),t("br"),t("br"),n._v("“Không có?” Mọi người không còn gì để nói."),t("br"),t("br"),n._v("Nói đến như thế sung sướng, còn tưởng rằng lúc nắm trong tay mấy ngàn, hơn vạn linh thạch, không có... Liền dám đánh cược, còn nói như thế lẽ thẳng khí hùng? Lá gan cũng không tránh khỏi quá mập đi!"),t("br"),t("br"),n._v("Một bên Triệu Phi Vũ cũng vỗ trán một cái."),t("br"),t("br"),n._v("Vị này mập mạp không sợ không sợ trời hai hàng bộ dáng, hắn nhưng là được chứng kiến nhiều lần lắm rồi, không nghĩ tới trực tiếp tìm người ta tứ tinh giám bảo sư đánh cược... Mấu chốt còn không có tiền!"),t("br"),t("br"),n._v("Ta đi, ngươi thật là mạnh!"),t("br"),t("br"),n._v("“Thật sự là có dạng gì chủ nhân, liền có dạng gì hạ nhân...”"),t("br"),t("br"),n._v("Nhịn không được lắc đầu."),t("br"),t("br"),n._v("Mập mạp này Tôn Cường thiếu chủ, liền là cái e sợ cho thiên hạ bất loạn gia hỏa, trước mặt mọi người giáo huấn Trại chủ không sợ bị đánh chết, hiện tại cái tên này thoạt nhìn ác hơn, thật không biết bọn này quái thai, đều từ nơi nào xuất hiện."),t("br"),t("br"),n._v("“Không có tiền, đánh cái gì cược?” Trần trưởng lão khuôn mặt xanh xám."),t("br"),t("br"),n._v("“Ta là không có tiền, Trại chủ có, ba ngàn linh thạch mà thôi, hắn sẽ cho mượn!” Mập mạp Tôn Cường mỉm cười."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Một bên xem náo nhiệt Trại chủ, không nghĩ tới chiến hỏa một cái đốt đến trên người mình, kém chút thổ huyết."),t("br"),t("br"),n._v("Hắn là nói qua muốn mượn tiền cho Trương Huyền, có thể cái kia là muốn kết giao đối phương lời khách khí, cái này hai chủ tớ người ngược lại tốt, một cái mở lời cho mượn ba ngàn hay sao, cái này càng là cho mượn ba ngàn đánh cược... Ta liền khách khí một cái, các ngươi còn tưởng là thật?"),t("br"),t("br"),n._v("Muốn hay không như thế hố?"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Đang muốn giải thích một câu, lời còn chưa nói ra, chỉ thấy trước mắt mập mạp, khoát tay chặn lại: “Coi như Trại chủ hẹp hòi, không tại nhớ thiếu gia đối với hắn ân tình, vậy cũng không sao, Triệu Phi Vũ công tử khẳng định sẽ cho ta mượn, ba ngàn linh thạch mà thôi, đối với công tử tới nói, cũng không tính cái gì đi!”"),t("br"),t("br"),n._v("“...” Triệu Phi Vũ."),t("br"),t("br"),n._v("Ta chính là cái đánh xì dầu, thế nào lửa lại đốt tới trên người ta..."),t("br"),t("br"),n._v("Trại chủ càng là con mắt trắng bệch."),t("br"),t("br"),n._v("Vì sao kêu ta hẹp hòi, không nhớ Trương Huyền ân tình? Ta không phải đưa cho hắn một gốc An Thần thảo sao? Thậm chí còn không để ý hồ đồ, lãng phí một trăm linh thạch, để hắn đi vào Lưu Thương Khúc Thủy..."),t("br"),t("br"),n._v("Như thế mà còn không gọi là báo ân?"),t("br"),t("br"),n._v("Hai người đang muốn chen vào nói, Tôn Cường lần nữa khoát tay áo: “Nhìn, bọn hắn đều không có phản đối, nói rõ ba ngàn linh thạch đối bọn hắn tới nói không tính là gì! Yên tâm đi, chỉ cần thua, khẳng định cầm ra được, mấu chốt là ngươi... Sẽ không vừa rồi trên miệng nói lời thề son sắt, trên thực tế cũng không dám đi? Còn tứ tinh giám bảo sư, liền chút tiền như vậy đều không bỏ ra nổi đến, không dám đánh cược, mất mặt...”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghĩ tới bị một cái hạ nhân khinh bỉ, Trần trưởng lão khuôn mặt xanh xám, suýt tức điên."),t("br"),t("br"),n._v("“Cái gì ngươi nha, ta nha, thực sự không dám lời nói, coi như xong. Bất quá, phải quản lý tốt miệng của ngươi, không nên nói lung tung, nhà chúng ta thiếu gia, thiên tư vô song, một cái nho nhỏ Lưu Thương Khúc Thủy mà thôi, không đáng kể chút nào!”"),t("br"),t("br"),n._v("Hừ một tiếng, Tôn Cường đầu lâu vung lên, đối Trương Huyền tràn đầy lòng tin."),t("br"),t("br"),n._v("“Tốt, ta và ngươi cược!” Thấy cái tên này thái độ càng ngày càng ngạo mạn, Trần trưởng lão cũng nhịn không được nữa."),t("br"),t("br"),n._v("Dựa theo đạo lý, hắn đường đường tứ tinh giám bảo sư, cùng một cái hạ nhân đưa khí, bây giờ nói không đi qua, có thể mập mạp này thực sự quá phận."),t("br"),t("br"),n._v("Ngữ khí không nghiêm túc, thái độ ngạo mạn, nhất là bây giờ, đầy vẻ khinh bỉ bộ dáng... Thật giống như hắn vị này đi đến bất kỳ địa phương nào đều được người tôn trọng tứ tinh giám bảo sư, cùng không biết xấu hổ ăn mày đồng dạng."),t("br"),t("br"),n._v("Bị ngũ tinh giám bảo sư, tứ tinh danh sư, thậm chí chủ khinh bỉ, hắn đều có thể nhẫn, bị một cái tu vi chỉ có, chỉ có... Dù sao rất thấp hạ nhân khinh bỉ, thực sự nuốt không trôi!"),t("br"),t("br"),n._v("“Không sai, còn có dám đánh cược không? Các ngươi có phải hay không cũng không tin nhà chúng ta thiếu gia có thể quá quan? Hiện tại cược còn kịp, nếu không, qua thôn này liền không có tiệm này...”"),t("br"),t("br"),n._v("Thấy đối phương bị tức đáp ứng, Tôn Cường cười hắc hắc, nhìn quanh một vòng, thương nhân tốt lợi khuôn mặt hiển thị rõ đi ra."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Nhìn thấy cái tên này cùng Trần trưởng lão đánh cược còn chưa đủ, thế mà còn muốn cùng mọi người đánh cược, mọi người tất cả đều im lặng."),t("br"),t("br"),n._v("“Mau nhìn, Lưu Thương Khúc Thủy bắt đầu...”"),t("br"),t("br"),n._v("Tôn Cường lớn tiếng mê hoặc, nhìn xem còn có thể hay không tiếp tục lừa gạt mấy người đánh cược, liền nghe đến trong đám người một thanh âm vang lên, ngẩng đầu hướng ngay phía trước mặt tường nhìn lại, chỉ thấy ánh sáng càng ngày càng thịnh, dường như bên trong một loại nào đó trận pháp bị triệt để khởi động lên."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trong trận pháp."),t("br"),t("br"),n._v("Đi vào đại môn, tiến vào thật giống không phải cái gian phòng, mà là một cái vườn hoa, trong đó sắc màu rực rỡ, hoa mai xông vào mũi, một đạo khe suối, từ đằng xa chậm rãi chảy xuôi mà đến, đập nham thạch, ra “Leng keng!” Thanh âm, tựa như tấu vang chương nhạc."),t("br"),t("br"),n._v("Khe suối uốn lượn chảy xuôi, tại phía trước tạo thành một cái vòng tròn, vừa vặn có thể để người ta đứng thẳng."),t("br"),t("br"),n._v("Đi tới trước mặt, trong nháy mắt cái nào đó trận pháp bị kích hoạt, một chiếc lá theo dòng suối chỗ sâu chảy xuôi tới."),t("br"),t("br"),n._v("Trên phiến lá trưng bày một cái kỳ quái hình dạng vật phẩm, hiện lên không theo quy tắc tám mặt tính, để cho người ta nhìn không ra đến cùng là cái gì."),t("br"),t("br"),n._v("“Xem ra đây chính là muốn khảo hạch bảo vật...”"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn vận chuyển, xuất hiện thế mà nhìn không thấu, Trương Huyền nhịn không được ánh mắt sáng lên."),t("br"),t("br"),n._v("Không hổ là làm khó tứ tinh danh sư bảo bối, Minh Lý Chi Nhãn coi như nhìn vật phẩm bên trên, càng hơn một bậc, cũng hoàn toàn không có đầu mối."),t("br"),n._v(" Lưu Thương Khúc Thủy, cái gọi là thương nhưng thật ra là một loại chén rượu, văn nhân mặc khách xưng hô, bọn hắn ngồi tại khe suối một bên, thương thuận dòng trôi nổi, đi tới ai trước mặt, ai liền làm thơ uống rượu."),t("br"),t("br"),n._v("Bảo vật nặng nề tự nhiên không có khả năng giống chén rượu đồng dạng tại trên nước trôi nổi, mà là dùng Tử Động thụ lá cây đến gánh chịu."),t("br"),t("br"),n._v("Loại này lá cây rất nhẹ, phiêu phù ở trên nước, tải trọng một, hai người đều không có vấn đề gì, bởi vậy, giám bảo Lưu Thương Khúc Thủy, cũng gọi Lưu Diệp Khúc Thủy."),t("br"),t("br"),n._v("“Đây rốt cuộc là cái gì?”"),t("br"),t("br"),n._v("Lá cây còn chưa tới đến trước mặt, bởi vậy chỉ có thể nhìn trước mắt kiện bảo bối này hình dạng cùng bên ngoài."),t("br"),t("br"),n._v("Dưa hấu lớn nhỏ, toàn thân ám tử sắc, thoạt nhìn như là đặc thù nào đó kim loại đúc thành."),t("br"),t("br"),n._v("Vừa cẩn thận nhìn qua, Trương Huyền nhịn không được lắc đầu."),t("br"),t("br"),n._v("Hắn chưa có xem liên quan tới giám bảo thư tịch, đối với cái nghề nghiệp này, biết đến cũng không rõ ràng, chỉ có thể nhìn cái đại khái."),t("br"),t("br"),n._v("Nhiều như vậy cao minh giám bảo sư cũng nhìn không ra, hắn loại này chưa hề giám định trôi qua người, lại như thế nào có thể rõ ràng."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Không nghĩ nhiều nữa, cũng không tại quan sát thứ này, tiện tay đem một bên chuẩn bị xong giấy cầm lấy, lẳng lặng chờ lá cây đi tới trước mặt, đưa tay ở phía trên đụng một cái."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Một quyển sách xuất hiện tại trong óc."),t("br"),t("br"),n._v("Dám đến xông Lưu Thương Khúc Thủy, chủ yếu bởi vì có Thiên Đạo thư viện làm dựa dẫm, nếu không, cái kia điểm ít ỏi giám bảo thuật, đừng nói một kiện bảo bối, nửa cái khẳng định cũng không nhận ra được."),t("br"),t("br"),n._v("Mở sách tịch, cầm lấy lông ngay tại trên giấy viết đi."),t("br"),t("br"),n._v("“Thanh Đồng chùy, ba ngàn năm trước, ngũ tinh luyện khí sư Đoan Mộc Nhất Quang vật phẩm, thu thập Tử Kim thiết, Bách Chiến ngân, Thanh Trần cát... Tổng cộng mười ba chủng hi hữu khoáng thạch dung hợp mà thành, rèn đúc bảy bảy bốn mươi chín ngày...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trương Huyền ở bên trong phấn viết nhanh, bên ngoài mọi người đang từng cái nhìn xem bức tường, một mặt khẩn trương."),t("br"),t("br"),n._v("Tôn Cường bốn phía tìm người đánh cược, cũng lại không có người để ý tới, tất cả mọi người muốn nhìn một chút, cái này không đủ hai mươi thanh niên, đến cùng có thể hay không giám định ra một kiện bảo bối, từ đó thuận lợi quá quan."),t("br"),t("br"),n._v("“Nhìn thời gian, hẳn là cái thứ nhất bảo vật đi tới trước mặt đi, các ngươi nói hắn có thể thành công ư?” Trong đám người một cái giám bảo sư nhịn không được nói."),t("br"),t("br"),n._v("“Thành công? Nói đùa cái gì! Lưu Thương Khúc Thủy Trại chủ đều xông qua nhiều lần, một kiện cũng chưa nhận ra được, đủ thấy độ khó, hắn một tiểu nhân vật làm sao có thể thành công?”"),t("br"),t("br"),n._v("“Không nói cái khác, liền nói cái kia hạ nhân tùy tiện bộ dạng, chân chính giám bảo sư, có phách lối như vậy?”"),t("br"),t("br"),n._v("“Cái này còn khó nói, dám đánh cược nói rõ có tự tin...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người thấp giọng, nghị luận ầm ĩ."),t("br"),t("br"),n._v("Ở đây giám bảo sư, trẻ tuổi đều muốn chừng ba mươi tuổi, một cái không đủ hai mươi người liền muốn xông Lưu Thương Khúc Thủy, bọn hắn đều có chút không tiếp thụ được."),t("br"),t("br"),n._v("“Yên tâm đi, Lưu Thương Khúc Thủy bên trong bảo vật, nếu là dễ dàng như vậy có thể phân biệt đi ra, cũng không trở thành bị xem như ngũ tinh giám bảo sư khảo hạch tiêu chuẩn!”"),t("br"),t("br"),n._v("Thấy Trần trưởng lão cũng một mặt khẩn trương nhìn về phía bức tường, Lộc trưởng lão đi tới, an ủi một câu."),t("br"),t("br"),n._v("Đối với cái này Trương Huyền, hắn cũng là không coi trọng."),t("br"),t("br"),n._v("Lưu Thương Khúc Thủy hắn cũng xông qua, trong đó xuất hiện bảo vật, khó mà nhận ra không nói, có thể quan sát thời gian thực sự quá ít, nhiều nhất mười cái hô hấp, liền theo bên người chảy xuôi mà qua, ngắn như vậy thời gian, phân biệt một cái chưa từng thấy qua bảo bối, làm sao có thể thành công."),t("br"),t("br"),n._v("“Lo lắng, có gì có thể lo lắng? Một cái vô tri gia hỏa mà thôi, có phải hay không giám bảo sư cũng còn không biết, liền dám nói khoác không biết ngượng...”"),t("br"),t("br"),n._v("Trần trưởng lão hừ một tiếng, đang muốn nói tiếp, đột nhiên chấn động toàn thân, một giờ tiếng vang."),t("br"),t("br"),n._v("Đông!"),t("br"),t("br"),n._v("Thanh âm xuyên thấu bức tường, giống như là tại sâu trong linh hồn vang lên."),t("br"),t("br"),n._v("“Tiếng chuông? Chẳng lẽ không có thông qua?”"),t("br"),t("br"),n._v("“Không đúng, chỉ có tất cả bảo vật toàn bộ giám định hoàn tất, mới có thể thống nhất đánh giá, vang lên tiếng chuông, bày tỏ không có thông qua, hiện tại theo thời gian suy tính, hắn nhiều nhất chỉ nhìn xong kiện bảo bối thứ nhất, coi như chuông reo, cũng không tới thời gian ah!”"),t("br"),t("br"),n._v("Mọi người sững sờ, tất cả đều mờ mịt."),t("br"),t("br"),n._v("Lưu Thương Khúc Thủy, tiếng chuông vang lên, đại biểu vượt quan thất bại, một cái bảo vật cũng chưa nhận ra được."),t("br"),t("br"),n._v("Nhưng bây giờ vị này Trương Huyền mới đi vào, bảo vật cũng chỉ hẳn là nhìn một cái, làm sao lại kết thúc?"),t("br"),t("br"),n._v("“Nghe nói, cái thứ nhất bảo vật không có nhận ra đến, bất luận cái gì chữ viết đều không có lưu lại, sẽ bị tự động phán định không cách nào đáp lại, trực tiếp phán định thất bại!”"),t("br"),t("br"),n._v("“Còn có loại thuyết pháp này?”"),t("br"),t("br"),n._v("“Đúng vậy a, trước kia liền từng có, ta xem qua thư tịch ghi chép!”"),t("br"),t("br"),n._v("Trong đám người không biết ai hô một câu, mọi người lập tức tất cả đều từng cái vẻ mặt cổ quái."),t("br"),t("br"),n._v("Vượt quan người, coi như không cách nào xác nhận cái thứ nhất bảo vật là cái gì, chí ít có thể đoán ra đại khái, viết ra cái phạm vi, nếu thật là cái gì đều không có viết liền phán định thất bại, vậy nhưng thật sự mất mặt!"),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Tất cả mọi người hướng một bên Tôn Cường nhìn lại, trong mắt tràn đầy đồng tình."),t("br"),t("br"),n._v("Không cần nghĩ, cái tên này lần này thua thảm rồi."),t("br"),t("br"),n._v("Leng keng!"),t("br"),t("br"),n._v("Không có kết thúc, liền nghe đến chén trà rơi trên mặt đất thanh âm vang lên."),t("br"),t("br"),n._v("Ngay sau đó liền thấy Trại chủ toàn thân không ngừng run rẩy, sắc mặt tái nhợt."),t("br"),t("br"),n._v("“Đây không phải... Tiếng chuông, mà là... Bảo vật linh tính khuấy động, nói cách khác, hắn không chỉ phân biệt đi ra, còn chiếm được bảo vật linh tính tán thành!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-466"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-468"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);