(window.webpackJsonp=window.webpackJsonp||[]).push([[1707],{2062:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-459-hoa-linh-te-minh-thu-hoa-tan-thanh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-459-hoa-linh-te-minh-thu-hoa-tan-thanh"}},[n._v("#")]),n._v(" Chương 459: Hóa Linh tê minh, thư hoạ tán thành")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 459: Hóa Linh tê minh, thư hoạ tán thành"),t("br"),t("br"),n._v("“Linh tính xao động?”"),t("br"),t("br"),n._v("Trần trưởng lão, Lộc trưởng lão nhìn nhau, đồng thời cứng đờ: “Cái này... Cái này sao có thể?”"),t("br"),t("br"),n._v("“Tiếng chuông dày nặng ngột ngạt, thanh âm bên trong mang theo âm trầm, mà cái này, bén nhọn chói tai, vang vọng nội tâm... Chỉ có trong truyền thuyết linh tính xao động, mới có thể để cho người từ nội tâm chỗ sâu, sinh ra vui vẻ cảm giác...” Trại các chủ nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hai người nhoáng một cái."),t("br"),t("br"),n._v("Thanh âm mới vừa rồi vang vọng dưới đáy lòng, cho người ta xao động cảm giác, cẩn thận hồi tưởng, quả thực không phải tiếng chuông có thể có."),t("br"),t("br"),n._v("Chẳng lẽ thực sự là... Linh tính xao động?"),t("br"),t("br"),n._v("Cái này sao có thể?"),t("br"),t("br"),n._v("Chỉ cảm thấy da mặt quất loạn, Trần trưởng lão bờ môi trắng bệch."),t("br"),t("br"),n._v("Vừa mới nghĩa chính ngôn từ nói người ta liền bảo vật đều không nhận ra không đến, kết quả nhân gia chẳng những nhận ra, còn lấy cái ..."),t("br"),t("br"),n._v("Muốn hay không như thế nghịch thiên?"),t("br"),t("br"),n._v("“Bảo vật tán thành, linh tính xao động! Đây là... Lợi hại linh binh, tự chủ sinh ra hưng phấn vang lên?”"),t("br"),t("br"),n._v("“Cái này, cái này...”"),t("br"),t("br"),n._v("Nghe được Trại các chủ lời nói, trước đó đồng tình Tôn Cường mọi người, tất cả đều từng cái trợn mắt hốc mồm, giống như là trong mồm bị nhét vào một cái rùa đen, rốt cuộc khép lại không được."),t("br"),t("br"),n._v("Mới vừa cảm thấy cái tên này khẳng định cũng thua đồ lót không dư thừa, kết quả là đại nghịch tập..."),t("br"),t("br"),n._v("May mắn vừa rồi không có ngốc không cứ thế trèo lên cùng hắn đánh cược, nếu không, không có quần lót chính là mình đám người!"),t("br"),t("br"),n._v("Lưng bên trên mồ hôi lạnh chảy ròng ròng, đồng thời hướng Trần trưởng lão nhìn lại, chỉ thấy hắn sắc mặt tái xanh, trên trán đều là hắc tuyến, tựa như lúc nào cũng sẽ ngất đi."),t("br"),t("br"),n._v("Ba ngàn linh thạch, coi như hắn là tứ tinh giám bảo sư, đoán chừng cũng thua hoài nghi nhân sinh."),t("br"),t("br"),n._v("“Vì sao kêu linh tính xao động?”"),t("br"),t("br"),n._v("Thấy mọi người bộ dáng này, Tôn Cường không tim không phổi, một mặt không rõ ràng cho lắm nhìn qua."),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Trần trưởng lão cũng nhịn không được nữa một ngụm máu tươi phun ra."),t("br"),t("br"),n._v("Bảo vật linh tính xao động, chỉ cần là giám bảo sư, không có không biết, thậm chí liền học đồ đều rõ rõ ràng ràng."),t("br"),t("br"),n._v("Cái tên này cái gì cũng không biết..."),t("br"),t("br"),n._v("Bại bởi ngũ tinh giám bảo sư, siêu cấp cường giả cũng nhận, thế mà bại bởi một cái liền học đồ cũng không bằng hạ nhân..."),t("br"),t("br"),n._v("Thế nào vừa rồi cứ như vậy ngốc, đáp ứng đổ ước đây?"),t("br"),t("br"),n._v("Thấy vị này mập mạp một mặt mờ mịt, dường như còn muốn đến hỏi có chút phát điên Trần trưởng lão, Lưu Xương cũng nhìn không được nữa, vội vàng đi tới “Bảo vật chia làm Thần, Thánh, linh, quỷ, phàm năm cái cấp bậc, ngươi đây hẳn phải biết đi!”"),t("br"),t("br"),n._v("“Ừm!” Tôn Cường gật đầu."),t("br"),t("br"),n._v("Đẳng cấp này phân loại, Thiên Huyền vương quốc cũng không tính là bí mật, người người biết, hắn mặc dù là cái thương nhân, cũng là người tu luyện, biết chẳng có gì lạ."),t("br"),t("br"),n._v("“Vũ khí đạt tới Linh cấp, liền sẽ nắm giữ linh tính. Cùng thư hoạ trong tác phẩm Hóa Linh đồng dạng, mặc dù không có khả năng giống người đồng dạng, có trí khôn, lại đối người tu luyện tiến hành tán thành, tán đồng. Điểm ấy, cùng man thú nhận chủ có chút giống.”"),t("br"),t("br"),n._v("Lưu Xương nói tiếp: “Tán thành người tu luyện vũ khí, liền sẽ phát ra loại này xao động thanh âm, cũng chính là cái gọi là linh tính xao động. Một khi xuất hiện, người tu luyện có thể trực tiếp nhỏ máu nhận chủ, luyện hóa trở thành đồ vật của mình...”"),t("br"),t("br"),n._v("“Luyện hóa?”"),t("br"),t("br"),n._v("Tôn Cường con mắt trợn tròn."),t("br"),t("br"),n._v("“Đúng vậy a... Lưu Thương Khúc Thủy bên trên vũ khí, bảo vật là mấy ngàn năm qua, vô số giám bảo sư đều giám định không được, mới đi vào một hồi liền linh tính xao động, chẳng lẽ... Hắn không chỉ nhận ra, còn nói rõ chi tiết ra bảo vật ưu, khuyết điểm?”"),t("br"),t("br"),n._v("Lưu Xương mặt mũi tràn đầy không thể tin được."),t("br"),t("br"),n._v("“Ưu khuyết điểm?”"),t("br"),t("br"),n._v("“Vũ khí, bảo vật, đều theo đã tốt muốn tốt hơn phương pháp đi rèn đúc, nhưng nhận hạn chế tài liệu, luyện khí sư trình độ... Lợi hại hơn nữa bảo vật phía trên đều sẽ có như vậy, như thế thiếu hụt, mà chỉ cần đem nó bổ túc, liền có thể để hắn phẩm giai tấn cấp.”"),t("br"),t("br"),n._v("“Binh khí linh tính, biết bản thân vấn đề, đáng tiếc không thể cùng người câu thông, cũng là không có cách nào tu bổ. Một chút lợi hại cường giả, có thể tuỳ tiện nhìn ra những vấn đề này vị trí, một khi tìm đúng, liền có thể dễ dàng đến linh tính tán thành, chỉ bất quá, đây là cấp cao luyện khí sư mới có năng lực, thế nào... Dùng đến giám bảo sư phía trên tới?”"),t("br"),t("br"),n._v("Giải thích vài câu, Lưu Xương mặt mũi tràn đầy mờ mịt."),t("br"),t("br"),n._v("Giám bảo sư, chỉ cần phân biệt bảo vật, biết là cái gì, lai lịch là cái gì là được, đối với bảo vật ưu khuyết điểm, là không nhìn ra."),t("br"),t("br"),n._v("Chỉ có những cái kia lợi hại luyện khí sư,"),t("br"),t("br"),n._v("Đối vũ khí, bảo bối, hiểu được cảnh giới cực sâu, mới có thể làm đến."),t("br"),t("br"),n._v("Chẳng lẽ... Cái tên này không phải giám bảo sư, mà là... Rất lợi hại luyện khí sư? Lúc này mới có thể chính xác nhận ra bảo vật là cái gì, lại phân phân ra ưu khuyết điểm?"),t("br"),t("br"),n._v("“Nếu như là luyện khí sư, trùng hợp kiện thứ nhất tới chính là vũ khí, có thể nhận ra, cũng rất bình thường. Chẳng qua giám bảo sư, không chỉ có thể phân biệt vũ khí, thư hoạ, đồ cổ, thiên tài địa bảo thậm chí công pháp, võ kỹ... Không gì không giỏi. Lưu Thương Khúc Thủy mười cái bảo vật, bao gồm rất nhiều chức nghiệp, chỉ là luyện khí sư lời nói, đoán chừng cũng chỉ có thể nhận ra đồng dạng...”"),t("br"),t("br"),n._v("Lộc trưởng lão cũng kịp phản ứng, nhìn về phía bên người bạn cũ, nói."),t("br"),t("br"),n._v("Luyện khí sư, đối vũ khí quen thuộc đến thực chất bên trong, có thể rất nhanh nhận ra, cũng tìm ra ưu khuyết điểm, thu hoạch được tán thành, chẳng có gì lạ."),t("br"),t("br"),n._v("Đáng tiếc Lưu Thương Khúc Thủy, khảo hạch là giám bảo sư, mà không phải luyện khí."),t("br"),t("br"),n._v("Trong đó mười cái bảo vật không riêng gì vũ khí, còn có những vật khác, coi như luyện khí sư có thể quen thuộc rất nhiều vũ khí, nhận ra một cái cũng coi như đến cuối, đằng sau khẳng định sẽ cuối cùng đều là thất bại."),t("br"),t("br"),n._v("Lộc trưởng lão vốn định an ủi bạn cũ, nói còn chưa dứt lời, liền nghe đến một bên mập mạp, lầm bầm lầu bầu thanh âm vang lên: “Đúng rồi, vừa rồi Trại các chủ có phải hay không nói, chỉ cần nhận ra một kiện bảo vật, coi như thông qua?”"),t("br"),n._v(" Trần trưởng lão mới vừa buông lỏng một cái, vẻ mặt lần nữa một lục, đau thấu tim gan."),t("br"),t("br"),n._v("Lưu Thương Khúc Thủy, nhiều năm như vậy, không có người thành công qua như nhau, bởi vậy, chỉ cần mười cái vật phẩm bên trong, nhận ra một cái, coi như thông qua... Nói cách khác, mặc kệ đằng sau vị này Trương Huyền có nhận hay không được đi ra, chính mình ba ngàn linh thạch, đều tính toán... Nhất định phải thua!"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bên ngoài khiếp sợ không tên, Trương Huyền nhìn trước mắt bộ dáng cổ quái thiết chùy, không còn gì để nói."),t("br"),t("br"),n._v("“Càn rỡ kêu to cái gì, dọa ta một hồi!”"),t("br"),t("br"),n._v("Vừa đem trong thư viện liên quan tới thứ này ghi chép viết một đoạn, cái đồ chơi này liền một tiếng oanh minh, dọa đến hắn liền bút lông đều kém chút rơi trên mặt đất."),t("br"),t("br"),n._v("Cái gì ngoạn ý nha."),t("br"),t("br"),n._v("Một cái phá chùy, cũng không phải chuông, cái chiêng loại hình, kêu to cái rắm ah!"),t("br"),t("br"),n._v("Có hiểu lễ phép hay không? Như vậy dọa người sẽ đem người hù chết."),t("br"),t("br"),n._v("“Được rồi, viết ra tên là có thể, phía sau có thể không viết...”"),t("br"),t("br"),n._v("Bị cắt đứt suy nghĩ, cũng là ngừng lại."),t("br"),t("br"),n._v("Thiên Đạo trong tiệm sách liên quan tới cái này Thanh Đồng chùy thiếu hụt miêu tả, chừng bảy mươi hai chỗ, toàn bộ vồ xuống đến, tuyệt đối phải mệt chết, như là đã xác định tên cùng lai lịch, cùng hai ba chỗ thiếu hụt, hẳn là không sai biệt lắm có thể thông qua được đi!"),t("br"),t("br"),n._v("Buông xuống bút lông, Trương Huyền không tại đi xem trên lá cây Thanh Đồng chùy, đang định chờ lấy đằng sau bảo vật tiến đến, chỉ thấy cái này nặng nề cục sắt, “Hô!” Theo trong ao, bay ra, rơi vào trước mặt."),t("br"),t("br"),n._v("“Cái này tình huống gì?”"),t("br"),t("br"),n._v("Trương Huyền nháy con mắt."),t("br"),t("br"),n._v("Ngươi một cái đại chùy, không ngoan ngoãn nằm trên lá cây, nhảy ta trước mặt làm gì?"),t("br"),t("br"),n._v("Ta cũng không phải mẹ chùy!"),t("br"),t("br"),n._v("“Quên đi...”"),t("br"),t("br"),n._v("Nghi ngờ nửa ngày, cũng nghĩ không ra đến cùng thế nào cái tình huống, thấy lá cây đã di chuyển, kiện thứ hai bảo vật, chậm rãi chảy xuôi mà đến, Trương Huyền lắc đầu, không để ý."),t("br"),t("br"),n._v("Mảnh thứ hai trên lá cây để đó một cái bức tranh, phiêu phù ở mặt nước, tản mát ra vầng sáng nhàn nhạt."),t("br"),t("br"),n._v("Chung quanh linh khí không tự chủ được bị hình ảnh hấp dẫn, từng con chim tước không ngừng bay lượn, tựa như đang sống."),t("br"),t("br"),n._v("“Ít nhất là một bộ ngũ cảnh tác phẩm hội họa!”"),t("br"),t("br"),n._v("Xem như tam tinh thư họa sư, đối thư hoạ vẫn hơi hiểu biết, tác phẩm này, mặc dù cách rất xa, cũng đã nhìn ra bất phàm, tuyệt không thấp hơn ngũ cảnh."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn vận chuyển."),t("br"),t("br"),n._v("Đáng tiếc, cùng vừa rồi Thanh Đồng chùy đồng dạng, đồng dạng nhìn không ra như thế về sau, nói cách khác, bức họa này trân quý trình độ, đã vượt ra khỏi hắn có thể nhẹ nhõm thăm dò phạm trù."),t("br"),t("br"),n._v("Rất nhanh, lá cây đi tới trước mặt, cũng thấy rõ phía trên tình huống."),t("br"),t("br"),n._v("Tác phẩm hội họa rõ ràng, bất quá phía trên không có lạc khoản (phần đề chữ, ghi tên trên bức vẽ) cùng tên, để cho người ta phân biệt không ra, đến cùng là ai lưu lại tác phẩm."),t("br"),t("br"),n._v("Phía trên chim tước thoạt nhìn cũng lạ mặt vô cùng, thuần thú bên trong học được man thú tư liệu, cũng không có một cái có thể đối ứng bên trên."),t("br"),t("br"),n._v("Nói cách khác, bức hoạ chẳng những không có tên, liền vẽ chim tước cũng không cách nào xác định đến cùng là vật gì loại."),t("br"),t("br"),n._v("Khó trách nhiều như vậy giám bảo sư, đều không phân biệt được."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Bàn tay cùng hình ảnh đụng một cái, Trương Huyền lần nữa múa bút thành văn."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Lão sư, bảo vật linh tính xao động qua đi, sợ rằng sẽ tự động nhận chủ, làm không cẩn thận, chúng ta Giám Bảo các không riêng gì phải trả 5000 linh thạch, món bảo vật này vậy...”"),t("br"),t("br"),n._v("Nghĩ tới điều gì, Lưu Xương không để ý tới cùng Tôn Cường giải thích, đi tới Trại các chủ trước mặt."),t("br"),t("br"),n._v("Có linh tính bảo bối, có thể tự do lựa chọn chủ nhân, nói cách khác, vị này Trương Huyền không chỉ xông qua Lưu Thương Khúc Thủy, kiếm lấy năm ngàn linh thạch, cược thắng Trần trưởng lão, chỉ sợ liền... Kiện binh khí này đều muốn cùng nhau bắt cóc."),t("br"),t("br"),n._v("Cái này gọi chuyện gì!"),t("br"),t("br"),n._v("Nhân gia vẽ một trăm linh thạch vượt quan, cơ bản phải bồi thường tiền, cái tên này chẳng những kiếm tiền, còn liền cược mang ngoặt..."),t("br"),t("br"),n._v("Giám Bảo các cái này xem như tổn thất quá lớn."),t("br"),t("br"),n._v("“Bảo vật có linh, có thể tự chủ lựa chọn chủ nhân. Mặc dù kiện vật phẩm này, lưu tại chúng ta Giám Bảo các nhiều năm như vậy, có thể một mực không có bị người nhận ra, hiện tại có người nói đi ra trải qua, tự nhiên hưng phấn dị thường, muốn nhận chủ, cũng không có cách nào ngăn cản, chỉ có thể thuận theo tự nhiên!”"),t("br"),t("br"),n._v("Trại các chủ cũng nghĩ đến điểm ấy, nhịn không được lắc đầu."),t("br"),t("br"),n._v("Giám Bảo các mỗi một kiện bảo vật đều vô cùng trân quý, hắn cũng không muốn lưu lạc ra ngoài, nhưng đối phương lấy được linh tính tán thành, muốn phủ nhận, cũng không có cách nào."),t("br"),t("br"),n._v("“Cái kia... Nếu như phía sau bảo vật lại xuất hiện loại tình huống này làm sao bây giờ?” Thấy lão sư cũng hết cách rồi, Lưu Xương mang theo lo lắng nhìn qua."),t("br"),t("br"),n._v("“Ha ha, yên tâm đi, kiện thứ nhất là vũ khí, giám bảo sư hoặc là trời sinh đối vũ khí có cảm giác hòa hợp người, có thể dễ dàng đến tán thành, nhưng phía sau liền không đồng dạng. Phía sau bảo vật, đủ loại, có đồ cổ, thư hoạ, các loại thiên tài địa bảo... Loại này không phải lợi hại giám bảo sư không cách nào nhận ra. Lại nói, coi như nhận ra, muốn có được trong đó linh tính tán thành, sao mà khó khăn, có thể nói gần như không có khả năng!”"),t("br"),t("br"),n._v("Trại các chủ mỉm cười: “Không cần buồn lo vô cớ, tự tìm phiền não.”"),t("br"),t("br"),n._v("“Cái này... Ngược lại là!”"),t("br"),t("br"),n._v("Lưu Xương sửng sốt một chút, lúc này mới kịp phản ứng, gật gật đầu đang muốn nói tiếp thứ gì, đột nhiên trước mắt linh khí một hồi kịch liệt ba động, thẳng tắp hướng trong môn phái hội tụ mà đi."),t("br"),t("br"),n._v("Ngay sau đó trước mắt mọi người một hoa, liền thấy linh khí hội tụ thành vô số không biết tên chim tước, nhao nhao bay lượn, cùng kêu lên vang lên."),t("br"),t("br"),n._v("“Hóa Linh tê minh, thư hoạ tán thành? Đây là thư hoạ loại chí bảo tán thành chủ nhân?”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-467"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-469"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);