(window.webpackJsonp=window.webpackJsonp||[]).push([[1710],{2065:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-461-boi-thuong-noi-dung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-461-boi-thuong-noi-dung"}},[n._v("#")]),n._v(" Chương 461: Bồi thường nội dung")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Thời gian trở lại một phút đồng hồ trước."),t("br"),t("br"),n._v("Liên tục bốn kiện bảo vật nhảy ra lá cây, đi tới trước mặt, Trương Huyền có ngốc cũng rõ ràng, là lấy được tán thành, những vật này, muốn cùng chính mình rời đi."),t("br"),t("br"),n._v("Cũng đúng, bảo vật cùng người đồng dạng, được luyện chế đi ra, liền muốn bày ra giá trị của mình."),t("br"),t("br"),n._v("Bị vây ở tối tăm không mặt trời Lưu Thương Khúc Thủy bên trong, ai cũng nhận không ra, bảo kiếm bị long đong, minh châu tối tăm, đổi lại bất luận một loại nào linh tính, đều khẳng định cực kỳ không cam lòng."),t("br"),t("br"),n._v("Bởi vậy, mỗi lần bị nhận ra, nhao nhao thuyết phục."),t("br"),t("br"),n._v("“Vậy ta liền không khách khí!”"),t("br"),t("br"),n._v("Khóe miệng vung lên, bàn tay lớn vồ một cái, liền đem nhảy ra mấy món bảo bối thu vào trữ vật giới chỉ."),t("br"),t("br"),n._v("Những vật này, Thiên Đạo thư viện đều đưa ra rõ ràng tên, coi như đối bảo vật hiểu không nhiều, cũng biết giá trị liên thành, tuyệt không phải mấy trăm linh thạch có thể mua sắm."),t("br"),t("br"),n._v("Vật trân quý như vậy nhận chủ, không muốn mới là lạ."),t("br"),t("br"),n._v("Vừa đem đồ vật thu lại, muốn tiếp tục chờ kế tiếp bảo vật xuất hiện, chỉ thấy trước mắt Lưu Thương Khúc Thủy một hồi lắc lư, ngừng lại, dòng nước dường như cũng đình chỉ."),t("br"),t("br"),n._v("“Chẳng lẽ, cái này cũng sẽ hỏng?”"),t("br"),t("br"),n._v("Lông mày nhíu một cái."),t("br"),t("br"),n._v("Lưu Thương Khúc Thủy từ trận pháp khởi động, dựa theo đạo lý, cũng không ngừng chảy xuôi, cho đến mười cái bảo vật toàn bộ xuất hiện, thế nào lúc này mới ra bốn cái, liền ngừng?"),t("br"),t("br"),n._v("“Nhìn xem có thể hay không sửa!”"),t("br"),t("br"),n._v("Tràn đầy nghi hoặc, đưa tay đối trước mắt bệ đá sờ lên."),t("br"),t("br"),n._v("Thứ này phải cùng Danh Sư đường khảo hạch danh sư cửa ải đồng dạng, là một kiện Linh Khí, khống chế nước chảy, bảo hộ bảo vật."),t("br"),t("br"),n._v("Đột nhiên dừng lại, hẳn là xảy ra vấn đề, có điều, không quan trọng, có thư viện nơi tay, chỉ cần là thiếu hụt, lỗ thủng, đều có thể nhẹ nhõm nhìn ra."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thư tịch xuất hiện, tiện tay lật ra, cầm lấy bút lông viết đi."),t("br"),t("br"),n._v("“Lưu Thương Khúc Thủy bàn điều khiển, Linh cấp hạ phẩm, từ Giám Bảo các thống nhất phối đưa...”"),t("br"),t("br"),n._v("Nhanh chóng viết, mới viết mấy cái thiếu hụt, liền nghe đến một hồi oanh minh, bàn điều khiển không ngừng lắc lư, đối với hắn sinh ra đặc thù thân mật cảm giác."),t("br"),t("br"),n._v("“Cái đồ chơi này cũng có thể thu lấy?”"),t("br"),t("br"),n._v("Cảm nhận được cái tên này thái độ cùng trước đó mấy món bảo vật giống nhau như đúc, Trương Huyền kinh ngạc."),t("br"),t("br"),n._v("Nếu như là thật, chẳng phải bày tỏ, bên trong bảo vật cũng có thể cùng nhau thu?"),t("br"),t("br"),n._v("“Thử một chút!”"),t("br"),t("br"),n._v("Nhẹ nhàng nở nụ cười, tinh thần khẽ động, quả nhiên cảm thấy trước mắt cái này cực lớn Lưu Thương Khúc Thủy, không có chút nào chống lại, “Hô!!” Một tiếng, bị thu vào trữ vật giới chỉ."),t("br"),t("br"),n._v("“Ta đi! Thật đúng là được...”"),t("br"),t("br"),n._v("Khóe miệng vung lên, Trương Huyền đang định nhìn xem trong phòng còn có cái gì có thể lại phân biệt, vơ vét, chỉ thấy cửa phòng “Kẹt kẹt!” Mở ra, Trại các chủ cùng Lộc trưởng lão, Trần trưởng lão vọt vào."),t("br"),t("br"),n._v("“Ngươi, ngươi...”"),t("br"),t("br"),n._v("“Ngươi đem Lưu Thương Khúc Thủy cũng thu...”"),t("br"),t("br"),n._v("Nhìn trước mắt rỗng tuếch đại sảnh, ba vị tứ tinh giám bảo sư từng cái bờ môi run rẩy, nhìn về phía thanh niên trước mắt, giống như là nhìn xem quái vật."),t("br"),t("br"),n._v("Chúng ta là để ngươi đến xông Lưu Thương Khúc Thủy, không phải để ngươi đến càn quét..."),t("br"),t("br"),n._v("Trước kia đi vào phòng thoạt nhìn cùng sơn dã đồng dạng, có núi đá, có nước chảy... Cái này ngược lại tốt, trụi lủi một cái mênh mông gian phòng, một cái lông gà đều không có."),t("br"),t("br"),n._v("Ngươi đây không phải vượt quan, mà là muốn đem Giám Bảo các đùa chơi chết tiết tấu!"),t("br"),t("br"),n._v("“Trương sư, Lưu Thương Khúc Thủy là... Giám Bảo các bảo vật, đại biểu uy nghiêm, có thể hay không lưu lại...”"),t("br"),t("br"),n._v("Cố nén muốn thổ huyết xúc động, Trại các chủ vẻ mặt đỏ lên, nói."),t("br"),t("br"),n._v("Trước đó còn xưng hô Trương lão đệ, hiện tại cũng không dám gọi."),t("br"),t("br"),n._v("Nhân gia vừa ra tay, không chỉ nhận ra nhiều như vậy bảo bối, liền bàn điều khiển đều thu, giám bảo trình độ, tuyệt đối ở trên hắn, xưng hô... Lão đệ, đã không đủ tư cách."),t("br"),t("br"),n._v("“Lưu lại? Nó nguyện ý nhận ta làm chủ!” Trương Huyền trừng mắt."),t("br"),t("br"),n._v("Nhìn trò đùa, nhiều như vậy bảo bối, làm sao có thể tùy tiện buông xuống?"),t("br"),t("br"),n._v("“Cái này... Ta biết!” Trại các chủ vẻ mặt đỏ lên."),t("br"),t("br"),n._v("Thiên tài địa bảo, người có đức chiếm lấy."),t("br"),t("br"),n._v("Những bảo vật này tán thành chủ nhân, làm giám bảo sư, hẳn là biết khó mà lui, lại cưỡng ép hỏi nhân gia muốn, cùng ăn cướp trắng trợn không có gì khác biệt."),t("br"),t("br"),n._v("Có thể... Lưu Thương Khúc Thủy, là bọn hắn Giám Bảo các trân quý nhất mang tính tiêu chí vật phẩm một trong, tuyệt không thể mất đi, nếu không, không cần tổng bộ trách cứ, hắn người Các chủ này cũng muốn tự nhận lỗi từ chức."),t("br"),t("br"),n._v("Vậy thì cùng có người xông vào Danh Sư đường, đem khảo hạch vượt quan Linh Khí cùng một chỗ lấy đi đồng dạng."),t("br"),t("br"),n._v("Truyền đi, tuyệt đối sẽ bị người sống chết cười."),t("br"),t("br"),n._v("“Như vậy, chỉ cần Trương sư đồng ý đem Lưu Thương Khúc Thủy lưu lại, chúng ta Giám Bảo các, nguyện ý... Tiến hành bồi thường!”"),t("br"),t("br"),n._v("Chần chờ một chút, Trại các chủ lần nữa nói."),t("br"),t("br"),n._v("“Bồi thường? Các ngươi phải bồi thường linh thạch cho ta? Cũng tốt!”"),t("br"),t("br"),n._v("Trương Huyền vạch lên tay tính toán: “Cái này Lưu Thương Khúc Thủy, bên trong ẩn chứa mười cái bảo vật, mỗi kiện cũng không công phu sư tử ngoạm, theo năm ngàn linh thạch tính toán, toàn bộ cộng lại cũng là năm vạn! Lại đem cái này bàn điều khiển cũng coi là... Lại thêm một vạn đi! Nói cách khác các ngươi cho ta sáu vạn linh thạch, thứ này liền cho các ngươi!”"),t("br"),t("br"),n._v("“Sáu vạn...”"),t("br"),t("br"),n._v("Ba đại giám bảo sư đồng thời nhoáng một cái, tròng mắt đỏ hoe."),t("br"),n._v("Bọn hắn là Giám Bảo các, không phải tiền trang... Hơn nữa, liền xem như tiền trang, cũng không bỏ ra nổi nhiều linh thạch như vậy ah."),t("br"),t("br"),n._v("“Cái này... Nhiều linh thạch như vậy, chúng ta thực sự không bỏ ra nổi tới...” Trại các chủ xấu hổ hận không thể có một cái lỗ chui vào."),t("br"),t("br"),n._v("Chuyện này là sao."),t("br"),t("br"),n._v("Lưu Thương Khúc Thủy không có người xông thời điểm, từng cái kỳ vọng có thể vượt qua, chân chính vượt qua, lại nhanh muốn khóc, cảm thấy tốt nhất không phải thật sự..."),t("br"),t("br"),n._v("“Không bỏ ra nổi đến? Vậy thì tốt, chỉ cần những vật này có thể lấy ra, ta không muốn linh thạch bồi thường cũng có thể!”"),t("br"),t("br"),n._v("Thấy đối phương khóc như mưa, ba đại tứ tinh giám bảo sư, tràn đầy phát điên, Trương Huyền cũng biết nhiều linh thạch như vậy bọn hắn khẳng định cũng không bỏ ra nổi đến, chần chờ một chút, nói tiếp: “Thứ nhất, giúp ta tìm Hóa Phàm cảnh công pháp, võ kỹ, càng nhiều càng tốt. Thứ hai, ta muốn Vô Hồn Kim Nhân cùng liên quan tới vu hồn sư hết thảy thư tịch, đồng dạng, số lượng không hạn!”"),t("br"),t("br"),n._v("Kỳ thật, cái này Lưu Thương Khúc Thủy cùng bên trong bảo bối, muốn cùng không muốn, đều râu ria."),t("br"),t("br"),n._v("Thật muốn lấy được linh thạch, cũng có thể tìm tới vô số phương pháp."),t("br"),t("br"),n._v("Hiện tại đối với hắn trọng yếu nhất chỉ có hai chuyện, một là, biết để linh hồn ngủ say thức tỉnh phương pháp, cứu chữa Lộ Xung. Hai là, tăng cao tu vi, tấn cấp danh sư, tranh thủ ba mươi tuổi đạt tới trước cửu tinh cấp bậc."),t("br"),t("br"),n._v("Bảo vật, linh thạch chỉ là phụ trợ thủ đoạn mà thôi, cho dù tốt, cùng hai thứ này so, cũng không tính được cái gì."),t("br"),t("br"),n._v("“Những thứ này...”"),t("br"),t("br"),n._v("Vốn cho rằng đối phương sẽ còn nói ra lời gì quá đáng, nghe được cũng chỉ là những vật này, Trại các chủ sững sờ."),t("br"),t("br"),n._v("Hóa Phàm cảnh công pháp, võ kỹ, bọn hắn tuy là Giám Bảo các, còn có thể tìm ra một chút, không tính là trân quý."),t("br"),t("br"),n._v("Về phần liên quan tới vu hồn sư thư tịch, mặc dù cái nghề nghiệp này, cụ thể tồn tại không tồn tại, tồn tại chắc chắn tranh luận, Giám Bảo các vẫn có một ít liên quan thư tịch... Bất quá, đều lời chi không rõ, tin tức có chính xác không cũng không thể mà biết."),t("br"),t("br"),n._v("Nói thật, những vật này... Đều không đáng bao nhiêu tiền, vô luận cùng những cái kia bảo vật, vẫn là Lưu Thương Khúc Thủy, đều kém một mảng lớn."),t("br"),t("br"),n._v("Trước đó thấy đối phương tham tài như mạng, vì linh thạch, không tiếc hết thảy, vốn cho rằng khẳng định sẽ cực kì vơ vét, lần nữa nói ra một cái để bọn hắn Giám Bảo các phá sản yêu cầu, không nghĩ tới... Chỉ cần những vật này."),t("br"),t("br"),n._v("Cái này... Không có vấn đề gì chứ?"),t("br"),t("br"),n._v("“Thế nào, làm không được?”"),t("br"),t("br"),n._v("Thấy hắn mặt mũi tràn đầy nghi hoặc, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Cái khác đều có thể tìm tới một chút, chỉ có Vô Hồn Kim Nhân... Thứ này cũng không phải là ta Giám Bảo các tất cả, mà là có người gửi bán, cụ thể như thế nào đấu giá, giá cả bao nhiêu có thể mua xuống, đấu giá hội không bắt đầu trước, ta không dám hứa chắc!”"),t("br"),t("br"),n._v("Trại các chủ vội nói."),t("br"),t("br"),n._v("Vô Hồn Kim Nhân là người khác gửi lại, cụ thể có thể bán ra bao nhiêu tiền, hắn cũng chỉ là cái đoán chừng, coi như bọn hắn Giám Bảo các ra tay, có thể hay không mua xuống, cũng chưa biết chừng. Bởi vậy, không dám hứa chắc có thể trăm phần trăm thanh toán."),t("br"),t("br"),n._v("Đương nhiên, đây cũng chỉ là giữ thuyết pháp, Giám Bảo các tài lực đối với thế lực khác xem như cực mạnh, lại đang hắn trên địa bàn đấu giá mua, nếu như bọn hắn cũng mua không được, những người khác muốn thành công, khả năng càng nhỏ hơn."),t("br"),t("br"),n._v("“Cái này không có việc gì, ngươi không nói xông qua Lưu Thương Khúc Thủy, nhưng có năm ngàn linh thạch ban thưởng ư? Ta chính mình mua sắm đều được! Chỉ cần có thể đem mới vừa nói cái kia mấy thứ sưu tập chỉnh tề, mỗi dạng không thua kém một ngàn bản là được!”"),t("br"),t("br"),n._v("Dừng lại một chút, Trương Huyền nói."),t("br"),t("br"),n._v("“Một ngàn bản?”"),t("br"),t("br"),n._v("Lông mày nhảy một cái, Trại các chủ vẻ mặt một lục."),t("br"),t("br"),n._v("Vốn cho rằng đối phương chỉ cần cái mấy chục vốn cũng không sai, mới mở miệng hơn ngàn, muốn nhiều như vậy thư làm gì?"),t("br"),t("br"),n._v("Còn tưởng rằng không muốn sáu vạn linh thạch, muốn thứ này là buông tha Giám Bảo các, hiện tại xem ra, điều kiện này cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("“Vu hồn sư thư tịch, không chỉ chúng ta như thế không nhiều như vậy, coi như Vạn Quốc thành cũng khẳng định tìm không thấy bao nhiêu, nhiều nhất chỉ có thể tìm tới... Mấy chục bản. Còn Hóa Phàm cảnh công pháp bí tịch, Hóa Phàm nhất trọng Tăng Thọ cảnh, có thể tìm được một ngàn vốn dĩ bên trên, dù sao cấp bậc này rất phổ thông, công pháp cũng nhiều.”"),t("br"),t("br"),n._v("“Về phần nhị trọng Nguyên Khí cảnh, liền đối lập thưa thớt, không nhiều như vậy, nhiều nhất năm trăm bản, tam trọng Âm Dương Kính, Vạn Quốc thành ngược lại là có thể tìm không ít, nơi này... Có thể tìm tới một trăm vốn cũng không sai!”"),t("br"),t("br"),n._v("Cẩn thận suy tính một lần, Trại các chủ đem có thể tìm tới đồ vật, nói rõ chi tiết một lần."),t("br"),t("br"),n._v("Hồng Hải thành mặc dù là vạn quốc liên minh một cái cự đại thành thị, dù sao không bằng Vạn Quốc thành mạnh mẽ, cho dù có bí tịch, cũng không có khả năng một cái tìm tới hơn ngàn bản."),t("br"),t("br"),n._v("Nhất là Hóa Phàm tam trọng, mỗi một bản đều bị các đại gia tộc của mình mình quý, chưa từng truyền ra ngoài, coi như Giám Bảo các ra mặt, có thể tìm tới một trăm vốn là rất tốt."),t("br"),t("br"),n._v("Hơn nữa còn là lục soát khắp tất cả có nội tình gia tộc."),t("br"),t("br"),n._v("“Cũng tốt, nắm chặt thời gian đi tìm!”"),t("br"),t("br"),n._v("Biết nhận hạn chế hoàn cảnh địa lý, có thể tìm nhiều như vậy đã không tệ, Trương Huyền gật đầu đồng ý, cổ tay khẽ đảo, Lưu Thương Khúc Thủy xuất hiện lần nữa."),t("br"),t("br"),n._v("Cái đồ chơi này còn cho đối phương có thể, chẳng qua trước đó nhận chủ cái kia bốn dạng, cũng đừng nghĩ."),t("br"),t("br"),n._v("“Đa tạ Trương sư thành toàn!”"),t("br"),t("br"),n._v("Trại các chủ nhẹ nhàng thở ra, vội vàng ôm quyền."),t("br"),t("br"),n._v("“Không có việc gì, mau đưa vượt quan năm ngàn linh thạch cho ta đi!”"),t("br"),t("br"),n._v("Trương Huyền vẫy tay."),t("br"),t("br"),n._v("“Ừm, ta vậy thì cho ngươi lấy!” Trại các chủ đi ra ngoài, thời gian không dài, đi tới trước mặt, cổ tay khẽ đảo, một đống linh thạch xuất hiện ở bên cạnh, linh khí xao động, tựa như gió lốc."),t("br"),t("br"),n._v("Tinh thần quét qua, biết có năm ngàn viên nhiều, nhẹ nhàng nở nụ cười, thu vào trữ vật giới chỉ."),t("br"),t("br"),n._v("“Thiếu gia, vị này Trần trưởng lão, mới vừa đánh cược thua, thiếu chúng ta sáu ngàn linh thạch!”"),t("br"),t("br"),n._v("Đem đồ vật lấy đi, đang muốn nói cái gì, chỉ thấy Tôn Cường đi đến."),t("br"),t("br"),n._v("“Đánh cược, sáu ngàn linh thạch?”"),t("br"),t("br"),n._v("Trương Huyền sững sờ, nhìn về phía cách đó không xa lão giả, một mặt kinh ngạc: “Vừa ra tay liền là sáu ngàn, Trần trưởng lão, ngươi quả nhiên tranh tài Các chủ hào phóng nhiều!”"),t("br"),t("br"),n._v("“Hào phóng?” Trần trưởng lão khóc."),t("br"),t("br"),n._v("Cái này không phải hào phóng, đây là muốn mạng ah..."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-469"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-471"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);