(window.webpackJsonp=window.webpackJsonp||[]).push([[1711],{2066:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-462-thien-cong-kheo-leo-hop"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-462-thien-cong-kheo-leo-hop"}},[n._v("#")]),n._v(" Chương 462: Thiên Công Khéo Léo hộp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Từ Tôn Cường giới thiệu, Trương Huyền rất nhanh liền biết trước đó chuyện phát sinh."),t("br"),t("br"),n._v("“Không sai, biểu hiện rất tốt, đáng tiếc duy nhất chính là, tiền đặt cược áp linh thạch ít, mới sáu ngàn, làm gì cũng muốn một vạn ah!”"),t("br"),t("br"),n._v("Không nghĩ tới cái này Tôn quản gia, thế mà còn có như thế biểu hiện kinh diễm, chỉ bằng vào đánh cược, giúp hắn kiếm lời nhiều linh thạch như vậy, Trương Huyền tán thưởng nhìn qua."),t("br"),t("br"),n._v("“Một vạn!”"),t("br"),t("br"),n._v("Trần trưởng lão lần nữa khóe miệng giật một cái."),t("br"),t("br"),n._v("Sáu ngàn cũng phải làm cho hắn buồn suýt tự sát, thật muốn một vạn, đoán chừng hiện tại đã sớm bỏ trốn mất dạng."),t("br"),t("br"),n._v("“Có chơi có chịu, ta cũng sẽ không quỵt nợ, bất quá...” Tiến về phía trước một bước, Trần trưởng lão một cắn răng: “Ta toàn bộ thân gia, cũng chỉ có thể xuất ra ba ngàn linh thạch, còn lại... Có thể hay không cho phép ta thời gian một năm?”"),t("br"),t("br"),n._v("“Một năm? Một năm sau, ngươi coi như cho ta hai vạn, cũng vô ích!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Theo Thiên Huyền vương quốc cùng nhau đi tới, tổng cộng cũng mới hai, ba tháng, một năm sau... Có thể tưởng tượng, ba ngàn linh thạch, đối với hắn mà nói, thật không quan hệ việc quan trọng, nhưng muốn có thể từ bỏ."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Vẻ mặt đỏ lên, Trần trưởng lão trên mặt âm tình bất định."),t("br"),t("br"),n._v("Thân là Hóa Phàm tam trọng cường giả, lại là tứ tinh giám bảo sư, nhân vật có mặt mũi, khi như thế nhiều người trước mặt, thừa nhận đánh cược, thua không nhận, không cần đối phương ra tay, cũng sẽ bị ngàn người chỉ trỏ, tươi sống ném chết."),t("br"),t("br"),n._v("Không giữ lời hứa, tin tức truyền đi, giám bảo kiếp sống cũng có thể là bởi vậy bị mất."),t("br"),t("br"),n._v("Chần chờ một lát, một cắn răng: “Ta thân là tứ tinh giám bảo sư, mặc dù vốn lưu động không có nhiều, nhưng cũng trân quý một chút bảo vật... Như vậy đi, bảo bối của ta ngươi có thể tùy ý tuyển chạy đồng dạng, coi như cái này ba ngàn linh thạch bồi thường!”"),t("br"),t("br"),n._v("Làm giám bảo sư, cùng đồ cổ người thu thập đồng dạng, vốn lưu động chưa chắc có bao nhiêu, nhưng trong nhà khẳng định có không ít có giá trị không nhỏ hàng tồn."),t("br"),t("br"),n._v("Những vật này, đều là hắn sưu tập cả đời bảo bối, nếu không phải là bị bức bất đắc dĩ, thực sự luyến tiếc lấy ra."),t("br"),t("br"),n._v("“Bảo vật? Để cho ta nhìn xem, nếu như phù hợp, đừng nói chống lại ba ngàn, chống lại ngươi vừa rồi sáu ngàn cũng có thể!”"),t("br"),t("br"),n._v("Biết những người này nghịch cả một đời bảo, khẳng định có chút hàng lậu, Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Biết không tránh thoát, Trần trưởng lão cổ tay khẽ đảo."),t("br"),t("br"),n._v("Rầm rầm!"),t("br"),t("br"),n._v("Thời gian nháy mắt, một đống bảo vật xuất hiện ở trước mặt mọi người."),t("br"),t("br"),n._v("Có đồ cổ, thư hoạ, vũ khí, khôi giáp, thậm chí còn có chút cổ quái bùn tượng."),t("br"),t("br"),n._v("Rất nhiều bảo vật cái gì cần có đều có, vị này Trần trưởng lão sưu tập vật phẩm nhiều, thế mà đều so ra mà vượt lầu hai một cái tủ nhỏ đài, thậm chí còn có chút càng thêm trân quý."),t("br"),t("br"),n._v("“Chỉ những thứ này, ngươi tùy tiện tuyển...”"),t("br"),t("br"),n._v("Trần trưởng lão ra vẻ hào phóng vẫy tay."),t("br"),t("br"),n._v("Đây đều là bảo bối của hắn, mỗi một cái đều luyến tiếc, đáng tiếc, ai bảo đánh cược thua, không muốn cho cũng phải cấp."),t("br"),t("br"),n._v("Nếu không, danh dự sạch không, coi như muốn vãn hồi, cũng tại không cứu vãn nổi."),t("br"),t("br"),n._v("“Ta xem một chút!”"),t("br"),t("br"),n._v("Minh Lý Chi Nhãn vận chuyển, Trương Huyền hướng trên đất một đống đồ vật nhìn sang."),t("br"),t("br"),n._v("Một lát sau, lắc đầu."),t("br"),t("br"),n._v("đọc ngantruyen.com/"),t("br"),n._v("Những vật này mặc dù đều rất thưa thớt, cũng có rất trân quý, nhưng đối với hắn mà nói, đều có thể có thể không, không tính là kinh diễm."),t("br"),t("br"),n._v("Nói cách khác, muốn cùng không muốn, đều không cần gấp, còn không bằng muốn chút linh thạch, ảnh hưởng lớn hơn một chút."),t("br"),t("br"),n._v("Mặt mũi tràn đầy bất đắc dĩ, Trương Huyền nhìn qua: “Ngươi sưu tập cả một đời... Chỉ những thứ này phá ngoạn ý?”"),t("br"),t("br"),n._v("“Ta...” Biết đối phương là giám bảo đại tông sư, ánh mắt cao, vượt qua Các chủ, nói như vậy, rất rõ ràng không coi trọng một kiện, Trần trưởng lão vẻ mặt đỏ lên."),t("br"),t("br"),n._v("Hắn như thế trân quý, đồng dạng đều không bỏ được lấy ra bảo bối, tại trong mắt đối phương thế mà chỉ là phá ngoạn ý, lập tức cảm thấy mặt mũi tràn đầy khó xử."),t("br"),t("br"),n._v("“Trần trưởng lão, đem lần trước lấy được vật kia lấy ra đi! Vạn nhất... Trương sư ưa thích, chống lại sáu ngàn linh thạch, cũng coi như kiếm lời!”"),t("br"),t("br"),n._v("Ngay tại không biết nên như thế nào làm thời điểm, bên tai vang lên Lộc trưởng lão truyền âm."),t("br"),t("br"),n._v("“Cái kia?”"),t("br"),t("br"),n._v("Lập tức nghĩ tới điều gì, Trần trưởng lão chần chờ một chút, lập tức gật đầu, nhìn về phía Trương Huyền: “Trương sư, ngươi chờ một chút, ta có một kiện đồ vật, từ khi lấy được một mực không nhận ra cụ thể vật gì, ta vậy thì lấy ra tới. Nếu như thứ này, lại nhìn không lên, liền không có biện pháp nào khác!”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nghe hắn còn có một cái bảo bối, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Dù sao cũng không vội vã, chờ một hồi cũng không sao."),t("br"),t("br"),n._v("Trần trưởng lão quay người rời khỏi, nửa giờ sau, một lần nữa trở về, cổ tay khẽ đảo, một cái đen kịt đồ vật xuất hiện tại trước mặt."),t("br"),t("br"),n._v("Lớn nhỏ cỡ nắm tay, viên cầu bộ dáng, thoạt nhìn đen sì, không có một chút hình dạng, chợt nhìn giống như là cái quả tạ."),t("br"),t("br"),n._v("“Đây cũng là bảo bối?”"),t("br"),t("br"),n._v("Tôn Cường chào đón, nhìn thoáng qua: “Ngươi muốn dùng thứ này để thay thế sáu ngàn linh thạch, Trần trưởng lão, làm như vậy có chút quá không phúc hậu đi!”"),t("br"),t("br"),n._v("“Ta...” Trần trưởng lão hơi đỏ mặt."),t("br"),t("br"),n._v("“Như thế oan uổng Trần trưởng lão!” Trại các chủ mở lời: “Thứ này là mười năm trước, tại một chỗ cổ địa đào ra, thoạt nhìn chẳng ra sao cả, nhưng thủy hỏa bất xâm, đao chém không thương tổn, vô luận dùng dạng gì liệt hỏa đều không thể hòa tan. Những năm này chúng ta đều nghiên cứu qua thật nhiều lần, cũng không biết là cái gì.”"),t("br"),t("br"),n._v("“Không biết là cái gì, cũng là không phải bảo bối gì?” Tôn Cường nói."),t("br"),t("br"),n._v("“Cũng không thể nói như vậy, cũng có thể là một loại chưa từng thấy qua khoáng thạch, nếu như có thể luyện hóa, làm thành vũ khí, hẳn là sẽ cấp bậc không thấp.” Trại các chủ nói."),t("br"),t("br"),n._v("“Có thể luyện hóa? Vậy còn không biết lúc nào...” Tôn Cường khẽ nói."),t("br"),t("br"),n._v("“Tốt, ta xem một chút!”"),t("br"),n._v("Cắt ngang Tôn quản gia lời nói, Trương Huyền đi tới trước mặt."),t("br"),t("br"),n._v("Cái này thiết cầu lấy tới, dùng Minh Lý Chi Nhãn nhìn, lại phát hiện thế mà không nhìn thấu!"),t("br"),t("br"),n._v("Toàn bộ Giám Bảo các, cơ hồ đều đi một lượt, bị hắn nhìn không thấu, tuyệt đối là bảo vật."),t("br"),t("br"),n._v("Tiếp nhận viên cầu, đáy lòng thầm hô “Thiếu hụt”."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thư viện một quyển sách hiện lên, tiện tay lật ra."),t("br"),t("br"),n._v("“Thiên Công Khéo Léo hộp, Thiên Công viện luyện chế mà thành, nhanh nhẹn linh hoạt vô song, không có riêng biệt phương pháp, không cách nào mở ra, có thể chứa đựng một chút quan trọng cơ mật bảo vật. Khuyết điểm...”"),t("br"),t("br"),n._v("Đằng sau lít nha lít nhít ký thuật thứ này thiếu hụt."),t("br"),t("br"),n._v("“Chỉ có mười bảy chỗ?”"),t("br"),t("br"),n._v("Mặc dù thư viện không tiêu chí chú thứ này giá trị, Trương Huyền cũng chưa từng nghe qua cái gì Thiên Công viện, có thể thấy thiếu hụt số lượng, vẫn là giật nảy mình."),t("br"),t("br"),n._v("Thư viện có thể nhìn ra thiếu hụt, đều rất nhiều, theo chất liệu đến kỹ xảo, đều bao gồm."),t("br"),t("br"),n._v("Tựa như Lưu Thương Khúc Thủy đi ra kiện thứ nhất bảo vật Thanh Đồng chùy, Minh Lý Chi Nhãn cũng nhìn không ra đến vấn đề, nhưng có khoảng chừng bảy mươi hai chỗ khuyết điểm."),t("br"),t("br"),n._v("Đương nhiên, cũng không phải là nói khuyết điểm ít, bảo vật liền trân quý, cái này còn cùng một chút vật phẩm trình độ phức tạp có quan hệ."),t("br"),t("br"),n._v("Đại đạo chí giản, càng phức tạp đồ vật, lỗ thủng ngược lại càng nhiều."),t("br"),t("br"),n._v("Thứ này, gọi thiên công nhanh nhẹn linh hoạt hộp, hơn nữa nhìn trong đó một chút thiếu hụt miêu tả, kết cấu kín đáo, trình độ phức tạp vượt xa Thanh Đồng chùy loại tác dụng này một loại vũ khí."),t("br"),t("br"),n._v("So Thanh Đồng chùy phức tạp, khuyết điểm còn ít nhiều như vậy, chẳng phải cho thấy, thứ này giá trị, so Thanh Đồng chùy cao hơn rất nhiều?"),t("br"),t("br"),n._v("Bảo bối!"),t("br"),t("br"),n._v("Tuyệt đối là cái bảo bối."),t("br"),t("br"),n._v("“Trương sư khả năng nhìn ra thứ này đến cùng là vật gì?”"),t("br"),t("br"),n._v("Thấy hắn nghiên cứu nửa ngày, Trần trưởng lão hỏi."),t("br"),t("br"),n._v("Trại các chủ, Lộc trưởng lão cũng không nhịn được nhìn qua."),t("br"),t("br"),n._v("Thứ này, bọn hắn nghiên cứu mấy năm, các loại thủ đoạn đều dùng qua, không có một chút kết quả, trước mắt vị này giám bảo thực lực, liền Lưu Thương Khúc Thủy đều có thể thu phục, có lẽ thật có thể nhìn ra cái gì."),t("br"),t("br"),n._v("“Cụ thể là cái gì, ta cũng không nhìn ra, khả năng cần nghiên cứu một đoạn thời gian.”"),t("br"),t("br"),n._v("Biết tiền tài không để ra ngoài đạo lý, Trương Huyền cũng không nói thật, chần chờ một chút, nói: “Như vậy đi, ta mặc dù nhìn không ra, lại biết cũng không đơn giản, liền thay thế sáu ngàn linh thạch, ngươi ta đổ ước, xóa bỏ, như thế nào?”"),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Không nghĩ tới cái này nghiên cứu không ra được đồ vật, quả nhiên có thể thay thế sáu ngàn linh thạch, Trần trưởng lão nhẹ nhàng thở ra, đồng thời lặng lẽ hướng cách đó không xa mập mạp nhìn lại."),t("br"),t("br"),n._v("Cái tên này cùng hắn đánh cược, quyết định như vậy, sẽ có hay không có cái gì ý kiến?"),t("br"),t("br"),n._v("Trương sư thân là danh sư, coi trọng mặt mũi, coi như thứ này không đáng, cũng sẽ không truy cứu, có thể cái này hạ nhân liền không giống."),t("br"),t("br"),n._v("Hắn thật đúng là sợ đối phương tiếp tục ồn ào, thật không biết phải làm gì cho đúng."),t("br"),t("br"),n._v("Tràn đầy lo lắng, có điều, nhìn thoáng qua, đã thấy mập mạp mắt nhìn mũi mũi nhìn tâm, thật giống nghe đều không có nghe được, tựa hồ đối với Trương sư quyết định không thèm quan tâm."),t("br"),t("br"),n._v("“Xem ra hắn vẫn rất tôn trọng vị thiếu gia này!”"),t("br"),t("br"),n._v("Thấy đối phương không có động tĩnh, Trần trưởng lão trầm tĩnh lại."),t("br"),t("br"),n._v("Kỳ thật, hắn không biết, Tôn Cường không nói lời nào, tôn trọng Trương Huyền là một nguyên nhân, càng quan trọng hơn là, biết vị thiếu gia này, so với hắn còn cơ linh, không bao giờ làm thua thiệt mua bán. Đã nguyện ý hoa sáu ngàn linh thạch mua sắm thứ này, vậy liền cho thấy, cái đồ chơi này giá trị, khẳng định vượt xa số này."),t("br"),t("br"),n._v("Đã như vậy, làm gì còn nhiều hơn miệng?"),t("br"),t("br"),n._v("“Ta đã phái người đi sưu tập ngươi muốn những sách vở kia, đoán chừng muốn ba ngày trái phải mới có thể tìm đủ!”"),t("br"),t("br"),n._v("Thấy Trương Huyền đem viên cầu thu vào nhẫn, Trại các chủ cười cười: “Nhìn thời gian đấu giá hội gần bắt đầu, nếu không chúng ta cũng đi qua đi!”"),t("br"),t("br"),n._v("Trải qua Lưu Thương Khúc Thủy, chờ Trần trưởng lão lấy lại viên cầu... Trước đó nói một canh giờ, đã nhanh phải kết thúc, tính toán thời gian, đấu giá hội quả thực cũng lập tức sẽ bắt đầu."),t("br"),t("br"),n._v("Hiện tại đi qua, hẳn là không kém quá cứng tốt."),t("br"),t("br"),n._v("“Tốt!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Lần này tới chính là vì mua sắm Vô Hồn Kim Nhân, đấu giá hội lập tức bắt đầu, khẳng định không thể để cho đồ vật rơi vào trong tay người khác."),t("br"),t("br"),n._v("Trại các chủ phía trước dẫn đường, mọi người theo sát trên đó."),t("br"),t("br"),n._v("Phòng đấu giá lại lầu hai, khoảng cách Nhất Phẩm các không xa, còn chưa tới đến trước mặt, liền thấy chen chúc dòng người."),t("br"),t("br"),n._v("Xem ra muốn bán đấu giá vật phẩm, lực hấp dẫn rất lớn, không ít có chửa giá, người có thực lực đều tới."),t("br"),t("br"),n._v("Minh Lý Chi Nhãn mở ra, hướng đám người nhìn lại, tùy tiện quét qua, liền nhìn ra không số ít mười vị Hóa Phàm cường giả, hơn nữa đều đạt đến nhị trọng, tam trọng thực lực."),t("br"),t("br"),n._v("Không hổ là vạn quốc liên minh, quả nhiên cường giả như mây."),t("br"),t("br"),n._v("“Cái này phòng đấu giá không phải là các ngươi Giám Bảo các mở?”"),t("br"),t("br"),n._v("Đi theo Trại các chủ phía sau, thuận lợi đi vào bên trong sân, chỉ chốc lát đi tới một cái rộng lớn phòng, Trương Huyền nhịn không được hỏi."),t("br"),t("br"),n._v("Nơi này là Giám Bảo các, phòng đấu giá hẳn là bọn hắn mở, vì cái gì còn cần thư mời loại hình đồ vật."),t("br"),t("br"),n._v("Chẳng lẽ, cùng phía dưới cửa hàng đồng dạng, Giám Bảo các cũng không tham dự?"),t("br"),t("br"),n._v("“Thật đúng là không phải chúng ta mở, cùng Nhất Phẩm các đồng dạng, cùng Giám Bảo các có quan hệ hợp tác. Chúng ta có trách nhiệm phân biệt, bọn hắn tìm kiếm hàng hóa, triệu tập khách nhân tiến hành đấu giá... Nếu như chuyện gì đều quản, chúng ta cũng không nhiều như vậy tinh lực!”"),t("br"),t("br"),n._v("Trại các chủ nói."),t("br"),t("br"),n._v("Thuật nghiệp hữu chuyên công, giám bảo sư am hiểu là giám bảo, phán đoán giá trị, làm ăn cũng không am hiểu."),t("br"),t("br"),n._v("Thật làm cho bọn hắn khai mạc bán trận, làm không cẩn thận sớm muộn gì đều sẽ sập tiệm."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-470"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-472"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);