(window.webpackJsonp=window.webpackJsonp||[]).push([[1712],{2067:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-463-đau-gia-hoi-bat-đau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-463-đau-gia-hoi-bat-đau"}},[n._v("#")]),n._v(" Chương 463: Đấu giá hội bắt đầu")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Khó trách...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Nếu thật là bọn hắn mở đấu giá hội, Vô Hồn Kim Nhân khẳng định có thể mua đến, không đến mức nói không xác định."),t("br"),t("br"),n._v("“Cái này Vô Hồn Kim Nhân, ngươi có phải hay không giám định qua, đến cùng chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Nhìn xem phương đấu giá hội còn chưa bắt đầu, Trương Huyền hỏi lần nữa."),t("br"),t("br"),n._v("Năm ngàn linh thạch tới tay, hắn không chỉ đem đối phương cấp cho mình vượt quan một trăm linh thạch trả hết, lại bồi thường một trăm."),t("br"),t("br"),n._v("Nói cách khác hiện tại trong tay tổng cộng có 4800 linh thạch, mặc dù Trại các chủ nói lời thề son sắt, khẳng định đầy đủ, vẫn là muốn nghe ngóng kỹ càng một chút, tốt làm phán đoán."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Trại các chủ gật gật đầu: “Đại khái hơn một tháng trước, một cái lão giả đi tới Giám Bảo các, muốn ta giám định một kiện vật phẩm. Thứ này toàn thân kim thiết, tựa như vàng đúc thành, thoạt nhìn như là khôi lỗi, ta nghiên cứu hồi lâu, cảm thấy cùng trong truyền thuyết Vô Hồn Kim Nhân, giống như đúc, lại để cho Lộc trưởng lão, Trần trưởng lão đồng thời xác nhận, lúc này mới định ra đến!”"),t("br"),t("br"),n._v("“Cái này kim nhân, cứng rắn không gì sánh được, hẳn là dùng khi còn sống, nuốt kim thiết, dùng đặc thù bí pháp nung mà thành. Toàn thân mượt mà như một, không có chút nào khí tức tiết ra ngoài, trong cơ thể bố trí trận pháp, lợi dụng linh thạch có thể khởi động, uy lực vô cùng, đơn thuần sức chiến đấu, có thể so với Hóa Phàm tam trọng cường giả.”"),t("br"),t("br"),n._v("“Khi còn sống... Chẳng lẽ Vô Hồn Kim Nhân không phải chế tạo, mà là người thật luyện chế mà thành?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Đối với vu hồn sư, hắn chỉ là nghe Lạc đường chủ thuận miệng nói ra một câu, tình huống cụ thể hoàn toàn không biết, trước đó nghe được Vô Hồn Kim Nhân, còn tưởng rằng cùng Danh Sư đường vượt quan lúc gặp phải đồng dạng, là nhân tạo đồ vật, nghe nói như thế, rõ ràng không giống."),t("br"),t("br"),n._v("“Dĩ nhiên không phải! Nhân tạo khôi lỗi, cùng luyện chế bảo vật đồng dạng, thời gian lâu dài sẽ tự chủ sinh ra linh tính, linh hồn của con người không có cách nào xâm nhập trong đó, coi như có thể đi vào, tại khống chế bên trên cũng hơi có vẻ không đủ. Vậy thì cùng vũ khí, khôi giáp loại hình công cụ tương đương, dùng cũng rất thuận tiện, lại sẽ không cùng tứ chi đồng dạng thuận buồm xuôi gió.” Trại các chủ nói."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Công cụ dùng lại thuận tay, cũng không bằng bàn tay tứ chi, nhân tạo khôi lỗi, kỳ thật liền là nhân tạo vũ khí, coi như linh hồn có thể khởi động, cũng cùng cầm vũ khí đồng dạng, không như vậy thông thuận."),t("br"),t("br"),n._v("“Vô Hồn Kim Nhân không giống, là người sống luyện chế mà thành, toàn thân đúc bằng sắt thép, không chỉ phòng ngự vô địch, lực lượng cũng cực kỳ cường đại. Mấu chốt nhất là... Còn có thể hoàn mỹ dung nạp hồn phách, dễ như trở bàn tay khống chế. Trong truyền thuyết vu hồn sư, đối linh hồn khống chế mạnh mẽ, nhưng thân thể, thực lực đều cực kém. Liền là mượn nhờ linh hồn, đối với mấy cái này đồ vật tiến hành khống chế, không ai địch nổi, để cho người ta kiêng kị.”"),t("br"),t("br"),n._v("Trại các chủ giải thích."),t("br"),t("br"),n._v("Trương Huyền hiểu được, trong lòng hoảng sợ."),t("br"),t("br"),n._v("Đem người sống nung thành khôi lỗi, chỉ vì bảo vệ mình... Loại nghề nghiệp này thật đúng là đủ quỷ dị, tà ác."),t("br"),t("br"),n._v("“Vu hồn sư, thời kỳ Thượng Cổ cùng độc sư nổi danh, đều để người kiêng kị. Nghe nói, có thể mượn nhờ đối linh hồn cường đại lực khống chế, cưỡng ép đem linh hồn của con người theo thân thể bên trong hấp thụ đi ra, thân thể luyện chế thành kim nhân. Một chút cường đại vu hồn sư, thậm chí có thể sáng tạo ra một cái kinh khủng kim nhân quân đoàn.”"),t("br"),t("br"),n._v("Trại các chủ nói: “Chính vì vậy, bị những nghề nghiệp khác phản đối, Danh Sư đường tự mình ra tay, đem nó giết chết, truyền thừa đều bởi vậy đoạn tuyệt. Ta cũng là tại rất nhiều trong cổ tịch nhìn qua Vô Hồn Kim Nhân giới thiệu, mới có thể xác nhận thứ này tồn tại, nếu không, thật khả năng coi nó là thành phổ thông khôi lỗi.”"),t("br"),t("br"),n._v("“Nghe nói cái nghề nghiệp này... Có thể đem ngủ say linh hồn đánh thức, không biết có phải hay không thật?” Ngăn chặn khẩn trương, Trương Huyền hỏi."),t("br"),t("br"),n._v("Lạc Thiên Hồng mặc dù là tam tinh danh sư, có thể cùng tứ tinh giám bảo sư Trại các chủ so, hiểu biết bên trên khẳng định kém một chút, hỏi hắn nên được đến đáp án càng thêm chuẩn xác."),t("br"),t("br"),n._v("“Linh hồn phức tạp huyền diệu, trừ phi đạt tới thất tinh danh sư, mới có thể liên quan đến, nếu không, mạnh hơn thực lực, dù là Hóa Phàm cửu trọng đỉnh phong, cũng không dám đụng vào. Thực lực không đủ, còn có thể làm được, cũng chỉ có vu hồn sư! Loại nghề nghiệp này liền người sống linh hồn đều có thể bị rút mất, đánh thức ngủ say linh hồn, hẳn là dễ như trở bàn tay.”"),t("br"),t("br"),n._v("Trại các chủ khuôn mặt nặng nề mà nói."),t("br"),t("br"),n._v("Nghe được đối phương xác nhận, Trương Huyền lần nữa nhẹ nhàng thở ra."),t("br"),t("br"),n._v("“Tốt, đấu giá hội bắt đầu!”"),t("br"),t("br"),n._v("Hai người trong lúc nói chuyện với nhau, phía dưới một hồi thanh âm huyên náo vang lên, quay đầu nhìn lại, quả nhiên thấy đấu giá hội đã bắt đầu."),t("br"),t("br"),n._v("Chủ trì đấu giá hội, không phải người khác, chính là Trần trưởng lão."),t("br"),t("br"),n._v("Hắn làm tứ tinh giám bảo sư, Giám Bảo các trưởng lão, càng có sức thuyết phục, để cho người ta càng tin tưởng bảo vật chân thực tính."),t("br"),t("br"),n._v("“Hôm nay bán đấu giá đồ vật, bao gồm vũ khí, khôi giáp, đồ cổ tranh chữ, thiên ngoại dị bảo, Thượng Cổ lưu lại khôi lỗi kim nhân các loại, ròng rã ba mươi lăm dạng. Mỗi một cái, đều trải qua chúng ta Giám Bảo các giám định qua, hơn nữa cấp cho phán đoán chuẩn xác, bình luận chính xác không sai, không có chút nào giở trò dối trá.”"),t("br"),t("br"),n._v("Nhìn quanh một vòng, Trần trưởng lão khí tức kinh người."),t("br"),t("br"),n._v("Vừa đến bàn đấu giá, lần nữa biến thành cái kia khí độ vô song tứ tinh giám bảo sư, mà không phải trước đó bị Tôn Cường làm cho dục tiên dục tử, không ngừng thổ huyết gia hỏa."),t("br"),t("br"),n._v("“Ba mươi lăm dạng? Nhiều như vậy bảo bối?”"),t("br"),t("br"),n._v("“Đó là đương nhiên, Giám Bảo các đấu giá, một năm chỉ cử hành một lần, xuất ra bán đồ vật, tất cả đều giá trị liên thành, nếu không ngươi cho rằng ai cũng có thể được đến thư mời?”"),t("br"),t("br"),n._v("“Ta thế nhưng là chuẩn bị kỹ càng lâu, trong này có ta không thể không cần đến đồ vật, nhất định phải mua được...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được giới thiệu, chung quanh nghị luận ầm ĩ."),t("br"),t("br"),n._v("Phòng đấu giá, cùng phổ thông tỷ thí trận có chút tương tự, hình khuyên một vòng, Trại các chủ loại này người có thân phận, vị trí chính là phòng, phía dưới vẫn là có không ít tán tòa."),t("br"),t("br"),n._v("Những người này có tán tu, cũng có một chút đại gia tộc khách đến thăm, từng người chuẩn bị hồi lâu, chuẩn bị mua được từng người muốn có được đồ vật."),t("br"),t("br"),n._v("“Xem ra Vô Hồn Kim Nhân lực hấp dẫn rất lớn, không ít cao thủ đều tới!” Xuyên thấu qua cửa sổ nhìn xuống phía dưới một vòng, Trại các chủ cảm khái."),t("br"),t("br"),n._v("“Đúng vậy a!” Kim Tòng Hải cũng gật gật đầu."),t("br"),n._v(" Hắn là Hóa Phàm tứ trọng cường giả, lại đi theo vạn quốc liên minh vương thất không biết bao lâu, minh bên trong có tên cao thủ, cơ hồ đều biết."),t("br"),t("br"),n._v("Cái này đấu giá hội mặc dù chỉ là Hồng Hải thành, nhìn một cái, nhưng tới không ít thực lực không kém gì hắn cường giả, thậm chí mạnh hơn hắn đều có mấy vị, làm người ta kinh ngạc."),t("br"),t("br"),n._v("Bình thường đấu giá, đều không có nhiều người như vậy, giờ phút này xuất hiện, hiển nhiên đều là Vô Hồn Kim Nhân mà tới."),t("br"),t("br"),n._v("“Chỉ sợ ba ngàn linh thạch, chưa hẳn có thể mua xuống!”"),t("br"),t("br"),n._v("Lần nữa nhìn một hồi, Trại các chủ sắc mặt nghiêm túc, bất đắc dĩ cười khổ."),t("br"),t("br"),n._v("Trước đó hắn phán đoán, cái này kim nhân giá trị cũng là 1500 linh thạch, coi như đấu giá gấp bội, cũng chỉ ba ngàn, giờ phút này nhiều cao thủ đều tới, chỉ sợ cái giá tiền này muốn cầm xuống, không dễ dàng như vậy."),t("br"),t("br"),n._v("“Chạy một bước nhìn một bước đi! Nếu như không đủ, Trại các chủ tiếp tục cho ta mượn tiền là được!” Trương Huyền cười cười."),t("br"),t("br"),n._v("“Ây...” Trại các chủ con mắt đảo một vòng, tình cảm cái tên này, còn không có quên cái này gốc."),t("br"),t("br"),n._v("“Chư vị yên tĩnh, đấu giá hội hiện tại bắt đầu, hiện tại bắt đầu bên trên kiện thứ nhất vật phẩm đấu giá!”"),t("br"),t("br"),n._v("Để mọi người phát tiết xong rung động, Trần trưởng lão lúc này mới mỉm cười, bàn tay lớn vẫy một cái, đưa tay về phía trước."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Trước mắt bệ đá từ dưới đất chậm rãi dâng lên, phía trên xuất hiện một cái hẹp dài hộp ngọc."),t("br"),t("br"),n._v("Hộp ngọc mở ra, một gốc óng ánh sáng long lanh thực vật xuất hiện ở trước mặt mọi người, hàn băng thấu xương, thấu triệt nội tâm, cho dù từ xa nhìn lại, đều cho người ta một loại, sắp đông thành băng côn cảm giác."),t("br"),t("br"),n._v("“Đây là năm trăm năm hàn băng linh thảo, đối tu luyện băng hàn thuộc tính công pháp người, có hiệu quả, luyện chế thành đan dược ăn vào, tu vi tuyệt đối bạo tăng. Giá quy định 220 linh thạch, mỗi lần nâng giá, không thể ít hơn 20 linh thạch!”"),t("br"),t("br"),n._v("“Băng hàn thảo? Có thể luyện chế tứ phẩm Băng Phách đan, không chỉ đối tu luyện băng hàn công pháp người hữu dụng, đối Hóa Phàm tam trọng Âm Dương Kính, cũng có cực lớn hiệu quả!”"),t("br"),t("br"),n._v("“Đúng vậy a, bảo bối, chân chính bảo bối, không hổ là Giám Bảo các một năm một lần đấu giá hội, vừa ra tay liền cho thấy bất phàm!”"),t("br"),t("br"),n._v("“Cái này gốc hàn băng linh thảo ta muốn, 240 linh thạch!”"),t("br"),t("br"),n._v("“Chút tiền ấy liền muốn mua một cái năm trăm năm linh dược? Nằm mơ, 260 linh thạch!”"),t("br"),t("br"),n._v("“Vẫn là cho ta đi! 300 linh thạch...”"),t("br"),t("br"),n._v("Kiện thứ nhất bảo vật mặc dù chỉ là cái linh dược, nhưng trong nháy mắt liền đem bầu không khí kéo theo lên."),t("br"),t("br"),n._v("Băng hàn thân thảo liền thưa thớt, huống chi đạt tới năm trăm năm cấp bậc, thời gian không dài, liền bị xào đến 500 linh thạch, cuối cùng giá sau cùng càng là đạt đến 540 linh thạch!"),t("br"),t("br"),n._v("“Không hổ là đấu giá hội, quả nhiên nóng nảy...”"),t("br"),t("br"),n._v("Thấy cảnh này, Trương Huyền không khỏi cảm khái."),t("br"),t("br"),n._v("Dựa theo trong lòng của hắn giá cả, thứ này cũng là gặp 300 linh thạch trái phải, cuối cùng thế mà xào đến 540, tiếp cận gấp đôi, chỉ sợ cái kia Vô Hồn Kim Nhân sẽ cùng Trại các chủ nói đồng dạng, không tốt như vậy mua."),t("br"),t("br"),n._v("Ngay sau đó kiện thứ hai, đây là một loại đặc thù khoáng thạch, luyện chế vũ khí gia nhập một chút, liền có thể để uy lực của nó tăng nhiều, sắc bén hiệu quả gia tăng."),t("br"),t("br"),n._v("Khoáng thạch một màn, lập tức so trước đó băng hàn thảo có vẻ càng thêm cuồng bạo."),t("br"),t("br"),n._v("Cùng người tu luyện ngày đêm làm bạn, liền là vũ khí, cấp bậc càng cao, năng lực bảo vệ tính mạng cũng là càng mạnh, thứ đồ tốt này, ai nguyện ý bỏ lỡ."),t("br"),t("br"),n._v("Rất nhanh, giá cả liền vượt qua 600 linh thạch, cuối cùng bị một người trung niên lấy 720 linh thạch giá cả bỏ vào trong túi."),t("br"),t("br"),n._v("“Thứ ba kiện, là một thanh trường kiếm, tên là , là ba trăm năm trước, tứ tinh đỉnh phong luyện khí sư Cửu Mục Tử, thu thập ba mươi bảy loại khoáng thạch, cô đọng mà thành, trong đó trân quý nhất là một cái âm hàn thạch, âm thuộc tính, thuộc tính hàn người tu luyện khởi động, uy lực gấp bội, có thể phát huy ra lực chiến đấu mạnh hơn!”"),t("br"),t("br"),n._v("Trần trưởng lão lấy ra một thanh trường kiếm, nhẹ nhàng rút ra, lập tức hàn mang bắn ra bốn phía, khí tức kinh người."),t("br"),t("br"),n._v("“Đây là một kiện Linh cấp trung phẩm vũ khí, chém sắt như chém bùn, uy lực vô song, giá khởi đầu 500 linh thạch, mỗi lần nâng giá, không muốn thấp hơn 50 linh thạch!”"),t("br"),t("br"),n._v("“Hàn Âm kiếm? Ta nghe qua, là nữ tử dùng kiếm, thập phần cường đại!”"),t("br"),t("br"),n._v("“Linh cấp trung phẩm vũ khí, coi như Hóa Phàm tứ trọng cường giả sử dụng, đều dư xài, 500 linh thạch không quý, ta muốn!”"),t("br"),t("br"),n._v("“Thứ này ta muốn để lại cho ta đạo lữ, ngươi vẫn là đừng suy nghĩ, 550 linh thạch...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Trường kiếm một màn, phía dưới từng cái ánh mắt quyết liệt."),t("br"),t("br"),n._v("“Vật này không yếu, Triệu Nhã, ngươi muốn không?”"),t("br"),t("br"),n._v("Nhìn thoáng qua, Trương Huyền cũng ánh mắt sáng lên, quay đầu nhìn sang."),t("br"),t("br"),n._v("Triệu Nhã Thuần Âm thể chất, thanh trường kiếm này vừa vặn thích hợp, nếu như có thể mua xuống, đưa cho nàng, khẳng định sức chiến đấu sẽ có trên phạm vi lớn gia tăng."),t("br"),t("br"),n._v("“Tiền vẫn là giữ lại mua sắm Vô Hồn Kim Nhân, cứu tỉnh Lộ Xung đi, ta... Từ bỏ!”"),t("br"),t("br"),n._v("Triệu Nhã lắc đầu, ánh mắt lại mang theo khát vọng."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-471"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-473"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);