(window.webpackJsonp=window.webpackJsonp||[]).push([[1713],{2068:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-464-ta-đi-thu-mot-chut"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-464-ta-đi-thu-mot-chut"}},[n._v("#")]),n._v(" Chương 464: Ta đi thử một chút")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Mặc dù rất muốn thanh trường kiếm này, nhưng Triệu Nhã biết, lão sư chuyên môn đến Hồng Hải thành, là vì Vô Hồn Kim Nhân, nếu là bởi vì giúp nàng mua thanh kiếm, dẫn đến cuối cùng không có tiền mua sắm, khẳng định sẽ hối hận cả đời."),t("br"),t("br"),n._v("“Ưa thích liền tốt, linh thạch không đủ, còn muốn những biện pháp khác là được!”"),t("br"),t("br"),n._v("Biết ý nghĩ của nàng, Trương Huyền nói."),t("br"),t("br"),n._v("Trước đó một mực không gặp được thích hợp đồ vật, cũng là không giúp bọn hắn mua sắm, hiện tại đã thấy được, tự nhiên muốn mua lại."),t("br"),t("br"),n._v("“Nghĩ biện pháp? Bây giờ còn có thể có biện pháp nào?” Triệu Nhã sững sờ."),t("br"),t("br"),n._v("Vì cái này năm ngàn linh thạch, lão sư liền nhân gia Giám Bảo các đều phá hủy, đi nơi đó đang lộng càng nhiều linh thạch?"),t("br"),t("br"),n._v("Cũng không thể lại đem cái này phòng đấu giá cũng phá hủy đi."),t("br"),t("br"),n._v("“Đơn giản!”"),t("br"),t("br"),n._v("Nhẹ nhàng nở nụ cười, Trương Huyền đối Tôn Cường khoát tay áo: “Đi đem đấu giá hội phụ trách người gọi qua!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Tôn Cường nháy con mắt, không hiểu rõ chuyện gì xảy ra."),t("br"),t("br"),n._v("Bất quá, mặc dù không biết là thiếu gia mục đích, nhưng đã làm như vậy, nhất định là có dự định, cũng không nhiều lời, quay người rời khỏi."),t("br"),t("br"),n._v("Triệu Nhã đám người hai mặt nhìn nhau... Chẳng lẽ lão sư thật đúng là dự định hủy đi cái này phòng đấu giá?"),t("br"),t("br"),n._v("Thời gian không dài, Tôn Cường mang theo một người trung niên đi tới."),t("br"),t("br"),n._v("Dựa theo đạo lý, thân phận của hắn, không có khả năng đem người kêu đến, có điều, đây là Trại các chủ phòng, cũng coi như cáo mượn oai hùm, mượn một chút thế lực."),t("br"),t("br"),n._v("“Trại các chủ!”"),t("br"),t("br"),n._v("Vừa tiến vào gian phòng, trung niên nhân liền đem ánh mắt tập trung ở trên người lão giả."),t("br"),t("br"),n._v("“Lạc chấp sự, ngươi đã đến. Giới thiệu cho ngươi một chút, vị này là Trương sư!”"),t("br"),t("br"),n._v("Trại các chủ cười cười: “Trương sư, vị này là Lạc chấp sự, Lạc Tần, đấu giá hội sự tình tìm hắn, cơ bản đều có thể giải quyết!”"),t("br"),t("br"),n._v("“Trương sư?”"),t("br"),t("br"),n._v("Lạc Tần lúc này mới chú ý tới người thanh niên này, cùng Trại các chủ cũng xếp hàng ngồi, khí chất bên trên không hề yếu, rõ ràng địa vị không thấp."),t("br"),t("br"),n._v("“Không biết Trương sư tìm tại hạ cần làm chuyện gì?”"),t("br"),t("br"),n._v("Tiến lên một bước, mỉm cười ôm quyền."),t("br"),t("br"),n._v("“A, muốn thêm một vật đấu giá!” Trương Huyền nói."),t("br"),t("br"),n._v("“Thêm đồ vật?”"),t("br"),t("br"),n._v("Lạc Tần sững sờ, lập tức mặt lộ vẻ khó xử: “Cái này... Đấu giá hội đã bắt đầu, còn muốn thêm, chỉ sợ đã không làm được...”"),t("br"),t("br"),n._v("Trước đó không nói, lúc này còn muốn đấu giá đồ vật, nói đùa cái gì."),t("br"),t("br"),n._v("Huống chi, ai cũng không biết ngươi phải thêm đồ vật, đáng tiền không đáng tiền, vạn nhất là cái đồ dỏm, phòng đấu giá tên tuổi chẳng phải nện ở trong tay."),t("br"),t("br"),n._v("“Đừng có gấp cự tuyệt, nhìn xong mới quyết định!”"),t("br"),t("br"),n._v("Biết đối phương sẽ có loại thái độ này, Trương Huyền không thèm để ý chút nào, bàn tay vẫy một cái, ngắt lời hắn."),t("br"),t("br"),n._v("“Được rồi!”"),t("br"),t("br"),n._v("Mặc dù có chút không nguyện ý, nhưng nghĩ tới người này là Trại các chủ khách nhân, lại được xưng là “Sư”, khẳng định vị không thấp, Lạc Tần vẫn là không nhịn được nhẹ gật đầu."),t("br"),t("br"),n._v("Trại các chủ cũng kỳ quái nhìn qua, muốn nhìn một chút cái này để hắn một mực hiếu kỳ thanh niên đến cùng có thể xuất ra cái gì."),t("br"),t("br"),n._v("Theo đạo lý, nếu là thật có bảo vật gì, có thể trên đấu giá hội bán ra, khẳng định trước đó liền hỏi thăm, không đến mức nhất định phải xông Lưu Thương Khúc Thủy, làm bọn hắn bị động như vậy."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Ánh mắt nghi hoặc bên trong, trong tay đối phương thêm ra một vật, thấy rõ ràng thứ này bộ dáng, Trại các chủ thân thể nhoáng một cái, kém chút không thổ huyết."),t("br"),t("br"),n._v("Chỉ thấy thanh niên lòng bàn tay đặt ngang lấy một bức họa, chính là Lưu Thương Khúc Thủy kiện thứ hai bảo vật."),t("br"),t("br"),n._v("“Liền thứ này, nhìn xem có thể hay không giúp ta bán đi!”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Trên người hắn thứ đáng giá, tự nhiên là mới từ Lưu Thương Khúc Thủy lấy được bảo bối."),t("br"),t("br"),n._v("Đã có thể trở thành Giám Bảo các trấn các chi bảo, chỉ cần đấu giá, đừng nói mua một thanh trường kiếm, coi như mua lấy hai thanh, cũng hẳn là không khó."),t("br"),t("br"),n._v("Nguyên nhân chính là như thế, mặc dù biết rõ tiền trên người khả năng không quá đủ mua sắm Vô Hồn Kim Nhân, cũng không thèm để ý."),t("br"),t("br"),n._v("“Trương sư, thứ này... Vô cùng trân quý, vẫn là chớ bán...”"),t("br"),t("br"),n._v("Xấu hổ nở nụ cười, Trại các chủ đáy lòng nhỏ máu."),t("br"),t("br"),n._v("Đây chính là bọn hắn Giám Bảo các đồ vật..."),t("br"),t("br"),n._v("Ngươi nhận chủ ngược lại cũng thôi, cũng coi như để bảo vật tìm được kết cục, bán đi... Chuyện này là sao."),t("br"),t("br"),n._v("“Nếu như cảm thấy bán đáng tiếc... Trại các chủ có thể mua về! Đều là người quen, ta cũng không cần giá cao, năm ngàn linh thạch là đủ...” Trương Huyền cười nhẹ nhàng nhìn qua."),t("br"),t("br"),n._v("“Khụ khụ...” Hơi đỏ mặt, Trại các chủ kém chút bị ngụm nước bị nghẹn, bất đắc dĩ lắc đầu: “Được rồi, ngươi vẫn là tiếp tục... Đi.”"),t("br"),t("br"),n._v("Hắn liền ba ngàn linh thạch đều không bỏ ra nổi đến, đi nơi đó tìm năm ngàn?"),t("br"),n._v(" Coi như không đành lòng Giám Bảo các bảo bối xói mòn bên ngoài, lúc này cũng không có biện pháp."),t("br"),t("br"),n._v("“Thứ này là đồ thật, có thể để cho Trại các chủ giám định!” Thấy đối phương không ngăn trở, Trương Huyền đối trước mắt Lạc Tần nói."),t("br"),t("br"),n._v("“Không cần giám định, bức họa này, tuyệt đối là thật, ta cũng có thể nhìn ra...” Tiếp nhận bức tranh, tiện tay mở ra, chỉ nhìn liếc mắt, Lạc Tần liền gật đầu."),t("br"),t("br"),n._v("Làm phòng đấu giá chấp sự, cũng có rất lợi hại phân biệt năng lực, nếu không, gặp được bảo vật cũng không nhận ra, thật sự mất mặt."),t("br"),t("br"),n._v("Trước mắt bức họa này, khí tức ngưng trọng, linh khí mười phần, cầu bên trong chim tước bất cứ lúc nào cũng sẽ bay lượn mà ra, có ngốc cũng biết, tuyệt đối là cái giá trị liên thành danh họa."),t("br"),t("br"),n._v("“Vậy thì tốt, ngươi cầm lấy đi đấu giá đi! Tốt nhất có thể vượt qua năm ngàn linh thạch bán đi.” Thấy hắn cũng nhận biết, Trương Huyền vừa vặn tiết kiệm phiền phức, nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Nghe hắn nói như vậy, Lạc Tần chần chờ một chút: “Trương sư, bức họa này, mặc dù cấp bậc cực cao, linh tính mười phần, có thể... Muốn bán đi năm ngàn linh thạch, gần như không có khả năng!”"),t("br"),t("br"),n._v("“Ồ? Cái kia có thể bán bao nhiêu?” Trương Huyền nghi hoặc."),t("br"),t("br"),n._v("Hắn đối bảo vật giá trị, biết đến không tính là quá rõ ràng, nhất là lấy linh thạch làm đơn vị bán ra. Vốn cho rằng thứ này đã được thu tại Lưu Thương Khúc Thủy, làm trấn các chi bảo, giá cả bao lớn, khẳng định vượt xa năm ngàn... Không nghĩ cái tên này lại nói không có khả năng."),t("br"),t("br"),n._v("Đây là có chuyện gì?"),t("br"),t("br"),n._v("“Nhiều nhất... Năm trăm linh thạch!” Lạc Tần hơi đỏ mặt."),t("br"),t("br"),n._v("“Năm trăm?”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, Trương Huyền tràn đầy không vui: “Bức họa này, xuất từ một vị ngũ tinh thư họa sư tay, tự mang linh tính, không chỉ có thể hấp thu linh khí bổ sung bức tranh, trong đó càng mang theo người tu luyện đối với thiên địa tự nhiên cảm ngộ, thời gian dài quan sát, Hóa Phàm ba, bốn trọng cường giả, đều có lợi ích rất lớn... Làm sao có thể chỉ trị giá năm trăm?”"),t("br"),t("br"),n._v("Trải qua Thiên Đạo thư viện phân biệt, thuộc về chính phẩm, ngũ tinh thư họa sư lưu lại bảo bối, trọn vẹn đạt đến đệ lục cảnh đỉnh phong, khoảng cách truyền thuyết đệ thất cảnh, cũng chỉ có kém một bước."),t("br"),t("br"),n._v("Chỉ trị giá năm trăm? Nói đùa sao!"),t("br"),t("br"),n._v("“Đúng vậy a, ngươi lừa gạt ai đây? Tốt như vậy danh họa, làm sao có thể liền điểm ấy giá tiền?” Tôn Cường mấy người cũng mặt mũi tràn đầy không tin."),t("br"),t("br"),n._v("“Trương sư đừng có gấp, nghe ta cẩn thận giải thích!”"),t("br"),t("br"),n._v("Lạc Tần liền vội vàng khom người nói, tràn đầy xấu hổ: “Bức họa này họa phong thuần phác, đại khai đại hợp, là mọi người chi tác, không thể nghi ngờ. Nhưng... Không có tên, cũng không lưu lại mặc bảo, giá trị một cái liền ngã xuống. Thư hoạ đáng giá nhất, cũng không phải là tác phẩm hội họa bản thân, mà là cùng đồ cổ đồng dạng, lưu lại niên đại lắng đọng cùng vẽ tranh người danh khí. Nếu như đây là một vị nổi tiếng liên minh danh gia làm, năm ngàn linh thạch nhẹ nhõm có thể bán ra... Nhưng bây giờ như vậy, năm trăm xem như rất cao!”"),t("br"),t("br"),n._v("Nghe được giải thích, sửng sốt một chút, Trương Huyền nhìn về phía cách đó không xa Trại các chủ, chỉ thấy hắn cũng nhẹ gật đầu."),t("br"),t("br"),n._v("“Còn có việc này?” Không còn gì để nói."),t("br"),t("br"),n._v("Suy nghĩ kỹ một chút cũng đúng."),t("br"),t("br"),n._v("Thư hoạ thứ này, cũng không phải là vũ khí, thiên tài địa bảo, đối người tu luyện có thực sự hiệu quả, mà là đột hiển một người phẩm vị cùng yêu thích, nếu như không phải danh gia làm, cất giữ giá trị, quả thực sẽ lập tức đại giảm."),t("br"),t("br"),n._v("“Bức họa này là tám trăm năm trước, ngũ tinh thư hoạ đại tông sư, Ngô Hiên Tử lưu lại, chẳng lẽ hắn cũng không tính danh gia?” Trương Huyền nói."),t("br"),t("br"),n._v("“Ngô Hiên Tử? Hắn đương nhiên là danh gia, ngũ tinh thư hoạ đại sư, càng là vạn quốc liên minh vị thứ nhất ngũ tinh tuần thú sư, nếu thật là hắn bút tích thực, giá cả chỉ sợ không chỉ năm ngàn. Chỉ bất quá... Hắn danh khí mặc dù vang dội, nhưng tại vạn quốc liên minh, đợi đến thời gian không dài, cũng không lưu lại quá nhiều tác phẩm... Bức họa này, thế nào xác nhận là hắn?”"),t("br"),t("br"),n._v("Lạc Tần sững sờ, nhịn không được hỏi."),t("br"),t("br"),n._v("Ngô Hiên Tử, tám trăm năm trước vạn quốc liên minh nhất tiếng tăm lừng lẫy nhân vật vô thượng, không đủ hai trăm tuổi, liền đem thuần thú cùng thư hoạ hai loại chức nghiệp đột phá đến cấp năm sao đừng."),t("br"),t("br"),n._v("Đáng tiếc, lưu tồn ở thế tác phẩm quá ít, không ít giám bảo sư liền đầu bút lông của hắn, thói quen cũng không biết, cũng là không cách nào phán định bức họa này có phải là hắn hay không."),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt."),t("br"),t("br"),n._v("Hắn là thông qua thư viện mới biết được này tấm tác phẩm lai lịch, có thể cái này... Không cách nào biểu hiện ra cho người khác nhìn ah."),t("br"),t("br"),n._v("Ngô Hiên Tử lại không lưu cái gì tác phẩm hội họa, coi như so với, cũng không thể nào nói đến, nếu không phải như thế, bức họa này, cũng không có khả năng đặt ở Giám Bảo các nhiều năm như vậy, không người nhận ra, cũng không có người biết lai lịch."),t("br"),t("br"),n._v("“Trừ phi... Có một vị lợi hại thư họa sư, vì bản vẽ này đề danh, coi như danh khí không bằng Ngô Hiên Tử, chỉ cần cấp bậc tương đương, nói ra chân vận, lấy được linh tính tán thành, cũng có thể để giá trị tăng gấp đôi, bất quá... Nhiều nhất chỉ có thể lật đến hai ngàn linh thạch trái phải!”"),t("br"),t("br"),n._v("Lạc Tần nói."),t("br"),t("br"),n._v("“Đề danh? Nói ra chân vận?”"),t("br"),t("br"),n._v("“Ừm, có điều, muốn cho tác phẩm hội họa đề danh, nào có dễ dàng như vậy. Mỗi người ý nghĩ không giống, nhìn tác phẩm hội họa sau cảm nhận cũng khác biệt, tư duy hơi cùng tác giả không giống, liền không cách nào lấy được tán thành! Như vậy, không những không thể để cho thư hoạ giá trị tăng lên, làm không cẩn thận sẽ còn tổn hại vốn có họa phong, để giá trị sụt giảm!”"),t("br"),t("br"),n._v("Lạc Tần giải thích nói: “Cũng chỉ có trước thời hạn hiểu nguyên tác giả thói quen sinh hoạt, đối với hắn ý nghĩ biết rất nhiều, mới có cơ hội nói ra chân vận, để giá trị gia tăng, nếu không...”"),t("br"),t("br"),n._v("Lời tuy chưa nói xong, trong giọng nói nhưng tiết lộ ý tứ."),t("br"),t("br"),n._v("Một vạn người nhìn vẽ, có một vạn loại ý nghĩ."),t("br"),t("br"),n._v("Muốn phỏng đoán tiền nhân ý tứ, tới tương đương sao mà khó khăn, có thể nói, gần như là không thể nào làm được."),t("br"),t("br"),n._v("“Ý của ngươi là... Biết nguyên tác giả đối vẽ lĩnh ngộ, đề ở phía trên, liền vô cùng có khả năng nói ra chân vận?” Trương Huyền hỏi."),t("br"),t("br"),n._v("“Nếu như biết nguyên tác giả... Cũng chính là Ngô Hiên Tử ý nghĩ, viết ở phía trên, tuyệt đối có thể để cho giá trị bạo tăng, đáng tiếc Ngô Hiên Tử phải chăng lưu lại tác phẩm hội họa đều không có người xác định, hắn làm người khiêm tốn, lúc nào vẽ, tư tưởng trạng thái càng không vì người biết... Lại thêm này tấm, quỷ dị khó lường, cụ thể là cái gì chim, đều nhận không ra... Muốn phỏng đoán, khó!”"),t("br"),t("br"),n._v("Lạc Tần lắc đầu."),t("br"),t("br"),n._v("Tuy chỉ nhìn thoáng qua, nhưng cũng biết, muốn tại trên bức họa này đề danh hơn nữa nói ra chân vận, cùng Ngô Hiên Tử ý nghĩ đồng dạng, quả thực liền là không thể nào."),t("br"),t("br"),n._v("“Khó?”"),t("br"),t("br"),n._v("Nghe xong giải thích của hắn, Trương Huyền sửng sốt một chút, nhẹ nhàng nở nụ cười, nhìn lại."),t("br"),t("br"),n._v("“Đã đề danh liền có thể để bức tranh này tăng giá trị tài sản, không bằng...”"),t("br"),t("br"),n._v("“Ta đi thử một chút!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-472"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-474"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);