(window.webpackJsonp=window.webpackJsonp||[]).push([[1715],{2070:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-466-nha-chung-ta-thieu-gia-mua-【-canh-thu-nhat-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-466-nha-chung-ta-thieu-gia-mua-【-canh-thu-nhat-】"}},[n._v("#")]),n._v(" Chương 466: Nhà chúng ta thiếu gia mua! 【 canh thứ nhất 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Làm tứ tinh đỉnh phong giám bảo sư, đối cái nghề nghiệp này hiểu được cốt tủy, có thể căn cứ đầu bút lông, vẽ tranh đặc điểm phân tích ra tác giả là ai, liền rất nghịch thiên, trực tiếp giám định ra uống rượu xong vẽ sai, còn tự hủy tên, lạc khoản (phần đề chữ, ghi tên trên bức vẽ)... Đây thật là giám bảo có thể thấy được?"),t("br"),t("br"),n._v("Không chỉ Trại các chủ nghĩ như vậy, Triệu Phi Vũ, Lạc Tần cũng tất cả đều mặt mũi tràn đầy choáng váng, gắt gao chằm chằm tới, muốn biết hắn đến cùng là như thế nào biết nhiều như vậy."),t("br"),t("br"),n._v("“Ha ha!”"),t("br"),t("br"),n._v("Gặp bọn họ ánh mắt, Trương Huyền nhẹ nhàng nở nụ cười: “Giám bảo, không chỉ muốn giám định bảo vật niên đại, quan trọng hơn suy bụng ta ra bụng người, suy đoán đối phương đang làm cái gì, như vậy mới có thể có càng sâu hiểu rõ!”"),t("br"),t("br"),n._v("“Bản vẽ này, nhìn như chỉnh tề, trên thực tế cũng không quy tắc. Giám định thư hoạ, người người đều biết, trọng yếu nhất chính là đối xứng, mà bộ này, bên trái cao bên phải thấp, cho người ta một loại phía bên trái nghiêng cảm giác. Rất rõ ràng, vẽ tranh người đứng không vững, hẳn là nằm ở trên giấy tuyên. Ngươi nhìn chỗ này, chỗ này còn có chỗ này, cũng có thể thấy được, chỉ có nằm sấp mới thích hợp nhất...”"),t("br"),t("br"),n._v("“Mặt khác các ngươi chất vấn Bạch Tích chim, cánh ngắn, mỏ trắng, hắc trảo, hồng đầu, hình thể gầy, cùng trong hiện thực không một chỗ tương đồng, chợt nhìn, quả thực không biết là vật gì loại!”"),t("br"),t("br"),n._v("“Nhưng cẩn thận nhìn, bức hoạ bên trong, khắp nơi đều là cỏ lau, lại thêm có Tử Tân hoa nở. Hai thứ đồ này là Bạch Tích chim yêu nhất, cực kỳ mấu chốt chính là, một con chim tước trong miệng ngậm một cọng cỏ!”"),t("br"),t("br"),n._v("Trương Huyền một chỉ."),t("br"),t("br"),n._v("Mọi người quả nhiên ở trong bức tranh nhìn thấy một cái Bạch Tích chim trong miệng ngậm lấy một gốc cỏ mịn."),t("br"),t("br"),n._v("“Cọng cỏ này cùng Bồ diệp thảo có chín phần tương tự, Bồ diệp thảo, ẩn chứa kịch độc, không có linh thú ưa thích, cho nên đối với người sinh ra lừa dối, nhưng nếu như nói đây là Bạch Giác căn đây?”"),t("br"),t("br"),n._v("“Bạch Giác thảo rễ, cùng Bồ diệp thảo tương tự, thậm chí có chút dược liệu thương nghiệp cung ứng, cố ý lấy giả đánh tráo... Người sau, Bạch Tích chim sẽ không tiếp xúc, có thể Bạch Giác thảo rễ, lại là nó yêu nhất...”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trại các chủ, Lạc Tần tất cả đều thân thể cứng đờ."),t("br"),t("br"),n._v("Đối phương nói nhịp nhàng ăn khớp, không có bất kỳ cái gì sai lầm, nếu như dựa theo loại này phân tích, rất dễ dàng liền biết liền là Bạch Tích chim."),t("br"),t("br"),n._v("“Xác định chim chủng loại, còn lại liền đơn giản. Cẩn thận quan sát, có thể phát hiện, tranh một mạch mà thành, dùng không phải thường thấy nhất tranh thuỷ mặc, mà là thẳng thắn phát biểu suy nghĩ trong lòng. Làm một cái có thể vẽ ra lục cảnh tác phẩm siêu cấp thư họa sư, làm sao có thể liền cơ sở nhất vấn đề đều sẽ xem nhẹ? Mấu chốt nhất là... Còn có chim tước trên người xuất hiện xoá và sửa dấu vết? Lại thêm trước đó suy đoán, có thể tuỳ tiện đoán ra, là say rượu mà làm!”"),t("br"),t("br"),n._v("“Cân nhắc ra nhiều như vậy, biết tên, lai lịch cũng là dễ dàng...”"),t("br"),t("br"),n._v("Khóe miệng vung lên, Trương Huyền giải thích."),t("br"),t("br"),n._v("Những này cũng không phải ăn nói lung tung, làm tam tinh trở lên thư họa sư, biết kết quả, phản lấy cân nhắc liền dễ dàng không ít, kết hợp với thư viện cho thiếu hụt, có thể tuỳ tiện liền có thể phát hiện vấn đề."),t("br"),t("br"),n._v("Bất quá, nếu như không biết đáp án, chỉ bằng vào quan sát liền muốn nhìn ra những này, độ khó bao lớn, có thể nói gần như không có khả năng."),t("br"),t("br"),n._v("“Cái này, cái này...”"),t("br"),t("br"),n._v("Thấy hắn chậm rãi mà nói, có lý có chứng cứ, gian phòng tất cả mọi người mặt mũi tràn đầy phát điên."),t("br"),t("br"),n._v("Trại các chủ giờ mới hiểu được, đối phương giám bảo trình độ quả thực mạnh hơn hắn không chỉ một lần, căn bản không tại cùng một cái cấp bậc."),t("br"),t("br"),n._v("“Phải lập tức báo cáo tổng bộ, ánh mắt như thế cùng năng lực, giám bảo trình độ chí ít đạt đến ngũ tinh, thậm chí... Cao hơn!”"),t("br"),t("br"),n._v("Nuốt nước bọt, trong lòng ngầm hạ quyết định."),t("br"),t("br"),n._v("Trước mắt vị này trình độ, đã vượt qua vạn quốc liên minh phạm trù, coi như so với ngũ tinh giám bảo sư đều chỉ mạnh không yếu."),t("br"),t("br"),n._v("Thiên tài như thế, nhất định phải nhanh báo cáo tổng bộ, không cho minh châu bị long đong."),t("br"),t("br"),n._v("“Tốt, đừng chậm trễ đấu giá hội, ngươi cầm tới đấu giá đi!”"),t("br"),t("br"),n._v("Giải thích hết, Trương Huyền nhìn thoáng qua, thấy toàn bộ phòng đấu giá đều bị kinh động, ánh mắt mọi người đều tập trung tới, khoát tay áo."),t("br"),t("br"),n._v("“Vâng!” Lạc Tần lúc này mới kịp phản ứng, hai tay đem thư hoạ bưng lên, lui ra ngoài."),t("br"),t("br"),n._v("Thời gian không dài, trên đài đấu giá Trần trưởng lão tràn đầy khiếp sợ thanh âm vang lên, dường như ngay cả hắn chính mình cũng không quá tin tưởng: “Vừa rồi cảnh tượng, chư vị chắc hẳn đều thấy được, một bộ thất cảnh thư hoạ tại cái này sinh ra, chúng ta nhiều người như vậy cùng chứng kiến cái này một thời khắc huy hoàng!”"),t("br"),t("br"),n._v("“Tranh bản thân là từ tám trăm năm trước thư hoạ đại tông sư Ngô Hiên Tử làm, từ một vị tân thư hoạ đại tông sư tự tay viết đề danh, đã dẫn phát linh tính xao động, đem nó theo lục cảnh đỉnh phong trực tiếp đột phá!”"),t("br"),t("br"),n._v("“Thất cảnh tác phẩm hội họa, vạn quốc liên minh cái thứ nhất thất cảnh tác phẩm hội họa, tiền nhân cùng hậu nhân hoàn mỹ hợp tác kết quả, không nói sau này không còn ai, nhưng tuyệt đối là xưa nay chưa từng có!”"),t("br"),t("br"),n._v("Trần trưởng lão thanh âm càng ngày càng kích động: “Hiện tại, bức tranh này chủ nhân, dự định đem nó đấu giá, giá khởi đầu, tám ngàn linh thạch! Mỗi lần gia tăng không thể ít hơn một ngàn, cạnh tranh bắt đầu!”"),t("br"),t("br"),n._v("“Bức tranh này thật bán? Quá tốt rồi, ta muốn mua...”"),t("br"),t("br"),n._v("“Tám ngàn linh thạch giá quy định? Mặc dù đắt, thế nhưng đáng giá, nhanh báo tin gia tộc, đem bất động sản thế chấp, cũng muốn mua xuống!”"),t("br"),t("br"),n._v("“Mặc kệ tốn hao đại giới cỡ nào...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Nghe được bức tranh này thế mà đấu giá, phía dưới tất cả mọi người xôn xao, từng cái lâm vào điên cuồng."),t("br"),t("br"),n._v("Thất cảnh tác phẩm hội họa, vạn quốc liên minh chưa hề xuất hiện qua, có thể mua lấy một bộ, táng gia bại sản đều gặp!"),t("br"),t("br"),n._v("Không nói cái khác, chuyển tay lần nữa bán ra, giá cả vô cùng có khả năng liền sẽ gấp bội."),t("br"),t("br"),n._v("“Ta ra chín ngàn!”"),t("br"),t("br"),n._v("“Một vạn!”"),t("br"),t("br"),n._v("“Một vạn một!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Giá cả trong nháy mắt tăng vọt."),t("br"),t("br"),n._v("“Cái này...” Nhìn xem phía dưới đã điên cuồng, Trương Huyền nháy con mắt."),t("br"),t("br"),n._v("Vốn cho rằng nắm giữ giám bảo sư nghề nghiệp Trại các chủ không bỏ ra nổi ba ngàn linh thạch, bán năm ngàn tuyệt đối là cực hạn, nằm mơ đều không có nghĩ đến, trực tiếp tiêu thăng đến hơn một vạn."),t("br"),n._v("“Ta cùng Trần trưởng lão bọn hắn đều như thế, tiền trên người, đều sưu tập bảo vật, chân chính có thể sử dụng linh thạch, cũng không có quá nhiều. Lại nói, ta lại nhiều cũng chỉ là người, bọn hắn đều đại biểu một cái thế lực cùng đại gia tộc...”"),t("br"),t("br"),n._v("Nhìn ra hắn nghi hoặc, Trại các chủ cười khổ nói."),t("br"),t("br"),n._v("Dựa theo đạo lý, nắm giữ giám bảo sư chức nghiệp, kiếm tiền nhanh, hẳn là thân gia cực cao, không thể so với phía dưới những cái kia yếu."),t("br"),t("br"),n._v("Đáng tiếc, tiền của hắn đều sưu tập bảo vật chứa đựng, lưu thông tài chính cũng không nhiều, có thể xuất ra ba ngàn đã là cực hạn."),t("br"),t("br"),n._v("Mà phía dưới những cái kia không giống, một cái gia tộc một cái thế lực, mấy ngàn năm tích lũy, tài phú kinh người, hơn một vạn linh thạch đối với hắn mà nói, không bỏ ra nổi đến, nhưng đối với những người này tới nói, nhiều nhất thương cân động cốt, chỉ cần nghĩ biện pháp lần nữa bán ra, chẳng những hồi vốn, càng sẽ kiếm một món hời."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Phòng đấu giá một cái gian phòng bên trong."),t("br"),t("br"),n._v("Một thanh niên ngồi ngay ngắn ở cửa sổ trước mặt, long bàng hổ cứ, mang theo ngạo nghễ khí tức."),t("br"),t("br"),n._v("Phía sau hắn đứng đấy một cái lão giả, mặc dù không biểu hiện ra tu vi, nhưng cho người ta một loại như vực sâu biển lớn cảm giác, nhìn không ra sâu cạn, chỉ sợ so với Kim Tòng Hải, đều chỉ mạnh không yếu."),t("br"),t("br"),n._v("“Thất cảnh tác phẩm hội họa? Có ý tứ!”"),t("br"),t("br"),n._v("Nhìn thấy phía dưới Trần trưởng lão đem  bày ra đến, thanh niên mắt sáng lên."),t("br"),t("br"),n._v("“Thiếu gia, nhưng có hứng thú?” Lão giả khom người."),t("br"),t("br"),n._v("“Thất cảnh tác phẩm hội họa, liền xem như tông môn, cũng coi như được bảo vật, mua lại, đối ta phụ tu thư hoạ chức nghiệp, khẳng định có rất lớn ích lợi.” Thanh niên gật đầu."),t("br"),t("br"),n._v("“Vậy thì tốt, ta liền mua xuống!”"),t("br"),t("br"),n._v("Lão giả gật đầu, liền muốn báo giá."),t("br"),t("br"),n._v("“Trước không vội, chờ bọn hắn báo không sai biệt lắm, chúng ta lại mở miệng.” Thanh niên bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Vâng!” Lão giả chần chờ một chút: “Chỉ là... Ta sợ một lát nữa giá cả xào quá cao, chúng ta mang linh thạch, không đủ mua sắm Vô Hồn Kim Nhân...”"),t("br"),t("br"),n._v("“Vô Hồn Kim Nhân liên quan vu hồn sư cái nghề nghiệp này, nếu có thể lấy được một chút hữu dụng tin tức, mang về tông môn, địa vị của ta khẳng định trong nháy mắt bạo tăng, thu hoạch được Danh Sư đường coi trọng... Trở thành thế hệ trẻ tuổi thứ nhất, chỉ là vấn đề thời gian.”"),t("br"),t("br"),n._v("Thanh niên nhẹ nhàng nở nụ cười: “Bất quá... Điểm ấy không cần lo lắng, Vô Hồn Kim Nhân sự tình, ta đã chuyên môn nghe ngóng rõ ràng, đấu giá thứ này gia hỏa, dường như không muốn lấy linh thạch phương thức bán ra.”"),t("br"),t("br"),n._v("“Không lấy linh thạch bán ra?”"),t("br"),t("br"),n._v("Lão giả sững sờ."),t("br"),t("br"),n._v("Lấy ra bán, không phải là kiếm tiền ư? Không muốn lấy linh thạch phương thức bán ra, có ý tứ gì?"),t("br"),t("br"),n._v("“Nghe nói cái tên này thọ nguyên gần lấy hết, chỉ cần tu vi không cách nào đột phá giam cầm, linh thạch lại nhiều cũng là phế phẩm, đoán không sai, hắn muốn chính là chương trình học ngọc phù, hơn nữa cấp bậc càng cao càng tốt.” Thanh niên nói."),t("br"),t("br"),n._v("Linh thạch, mặc dù đại biểu tài phú, nhưng làm đạt tới số lượng nhất định, cũng chỉ là một đống con số."),t("br"),t("br"),n._v("Tu vi không cách nào đột phá, tuổi thọ không có cách nào kéo dài, sớm muộn gì đều sẽ biến thành đất vàng, biến mất ở thế giới... Người đều chết rồi, tiền lại nhiều thì có ích lợi gì?"),t("br"),t("br"),n._v("Còn không bằng hối đoái danh sư chương trình học ngọc phù, vạn nhất có thể mượn cơ hội đột phá, tuyệt đối so với chỉ lấy được một ít linh thạch phải tốt hơn nhiều."),t("br"),t("br"),n._v("Huống chi thực lực gia tăng, muốn kiếm tiền còn không dễ dàng?"),t("br"),t("br"),n._v("Vô luận nơi nào, thực lực vẫn là trọng yếu nhất căn bản nhất, cái khác đều là vật ngoài thân."),t("br"),t("br"),n._v("“Nếu như chỉ cần chương trình học ngọc phù, cái kia quả thực liền đơn giản... Thiếu gia mình, chính là một vị thiên tài danh sư, giúp chỗ này tiểu nhân vật chỉ điểm, để hắn tấn cấp, không khó lắm!” Lão giả hiểu được mỉm cười."),t("br"),t("br"),n._v("“Cái kia là tự nhiên, lần này danh sư thi đấu, ta thế nhưng là ít nhất phải đi vào năm vị trí đầu!”"),t("br"),t("br"),n._v("Thanh niên trên mặt lộ ra tràn đầy tự tin."),t("br"),t("br"),n._v("“Thiếu gia thiên phú vô song, mặc kệ tâm cảnh khắc độ vẫn là đối võ đạo lý giải, đều vượt xa thường nhân, ta xem không chỉ năm vị trí đầu, trước ba đều không có vấn đề gì!” Lão giả nói."),t("br"),t("br"),n._v("“Trước ba... Cái này khó mà nói, mấy cái kia cũng không tốt đối phó, có điều, ta cũng không sợ, liền thấy thời điểm ai trạng thái tốt!”"),t("br"),t("br"),n._v("Vẻ mặt nghiêm túc một cái, thanh niên lập tức khoát khoát tay, hướng phòng đấu giá nhìn lại: “Tốt, không sai biệt lắm kết thúc, kêu giá đi! Đúng rồi, linh thạch ta còn có tác dụng, không muốn lãng phí.”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Lão giả gật gật đầu."),t("br"),t("br"),n._v("Lúc này, giá cả đã báo đến không sai biệt lắm, coi như phòng đấu giá tới không ít có tiền gia tộc và thế lực lớn, đối mặt giá cả cỡ này, cũng chùn bước, không thể không dừng lại."),t("br"),t("br"),n._v("“Hai vạn linh thạch một lần, hai vạn linh thạch hai lần, còn có hay không so giá tiền này cao? Nếu như không có, bộ này say rượu một giấc chiêm bao cảm giác, liền cho bán cho số 14 phòng bằng hữu...”"),t("br"),t("br"),n._v("Trần trưởng lão nhìn quanh một vòng."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Hai vạn linh thạch? Không nghĩ tới một bức họa, thế mà bán nhiều tiền như vậy...” Nhìn phía dưới cạnh tranh mà lên giá cả, Trương Huyền con mắt tỏa ánh sáng."),t("br"),t("br"),n._v("“Đúng vậy a, xem ra hẳn là không có người cạnh tranh, hai vạn linh thạch, cái này có thể so sánh... Vượt quan kiếm tiền nhanh cỡ nào!” Tôn Cường cũng hưng phấn mà gật đầu."),t("br"),t("br"),n._v("Hai người tiếng nói chưa rơi, liền nghe đến một cái lão giả thanh âm vang lên."),t("br"),t("br"),n._v("“Chậm rãi, tứ tinh chương trình học ngọc phù 21 viên, bức họa này, nhà chúng ta thiếu gia mua!”"),t("br"),t("br"),n._v("(Hôm nay bộc phát, mọi người nguyệt phiếu, phiếu đề cử này lên, canh thứ nhất đến!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-474"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-476"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);