(window.webpackJsonp=window.webpackJsonp||[]).push([[1717],{2072:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-468-vo-hon-kim-nhan-đau-gia-ha"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-468-vo-hon-kim-nhan-đau-gia-ha"}},[n._v("#")]),n._v(" Chương 468: Vô Hồn Kim Nhân đấu giá (hạ)")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Tùy ý nói?”"),i("br"),i("br"),n._v("“Chỉ cần hắn hài lòng? Liền sẽ trao đổi? Đây là điều kiện gì?”"),i("br"),i("br"),n._v("“Chẳng lẽ cái tên này háo sắc, còn muốn chuẩn bị mấy mỹ nữ hay sao?”"),i("br"),i("br"),n._v("“Đúng vậy a, điều kiện này cũng rất cổ quái, cũng không thể để chúng ta phỏng đoán tâm ý của hắn, đi làm quyết định đi!”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe được cổ quái như vậy đấu giá điều kiện, tất cả mọi người mộng."),i("br"),i("br"),n._v("Đấu giá hội có đòi tiền, chương trình học ngọc phù, các loại trân quý vật phẩm, chưa từng nghe qua điều kiện không hạn, chỉ vì để hắn hài lòng."),i("br"),i("br"),n._v("Một mực không gặp được hài lòng, còn không bán?"),i("br"),i("br"),n._v("Đây coi là cái gì?"),i("br"),i("br"),n._v("“Có ý tứ!”"),i("br"),i("br"),n._v("Số 23 bên trong phòng, thanh niên lúc này đã khôi phục lại, lại không có trước đó nôn nóng, ngược lại mỉm cười."),i("br"),i("br"),n._v("“Có ý tứ?” Lão giả không hiểu."),i("br"),i("br"),n._v("“Hắn đây là ngay tại chỗ lên giá, muốn đến cái lớn!” Thanh niên nói."),i("br"),i("br"),n._v("“Thiếu gia, thế nhưng là nhìn ra cái gì?”"),i("br"),i("br"),n._v("“Rất đơn giản, bởi vì không có hạn chế, cũng không có giá rẻ, muốn mua người mua, sợ bị người cướp đi, liền sẽ tùy ý tăng giá cả, nói ra một cái có thể để cho hắn giá vừa ý.”"),i("br"),i("br"),n._v("Thanh niên nhẹ nhàng nở nụ cười, lộ ra rõ ràng trong lòng biểu lộ: “Như vậy trải qua, giá cả liền sẽ bay đầy trời, các loại bảo vật tầng tầng lớp lớp, hắn chỉ cần lựa chọn một cái rất muốn nhất, giá trị cao nhất, khẳng định so đấu giá còn mạnh hơn nhiều.”"),i("br"),i("br"),n._v("“Cái này...” Lão giả sửng sốt."),i("br"),i("br"),n._v("Đúng là như thế."),i("br"),i("br"),n._v("Có chút bảo vật bị một chút thế lực lớn tư tàng, là đấu giá hội bên trên không nhất định có thể có được, nếu như bọn hắn không nguyện ý lấy ra, rất nhiều người đem vĩnh viễn không gặp được."),i("br"),i("br"),n._v("Lấy vật đổi vật, những thế lực này vì lấy được Vô Hồn Kim Nhân, liền vô cùng có khả năng xuất ra, như vậy trải qua, hắn có được đồ vật, khẳng định lại thêm có giá trị."),i("br"),i("br"),n._v("“Trước mặc kệ, nhìn xem những người này đều có thể xuất ra cái gì đi!”"),i("br"),i("br"),n._v("Biết mục đích của đối phương, thanh niên cười cười, không nói thêm lời."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("“Để hắn hài lòng?”"),i("br"),i("br"),n._v("Nghe được điều kiện này, Trương Huyền cũng là sững sờ."),i("br"),i("br"),n._v("“Thật sự là khẩu khí thật lớn, Vô Hồn Kim Nhân coi như trân quý, cũng chỉ là cái không biết chết rồi bao nhiêu năm khôi lỗi mà thôi, có thể hay không dùng còn chưa nhất định. Muốn mua, cũng chỉ là muốn quan sát một chút Thượng Cổ vu hồn sư kỹ nghệ, chân chính muốn mua người, chưa chắc sẽ có bao nhiêu!”"),i("br"),i("br"),n._v("Triệu Phi Vũ hừ một tiếng."),i("br"),i("br"),n._v("Vô Hồn Kim Nhân, nói trắng ra là liền là cái điểm cao minh khôi lỗi, thực lực cùng Hóa Phàm tam trọng tương đương, nếu như không phải nghiên cứu, ai muốn cái đồ chơi này làm gì?"),i("br"),i("br"),n._v("Mấy ngàn linh thạch, Hóa Phàm cảnh tam trọng cường giả, đều có thể thuê mấy cái."),i("br"),i("br"),n._v("“Ngươi sai... Kỳ thật, bọn hắn muốn Vô Hồn Kim Nhân mục đích, cũng không phải là thứ này, mà là vu hồn sư truyền thừa!”"),i("br"),i("br"),n._v("Trại các chủ lắc đầu."),i("br"),i("br"),i("span",[n._v("   http://ngantruyen.com/"),i("br")]),n._v(" “Truyền thừa?”"),i("br"),i("br"),n._v("Triệu Phi Vũ nhìn qua."),i("br"),i("br"),n._v("“Ừm, đầu này Vô Hồn Kim Nhân ta chuyên môn quan sát qua, mặc dù niên đại xa xưa, nhưng bảo tồn vô cùng tốt, nếu như cẩn thận nghiên cứu lời nói, vô cùng có khả năng tìm tới bào chế chi pháp! Mặc dù thứ này, chỉ là vu hồn sư một bộ phận năng lực, một khi tìm tới, tuyệt đối là một số lớn tài phú.” Trại các chủ nói."),i("br"),i("br"),n._v("“Tìm tới bào chế chi pháp thì có ích lợi gì? Chẳng lẽ... Còn muốn cầm người sống luyện chế thứ quỷ này?” Triệu Phi Vũ sửng sốt, nhướng mày: “Nếu như cho Danh Sư đường biết, khẳng định sẽ đi đầu giết chết.”"),i("br"),i("br"),n._v("“Không phải luyện chế cái này...”"),i("br"),i("br"),n._v("Trại các chủ lắc đầu: “Tuổi thọ của con người có hạn, có thể linh hồn lại là có thể sống thật lâu, thật tốt bảo dưỡng, bốn năm trăm năm, cũng không thành vấn đề. Tìm tới thứ này bào chế phương pháp, liền vô cùng có khả năng đem nhiều lần sắp tử vong người linh hồn, bật ở phía trên. Thời kỳ Thượng Cổ, Danh Sư đường vây quét vu hồn sư thời điểm, liền từng bị bọn hắn dùng loại phương pháp này đào thoát qua nhiều lần...”"),i("br"),i("br"),n._v("“Dời đi linh hồn, mượn nhờ Vô Hồn Kim Nhân mà sống sót?”"),i("br"),i("br"),n._v("Triệu Phi Vũ trừng to mắt."),i("br"),i("br"),n._v("Coi như hắn đọc sách nhiều, tri thức uyên bác, cũng là lần đầu tiên nghe được."),i("br"),i("br"),n._v("“Đúng vậy a!” Trại các chủ gật đầu."),i("br"),i("br"),n._v("Những chuyện này, cũng là hắn biết đây là Vô Hồn Kim Nhân về sau, nghiên cứu vô số liên quan thư tịch mới biết."),i("br"),i("br"),n._v("Đến đây mua sắm thứ này thế lực lớn, không biết cũng từ nơi nào lấy được tin tức, từng cái tràn đầy khát vọng."),i("br"),i("br"),n._v("Nhất là những cái kia gần tử vong đám lão già này, nếu là có thể kéo dài hơi tàn, dù là lại nhiều mười năm tám năm, khẳng định cũng là cực kỳ vui lòng."),i("br"),i("br"),n._v("“Thật sự là như vậy, cái này Vô Hồn Kim Nhân mua sắm, sẽ gió tanh mưa máu...”"),i("br"),i("br"),n._v("Đem hai người đối thoại nghe vào trong tai, Trương Huyền than thở."),i("br"),i("br"),n._v("Quả thật như thế, Vô Hồn Kim Nhân đủ có thể khiến không ít người điên cuồng, mua sắm giá cả, khả năng cũng sẽ biến thành giá trên trời."),i("br"),i("br"),n._v("“Đã không ai mở miệng, vậy liền ta tới trước đi!”"),i("br"),i("br"),n._v("Mọi người mỗi người có suy nghĩ riêng thời điểm, phòng đấu giá một cái lão giả đi đầu hô lên."),i("br"),i("br"),n._v("“Là Kim Thánh Quỷ Thủ, Liêu Ngọc Tỉ, cái tên này một trăm năm trước đã vang danh thiên hạ, không nghĩ tới thế mà còn chưa có chết!”"),i("br"),i("br"),n._v("“Hắn tới trước, làm không cẩn thận sẽ cho ra một cái làm cho không người nào có thể phản bác giá cả!”"),i("br"),i("br"),n._v("“Yên lặng theo dõi kỳ biến đi...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Thấy rõ ràng người nói chuyện, mọi người nhất thời thấp giọng."),i("br"),i("br"),n._v("Kim Thánh Quỷ Thủ, một trăm năm trước liền vang danh thiên hạ siêu cấp cường giả, vốn cho rằng đã sớm trở về với cát bụi, không nghĩ tới chẳng những không chết, còn tới tham gia cái này đấu giá hội, hơn nữa cái thứ nhất kêu giá."),i("br"),i("br"),n._v("Sống lâu như thế cường giả, trong tay làm sao có thể không có một chút hàng tồn."),i("br"),i("br"),n._v("“Một chuôi Thiên Xu tán!”"),i("br"),i("br"),n._v("Nhìn quanh một vòng, Kim Thánh Quỷ Thủ Liêu Ngọc Tỉ nói."),i("br"),n._v("“Thiên Xu tán? Đó là cái gì?”"),i("br"),i("br"),n._v("“Là một kiện linh phẩm trung kỳ vũ khí, lấy trong bắc đẩu thất tinh Thiên Xu tinh vì tham chiếu cô đọng mà thành, không bàn mà hợp trận pháp, (tụ) tập công kích, phòng ngự cùng một thể, là một kiện không nhiều lắm bảo vật. Hơn một trăm năm trước, danh chấn vạn quốc liên minh, không nghĩ tới thế mà đem thứ này lấy ra!”"),i("br"),i("br"),n._v("“Hối đoái thành linh thạch lời nói, thứ này, giá trị không thể đo lường...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe được Liêu Ngọc Tỉ mở lời, có nhận biết bảo vật này, từng cái kinh ngạc vô cùng, khó mà tin được."),i("br"),i("br"),n._v("“Thiên Xu tán quả thực bất phàm, nhưng muốn thay đổi đầu này Vô Hồn Kim Nhân, rõ ràng còn chưa đủ, vẫn là ta tới đi! Nhìn vị tiên sinh này, khí tức suy yếu, hẳn là thân thể không quá khoẻ mạnh, ta cái này có một gốc ngàn năm huyết sâm. Coi như khí huyết hao tổn thọ nguyên gần tới, nuốt phía sau cũng có thể cam đoan, sống lâu mười năm tám năm!”"),i("br"),i("br"),n._v("Mọi người khiếp sợ còn không có kết thúc, một cái hừ lạnh vang lên."),i("br"),i("br"),n._v("Đồng loạt nhìn lại, nói chuyện chính là cái lông mi đen nhánh trung niên nhân, quần áo lộng lẫy, khí vũ hiên ngang."),i("br"),i("br"),n._v("“Là Bảo Dược các Đại Dược Vương! Ngàn năm huyết sâm, cái này so trước đó bán đấu giá hàn băng linh thảo trân quý nhiều, Thiên Xu tán cũng so ra kém!”"),i("br"),i("br"),n._v("“Đúng vậy a, lấy vật đổi vật quả nhiên điên cuồng, mới cái thứ hai, liền phong bế một bộ phận lớn người ý nghĩ!”"),i("br"),i("br"),n._v("“Loại vật này, có thể ngộ nhưng không thể cầu, không phải có tiền liền có thể mua được...”"),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v("Nghe được vị này Đại Dược Vương thế mà xuất ra ngàn năm huyết sâm, lần nữa xôn xao."),i("br"),i("br"),n._v("Ngay cả giữa đài lão giả, cũng lông mày bỗng nhúc nhích, có điều, lập tức an tĩnh lại."),i("br"),i("br"),n._v("Rất hiển nhiên, thứ này cũng không phải hắn vô cùng cần thiết."),i("br"),i("br"),n._v("“Ngàn năm huyết sâm nghe dọa người, không biết được phương pháp ăn, cứ như vậy phục dụng, không những không thể gia tăng tuổi thọ, làm không cẩn thận sẽ còn bạo thể mà chết. Vẫn là dùng của ta đi, tứ phẩm đỉnh phong linh đan, ! Phục dụng về sau, chỉ cần không có vấn đề quá lớn, hoàn toàn có thể gia tăng một kỷ thọ nguyên. Hơn nữa không có bất kỳ cái gì tác dụng phụ, so cái gì huyết sâm mạnh hơn nhiều lắm!”"),i("br"),i("br"),n._v("Một cái lão giả khẽ nói."),i("br"),i("br"),n._v("“Là luyện đan sư công hội hội trưởng...”"),i("br"),i("br"),n._v("“Kỷ Thọ đan, gia tăng một kỷ thọ nguyên... Thứ này, tuy là tứ phẩm đan dược, nhưng lại không phải ngũ tinh luyện đan sư không thể luyện chế, vừa ra tay chính là cái này, quá hào phóng!”"),i("br"),i("br"),n._v("“Điên rồi, tất cả đều điên rồi...”"),i("br"),i("br"),n._v("Tuy chỉ có ba người ra giá, cũng đã đem bầu không khí đẩy lên cao trào."),i("br"),i("br"),n._v("Trước đó không ít tự nhận là Vô Hồn Kim Nhân nhất định rơi vào bọn hắn tay thế lực, tất cả đều cảm nhận được nồng đậm áp lực."),i("br"),i("br"),n._v("“Cái gì huyết sâm, cái gì Kỷ Thọ đan, thân thể thuộc tính không thích hợp, phục dụng chỉ có thể biến thành kịch độc. Ta có một bộ Linh cấp công pháp, thích hợp tất cả thể chất, chỉ cần tu luyện, tu vi tất nhiên tăng nhiều! Thực lực mạnh, mới có thể cam đoan tuổi thọ tăng trưởng không phải không trung lâu các!”"),i("br"),i("br"),n._v("Lại một người nói."),i("br"),i("br"),n._v("“Công pháp huyền ảo, muốn luyện thành không có mấy năm khổ công làm sao có thể, ta có một khỏa tứ phẩm phá chướng đan, phục dụng cam đoan ngươi có thể đột phá bình chướng, tu vi nước chảy thành sông...”"),i("br"),i("br"),n._v("“Đừng nghe bọn họ, ta có một cái thiên cơ cầu, trấn áp bát phương, khí thôn sơn hà...”"),i("br"),i("br"),n._v("“Ta có...”"),i("br"),i("br"),n._v("Có người mở đầu, đằng sau rất nhiều thế lực lại không giữ lại, nhao nhao mở lời."),i("br"),i("br"),n._v("Tới mua đồ rất nhiều thế lực, tất cả đều không kém gì Giám Bảo các, tĩnh tâm chuẩn bị bảo vật, giá trị bao lớn, có thể nghĩ."),i("br"),i("br"),n._v("“Tốt, chư vị!”"),i("br"),i("br"),n._v("Mọi người ở đây từng người cãi lộn, nhao nhao nói ra mình có thể xuất ra vật phẩm lúc, một cái thanh âm nhàn nhạt vang lên, ở giữa nhất lão giả mở lời."),i("br"),i("br"),n._v("Thấy hắn nói chuyện, gian phòng lập tức an tĩnh lại."),i("br"),i("br"),n._v("“Chư vị bảo vật, đều phi thường trân quý, đáng tiếc, đều không phải là ta muốn.”"),i("br"),i("br"),n._v("Lão giả lắc đầu."),i("br"),i("br"),n._v("“Không phải ngươi muốn? Vậy ngươi đến cùng muốn cái gì? Không ngại nói ra, chúng ta nhiều người như vậy, có thể lấy ra liền tiếp, không bỏ ra nổi tới, cũng tự nhận xui xẻo.” Một thanh âm khẽ nói."),i("br"),i("br"),n._v("“Không sai, muốn cái gì liền nói, đừng để chúng ta đoán đến đoán đi, mặc dù muốn mua Vô Hồn Kim Nhân, thế nhưng không thời gian lâu như vậy cùng ngươi hao tổn!”"),i("br"),i("br"),n._v("Thấy nói ra nhiều như vậy bảo bối, mỗi một cái đều giá trị liên thành, cái tên này lại nói đều không muốn, mọi người từng cái tất cả đều rống lên."),i("br"),i("br"),n._v("Mọi người cứ việc rất muốn mua sắm Vô Hồn Kim Nhân, thế nhưng không muốn suy đoán người khác tâm lý, ngươi một mực làm trò bí hiểm, rốt cuộc muốn làm gì?"),i("br"),i("br"),n._v("Chẳng lẽ đang đùa mọi người chơi?"),i("br"),i("br"),n._v("“Ta...” Chần chờ một chút, lão giả muốn nói cái gì, đột nhiên thân thể cứng đờ, ngừng lại, con mắt đóng chặt, không nói một lời."),i("br"),i("br"),n._v("“Cái tên này muốn làm gì?”"),i("br"),i("br"),n._v("“Không biết, đùa nghịch chúng ta chơi đây?”"),i("br"),i("br"),n._v("“Đáng giận!”"),i("br"),i("br"),n._v("Không nghĩ tới cái tên này muốn nói còn ngừng, bộ dáng như thế, mọi người tất cả đều mặt mũi tràn đầy tức giận."),i("br"),i("br"),n._v("Nếu không phải băn khoăn nơi này là phòng đấu giá, khẳng định đã sớm động thủ đem hắn đánh chết tươi."),i("br"),i("br"),n._v("“Ừm?”"),i("br"),i("br"),n._v("Người này đột nhiên cứng ngắc, không nói thêm gì nữa, Trương Huyền cũng cảm thấy kỳ quái, đang muốn lại dùng Minh Lý Chi Nhãn quan sát, đã cảm thấy trong đầu chấn động, một quyển sách xuất hiện."),i("br"),i("br"),n._v("“Hắn chẳng lẽ thi triển võ kỹ?”"),i("br"),i("br"),n._v("Nhịn không được sững sờ."),i("br"),i("br"),n._v("Thư viện xuất hiện thư tịch, chỉ có hai loại tình huống, hiện tại cái tên này khoảng cách xa như vậy, bỗng dưng toát ra, chỉ sợ là thầm thi triển một loại nào đó võ kỹ."),i("br"),i("br"),n._v("Nói được nửa câu, đột nhiên im miệng sử dụng võ kỹ... Đây cũng quá quỷ dị đi!"),i("br"),i("br"),n._v("“Xem trước một chút lại nói!”"),i("br"),i("br"),n._v("Trong lòng nghi hoặc, Trương Huyền biết, suy nghĩ nhiều hơn nữa cũng vô ích, lúc này gỡ xuống thư tịch, tiện tay lật ra."),i("br"),i("br"),n._v("Nhìn thoáng qua, lúc này mặt mũi tràn đầy cổ quái."),i("br"),i("br"),n._v("“Còn có loại tình huống này?”"),i("br"),i("br"),n._v("Con mắt nháy mấy lần, có chút không dám tin tưởng."),i("br"),i("br"),n._v("(Canh [3], vì minh chủ  tăng thêm, Canh [4] nhanh viết xong, một hồi liền có thể truyền lên, còn kém 14 phiếu đến nguyệt phiếu thứ ba, mọi người còn gì nữa không?)"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-476"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-478"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);