(window.webpackJsonp=window.webpackJsonp||[]).push([[1718],{2074:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-469-giup-nguoi-lay-ra"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-469-giup-nguoi-lay-ra"}},[n._v("#")]),n._v(" Chương 469: Giúp ngươi lấy ra")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Trương Huyền bên này sửng sốt, bên kia lão giả lần nữa khôi phục lại, vẻ mặt thoáng trắng bệch, nhìn quanh một vòng: “Chư vị muốn mua sắm thứ này mục đích, ta biết rất rõ ràng, nếu như ai có thể cho ra ta muốn bảo vật, không chỉ lấy được đầu này Vô Hồn Kim Nhân, sẽ còn dẫn hắn đi đào móc kim nhân vị trí, thăm dò vu hồn sư bí mật!”"),t("br"),t("br"),n._v("“Cái gì?”"),t("br"),t("br"),n._v("“Truyền thuyết, Vô Hồn Kim Nhân chỉ đi theo vu hồn sư trước mặt, một tấc cũng không rời, cho dù chết về sau, cũng sẽ chôn cùng, sẽ không rời khỏi... Đào móc chi địa, chẳng phải bày tỏ, là vu hồn sư mộ địa?”"),t("br"),t("br"),n._v("“Vu hồn truyền thừa, bị Danh Sư đường diệt tuyệt về sau, biến mất trên thế gian. Chúng ta không nói có thể được đến truyền thừa, coi như biết một chút liên quan tới loại nghề nghiệp này tin tức, cũng tất nhiên giá trị bản thân bạo tăng.”"),t("br"),t("br"),n._v("“Mấu chốt nhất là, Thượng Cổ vu hồn sư, liên quan linh hồn, mệnh danh có tiền nhất chức nghiệp, giám bảo sư tới so sánh đều kém xa, thật sự là loại người này mộ địa, tất nhiên lưu lại vô cùng tài phú.”"),t("br"),t("br"),n._v("“Đúng vậy a, thật có thể đi, liền đại biểu phát tài!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Vốn là đối lão giả tràn đầy ý kiến, hận không thể đem nó băm rất nhiều thế lực, nghe nói như thế, tất cả đều kích động lên."),t("br"),t("br"),n._v("Danh Sư đường, dạy bảo thiên hạ, hữu giáo vô loại, không phải vạn bất đắc dĩ, sẽ không đối với những khác chức nghiệp động thủ. Lúc trước vu hồn sư làm được quá tuyệt, mới đưa đến kết quả kia..."),t("br"),t("br"),n._v("Hậu nhân lấy được bộ phận truyền thừa, chỉ cần không vì không phải làm bậy, là sẽ không quản."),t("br"),t("br"),n._v("Hơn nữa, có thể đem tốt thêm lợi dụng, làm không cẩn thận sẽ còn lấy được bọn hắn che chở cùng tán dương."),t("br"),t("br"),n._v("Mặt khác, có thể đào ra hoàn chỉnh Vô Hồn Kim Nhân, nói rõ cái này chết đi vu hồn sư cực kỳ cường đại, thật tìm tới loại này mộ huyệt, coi như không chiếm được truyền thừa, lưu lại bảo vật, khẳng định cũng không phải số ít, đủ có thể khiến vô số người vì đó sôi trào cùng điên cuồng."),t("br"),t("br"),n._v("“Nhất định phải lấy được!”"),t("br"),t("br"),n._v("“Không sai, chỉ là... Nhiều như vậy bảo bối đều báo, nhân gia một chút hứng thú đều không có, cũng không thể đem gia tộc đều cho hắn đi!”"),t("br"),t("br"),n._v("“Gia tộc? Nhân gia cũng muốn nguyện ý tiếp thu mới được...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Hưng phấn qua đi, tất cả mọi người xoa mi tâm."),t("br"),t("br"),n._v("Cái tên này cũng không nói mình muốn cái gì, mọi người có thể xuất ra, cơ bản đều cầm, chẳng lẽ... Còn chưa đủ trân quý, không đạt tới yêu cầu?"),t("br"),t("br"),n._v("“Ta chỗ này có một gốc Thiên Diệp thụ mầm non, không biết ngươi có hứng thú hay không!”"),t("br"),t("br"),n._v("Mọi người ở đây cũng không biết nên báo gì gì đó thời điểm, một cái gian phòng vang lên thanh âm nhàn nhạt."),t("br"),t("br"),n._v("“Thiên Diệp thụ?”"),t("br"),t("br"),n._v("“Trong truyền thuyết cái chủng loại kia có thể thủ bảo hộ gia tộc, giúp người chiến đấu linh thụ?”"),t("br"),t("br"),n._v("“Thiên Diệp thụ, một thân ngàn nhánh, một nhánh ngàn lá. Hoàn toàn trưởng thành về sau, sẽ cùng linh binh đồng dạng, tự động hộ chủ, so Hóa Phàm tứ trọng Trọc Thanh cảnh cường giả, đều không hề yếu!”"),t("br"),t("br"),n._v("“Có thể được đến một gốc mầm non, trồng ở gia tộc, trăm năm về sau, tương đương với thêm ra một vị Hóa Phàm tứ trọng lão tổ, ngày đêm thủ hộ, tuyệt đối là thế lực lớn tranh đoạt tuyệt thế bảo vật!”"),t("br"),t("br"),n._v("“So cái gì linh binh, đan dược, công pháp bí tịch mạnh hơn nhiều lắm, cái này phòng đến cùng là ai, đồ tốt như vậy đều cam lòng lấy ra?”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tuổi thọ của con người có hạn, coi như lợi hại hơn nữa, sớm muộn cũng sẽ có vẫn lạc một ngày, một cái gia tộc, một cái thế lực, không có khả năng lấy được lâu dài bảo hộ."),t("br"),t("br"),n._v("Thiên Diệp thụ không giống, gieo xuống đi, trăm năm phía sau hiển uy lực, có thể sống mấy ngàn năm, hoàn toàn có thể che chở một cái gia tộc, ngàn năm không ngã."),t("br"),t("br"),n._v("Có thể nói, thứ này tới tay lời nói, chẳng khác nào nhiều một vị trấn thủ gia tộc lão tổ."),t("br"),t("br"),n._v("Làm sao không để cho người ta kích động?"),t("br"),t("br"),n._v("Dùng cái này đến đổi một đầu Vô Hồn Kim Nhân... Có thể nói, tuyệt đối là thua thiệt lớn."),t("br"),t("br"),n._v("“Cái này... Hắn lại không cách nào cự tuyệt đi!”"),t("br"),t("br"),n._v("Mọi người nhìn qua, muốn biết vị lão giả này đến cùng lựa chọn ra sao."),t("br"),t("br"),n._v("Vô Hồn Kim Nhân mặc dù trân quý, có thể cùng Thiên Diệp thụ so, kém không chỉ một đoạn."),t("br"),t("br"),n._v("Dùng trân quý đồ vật đổi tiện nghi... Có ngốc cũng biết như thế nào lấy hay bỏ."),t("br"),t("br"),n._v("“Thật không tiện, ta đối Thiên Diệp thụ không có bất kỳ cái gì hứng thú.”"),t("br"),t("br"),n._v("Mọi người ở đây cảm giác khẳng định danh hoa có chủ thời điểm, lão giả lần nữa lắc đầu."),t("br"),t("br"),n._v("“Không hứng thú?”"),t("br"),t("br"),n._v("“Trực tiếp cự tuyệt? Cái này... Nếu là ta, khẳng định trực tiếp đổi!”"),t("br"),t("br"),n._v("“Phung phí của trời ah...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không nghĩ tới liền Thiên Diệp thụ cũng không đánh động đối phương, tất cả mọi người cảm thấy có chút điên rồi."),t("br"),t("br"),n._v("Rốt cuộc muốn cái gì?"),t("br"),t("br"),n._v("“Ta vẫn là câu nói kia, chỉ cần chư vị xuất ra đồ vật, ta có hứng thú, tự nhiên sẽ đổi, cũng không phải là càng quý giá càng tốt!” Lão giả vẫy tay."),t("br"),t("br"),n._v("“Hứng thú?”"),t("br"),t("br"),n._v("Lần nữa nghe nói như thế, tất cả mọi người trầm mặc, trong nháy mắt, lại không có la giá."),t("br"),t("br"),n._v("Ngươi nói ngươi có hứng thú, có thể lại không nói cần gì, để chúng ta như thế báo, lúc nào là kích cỡ? Đấu giá hội thế nào trị tới như vậy một cái quái thai?"),t("br"),t("br"),n._v("“Lạc chấp sự, ta nhìn cái tên này, liền là tới quấy rối, hơn nữa lấy vật đổi vật, các ngươi cũng lấy không được chỗ tốt, phòng đấu giá vì sao phải thu?”"),t("br"),t("br"),n._v("Phòng đấu giá có trách nhiệm các loại bảo vật đưa ra, thu hồi kẻ giật dây, nhìn xem giữa đài Lạc Tần, Trần trưởng lão nhịn không được nói."),t("br"),t("br"),n._v("Rõ ràng liền là tới làm loạn đây!"),t("br"),t("br"),n._v("Nào có tới đấu giá, không nói mình muốn cái gì?"),t("br"),t("br"),n._v("Hơn nữa, bình thường đấu giá hối đoái linh thạch, phòng đấu giá có thể rút ra chia làm, lấy vật đổi vật, thế nào rút?"),t("br"),t("br"),n._v("“Hắn cũng không phải là làm loạn, hơn nữa đã đem thù lao thanh toán xong, trọn vẹn năm ngàn linh thạch!” Lạc Tần lắc đầu, nói."),t("br"),t("br"),n._v("“Còn không có đấu giá, liền đem thù lao thanh toán? Năm ngàn?” Trần trưởng lão sững sờ."),t("br"),t("br"),n._v("Nhiều linh thạch như vậy, hắn đều người đứng đầu không bỏ ra nổi đến, đối phương không những xuất ra, còn sớm thanh toán... Mảy may đều không lo lắng Vô Hồn Kim Nhân có thể hay không bán ra..."),t("br"),t("br"),n._v("“Đúng vậy a, cho nên, bất kể thế nào đấu giá, dùng cái gì thủ đoạn, đối phòng đấu giá tới nói, cũng sẽ không thua thiệt.” Lạc Tần nói."),t("br"),n._v("“Cái kia... Hắn đến cùng muốn cái gì?”"),t("br"),t("br"),n._v("Trần trưởng lão cũng kì quái."),t("br"),t("br"),n._v("Có thể tiện tay xuất ra năm ngàn linh thạch, nói rõ đối tiền không thiếu, vừa rồi nhiều như vậy bảo vật kêu giá, cơ hồ bao gồm từng cái phương diện, không có chút nào động tâm, thậm chí Thiên Diệp thụ đều không để ý... Rốt cuộc muốn cái gì?"),t("br"),t("br"),n._v("“Ta cũng không biết, hắn từ khi đi tới, tổng cộng nói với ta không đến mười câu, trong lòng nghĩ cái gì, ta làm sao biết!”"),t("br"),t("br"),n._v("Lạc Tần cũng cười khổ."),t("br"),t("br"),n._v("Mặc dù là đấu giá hội quản sự, Vô Hồn Kim Nhân đấu giá là hắn một tay an bài, có thể cùng cái tên này tiếp xúc ít đến thương cảm."),t("br"),t("br"),n._v("Cơ hồ không có gì giao lưu."),t("br"),t("br"),n._v("Về phần hắn suy nghĩ gì, cần gì, cũng hoàn toàn không biết gì cả."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Thiên Diệp thụ đều không cần, có ý tứ!”"),t("br"),t("br"),n._v("Số 23 phòng, thanh niên nhẹ nhàng nở nụ cười."),t("br"),t("br"),n._v("Thiên Diệp thụ đúng là hắn kêu đi ra, thứ này giá trị bao lớn, ba đầu Vô Hồn Kim Nhân đều rất khó so ra mà vượt, cũng chỉ có loại này tông môn tới thiên tài mới có thể nhẹ nhõm xuất ra."),t("br"),t("br"),n._v("Nhưng rất đáng tiếc, đối phương vẫn như cũ không hứng thú."),t("br"),t("br"),n._v("“Thiếu gia, xem ra hắn cũng không phải là trữ hàng đầu cơ tích trữ, muốn bán ra giá cao.” Hộ vệ lão giả nói."),t("br"),t("br"),n._v("Trước đó thiếu gia nói, cái tên này cố ý không nói ra mình muốn cái gì, là muốn làm cái giá cao, hiện tại xem ra, cũng không phải là như thế."),t("br"),t("br"),n._v("Muốn nói giá cả cao, thứ gì có thể so sánh được Thiên Diệp thụ?"),t("br"),t("br"),n._v("Liền thứ này đều không cần, nói rõ hắn mục đích thực sự, khả năng thật là vì tìm tới thứ cần thiết, mà không phải giá cả càng cao càng tốt."),t("br"),t("br"),n._v("“Quản hắn cần gì, thứ này hắn khẳng định không cách nào cự tuyệt!”"),t("br"),t("br"),n._v("Cổ tay khẽ đảo, thanh niên lòng bàn tay thêm ra một cái ngọc bài."),t("br"),t("br"),n._v("Nhìn thấy thứ này, lão giả chấn động, tràn đầy không thể tưởng tượng nổi: “Thiếu gia, thứ này vô cùng trân quý, ngươi... Muốn khẳng định muốn dùng?”"),t("br"),t("br"),n._v("“Không sai!”"),t("br"),t("br"),n._v("Mắt sáng lên, thanh niên mỉm cười: “Liền Thiên Diệp thụ đều không cần, ta thực sự nghĩ không ra, có thể có cái gì, có thể gây nên hứng thú của hắn.”"),t("br"),t("br"),n._v("“Nhưng... Thứ này đối thiếu gia cũng hữu dụng...” Lão giả chần chờ."),t("br"),t("br"),n._v("“Ta có khó khăn gì, có thể trực tiếp hỏi lão sư, thứ này, có cũng được mà không có cũng không sao, râu ria!”"),t("br"),t("br"),n._v("Thanh niên vẫy tay: “Kêu giá đi!”"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Thấy hắn kiên quyết, lão giả không nói thêm lời, gật gật đầu tiến về phía trước một bước, đi tới cửa sổ trước mặt: “Nửa bước ngũ tinh danh sư, viễn trình chương trình học ngọc phù một viên!”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Vốn là toàn bộ phòng đấu giá, đều tại phỏng đoán cái tên này muốn cái gì, một hồi ngột ngạt, nghe được thanh âm, sửng sốt một chút, lập tức nổ tung."),t("br"),t("br"),n._v("“Nửa bước ngũ tinh danh sư? Có ý tứ gì?”"),t("br"),t("br"),n._v("“Danh sư đạt tới tứ tinh trở lên, muốn tấn cấp, độ khó sẽ gấp bội gia tăng, có rất nhiều người vây ở tứ tinh đỉnh phong, từ đầu đến cuối không cách nào đột phá. Nương theo thời gian xa xưa, chỉ điểm năng lực vượt qua bình thường tứ tinh đỉnh phong, có thể lại không đạt tới ngũ tinh... Liền diễn sinh cấp bậc này!”"),t("br"),t("br"),n._v("“Nửa bước ngũ tinh, toàn bộ vạn quốc liên minh đều không có một vị, loại này cấp bậc danh sư chương trình học ngọc phù, mặc dù cùng là tứ tinh, nhưng so bình thường tứ tinh giá trị cao nhiều lắm... Không nói cái khác, liền nói vừa rồi cái này phòng báo ra 21 viên, chỉ sợ cũng không sánh nổi cái này một cái!”"),t("br"),t("br"),n._v("“Nửa bước ngũ tinh danh sư, so phổ thông tứ tinh đỉnh phong cao minh không ít, cái này có thể đoán được, viễn trình chương trình học ngọc phù là cái gì?”"),t("br"),t("br"),n._v("“Bình thường chương trình học ngọc phù, chỉ cần lấy được, có thể trực tiếp đi tìm vị danh sư này, yêu cầu một canh giờ một đối một giảng bài. Có thể... Muốn tìm tới vị danh sư này, chỉ sợ cũng phải hao phí không ít thời gian, mà viễn trình chương trình học ngọc phù liền không cần. Ngọc phù bên trong tồn tại danh sư một tia tàn hồn, máu tươi nhỏ vào, có thể trực tiếp đi vào giả lập lớp học, ở trước mặt giải quyết vấn đề, tiến hành giảng bài.”"),t("br"),t("br"),n._v("“Nói cách khác, chỉ cần lấy được thứ này, chẳng khác nào bên người tùy thời có một vị nửa bước ngũ tinh danh sư, có cái gì trong vấn đề tu luyện, tùy thời trưng cầu ý kiến, mặc dù chỉ là cái duy nhất một lần vật phẩm, có thể giá trị bao lớn, không thể ước lượng!”"),t("br"),t("br"),n._v("Tùy thời có thể lấy tiếp nhận một cái nửa bước ngũ tinh danh sư chỉ điểm, cùng cầm ngọc phù khắp bầu trời tìm lung tung, rõ ràng là hai khái niệm."),t("br"),t("br"),n._v("Cái này một viên ngọc phù, giá trị bao lớn, vượt qua trước đó 21 viên liên hợp."),t("br"),t("br"),n._v("Hiểu rõ những này, tất cả mọi người đồng loạt nhìn về phía trên đài lão giả, xem hắn đến cùng lựa chọn ra sao."),t("br"),t("br"),n._v("Nếu như thứ này lại cự tuyệt, những người khác thật sự không biết nên lấy cái gì bảo vật."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Lão giả rõ ràng cũng không nghĩ tới cái này phòng gia hỏa, có thể xuất ra quý giá như vậy vật phẩm, mặt mũi tràn đầy chần chờ, một lát sau một cắn răng: “Tốt, ta đáp ứng trao đổi!”"),t("br"),t("br"),n._v("“Trao đổi?”"),t("br"),t("br"),n._v("“Ta cảm thấy hắn cũng khẳng định sẽ đồng ý...”"),t("br"),t("br"),n._v("Thấy hắn đáp ứng, mọi người mặc dù thất lạc, nhưng cũng biết quả ngọc phù này giá trị quá cao, bất kỳ người nào đều khó có khả năng cự tuyệt."),t("br"),t("br"),n._v("“Cái này còn tạm được...”"),t("br"),t("br"),n._v("Số 23 phòng thanh niên, nghe hắn đồng ý, nhẹ nhàng nở nụ cười."),t("br"),t("br"),n._v("Cái đồ chơi này đều lấy ra, không đáp ứng nữa, thật gọi gặp quỷ."),t("br"),t("br"),n._v("Đang định an bài lão giả giao tiếp Vô Hồn Kim Nhân, đột nhiên số một phòng phương hướng, một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("“Trước đừng có gấp đáp ứng, ta còn chưa báo giá!”"),t("br"),t("br"),n._v("“Ngươi? Giá tiền của ngươi chẳng lẽ có thể cao hơn ta chương trình học ngọc phù?” Thanh niên đứng dậy, mặt mũi tràn đầy không vui."),t("br"),t("br"),n._v("“Giá cả có cao hay không ta không biết, bởi vì ta báo giá, chỉ có một câu...”"),t("br"),t("br"),n._v("Thanh âm thản nhiên: “Ta có thể giúp ngươi... Lấy ra!”"),t("br"),t("br"),n._v("“Ngươi nói cái gì?”"),t("br"),t("br"),n._v("Bàn đấu giá ở giữa lão giả, vốn là không thèm để ý chút nào thần sắc đột nhiên cứng ngắc, sắc mặt trắng nhợt, không ngừng run rẩy."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-477"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-479"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);