(window.webpackJsonp=window.webpackJsonp||[]).push([[172],{525:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1131-than-thuc-canh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1131-than-thuc-canh"}},[n._v("#")]),n._v(" Chương 1131: Thần Thức cảnh!")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1131: Thần Thức cảnh!"),t("br"),t("br"),n._v("Thực lực của hắn bây giờ, tại Thanh Nguyên đế quốc cứ việc không tính là gì, nhưng ở cùng cấp bậc bên trên, nắm giữ tuyệt đối tự tin."),t("br"),t("br"),n._v("Mặc kệ đối phương là ai, loại trừ phân thân cái kia đại biến thái, chỉ cần tu vi không cao hơn quá nhiều, tuyệt đối có thể đánh kêu cha gọi mẹ, không dám tiếp tục đến tìm phiền phức."),t("br"),t("br"),n._v("Chiến sư đường cũng dám kiếm chuyện... Lá gan thật là lớn!"),t("br"),t("br"),n._v("Có ta Trương Huyền tại, quản ngươi yêu ma quỷ quái, quản ngươi tôm tép nhãi nhép, hết thảy không đỡ nổi một đòn!"),t("br"),t("br"),n._v("“Có Tôn sư hứa hẹn, ta an tâm...” Nghe hắn nói như vậy, Hình đường chủ thở phào nhẹ nhõm, trên mặt tươi cười."),t("br"),t("br"),n._v("Trước mắt vị này, nhờ vào Thánh vực nhất trọng đỉnh phong, liền có thể tránh né ba người bọn họ liên tục tiến công, sức chiến đấu mạnh, không thể nghi ngờ, vị kia Trương sư, cho dù mạnh hơn, muốn vượt qua, cũng chỉ sợ không dễ dàng như vậy."),t("br"),t("br"),n._v("“Bất quá, Tôn sư ngươi vẫn là muốn cẩn thận, ta nói cho ngươi vị này đại phiền toái, cùng cấp bậc cũng là vô địch tồn tại, mặc dù không thấy tận mắt thực lực của hắn, nhưng nghe qua không ít tin đồn, so với ngươi chỉ sợ đều không hề yếu!”"),t("br"),t("br"),n._v("Chần chờ một chút, Hình đường chủ lần nữa dặn dò."),t("br"),t("br"),n._v("Cứ việc đối trước mắt vị này nắm giữ tuyệt đối tự tin, nhưng đối phương cũng không phải đèn đã cạn dầu, vẫn là cẩn thận một chút cho thỏa đáng."),t("br"),t("br"),n._v("“Không thể so với ta yếu?” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Lấy lực chiến đấu của hắn, cùng cấp bậc cứ việc có thể thắng được không nhiều, thế nhưng không có nghĩa là không có!"),t("br"),t("br"),n._v("Thật giống như phân thân... Vô luận như thế nào cùng cấp bậc, đều sẽ bị nhẹ nhõm nghiền ép, suy nghĩ một chút đều cảm thấy tâm tắc."),t("br"),t("br"),n._v("Đã có thể để cho Hình đường chủ thận trọng như thế, nói rõ thực lực của người kia, khẳng định cũng không đơn giản, thật có thể cũng giống như mình, cũng coi như tìm được đối thủ."),t("br"),t("br"),n._v("“Đúng vậy a, chúng ta chưa thấy qua bản thân hắn, nhưng hắn học sinh sức chiến đấu, liền kinh khủng đến cực điểm, ta cùng cấp bậc cũng không là đối thủ... Cho nên, Tôn sư tuyệt đối không nên khinh địch...”"),t("br"),t("br"),n._v("“Đối phương học sinh, ngươi đều chiến thắng không qua?” Trương Huyền hoảng sợ, trong lòng sinh ra bội phục chi ý."),t("br"),t("br"),n._v("Còn tưởng rằng, trên đời này, chỉ có chính hắn, giảng dạy học sinh, lợi hại như vậy, không nghĩ tới sơn ngoại hữu sơn, người bên trên có người."),t("br"),t("br"),n._v("“Như ngươi vậy nói chuyện, ta đối cái này đại phiền toái, thì càng có hứng thú...” Hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Có thể cùng như vậy siêu cấp danh sư, quyết đấu một lần, đối với hắn ý niệm tất nhiên cũng có rất lớn tăng lên."),t("br"),t("br"),n._v("“Như vậy đi, Tôn sư, chúng ta cùng đối phương hẹn một chút, qua mấy ngày mới tiến hành quyết đấu. Thừa dịp trong khoảng thời gian này, ngươi tại Chiến sư thập điện bên trong tự do tu luyện, thuận tiện cường hóa một chút thực lực của mình! Thực không dám giấu giếm, chúng ta quyền pháp điện, chưởng pháp điện, thân pháp điện... Đều có hoàn chỉnh truyền thừa, đối một người thực lực tấn thăng, có trợ giúp thật lớn!”"),t("br"),t("br"),n._v("Hình đường chủ nói tiếp."),t("br"),t("br"),n._v("Vị này thực lực, đã rất mạnh mẽ, nhưng hắn tu luyện Đại Tinh La Chỉ Lực nhanh như vậy, tu luyện cái khác, hẳn là cũng sẽ không quá chậm! Mấy ngày ngắn ngủi thời gian, cho dù không cách nào tiến bộ quá lớn, khẳng định cũng sẽ có điều trợ giúp, để phần thắng lớn hơn một chút."),t("br"),t("br"),n._v("“Tốt!” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Hắn chưởng pháp, quyền pháp, thân pháp loại hình, vừa vặn thiếu khuyết cấp bậc cao hơn võ kỹ bổ sung, đã có thể học tập, tự nhiên không thể lãng phí cơ hội."),t("br"),t("br"),n._v("“Đây là đại biểu thân phận ta lệnh bài, chỉ cần cầm, Chiến sư thập điện khảo hạch thông đạo, có thể tùy tiện đi, Tàng Thư khố có thể tùy tiện nhìn!”"),t("br"),t("br"),n._v("Thấy trước mắt vị này, thực lực như thế, cũng không có bành trướng, Hình đường chủ thở phào nhẹ nhõm, lấy ra một cái lệnh bài."),t("br"),t("br"),n._v("Trương Huyền tiện tay nhận lấy."),t("br"),t("br"),n._v("Tiếp lấy lần nữa cổ tay khẽ đảo, lấy ra một cái hẹp dài hộp ngọc: “Đây là năm cái tinh nguyên linh thạch thượng phẩm, cũng trước cho Tôn sư, hy vọng có thể trong khoảng thời gian ngắn thực lực tiến thêm một bước, như vậy chúng ta nắm chắc cũng lớn hơn một chút!”"),t("br"),t("br"),n._v("“Đa tạ...”"),t("br"),t("br"),n._v("Không nghĩ tới, bản thân chỉ là đáp ứng, còn chưa có đi đối phó cái kia đại phiền toái, đối phương liền đem linh thạch cho, Trương Huyền tràn đầy cảm kích."),t("br"),t("br"),n._v("Đối phương như vậy tín nhiệm, vậy mình, nhất định phải trợ giúp đối phương, giải quyết triệt để phiền phức, kém cỏi nhất cũng muốn đem tên kia đánh sinh hoạt không thể tự gánh vác!"),t("br"),t("br"),n._v("Cho hắn biết, Chiến sư đường, không phải dễ khi dễ."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Yên tĩnh ngồi tại trong tĩnh thất, Trương Huyền điều chỉnh tốt trạng thái, lúc này mới đem hẹp dài hộp ngọc lấy ra."),t("br"),t("br"),n._v("Đáp ứng Hình đường chủ về sau, hắn liền hướng đối phương muốn cái tĩnh thất."),t("br"),t("br"),n._v("Tinh nguyên linh thạch thượng phẩm tới tay, cái kia chuẩn bị đột phá!"),t("br"),t("br"),n._v("Ngón tay nhẹ nhàng vạch một cái, hộp ngọc bên trên phong ấn mở ra, một đạo tinh thuần linh khí lập tức phát ra."),t("br"),t("br"),n._v("Trong hộp ngọc song song bày đặt năm khối linh thạch, mỗi một cái đều óng ánh long lanh, giống như thủy tinh, tinh thuần linh khí, ở phía trên hình thành từng đầu tựa như tiểu long bộ dáng khí lưu, xuyên tới xuyên lui, tựa như bất cứ lúc nào cũng sẽ bay ra ngoài, bay lượn bầu trời."),t("br"),t("br"),n._v("“Linh khí quả nhiên so với Thượng phẩm linh thạch tinh thuần thật nhiều...”"),t("br"),t("br"),n._v("Nhìn một cái, Trương Huyền mắt sáng rực lên."),t("br"),t("br"),n._v("Căn cứ quan sát của hắn, cái này tinh nguyên linh thạch thượng phẩm tinh thuần lần hẳn là bình thường linh thạch gấp ba, hẳn là có thể nhẹ nhõm để cho mình tu vi phá tan Thánh vực nhất trọng quan ải."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Lấy ra một cái linh thạch, giữ tại lòng bàn tay, chậm rãi nhắm mắt lại."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Tinh thuần linh khí, lập tức dọc theo huyệt đạo tràn vào thân thể, biến thành Thiên Đạo chân khí, chậm rãi chảy xuôi."),t("br"),t("br"),n._v("Trước đó sử dụng bình thường linh thạch thượng phẩm, bởi vì linh khí độ tinh thuần không đủ, tám chín phần mười đều lãng phí, mà sử dụng cái này, hầu như trăm phần trăm đều có thể hấp thu, sử dụng dẫn đầu gia tăng thật lớn."),t("br"),t("br"),n._v("Bởi vậy, cho dù chỉ là một cái linh thạch, đối với hắn tác dụng, cũng vượt qua mấy ngàn bình thường."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Thời gian nháy mắt, linh khí đi tới tròn nguyên thai vị trí, trước đó có mấy lần xung kích kinh nghiệm, không có bất kỳ cái gì không thạo, tinh thần khẽ động, hồng lưu giống như khí tức liền lao qua."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Chắc chắn nguyên thai, tại khí lưu trùng kích vào, từ từ mở ra, có điều, chỉ mở ra một tia, thật giống như linh khí không đủ, lập tức liền muốn dừng lại."),t("br"),t("br"),n._v("“Tiếp tục!”"),t("br"),n._v(" Biết xung kích cửa ải, không thể có mảy may do dự, Trương Huyền không có ngừng lại, lần nữa cầm lấy một cái tinh nguyên linh thạch thượng phẩm, toàn lực hấp thu trong đó lực lượng."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Không biết qua bao lâu, chắc chắn nguyên thai, rốt cục hoàn toàn mở ra, một cái tựa như con mắt đồng dạng đồ vật, vắt ngang trong đó, chiếu rọi khắp nơi."),t("br"),t("br"),n._v("Ngay sau đó Trương Huyền cảm thấy ý thức của mình, nương theo thai mắt mở ra, một chút nhún nhảy mà ra, hướng bốn phía lan tràn mà ra, thời gian nháy mắt, bốn phía tình cảnh thanh tân xuất hiện ở trước mắt, chút xíu tất hiện."),t("br"),t("br"),n._v("Trước đó, chỉ có mở ra Minh Lý Chi Nhãn mới có thể nhìn thấy đồ vật, hiện tại nhẹ nhõm có thể trông thấy, xa xa sách, cho dù không cần mở ra, cũng có thể từng tờ một nhìn thấy nội dung trong đó..."),t("br"),t("br"),n._v("“Đây chính là thần thức? Quả nhiên mạnh mẽ...”"),t("br"),t("br"),n._v("Kích động vẻ mặt đỏ lên."),t("br"),t("br"),n._v("Trước kia, mượn nhờ Vu hồn năng lực, linh hồn của hắn cũng có thể liếc nhìn, có điều, chỉ là không rõ ràng, mà bây giờ, thật giống như lắp đặt kính lúp, lại nhỏ xíu đồ vật, cũng có thể rõ rõ ràng ràng."),t("br"),t("br"),n._v("Khó trách Thần Thức cảnh, chân khí, lực lượng cũng không tăng trưởng, sức chiến đấu lại tăng cường... Thời điểm chiến đấu, thần thức tản ra, hết thảy chung quanh đều ở đáy mắt, đối phương bất luận cái gì chiêu số, đều có thể nhìn rõ rõ ràng ràng, thực lực tự nhiên cũng sẽ tùy theo gia tăng."),t("br"),t("br"),n._v("“Nếu như trước đó liền nắm giữ thần thức, bị Hình đường chủ đám người vây công, liền không đến mức bị động như vậy...”"),t("br"),t("br"),n._v("Một ngụm trọc khí phun ra."),t("br"),t("br"),n._v("Trước đó bị Hình đường chủ ba người vây công, hắn mặc dù may mắn tránh thoát, nhưng cũng chật vật không chịu nổi, nguyên nhân chủ yếu nhất, liền là đối cảnh vật chung quanh thiếu khuyết một cái thống nhất nắm chắc, mà bây giờ thần thức sinh ra, gặp lại loại tình huống kia, tuyệt đối có thể trước thời hạn ứng đối, không đến mức luống cuống tay chân."),t("br"),t("br"),n._v("“Tiếp tục!”"),t("br"),t("br"),n._v("Thành công đột phá, trong lòng thở phào nhẹ nhõm, Trương Huyền tiếp tục cầm lấy một cái tinh nguyên linh thạch thượng phẩm, vận chuyển chân khí."),t("br"),t("br"),n._v("Thần Thức cảnh sơ kỳ!"),t("br"),t("br"),n._v("Thần Thức cảnh trung kỳ!"),t("br"),t("br"),n._v("Thần Thức cảnh hậu kỳ!"),t("br"),t("br"),n._v("Thần Thức cảnh đỉnh phong!"),t("br"),t("br"),n._v("Sau nửa canh giờ, tu vi của hắn liền đạt đến Thánh vực nhị trọng đỉnh phong, có điều, năm cái tinh nguyên linh thạch thượng phẩm cũng hoàn toàn tiêu hao hầu như không còn, biến thành bột màu trắng."),t("br"),t("br"),n._v("“Nhìn một chút thần thức có thể lan tràn bao xa!”"),t("br"),t("br"),n._v("Thở ra một hơi, Trương Huyền ánh mắt ngưng trọng."),t("br"),t("br"),n._v("Căn cứ sách bên trên ghi chép, tu luyện công pháp mạnh yếu, thần thức lan tràn khoảng cách cũng là khác biệt, bình thường Thần Thức cảnh đỉnh phong, thần thức đại khái có thể lan tràn 400 mét trái phải."),t("br"),t("br"),n._v("Công pháp cường đại, cũng liền 500, 600 mét, hầu như không có người có thể vượt qua một ngàn mét."),t("br"),t("br"),n._v("Hắn tu luyện Phong Thánh giải, lại tu luyện Thiên Đạo công pháp, muốn nhìn một chút, đến cùng có thể lan tràn bao xa."),t("br"),t("br"),n._v("Hô hô!"),t("br"),t("br"),n._v("Thần thức rời khỏi thân thể, hướng tĩnh thất bên ngoài lan tràn mà đi."),t("br"),t("br"),n._v("“400, 500...”"),t("br"),t("br"),n._v("Trong lòng mặc niệm, thời gian nháy mắt liền lan tràn đến năm trăm mét có hơn."),t("br"),t("br"),n._v("“1000 m, 2000 mét...”"),t("br"),t("br"),n._v("Vốn cho rằng một ngàn mét cũng là hắn cực hạn, không nghĩ tới cãi nhau về sau, tiếp tục lan tràn, rốt cục tại 2 500 mét trái phải vị trí ngừng lại."),t("br"),t("br"),n._v("“Hai ngàn năm trăm mét, bình thường tu luyện giả gấp sáu lần còn nhiều hơn...”"),t("br"),t("br"),n._v("Thu hồi thần thức, Trương Huyền hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Đã sớm biết Thiên Đạo công pháp rất mạnh, thần thức tất nhiên không yếu, không nghĩ tới đã cường đại đến loại tình trạng này."),t("br"),t("br"),n._v("Như vậy thần thức phạm vi, cho dù có người muốn giết bản thân, cũng có thể tuỳ tiện phát hiện, không đến mức giống như kiểu trước đây bất đắc dĩ."),t("br"),t("br"),n._v("“Thần thức vượt qua người bình thường gấp sáu lần, hơn nữa chính xác mức độ càng hơn... Chuyện này không thể cùng người khác nói, nếu không, phiền phức không ít!”"),t("br"),t("br"),n._v("Thở ra một hơi."),t("br"),t("br"),n._v("Chuyện này quá mức nghịch thiên, nói ra, đoán chừng rất nhiều người sẽ bị tại chỗ hù chết."),t("br"),t("br"),n._v("Vẫn là muốn khiêm tốn một chút cho thỏa đáng."),t("br"),t("br"),n._v("“Chiến sư đường không hổ là Thanh Nguyên thành thế lực cường đại nhất một trong...”"),t("br"),t("br"),n._v("Vừa rồi thần thức mặc dù chỉ là quét một chút, lại phát hiện toàn bộ Chiến sư đường, bố trí vô số trận pháp cường đại, một khi xúc động, Nguyên Thần cảnh cường giả đều sẽ bị tuỳ tiện gạt bỏ."),t("br"),t("br"),n._v("May mắn, bản thân đi tới Chiến sư đường về sau, để Nhược Hoan đám người ước thúc Huyền Huyền hội người, không có làm loạn, nếu không, khẳng định phiền phức không nhỏ."),t("br"),t("br"),n._v("“Đột phá Thần Thức cảnh, hiện tại cho dù gặp được Nguyên Thần cảnh cường giả, cũng có thể đánh một trận...”"),t("br"),t("br"),n._v("Thần thức đột phá, cứ việc lực lượng không gia tăng, sức chiến đấu lại tăng lên rất nhiều, trước đó gặp được Nguyên Thần cảnh cái trước, trừ phi đánh lén hoặc là cận thân, nếu không rất khó vượt qua, mà bây giờ, cho dù công bằng chiến đấu, đối phương muốn vượt qua hắn, cũng không dễ dàng như vậy."),t("br"),t("br"),n._v("Đương nhiên gặp được Hình đường chủ loại này Xuất Khiếu cảnh cường giả, vẫn có thể trốn bao xa trốn bao xa."),t("br"),t("br"),n._v("“Tu luyện một cái cấp bậc, dùng năm cái linh thạch thượng phẩm, tiếp tục hướng xuống tu luyện, khẳng định tiêu hao càng nhiều... Nhìn tới lại nếu muốn phương pháp kiếm tiền...”"),t("br"),t("br"),n._v("Nhìn xem trên mặt đất rải rác linh thạch cặn bã, Trương Huyền xoa xoa mi tâm, có chút bất đắc dĩ."),t("br"),t("br"),n._v("Chém giết Thanh Điền thập đại vương giả, mới vừa cảm thấy đã là cái to lớn phú ông, của cải kinh người, không nghĩ tới vừa tu luyện, lại trở thành kẻ nghèo hèn."),t("br"),t("br"),n._v("Nhất định phải nhanh nghĩ biện pháp, tìm kiếm càng nhiều tinh nguyên linh thạch thượng phẩm, mới có thể tốt hơn tiến bộ."),t("br"),t("br"),n._v("“Cái này qua mấy ngày lại nói, kế trước mắt là giúp Hình đường chủ xử lý tốt đại phiền toái... Đi quyền pháp điện, chưởng pháp điện, thân pháp điện nhìn một chút...”"),t("br"),t("br"),n._v("Đối phương như vậy đợi hắn, đương nhiên muốn giúp đối phương giải quyết vấn đề lại đi làm mình sự tình."),t("br"),t("br"),n._v("Hiểu điểm ấy, Trương Huyền đi ra tĩnh thất, hướng quyền pháp điện đi tới."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1139"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1141"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);