(window.webpackJsonp=window.webpackJsonp||[]).push([[1720],{2075:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-39-han-duong-mau-thao"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-39-han-duong-mau-thao"}},[n._v("#")]),n._v(" Chương 39: Hàn Dương Mẫu Thảo")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Đúng rồi, ngươi nơi này có Hàn Dương Thảo không?” "),t("br"),t("br"),n._v(" Lắc đầu một cái đem trong đầu suy nghĩ lung tung dứt bỏ, Trương Huyền hỏi."),t("br"),t("br"),n._v(" Cái này quầy hàng không riêng bán một ít tạp vật, còn có một chút dược liệu."),t("br"),t("br"),n._v(" Ở Tàng Thư Các xem xong tàng thư, hắn đã biết rồi Triệu Nhã vấn đề phương pháp giải quyết, Thuần Âm thân thể, nên tu luyện ôn hòa trung tính công pháp, như vậy liền có thể giải quyết âm khí chồng chất vấn đề!"),t("br"),t("br"),n._v(" Bất quá, Thuần Âm công pháp nàng đã tu luyện lâu như vậy rồi, trong cơ thể kinh mạch dĩ nhiên thích ứng, đột nhiên thay đổi, làm không cẩn thận sẽ bị thương nặng, tu vi giảm lớn, vì phòng ngừa loại hiện tượng này, còn cần sớm làm chuẩn bị!"),t("br"),t("br"),n._v(" Một người trong đó, chính là dùng dược liệu ôn dưỡng kinh mạch!"),t("br"),t("br"),n._v(" Hàn Dương Thảo, âm hàn chỗ sinh trưởng, rồi lại yêu thích ánh mặt trời, ẩn chứa trong đó linh khí, nóng lạnh xung đột lẫn nhau, âm dương giao hòa, chính là ôn dưỡng nàng kinh mạch không có chỗ thứ hai, nếu đến thương trường, vừa vặn hỏi dò một hồi."),t("br"),t("br"),n._v(" “Hàn Dương Thảo? Ngươi thực sự là hỏi đúng dịp, không phải khoác lác, toàn bộ Thiên Vũ Thương Thành, ta dám cam đoan, chỉ ta một nhà có vật này, là ta mấy ngày trước đi Thiên Huyền rừng rậm, ở một chỗ hiểm địa hái, tổng cộng mười cây, 100 kim tệ một cây, ngươi muốn mấy cái?” "),t("br"),t("br"),n._v(" Chủ sạp nở nụ cười, xoay người lấy ra một cái rương, mở ra xem, bên trong nằm ngang mấy cây tươi đẹp thực vật."),t("br"),t("br"),n._v(" Màu đỏ rễ cây, lá cây, trắng như tuyết đóa hoa."),t("br"),t("br"),n._v(" “Ta xem trước một chút!” "),t("br"),t("br"),n._v(" Trương Huyền chỉ là từ thư tịch biết Hàn Dương Thảo, trên thực tế cũng không nhận ra, đưa tay lấy ra một cây, đặt ở lòng bàn tay."),t("br"),t("br"),n._v(" Vù!"),t("br"),t("br"),n._v(" Thư viện chấn động, một quyển sách xuất hiện."),t("br"),t("br"),n._v(" “Hàn Dương Thảo, Thiên Huyền rừng rậm 200 km Vô Danh bên trong thung lũng hái, hai năm dược linh... Khuyết điểm...” "),t("br"),t("br"),n._v(" Trên sách viết dược liệu danh xưng, nơi sản xuất cùng đủ loại khuyết điểm."),t("br"),t("br"),n._v(" “Là Hàn Dương Thảo, cái tên này không gạt ta!” "),t("br"),t("br"),n._v(" Thấy thư tịch trên xuất hiện tên không có sai lầm, Trương Huyền biết đối phương không lừa hắn, đây là sự thực dược liệu, gật gật đầu, đang muốn nói chuyện, đột nhiên con mắt rơi cái rương tận cùng bên trong."),t("br"),t("br"),n._v(" “Căn này Hàn Dương Thảo thế nào thấy có chút khô vàng? Không biết ngươi hái phương pháp không đúng, giết chết đi!” "),t("br"),t("br"),n._v(" Hái dược liệu cần chính xác thủ pháp, phương pháp sai rồi, rất khó bảo vệ dược hiệu, giá cả cũng sẽ mất giá rất nhiều."),t("br"),t("br"),n._v(" Hắn nói căn này Hàn Dương Thảo, khô vàng nhỏ gầy, xem ra cùng không thành thục như thế, hơn nữa không có một chút nào tươi đẹp vẻ, ỉu xìu đầu đạp não, nhìn dáng dấp bất cứ lúc nào cũng sẽ khô héo."),t("br"),t("br"),n._v(" “Vị công tử này nói giỡn, ta liền lấy cái này ăn cơm, làm sao có khả năng tính sai! Bụi dược liệu này bản thân liền không tốt lắm, ta hái thời điểm cứ như vậy, nếu như ngươi muốn, căn này cho ngươi nửa giá, 50 kim tệ liền có thể lấy đi!” "),t("br"),t("br"),n._v(" Chủ sạp nói."),t("br"),t("br"),n._v(" “Ừm!” "),t("br"),t("br"),n._v(" Trương Huyền sau đó đem căn này khô vàng Hàn Dương Thảo cầm lấy."),t("br"),t("br"),n._v(" Vù!"),t("br"),t("br"),n._v(" “Hàn Dương Mẫu Thảo, cái khác Hàn Dương Thảo căn bản, Thiên Huyền rừng rậm 200 km Vô Danh bên trong thung lũng hái, mười lăm năm dược linh...” Mở sách tịch, trên đó viết tên gọi cùng ghi chép."),t("br"),t("br"),n._v(" “Hàn Dương Mẫu Thảo?” Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v(" Vật này hắn từng ở thư tịch trên từng thấy, mười phần quý giá, có thể gặp không thể cầu, là Hàn Dương Thảo căn bản, lấy vật này làm thuốc, hiệu quả ít nhất là người sau hơn gấp mười lần!"),t("br"),t("br"),n._v("  Không nghĩ tới tùy tiện mua Hàn Dương Thảo, lại đụng tới vật này, hơn nữa còn là 15 năm dược linh, đơn loại này, liền có giá trị không nhỏ!"),t("br"),t("br"),n._v(" Triệu Nhã nếu như dùng Hàn Dương Thảo ôn dưỡng kinh mạch, mặt sau còn cần không ít thủ đoạn, tiếp tục duy trì, mới có thể làm cho thuận lợi đổi công pháp, nhưng có này cây Hàn Dương Mẫu Thảo, là có thể ung dung giải quyết vấn đề, thủ đoạn khác không cần cũng sẽ không có quá to lớn ảnh hưởng!"),t("br"),t("br"),n._v(" Tuyệt đối là thích hợp nhất Triệu Nhã thuốc, không có cái thứ hai!"),t("br"),n._v("  “Đem này cây cho ta bao tiến lên!” "),t("br"),t("br"),n._v(" Trong lòng kích động, mặt cũng không lộ vẻ gì khác thường, tiện tay đưa cho chủ sạp."),t("br"),t("br"),n._v(" Nếu như cho đối phương biết đây là cây mẹ cỏ, mà không phải một cây cỏ úa, e sợ giá cả sẽ lập tức vượt lên gấp mười lần, hơn nữa còn chưa chắc chắn mua được!"),t("br"),t("br"),n._v(" Là một người tiếp thụ qua hiện đại văn minh “thanh niên bốn có”, có thể kiếm lời tiện nghi, đương nhiên sẽ không lỗ."),t("br"),t("br"),n._v(" “50 kim tệ!” "),t("br"),t("br"),n._v(" Chủ sạp nhìn người thanh niên này quần áo ngăn nắp,"),t("br"),t("br"),n._v(" Vốn tưởng rằng xảy ra giá cao mua lấy vài cây, không nghĩ tới chỉ mua một cây bệnh, bĩu môi, tiện tay bao bên trên, lại không vừa nãy nhiệt tình."),t("br"),t("br"),n._v(" Bán đồ cũng phải nhìn người, trước mắt vị này, liền mua dược liệu đều không nỡ lòng bỏ mua xong, khẳng định là quỷ nghèo, nói nhiều, chỉ là lãng phí thời gian."),t("br"),t("br"),n._v(" “Hảo!” "),t("br"),t("br"),n._v(" Tiếp nhận dược liệu, Trương Huyền hướng về túi áo sờ mó, sắc mặt lập tức lúng túng."),t("br"),t("br"),n._v(" Vừa nãy một mực đang nghĩ thư viện sự tình, đem tiền sự tình đã quên... Tiền thân là cái cấp thấp giáo sư, tiền lương vốn là thấp, bởi vì thầy giáo sát hạch được không điểm, lại khấu trừ đại lượng tiền tài... Nói thật, hắn trong túi tiền liền mười viên kim tệ đều không bỏ ra nổi đến, đừng nói 50!"),t("br"),t("br"),n._v(" “Thế nào?” Thấy hắn sắc mặt khác thường, chủ sạp nhìn sang."),t("br"),t("br"),n._v(" “Khụ khụ, ngươi có thể... Tám viên kim tệ bán cho ta sao?” Trương Huyền bàn tay từ miệng túi bên trong móc ra, tinh tế đếm đếm, chỉ có tám viên."),t("br"),t("br"),n._v(" “Tám viên? Ngươi nằm mơ a? Không mang tiền, đi ra mua cái gì đều đồ vật...” "),t("br"),t("br"),n._v(" Nhìn thấy đối phương lòng bàn tay sắp xếp ra mấy viên tiền xu, chủ sạp kém chút không một cái bổ nhào ngã xuống đất."),t("br"),t("br"),n._v(" Gặp móc, chưa từng thấy như thế móc!"),t("br"),t("br"),n._v(" Tám viên kim tệ đã nghĩ mua Hàn Dương Thảo? Ngươi chưa tỉnh ngủ đi! Coi như này cây là bệnh, cũng không thể bán a!"),t("br"),t("br"),n._v(" Đoạt lấy dược liệu, khắp khuôn mặt là không nhịn được vung vung tay: “Không có tiền liền đi đi thôi, ta không có rảnh cho ngươi mò mẫm!” "),t("br"),t("br"),n._v(" “Khụ khụ! Ta là không mang tiền, bất quá, ngươi xác định không bán cho ta?” "),t("br"),t("br"),n._v(" Thấy đối phương dáng dấp này, Trương Huyền lắc đầu một cái."),t("br"),t("br"),n._v(" “Phí lời, này cây Hàn Dương Thảo coi như khó coi, dược hiệu khẳng định vẫn còn, lại kém cũng có thể bán ra ba, bốn mươi viên kim tệ, ngươi tám viên kim tệ? Đùa gì thế! Mau mau cút, không phải vậy liền gọi đội chấp pháp!” "),t("br"),t("br"),n._v(" Chủ sạp thấy này không có tiền gia hỏa, lại còn đổ thừa không đi, tức giận đến khoát tay áo một cái."),t("br"),t("br"),n._v(" Loại cỡ lớn Thương Thành vì phòng ngừa ép mua ép bán, có người quấy rối, đều sắp đặt chuyên môn đội chấp pháp đội tuần tra, chỉ cần một tiếng bắt chuyện, liền sẽ tới, dù cho ngươi là võ giả năm tầng Đỉnh Lực cảnh cường giả đều sẽ bị trực tiếp đuổi ra ngoài!"),t("br"),t("br"),n._v(" Hơn nữa, đối kháng đội chấp pháp chẳng khác nào đối kháng toàn bộ Thương Thành, người bình thường không gan này."),t("br"),t("br"),n._v(" Chủ sạp thấy cái tên này không có tiền còn không đi, nhận định hắn đang quấy rối, trực tiếp mở miệng."),t("br"),t("br"),n._v(" “Không muốn gọi đội chấp pháp, ta là chân chính muốn mua!” Trương Huyền đánh gãy hắn, một bộ lão thần thường tại dáng dấp, nhẹ nhàng nở nụ cười: “Ta tuy rằng không có tiền, nhưng cũng không phải quấy rối, ngươi có tin hay không, ta chỉ cần mở miệng, ngươi một lúc nữa không chỉ muốn cam tâm tình nguyện bán cho ta, e sợ còn muốn miễn phí biếu tặng?” "),t("br"),t("br"),n._v(" “Miễn phí biếu tặng? Ngươi chưa tỉnh ngủ chứ?” "),t("br"),t("br"),n._v(" Không nghĩ đến cái này không có tiền gia hỏa nói ra lời này, chủ sạp giận quá mà cười, như là nhìn thằng ngốc như thế nhìn người thiếu niên trước mắt này: “Nếu như ta thật có thể miễn phí đưa cho ngươi, ta bảo ngươi gia gia!” "),t("br"),t("br"),n._v(" Dưới cái nhìn của hắn, tiểu tử này quả thực chính là não tàn, quá tới mua đồ không mang theo tiền không nói, còn cho là mình sẽ miễn phí biếu tặng..."),t("br"),t("br"),n._v(" Tự mình lại không điên? Làm gì đưa cho ngươi?"),t("br"),t("br"),n._v(" Ngươi cho rằng ngươi là ai a!"),t("br"),t("br"),n._v(" “Kêu ta là ông nội gia? Vậy thì không cần!” Trương Huyền lắc đầu một cái, cười khanh khách nhìn về phía đối phương: “Ta nói ngươi sẽ miễn phí biếu tặng liền sẽ miễn phí biếu tặng, không tin... Đi hai bước... Khụ khụ, đánh một quyền nhìn!” "),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-47"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-49"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);