(window.webpackJsonp=window.webpackJsonp||[]).push([[1721],{2076:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-471-nguoi-dam-sao"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-471-nguoi-dam-sao"}},[n._v("#")]),n._v(" Chương 471: Ngươi dám sao?")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 471: Ngươi dám sao?"),i("br"),i("br"),n._v("Bành bành bành bành!"),i("br"),i("br"),n._v("Lão giả tu vi bị giam cầm, chỗ nào chống đỡ được Trương Huyền, liên tục mấy lần, bị đánh như là đầu heo."),i("br"),i("br"),n._v("“Ta muốn giết ngươi...”"),i("br"),i("br"),n._v("Mang theo Vô Hồn Kim Nhân tới, là muốn trị liệu bệnh gì, kết quả lại bị đánh thành như vậy, lão giả cũng nhịn không được nữa, răng cắn “Khanh khách!” Rung động, hai mắt huyết hồng."),i("br"),i("br"),n._v("“Giết ta? Ngươi không phải là muốn mượn tay của ta, giết hắn ư? Vì cái gì lại muốn giết ta?”"),i("br"),i("br"),n._v("Mỉm cười, Trương Huyền thần sắc khoan thai nhìn qua."),i("br"),i("br"),n._v("“Ngươi nói cái gì? Ta... Ta không biết!”"),i("br"),i("br"),n._v("Nghe nói như thế, lão giả lộ ra một vẻ bối rối, lập tức bị che lấp lại đi."),i("br"),i("br"),n._v("“Không biết? Chạy tới đoạt xá thân thể người khác, còn muốn đem người ta linh hồn gạt bỏ... Thế mà nói với ta không biết?”"),i("br"),i("br"),n._v("Trương Huyền khóe miệng vung lên."),i("br"),i("br"),n._v("“Đoạt xá?”"),i("br"),i("br"),n._v("Kim Tòng Hải, Trại các chủ đám người tất cả đều sững sờ, lần nữa nhìn về phía lão giả, tràn đầy cảnh giác."),i("br"),i("br"),n._v("Đoạt xá là vu hồn sư một loại thủ đoạn đặc thù, cũng gọi cho mượn thể hoàn hồn. Tên như ý nghĩa, thọ nguyên hao hết vu hồn sư, cưỡng ép đem linh hồn người khác trấn áp hoặc là giết chết, mượn nhờ đối phương thân thể, một lần nữa sống tới."),i("br"),i("br"),n._v("đăng nhậｐ http://ngantruyen.com để đọc truyện"),i("br"),n._v("Thần bí khó lường, quỷ dị không gì sánh được."),i("br"),i("br"),n._v("“Không cần lo lắng, hắn bây giờ còn chưa triệt để thành công!” Thấy mọi người bộ dạng, Trương Huyền cười cười: “Nếu quả thật thành công, cái kia chính là ác linh phụ thể, ta thật sự không có cách nào giải quyết.”"),i("br"),i("br"),n._v("Vừa rồi hắn nói qua, ác linh phụ thể hắn hết cách rồi, để mọi người không hiểu rõ nổi, nguyên lai cái tên này cũng không phải là ác linh phụ thể, mà là bị đoạt xá."),i("br"),i("br"),n._v("“Ngươi... Không nên nói bậy, ta chính là ta, ta là bị ác linh phụ thể, không cách nào giải quyết...” Lão giả răng cắn chặt."),i("br"),i("br"),n._v("“Ngươi bị phụ thể?”"),i("br"),i("br"),n._v("Trương Huyền lắc đầu, lần nữa dọc theo lão giả dạo qua một vòng, hai tay chắp sau lưng: “Ngươi dùng thủ đoạn gì, ngăn chặn đối phương linh hồn, ta không rõ lắm, có điều, cụ thể tình huống gì, còn có thể nhìn ra được.”"),i("br"),i("br"),n._v("“Đi tới phòng đấu giá, đấu giá Vô Hồn Kim Nhân, nhưng không nói mình muốn cái gì, để mọi người đoán... Nếu như không nhìn lầm, khi đó hay là hắn linh hồn chủ đạo, không phải là không muốn nói, mà là ngươi đối với hắn áp chế, để hắn không cách nào nói ra miệng đi!”"),i("br"),i("br"),n._v("“Tại mọi người ép hỏi bên dưới, hắn cảm xúc kích động, ngươi có thể thừa dịp, tiếp theo chiếm cứ thân thể... Sau đó nói ai mua Vô Hồn Kim Nhân, liền mang ai đi tìm kiếm vu hồn sư mộ huyệt. Nếu như không đoán sai, ngươi là muốn hấp dẫn người khác đi qua, thiết hạ bẫy rập, nhiều hại chết một chút cường giả!”"),i("br"),i("br"),n._v("Trương Huyền khẽ nói: “Nếu không, mình đào móc Vô Hồn Kim Nhân đều bị ác linh phụ thể, mạng không lâu dài, lại thế nào khả năng còn muốn đi lần thứ hai? Chẳng lẽ thụ ngược đãi đã quen, còn muốn mình tìm chết?”"),i("br"),i("br"),n._v("“Cái này...”"),i("br"),i("br"),n._v("Kim Tòng Hải, Trại các chủ đồng thời chấn động."),i("br"),i("br"),n._v("Vừa rồi lão giả đang đấu giá đài, bị mọi người ép hỏi cực kỳ, xác thực muốn nói gì, đáng tiếc thân thể nhoáng một cái, liền lại không mở lời, ngay sau đó liền nói ra cái này."),i("br"),i("br"),n._v("Hiện tại tưởng tượng, quả thực có chút quái dị."),i("br"),i("br"),n._v("Tại vu hồn sư mộ huyệt, mặc dù lấy được Vô Hồn Kim Nhân, nhưng cũng bị linh hồn đoạt xá, gần chết đi, chỗ nguy hiểm như vậy, thế nào còn muốn lại đi?"),i("br"),i("br"),n._v("Rất rõ ràng liền là một cái cực lớn lỗ thủng."),i("br"),i("br"),n._v("Chỉ tiếc, bọn hắn cũng bị vu hồn sư truyền thừa cùng trong đó bảo vật mê hoặc, không nghĩ tới điểm ấy mà thôi."),i("br"),i("br"),n._v("“Ta chỉ là muốn nâng lên bảng giá...”"),i("br"),i("br"),n._v("Lão giả nói."),i("br"),i("br"),n._v("“Nâng lên bảng giá? Cái kia tốt!”"),i("br"),i("br"),n._v("Trương Huyền nhẹ nhàng nở nụ cười: “Kim tiền bối, Trại các chủ, còn muốn làm phiền các ngươi, đem hắn trên người phong ấn giải khai!”"),i("br"),i("br"),n._v("“Mở ra phong ấn?”"),i("br"),i("br"),n._v("Hai người sửng sốt một chút."),i("br"),i("br"),n._v("Vừa rồi đánh mặc dù rất ác độc, trên thực tế nhưng chỉ là da thịt, không làm bị thương căn bản, phong ấn một khi giải khai, khôi phục thực lực đến Hóa Phàm nhị trọng đỉnh phong, khẳng định không phải là đối thủ."),i("br"),i("br"),n._v("“Yên tâm đi!” Biết lo lắng của bọn hắn, Trương Huyền cho cái an ủi ánh mắt."),i("br"),i("br"),n._v("“Tốt!”"),i("br"),i("br"),n._v("Biết vị thanh niên này, mặc dù tuổi trẻ, nhưng xưa nay không làm không nắm chắc sự tình, hai người đồng thời bàn tay lớn vồ một cái."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Áp chế ở trên người lão giả lực lượng biến mất, khí tức của hắn lần nữa khôi phục."),i("br"),i("br"),n._v("“Hừ, ta nhìn các ngươi, cũng không biết khu trừ ác linh, đã như vậy, cáo từ!”"),i("br"),i("br"),n._v("Phong ấn giải khai, lão giả nhẹ nhàng thở ra, quay người liền muốn rời khỏi."),i("br"),i("br"),n._v("Còn chưa đi lên hai bước, liền thấy thanh niên ngăn tại trước mặt."),i("br"),i("br"),n._v("“Thế nào, chỉ bằng thực lực của ngươi, cũng muốn cản ta?” Cười lạnh một tiếng."),i("br"),i("br"),n._v("Mặc dù nhìn không ra thanh niên xác thực thực lực, nhưng vừa rồi khiến người khác ra tay, liền có thể đoán ra, thực lực khẳng định không bằng mình."),i("br"),i("br"),n._v("“Vậy ngươi liền thử một chút!” Cũng không nhiều lời, Trương Huyền bàn chân nâng lên, lần nữa đạp tới."),i("br"),i("br"),n._v("“Hừ!”"),i("br"),i("br"),n._v("Lão giả sầm mặt lại, bàn tay đột nhiên đưa tay về phía trước."),i("br"),i("br"),n._v("Bất quá, cái gì đều không có bắt đến, ngược lại ngực tê rần."),i("br"),i("br"),n._v("Bành!"),i("br"),i("br"),n._v("Lần nữa bay rớt ra ngoài, tầng tầng quẳng xuống đất."),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Lão giả con ngươi co rụt lại."),i("br"),i("br"),n._v("“Có phải hay không thật bất ngờ?” Thân thể nhoáng một cái, Trương Huyền lần nữa đi tới trước mặt, lật bàn tay một cái giật xuống."),i("br"),i("br"),n._v("Ba!"),i("br"),i("br"),n._v("Đỏ mặt lên, lần nữa thêm ra năm cái dấu bàn tay, lão giả té ngã trên đất."),i("br"),n._v(" “Cái này...”"),i("br"),i("br"),n._v("Mọi người hai mặt nhìn nhau."),i("br"),i("br"),n._v("“Hóa Phàm nhị trọng đỉnh phong, hoàn toàn ngăn cản không nổi nửa bước Hóa Phàm, cái này... Chuyện gì xảy ra?”"),i("br"),i("br"),n._v("“Ta cũng không hiểu rõ...”"),i("br"),i("br"),n._v("Lão giả này rõ ràng là Hóa Phàm nhị trọng đỉnh phong thực lực, vừa rồi phong ấn thời điểm, lực lượng cũng hung mãnh không gì sánh được, thế nào đột nhiên lần này biến thành nhuyễn chân tôm? Bị một cái nửa bước Hóa Phàm đánh mảy may không năng lực phản kháng?"),i("br"),i("br"),n._v("Coi như vị này Trương sư có thể vượt cấp chiến đấu, có thể đối mặt Hóa Phàm sơ kỳ, liền rất khó khăn, đánh tơi bời Hóa Phàm nhị trọng đỉnh phong, còn để hắn không cách nào đánh trả, tuyệt đối không thể."),i("br"),i("br"),n._v("“Chẳng lẽ lão sư thực lực lại tăng lên?”"),i("br"),i("br"),n._v("Cùng bọn hắn khiếp sợ không giống, Triệu Nhã đám người mặt mũi tràn đầy hưng phấn."),i("br"),i("br"),n._v("Lão sư đột phá như uống nước, sớm tại trong lòng bọn họ hình thành quen thuộc, nhìn thấy Hóa Phàm nhị trọng đỉnh phong bị đánh răng rơi đầy đất, còn tưởng rằng Trương Huyền đã đột phá."),i("br"),i("br"),n._v("“Không phải, là... Lão giả thực lực trở nên yếu đi! Hiện tại sức chiến đấu, nhiều nhất Hóa Phàm nhất trọng sơ kỳ!” Triệu Phi Vũ nói."),i("br"),i("br"),n._v("Nàng nhận hạn chế thân thể, không chút tu luyện, kiến thức rộng rãi, ánh mắt lại là rất tốt."),i("br"),i("br"),n._v("“Ừm, công tử nói không sai, thực lực của hắn bây giờ, nhiều nhất Hóa Phàm nhất trọng sơ kỳ, chỉ là... Trên người tu vi, lại là thực sự Hóa Phàm nhị trọng đỉnh phong, cụ thể... Vì cái gì, ta cũng không hiểu rõ!”"),i("br"),i("br"),n._v("Kim Tòng Hải nói."),i("br"),i("br"),n._v("“Hóa Phàm nhị trọng đỉnh phong tu vi, cũng chỉ có nhất trọng sơ kỳ thực lực, cái này...”"),i("br"),i("br"),n._v("Nghe bọn hắn xác nhận, Triệu Nhã mấy người cũng không hiểu rõ nổi."),i("br"),i("br"),n._v("Lạc Tần càng là không hiểu ra sao."),i("br"),i("br"),n._v("“Nghe được đi? Không chỉ ta đã nhìn ra, những người khác cũng đều đã nhìn ra, ngươi bây giờ còn có lời gì nói?”"),i("br"),i("br"),n._v("Trương Huyền dừng lại công kích, cười khanh khách nhìn về phía bị lần nữa cuồng đánh một trận lão giả."),i("br"),i("br"),n._v("“Ta... Bị thương, không cách nào phát huy thực lực mà thôi...”"),i("br"),i("br"),n._v("Lão giả cắn răng."),i("br"),i("br"),n._v("“Coi như bị thương, cũng không có khả năng để ngươi không phát huy ra tiếp cận hai cái đại cấp bậc thực lực đi!” Dường như sớm biết hắn lại như vậy nói, Trương Huyền nói."),i("br"),i("br"),n._v("“Trương sư, đây rốt cuộc chuyện gì xảy ra?”"),i("br"),i("br"),n._v("Thấy cái tên này có chút từ nghèo, sơ hở càng ngày càng nhiều, Trại các chủ cũng nhịn không được nữa."),i("br"),i("br"),n._v("“Rất đơn giản, đoạt xá linh hồn, cùng bị đoạt xá người, thực lực không ngang nhau! Nếu như ta không nhìn lầm, bị đoạt xá người là Hóa Phàm nhị trọng đỉnh phong, mà đoạt xá linh hồn, chỉ có nhất trọng sơ kỳ!”"),i("br"),i("br"),n._v("Trương Huyền nói: “Mặc dù chiếm cứ thân thể, nhưng không có thích ứng, nhất là mới vừa ta bị ta đánh một trận, thân thể linh hồn khó mà phù hợp, coi như có thể khống chế, không lộ sơ hở, lại không cách nào điều động chân nguyên, lúc này mới có vẻ nhỏ yếu!”"),i("br"),i("br"),n._v("Vừa rồi lần đầu tiên đang đấu giá đài nhìn thấy vị lão giả này thời điểm, Minh Lý Chi Nhãn nhìn ra không ít sơ hở, liền để tâm hắn sinh nghi nghi ngờ."),i("br"),i("br"),n._v("Dựa theo tình huống bình thường, hắn tu vi hiện tại, nhiều nhất chỉ có thể nhìn xuyên Hóa Phàm sơ kỳ, có thể... Nhị trọng đỉnh phong có thể nhìn ra vấn đề, bây giờ suy nghĩ một chút, chính là bởi vì trong cơ thể dung hợp cái này Hóa Phàm sơ kỳ linh hồn nguyên nhân."),i("br"),i("br"),n._v("Đoạt xá, cùng thân thể dung hợp tính vốn là rất kém cỏi, thân thể bị đánh, tự nhiên sẽ tự chủ lựa chọn tới gần vốn là linh hồn, cùng đoạt xá linh hồn xa lánh... Cũng là tạo thành, hắn rõ ràng có Hóa Phàm nhị trọng đỉnh phong thực lực, lại không biện pháp phát huy ra."),i("br"),i("br"),n._v("“Ngươi, ngươi...”"),i("br"),i("br"),n._v("Lão giả vẻ mặt trắng bệch."),i("br"),i("br"),n._v("Vừa rồi đều có thể giải thích, lần này thẳng vào chỗ yếu hại, coi như muốn giải thích, cũng không giải thích được."),i("br"),i("br"),n._v("Dù sao, vừa rồi chỉ là vết thương da thịt, loại trình độ này, đối Hóa Phàm nhị trọng cường giả đỉnh phong tới nói, hoàn toàn có thể bỏ qua không tính."),i("br"),i("br"),n._v("“Không cần, ngươi nha, ta nha, ta vừa rồi không nói đoạt xá, cố ý nói là ác linh phụ thể, mục đích đúng là vì tiêu trừ băn khoăn của ngươi, ngươi cảm thấy đúng lúc là một cơ hội, muốn mượn tay của ta, đem cái kia cùng ngươi cạnh tranh linh hồn gạt bỏ... Ta nói không sai chứ!”"),i("br"),i("br"),n._v("Trương Huyền nói tiếp."),i("br"),i("br"),n._v("Đối phương mặc dù đoạt xá thành công, nhưng cũng không đem cái kia linh hồn chém giết, thậm chí cái kia linh hồn còn muốn chiếm cứ chủ đạo."),i("br"),i("br"),n._v("Bằng không, cũng sẽ không có chạy đến nơi đây đấu giá Vô Hồn Kim Nhân, càng muốn hối đoái trấn áp nó phương pháp."),i("br"),i("br"),n._v("Ý nghĩ tuy tốt, đổi lại những người khác, khả năng thật đúng là sẽ trúng kế, đem vốn là linh hồn giết chết, thành toàn cái này đoạt xá, đáng tiếc... Gặp Trương Huyền."),i("br"),i("br"),n._v("Thiên Đạo bên dưới, không chỗ che thân, đừng nói linh hồn, coi như hóa thành một tia khói, cũng khó thoát pháp nhãn."),i("br"),i("br"),n._v("“Ngươi... Đã đều bị ngươi nhìn ra, ta cũng không tại ngụy trang, không sai, ta là đoạt xá cái tên này!”"),i("br"),i("br"),n._v("Thấy lại không có cách nào che giấu, lão giả đứng dậy, hừ lạnh một tiếng, không tại phủ nhận."),i("br"),i("br"),n._v("“Quả nhiên là?”"),i("br"),i("br"),n._v("“Hóa Phàm nhất trọng linh hồn đoạt xá nhị trọng đỉnh phong thân thể...”"),i("br"),i("br"),n._v("Nghe hắn thừa nhận, tất cả mọi người đều dọa đến vẻ mặt trắng bệch."),i("br"),i("br"),n._v("Hóa Phàm nhất trọng sơ kỳ, cùng nhị trọng đỉnh phong, chênh lệch tiếp cận hai cái đại cấp bậc, chuyển đổi thành sức chiến đấu, có thể nói khác nhau một trời một vực, không có khả năng vượt qua, cái này linh hồn nhưng có thể thành công đoạt xá..."),i("br"),i("br"),n._v("Đến cùng làm sao làm được?"),i("br"),i("br"),n._v("Thật là đáng sợ đi!"),i("br"),i("br"),n._v("“Thừa nhận liền tốt, vừa rồi những cái kia, kỳ thật... Là ta đoán, nếu như ngươi không thừa nhận, ta sẽ cảm thấy đoán sai...” Trương Huyền nói."),i("br"),i("br"),n._v("Hắn thực sự nói thật."),i("br"),i("br"),n._v("Thiên Đạo thư viện chỉ có thể nhìn khuyết chức khuyết điểm, cụ thể tình huống gì, không có phân tích, cũng sẽ không cho ra đáp án."),i("br"),i("br"),n._v("Hắn cũng là thông qua rất nhiều sơ hở sai lầm, kết hợp với trong đầu tri thức, kết hợp đi ra giả thiết, không nghĩ tới... Thế mà đoán đúng."),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Hơi đỏ mặt, lão giả kém chút thổ huyết."),i("br"),i("br"),n._v("Sớm biết hắn chỉ là đoán, đánh chết cũng không thừa nhận ah..."),i("br"),i("br"),n._v("Hiện tại tốt, thừa nhận về sau, coi như muốn bác bỏ, cũng không có biện pháp..."),i("br"),i("br"),n._v("“Ngươi không dùng đến ý!”"),i("br"),i("br"),n._v("Híp mắt lại, đem buồn bực dứt bỏ, lão giả cười lạnh: “Ta hiện tại linh hồn chiếm chủ đạo, liền là thân thể chủ nhân, coi như ngươi biết ta là đoạt xá, thì có ích lợi gì? Chẳng lẽ còn có thể đem ta giết...”"),i("br"),i("br"),n._v("“Ngươi dám không?”"),i("br"),i("br"),n._v("(Tối hôm qua tăng ca đến 12 giờ nhiều lại làm ra một chương, ban ngày còn muốn giám thị, lòng chua xót, cầu an ủi!)"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-479"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-481"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);