(window.webpackJsonp=window.webpackJsonp||[]).push([[1724],{2079:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-474-quy-di-vo-hon-kim-nhan-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-474-quy-di-vo-hon-kim-nhan-thuong"}},[n._v("#")]),n._v(" Chương 474: Quỷ dị Vô Hồn Kim Nhân (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 474: Quỷ dị Vô Hồn Kim Nhân (thượng)"),t("br"),t("br"),n._v("Người khác nghe được vu hồn sư đều là nghe đến đã biến sắc, có thể trốn bao xa trốn bao xa, cái tên này đến tốt, còn muốn trị đến vài đầu, để hắn học sinh tấn cấp?"),t("br"),t("br"),n._v("Tất cả mọi người cảm thấy quai hàm đều rơi trên mặt đất, gần thổ huyết."),t("br"),t("br"),n._v("Đổi lại người khác nói như vậy, khẳng định cảm thấy có phải điên rồi hay không, nhưng nhìn đến Tôn Cường kinh lịch vừa rồi, biết đối phương thực sự nói thật..."),t("br"),t("br"),n._v("Bắt vu hồn sư, tăng thực lực lên... Đại ca, ngươi coi cái này làm cho người kinh khủng nghề nghiệp là phá cảnh đan ah..."),t("br"),t("br"),n._v("“Cái này... Trương sư, vu hồn sư thời kỳ Thượng Cổ đã tiêu diệt hầu như không còn, đầu này sống sót, hẳn là dùng bí pháp nào đó rơi vào trạng thái ngủ say, kết quả bị ta trong lúc vô tình phát động, mới lấy thức tỉnh... Loại vật này, bản thân không nhiều.”"),t("br"),t("br"),n._v("Lau lau mồ hôi lạnh trên đầu, Lạc Trúc vội vàng giải thích."),t("br"),t("br"),n._v("Vu hồn sư thời kỳ Thượng Cổ liền không nhiều, về sau càng bị Danh Sư đường diệt chỉ toàn, có thể còn sống sót, đều cùng hổ phách đồng dạng, thuộc về cơ duyên xảo hợp, ngươi cho rằng là đường phố khoai tây, củ cải, vừa nắm một bó to? Một đào tê rần túi?"),t("br"),t("br"),n._v("Còn trị vài đầu để học sinh tấn cấp..."),t("br"),t("br"),n._v("Thực có can đảm muốn!"),t("br"),t("br"),n._v("“Không nhiều? Vậy thì thật là thật là đáng tiếc...”"),t("br"),t("br"),n._v("Trương Huyền mặt mũi tràn đầy tiếc hận."),t("br"),t("br"),n._v("Xem người ta Tôn Cường tấn cấp, cùng uống nước đồng dạng đơn giản, Tông Sư cảnh trực tiếp đạt tới Hóa Phàm nhất trọng... Nếu là thật có thể trị đến vài đầu, Triệu Nhã bọn hắn, khẳng định cũng sẽ thuận lợi đột phá, thật muốn đạt tới Hóa Phàm cảnh, coi như đi vạn quốc liên minh, cũng sẽ trở thành một sự giúp đỡ lớn."),t("br"),t("br"),n._v("“Đúng rồi, Vô Hồn Kim Nhân, vu hồn sư đến cùng chuyện gì xảy ra? Có thể hay không cùng chúng ta nói tỉ mỉ một cái?”"),t("br"),t("br"),n._v("Biết thứ này có thể ngộ nhưng không thể cầu, cũng không phải là tùy tiện liền có thể lấy được, Trương Huyền không đang xoắn xuýt, nhìn lại."),t("br"),t("br"),n._v("Những người khác cũng đầy mặt hiếu kỳ."),t("br"),t("br"),n._v("Cái tên này là từ đâu lấy được Vô Hồn Kim Nhân, lại thế nào bị vu hồn sư quấn lên?"),t("br"),t("br"),n._v("“Muốn trách, thì trách ta lòng tham! Theo một bản trong cổ thư, tìm được vu hồn sư mộ địa, cho là có vô số trân bảo liền đi qua... Kết quả gặp Vô Hồn Kim Nhân, mới vừa thu lại một cái, liền bị linh hồn đoạt xá, cũng không dám lại tiếp tục nữa, vội vàng trốn thoát...”"),t("br"),t("br"),n._v("Lạc Trúc hơi đỏ mặt."),t("br"),t("br"),n._v("Kinh nghiệm của hắn cùng trước đó mọi người đoán không sai biệt lắm, phát hiện Thượng Cổ vu hồn sư lưu lại mộ huyệt, muốn đi vào tầm bảo, lại không nghĩ rằng bị một cái linh hồn đoạt xá."),t("br"),t("br"),n._v("Liên tục tranh đoạt không biết bao nhiêu ngày, cảm giác đối phương càng ngày càng mạnh thế, biết tiếp tục nữa sớm muộn gì đều sẽ bị gạt bỏ. Lúc này mới bất đắc dĩ đi tới phòng đấu giá, bán đi Vô Hồn Kim Nhân, hy vọng có thể mượn nhờ Giám Bảo các tên tuổi, để càng nhiều đối vu hồn sư cảm thấy hứng thú người tới, thuận tiện giúp bề bộn giải quyết trong cơ thể tai hoạ ngầm."),t("br"),t("br"),n._v("May mắn, vận khí không tệ, gặp vị này Trương sư."),t("br"),t("br"),n._v("Nếu không, đổi lại những người khác, khẳng định sẽ đem sai liền sai, trực tiếp cùng đầu kia linh hồn giao dịch, đem hắn luyện hóa."),t("br"),t("br"),n._v("“Ta muốn đi xem một chút, thuận tiện tìm kiếm một cái vu hồn sư truyền thừa, ngươi có thể hay không mang ta đi cái kia mộ huyệt?”"),t("br"),t("br"),n._v("Nghe xong kinh nghiệm của hắn, Trương Huyền mở lời."),t("br"),t("br"),n._v("Đến Hồng Hải thành, mục đích lớn nhất liền là tìm kiếm vu hồn sư truyền thừa, tìm tới cứu tỉnh Lộ Xung phương pháp, hiện tại có cơ hội này, đương nhiên sẽ không buông tha."),t("br"),t("br"),n._v("“Đi mộ huyệt?” Lạc Trúc thân thể run lên."),t("br"),t("br"),n._v("Lần trước trải qua, để hắn đến nay khó quên, nếu như lại đi, thật có chút sợ hãi cùng kiêng kị."),t("br"),t("br"),n._v("“Nếu như ngươi lo lắng, cũng có thể đem địa chỉ nói cho ta biết, ta tự mình đi là đủ.”"),t("br"),t("br"),n._v("“Cái này... Không cần! Ta cái mạng này là Trương sư cứu, nếu như Trương sư thật muốn đi, dẫn ngươi đi là được!” Chần chờ một lát, Lạc Trúc một cắn răng."),t("br"),t("br"),n._v("Nếu không phải vị này Trương sư, hắn hiện tại linh hồn đã bị ma diệt, hồn phi phách tán, lớn như thế ân, tuyệt không phải một đầu Vô Hồn Kim Nhân liền có thể báo đáp."),t("br"),t("br"),n._v("“Bất quá... Trương sư phải cẩn thận, chỗ kia quỷ dị không gì sánh được, tốt nhất có thể sớm chuẩn bị, nếu không, ta sợ... Coi như thực lực ngươi mạnh mẽ, cũng khó lòng phòng bị!”"),t("br"),t("br"),n._v("Nhớ tới trong huyệt mộ quỷ dị, Lạc Trúc lòng còn sợ hãi."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Có thể trở thành Thượng Cổ nhất làm cho người hoảng sợ, sợ hãi chức nghiệp một trong, vu hồn sư khẳng định không đơn giản, Tôn Cường sở dĩ có thể tuỳ tiện đem đầu kia gia hỏa luyện hóa, Thiên Đạo chân khí chiếm ảnh hưởng, càng quan trọng hơn là, cái tên này sống không biết bao lâu, đã cực đoan suy yếu."),t("br"),t("br"),n._v("Lại thêm cùng Lạc Trúc cạnh tranh thân thể chưởng khống quyền lâu như vậy, giống như vừa mới hành quân mệt mỏi binh sĩ, bị tuỳ tiện đánh tan, chẳng có gì lạ."),t("br"),t("br"),n._v("Nhưng này cái mộ huyệt không giống, bên trong vạn nhất còn có linh hồn, thực lực không biết, thủ đoạn không biết, tuyệt đối không dễ dàng như vậy."),t("br"),t("br"),n._v("“Thứ cần thiết, ta vậy thì chuẩn bị, sau ba ngày, chúng ta xuất phát!” Suy tư một chút, Lạc Trúc nói."),t("br"),t("br"),n._v("Điều tra vu hồn sư mộ huyệt lâu như vậy, đối linh hồn kiêng kỵ đồ vật, cũng biết một chút, có điều, những vật phẩm này cần chuyên môn định chế, ngoại giới không cách nào mua sắm, cần hắn tự mình chuẩn bị."),t("br"),t("br"),n._v("“Tốt!” Trương Huyền đồng ý."),t("br"),t("br"),n._v("Vừa vặn hắn để Giám Bảo các chuẩn bị công pháp, võ kỹ cũng không tìm đủ, nơi này cũng còn có không ít sự tình phải xử lý, ba ngày đối với hắn mà nói, cũng đúng lúc kịp."),t("br"),t("br"),n._v("“Cái kia... Ta trước hết cáo từ!”"),t("br"),t("br"),n._v("Lại nói một hồi, quyết định đi vu hồn sư mộ địa chi tiết, Lạc Trúc lúc này mới ôm quyền rời khỏi."),t("br"),t("br"),n._v("“Trương sư, vu hồn sư truyền thừa đoạn tuyệt, quỷ dị không gì sánh được, ta... Cũng muốn theo tới nhìn xem, không biết có thể?”"),t("br"),t("br"),n._v("Lạc Trúc rời khỏi, gian phòng an tĩnh lại, Trại các chủ hơi đỏ mặt, nói."),t("br"),t("br"),n._v("Lần này mua sắm Vô Hồn Kim Nhân, hắn cái gì bề bộn đều không có giúp đỡ, nhưng muốn trực tiếp hưởng dụng thành quả, có chút xấu hổ."),t("br"),t("br"),n._v("Bất quá, Vu hồn văn hóa tại thời kỳ Thượng Cổ, có dày đặc diễm mạt một bút, làm giám bảo sư, có thể có cơ hội tiếp xúc gần gũi, tự nhiên muốn gia nhập."),t("br"),t("br"),n._v("“Trương sư, ta cũng muốn đi xem nhìn...”"),t("br"),t("br"),n._v("Lạc Tần cũng vội vàng nói."),t("br"),t("br"),n._v("Đấu giá hội vô lợi không dậy sớm, vu hồn sư nghiên cứu linh hồn, mỗi lần đều cần đại lượng cống phẩm, mới vừa xuất thủ, có thể đi qua nhìn một chút, một khi lấy được chút bảo vật, đối đấu giá hội tới nói, cũng có chỗ tốt cực lớn."),t("br"),n._v("“Muốn đi xem?” Trương Huyền nhíu mày một cái, đột nhiên ánh mắt sáng lên: “Tốt, có điều, ta khuyên các ngươi sớm chuẩn bị tốt đầy đủ linh thạch, vạn nhất có cái gì bảo bối, cũng có thể có tiền mua sắm!”"),t("br"),t("br"),n._v("Hai cái này đều là nhân vật có tiền, vạn nhất trong huyệt mộ mò được đồ tốt, có thể trực tiếp bán cho bọn hắn, từ đó kiếm lấy lợi ích."),t("br"),t("br"),n._v("Hiện tại kim tệ đối với hắn mà nói không có gì dùng, có thể đủ nhiều làm chút linh thạch, mặc kệ chính mình vẫn là Triệu Nhã đám người, tu luyện đều sẽ làm ít công to."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Hai người gật đầu đáp ứng."),t("br"),t("br"),n._v("Đổi lại người khác dám nói như vậy, lấy hai người thân phận, khẳng định đánh đối phương răng rơi đầy đất."),t("br"),t("br"),n._v("Có thể từ khi hiểu biết vị này Trương sư thủ đoạn thần quỷ khó lường, liền vu hồn sư đều có thể tuỳ tiện chém chết... Loại kia ý nghĩ tuyệt không dám có."),t("br"),t("br"),n._v("Tùy tiện xông Lưu Thương Khúc Thủy tê liệt, liếc mắt nhìn ra đoạt xá... Lại thêm danh sư thân phận, mặc kệ cái nào, đều không phải là bọn hắn dám tùy ý làm bậy."),t("br"),t("br"),n._v("“Đúng rồi, có thể hay không phiền phức thi đấu cách chủ, mang ta đi các ngươi Tàng Thư khố, nhìn xem liên quan tới vu hồn sư thư tịch!”"),t("br"),t("br"),n._v("Xác định được, Trương Huyền nhìn về phía Trại các chủ."),t("br"),t("br"),n._v("Đi tới Giám Bảo các, đương nhiên muốn đem bọn hắn Tàng Thư khố cũng xem một lần, đối sau này giám bảo, vu hồn sư cũng có thể lý giải nhiều một ít, không đến mức bị động như vậy."),t("br"),t("br"),n._v("“Cái này đơn giản!”"),t("br"),t("br"),n._v("Còn tưởng rằng là yêu cầu gì, chỉ là đọc sách, Trại các chủ mỉm cười."),t("br"),t("br"),n._v("Trước mắt vị này, giám bảo trình độ bản thân cao hơn hắn, coi như không yêu cầu, cũng có tư cách đi vào quan sát."),t("br"),t("br"),n._v("“Vậy là tốt rồi! Khả năng còn muốn làm phiền Các chủ chuẩn bị chỗ ở, ta mấy cái này học sinh, còn có vị này Triệu công tử, cần nghỉ ngơi!”"),t("br"),t("br"),n._v("Trương Huyền nói tiếp."),t("br"),t("br"),n._v("Theo Hiên Viên vương quốc một đường chạy đến, vừa đến liền tiến vào Giám Bảo các, sau đó lại tham gia đấu giá hội... Tất cả mọi người cảm thấy có chút mệt mỏi."),t("br"),t("br"),n._v("Trại các chủ gật đầu lui ra ngoài, thời gian không dài, liền an bài mấy cái an tĩnh gian phòng."),t("br"),t("br"),n._v("Tiến vào bên trong, đem Triệu Nhã đám người thét lên bên người."),t("br"),t("br"),n._v("“Đi vu hồn sư mộ huyệt nguy hiểm không gì sánh được, mấy ngày nay mau chóng tăng thực lực lên, mới có thể cam đoan an toàn.”"),t("br"),t("br"),n._v("Vu hồn sư mộ huyệt, liền Hóa Phàm nhị trọng đỉnh phong Lạc Trúc đều gặp phải công kích, những học sinh này, chẳng qua Tông sư, vẫn là kém một mảng lớn, nhất định phải nhanh tăng thực lực lên, nếu không, coi như đi qua, cũng là vướng víu."),t("br"),t("br"),n._v("“Linh thạch tùy tiện sử dụng, ba ngày thời gian, nhất định phải toàn bộ đột phá đến Chí Tôn, nếu không, không cần đi theo!”"),t("br"),t("br"),n._v("Lấy ra một đống linh thạch ném tới, Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("Trước đó nghèo, không có linh thạch, chính mình cũng không thôi dùng, học sinh cũng chưa từng gặp qua. Hiện tại kinh qua đấu giá hội cùng Lưu Thương Khúc Thủy, hơn hai vạn linh thạch cầm ở trong tay, muốn làm sao dùng liền dùng như thế nào, không chút nào keo kiệt."),t("br"),t("br"),n._v("“Vâng!” Nghe được lão sư cho mệnh lệnh như vậy, mọi người đồng thời cắn răng."),t("br"),t("br"),n._v("Tông sư đột phá đến Chí Tôn rất khó, nhưng trong vấn đề tu luyện, lão sư toàn bộ giảng giải qua, lại có lão sư chuyên môn sáng tạo công pháp, phối hợp nhiều linh thạch như vậy, lại không cách nào thành công, cũng sẽ không cần sống."),t("br"),t("br"),n._v("“Tu luyện đi thôi!”"),t("br"),t("br"),n._v("Giao phó xong, Trương Huyền rời phòng, cùng Trại các chủ phía sau, hướng Tàng Thư khố đi đến."),t("br"),t("br"),n._v("Giám bảo, liên quan nhân văn, lịch sử, thư hoạ, đồ cổ, vũ khí... Rất nhiều chức nghiệp, ẩn chứa trong đó tri thức nhiều vô số kể, Tàng Thư khố sưu tập thư tịch, cũng so những nghề nghiệp khác càng thêm phong phú."),t("br"),t("br"),n._v("Nhìn thoáng qua, Trương Huyền liền bị Tàng Thư khố hùng vĩ rung động."),t("br"),t("br"),n._v("Cùng nơi này so sánh, trước đó nhìn thấy Thiên Vũ vương quốc Tàng Thư khố, thậm chí Hiên Viên vương quốc Danh Sư đường, đều không đáng nhấc lên."),t("br"),t("br"),n._v("“Bắt đầu!”"),t("br"),t("br"),n._v("Trương Huyền lần nữa thu nhận sử dụng sách."),t("br"),t("br"),n._v("Hoa lạp lạp lạp!"),t("br"),t("br"),n._v("Hao phí tiếp cận một ngày thời gian, mới đưa thư tịch thu sạch ghi chép sạch."),t("br"),t("br"),n._v("Trở lại chỗ ở, lúc này mới phát hiện toàn thân xụi lơ, đều nhanh không đứng dậy nổi."),t("br"),t("br"),n._v("Theo Hiên Viên vương quốc đi tới, Giám Bảo các, Lưu Thương Khúc Thủy, đấu giá hội một đường chơi đùa, trên thân thể tiêu hao còn không tính cái gì. Minh Lý Chi Nhãn vận dụng quá nhiều, tâm cảnh khắc độ lên hao tổn, lại thêm nhanh chóng thu lấy thư tịch, để cảm giác khác đến mỏi mệt chịu không nổi."),t("br"),t("br"),n._v("Ngủ một giấc, thẳng đến giữa trưa ngày thứ hai, lúc này mới khôi phục lại, một lần nữa cảm thấy sảng khoái tinh thần."),t("br"),t("br"),n._v("Tâm cảnh khắc độ, chỉ cần không làm bị thương vốn liếng, nghỉ ngơi đầy đủ, là hoàn toàn có thể khôi phục."),t("br"),t("br"),n._v("“Nhìn có thể hay không tìm tới đầu mối gì!”"),t("br"),t("br"),n._v("Tỉnh táo lại, duỗi lưng một cái, Trương Huyền cổ tay khẽ đảo, một cái cực lớn kim nhân xuất hiện tại trước mặt."),t("br"),t("br"),n._v("Theo phòng đấu giá mua được, đều không có thế nào quan sát qua, hiện tại vừa vặn có rảnh, thật tốt nghiên cứu một chút, có lẽ có thể tìm tới một chút dấu vết để lại, đối vu hồn sư có càng sâu hiểu rõ."),t("br"),t("br"),n._v("Kim nhân an tĩnh nằm, cùng thi thể, khôi lỗi không có gì khác biệt, Trương Huyền nhìn một hồi, không nhìn ra cái gì, ngón tay tại trên người đối phương một đáp."),t("br"),t("br"),n._v("“Thiếu hụt!”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Trong óc hình thành một quyển sách."),t("br"),t("br"),n._v("Tiện tay lật ra, Trương Huyền cúi đầu nhìn sang, ngay sau đó lông mày nhíu một cái, vẻ mặt trở nên dị thường cổ quái."),t("br"),t("br"),n._v("“Cái này, cái này... Làm sao có thể?”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-482"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-484"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);