(window.webpackJsonp=window.webpackJsonp||[]).push([[1725],{2080:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-475-quy-di-vo-hon-kim-nhan-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-475-quy-di-vo-hon-kim-nhan-ha"}},[n._v("#")]),n._v(" Chương 475: Quỷ dị Vô Hồn Kim Nhân (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 475: Quỷ dị Vô Hồn Kim Nhân (hạ)"),t("br"),t("br"),n._v("Chỉ thấy thư tịch lít nha lít nhít viết một đống."),t("br"),t("br"),n._v("“Cấp một Vô Hồn Kim Nhân, vu hồn sư luyện chế đặc thù khôi lỗi, có ý thức tự chủ, cũng có thể để cho người ta linh hồn xâm nhập khống chế, trong cơ thể có bày linh thạch trận, không cần tu luyện. Vài đầu phối hợp, thống nhất khống chế, có lớn lao lực công kích. Khuyết điểm:”"),t("br"),t("br"),n._v("Có ý thức tự chủ?"),t("br"),t("br"),n._v("Có ý tứ gì?"),t("br"),t("br"),n._v("Chẳng lẽ, cái đồ chơi này không phải chết, mà là sống? Nếu không... Tại sao có thể có ý thức tự chủ?"),t("br"),t("br"),n._v("Tiếp tục hướng xuống nhìn lại."),t("br"),t("br"),n._v("“... Khuyết điểm bốn, ý thức bị tự nguyện rút ra thân thể, trong cơ thể chỉ có một chút bất diệt chi hỏa, không cách nào thanh tỉnh. Khuyết điểm năm...”"),t("br"),t("br"),n._v("“Chẳng lẽ...” Đột nhiên nghĩ đến cái gì, Trương Huyền sầm mặt lại."),t("br"),t("br"),n._v("Truyền thuyết, Vô Hồn Kim Nhân là vu hồn sư lấy người sống luyện chế mà thành, vốn đang cảm thấy không quá tin tưởng, hiện tại xem ra, chỉ sợ đúng là như thế."),t("br"),t("br"),n._v("Cứ thế mà đem linh hồn rút ra, trong cơ thể lưu lại một tia bất diệt chi hỏa, mục đích là vì để cho người sống xuống, thân thể sẽ không hư thối, vậy thì cùng người thực vật đồng dạng, thoạt nhìn cùng tử thi không có gì khác nhau, trong cơ thể nhưng có hoạt tính, xem như người sống."),t("br"),t("br"),n._v("Nếu không, linh hồn không có, người chẳng khác nào chết rồi, thân thể cũng là không cách nào bảo tồn, lại như thế nào luyện chế thành kim nhân?"),t("br"),t("br"),n._v("Không nói cái khác, chỉ riêng đem toàn thân thẩm thấu kim loại, biến thành bộ dáng này, liền cần tốn hao không ít thời gian, không bảo trì thân thể còn sống, khả năng còn không có thành công, cũng đã mục nát."),t("br"),t("br"),n._v("Nắm đấm căng thẳng, đang cảm thấy vu hồn sư tàn nhẫn không gì sánh được, ngay sau đó lắc đầu: “Không đúng, ý thức là bị tự nguyện rút ra thân thể, chẳng lẽ, những người này cam nguyện tử vong?”"),t("br"),t("br"),n._v("Thiên Đạo thư viện biểu hiện chính là linh hồn tự nguyện bị rút ra."),t("br"),t("br"),n._v("Nói cách khác, tước đoạt linh hồn thời điểm, cũng không phải là vu hồn sư ép buộc, mà là kim nhân chủ nhân cũ tự nguyện, như vậy mới có thể lưu lại bất diệt chi hỏa, nếu không, kháng cự lời nói, ngọc thạch câu phần, toàn bộ linh hồn đều sẽ biến mất."),t("br"),t("br"),n._v("Đừng nói không cách nào chế tác kim nhân, chỉ sợ liền thi thể đều bảo tồn không xuống."),t("br"),t("br"),n._v("“Truyền thuyết, một chút thế lực lớn có thể nuôi dưỡng một chút cam tâm chịu chết tử sĩ, chỉ sợ những này kim nhân cũng là như vậy. Vì vu hồn sư nuôi, sứ mệnh liền là biến thành kim nhân, triệt để hiệu trung...” Nghĩ nửa ngày, trong lòng suy đoán."),t("br"),t("br"),n._v("Thế lực lớn có thể nuôi dưỡng tử sĩ, vu hồn sư chưa hẳn không thể, lại nói, loại nghề nghiệp này am hiểu linh hồn, đem người tu luyện tẩy não, để tâm hắn cam tình nguyện bị rút ra linh hồn cũng là có khả năng."),t("br"),t("br"),n._v("Kinh khủng!"),t("br"),t("br"),n._v("Quỷ dị!"),t("br"),t("br"),n._v("Lấy người sống luyện chế khôi lỗi, còn muốn cho tâm hắn cam tình nguyện, vu hồn sư quả nhiên đáng sợ."),t("br"),t("br"),n._v("Vốn còn nghĩ, mình muốn hay không cũng bắt vài đầu, luyện hóa tăng cao tu vi, nhìn thấy bọn hắn tàn nhẫn thủ đoạn, rùng mình một cái... Vẫn là thôi đi!"),t("br"),t("br"),n._v("Tiếp tục hướng xuống nhìn lại, rất mau đem cả bản thư tịch lật một lần."),t("br"),t("br"),n._v("Còn lại cũng không có cái gì đáng giá chú ý nội dung, cơ bản đều là giới thiệu Vô Hồn Kim Nhân nhược điểm, cùng phương pháp sử dụng."),t("br"),t("br"),n._v("“Đáng tiếc, ta không thể sử dụng...”"),t("br"),t("br"),n._v("Nhìn xong nội dung, Trương Huyền bất đắc dĩ lắc đầu."),t("br"),t("br"),n._v("Vu hồn sư am hiểu linh hồn, có thể linh hồn xuất thể, đối cái đồ chơi này tiến hành khống chế, mà hắn không được."),t("br"),t("br"),n._v("Tu luyện tới hiện tại, còn không có tiếp xúc qua liên quan tới linh hồn phương pháp tu luyện, nói cách khác... Mặc dù thực lực đạt đến nửa bước Hóa Phàm, linh hồn nhưng cùng thân thể hợp hai làm một, không cách nào ly thể đơn độc làm việc."),t("br"),t("br"),n._v("Cái này rất giống lái xe, muốn đem xa hành chạy mình muốn đi địa phương, người muốn trước đi vào, linh hồn không cách nào ly thể, cũng là vào không được, lại thế nào khả năng đối cái đồ chơi này tiến hành khống chế?"),t("br"),t("br"),n._v("Trước đó còn muốn lấy, mua được về sau, một khi có thể khống chế, chẳng khác nào có một cái Hóa Phàm cảnh tử sĩ, hiện tại xem ra, căn bản cũng không phải là chuyện này."),t("br"),t("br"),n._v("Linh hồn hắn giam cầm tại thân thể, không cách nào xuất thể, cũng là không có cách nào dùng, nói cách khác... Thứ này từ đầu đến cuối chỉ có thể nằm ở đây, cái gì dùng đều không có."),t("br"),t("br"),n._v("Đương nhiên, nếu quả thật gặp được nguy hiểm, ngược lại là có thể đem hắn tế ra đến, làm cái khiên thịt."),t("br"),t("br"),n._v("Khụ khụ, đấu giá lâu như vậy, tốn hao như thế lớn đại giới, chỉ mua cái lá chắn... Suy nghĩ một chút cũng là say."),t("br"),t("br"),n._v("“Thu!”"),t("br"),t("br"),n._v("Lại nhìn một hồi, lại không phát hiện cái gì tin tức hữu dụng, đem kim nhân thu vào trữ vật giới chỉ, ngay sau đó lòng bàn tay nhoáng một cái, một cái viên cầu xuất hiện."),t("br"),t("br"),n._v("Chính là đánh cược thua, Trần trưởng lão không có tiền trả lại, bồi thường cái kia Thiên Công Khéo Léo hộp."),t("br"),t("br"),n._v("“Nơi này có một chỗ thiếu hụt, nơi này cũng có, nơi này còn có...”"),t("br"),t("br"),n._v("Vuốt ve viên viên như là quả tạ đồng dạng đồ vật, ngón tay truyền vào chân khí, dựa theo trong tiệm sách ghi chép thiếu hụt từng cái nhấn tới."),t("br"),t("br"),n._v("Khuyết điểm tổng cộng mười bảy chỗ, rất nhanh lên một chút xong."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Một cái cơ lò xo đổ xuống thanh âm vang lên, trong tay viên cầu đột nhiên phát sinh biến hóa, tựa như hộp bị mở ra."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền giật nảy mình."),t("br"),t("br"),n._v("Thứ này từ bên ngoài nhìn vào, cùng quả tạ không khác, không có chút nào khe hở cùng kết hợp địa phương, trước đó cũng hoài nghi là cái cục sắt."),t("br"),t("br"),n._v("Nằm mơ đều không có nghĩ đến thế mà có thể mở ra."),t("br"),t("br"),n._v("“Quá tinh xảo...”"),t("br"),t("br"),n._v("Cúi đầu nhìn thoáng qua, nhịn không được tán thưởng: “Chẳng lẽ cái này cái gọi là Thiên Công viện, cùng Thú đường, luyện đan sư công hội đồng dạng, là Thiên Công sư hội tụ nơi chốn?”"),t("br"),t("br"),n._v("Đem Giám Bảo các thư tịch toàn bộ nhìn xong, mặc dù tri thức không thông hiểu đạo lí, nhưng cũng biết một cái rất lợi hại chức nghiệp —— Thiên Công sư!"),t("br"),t("br"),n._v("Loại nghề nghiệp này, không có quá cao võ lực, nghiên cứu chính là nhanh nhẹn linh hoạt, thuộc về hạ cửu lưu chức nghiệp."),t("br"),t("br"),n._v("Truyền thuyết có thể thiết kế trên đời này tinh mật nhất ám khí, cùng phức tạp nhất khôi lỗi, nhất là khôi giáp, luyện khí sư luyện chế mặc dù phù hợp thân thể, nhưng muốn phát huy càng lớn hiệu quả, liền cần tìm kiếm loại nghề nghiệp này người."),t("br"),t("br"),n._v("Vô số cường giả, luyện chế vũ khí, sẽ đem Thiên Công sư cùng luyện khí sư đồng thời mời tới, xin bọn họ tự mình thiết kế, thường thường một kiện binh khí bên trong, ẩn chứa vô số pháp môn, để cho người ta khó lòng phòng bị."),t("br"),t("br"),n._v("Không nói cái khác, Trịnh Dương cây kia trường thương, là bình thường nhất, cần từng đoạn từng đoạn chồng chất, phiền phức không gì sánh được. Nếu như Thiên Công sư xuất tay, trong đó sắp đặt cơ lò xo, cầm trong tay chỉ có dài bằng chiếc đũa ngắn, nhẹ nhàng nhấn một cái, liền có thể thay đổi dài hơn một trượng, uy lực vô cùng."),t("br"),n._v(" Còn có chính là cái này Thiên Công Khéo Léo hộp, trong tiệm sách ghi chép khuyết điểm, hẳn là hắn kết nối chỗ bạc nhược, chỉ có đụng vào những vật này, mới có thể triệt để mở ra."),t("br"),t("br"),n._v("Nếu không, coi như lực lượng mạnh hơn, cũng không thể tránh được, mà cưỡng ép công kích, chỉ có thể đem bên trong bảo tồn đồ vật triệt để hủy đi."),t("br"),t("br"),n._v("“Có thể sử dụng thứ này chứa đựng, đến cùng là cái gì trân quý vật phẩm?”"),t("br"),t("br"),n._v("Tiện tay đem Thiên Công Khéo Léo hộp kéo ra."),t("br"),t("br"),n._v("Mới vừa tiếp xúc thứ này thời điểm, Thiên Đạo thư viện liền cấp cho phán đoán, nói là “Thiên Công viện luyện chế mà thành, nhanh nhẹn linh hoạt vô song, không có riêng biệt phương pháp, không cách nào mở ra, có thể chứa đựng một chút quan trọng cơ mật bảo vật!”"),t("br"),t("br"),n._v("Đã dùng như thế cơ diệu đồ vật chứa đựng, khẳng định không đơn giản."),t("br"),t("br"),n._v("Mở ra hộp, bên trong chứa đựng một tấm thật mỏng da thú, không biết làm bằng vật liệu gì, dị thường cứng cỏi, giữ không biết đã bao nhiêu năm, thế mà hoàn hảo không chút tổn hại, không có chút nào hư hại."),t("br"),t("br"),n._v("Tiện tay lật ra."),t("br"),t("br"),n._v("“Hồng Trần Đạp Thiên bộ? Đây là vật gì?”"),t("br"),t("br"),n._v("Da thú phía trước nhất viết năm cái chữ lớn “Hồng Trần Đạp Thiên bộ”, để cho người ta xem không hiểu có ý tứ gì."),t("br"),t("br"),n._v("Tiếp tục hướng xuống nhìn lại, một nhóm câu thơ xuất hiện tại tầm mắt."),t("br"),t("br"),n._v("“Hồng trần ba ngàn dặm, cười một tiếng đạp trời xanh!”"),t("br"),t("br"),n._v("Câu thơ đằng sau, ký thuật thứ này lai lịch."),t("br"),t("br"),n._v("“Hồng Trần Đạp Thiên bộ, chính là Khổng sư môn sinh, Trọng Thánh sáng tạo, công thành ngày, gánh vác đại sơn, một bước ba ngàn dặm, bay lượn trên chín tầng trời. Ta mạch được Trọng Thánh chiếu cố, truyền Đạp Thiên bộ một quyển, lưu truyền hậu bối con cháu, hi vọng thiện đãi. Vì phòng ngừa biến cố, đặc biệt đặt ở Thiên Công Khéo Léo hộp bên trong, không phải ta hậu bối máu, không cách nào mở ra...”"),t("br"),t("br"),n._v("“Lại là bộ pháp?”"),t("br"),t("br"),n._v("Trương Huyền hiểu được, mắt sáng rực lên."),t("br"),t("br"),n._v("Khổng sư môn sinh sáng tạo, bộ công pháp này giá trị bao lớn, có thể nghĩ."),t("br"),t("br"),n._v("Khó trách muốn dùng Thiên Công Khéo Léo hộp chứa đựng."),t("br"),t("br"),n._v("Dựa theo đạo lý, thứ này thiết lập đặc thù mở ra phương pháp, cùng phía trên viết đồng dạng, không có bọn hắn một môn hậu bối huyết dịch, không cách nào mở ra. Trương Huyền tự nhiên không phải bọn hắn hậu bối, thậm chí cũng không biết cái môn này là cái gì."),t("br"),t("br"),n._v("Sở dĩ có thể mở ra, là bởi vì Thiên Đạo thư viện, tìm được thứ này thiếu hụt."),t("br"),t("br"),n._v("Vậy thì cùng kiếp trước mật mã đồng dạng, biết mật mã có thể mở ra, nếu như quên, đi vào nhân viên quản lý giới diện, dùng nhân viên quản lý mật mã, đồng dạng có thể mở ra."),t("br"),t("br"),n._v("Hắn hiện tại dùng chính là cái này."),t("br"),t("br"),n._v("Thư viện trực tiếp hiểu thấu đáo kỹ xảo hộp bản chất, theo căn bản ra tay, lại nghiêm mật, lại kỹ xảo, đều trở nên không ngừng một kích."),t("br"),t("br"),n._v("“Khổng sư môn sinh lưu lại công pháp, không biết có hay không thiếu hụt!”"),t("br"),t("br"),n._v("Cầm bí tịch, Trương Huyền thầm hô thiếu hụt."),t("br"),t("br"),n._v("Hiện tại mặc kệ công pháp gì, đều hình thành quen thuộc, xem trước một chút có hay không khuyết điểm."),t("br"),t("br"),n._v("Thứ này là Khổng sư môn sinh tự mình sáng tạo, cấp bậc cao có thể nghĩ, bất quá... Cấp bậc cao, khẳng định cũng có lỗ thủng, xem trước một chút lại nói."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Trong đầu xuất hiện đồng dạng một quyển sách, tiện tay lật ra."),t("br"),t("br"),n._v("“Chỉ có bốn mươi hai chỗ thiếu hụt?”"),t("br"),t("br"),n._v("Rất mau đem thư tịch lật hết, Trương Huyền líu lưỡi."),t("br"),t("br"),n._v("Không hổ là Khổng sư môn sinh sáng lập ra bộ pháp, thật là đáng sợ, chí ít có khoảng bốn mươi chỗ thiếu hụt, tại võ kỹ bên trong xem như tương đối ít."),t("br"),t("br"),n._v("Trước đó hắn nhìn thấy võ kỹ, cơ bản cũng phải có mấy trăm chỗ khuyết điểm, chính vì vậy, mỗi một bộ công pháp, đều cần hơn ngàn bản bí tịch để đền bù, mới có thể viên mãn như một, lại không lỗ thủng."),t("br"),t("br"),n._v("Võ kỹ này, liền năm mươi chỗ đều không có, đủ để chứng minh lợi hại, truyền đi tất nhiên gây nên gió tanh mưa máu."),t("br"),t("br"),n._v("“Có nhiều như vậy thiếu hụt, cũng không dám tu luyện ah...”"),t("br"),t("br"),n._v("Không biết khuyết điểm ngược lại cũng thôi, biết khuyết điểm, Trương Huyền mặt mũi tràn đầy mướp đắng."),t("br"),t("br"),n._v("Cái này rất giống hoa quả ăn thật ngon, khi thấy trong đó có vô số côn trùng bò loạn, cũng cảm giác lại không khẩu vị."),t("br"),t("br"),n._v("Tu luyện không khuyết điểm công pháp, võ kỹ quen thuộc, biết trong này có hơn bốn mươi chỗ, có chút khó mà nuốt xuống."),t("br"),t("br"),n._v("“Đúng rồi, Thiên Đạo thân pháp cũng là bộ pháp một loại, nhìn xem có thể hay không dung hợp lại cùng nhau!”"),t("br"),t("br"),n._v("Thiên Đạo thân pháp, là hắn sưu tập vô số bản bộ pháp, thân pháp bí tịch dung hợp mà thành, tuy chỉ là cơ sở, nhưng đại biểu đại đạo chí giản bí ẩn, có lẽ cả hai có thể dung hợp."),t("br"),t("br"),n._v("“Dung hợp, chính xác!”"),t("br"),t("br"),n._v("Đem hai quyển thư tịch đặt chung một chỗ, tinh thần lần nữa khẽ động."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một bản tân thư tịch xuất hiện."),t("br"),t("br"),n._v("Lần nữa lật ra."),t("br"),t("br"),n._v("“Chỉ có ba chỗ khuyết điểm? Quá tốt rồi, mặc dù chỉ có ba chỗ, đã có thể tu luyện...”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("Ba chỗ thiếu hụt mặc dù cùng trước đó công pháp so, cũng không ít, nhưng cũng đáng giá tu luyện, cùng lắm thì về sau lại nhiều sưu tập bộ pháp loại bí tịch, lần nữa nghĩ biện pháp dung hợp."),t("br"),t("br"),n._v("Biết có thể tu luyện, không đang do dự, hướng bí tịch nội dung nhìn sang."),t("br"),t("br"),n._v("Vừa nhìn phía dưới, con ngươi co rụt lại, bờ môi trong nháy mắt trắng bệch."),t("br"),t("br"),n._v("“Hồng Trần Đạp Thiên bộ —— Tề Thiên cuốn, chẳng lẽ là... Phi hành?”"),t("br"),t("br"),n._v("(Cảm tạ thư hữu  trở thành quyển sách thứ 26 vị minh chủ, thêm một canh, hôm nay ba canh.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-483"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-485"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);