(window.webpackJsonp=window.webpackJsonp||[]).push([[1726],{2082:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-476-muon-chi-điem-truong-huyen-tong-sieu-thuong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-476-muon-chi-điem-truong-huyen-tong-sieu-thuong"}},[n._v("#")]),n._v(" Chương 476: Muốn chỉ điểm Trương Huyền Tống Siêu (thượng)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 476: Muốn chỉ điểm Trương Huyền Tống Siêu (thượng)"),t("br"),t("br"),n._v("Tề Thiên, cùng trời đồng thời ngang."),t("br"),t("br"),n._v("Phổ thông người tu luyện, cho dù là Hóa Phàm cường giả, cũng không thể phi hành, chỉ có thể mượn nhờ phi hành man thú, chạy nhanh thiên hạ. Quyển công pháp này lại là mệnh danh Tề Thiên, chẳng phải cho thấy, có thể bay lên, cùng trời sóng vai?"),t("br"),t("br"),n._v("Phi hành, thế nhưng là mơ ước lớn nhất, dù là kiếp trước kiếp này, đều trong lòng còn có khát vọng, đột nhiên nhìn thấy nội dung, kích động trong lòng có thể nghĩ."),t("br"),t("br"),n._v("“Hồng trần cười một tiếng, cưỡi gió mà đi. Tề Thiên quyển, sau khi tu luyện thành, bay lượn trời cao, có thể cùng trời ngang...”"),t("br"),t("br"),n._v("Mở sách bản, nội dung đập vào mặt."),t("br"),t("br"),n._v("“Lợi hại...”"),t("br"),t("br"),n._v("Cẩn thận đọc nội dung trong đó, Trương Huyền cảm thấy đối tu luyện lý giải, bị trong nháy mắt mở ra."),t("br"),t("br"),n._v("Bay, Hóa Phàm cửu trọng cao thủ cũng chưa tới, truyền thuyết chỉ có đột phá cảnh giới này, mới có thể hoàn thành, mà phía trên này ghi lại, chỉ cần thật tốt tu luyện, Hóa Phàm nhất trọng liền có thể làm đến, thậm chí... Hắn loại này nửa bước Hóa Phàm cũng có thể hoàn thành!"),t("br"),t("br"),n._v("“Hóa Phàm, cảm ngộ tự nhiên, đối hoàn cảnh chung quanh có nhất định năng lực chưởng khống, mà đem loại năng lực này, chuyển hóa hội tụ, liền có thể đạp không mà đi, tựa như trèo lên bậc thang!”"),t("br"),t("br"),n._v("Lý giải ý tứ trong đó."),t("br"),t("br"),n._v("Đổi lại cái khác nửa bước Hóa Phàm, thâm ảo như vậy công pháp, coi như cho đều xem không hiểu, nhưng hắn không giống, Thiên Đạo thư viện tra ra thiếu hụt, trong đầu lại dung hợp nhiều như vậy tri thức, rất nhiều nơi mặc dù khó có thể lý giải được, nhưng cũng không tạo thành quá lớn khó khăn."),t("br"),t("br"),n._v("“Người rơi vào trong nước, có thể giẫm lên nước liên tục leo về phía trước, nước không thực chất, lại có thể mang người nổi lên... Không khí cũng có thể! Chỉ cần tìm đúng khống chế tự nhiên phương pháp, đạp trên không khí liên tục phi hành, không phải việc khó, cũng chính là cái gọi là lấy khí ngự khí...”"),t("br"),t("br"),n._v("Không biết qua bao lâu, Trương Huyền một ngụm trọc khí phun ra, hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("“Thế mà có thể sáng chế lợi hại như vậy võ kỹ, lợi hại!”"),t("br"),t("br"),n._v("Không hổ là Khổng sư môn sinh, đối với thiên địa tự nhiên lý giải, đã đạt đến làm cho người không thể tưởng tượng tình trạng."),t("br"),t("br"),n._v("Trước đó đã gặp rất nhiều võ kỹ, công pháp, tới so sánh, kém nhiều lắm, hoàn toàn không thể so sánh."),t("br"),t("br"),n._v("Nhìn xong cái này, thật giống như đẩy ra một cái thế giới mới, mở ra một cái tân đại môn, đối trước kia tu luyện võ kỹ, công pháp, cũng có càng sâu hiểu rõ."),t("br"),t("br"),n._v("“Đáng tiếc, đây chỉ là quyển thứ nhất, không biết phía sau sẽ cao minh đến mức nào!”"),t("br"),t("br"),n._v("Trong lòng cũng có chút tiếc hận."),t("br"),t("br"),n._v("Nhìn xong cũng đã biết, đây chỉ là Hồng Trần Đạp Thiên bộ khúc dạo đầu bộ phận, chỉ thích hợp Hóa Phàm cảnh người tu luyện, đợi đạt tới cảnh giới cao hơn, cơ bản liền không có gì dùng."),t("br"),t("br"),n._v("Bởi vì siêu việt Hóa Phàm, liền có thể phi hành, coi như lại tu luyện, cũng chỉ sẽ để cho tốc độ phi hành gia tăng một chút, tác dụng sẽ không rất lớn."),t("br"),t("br"),n._v("“Thử một chút...”"),t("br"),t("br"),n._v("Lại điều chỉnh một hồi, đem trọn cái Tề Thiên quyển nội dung cắt tỉa một lần, lúc này mới ngồi xếp bằng, dựa theo võ kỹ bên trong vận chuyển chân khí phương pháp, bắt đầu tu luyện."),t("br"),t("br"),n._v("Mặc dù chỉ là nửa bước Hóa Phàm, nhưng đối hoàn cảnh chung quanh đã có nhất định năng lực chưởng khống, tinh thần cao độ tập trung, lực lượng toàn thân tập trung ở một điểm."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Khoanh chân ngồi tại nguyên chỗ thân thể, chậm chạp trôi nổi mà lên, tựa như rơi vào đáy nước khí cầu, tự động hướng lên lơ lửng."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Duy trì không đến ba cái hô hấp, rớt xuống, miệng lớn thở hổn hển."),t("br"),t("br"),n._v("“Thật là khủng khiếp tiêu hao...”"),t("br"),t("br"),n._v("Trương Huyền vẻ mặt trắng bệch."),t("br"),t("br"),n._v("Chỉ ở không trung duy trì ba cái hô hấp, hắn còn kém không nhiều đem chân khí trong cơ thể tiêu hao sạch sẽ, cái này Hồng Trần Đạp Thiên bộ, nghe mạnh mẽ, có thể phi hành cũng rất uy phong, nhưng đối với chân khí tiêu hao cũng thực sự quá khoa trương."),t("br"),t("br"),n._v("Chỉ có thể dùng ngắn như vậy thời gian, hắn sợ mới vừa bay lên, còn không có làm ra hai cái động tác, liền sẽ tươi sống ngã xuống ngã chết."),t("br"),t("br"),n._v("“Nhìn tới... Là cái có chút vô bổ võ kỹ...”"),t("br"),t("br"),n._v("Một mặt mất tinh thần."),t("br"),t("br"),n._v("Vốn cho rằng học được về sau, có thể bay lượn thiên địa, tung hoành ngang dọc, hiện tại xem ra... Suy nghĩ nhiều!"),t("br"),t("br"),n._v("Chỉ có thể duy trì ba cái hô hấp phi hành, chỉ sợ mới bay lên, cũng bởi vì không có chân khí, tại chỗ ngã thành bánh thịt."),t("br"),t("br"),n._v("Cái này gọi chuyện gì, rõ ràng học được một cái trâu tới cực điểm võ kỹ, một khi cho người khác thi triển... Kết quả, còn không có nhảy cao, hơn nữa ba cái hô hấp phía sau lại bởi vì chân khí không tốt, ngã xuống... Được rồi, về sau vẫn là đừng khoe khoang, hắn sợ sẽ tươi sống ném chết."),t("br"),t("br"),n._v("Tiện tay lấy ra mấy khối linh thạch, điên cuồng hấp thu, sau nửa canh giờ, lúc này mới khôi phục lại."),t("br"),t("br"),n._v("Nói cho cùng, hay là hắn tu vi có chút thấp, chân khí số lượng dự trữ không đủ."),t("br"),t("br"),n._v("Nếu là tu vi lại cao hơn mấy cái cấp bậc, khả năng liền sẽ nhẹ nhõm rất nhiều, phi thiên nhập địa, cũng đều trở nên đơn giản."),t("br"),t("br"),n._v("Lại nghiên cứu một hồi, biết thực lực bây giờ, quả thực không thích hợp sử dụng võ kỹ này, Trương Huyền lúc này mới duỗi lưng một cái, đẩy cửa đi ra ngoài."),t("br"),t("br"),n._v("“Thiếu gia, bên ngoài có người nhất định phải gặp ngươi, đã bị ta đuổi đi ba lần, mỗi ngày còn không biết xấu hổ tới, ngươi nhìn... Có nên hay không nói cho Kim tiền bối, trực tiếp ném đi được rồi!”"),t("br"),t("br"),n._v("Vừa ra cửa chỉ thấy Tôn Cường vội vàng tiến lên đón, một mặt nổi giận đùng đùng."),t("br"),t("br"),n._v("  “Muốn gặp ta? Ai?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("Trong này hắn không có gì người quen, chạy tới tìm mình làm gì?"),t("br"),t("br"),n._v("“Không biết là ai, hỏi thật là nhiều lần, cũng không nói, chảnh chứ ngồi chém gió tự kỷ giống như, nhìn xem liền tức giận...” Tôn Cường khẽ nói."),t("br"),t("br"),n._v("“Chảnh?” Trương Huyền lại càng kỳ quái."),t("br"),t("br"),n._v("Trên thế giới này còn có so trước mắt vị này Cường ca còn chảnh chứ người?"),t("br"),t("br"),n._v("Có thể đem hắn tức thành như vậy, cũng coi như kỳ hoa."),t("br"),t("br"),n._v("“Bây giờ còn tại ư?” Nhịn không được hỏi."),t("br"),t("br"),n._v("“Tại!”"),t("br"),t("br"),n._v("“Mang ta nhìn xem!” Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Ừm, chính ở đằng kia phòng khách!” Tôn Cường một chỉ."),t("br"),n._v("Trại các chủ cho bọn hắn tìm chỗ ở, nói trắng ra là là cái độc tòa tiểu viện, bảy, tám gian phòng ngủ, cùng một cái không lớn phòng tiếp khách."),t("br"),t("br"),n._v("Đi tới phòng khách, quả nhiên thấy một thanh niên ngồi ngay ngắn trong đó, thanh niên phía sau là cái lão giả, toàn thân khí tức mờ mờ ảo ảo, cho thấy bất phàm tu vi."),t("br"),t("br"),n._v("Chính là số 23 phòng người thanh niên kia cùng hộ vệ của hắn lão giả."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Ngồi trong phòng, Tống Siêu có chút nôn nóng."),t("br"),t("br"),n._v("Ngày đó bị số một phòng tiệt hồ, trong lòng khó chịu, thầm tìm người nghe ngóng một phen, rốt cục làm rõ ràng."),t("br"),t("br"),n._v("Cùng Trại các chủ cùng nhau là cái danh sư, gọi là Trương Huyền, cụ thể thân phận không rõ lắm."),t("br"),t("br"),n._v("Nghe được là danh sư, lập tức nhẹ nhàng thở ra."),t("br"),t("br"),n._v("Đổi lại cái khác thân phận, hắn còn không thật nhiều nói, danh sư nha... Bản thân hắn đạt đến tứ tinh, vạn quốc liên minh đều tính toán cao cấp nhất, một cái không đủ hai mươi tiểu gia hỏa mà thôi, khẳng định không cấp bậc của hắn cao."),t("br"),t("br"),n._v("Danh sư, bối phận sâm nghiêm, Tinh cấp trong lúc đó, muốn lấy sư trưởng tôn xưng, đã đối phương cấp bậc thấp, vậy liền đơn giản."),t("br"),t("br"),n._v("Tràn đầy phấn khởi đi tìm đến, kết quả, liên tục ba chuyến đều không có nhìn thấy người ngược lại cũng thôi..."),t("br"),t("br"),n._v("Mấu chốt nhất là hắn cái kia quản gia, trực tiếp coi hắn là thành lừa đảo ra bên ngoài đuổi... Nếu không phải băn khoăn thân phận, khẳng định đã sớm tươi sống đem hắn quất chết."),t("br"),t("br"),n._v("“Thiếu gia, ngươi nói... Hắn sẽ bán cho chúng ta ư?”"),t("br"),t("br"),n._v("Nhìn thấy thiếu gia trong mắt mang theo buồn bực, hộ vệ lão giả nhịn không được nói."),t("br"),t("br"),n._v("Lần này không xa vạn dặm tới, liền là nghe được Vô Hồn Kim Nhân tin tức, kết quả đấu giá hội không cạnh tranh qua đối phương. Hiện tại nghe ngóng hết rồi, muốn từ trong tay đối phương mua xuống... Nhân gia tốn hao đại giới mua, làm sao có thể bán ra?"),t("br"),t("br"),n._v("“Hừ, không bán ra cũng muốn bán ra, chỉ cần hắn là danh sư, ta liền có biện pháp, để hắn bán cho ta!” Bàn tay đỡ tại cái ghế cầm trên tay, Tống Siêu khuôn mặt lạnh lùng."),t("br"),t("br"),n._v("“Chẳng lẽ thiếu gia có biện pháp?” Hộ vệ lão giả ánh mắt sáng lên."),t("br"),t("br"),n._v("“Đương nhiên, ta là tứ tinh danh sư, nhìn ra hắn trong tu luyện một vài vấn đề, vẫn là rất đơn giản, sau đó chỉ điểm một chút, không tin không ngoan ngoãn đem đồ vật giao ra...”"),t("br"),t("br"),n._v("Khóe miệng vung lên."),t("br"),t("br"),n._v("Chỉ cần tu luyện, liền không khả năng không có vấn đề, hắn đường đường thiên tài tứ tinh danh sư, chỉ điểm một cái không đủ hai mươi tiểu nhân vật tu luyện, vẫn là rất đơn giản."),t("br"),t("br"),n._v("Một khi chỉ điểm đột phá, còn không mang ơn, đừng nói Vô Hồn Kim Nhân, coi như tại chỗ bái sư, cũng không thành vấn đề."),t("br"),t("br"),n._v("“Như thế...”"),t("br"),t("br"),n._v("Lão giả sửng sốt một chút, nhịn không được gật đầu."),t("br"),t("br"),n._v("Danh sư quả thực có loại năng lực này cùng mị lực, mình cam tâm tình nguyện thủ hộ tại thiếu gia phía sau, loại trừ gia tộc ân tình, càng quan trọng hơn cũng là bởi vì cái này."),t("br"),t("br"),n._v("Đi theo danh sư phía sau, thường xuyên lắng nghe, trên việc tu luyện có thể ít chạy không ít đường quanh co, trên việc tu luyện làm ít công to."),t("br"),t("br"),n._v("“Thiếu gia, liền là bọn hắn muốn gặp ngươi!”"),t("br"),t("br"),n._v("Đang muốn hôm nay đến cùng có thể hay không nhìn thấy vị này Trương sư, liền nghe đến thanh âm của quản gia vang lên, hai người đi tới."),t("br"),t("br"),n._v("Đi đầu một cái niên cấp không lớn, thoạt nhìn không đến hai mươi, thoạt nhìn hẳn là trong truyền thuyết vị kia Trương Huyền."),t("br"),t("br"),n._v("“Các ngươi tìm ta?”"),t("br"),t("br"),n._v("Đi vào gian phòng, Trương Huyền không hiểu ra sao, hai người này, chưa bao giờ thấy qua, tìm hắn làm gì?"),t("br"),t("br"),n._v("“Không sai, tại hạ là tứ tinh danh sư Tống Siêu!” Tống Siêu đứng dậy, bàn tay lớn vẫy một cái."),t("br"),t("br"),n._v("“Tứ tinh danh sư?” Trương Huyền sửng sốt một chút, liền ôm quyền: “A, Trương Huyền gặp qua Tống sư!”"),t("br"),t("br"),n._v("Không nghĩ tới kẻ trước mắt này thoạt nhìn không đến ba mươi, đã là tứ tinh danh sư! Không hổ là vạn quốc liên minh, quả nhiên nhân tài đông đúc."),t("br"),t("br"),n._v("“Ừm!” Thấy hắn thái độ không sai, Tống Siêu hài lòng gật đầu, đang muốn bày ra cao nhân khí chất, cho hắn chỉ điểm, để hắn thuyết phục, liền nghe đến lại một cái tiếng bước chân truyền đến."),t("br"),t("br"),n._v("“Trương sư, ngươi xuất quan, thứ ngươi muốn, đã toàn bộ sưu tập tốt...”"),t("br"),t("br"),n._v("Trại các chủ đi đến."),t("br"),t("br"),n._v("Đi vào phòng, thấy được Tống Siêu, Trại các chủ ôm quyền: “Gặp qua Tống sư!”"),t("br"),t("br"),n._v("Cái tên này hai ngày này nghe ngóng tin tức, cũng cùng hắn đã gặp mặt, đã sớm đã gặp mặt."),t("br"),t("br"),n._v("Nói một tiếng, Trại các chủ không tiếp tục để ý, một mặt hưng phấn đi tới Trương Huyền trước mặt, tràn đầy nịnh nọt đưa tới một cái trữ vật giới chỉ."),t("br"),t("br"),n._v("“Sưu tập đủ, nhanh như vậy?”"),t("br"),t("br"),n._v("Không phải nói muốn ba ngày ư? Lúc này mới hơn hai ngày điểm, liền toàn bộ tìm đủ?"),t("br"),t("br"),n._v("Tiếp nhận nhẫn, Trương Huyền tinh thần quét qua, quả nhiên phát hiện bên trong tất cả đều là thư tịch, công pháp, võ kỹ từng quyển từng quyển, từng loại, cộng lại chừng hơn vạn bản nhiều."),t("br"),t("br"),n._v("Có Hóa Phàm nhất trọng, nhị trọng cùng tam trọng."),t("br"),t("br"),n._v("Chẳng qua số lượng cùng hắn lần trước nói không kém nhiều, liên tục giảm dần."),t("br"),t("br"),n._v("Dù vậy, cũng không tệ."),t("br"),t("br"),n._v("Xem ra hẳn là có thể hoàn mỹ lấy ra Hóa Phàm nhất trọng Tăng Thọ cảnh công pháp."),t("br"),t("br"),n._v("“Làm tốt lắm!”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền tán dương âm thầm gật đầu."),t("br"),t("br"),n._v("“Đáp ứng ngươi, đương nhiên phải nhanh chút...”"),t("br"),t("br"),n._v("Lấy được tán dương, Trại các chủ lên tiếng, tiếp lấy cổ tay khẽ đảo, lấy ra một cái thân phận lệnh bài: “Trương sư, ngươi thông qua Lưu Thương Khúc Thủy sự tình, ta báo lên tổng bộ, đây là tổng bộ ban cho ngũ tinh giám bảo sư huy chương...”"),t("br"),t("br"),n._v("“Ngũ tinh huy chương?” Trương Huyền tiếp nhận, quả nhiên thấy phía trên năm ngôi sao tinh sáng sủa chói mắt."),t("br"),t("br"),n._v("Xông cái Lưu Thương Khúc Thủy, liền lấy đến ngũ tinh giám bảo sư thân phận, thế nào cảm giác không thể tin được?"),t("br"),t("br"),n._v("Giám bảo sư khảo hạch cũng không tránh khỏi quá dễ dàng đi!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-484"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-486"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);