(window.webpackJsonp=window.webpackJsonp||[]).push([[1727],{2081:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-477-muon-chi-điem-truong-huyen-tong-sieu-ha"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-477-muon-chi-điem-truong-huyen-tong-sieu-ha"}},[n._v("#")]),n._v(" Chương 477: Muốn chỉ điểm Trương Huyền Tống Siêu (hạ)")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Chương 477: Muốn chỉ điểm Trương Huyền Tống Siêu (hạ)"),i("br"),i("br"),n._v("“Lưu Thương Khúc Thủy là khảo hạch ngũ tinh giám bảo sư cửa ải, độ khó cực lớn, có thể giám định ra đồng dạng coi như thông qua. Ngươi giám định ra ba loại, thậm chí liền... Lưu Thương Khúc Thủy bàn điều khiển đều muốn nhận chủ, lại liền ngũ tinh giám bảo sư đều thi không đậu, thật sự nguy rồi...”"),i("br"),i("br"),n._v("Nhìn ra ý nghĩ của hắn, Trại các chủ cười khổ."),i("br"),i("br"),n._v("Nói thật, hắn thấy, coi như ban cho ngũ tinh giám bảo sư, cấp bậc đều cảm thấy thấp."),i("br"),i("br"),n._v("Nhờ vào giám định, để bảo vật tự động nhận chủ, đây ít nhất là lục tinh trở lên giám bảo sư mới có năng lực."),i("br"),i("br"),n._v("Đương nhiên, hắn cái này Giám Bảo các cấp bậc quá thấp, còn không cách nào xin cao như vậy cấp bậc, nhiều nhất chỉ là cái ngũ tinh."),i("br"),i("br"),n._v("“Giám bảo sư không yêu cầu tu vi?” Trương Huyền nhịn không được hỏi."),i("br"),i("br"),n._v("Danh sư, luyện đan sư, tuần thú sư, y sư... Những này thượng cửu lưu đặc thù chức nghiệp, đối tu vi yêu cầu rất lớn, cũng không đủ tu vi, coi như trình độ lại cao hơn, cũng cơ bản vô duyên."),i("br"),i("br"),n._v("Thật giống như hắn tại Thiên Vũ vương quốc Y Sư công hội làm ra động tĩnh lớn như vậy, cuối cùng cũng chỉ là cho cái vạn quốc liên minh Y Sư công hội thân phận trưởng lão, cụ thể đẳng cấp cũng không xác định."),i("br"),i("br"),n._v("Đây trực tiếp cho ngũ tinh giám bảo sư cấp bậc, có thể hay không quá trò đùa?"),i("br"),i("br"),n._v("“Giám bảo sư mục đích là giám định bảo vật, đối tu vi không có cái gì cứng nhắc yêu cầu. Kỳ thật, rất nhiều trung cửu lưu, hạ cửu lưu chức nghiệp, đều là như thế, chỉ có thượng cửu lưu chức nghiệp đối tu vi yêu cầu khắc nghiệt!” Trại các chủ giải thích."),i("br"),i("br"),n._v("Trương Huyền gật đầu."),i("br"),i("br"),n._v("Hai người cười cười nói nói, bên kia Tống Siêu chỉ cảm thấy hai mắt biến thành màu đen, gần tức chết."),i("br"),i("br"),n._v("Bất kể nói thế nào, hắn đều là tứ tinh danh sư, vạn quốc liên minh địa vị cao nhất tồn tại, một câu gặp qua Tống sư, không để ý tới ngược lại cũng thôi... Trại các chủ cũng là một câu, tiếp lấy cười cười nói nói, nói chuyện phiếm đi, đối với hắn coi thường..."),i("br"),i("br"),n._v("Còn có hay không để hắn vào trong mắt?"),i("br"),i("br"),n._v("Vốn cho rằng trước đó quản gia đuổi hắn rời khỏi, là đầu óc có vấn đề, trời sinh hai như vậy, hiện tại xem ra... Thật đúng là có chủ nhân gì mới có cái gì hạ nhân!"),i("br"),i("br"),n._v("Một cái đường đường tứ tinh danh sư ngồi ở chỗ này, chính các ngươi nhưng nói chuyện trời đất, chẳng lẽ một điểm tôn trọng đều không có à..."),i("br"),i("br"),n._v("“Hừ!” Cũng nhịn không được nữa, Tống Siêu hất lên ống tay áo, đầu lâu vung lên, hai tay chắp sau lưng: “Trương sư, Trại các chủ, tứ tinh danh sư tại đây, đây chính là các ngươi đạo đãi khách...”"),i("br"),i("br"),n._v("Tiếng nói còn không có kết thúc, liền nghe đến hộ vệ lão giả tràn đầy lúng túng thanh âm vang lên: “Thiếu gia, nhân gia đi...”"),i("br"),i("br"),n._v("“Đi?”"),i("br"),i("br"),n._v("Sửng sốt một chút, vội vàng quay đầu nhìn lại, quả nhiên thấy cái kia Trương Huyền cùng Trại các chủ, vừa nói vừa cười đi ra khỏi phòng."),i("br"),i("br"),n._v("Công pháp, võ kỹ tới tay, Trương Huyền dự định hiện tại liền trở về hội tụ Thiên Đạo công pháp, xung kích Hóa Phàm nhất trọng, nào sẽ trong này dừng lại thêm."),i("br"),i("br"),n._v("“Ngươi...”"),i("br"),i("br"),n._v("Tống Siêu gần thổ huyết."),i("br"),i("br"),n._v("Coi thường ngược lại cũng thôi, lời nói đều không có nói, xoay người rời đi, lại đem hắn một cái đường đường tứ tinh danh sư quên... Đáng giận!"),i("br"),i("br"),n._v("Là trái tim cũng quá lớn, hay là hắn người danh sư này, một điểm trọng lượng đều không có?"),i("br"),i("br"),n._v("“Đứng lại cho ta...”"),i("br"),i("br"),n._v("Một tiếng gầm thét, cũng nhịn không được nữa, thân thể nhoáng một cái, cả người như là như ảo ảnh, bay ra khoảng cách mấy chục mét, ngăn tại hai người trước mặt."),i("br"),i("br"),n._v("Hơi sử dụng võ kỹ, lập tức cho thấy tu vi, lại là Hóa Phàm tam trọng Âm Dương cảnh sơ kỳ cường giả."),i("br"),i("br"),n._v("“Trương sư, ngươi cũng đã biết ngươi tu luyện công pháp, xảy ra vấn đề? Ngàn cân treo sợi tóc, còn không tự biết?”"),i("br"),i("br"),n._v("Cố nén tức giận, Tống Siêu biết chiến đấu là không thể giải quyết vấn đề, nhìn qua, một bộ cao nhân bộ dáng."),i("br"),i("br"),n._v("“Ta tu luyện công pháp?”"),i("br"),i("br"),n._v("Hắn tu luyện công pháp là Thiên Đạo thư viện sàng chọn, không có một chỗ sai lầm, làm sao có thể xảy ra vấn đề?"),i("br"),i("br"),n._v("Bất quá, sửng sốt một chút, Trương Huyền liền hiểu được."),i("br"),i("br"),n._v("Chỉ cần tu luyện, khẳng định sẽ xuất hiện như vậy vấn đề như vậy, không thể tránh né, cái tên này hẳn là dùng ngôn ngữ lừa dối mình, sau đó lại nghĩ biện pháp tìm ra công pháp lỗ thủng, giúp mình chỉ điểm."),i("br"),i("br"),n._v("Không ít danh sư cưỡng ép thu đồ, là làm như vậy."),i("br"),i("br"),n._v("Thậm chí liền hắn, cũng từng làm như thế, lúc trước Vương Dĩnh đám người chính là như vậy bị dao động."),i("br"),i("br"),n._v("“Không sai, nếu như ngươi muốn đột phá cảnh giới bây giờ, ta có thể giúp ngươi chỉ điểm, để ngươi ít đi đường quanh co!”"),i("br"),i("br"),n._v("Thấy hắn mắc câu, Tống Siêu trong lòng đắc ý, trên mặt lộ ra thành khẩn chi sắc, đang muốn nói ra một chút trên việc tu luyện phổ biến vấn đề, hù dọa đối phương, chỉ thấy thanh niên trước mắt, mày nhăn lại dọc theo hắn dạo qua một vòng."),i("br"),i("br"),n._v("“Tống sư, ngươi có phải hay không gần nhất rất cảm thấy  có chút suy nhược, tinh thần rất khó tập trung?”"),i("br"),i("br"),n._v("“Ta đang nói vấn đề của ngươi, ngươi...” Đang muốn quát lớn đối phương, nghe rõ ràng lời nói nội dung, Tống Siêu thân thể cứng đờ: “Làm sao ngươi biết?”"),i("br"),i("br"),n._v("Gần nhất tu luyện, thật sự là hắn rất cảm thấy Hối Hải huyệt, chân khí không có cách nào hội tụ, có loại xé rách đau đớn, Bách Trì huyệt cũng có chút suy nhược, cả người tinh thần không cách nào tập trung."),i("br"),i("br"),n._v("Chính vì vậy, hắn mới chạy đến giải sầu, trong lúc vô tình nghe được Vô Hồn Kim Nhân tin tức, lại tới đây, nếu không, đường đường thiên tài tứ tinh danh sư, nào có nhiều như vậy thời gian rỗi, chạy loạn khắp nơi?"),i("br"),i("br"),n._v("“Không chỉ có như thế, ngươi lúc tu luyện, có phải hay không còn cảm thấy chân trái hơi tê tê, chân phải thường xuyên run rẩy?”"),i("br"),i("br"),n._v("Trương Huyền nói tiếp."),i("br"),i("br"),n._v("“Ta...” Tống Siêu khuôn mặt trắng bệch."),i("br"),i("br"),n._v("Một điểm không sai."),i("br"),i("br"),n._v("“Nếu như ta không nhìn lầm, ngươi tu luyện hẳn là , tu luyện không sai, công pháp cũng không yếu, đến linh phẩm trung cấp, đáng tiếc... Ba tháng trước cưỡng ép tăng thực lực lên, xung kích tứ tinh danh sư, xuất hiện chỗ sơ suất. Dẫn đến Hàn Tố kinh tắc nghẽn, chân trái run lên, Bách Trì huyệt suy nhược. Hồng Thường kinh đứt gãy, chân phải run rẩy, Hối Hải huyệt cảm giác đau đớn!”"),i("br"),i("br"),n._v("Trương Huyền thản nhiên nói."),i("br"),n._v(" Cái tên này vừa rồi sử dụng võ kỹ xông lại chặn đường, trong đầu tạo thành sách báo, đem thiếu hụt nhìn rõ ràng."),i("br"),i("br"),n._v("“Cái này... Nhưng có phương pháp giải quyết?”"),i("br"),i("br"),n._v("Tống Siêu nhoáng một cái, vội vàng nhìn qua."),i("br"),i("br"),n._v("Đối phương nói những này, đích xác là hắn tình huống hiện tại, nhưng kinh mạch tắc nghẽn, đứt gãy, cũng không có biện pháp tốt, chỉ có thể chờ đợi thời gian trôi qua chậm rãi khôi phục, ngay cả lão sư hắn, vị kia nửa bước ngũ tinh danh sư, cũng không thể giải quyết."),i("br"),i("br"),n._v("Vốn cho rằng, chỉ có thể làm như vậy chờ lấy, không nghĩ tới thanh niên này nói thẳng ra, không có sai lầm chút nào... Đến cùng làm sao nhìn ra được?"),i("br"),i("br"),n._v("“Năm ngàn linh thạch, giúp ngươi giải quyết, thuận tiện để ngươi đột phá cảnh giới!” Trương Huyền thản nhiên nói."),i("br"),i("br"),n._v("“Năm ngàn?” Tống Siêu chần chờ."),i("br"),i("br"),n._v("“Ngươi vấn đề này, nếu như ta không nhìn lầm, ngươi đi tìm những người khác, đáng tiếc, đều không thể giải quyết, không dùng phương pháp của ta, ngươi ít nhất phải khôi phục một năm. Một thiên tài một năm không cách nào tu luyện, hậu quả gì, có thể nghĩ!” Trương Huyền cũng không thúc giục."),i("br"),i("br"),n._v("“Tốt, đây là linh thạch, còn xin Trương sư chỉ điểm!” Không chần chờ bao lâu, Tống Siêu một cắn răng."),i("br"),i("br"),n._v("Cổ tay khẽ đảo, một đống linh thạch rơi trên mặt đất, lập tức linh khí hội tụ, khí tức bức người."),i("br"),i("br"),n._v("“Ừm!” Trương Huyền hài lòng gật đầu, bàn tay duỗi ra đem linh thạch thu vào trữ vật giới chỉ: “Phương pháp rất đơn giản, ngươi cần đem chân khí, dọc theo kinh mạch chảy xuôi...”"),i("br"),i("br"),n._v("“Tiện tay chỉ điểm tứ tinh danh sư? Vậy vị này Trương sư, đến cùng cấp bậc gì...”"),i("br"),i("br"),n._v("Trại các chủ trừng to mắt."),i("br"),i("br"),n._v("Sớm biết vị này Trương sư không đơn giản, không nghĩ tới lợi hại như vậy."),i("br"),i("br"),n._v("Vị này Tống Siêu thế nhưng là tứ tinh danh sư, bị nói thẳng ra trong tu luyện vấn đề, tiến hành chỉ điểm, thực lực cao, để cho người ta bái phục."),i("br"),i("br"),n._v("Không chỉ hắn, trong phòng cái kia hộ vệ lão giả, càng là toàn thân cứng ngắc, bệnh suyễn đều nhanh phát tác."),i("br"),i("br"),n._v("Thiếu gia đi ra ngoài muốn cho đối phương chỉ điểm, làm cho đối phương cảm kích, tốt thừa cơ lấy được Vô Hồn Kim Nhân, thế nào một câu xuống, biến thành tiếp nhận đối phương chỉ điểm, còn giao tiền?"),i("br"),i("br"),n._v("Muốn hay không như thế kéo?"),i("br"),i("br"),n._v("Cho dù tận mắt nhìn thấy, đều cảm thấy gần điên rồi."),i("br"),i("br"),n._v("“... Tốt, liền theo ta nói tu luyện, bảo đảm ngươi ba ngày, bệnh gì diệt hết!”"),i("br"),i("br"),n._v("Bên này hai người dọa đến nói không ra lời, bên kia Trương Huyền chỉ điểm hết rồi vấn đề, thản nhiên nói."),i("br"),i("br"),n._v("“Đa tạ Trương sư chỉ điểm...”"),i("br"),i("br"),n._v("Đem đối phương nói phương pháp ở trong lòng nhớ lại một lần, Tống Siêu lòng tràn đầy rung động, không tự chủ được cảm kích ôm quyền."),i("br"),i("br"),n._v("“Ừm!”"),i("br"),i("br"),n._v("Trương Huyền gật đầu tiếp tục đi đến phía trước."),i("br"),i("br"),n._v("Đối phương điểm ấy trong vấn đề tu luyện, đổi lại cái khác danh sư, quả thực khó mà nhìn ra, chỉ khi nào tìm tới nguyên nhân, giải quyết liền sẽ đơn giản rất nhiều, trước mắt vị này nếu là tứ tinh danh sư, chắc hẳn cũng có thể biết mình nói thật hay giả."),i("br"),i("br"),n._v("“Thiếu gia... Ngươi không phải muốn hướng hắn yêu cầu Vô Hồn Kim Nhân ư? Thế nào ngược lại cho hắn tiền...”"),i("br"),i("br"),n._v("Trương Huyền rời khỏi, lão giả vội vàng đi tới trước mặt."),i("br"),i("br"),n._v("“A?”"),i("br"),i("br"),n._v("Theo nhớ lại phương pháp tu luyện bên trong tỉnh táo lại, Tống Siêu lúc này mới kịp phản ứng."),i("br"),i("br"),n._v("Vừa rồi chẳng biết tại sao nghe được đối phương nói trúng mấu chốt, liền không nhịn được rơi vào loại này mê hoặc hoàn cảnh."),i("br"),i("br"),n._v("“Là... Sư ngôn thiên bẩm? Hắn đối ta thi triển sư ngôn thiên bẩm? Ta vậy mà trúng chiêu...”"),i("br"),i("br"),n._v("Khóe miệng giật một cái, nhớ tới cái gì, Tống Siêu thiếu chút nữa ngất đi."),i("br"),i("br"),n._v("Coi như nghe được đối phương, nói chuẩn xác không hai, dựa theo đạo lý, hắn cũng không nên nói gì nghe nấy, thậm chí giao tiền, xuất hiện loại tình huống này nguyên nhân chủ yếu... Lại là đối phương đối với hắn thi triển sư ngôn thiên bẩm!"),i("br"),i("br"),n._v("Đây là danh sư năng lực đặc thù, chỉ cần nói chính xác, không chỉ có thể dẫn động chân khí trong cơ thể biến hóa, còn có thể để cho người ta một cách tự nhiên sinh ra tin phục, không dám không phản bác."),i("br"),i("br"),n._v("Đường đường tứ tinh danh sư, vốn là muốn đi cho người ta chỉ điểm, kết quả nhưng trúng đối phương sư ngôn thiên bẩm... Tống Siêu cảm thấy gần phát điên."),i("br"),i("br"),n._v("Bất quá, sư ngôn thiên bẩm đối tâm cảnh yêu cầu rất cao, mình đường đường tứ tinh danh sư, tâm cảnh khắc độ đạt đến 12. 0, để cho mình nói gì nghe nấy, không dám phản bác, chẳng phải đại biểu tâm cảnh của hắn khắc độ, so với chính mình chỉ cao hơn chứ không thấp hơn?"),i("br"),i("br"),n._v("Thật hay giả?"),i("br"),i("br"),n._v("“Hơn nữa, chỉ có sư ngôn thiên bẩm còn chưa đủ... Càng quan trọng hơn là tiền bối tán thành! Trải qua Danh Sư đường tán thành tiền bối số lượng nhiều, sẽ tự động sinh ra một loại làm cho người tin phục lực lượng... Chẳng lẽ cái tên này so ta được đến tiền bối tán thành còn nhiều?”"),i("br"),i("br"),n._v("Nắm đấm lần nữa căng thẳng."),i("br"),i("br"),n._v("Danh Sư đường thu hoạch được tiền bối công nhận danh sư, lấy được công nhận số lượng càng nhiều, về sau tiềm lực cũng là càng lớn, trên người cũng sẽ có một loại để cho người ta trời sinh tin phục khí chất, danh sư đối loại khí chất này càng thêm mẫn cảm, rất dễ dàng tin vào lời nói."),i("br"),i("br"),n._v("Đây cũng là vì cái gì nói, lấy được tiền bối tán thành, coi như không có danh sư huy chương, người khác cũng biết thân phận nguyên nhân chủ yếu."),i("br"),i("br"),n._v("Hắn niên thiếu thiên tài, bị tông môn phái tới tham gia danh sư thi đấu, có thể thấy được thiên phú, tiền bối công nhận thời điểm, càng là đạt đến chín cái nhiều... Nhân vật như vậy, bị người một câu nói mê mang, tin tưởng không nghi ngờ, chẳng phải bày tỏ đối phương lấy được danh sư tán thành so với hắn còn nhiều?"),i("br"),i("br"),n._v("Đây, cái này..."),i("br"),i("br"),n._v("Cái này sao có thể?"),i("br"),i("br"),n._v("Cái tên này đến cùng từ đâu xuất hiện, làm sao lại cường đại như vậy?"),i("br"),i("br"),n._v("Tống Siêu mộng."),i("br"),i("br"),n._v("(Làm gốc thư thứ 26 vị minh chủ  tăng thêm, rất nhai có bộc phát, mọi người, nguyệt phiếu đây? Mặt khác gửi cho bạn bè một quyển sách 《 hoa đều thấu thị Tiên thầy thuốc 》, thư hoang có thể nhìn xem.)"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-485"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-487"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);