(window.webpackJsonp=window.webpackJsonp||[]).push([[1728],{2083:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-478-tran-phap-su-cong-hoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-478-tran-phap-su-cong-hoi"}},[n._v("#")]),n._v(" Chương 478: Trận pháp sư công hội")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 478: Trận pháp sư công hội"),t("br"),t("br"),n._v("“Thiếu gia, ngươi... Vô Hồn Kim Nhân sự tình hỏi ư?”"),t("br"),t("br"),n._v("Thấy thiếu gia thân thể run rẩy, đần độn tại chỗ, lão giả biết tiếp tục trong vấn đề này xoắn xuýt đi xuống, khẳng định phải nổi điên, vội vàng nói sang chuyện khác."),t("br"),t("br"),n._v("“A? Ta... Quên!”"),t("br"),t("br"),n._v("Tống Siêu nhoáng một cái."),t("br"),t("br"),n._v("Vừa thấy mặt, mới nói một câu, đối phương liền chỉ ra hắn trên việc tu luyện mấu chốt cùng vấn đề, sau đó... Liền lâm vào nói thế công bên trong, đã mất đi bản thân."),t("br"),t("br"),n._v("Đừng nói Vô Hồn Kim Nhân, ngay cả lời đều không có nói thế nào."),t("br"),t("br"),n._v("Hỏi thế nào?"),t("br"),t("br"),n._v("“Cái kia... Ta đi hỏi một chút...”"),t("br"),t("br"),n._v("Lão giả nhịn không được nói."),t("br"),t("br"),n._v("“Ừm, đi thôi... Cẩn thận chút!” Chần chờ một lát, Tống Siêu gật đầu."),t("br"),t("br"),n._v("Hắn hiện tại hơi sợ, sợ chạy tới, lần nữa bị đối phương nói mê hoặc, ngoan ngoãn nghe lời, còn không bằng để cái này hạ nhân đi qua."),t("br"),t("br"),n._v("Bất quá... Đối phương có thể sử dụng sư ngôn thiên bẩm cho mình giảng bài, lại có thể nhìn ra hắn vấn đề vị trí, danh sư cấp bậc không thể so với hắn thấp, loại người này lấy được Vô Hồn Kim Nhân, khẳng định phải mình nghiên cứu, làm sao có thể chuyển tay?"),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Lão giả gật đầu, vội vàng mấy bước đi tới đã đi xa một chút Trương Huyền trước mặt: “Trương sư, chúng ta muốn mua sắm đầu kia Vô Hồn Kim Nhân, không biết... Ngươi có hứng thú hay không bán ra? Nhà chúng ta thiếu gia nguyện ý ra hai cái nửa bước ngũ tinh danh sư chương trình học ngọc phù!”"),t("br"),t("br"),n._v("“Bốn vạn linh thạch!” Trương Huyền dừng lại."),t("br"),t("br"),n._v("“Cái giá tiền này đã rất cao, nửa bước ngũ tinh danh sư chương trình học ngọc phù, thiếu gia của chúng ta tổng cộng cũng chỉ có ba cái... Các loại, ngươi nói cái gì?”"),t("br"),t("br"),n._v("Vốn cho rằng Trương sư sẽ trực tiếp cự tuyệt, không nghĩ tới một câu phản bác đều không có, trực tiếp cho ra giá cả, lão giả sững sờ: “Trương sư nói bốn vạn linh thạch... Bán ra?”"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Lời còn chưa dứt, thấy hoa mắt, một đầu Vô Hồn Kim Nhân liền xuất hiện trên mặt đất."),t("br"),t("br"),n._v("“Xuất ra linh thạch, kim nhân lấy đi, không chấp nhận chương trình học ngọc phù, không có... Xin rời khỏi!”"),t("br"),t("br"),n._v("Trương Huyền vẫy tay."),t("br"),t("br"),n._v("Hắn sốt ruột trở về nghiên cứu Thiên Đạo công pháp, nào có thời gian cùng đối phương nét bút."),t("br"),t("br"),n._v("Đã cái tên này muốn Vô Hồn Kim Nhân, dù sao thứ này hình thành thư tịch, cũng là cơ bản không có gì dùng, cùng hắn lưu tại trong tay đập, còn không bằng bán đi."),t("br"),t("br"),n._v("“... Đơn giản như vậy?”"),t("br"),t("br"),n._v("Phía sau Tống Siêu, vốn cho rằng lão giả tiến lên nói thẳng, khẳng định không được, nằm mơ đều không có nghĩ đến, đơn giản như vậy..."),t("br"),t("br"),n._v("Sớm biết cái tên này dự định bán, làm gì còn trang bức giống như muốn cho nhân gia chỉ điểm, cái này ngược lại tốt... Chỉ điểm không được, bị chỉ điểm..."),t("br"),t("br"),n._v("Đem mặt đều mất hết..."),t("br"),t("br"),n._v("Tống Siêu đều cảm thấy có chút điên."),t("br"),t("br"),n._v("Người khác, mặc kệ làm cái gì, làm danh sư, vẫn có thể nhìn ra một ít quy luật, nhưng trước mắt này gia hỏa, hoàn toàn không theo lẽ thường ra bài, để đầu hắn giống như là đâm vào trên tảng đá, từng đợt mê muội."),t("br"),t("br"),n._v("“Trương sư, thật dự định bốn vạn linh thạch đem đầu này Vô Hồn Kim Nhân bán cho ta?”"),t("br"),t("br"),n._v("Thực sự nhịn không được, đi tới trước mặt."),t("br"),t("br"),n._v("“Mua không nổi coi như xong!”"),t("br"),t("br"),n._v("Đem kim nhân thu hồi, Trương Huyền liền muốn rời khỏi."),t("br"),t("br"),n._v("“Ta mua...”"),t("br"),t("br"),n._v("Hơi đỏ mặt, Tống Siêu vội vàng mở lời."),t("br"),t("br"),n._v("Soạt!"),t("br"),t("br"),n._v("Tiếng nói kết thúc, một đống linh thạch xuất hiện ở trước mắt mọi người, không nhiều không ít, vừa vặn bốn vạn."),t("br"),t("br"),n._v("“Ừm, kim nhân là của ngươi!” Đem linh thạch lấy đi, tiện tay đem kim nhân ném ở đối phương bên người, Trương Huyền xoay người rời đi."),t("br"),t("br"),n._v("“Đa tạ Trương sư...”"),t("br"),t("br"),n._v("Tiếp nhận Vô Hồn Kim Nhân, Tống Siêu sợ xảy ra vấn đề, vội vàng đem hắn thu vào trữ vật giới chỉ, vừa đem đồ vật lấy được, đột nhiên sửng sốt một cái, kém chút không đập đầu chết."),t("br"),t("br"),n._v("“Lại trúng chiêu, của ta bốn vạn linh thạch...”"),t("br"),t("br"),n._v("Rất rõ ràng, lại trúng sư ngôn thiên bẩm, kìm lòng không được đem tiền đem ra..."),t("br"),t("br"),n._v("Những năm này tổng cộng cũng là gom góp tích luỹ như thế điểm, không nghĩ tới, đều bị đối phương ép khô... Muốn hay không như thế hố?"),t("br"),t("br"),n._v("Cái tên này đến cùng là cái gì quái vật..."),t("br"),t("br"),n._v("“Chúc mừng thiếu gia, mua được Vô Hồn Kim Nhân...” Lão giả ôm quyền."),t("br"),t("br"),n._v("“Cút!”"),t("br"),t("br"),n._v("Vốn là buồn bực, nghe được chúc mừng, kém chút nổ tung, Tống Siêu quát to một tiếng."),t("br"),t("br"),n._v("“Ây... Vâng!”"),t("br"),t("br"),n._v("Lão giả vò đầu, mặt mũi tràn đầy nghi hoặc, thiếu gia đây là thế nào? Đầu óc... Không xảy ra vấn đề đi..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Không nghĩ tới đây không biết từ nơi nào xuất hiện tứ tinh danh sư, một cái cống hiến bốn vạn linh thạch, Trương Huyền tràn đầy hưng phấn về đến phòng, cổ tay rung lên, từng dãy giá sách xuất hiện tại trước mặt."),t("br"),t("br"),n._v("Có nhiều linh thạch như vậy, tu luyện liền sẽ đơn giản không ít, cũng nên nhìn xem thư tịch, thuận tiện để tu vi thích hợp tăng lên một cái."),t("br"),t("br"),n._v("Ngón tay tại từng dãy thư tịch bên trên xẹt qua, trong đầu thêm ra tương đồng thư tịch."),t("br"),t("br"),n._v("Rất nhanh, Trại các chủ thu nhận sử dụng công pháp, võ kỹ liền bị thu sạch ghi chép vào Thiên Đạo thư viện."),t("br"),t("br"),n._v("“Chính xác!”"),t("br"),t("br"),n._v("Đem Hóa Phàm nhất trọng Tăng Thọ cảnh công pháp chất thành một đống, tinh thần khẽ động."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một bản mới tinh thư tịch xuất hiện tại trước mặt."),t("br"),t("br"),n._v("Tiện tay cầm lấy, nhẹ nhàng lật ra."),t("br"),n._v("“Hóa Phàm, cũng gọi siêu phàm, thoát phàm, đại biểu cho thoát ly phàm nhân ràng buộc, sinh mệnh cấp độ tăng lên trên diện rộng. Muốn thoát ly phàm nhân, liền muốn tích ngũ cốc chi cám bã, ăn linh khí của thiên địa. Người ăn rau trí tuệ mà thiên, thực khí giả Thần Minh mà thọ...”"),t("br"),t("br"),n._v("Thư tịch nội dung đi vào tầm mắt."),t("br"),t("br"),n._v("Không biết qua bao lâu, một ngụm trọc khí phun ra, Trương Huyền mở to mắt."),t("br"),t("br"),n._v("Hóa Phàm nhất trọng Thiên Đạo công pháp, hắn đã toàn bộ lĩnh ngộ lý giải, có thể tùy thời tu luyện."),t("br"),t("br"),n._v("“Bắt đầu đi!”"),t("br"),t("br"),n._v("Khoanh chân ngồi ở trên giường, cổ tay khẽ đảo, linh thạch xuất hiện tại lòng bàn tay, chân khí dựa theo công pháp vận chuyển phương hướng, chậm chạp chảy xuôi."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Hai cái hô hấp về sau, trong tay linh thạch vỡ thành bột phấn."),t("br"),t("br"),n._v("“Ừm? Cái này...”"),t("br"),t("br"),n._v("Da mặt nhảy một cái."),t("br"),t("br"),n._v("Linh thạch thiên địa hình thành, ở trong chứa linh khí, trong suốt như ngọc... Dựa theo đạo lý, là mười phần chắc chắn, thế nào một cái liền bể nát?"),t("br"),t("br"),n._v("Cúi đầu vừa nhìn, lập tức mặt mũi tràn đầy cổ quái."),t("br"),t("br"),n._v("“Linh khí... Bị hấp thu hết?”"),t("br"),t("br"),n._v("Chỉ thấy trên mặt đất bể nát linh thạch, toàn thân trắng bệch, cùng đập nát vôi đồng dạng, rất rõ ràng là trong đó linh khí bị thôn phệ sạch nguyên nhân."),t("br"),t("br"),n._v("“Cái này... Muốn như vậy xung kích Hóa Phàm nhất trọng, nên cần bao nhiêu linh thạch?”"),t("br"),t("br"),n._v("Trương Huyền ngất."),t("br"),t("br"),n._v("Vốn cho rằng xung kích cái Hóa Phàm nhất trọng, mấy trăm khối linh thạch, khẳng định có thể thành công, trong tay hắn có hết mấy vạn, dễ như trở bàn tay, hiện tại xem ra, ý nghĩ hão huyền."),t("br"),t("br"),n._v("Hóa Phàm, đột phá phàm trần."),t("br"),t("br"),n._v("Thân thể của hắn bị Thiên Đạo chân khí rèn luyện, hơn xa thường nhân, đánh vỡ cái này phàm nhân cùng linh cảnh khoảng cách, độ khó so với người bình thường lớn đâu chỉ gấp trăm lần."),t("br"),t("br"),n._v("Thật giống như để đường kính một mét hố nước, gia tăng gấp đôi lượng nước dễ dàng, có thể để một cái hải dương gia tăng gấp đôi lượng nước... Sao mà khó khăn!"),t("br"),t("br"),n._v("Hắn chính là như vậy."),t("br"),t("br"),n._v("Toàn thân cao thấp bị Thiên Đạo chân khí tẩm bổ, thân thể, linh hồn đều có biến hóa, cùng cấp bậc vô địch, muốn bước ra sau cùng cửa ải, cũng là lại thêm khó."),t("br"),t("br"),n._v("“Từng cái hấp thu, sớm muộn gì đều sẽ mệt chết...”"),t("br"),t("br"),n._v("Linh thạch chừng mấy vạn khối, dựa theo tình huống bình thường, coi như đột phá cần tiêu hao lại nhiều, cũng hoàn toàn đầy đủ, có thể... Loại này hạ phẩm linh thạch bên trong, ẩn chứa linh khí có hạn, hắn cần lượng lại cực lớn... Như vậy hai cái hô hấp liền vỡ một khối, hai cái hô hấp liền vỡ một khối, đứt quãng hấp thu, tu luyện cũng phải bị cắt ngang ah!"),t("br"),t("br"),n._v("Hơn nữa... Như vậy từng cái cầm ở trong tay hấp thu... Ngày tháng năm nào mới có thể xung kích Hóa Phàm thành công?"),t("br"),t("br"),n._v("Thật giống như dùng ống truyền dịch uống nước, coi như truyền nước bên trong nước đủ uống, giọt giọt xuống, cũng khẳng định sẽ đem người chết khát."),t("br"),t("br"),n._v("Nói cách khác, nhất định phải nghĩ biện pháp để linh thạch linh khí, nhanh chóng phát ra, mới có thể phối hợp hắn tu luyện."),t("br"),t("br"),n._v("“Trận pháp!”"),t("br"),t("br"),n._v("Lông mày nhíu một cái, một cái chức nghiệp xuất hiện tại trong óc."),t("br"),t("br"),n._v("Từng cái linh thạch cầm ở trong tay tu luyện, khẳng định không được, nếu như nếu muốn để mấy trăm nhanh, mấy ngàn khối linh thạch đồng thời cung cấp linh khí, chỉ có một cái biện pháp, cái kia chính là... Sử dụng trận pháp!"),t("br"),t("br"),n._v("Lúc trước hủy diệt Lâm gia, học qua không ít liên quan tới trận pháp thư tịch, biết một chút lợi hại Tụ Linh Trận, có thể đem linh thạch bên trong linh khí tháo rời ra, hội tụ thành hồng lưu."),t("br"),t("br"),n._v("Nếu có loại trận pháp này, đem linh thạch để vào trong đó, tự động rút ra trong đó linh lực, tu luyện, liền sẽ đơn giản không ít."),t("br"),t("br"),n._v("“Trước tìm trận pháp đi!”"),t("br"),t("br"),n._v("Đứng dậy."),t("br"),t("br"),n._v("Nương theo tu vi gia tăng, đối với linh khí nhu cầu càng lúc càng lớn, không có loại trận pháp này, cho dù có đầy đủ linh thạch, tu luyện, cũng khá là phiền toái."),t("br"),t("br"),n._v("Ra khỏi phòng, lần nữa tìm tới Trại các chủ."),t("br"),t("br"),n._v("“Các chủ, không biết chúng ta Hồng Hải thành có trận pháp sư công hội không, có thể hay không mang ta tới một chuyến?”"),t("br"),t("br"),n._v("“Hồng Hải thành làm mậu dịch chi đô, tự nhiên có trận pháp sư công hội, Trương sư chẳng lẽ muốn đi qua mua sắm trận bàn, vì đi Vu hồn mộ làm chuẩn bị?”"),t("br"),t("br"),n._v("Trại các chủ nhìn qua."),t("br"),t("br"),n._v("Trận bàn là trận pháp giảm bớt, có thể tùy thân mang theo, lấy chân khí khởi động, dù là không phải trận pháp sư cũng có thể sử dụng, là trận pháp sư công hội được hoan nghênh nhất vật phẩm."),t("br"),t("br"),n._v("Lần này đi Vu hồn mộ nguy hiểm tầng tầng, có trận bàn nơi tay, bảo mệnh năng lực liền sẽ gia tăng thật lớn."),t("br"),t("br"),n._v("“Trận bàn? Đúng, mang ta đi nhìn xem có thể hay không mua chút trở về!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Nếu có Tụ Linh Trận trận bàn, mua lấy mang theo, có thể tùy chỗ hội tụ linh khí, tiến hành tu luyện, khẳng định sẽ thuận tiện không ít."),t("br"),t("br"),n._v("“Ta cùng trận pháp sư công hội Trịnh Kính Phong hội trưởng là nhiều năm bạn cũ, chỗ của hắn có toàn bộ Hồng Hải thành cấp bậc cao nhất trận bàn, mang ngươi tới nhìn xem, bao ngươi hài lòng!”"),t("br"),t("br"),n._v("Trại các chủ nói."),t("br"),t("br"),n._v("“Vậy làm phiền Các chủ!” Biết đối phương có ý định kết giao, Trương Huyền cũng không chối từ."),t("br"),t("br"),n._v("Giám Bảo các chức nghiệp đặc thù, thuộc về rất nhiều công hội vị trí trung tâm, khoảng cách trận pháp sư công hội cũng không xa, đi không đến mười phút đồng hồ, liền đến đến trước mặt."),t("br"),t("br"),n._v("Trước đó tại Thiên Vũ vương quốc thời điểm, Trương Huyền đi qua trận pháp sư công hội một chuyến, ở bên trong sưu tập không ít sách vở, cùng trước mắt cái này so, kém không chỉ một đoạn."),t("br"),t("br"),n._v("Công hội đại môn cao lớn uy vũ, in Bát Quái, cho người ta một loại huyền diệu cảm giác."),t("br"),t("br"),n._v("Tiến vào bên trong, lập tức cảm thấy khí tức biến đổi, trong môn phái không khí ướt át, linh khí dồi dào, nhiệt độ thích hợp, tựa như đi vào mùa xuân vườn hoa, không nói ra được thả lỏng cùng hài lòng."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trương Huyền con mắt chớp chớp."),t("br"),t("br"),n._v("Hiện tại đã là cuối thu, tiếp cận bắt đầu mùa đông, bên ngoài có chút lạnh lẽo, thế nào nơi này ấm áp như xuân?"),t("br"),t("br"),n._v("“Trận pháp sư công hội bố trí ở chỗ này trận pháp, tiến vào bên trong, bốn mùa như mùa xuân...” Nhìn thấy nét mặt của hắn, biết nghĩ cái gì, Trại các chủ vuốt râu: “Yên tâm đi, trận pháp này, chỉ làm cho người cảm thấy thoải mái dễ chịu, không có bất kỳ cái gì tính công kích, sẽ không tạo thành tổn thương.”"),t("br"),t("br"),n._v("“Bố trí cái đông ấm hè mát trận pháp, chỉ vì để người tiến vào dễ chịu... Không hổ là trận pháp sư công hội, thủ bút thật lớn!”"),t("br"),t("br"),n._v("Trương Huyền tấm tắc lấy làm kỳ lạ."),t("br"),t("br"),n._v("Lớn như thế trận pháp, khống chế tinh như vậy xác thực, chỉ ở công hội bên trong, không chút nào ảnh hưởng đến ngoại giới, không hổ là vạn quốc liên minh trận pháp sư công hội, liền trận pháp này, chỉ sợ cũng muốn tại cấp ba đỉnh phong, thậm chí đạt đến cấp bốn!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-486"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-488"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);