(window.webpackJsonp=window.webpackJsonp||[]).push([[1731],{2086:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-40-đai-su-đen-roi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-40-đai-su-đen-roi"}},[n._v("#")]),n._v(" Chương 40: Đại sư đến rồi")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v(" “Đánh một quyền?” Chủ sạp không biết thiếu niên này giở trò quỷ gì."),t("br"),t("br"),n._v(" “Đúng vậy a!” Trương Huyền gật đầu."),t("br"),t("br"),n._v(" “Ngươi đùa bỡn ta đây!” Thấy đối phương bộ dáng nghiêm túc, chủ sạp lửa giận một hồi dâng lên trên, một quyền đập tới."),t("br"),t("br"),n._v(" Có thấy bệnh, chưa từng thấy như thế có bệnh, còn để cho mình đánh một quyền, vậy thì tốt, có tin hay không lão tử đánh chết ngươi?"),t("br"),t("br"),n._v(" Quyền pháp vừa ra tay, lập tức dùng sức mạnh toàn thân, tiếng gió rít gào cũng cho thấy tu vi của hắn, võ giả ba tầng Chân Khí cảnh sơ kỳ!"),t("br"),t("br"),n._v(" “Không sai!” "),t("br"),t("br"),n._v(" Nhẹ nhàng nở nụ cười, Trương Huyền thân thể hơi động, né qua."),t("br"),t("br"),n._v(" Hắn đã đạt đến võ giả năm tầng Đỉnh Lực cảnh đỉnh cao, võ giả ba tầng sơ kỳ sức mạnh, tự nhiên không đả thương được hắn!"),t("br"),t("br"),n._v(" “Ừm?” "),t("br"),t("br"),n._v(" Vốn tưởng rằng thiếu niên chính là tới quấy rối gia hỏa, không tu vi gì, thấy hắn ung dung tránh thoát công kích, lập tức biết thực lực lại còn cao hơn chính mình, chủ sạp không nhịn được ngừng lại, đồng thời trong lòng kỳ quái, một cái đạt đến võ giả ba tầng gia hỏa, không biết thật chỉ có 8 viên kim tệ liền tới mua đồ đi..."),t("br"),t("br"),n._v(" “Nếu như ta không nhìn lầm, ngươi là Hạ Bình Ấp người đi!” "),t("br"),t("br"),n._v(" Đối phương ra tay, Trương Huyền trong đầu được như nguyện nhận được liên quan với trước mắt người này thư tịch, khẽ mỉm cười."),t("br"),t("br"),n._v(" “Ngươi... Làm sao ngươi biết?” Chủ sạp sững sờ."),t("br"),t("br"),n._v(" Hạ Bình Ấp là Thiên Huyền Vương Thành một cái ngoại ô thị trấn thành phố, diện tích không lớn, loại cấp bậc này thành trấn, ở Vương Thành xung quanh không có một trăm cũng có ít nhất tám mươi, cái tên này một cái nói ra, để hắn tràn đầy nghi hoặc."),t("br"),t("br"),n._v(" Không nhịn được cúi đầu nhìn một chút, khắp toàn thân từ trên xuống dưới tựa hồ không có bất kỳ cái gì đặc thù có thể nhìn ra tự mình là từ đâu tới!"),t("br"),t("br"),n._v(" “Đừng động ta làm sao mà biết được!” Trương Huyền triển khai tinh tướng hình thức, lần thứ hai nhìn về phía đối phương, một mặt hờ hững ngạo khí: “Nửa năm trước ngươi ở Thiên Huyền rừng rậm, nên gặp được một lần nguy hiểm, bị Lãnh Trúc Xà cắn bị thương quá! Tuy rằng lúc đó đào thoát tính mạng, nhưng thận chịu đến tổn thương, nếu như ta không nhìn lầm... Cái kia thời điểm, rất khó bày ra nam nhân hùng phong!” "),t("br"),t("br"),n._v(" “Ngươi... Ngươi...” "),t("br"),t("br"),n._v(" Chủ sạp lui về sau hai bước, con mắt trừng lớn, con ngươi kém chút rơi trên mặt đất."),t("br"),t("br"),n._v(" Nửa năm trước, hắn đúng là Thiên Huyền rừng rậm bị Lãnh Trúc Xà cắn bị thương! Về sau một mực... Cũng là chân thật tồn tại."),t("br"),t("br"),n._v(" Nhưng chuyện này, ngoại trừ một mình hắn cùng thanh lâu cô nương kia biết ra, chưa bao giờ cùng người khác nói qua, thiếu niên này làm sao mà biết được?"),t("br"),t("br"),n._v(" Lẽ nào có thể đoán mệnh? Biết trước?"),t("br"),t("br"),n._v(" Chủ sạp tràn đầy không thể tin được."),t("br"),t("br"),n._v(" “Thế nào, ta nói không đúng?” Nhìn thấy đối phương vẻ mặt, Trương Huyền biết Thiên Đạo Thư Viện chắc chắn sẽ không có sai lầm, trên mặt lộ ra vẻ tiếc hận: “Nếu như ngươi cảm thấy ta nói không đúng, dược liệu ta có thể không cần, hiện tại xoay người rời đi, bất quá... Ngươi duy nhất trị cơ hội tốt, liền rất có thể liền như vậy đánh mất! Ai, sau đó cũng lại làm không được nam nhân, cũng đừng trách ta không nhắc nhở ngươi...” "),t("br"),t("br"),n._v(" "),t("br"),n._v(" “Ta... Công tử đừng đi!” "),t("br"),t("br"),n._v(" Nghe đối phương nói có thể chữa khỏi, chủ sạp chấn động toàn thân, vội vàng bước lên trước: “Ngươi... Ngươi thật có thể giúp ta chữa khỏi?” "),t("br"),t("br"),n._v(" Đối với một người đàn ông tới nói, không có chuyện gì có thể so sánh đánh mất nam nhân uy phong bi thảm, những ngày này bằng hữu gọi hắn đi ra ngoài, hắn đều không dám, sợ bị nhìn ra vấn đề, một mực chịu đựng dày vò, cũng tìm không ít y sư, kết quả nhưng nhận được thống nhất kết luận, khó có thể trị liệu, biện pháp duy nhất chính là cắt..."),t("br"),t("br"),n._v(" Không nghĩ tới tùy tiện gặp phải một cái mua đồ, một cái nói ra, còn nói có thể trị liệu, để hắn có chút không dám tin tưởng."),t("br"),t("br"),n._v(" “Ngươi có thể lựa chọn tin tưởng, cũng có thể không tin, ta không bắt ép ngươi! Bất quá, ta nếu có thể nhìn ra, tự nhiên có biện pháp trị liệu!” Trương Huyền nói."),t("br"),t("br"),n._v(" “Còn cầu công tử hỗ trợ cứu trị, chỉ cần có thể chữa khỏi... Đừng nói một cây Hàn Dương Thảo, những dược liệu này ngươi toàn bộ lấy đi đều được!” Chủ sạp vội vàng hướng về phía trước, tràn đầy cầu xin."),t("br"),t("br"),n._v(" Thiên hạ kỳ nhân sở trường quá nhiều, đối phương nói không sai, hắn nếu có thể nhìn ra, khẳng định có biện pháp trị liệu!"),t("br"),t("br"),n._v(" Hơn nữa, cho dù có một tia hi vọng, cũng phải nghe một chút, dù sao quan hệ sau này hạnh phúc..."),t("br"),t("br"),n._v(' "Hừm, ngươi là bị Lãnh Trúc Xà cắn bị thương, vật này trời sinh âm hàn,'),t("br"),t("br"),n._v(' Một khi bị cắn bị thương, khí âm hàn liền sẽ xâm nhập trong cơ thể, bất kỳ cái gì thuốc đều không thể loại bỏ, để nhân công năng đánh mất! Thậm chí một ít Luyện đan sư, đều không thể ra sức!"'),t("br"),n._v("  “Nhưng để người kỳ quái là, này rắn, toàn thân đều là lạnh, xà đảm nhưng là cực nóng, ngươi chỉ cần lại tìm một cái đi ra, đem lột da giết chết, xà đảm dùng, liền có thể đem trong cơ thể khí âm hàn triệt để loại bỏ sạch sẽ, bị thương thận cũng có thể khôi phục, tái hiện nam nhân hùng phong!” "),t("br"),t("br"),n._v(" Trương Huyền nói."),t("br"),t("br"),n._v(" Lãnh Trúc Xà độc tuy rằng có bao nhiêu loại thuốc giải, nhưng tốt nhất thuốc giải ngay ở nó chính mình trên thân, xà đảm chính là hóa giải nó đặc biệt hàn khí then chốt."),t("br"),t("br"),n._v(" Cái này ở Hồng Thiên Tàng Thư Các một quyển sách trên tỉ mỉ viết, nghĩ đến Lãnh Trúc Xà, liền lập tức xuất hiện đầu này tin tức."),t("br"),t("br"),n._v(" Chính là bởi vì bởi vậy dựa dẫm, hắn mới nói Lãnh Trúc Xà, không phải vậy, Thiên Đạo Thư Viện có thể biểu hiện vô số khuyết điểm, tùy tiện nói một cái có thể tìm tới câu trả lời là được, không cần đề cái này."),t("br"),t("br"),n._v(" “Ăn Lãnh Trúc Xà xà đảm?” Chủ sạp còn có chút không tin."),t("br"),t("br"),n._v(" Chỉ đơn giản như vậy?"),t("br"),t("br"),n._v(" Thật nếu như vậy, làm sao tự mình tìm nhiều như vậy y sư đều không có người nói quá?"),t("br"),t("br"),n._v(" “Cái này Thương Thành nên có bán ra Lãnh Trúc Xà a, không tin ngươi có thể mua một cái hiện tại liền thử xem, nhận được đáp án về sau, ta rời đi!” Trương Huyền nói."),t("br"),t("br"),n._v(" Thiên Vũ Thương Thành không chỉ có vô số dược liệu, các loại bảo vật, còn có Man Thú, động vật bán ra, Lãnh Trúc Xà là một loại cấp thấp Man Thú, tuy rằng thân ngậm kịch độc, nhưng có thể làm thuốc, xác thực có người bán ra."),t("br"),t("br"),n._v(" “Vậy thì tốt, ngươi chờ...” "),t("br"),t("br"),n._v(" Chần chờ một chút, chủ sạp xoay người rời đi, thời gian không lâu, mang theo một cái lồng sắt đi tới, bên trong chính cuộn lại một cái Lãnh Trúc Xà."),t("br"),t("br"),n._v(" Thân là Chân Khí cảnh võ giả, chỉ cần sớm có chuẩn bị, giết chết một cái Lãnh Trúc Xà vẫn là rất đơn giản, thời gian không lâu, xà đảm liền bị lấy ra ngoài, không có quá nhiều chần chờ, chủ sạp trực tiếp nuốt vào."),t("br"),t("br"),n._v(" “Ừm?” "),t("br"),t("br"),n._v(" Thời gian không lâu, chủ sạp ánh mắt sáng lên."),t("br"),t("br"),n._v(" Có hiệu quả hay không, hắn hiển nhiên đã biết."),t("br"),t("br"),n._v(" “Đa tạ công tử cứu trị, những này Hàn Dương Thảo đều là của ngươi!” Hưng phấn hai mắt tỏa ánh sáng, chủ sạp kém chút không cao hứng quỳ xuống."),t("br"),t("br"),n._v(" Chuyện này một mực là tâm bệnh của hắn, có một quãng thời gian đều không muốn sống, bởi vì trị liệu, mấy năm qua tích trữ cũng hoa gần đủ rồi, nằm mơ đều không có nghĩ đến, cứ như vậy chữa khỏi!"),t("br"),t("br"),n._v(" “Ta nói rồi ngươi sẽ đưa cho ta, không lừa người đi!” "),t("br"),t("br"),n._v(" Đối phương biếu tặng, Trương Huyền cũng không khách khí, tiện tay đem chứa đựng Hàn Dương Thảo cái rương cầm ở trong tay."),t("br"),t("br"),n._v(" Tri thức cũng là của cải, tự mình hỗ trợ chữa khỏi đối phương chứng bệnh, thu lấy một ít thù lao là nên."),t("br"),t("br"),n._v(" “Công tử đại tài, là ta có mắt không nhìn được Thái Sơn... Xin hãy tha lỗi!” Chủ sạp vò đầu, tràn đầy thật không tiện."),t("br"),t("br"),n._v(" Mới vừa rồi còn lời thề son sắt mà nói, nếu như mình thật biếu tặng cho đối phương Hàn Dương Thảo, liền gọi hắn là gia gia, không nghĩ tới thật sự làm như vậy..."),t("br"),t("br"),n._v(" “Không có gì!” Trương Huyền vung vung tay, đang muốn rời đi, đột nhiên nhìn thấy một đám người sốt ruột hướng về phía trước chạy đi, từng cái từng cái kích động sắc mặt thấu đỏ."),t("br"),t("br"),n._v(" “Nhanh lên một chút, Mặc Dương đại sư đến rồi!” "),t("br"),t("br"),n._v(" “Có thể được đến Mặc Dương đại sư chỉ điểm, vinh thịnh hoa cổn, lần này ngàn vạn không thể bỏ qua cơ hội!” "),t("br"),t("br"),n._v(" “Lần này cần là Mặc Dương đại sư có thể giúp ta giám định một cái bảo vật, bao nhiêu tiền ta đều sẽ mua!” "),t("br"),t("br"),n._v(" “Đúng vậy a, lần trước lãng phí cơ hội, thực sự là đáng tiếc...” "),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v(" Vừa đi mọi người một bên nghị luận."),t("br"),t("br"),n._v(" “Đây là thế nào?” "),t("br"),t("br"),n._v(" Nhìn thấy mọi người dáng dấp này, thật giống cùng cái gì minh tinh đến rồi như thế, Trương Huyền không nhịn được kỳ quái."),t("br"),t("br"),n._v(" “Là Mặc Dương đại sư đến rồi!” Chủ sạp cũng đầy mặt hưng phấn, hai mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Convert by: Hoang Chau"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-48"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-50"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);