(window.webpackJsonp=window.webpackJsonp||[]).push([[1744],{2100:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-491-on-ngon-linh"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-491-on-ngon-linh"}},[n._v("#")]),n._v(" Chương 491: Ôn Ngôn lĩnh")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 491: Ôn Ngôn lĩnh"),t("br"),t("br"),n._v("Linh thú thẳng tắp tiến lên, xẹt qua chân trời."),t("br"),t("br"),n._v("Rời khỏi Hồng Hải thành, đã ròng rã ba ngày."),t("br"),t("br"),n._v("Vu hồn mộ vị trí, cùng đi Vạn Quốc thành cũng không tương đồng, mà là tại liên minh mặt khác một bên."),t("br"),t("br"),n._v("Nơi này sơn mạch uốn lượn vặn vẹo, như là từng đầu nằm rạp trên mặt đất cự long, tựa như lúc nào cũng sẽ bay lượn bay lên, chạy nhanh Cửu Tiêu."),t("br"),t("br"),n._v("Thú kém trong phòng, Trương Huyền đứng tại cửa sổ trước mặt, hai mắt khép hờ."),t("br"),t("br"),n._v("Ba ngày qua, đã triệt để củng cố Hóa Phàm nhất trọng đỉnh phong lực lượng, hắn giờ phút này, cũng không phải là tu luyện, mà là chỉnh lý trong đầu liên quan tới vu hồn sư ghi chép."),t("br"),t("br"),n._v("Vì xác định Vô Hồn Kim Nhân, Trại các chủ từng sưu tập không ít liên quan tới vu hồn sư thư tịch, ban đầu ở Giám Bảo các đọc sách thời điểm, thu sạch vào trong óc, một mực không thời gian nhìn kỹ, lúc này vừa vặn có rảnh."),t("br"),t("br"),n._v("“Chính xác!”"),t("br"),t("br"),n._v("Trong óc thấp giọng hô, mấy chục bản thư tịch đem không có khuyết điểm tin tức hội tụ vào một chỗ."),t("br"),t("br"),n._v("Liên quan tới vu hồn sư miêu tả, tất cả thư tịch, đều có cùng một cái nội dung quỷ dị, khiến cho người không rét mà run."),t("br"),t("br"),n._v("Đối linh hồn nghiên cứu cực kỳ thấu triệt, để cho người ta kiêng kị."),t("br"),t("br"),n._v("Rất mau đem thư tịch lật một lần, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Cái nghề nghiệp này truyền thừa đoạn tuyệt, trên cơ bản không lưu lại ghi chép, hiện tại thư tịch lên, cũng đều là đôi câu vài lời, không có quá nhiều thực tế đồ vật."),t("br"),t("br"),n._v("Nhìn một hồi, không những không hiểu rõ, còn làm lơ ngơ."),t("br"),t("br"),n._v("“Được rồi, đi một bước nhìn một bước đi!”"),t("br"),t("br"),n._v("Biết được không đến cái gì hữu dụng nội dung, than thở một tiếng, mở to mắt, quay đầu nhìn về phía Tôn Cường, Triệu Nhã: “Các ngươi tới!”"),t("br"),t("br"),n._v("“Lão sư!”"),t("br"),t("br"),n._v("“Thiếu gia!”"),t("br"),t("br"),n._v("Mọi người đi tới trước mặt."),t("br"),t("br"),n._v("“Trương sư có việc giao phó lời nói, có cần hay không chúng ta né tránh?” Biết thần sắc hắn ngưng trọng, Triệu Phi Vũ nhìn qua."),t("br"),t("br"),n._v("Sư môn truyền thừa, liên quan không ít bí mật, bình thường tình huống, ngoại nhân là không tiện nghe."),t("br"),t("br"),n._v("“Không cần, cũng không phải cái đại sự gì, chính là nhắc nhở bọn hắn đi Vu hồn mộ về sau, phải chú ý một ít chuyện.” Trương Huyền vẫy tay."),t("br"),t("br"),n._v("“Ồ? Chú ý sự tình? Vậy chúng ta có thể nghe ư?” Triệu Phi Vũ, Trại các chủ đám người ánh mắt sáng lên, đồng loạt nhìn qua."),t("br"),t("br"),n._v("Đây là ai?"),t("br"),t("br"),n._v("Thế nhưng là ngay cả đoạt xá linh hồn đều có thể đuổi đi ra danh sư, càng là ngũ tinh giám bảo sư, trận pháp đại tông sư, trịnh trọng như vậy bàn giao, khẳng định mười phần khẩn yếu."),t("br"),t("br"),n._v("Có lẽ sau khi nghe xong, liền sẽ được ích lợi không nhỏ, đi vào Vu hồn mộ, bảo mệnh năng lực đại tăng."),t("br"),t("br"),n._v("“Đương nhiên có thể!”"),t("br"),t("br"),n._v("Không để ý tới mọi người hưng phấn, Trương Huyền nhìn về phía trước mắt học sinh cùng hạ nhân, vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("“Đến Vu hồn mộ, đều cho ta chú ý, ai muốn bị đoạt xá tuyệt đối đừng cướp nuốt, ta còn có việc muốn hỏi!”"),t("br"),t("br"),n._v("Nếu là trước đó đoạt xá Lạc Trúc hồn phách còn sống, nhất định có thể hỏi ra tin tức hữu dụng, có lẽ cũng không cần mạo hiểm đi Vu hồn mộ."),t("br"),t("br"),n._v("Càng nghĩ càng thấy đến hối hận, cho nên lần này cần trịnh trọng việc bàn giao."),t("br"),t("br"),n._v("Đừng chờ thêm một hồi nói đã chậm, bọn này oắt con cùng Tôn Cường, cướp để người ta giết chết, coi như còn muốn hỏi thăm, cũng không kịp."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Triệu Nhã đám người liền vội vàng gật đầu."),t("br"),t("br"),n._v("Vì phòng ngừa ngoài ý muốn, trước khi đến lão sư đã trên người bọn hắn đều thả một đạo chân khí, coi như gặp phải hồn phách xông lại, cũng có thể chống cự, không bị đoạt xá."),t("br"),t("br"),n._v("“Ừm, đây còn tạm được! Liền chuyện này, đều đi tu luyện đi!”"),t("br"),t("br"),n._v("Giao phó xong, Trương Huyền vẫy tay."),t("br"),t("br"),n._v("Triệu Nhã đám người gật đầu, lần nữa phân tán ra đến, an tĩnh ngồi xuống."),t("br"),t("br"),n._v("“Vậy thì hết rồi”"),t("br"),t("br"),n._v("Vốn cho rằng có thể bàn giao cái gì chú ý hạng mục, để bọn hắn trông mong nhìn xem, tràn ngập chờ mong, nghe nói như thế Triệu Phi Vũ, Trại các chủ đám người khóe miệng giật một cái, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Người khác đi Vu hồn mộ, đều khẩn trương muốn chết, sợ xảy ra vấn đề, cẩn thận lại cẩn thận cái tên này ngược lại tốt, thế nào cảm giác không phải đi địa phương nguy hiểm, mà là mang theo học sinh đi nấu cơm dã ngoại?"),t("br"),t("br"),n._v("Nếu không, vì sao phải trịnh trọng như vậy bàn giao mọi người ăn ít một chút, thật chừa cho hắn mấy ngụm"),t("br"),t("br"),n._v("“Khụ khụ!”"),t("br"),t("br"),n._v("Lạc Trúc càng là ho khan mấy tiếng, bị ngụm nước kém chút sặc chết."),t("br"),t("br"),n._v("Hắn vì đi cái này Vu hồn mộ, chuẩn bị không biết bao nhiêu thứ, thậm chí liền di chúc đều viết xong cái tên này đến tốt, cảm giác cùng dạo phố giống như, tâm tình không gì sánh được nhàn nhã."),t("br"),t("br"),n._v("Người cùng người quả nhiên là có khoảng cách"),t("br"),t("br"),n._v("Cố nén buồn bực, cúi đầu nhìn thoáng qua phía dưới địa hình, phán đoán một cái vị trí, đi tới Trương Huyền trước mặt."),t("br"),t("br"),n._v("“Trương sư, Vu hồn mộ lập tức tới ngay!”"),t("br"),t("br"),n._v("“Nhanh đến? Đây là địa phương nào?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Là vạn quốc liên minh Cửu Khúc sơn.”"),t("br"),t("br"),n._v("“Vạn dặm Xích Ba hải, ngàn dặm Cửu Khúc sơn. Nơi này là cùng Minh Hạ đế quốc giáp giới địa phương?” Trương Huyền lông mày nhíu một cái."),t("br"),n._v("Giám Bảo các có vương quốc liên minh địa đồ, lúc trước hắn cũng nhìn một lần, biết một chút đại khái phương hướng, vốn cho rằng cái gọi là Vu hồn mộ, sẽ ở trong liên minh Bộ mỗ ta cái địa phương, không nghĩ tới thế mà đến biên cảnh."),t("br"),t("br"),n._v("Minh Hạ đế quốc là cùng vạn quốc liên minh tương đồng thế lực cực lớn quốc gia, danh sư thi đấu hai mươi bảy chỗ thế lực bên trong, có tên của bọn hắn."),t("br"),t("br"),n._v("Cửu Khúc sơn làm hai thế lực lớn giao giới, hàng năm chinh chiến liên tục, thuộc về cực kỳ nguy hiểm khu vực một trong."),t("br"),t("br"),n._v("“Còn chưa tới giáp giới địa phương, tại thứ ba khúc Ôn Ngôn lĩnh!” Lạc Trúc nói."),t("br"),t("br"),n._v("Cửu Khúc sơn, sơn mạch cửu khúc, mỗi một khúc, đều có một cái kỹ lưỡng hơn địa danh, Ôn Ngôn lĩnh chính là thứ ba khúc vị trí địa."),t("br"),t("br"),n._v("Truyền thuyết, một vị cao thủ tuyệt thế trên đường đi qua nơi đây, bị linh thú triều phục kích, không chút hoang mang, nhẹ lời khuyên bảo, cuối cùng thú triều lui bước, truyền vì giai thoại, Ôn Ngôn lĩnh danh xưng cũng bởi vậy mà tới."),t("br"),t("br"),n._v("Nơi này linh khí dồi dào, linh thú, man thú đông đảo, dù chưa đến hai nước giáp giới, nhưng cũng là cực kỳ nguy hiểm vị trí một trong, không ít người nghe được địa danh, cũng không nguyện ý đi tới."),t("br"),t("br"),n._v("“Ôn Ngôn lĩnh linh thú đông đảo, không thể bay thẳng được đi qua, nếu không, rất dễ dàng gặp phải không trung linh thú phục kích, nguy hiểm không gì sánh được!”"),t("br"),t("br"),n._v("Nghe được địa danh, Kim Tòng Hải mày nhăn lại."),t("br"),t("br"),n._v("Hóa Phàm cảnh cường giả, không thể phi hành, nếu như trên không trung gặp phải cái khác linh thú công kích, sẽ vô cùng nguy hiểm, một khi từ không trung rơi xuống, vô cùng có khả năng tại chỗ ngã chết."),t("br"),t("br"),n._v("Cho nên, linh thú phi hành đông đảo sơn mạch, có rất ít người bay lên đi qua."),t("br"),t("br"),n._v("“Kim tiền bối nói không sai, có điều, cũng không cần khẩn trương, công tử đầu này linh thú, đạt đến Hóa Phàm tam trọng đỉnh phong, thực lực cực mạnh, bình thường linh thú không dám đắc tội, hẳn là có thể đủ mang bọn ta đến địa phương.”"),t("br"),t("br"),n._v("Lạc Trúc nói: “Đương nhiên, cũng không có khả năng bay thẳng đến trước mặt, Vu hồn mộ phụ cận xác thực ở một đầu lợi hại linh thú phi hành, ta sợ vạn nhất quá mức tới gần, gây nên nó xao động, sẽ rất phiền phức!”"),t("br"),t("br"),n._v("“Ngươi nói là đầu kia Yêu Thần thú chứ?” Kim Tòng Hải nói."),t("br"),t("br"),n._v("“Tiền bối biết con thú này?” Lạc Trúc sững sờ."),t("br"),t("br"),n._v("“Đương nhiên biết, cái tên này đạt đến Hóa Phàm tứ trọng đỉnh phong, lại thêm nữa có thể phi hành, nhiều lần làm hại nhân mạng, liên minh từng phái ra không ít cao thủ vây giết, đáng tiếc đều thất bại!”"),t("br"),t("br"),n._v("Kim Tòng Hải vẻ mặt nghiêm túc: “Hơn nữa, nó không chỉ thực lực mạnh, tốc độ nhanh, nghe nói còn có Long tộc huyết mạch, phòng ngự vô địch, mười phần khó đối phó, ngay cả Thú đường bắt bọn hắn không có cách nào. Ngươi nói Vu hồn mộ, khoảng cách nó chỗ ở, cụ thể có bao xa?”"),t("br"),t("br"),n._v("“Không xa, chỉ có khoảng cách mấy trăm mét, lần trước ta tới, là nghiên cứu quy luật, chờ nó ra ngoài kiếm ăn, mới tìm cơ hội đi vào. Lần này chúng ta nhiều người, mục tiêu lớn, ta ý tứ vẫn là cẩn thận chút cho thỏa đáng, sớm chuẩn bị, đi bộ đi qua.”"),t("br"),t("br"),n._v("Lạc Trúc nói."),t("br"),t("br"),n._v("“Vài trăm mét? Đây cũng quá tới gần”"),t("br"),t("br"),n._v("Kim Tòng Hải lông mày nhăn lại."),t("br"),t("br"),n._v("Hóa Phàm tứ trọng Trọc Thanh cảnh, thanh trọc rõ ràng, hồn xác tách rời, coi như không cần nhìn, chỉ bằng vào cảm ứng, liền có thể phát hiện vài trăm mét bên ngoài đồ vật, khoảng cách gần như vậy, có thể nói, đã tại đối phương hang ổ hạch tâm."),t("br"),t("br"),n._v("Loại này khoảng cách, đừng nói bay lên đi qua, coi như đi bộ đi lại, cũng có thể quấy nhiễu đối phương."),t("br"),t("br"),n._v("Hắn mặc dù là Hóa Phàm tứ trọng, đáng tiếc chỉ là sơ kỳ, đối mặt loại này nắm giữ Long tộc huyết mạch phi hành man thú, hoàn toàn không có lực lượng chống lại."),t("br"),t("br"),n._v("Coi như thân là độc sư có thể dùng độc, cũng muốn đối phương không có chút nào phòng bị mới được, bay được, lại thêm Long tộc huyết mạch đối độc kháng tính lớn, một kích không ăn thua, chọc giận đối phương, làm không cẩn thận sẽ đem tất cả người đều giết."),t("br"),t("br"),n._v("Vốn cho rằng Vu hồn mộ liền đủ nguy hiểm, không nghĩ tới lân cận còn có hung mãnh như vậy gia hỏa."),t("br"),t("br"),n._v("“Là rất gần, có điều, ta trước đó trong này ẩn giấu ba tháng, tìm ra quy luật, chỉ cần chúng ta cẩn thận một chút, dựa theo quy luật làm việc, hẳn là sẽ không bị phát hiện.” Lạc Trúc chần chờ một chút nói."),t("br"),t("br"),n._v("“Đã ngươi tìm được quy luật, ta nhìn không bằng như vậy!” Chần chờ một chút, Kim Tòng Hải nhìn qua: “Trương sư, chúng ta có thể hay không sớm tại ngoài hai cây số địa phương xuống, sau đó đi bộ qua?”"),t("br"),t("br"),n._v("Hiện tại tất cả mọi người lấy Trương Huyền như Thiên Lôi sai đâu đánh đó."),t("br"),t("br"),n._v("Lựa chọn ra sao, tự nhiên muốn nghe hắn."),t("br"),t("br"),n._v("“Được.” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Hắn chưa từng tới, càng không biết Yêu Thần thú, rốt cuộc mạnh cỡ nào, đã hai người thương nghị hết rồi, quyết định như vậy liền tốt."),t("br"),t("br"),n._v("Hai cây số mà thôi, đối với bọn hắn loại thực lực này người mà nói, căn bản không tính là khoảng cách."),t("br"),t("br"),n._v("“Vậy thì tốt, quyết định như vậy đi, đến lúc đó ngươi nói một tiếng, ta sớm báo tin thú sủng, để nó rơi xuống đất.” Kim Tòng Hải nói."),t("br"),t("br"),n._v("“Ừm!” Lạc Trúc gật đầu."),t("br"),t("br"),n._v("“Cái này Yêu Thần thú ngươi cũng nghe qua?”"),t("br"),t("br"),n._v("Mấy người thương nghị, một bên Tôn Cường, thấy Trại các chủ nghe được Yêu Thần thú về sau, vẻ mặt trắng bệch, nhịn không được hỏi."),t("br"),t("br"),n._v("“Tự nhiên nghe qua!” Trại các chủ cười khổ: “Đầu này đại gia hỏa, thực lực cường đại vô song, nghe nói Thú đường đường chủ, mang theo mười hai vị trưởng lão, đồng thời săn bắn, đều không có thành công!”"),t("br"),t("br"),n._v("“Mười hai vị trưởng lão?”"),t("br"),t("br"),n._v("“Đúng vậy a, có thể trở thành Thú đường trưởng lão, thực lực đều cùng ta không sai biệt lắm, nhiều người như vậy đều không có thành công, có thể đủ nhìn ra mạnh mẽ!”"),t("br"),t("br"),n._v("Trại các chủ lắc đầu: “Cái tên này không chỉ có thể bay, bởi vì có Long tộc huyết mạch, phòng ngự cực mạnh, đao thương bất nhập. Coi như Linh cấp trung phẩm vũ khí, toàn lực đâm đi lên, đều không có cái gì tác dụng.”"),t("br"),t("br"),n._v("“Tốc độ nhanh, phòng ngự mạnh, tính cách táo bạo, địa bàn ý thức nặng như quả thật thủ ở nơi đó, chúng ta muốn đi qua, chỉ sợ không dễ dàng như vậy”"),t("br"),t("br"),n._v("“Ngươi sợ hãi?”"),t("br"),t("br"),n._v("Cắt ngang hắn, Tôn Cường đầy vẻ khinh bỉ: “Thật sợ cũng đừng đi, còn Giám Bảo các Các chủ”"),t("br"),t("br"),n._v("“Ta sợ hãi?” Nhìn thấy đối phương biểu lộ, Trại các chủ thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Hắn đường đường Hóa Phàm tứ trọng cường giả, thế mà bị một cái tiểu quản gia khinh bỉ, trong giọng nói còn mang theo chế giễu"),t("br"),t("br"),n._v("Chủ nhân phách lối, không nghĩ tới quản gia lại thêm phách lối, tức giận đến hô hô loạn thở, đang muốn nói chuyện, liền nghe đến Lạc Trúc thanh âm vang lên."),t("br"),t("br"),n._v("“Đến”"),t("br"),t("br"),n._v("Thanh âm kết thúc, mọi người lập tức cảm thấy phía dưới phi hành man thú lắc người một cái, thẳng tắp hướng lan tràn sơn mạch rơi xuống."),t("br"),t("br"),n._v("(Ban đêm gõ chữ thời điểm, con mắt không biết xài như thế nào, trên màn hình chữ, một cái đều nhìn không thấy, những vật khác có thể nhìn thấy, trước kia cũng xuất hiện qua, không biết chuyện ra sao. Nghỉ ngơi rất lâu, mới chậm tới, đổi mới hơi trễ, tạ lỗi. Mặt khác, tháng này kết thúc còn thừa lại hơn hai giờ, nguyệt phiếu không ném sẽ trễ! Nếu như ném đủ năm tấm, có thể vào quần tìm nhân viên quản lý sạch dương, hắn có biện pháp, để ngươi tiếp tục ủng hộ lão nhai!, cám ơn! Quần số: 289258448. Toàn bộ đặt mua bằng hữu có thể thêm VIP tiếp đãi nhóm: 299735404, nghiệm chứng phía sau liền có thể đi vào chủ quần.)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-499"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-501"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);