(window.webpackJsonp=window.webpackJsonp||[]).push([[1746],{2099:function(n,h,c){"use strict";c.r(h);var t=c(44),i=Object(t.a)({},(function(){var n=this,h=n.$createElement,c=n._self._c||h;return c("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[c("h1",{attrs:{id:"chuong-493-thu-đuong-đuong-chu"}},[c("a",{staticClass:"header-anchor",attrs:{href:"#chuong-493-thu-đuong-đuong-chu"}},[n._v("#")]),n._v(" Chương 493: Thú đường đường chủ")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("br"),c("br"),n._v("Chương 493: Thú đường đường chủ"),c("br"),c("br"),n._v("“Ngăn không được cũng muốn chặn!”"),c("br"),c("br"),n._v("Áo bào xám thanh niên cũng không lui lại, mà là vẫy tay một cái, tiến về phía trước một bước, lập tức, mấy người đồng thời xuất kiếm, một hồi kêu khẽ, sáu thanh kiếm hình thành kiếm võng, đâm tới."),c("br"),c("br"),n._v("Tu vi của bọn hắn cùng Kim Tòng Hải so, chênh lệch rất lớn, nhưng kiếm khí ngang dọc, cho người ta một loại khó mà đột phá cảm giác."),c("br"),c("br"),n._v("“Hợp kích trận pháp?”"),c("br"),c("br"),n._v("Kim Tòng Hải lông mày giương lên."),c("br"),c("br"),n._v("Mấy người kia chiêu số bổ sung, thi triển chính là một cái trận pháp, hơn nữa nhìn đẳng cấp, không hề yếu, coi như hắn thực lực vượt xa mọi người, thật muốn chiến đấu, chỉ sợ cũng không chiếm được lợi lộc gì."),c("br"),c("br"),n._v("Loại này vây công trận pháp, có thể để cho tu vi thấp, liên hợp lại, tốt hơn phát huy thực lực, lấy yếu thắng mạnh."),c("br"),c("br"),n._v("Tựa như lúc trước Triệu Nhã, Viên Đào đám người không kịp Lục Tầm, liên hợp lại, nhưng đem hắn đánh bị thương đồng dạng."),c("br"),c("br"),n._v("Sáu người này mạnh nhất áo bào xám thanh niên chỉ có Hóa Phàm tam trọng, còn lại chẳng qua nhị trọng, nhưng phối hợp trận pháp, sức chiến đấu liền rất khác nhau."),c("br"),c("br"),n._v("Khí tức phun ra nuốt vào, kiếm khí ngang dọc."),c("br"),c("br"),n._v("Coi như đối mặt Hóa Phàm tứ trọng cường giả, cũng không hề yếu."),c("br"),c("br"),n._v("“Hừ!”"),c("br"),c("br"),n._v("Lông mày vung lên, Kim Tòng Hải không khẩn trương chút nào chi ý, bàn chân trên mặt đất đạp mạnh, vừa người hướng về phía trước."),c("br"),c("br"),n._v("Thô to năm ngón tay chân khí gào thét, đem không khí chung quanh áp súc lên, đem vây quét mà đến kiếm khí, toàn bộ ngăn tại bên ngoài."),c("br"),c("br"),n._v("Trương Huyền đây là lần thứ nhất thấy Hóa Phàm tứ trọng cường giả lần thứ nhất toàn lực thi triển lực lượng, chỉ nhìn mấy lần, liền tràn đầy khiếp sợ."),c("br"),c("br"),n._v("Kim Tòng Hải mặc dù không dụng binh khí, nhưng một chiêu một thức trong lúc đó, tự mang chuẩn mực, lực lượng hoặc nhẹ hoặc nặng, đem không khí bốn phía điều động, sáu người hợp kích trận pháp cứ việc xảo diệu, muốn tổn thương hắn, nhưng cũng không dễ dàng như vậy."),c("br"),c("br"),n._v("Bất quá, hắn đồng dạng bị sáu cái trường kiếm vây khốn, trong thời gian ngắn muốn phá vỡ, cũng không đơn giản như vậy."),c("br"),c("br"),n._v("Đương nhiên, đây là hắn không dùng độc nguyên nhân, một khi sử dụng, mặc kệ trận pháp gì, đến lại nhiều người, cũng lập tức bại lui."),c("br"),c("br"),n._v("Chỉ là, giữa song phương đồng thời không có sinh tử đại thù, lại nói, mấy người này còn không đến mức để hắn dùng tới tuyệt chiêu."),c("br"),c("br"),n._v("Bành bành bành!"),c("br"),c("br"),n._v("Hai bên lực lượng hung mãnh, nhưng chỉ ở phương viên mấy chục mét bên trong xoay quanh, cũng không tiết lộ, cho thấy cực mạnh lực khống chế."),c("br"),c("br"),n._v("“Ta tới giúp ngươi!”"),c("br"),c("br"),n._v("Thấy hai bên đánh cho khó bỏ khó phân, trong thời gian ngắn không cách nào quyết ra thắng bại, sợ chậm trễ quá lâu đã quấy rầy Yêu Thần thú, Trại các chủ một tiếng thấp giọng hô, đi vào vòng chiến."),c("br"),c("br"),n._v("Thực lực của hắn cùng Kim Tòng Hải so, không hề yếu, một thêm vào, hợp kích kiếm trận lập tức không chịu nổi, sáu cái thanh niên nhao nhao lui lại."),c("br"),c("br"),n._v("Mấy chiêu về sau, trường kiếm tuột tay, mọi người tựa như hư thoát."),c("br"),c("br"),n._v("Thực lực của bọn hắn vốn cũng không như, lại có hai vị Hóa Phàm tứ trọng cường giả, tự nhiên không chống đỡ được."),c("br"),c("br"),n._v("“Tốt!”"),c("br"),c("br"),n._v("Thấy đem hắn đánh bại, Trại các chủ, Kim Tòng Hải còn muốn tiến lên, Trương Huyền khoát tay áo: “Đừng đánh nữa, đều là người một nhà!”"),c("br"),c("br"),n._v("Nghe nói như thế, mọi người tất cả đều sững sờ."),c("br"),c("br"),n._v("Chẳng lẽ Trương sư nhận biết những này?"),c("br"),c("br"),n._v("“Người một nhà?” Áo bào xám thanh niên càng là nghi hoặc, kỳ quái nhìn qua."),c("br"),c("br"),n._v("“Tại hạ tuần thú sư Trương Huyền!”"),c("br"),c("br"),n._v("Không để ý tới đối phương thái độ, Trương Huyền tiến về phía trước một bước, cổ tay khẽ đảo, đại biểu thân phận tuần thú sư huy chương lấy ra ngoài: “Chắc hẳn các vị cũng đều là tuần thú sư đi!”"),c("br"),c("br"),n._v("Nhìn thấy huy chương, mấy cái thanh niên lập tức nhẹ nhàng thở ra, từng người cổ tay khẽ đảo, cũng lấy ra đại biểu thân phận đồ vật."),c("br"),c("br"),n._v("Quả nhiên cùng Trương Huyền nói đồng dạng, đều là tuần thú sư."),c("br"),c("br"),n._v("“Các ngươi là Thú đường người?”"),c("br"),c("br"),n._v("Nhướng mày, Trại các chủ hỏi."),c("br"),c("br"),n._v("Có thể một cái xuất hiện sáu vị tuần thú sư, chỉ có Thú đường mới có năng lực này."),c("br"),c("br"),n._v("“Vâng!” Áo bào xám thanh niên gật đầu."),c("br"),c("br"),n._v("“Tại hạ Giám Bảo các Trại Tiêu Vũ, chẳng biết các ngươi là đường chủ là Hàn Xung, vẫn là La Minh?” Trại các chủ nói."),c("br"),c("br"),n._v("“Là Hàn Xung đường chủ! Trại Tiêu Vũ... Nguyên lai là Trại các chủ! Vãn bối thất kính...”"),c("br"),c("br"),n._v("Nghe được tên, đầu tiên là sững sờ, lập tức nhớ tới một người, áo bào xám thanh niên giật nảy mình, vội vàng ôm quyền."),c("br"),c("br"),n._v("Thú đường phân bộ, toàn bộ vạn quốc liên minh, có hai cái, đường chủ theo thứ tự là Hàn Xung cùng La Minh, nhưng Giám Bảo các chỉ có một cái, ngay tại Hồng Hải thành. Vị Các chủ này, tu vi khả năng không bằng bọn hắn hội trưởng, địa vị không chút nào không kém, thậm chí chịu đến các chức nghiệp tôn trọng, gặp nhau cực lớn."),c("br"),c("br"),n._v("Cùng bọn hắn Hàn Các chủ đồng dạng địa vị người,"),c("br"),c("br"),n._v("Thua cũng không tính là gì."),c("br"),c("br"),n._v("“Ừm, nguyên lai là lão Hàn người, ta và các ngươi Hàn đường chủ là bạn cũ.”"),c("br"),c("br"),n._v("Biết lai lịch, Trại các chủ gật gật đầu, đồng thời tràn đầy nghi ngờ nhìn qua: “Các ngươi Thú đường phân bộ tại Vạn Quốc thành lân cận, tới đây làm gì?”"),c("br"),c("br"),n._v("Hàn Xung vì đường chủ Thú đường phân bộ, tại khoảng cách Vạn Quốc thành rất gần bên trong dãy núi, nơi này là Cửu Khúc sơn, chênh lệch mấy chục vạn cây số, một đám tuần thú sư chạy nơi này làm gì?"),c("br"),c("br"),n._v("“Bẩm báo Trại các chủ, là chúng ta đường chủ ngay tại thuần phục Yêu Thần thú...” Biết đối phương là đường chủ bạn cũ, áo bào xám thanh niên cũng không đang giấu giếm, nói."),c("br"),c("br"),n._v("“Thuần phục Yêu Thần thú? Các ngươi đường chủ không phải ba năm trước đây liền động thủ, cũng không thành công ư?” Trại các chủ kỳ quái."),c("br"),c("br"),n._v("Vừa rồi hắn cùng Tôn Cường nói qua, vị này Hàn Xung đường chủ, từng tại ba năm trước đây mang theo mười hai vị trưởng lão vây bắt Yêu Thần thú, kết quả sắp thành lại bại, chẳng lẽ còn chưa hết hi vọng, lại chạy tới?"),c("br"),c("br"),n._v("“Đường chủ là tới qua, bởi vì lần kia không thành công, cho nên ba năm này, không làm gì liền đến thủ tại chỗ này, tới câu thông giao lưu... Thời gian chung sống dài như vậy, một người một thú quan hệ đã rất khá, cho nên... Đường chủ dự định hôm nay thi triển cuối cùng thủ đoạn, đem thuần phục, vì phòng ngừa bị quấy rầy, để chúng ta thủ tại chỗ này, không cho man thú, người xa lạ xông vào!”"),c("br"),n._v("Áo bào xám thanh niên nói."),c("br"),c("br"),n._v("Trại các chủ gật đầu."),c("br"),c("br"),n._v("Trương Huyền cũng hiểu được."),c("br"),c("br"),n._v("Phổ thông tuần thú sư không có thiên đạo chân khí, không có khả năng coi đây là mồi nhử, bức bách man thú khuất phục, chỉ có thể dựa vào tích lũy tháng ngày tiếp cận, nịnh nọt, đem hắn cảm hóa."),c("br"),c("br"),n._v("Đã muốn làm sơ Mạc Vũ thuần phục Thanh Ưng Thú, cũng là dựa vào các loại bảo vật hấp dẫn, mới thu hoạch được hảo cảm."),c("br"),c("br"),n._v("Thời gian ba năm, nhờ vào Hàn Xung đường chủ thủ đoạn, chắc hẳn đầu này Yêu Thần thú đã bị cảm hóa không sai biệt lắm, lúc này mới dự định nhất cổ tác khí đem thuần phục."),c("br"),c("br"),n._v("“Chúng ta có thể hay không đi qua nhìn một chút?”"),c("br"),c("br"),n._v("Nghe được bạn cũ ngay tại thuần phục đầu này toàn bộ liên minh đều tiếng xấu chiêu lấy man thú, do dự một chút, Trại các chủ nói: “Nếu như gặp phải phiền phức, cũng có thể hỗ trợ một hai.”"),c("br"),c("br"),n._v("Thuần thú cũng không phải là dễ dàng như vậy, nhẹ nhõm thuần phục còn tốt, không thành công liền vô cùng có khả năng chọc giận đối phương."),c("br"),c("br"),n._v("Nghe nói ba năm trước đây, Hàn đường chủ cũng đừng cái tên này tổn thương qua, tu dưỡng qua hơn một tháng."),c("br"),c("br"),n._v("“Ừm... Được rồi!” Chần chờ một chút, áo bào xám thanh niên gật gật đầu: “Bất quá, các ngươi phải cẩn thận một chút, Yêu Thần thú tính tình cao ngạo ngang ngược, ngàn vạn không thể chọc giận!”"),c("br"),c("br"),n._v("Trại các chủ địa vị tôn sùng, coi như hắn muốn ngăn trở, cũng không đủ tư cách, dù sao hiện tại ngay tại chuẩn bị, thuần thú còn chưa bắt đầu, coi như dẫn người vào đi, đường chủ cũng hẳn là sẽ không trách tội."),c("br"),c("br"),n._v("“Yên tâm đi, ta cùng các ngươi đường chủ là nhiều năm bạn cũ, sẽ không chậm trễ hắn sự tình!” Trại các chủ cười nói."),c("br"),c("br"),n._v("Biết loại này cường giả, tự kiềm chế thân phận, khinh thường tại nói dối, áo bào xám thanh niên nhẹ gật đầu, bàn giao còn lại mấy cái thanh niên một tiếng: “Mấy người các ngươi tiếp tục thủ tại chỗ này, đừng để man thú, người xa lạ đi qua!”"),c("br"),c("br"),n._v("Nói xong nói một tiếng, đi đầu hướng trong sơn cốc đi đến: “Trại các chủ, mời tới bên này.”"),c("br"),c("br"),n._v("Mọi người đi theo."),c("br"),c("br"),n._v("Đi một hồi, chật hẹp tràn đầy cây cối sơn cốc rộng mở trong sáng, tiếng nước oanh minh, một cái thác nước, theo khe núi bay chảy xuống, tại mặt đất tạo thành một cái đường kính vài trăm mét đầm nước."),c("br"),c("br"),n._v("Đầm nước cách đó không xa trên vách đá, là cái cự đại sơn động, ngoài động, mấy cái lão giả đang đứng ở bên ngoài, tựa hồ tại chờ đợi cái gì."),c("br"),c("br"),n._v("Nghe được tiếng bước chân, mấy người xoay đầu lại, ở giữa nhất một cái lão giả, đang muốn nói chuyện, thấy được trong đám người Trại các chủ, ánh mắt sáng lên: “Trại các chủ, làm sao ngươi tới nơi này?”"),c("br"),c("br"),n._v("“Vừa vặn đi ngang qua nơi đây, tìm vài thứ, bị của ngươi các học sinh ngăn lại, thế mới biết, ngươi dự định lần nữa thuần phục Yêu Thần thú, liền đến nhìn xem!” Trại các chủ nói."),c("br"),c("br"),n._v("Chính là Vạn Quốc thành Thú đường đường chủ, Hàn Xung!"),c("br"),c("br"),n._v("“Tình huống của ta, ngươi cũng biết, vạn quốc liên minh hai đại Thú đường, cạnh tranh kịch liệt, chỉ có một vị đường chủ, có đi vào Huyễn Vũ đế quốc bồi dưỡng cơ hội, nếu như không thuần phục một đầu có thể chấn nhiếp những người khác man thú, thực sự không có cách nào...”"),c("br"),c("br"),n._v("Hàn Xung đường chủ cười khổ."),c("br"),c("br"),n._v("Vạn quốc liên minh, hai đại Thú đường, một mực cạnh tranh, giằng co không xong, vì tranh đoạt đi cao hơn Thú đường bồi dưỡng tư cách, hai đại đường chủ, đã minh tranh ám đấu rất nhiều năm."),c("br"),c("br"),n._v("Đáng tiếc, đối với tuần thú sư tới nói, cái khác tranh đấu đều là giả, có thể thuần phục một đầu đem ra được linh thú, mới là vương đạo."),c("br"),c("br"),n._v("Bởi vậy, ba năm qua, một mực giữ vững không ngừng, chính là muốn đem đầu này Yêu Thần thú thuần phục, đi tại đối phương phía trước."),c("br"),c("br"),n._v("“Như thế... Nghe nói ba năm này ngươi một mực tới câu thông, thế nào?” Trại các chủ nhìn qua."),c("br"),c("br"),n._v("“Còn cùng trước đó không sai biệt lắm, Yêu Thần thú, trong cơ thể ẩn chứa Long tộc huyết mạch, tính cách cao ngạo quái gở, nếu như dễ dàng như vậy thuần phục, cũng không trở thành tốn hao như thế đại tâm huyết! Nếu như không phải Huyễn Vũ đế quốc đặc sứ, qua mấy ngày liền xuống đến, ta thực sự hết cách rồi, khẳng định không như thế lỗ mãng, hôm nay liền quyết định than bài!”"),c("br"),c("br"),n._v("Hàn đường chủ lắc đầu."),c("br"),c("br"),n._v("Đối với loại cấp bậc này linh thú tới nói, ba năm ở chung, vẫn là ngắn chút, nhưng thực sự hết cách rồi, đặc sứ lập tức liền muốn tới, nếu như không thể trước lúc này đem đầu này đại gia hỏa thuần phục, cùng vị kia La Minh đường chủ so, chẳng những không có mảy may ưu thế, thậm chí còn mang theo thế yếu."),c("br"),c("br"),n._v("Một khi tuyển không lên, tấn cấp ngũ tinh tuần thú sư con đường, không sai biệt lắm liền bị triệt để phá hỏng, cả đời vô vọng."),c("br"),c("br"),n._v("“Vậy các ngươi chờ ở chỗ này là...”"),c("br"),c("br"),n._v("Đã muốn thuần phục, làm nhân vật chính Yêu Thần thú ở nơi nào? Một đám người đứng ở chỗ này lại muốn làm cái gì?"),c("br"),c("br"),n._v("“Ngay tại cái kia trong sơn động, cái tên này bình thường đều là vào lúc giữa trưa đi ra kiếm ăn, hiện tại canh giờ không đến, còn tại nghỉ ngơi!” Hàn đường chủ chỉ về phía trước."),c("br"),c("br"),n._v("“Ah!”"),c("br"),c("br"),n._v("Trại các chủ nhẹ gật đầu."),c("br"),c("br"),n._v("“Mấy vị này là vãn bối của ngươi?”"),c("br"),c("br"),n._v("Giải thích hết, Hàn đường chủ nhìn về phía Trương Huyền đám người, nghi ngờ hỏi."),c("br"),c("br"),n._v("“Khụ khụ!”"),c("br"),c("br"),n._v("Nghe được nói Trương sư là vãn bối của hắn, Trại các chủ giật nảy mình, đang muốn giải thích, liền nghe Trương Huyền thanh âm vang lên: “Tại hạ Trương Huyền, là Trại các chủ bằng hữu!”"),c("br"),c("br"),n._v("“Bằng hữu?”"),c("br"),c("br"),n._v("Hàn đường chủ sững sờ."),c("br"),c("br"),n._v("Trại các chủ, thế nhưng là tứ tinh giám bảo sư, địa vị cao, so với hắn đều không hề yếu, tại sao cùng một cái niên kỷ không đủ hai mươi tiểu gia hỏa làm bằng hữu?"),c("br"),c("br"),n._v("“Chẳng biết... Trương tiểu huynh đệ, là loại nào chức nghiệp?”"),c("br"),c("br"),n._v("Tràn đầy nghi hoặc, nhịn không được hỏi."),c("br"),c("br"),n._v("“Bẩm báo đường chủ, hắn là nhị tinh tuần thú sư, vừa rồi ta xem qua huy chương!”"),c("br"),c("br"),n._v("Trương Huyền vẫn chưa trả lời, một bên áo bào xám thanh niên nói."),c("br"),c("br"),n._v("“Nhị tinh tuần thú sư?”"),c("br"),c("br"),n._v("Hàn đường chủ vẻ mặt càng thêm cổ quái."),c("br"),c("br"),n._v("Loại cấp bậc này tuần thú sư, bọn hắn Thú đường vừa nắm một bó to, lúc nào Trại các chủ sa đọa đến cùng loại người này trở thành bằng hữu?"),c("br"),c("br"),n._v("(Đầu tháng tất cả mọi người có giữ gốc nguyệt phiếu, xin bầu cho lão nhai ah, đều rớt xuống thứ 15 tên, ô ô!)"),c("br"),c("br"),n._v("Convert by: ThấtDạ"),c("br"),c("br"),c("br"),c("br")]),n._v(" "),c("hr"),n._v(" "),c("p",[c("a",{attrs:{href:"./chap-501"}},[n._v("Chương trước")]),n._v("  |  "),c("a",{attrs:{href:"./chap-503"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);