(window.webpackJsonp=window.webpackJsonp||[]).push([[1747],{2101:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-494-cung-ran-cuop-【-canh-3-】"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-494-cung-ran-cuop-【-canh-3-】"}},[n._v("#")]),n._v(" Chương 494: Cứng rắn cướp 【 Canh [3] 】")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Nguyên lai tiểu huynh đệ cũng là tuần thú sư, trẻ tuổi như vậy liền đạt tới nhị tinh, quả nhiên anh hùng còn trẻ!”"),t("br"),t("br"),n._v("Trong lòng kỳ quái, trên mặt lại không biểu hiện ra ngoài, chắp tay cười một câu, liền không nói thêm gì nữa."),t("br"),t("br"),n._v("Không đến hai mươi nhị tinh tuần thú sư, mặc dù thiên tài, nhưng ở bọn hắn Thú đường, còn có thể cào ra một nắm lớn, không tính là gì."),t("br"),t("br"),n._v("Thấy hắn trong giọng nói mang theo qua loa chi ý, Trương Huyền cũng lười giải thích."),t("br"),t("br"),n._v("Lần này tới là vì Vu hồn mộ, đối phương có biết hay không, coi trọng không coi trọng, đều cùng hắn không liên quan quá nhiều, chỉ cần không chậm trễ chính sự là được."),t("br"),t("br"),n._v("“Hàn đường chủ, trương...”"),t("br"),t("br"),n._v("Thấy bạn cũ bộ dáng này, biết hắn khẳng định không biết trước mắt vị thanh niên này thân phận, vừa định giải thích một chút, để hắn coi trọng, liền nghe đến ngay phía trước mặt đất chấn động, một cái kịch liệt thanh âm vang lên."),t("br"),t("br"),n._v("Rống!"),t("br"),t("br"),n._v("Theo sơn động truyền đến, mang theo sét đánh hương vị, tựa như lôi âm, sóng âm xao động xuống, chung quanh khô héo cây cối, lá cây nhao nhao rơi xuống."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt ngưng tụ."),t("br"),t("br"),n._v("Không nói cái khác, chỉ bằng vào thanh âm này, đứng mũi chịu sào lời nói, Tông sư trở xuống người tu luyện, chỉ sợ đều có thể tại chỗ đánh chết, không có chút nào năng lực phản kháng."),t("br"),t("br"),n._v("Một hống chi uy, cường đại như thế, không hổ là toàn bộ vạn quốc liên minh đều nổi danh hung thú, quả nhiên đáng sợ."),t("br"),t("br"),n._v("“Nó tỉnh, bắt đầu đi!”"),t("br"),t("br"),n._v("Hàn đường chủ ánh mắt sáng lên, lại không thời gian để ý tới Trại các chủ lời nói, quay đầu dặn dò một tiếng."),t("br"),t("br"),n._v("Bên cạnh hắn mấy cái lão giả, nghe được phân phó, đồng thời gật đầu, đồng loạt tiến lên một bước, đi đầu một cái, cổ tay khẽ đảo."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Một đầu cực lớn linh thú thi thể xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Hóa Phàm nhị trọng  thi thể? Thứ này đều có thể bắt đến săn giết?”"),t("br"),t("br"),n._v("Nhìn thấy cái tên này, trong đám người Triệu Phi Vũ, lông mày nhảy một cái, có vẻ không thể tin được."),t("br"),t("br"),n._v("“Cái này linh thú rất lợi hại?” Tôn Cường nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Lộc Quần thú là một loại Hóa Phàm nhị trọng cấp bậc linh thú, tu vi không cao, nhưng tốc độ cực nhanh, chất thịt ngon, là linh thú thậm chí người tu luyện đều ưa thích con mồi, số lượng thưa thớt, toàn bộ Cửu Khúc sơn, đều chưa hẳn có thể có một đầu. Có thể bắt được, đồng thời chém giết, khẳng định hao tốn không ít thời gian cùng đại giới!”"),t("br"),t("br"),n._v("Từ nhỏ mắc phải quái bệnh, thân thể không thể động, chỉ có thể dựa vào đọc sách sống qua ngày, Lộc Quần thú vốn là hiếu kỳ, Triệu Phi Vũ nhớ kỹ rất rõ ràng."),t("br"),t("br"),n._v("“Xem ra ngươi biết không ít. Đầu này Lộc Quần thú, là chúng ta Thú đường mười bảy vị tuần thú sư đồng thời lên đường, hao tốn thời gian nửa năm, bố trí xuống Thiên La Địa Võng mới đuổi tới! Có thể nói, toàn bộ vạn quốc liên minh liền đây một đầu, còn muốn tìm một cái khác, đều làm không được, vạn kim khó cầu!”"),t("br"),t("br"),n._v("Nghe được hai người nói chuyện, áo bào xám thanh niên mỉm cười, trên mặt lộ ra vẻ kiêu ngạo."),t("br"),t("br"),n._v("Lộc Quần thú, mặc dù cấp bậc không tính là quá cao, nhưng trời sinh tính cảnh giác, giảo hoạt như cáo, nếu không phải nhiều như vậy tuần thú sư đồng thời lên đường, cũng đừng hòng bắt lấy."),t("br"),t("br"),n._v("“Ừm!” Mặc dù đối phương nói tự luyến, Triệu Phi Vũ vẫn là tán đồng nhẹ gật đầu."),t("br"),t("br"),n._v("Thứ này quả thực không dễ dàng bắt, năm đó liên minh vương thất (cũng chính là minh chủ phủ), một vị vương tử bị bệnh, cần loại này linh thú trái tim làm thuốc, từng giá cao treo giải thưởng năm năm, đều không có lấy được."),t("br"),t("br"),n._v("Cuối cùng vị vương tử kia, bởi vì dược vật không cách nào phối tề, không trị mà chết, trở thành một kinh ngạc tột độ sự tình."),t("br"),t("br"),n._v("Hắn cũng chính là nghe nói chuyện này, tìm đọc thư tịch, biết Lộc Quần thú loại sinh mạng này."),t("br"),t("br"),n._v("“Lộc Quần thú không chỉ chất thịt ngon, trọng yếu nhất chính là, loại này linh thú, lấy linh dược làm thức ăn, thực lực không cao, linh lực lại là mười phần, cùng thuốc đại bổ, bị cái khác linh thú ăn hết, rất dễ dàng đột phá cảnh giới, đạt tới cao hơn thực lực!”"),t("br"),t("br"),n._v("Trại các chủ chen vào nói: “Có thể nói... Đó là cái di động tấn cấp đan!”"),t("br"),t("br"),n._v("“Thì ra là thế, xem ra bọn hắn là muốn dùng cái tên này thi thể, lấy lòng Yêu Thần thú!”"),t("br"),t("br"),n._v("Tôn Cường giờ mới hiểu được tới, nhịn không được líu lưỡi."),t("br"),t("br"),n._v("Như thế khó mà bắt linh thú, đều làm tới, xem ra vì cái này Yêu Thần thú, Thú đường đường chủ, thật đúng là bỏ hết cả tiền vốn."),t("br"),t("br"),n._v("Bên này thấp giọng thảo luận, trước mặt Hàn đường chủ, hít sâu một hơi, tiến lên một bước."),t("br"),t("br"),n._v("“Yêu Thần huynh, lần trước cùng ngươi nói đồ vật ta đã chuẩn bị xong, ngay ở chỗ này, đi ra xem một chút đi!”"),t("br"),t("br"),n._v("Thanh âm truyền vào sơn động, bên trong một hồi yên tĩnh, ngay tại tất cả mọi người tràn đầy khẩn trương thời điểm, một tiếng gào thét, một cái to lớn thân ảnh bay ra."),t("br"),t("br"),n._v("Là một đầu cực lớn man thú, thân cao bảy, tám mét, xòe hai cánh, chừng hơn ba mươi mét, cặp mắt tinh hồng như máu, dữ tợn đáng sợ, toàn thân phủ đầy lân phiến, cho thấy phòng ngự mạnh mẽ."),t("br"),t("br"),n._v("Hóa Phàm tứ trọng đỉnh phong, toàn bộ vạn quốc liên minh người người đều kiêng kỵ siêu cấp linh thú phi hành... Yêu Thần!"),t("br"),t("br"),n._v("Rống!"),t("br"),t("br"),n._v("Tựa hồ nghe đã hiểu lời của hắn nói, cực lớn linh thú, nhìn về phía mặt đất để đó Lộc Quần thú thi thể, tràn đầy quyết liệt."),t("br"),t("br"),n._v("Linh thú, đã có thể hấp thu thiên địa linh khí, bản thân tu luyện tấn cấp, cũng có không kém ai loại trí tuệ."),t("br"),t("br"),n._v("Đầu này Lộc Quần thú thi thể, tản mát ra mùi thuốc nồng nặc cùng kịch liệt linh lực ba động, nó biết chỉ cần ăn hết, nhất định có thể tu vi tiến nhanh, coi như không đạt tới Hóa Phàm ngũ trọng, cũng khẳng định lực lượng bạo tăng, làm sao không kích động."),t("br"),t("br"),n._v("“Yêu Thần huynh chỉ cần đáp ứng điều kiện của ta, đầu này Lộc Quần thú thi thể, liền là của ngươi!”"),t("br"),n._v(" Thấy nó ý động, Hàn đường chủ nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Đầu lâu quay lại, Yêu Thần thú dường như muốn nghe xem, hắn có điều kiện gì."),t("br"),t("br"),n._v("“Ba năm này, ta cùng Yêu Thần huynh, sớm chiều ở chung, làm người bản tính, chắc hẳn ngươi cũng biết, yêu cầu của ta rất đơn giản... Hi vọng ngươi có thể làm của ta thú sủng!” Hàn đường chủ nói: “Yên tâm, chúng ta có thể ký kết công bằng khế ước, tại làm bất cứ chuyện gì trước đó, chỉ cần không nguyện ý, có thể cự tuyệt!”"),t("br"),t("br"),n._v("“Rống!”"),t("br"),t("br"),n._v("Nghe được muốn làm thú sủng, Yêu Thần thú vẻ mặt dữ tợn lên tiếng gào thét, một cỗ khí lãng từ trên người nó tản ra, cho người ta một loại cảm giác rợn cả tóc gáy."),t("br"),t("br"),n._v("Nhìn nó bộ dạng, nếu như Hàn đường chủ dám nói tiếp, tuyệt đối sẽ động thủ giết người."),t("br"),t("br"),n._v("Cái này cũng khó trách, trong cơ thể nó nắm giữ linh tộc huyết mạch, cao quý không gì sánh được, làm sao có thể thần phục một cái nho nhỏ nhân loại, làm chó giữ nhà?"),t("br"),t("br"),n._v("“Đừng có gấp làm ra quyết định!”"),t("br"),t("br"),n._v("Thấy nó thái độ kiên quyết, không chần chờ chút nào, Hàn đường chủ vẻ mặt cũng có chút khó coi, bất quá vẫn là nở nụ cười: “Ta tại thế giới loài người thân phận, chắc hẳn ngươi cũng biết, là Thú đường đường chủ, địa vị tôn sùng. Yêu Thần huynh, chỉ cần đi cùng với ta, ta sẽ đem ngươi trở thành bằng hữu tốt nhất, trên việc tu luyện vật tư, chỉ cần mở lời, coi như khó khăn đi nữa, đều sẽ nghĩ biện pháp hỗ trợ đi tìm! Mặc dù linh thú tuổi thọ kéo dài, có thể chỉ bằng vào hấp thu linh khí tăng lên cần bao lâu thời gian? Đi theo bên cạnh ta, chẳng phải so ngươi trong này mạnh hơn nhiều?”"),t("br"),t("br"),n._v("Nói xong, Hàn đường chủ vẫy tay một cái."),t("br"),t("br"),n._v("Mấy cái lão giả lần nữa hướng về phía trước, mặt đất xuất hiện một đống linh thạch."),t("br"),t("br"),n._v("Đều là hạ phẩm linh thạch, tích tụ thành núi nhỏ, chừng mấy ngàn viên, kịch liệt linh khí lên như diều gặp gió, để cho người ta ngửi bên trên một cái, liền cảm thấy toàn thân lỗ chân lông mở rộng, không nói ra được dễ chịu."),t("br"),t("br"),n._v("Linh thạch không chỉ nhân loại tu luyện cần, linh thú cũng cần."),t("br"),t("br"),n._v("Hấp thu linh thạch bên trong linh khí, rất rõ ràng muốn so trong không khí hấp thu, nhanh hơn nhiều."),t("br"),t("br"),n._v("“Đây tổng cộng là hơn tám nghìn viên linh thạch, chỉ cần ngươi đồng ý, hiện tại liền có thể cho ngươi...”"),t("br"),t("br"),n._v("Hàn đường chủ nói."),t("br"),t("br"),n._v("Hắn có thể một cái xuất ra nhiều như vậy, mà Trại các chủ lại ngay cả năm ngàn đều không bỏ ra nổi đến, cũng không phải là người sau không hắn có tiền, mà là Trại các chủ linh thạch đều mua bảo vật trân tàng, có thể xuất ra linh thạch không nhiều, thật muốn nói thân gia lời nói, khẳng định là người sau càng hơn một bậc."),t("br"),t("br"),n._v("Thật giống như Trương Huyền kiếp trước những cái kia đồ cổ người thu thập, trong nhà cất giữ bảo bối, toàn bộ bán ra lời nói, mỗi một cái thân gia đều cực cao, nhưng muốn để hắn xuất ra tiền mặt mua sắm vật phẩm lời nói, chỉ sợ so ra kém một chút tài chính hùng hậu thương nhân."),t("br"),t("br"),n._v("“Nếu như những linh thạch này đều không có vừa ý, thứ này đối ngươi khẳng định có rất tốt đẹp chỗ!”"),t("br"),t("br"),n._v("Thấy xuất ra linh thạch, Yêu Thần thú vẫn không có động tĩnh, Hàn đường chủ chần chờ một chút, một cắn răng, cổ tay khẽ đảo, một cái hộp ngọc xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Nhẹ nhàng mở ra, một viên linh thạch khảm nạm trong đó."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Linh thạch vừa xuất hiện, linh khí chung quanh, lập tức trở nên càng thêm nồng nặc lên, như là tạo thành sương mù, chỉ cần nắm đây viên linh thạch, dù là không cần hấp thu, nồng đậm linh lực đều sẽ tự chủ đi vào thân thể, cải thiện lấy cơ bắp, thân thể."),t("br"),t("br"),n._v("Đơn giản tới nói, có như vậy một viên linh thạch, đặt ở trẻ mới sinh trên người, coi như người sau sẽ không tu luyện, lấy được linh khí trong đó tẩm bổ, cũng sẽ thân thể cường kiện, bách bệnh không sinh, không dùng đến hai tháng, thân thể cường tráng trình độ, cũng không dưới tại Bì Cốt cảnh cường giả."),t("br"),t("br"),n._v("“Chẳng lẽ là... Linh thạch trung phẩm?”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, Trương Huyền nắm đấm căng thẳng."),t("br"),t("br"),n._v("Nhìn qua vô số thư tịch, đối linh thạch phân cấp, cũng biết một chút."),t("br"),t("br"),n._v("Hắn hiện tại trong tay linh thạch, đều là cấp thấp nhất, cũng chính là cái gọi là hạ phẩm."),t("br"),t("br"),n._v("Loại cấp bậc này linh thạch, cho Hóa Phàm cảnh tứ trọng trở xuống tu luyện, không có vấn đề chút nào, nhưng muốn nhờ vào trong đó linh lực, xung kích cảnh giới cao hơn, sẽ rất khó."),t("br"),t("br"),n._v("Không nói những người khác, tựa như hắn, bởi vì Thiên Đạo chân khí quá mức tinh thuần, tuy chỉ là Hóa Phàm nhất trọng Tăng Thọ cảnh đỉnh phong, có thể hạ phẩm linh thạch, tác dụng đã không lớn, muốn đạt tới Nguyên Khí cảnh, không chỉ cần Thiên Đạo bí tịch, quan trọng hơn đến chính là loại này linh thạch trung phẩm!"),t("br"),t("br"),n._v("Không nói cái khác, cứ như vậy một viên, tuyệt đối có thể để cho hắn theo Tăng Thọ cảnh đỉnh phong, xung kích Nguyên Khí cảnh đỉnh phong, đều dư xài."),t("br"),t("br"),n._v("Tuy nói lượng biến có thể gây nên biến chất, nhưng bản chất chênh lệch quá lớn, lại nhiều lượng cũng vô dụng."),t("br"),t("br"),n._v("Thật giống như, đủ nhiều số lượng cỏ tranh, có lẽ có thể mua sắm kim cương, có thể nghĩ muốn thay thế kim cương tác dụng, khẳng định làm không được."),t("br"),t("br"),n._v("Bởi vậy, coi như một vạn lần phẩm linh thạch có thể thay đổi một viên trung phẩm, nhưng muốn thay thế linh thạch trung phẩm hiệu quả, vẫn là không thể nào."),t("br"),t("br"),n._v("“Không sai, chính là linh thạch trung phẩm!”"),t("br"),t("br"),n._v("Không nghĩ tới bạn cũ xuất ra thứ này, Trại các chủ con mắt cũng là trợn tròn."),t("br"),t("br"),n._v("Linh thạch trung phẩm trân quý vô song, coi như vạn quốc liên minh, đều không có tồn kho mấy cái, bạn cũ có thể xuất ra một viên, chỉ sợ tốn hao đại giới bao lớn, so với Lộc Quần thú đều chỉ nhiều không ít!"),t("br"),t("br"),n._v("Rống!"),t("br"),t("br"),n._v("Nhìn thấy linh thạch trung phẩm, không chỉ mọi người kinh ngạc, Yêu Thần thú con mắt càng thêm đỏ thẫm."),t("br"),t("br"),n._v("Xuất ra áp đáy hòm bảo bối, Hàn đường chủ cũng là mặt mũi tràn đầy không muốn, có điều, cũng biết không thấy tôm là không thả tép, một cắn răng: “Chỉ cần Yêu Thần huynh đáp ứng làm của ta thú sủng, đây viên linh thạch trung phẩm, liền tặng cho ngươi, để ngươi sử dụng... Hả? Yêu Thần huynh, ngươi muốn làm gì...”"),t("br"),t("br"),n._v("Tiếng nói còn không có kết thúc, chỉ thấy Yêu Thần thú, như là đao phong hai cánh bỗng nhiên một cái, thân thể khổng lồ, thẳng tắp lao đến, móng vuốt lăng không, trong tiếng thét gào, đối Hàn đường chủ trong tay linh thạch liền vồ tới."),t("br"),t("br"),n._v("“Cứng rắn cướp? Bà mẹ nó!”"),t("br"),t("br"),n._v("(Canh [3] đến! Bởi vì đổi chỗ cầu, hai ngày này một mực kẹt văn, có thể lão nhai vẫn như cũ không giảm bớt đổi mới, hôm qua Thiên Nhãn con ngươi đột nhiên hoa cái gì đều nhìn không thấy, vẫn như cũ giữ vững hoàn thành đổi mới, lại tăng thêm một chương... Khổ cực như vậy nghiêm túc lão nhai, có thể hay không đáng giá đem bọn ngươi nguyệt phiếu đưa tới, cảm tạ!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-502"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-504"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);