(window.webpackJsonp=window.webpackJsonp||[]).push([[1752],{2107:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-499-thuan-phuc-yeu-than-thu"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-499-thuan-phuc-yeu-than-thu"}},[n._v("#")]),n._v(" Chương 499: Thuần phục Yêu Thần thú")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 499: Thuần phục Yêu Thần thú"),t("br"),t("br"),n._v("Thả người?"),t("br"),t("br"),n._v("Ai cũng biết để nó thả người là trực tiếp nhất biện pháp, thế nhưng là... Ngươi nhìn cái tên này khí thế hùng hổ, muốn ăn thịt người bộ dạng, có thể thả ư?"),t("br"),t("br"),n._v("Vây khốn nhân gia, dùng hợp kích trận pháp động thủ, đã sớm kết xuống tử thù, hiện tại hận không thể giết hắn cho thống khoái, thả? Nói đùa cái gì!"),t("br"),t("br"),n._v("Vốn cho rằng người thanh niên này có thể đoán được phệ hồn kiến, vừa rồi càng là chỉ điểm giang sơn, lời thề son sắt, thật có thể có biện pháp tốt cứu mình, nằm mơ đều không có nghĩ đến, nói như thế không đáng tin cậy!"),t("br"),t("br"),n._v("Cùng hắn chờ Yêu Thần thú buông tha hắn, còn không bằng chờ lấy bị đánh chết tới trực tiếp."),t("br"),t("br"),n._v("Mặt mũi tràn đầy ưu thương, Hàn đường chủ đang cảm thấy lại không hi vọng, liền nghe Trại các chủ tra hỏi tiếp tục vang lên."),t("br"),t("br"),n._v("“Thả người? Cần làm cái gì, ta hiện tại liền chuẩn bị!”"),t("br"),t("br"),n._v("“Không cần chuẩn bị, đem đầu này đại gia hỏa thuần phục, trở thành thú sủng, tự nhiên là nghe lời!” Thanh niên thanh âm nhàn nhạt lần nữa truyền đến."),t("br"),t("br"),n._v("“Thuần phục? Trở thành thú sủng?” Hàn đường chủ mắt tối sầm lại, chợt cảm thấy sống không thể yêu."),t("br"),t("br"),n._v("Hắn tốn hao ròng rã ba năm, ném cho Yêu Thần thú bảo vật, đều tích tụ thành núi, lại dùng Lộc Quần thú, linh thạch trung phẩm các loại bảo vật làm trao đổi, đối phương nhìn cũng không nhìn một chút, ngươi ngược lại tốt, mở lời liền nói thuần phục, thật muốn đơn giản như vậy, ta còn bị giẫm ở đây, sắp tắt thở sao?"),t("br"),t("br"),n._v("“Được rồi, đừng hy vọng cái tên này, vẫn là ta chính mình nghĩ biện pháp đi!”"),t("br"),t("br"),n._v("Biết thanh niên này không đáng tin cậy, Hàn đường chủ trong cơ thể bỗng nhiên tuôn ra một cỗ lực lượng, muốn đẩy đối phương ra móng vuốt, kết quả mới vừa dùng sức, liền cảm thấy Yêu Thần thú lực lượng cũng đột nhiên bạo tăng."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Khí lãng lăn lộn, toàn thân mềm nhũn, bị dẫm đến rơi vào mặt đất hơn nửa thước sâu, mặt mũi tràn đầy bụi bặm."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Máu tươi lần nữa phun ra, mãnh liệt trùng kích vào, thân thể thương thế tăng thêm, cả người có chút mê muội."),t("br"),t("br"),n._v("“Không nghĩ tới thuần thú không được, cuối cùng chết ở chỗ này...”"),t("br"),t("br"),n._v("Trong lòng sinh ra một cỗ thê lương, cùng thuần thú đánh cả một đời quan hệ, vốn cho rằng rất nhiều thủ đoạn sử xuất, đầu này đại gia hỏa, khẳng định sẽ trở thành hắn thú sủng, nằm mơ đều không có nghĩ đến, cuối cùng vậy mà chết tại đối phương dưới vuốt, hơn nữa còn là bị cứ thế mà giẫm chết..."),t("br"),t("br"),n._v("Suy nghĩ một chút đều cảm thấy uất ức."),t("br"),t("br"),n._v("Ngay tại nhắm mắt đợi chết, cảm thấy sống không thể yêu thời điểm, liền nghe đến một cái tựa như trâu kêu, lại giống tiếng sấm thanh âm vang lên, ngay sau đó liền cảm thấy đè ở trên người móng vuốt, trong nháy mắt biến mất."),t("br"),t("br"),n._v("Ở trên người sờ soạng hai lần, phát hiện còn sống, hận không thể đem chính mình mới thành bánh thịt Yêu Thần thú, quả thực đem móng vuốt lấy ra, lúc này mới mở to mắt, nhịn không được nhìn lại, vừa nhìn phía dưới, toàn thân co lại, kém chút không tại chỗ ngất đi."),t("br"),t("br"),n._v("“Cái này... Cái này...”"),t("br"),t("br"),n._v("Bờ môi run rẩy, con mắt trừng đến sắp rơi ra hốc mắt, Hàn đường chủ phảng phất thấy được cảnh tượng khó tin, lập tức liền phải thừa nhận không được."),t("br"),t("br"),n._v("Chỉ gặp ngay phía trước, vừa rồi khí thế hùng hổ, muốn giết chết mình cho thống khoái, cao ngạo không bị trói buộc, cận kề cái chết đều không khuất phục Yêu Thần thú, lúc này đang ngoan ngoãn nằm ở một thân ảnh trước mặt, đầu lâu dán chặt lấy mặt đất, thô to đầu lưỡi không ngừng liếm láp đối phương đế giày, một mặt cung kính cùng lấy lòng, cùng một đầu thuần phục nhiều năm chó xù thấy được chủ nhân đồng dạng, không nói ra được hèn mọn."),t("br"),t("br"),n._v("Tình huống gì?"),t("br"),t("br"),n._v("Cái tên này không phải cao ngạo tới cực điểm, thề sống chết không theo ư? Không phải cho dù tốt ăn đồ vật, cũng không cách nào để hắn dao động ư?"),t("br"),t("br"),n._v("Thế nào thời gian nháy mắt liền liếm nhân gia đế giày?"),t("br"),t("br"),n._v("Hơn nữa, ngươi đây là ánh mắt gì? Một mặt tôn kính cùng sùng bái, càng là nước bọt chảy ròng, hận không thể đem người ta một cái khác đế giày cũng liếm lấy... Có thể hay không có chút tiết tháo?"),t("br"),t("br"),n._v("Dùng sức dụi dụi con mắt, đang cảm thấy tự mình có phải hay không nhìn lầm, Hàn đường chủ chỉ thấy đối diện thân ảnh, mặt mũi tràn đầy không nhịn được một cước đối Yêu Thần thú mặt to đạp tới."),t("br"),t("br"),n._v("“Cút một bên ăn cỏ đi, đem ta giày đều làm bẩn!”"),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Hàn đường chủ nhoáng một cái."),t("br"),t("br"),n._v("Yêu Thần thú cung kính như thế, ngươi thế mà đạp nó, còn để nó ăn cỏ? Nó thế nhưng là ăn thịt tính động vật, làm sao có thể ăn cỏ..."),t("br"),t("br"),n._v("Ngay tại chần chờ, chỉ thấy cao ngạo không gì sánh được Yêu Thần thú, không chần chờ chút nào, tràn đầy hưng phấn hấp tấp chạy đến một bên, mở ra miệng rộng, không ngừng xé rách trên đất cỏ xanh, nhai say sưa ngon lành, sợ người khác cùng nó đoạt..."),t("br"),t("br"),n._v("Ba!"),t("br"),t("br"),n._v("Sợ là đang nằm mơ, hung hăng giật mình một cái tát, kịch liệt đau đớn để hắn thanh tỉnh một chút, lần nữa nhìn lại, vẫn như cũ nhìn thấy Yêu Thần thú đang cái mông vểnh lên, ra sức cùng cỏ xanh đối nghịch."),t("br"),t("br"),n._v("“Ta X! Đây rốt cuộc chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Không ngừng nắm tóc, Hàn đường chủ triệt để ngất."),t("br"),t("br"),n._v("Cách đó không xa thân ảnh này, hắn thấy rõ ràng, chính là vừa rồi cái kia nói mạnh miệng nhị tinh thuần thú sư, Trương Huyền."),t("br"),t("br"),n._v("Thuần ba năm, đối phương không nhúc nhích, còn chuyên tâm muốn đem mình giết chết... Cái tên này chỉ rống lên một tiếng, vừa liếm giày, vừa ăn cỏ xanh..."),t("br"),t("br"),n._v("Còn có thiên lý hay không?"),t("br"),t("br"),n._v("Đến cùng mình là tứ tinh thuần thú sư, vẫn là đối phương là?"),t("br"),t("br"),n._v("“Chẳng lẽ... Trại các chủ đối với hắn cung kính, là bởi vì cái này?”"),t("br"),t("br"),n._v("Đột nhiên, một cái ý nghĩ xông ra."),t("br"),t("br"),n._v("Trại các chủ đối người thanh niên này cung kính có thừa, trước đó còn một mực kỳ quái, đến cùng chuyện gì xảy ra, hiện tại xem ra, chỉ sợ thanh niên này, không chỉ là nhị tinh thuần thú sư đơn giản như vậy."),t("br"),t("br"),n._v("Nếu không, làm sao có thể một cuống họng đi xuống, Yêu Thần thú lập tức thần phục, còn biểu hiện như thế không tôn nghiêm?"),t("br"),t("br"),n._v("Điều chỉnh một cái hô hấp, cố nén kịch liệt đau nhức, đi tới đối phương trước mặt: “Hàn mỗ đa tạ Trương thú sư ân cứu mạng!”"),t("br"),t("br"),n._v("Nếu không phải đối phương thuần phục Yêu Thần thú, hắn khẳng định đã chết, bất kể nói thế nào, ân cứu mạng, không thể không báo."),t("br"),t("br"),n._v("Trương Huyền khoát tay áo: “Ta là xem ở Trại các chủ trên mặt mũi, mới ra tay, muốn cảm ơn, liền cảm ơn Trại các chủ đi!”"),t("br"),n._v("Nếu không phải xem ở Trại các chủ trên mặt mũi, ai quản ngươi ah!"),t("br"),t("br"),n._v("“Vẫn là đa tạ Trương thú sư, nếu như ngươi không xuất thủ, ta khẳng định đã chết rồi!”"),t("br"),t("br"),n._v("Lần nữa cảm kích nói một câu, Hàn đường chủ lúc này mới ôm quyền nhìn qua: “Trại các chủ, đa tạ...”"),t("br"),t("br"),n._v("“Nhiều năm bạn cũ, không cần khách khí như thế!” Trại các chủ hơi đỏ mặt: “Ta cái gì bề bộn cũng không giúp đỡ, là Trương sư thuần phục Yêu Thần thú!”"),t("br"),t("br"),n._v("“Trương sư?”"),t("br"),t("br"),n._v("Trước đó mặc dù cũng nghe từng tới xưng hô thế này, nhưng cho rằng kêu là “Trương thú sư”, dù sao chiến đấu bên trong, khoảng cách lại xa, nghe không rõ ràng cũng bình thường, giờ phút này đứng tại trước mặt, nghe rõ ràng, chấn động trong lòng."),t("br"),t("br"),n._v("Chẳng lẽ, trước mắt vị này, vẫn là cái danh sư hay sao?"),t("br"),t("br"),n._v("“Ừm, Trương sư không riêng gì danh sư, càng là một vị nhị tinh thuần thú sư, ngũ tinh giám bảo sư, chuẩn tứ tinh trận pháp sư!” Trại các chủ giải thích nói."),t("br"),t("br"),n._v("“Danh sư, ngũ tinh... Giám bảo sư? Chuẩn tứ tinh trận pháp sư?” Hàn đường chủ hơi đỏ mặt."),t("br"),t("br"),n._v("Trước đó còn tưởng rằng đối phương chỉ là cái nhị tinh thuần thú sư, có nhiều khinh thị, nằm mơ đều không có nghĩ đến, còn có nhiều như vậy chức nghiệp!"),t("br"),t("br"),n._v("Không nói cái khác, chỉ nói danh sư đây một cái, liền có thể ép tới hắn không ngẩng đầu được lên."),t("br"),t("br"),n._v("Danh sư, không cho khinh thị cùng vũ nhục, dám làm như thế, coi như tại chỗ bị đánh chết, đều không có người dám thay hắn nói nhiều một câu."),t("br"),t("br"),n._v("Huống chi, vẫn là ngũ tinh giám bảo sư, chuẩn tứ tinh trận pháp sư!"),t("br"),t("br"),n._v("“Đó là đương nhiên, nhà chúng ta thiếu gia, không chỉ có những nghề nghiệp này, vẫn là y sư, luyện đan sư, thư họa sư!” Thấy hắn bị hù sợ, thanh niên sau lưng một tên mập đi lên phía trước, hừ một tiếng."),t("br"),t("br"),n._v("“Bảy đại... Chức nghiệp?” Hàn đường chủ nuốt ngụm nước bọt, bờ môi run rẩy."),t("br"),t("br"),n._v("Một người học tập một cái chức nghiệp, liền muốn tốn hao vô số tinh lực, vô cùng thời gian, trước mắt cái này, thế mà thân kiêm bảy đại chức nghiệp..."),t("br"),t("br"),n._v("Vạn quốc liên minh tại sao có thể có như vậy một cái quái thai?"),t("br"),t("br"),n._v("Khó trách Trại các chủ như thế tôn trọng, nếu là sớm biết những này thân phận, đánh chết cũng không dám giả vờ giả vịt ah."),t("br"),t("br"),n._v("“Đúng vậy a, thế nào, nhà chúng ta thiếu gia, cứu ngươi một mạng, một câu cảm tạ coi như xong?”"),t("br"),t("br"),n._v("Mập mạp con mắt nghiêng nhìn qua: “Vừa rồi nhìn ngươi thuần phục man thú đồ tốt cầm không ít, hiện tại mạng của ngươi, liền một đầu man thú đều không đáng?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Hàn đường chủ cứng đờ."),t("br"),t("br"),n._v("Ân cứu mạng, lớn như trời xanh, chỉ nói một câu lời cảm kích, quả thực có sai lầm thỏa đáng."),t("br"),t("br"),n._v("“Đây viên linh thạch, vốn là muốn dùng đến hấp dẫn Yêu Thần thú, thừa cơ đem hắn thuần phục...”"),t("br"),t("br"),n._v("Đem vừa rồi viên kia linh thạch trung phẩm lấy ra ngoài, Hàn đường chủ quay đầu nhìn thoáng qua vẫn như cũ cái mông vểnh lên, không ngừng gặm cỏ Yêu Thần thú, biết còn muốn thuần phục, khẳng định không có cơ hội, một cắn răng, đưa tới: “Hiện tại... Không có gì dùng, liền đưa cho Trương sư, dùng báo ân cứu mạng!”"),t("br"),t("br"),n._v("Mập mạp tiện tay tiếp nhận, nhếch miệng: “Liền đây một khối?”"),t("br"),t("br"),n._v("“Ta... Liền một khối!” Hơi đỏ mặt, Hàn đường chủ nói không ra lời."),t("br"),t("br"),n._v("Linh thạch trung phẩm, toàn bộ vạn quốc liên minh đều không có mấy cái, hắn có thể tìm đến một cái, cũng đã xem như vận khí rất khá."),t("br"),t("br"),n._v("“Vậy được rồi, một khối liền một khối!” Thấy đối phương không bỏ ra nổi càng nhiều, Tôn Cường lúc này mới lắc đầu, đem linh thạch hướng Trương Huyền đưa tới: “Thiếu gia!”"),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Tiện tay tiếp nhận, Trương Huyền hài lòng gật đầu."),t("br"),t("br"),n._v("Không thể không nói, vị này Tôn Cường quả thực biết hắn tâm tư, vừa rồi liền vừa ý khối linh thạch này, nếu không, hắn cũng không phải rảnh rỗi, làm sao có thể vô duyên vô cớ cứu đối phương?"),t("br"),t("br"),n._v("Danh sư cũng cần sinh hoạt, hắn cũng không phải cái gì Thánh Nhân, cứu người, đương nhiên muốn báo đáp."),t("br"),t("br"),n._v("Nếu không, thiên hạ nhiều người như vậy muốn hắn cứu, còn không sống sống mệt chết."),t("br"),t("br"),n._v("“Linh thạch trung phẩm, ta vừa vặn cần, liền chịu chi vô lễ, yên tâm, ta cũng sẽ không chiếm tiện nghi của ngươi, hiện tại liền giúp trị cho ngươi thật thương thế, xem như báo đáp!”"),t("br"),t("br"),n._v("Đem linh thạch cất kỹ, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Giúp ta chữa khỏi tổn thương?” Hàn đường chủ sững sờ: “Ta thương thế kia, có thể chữa khỏi?”"),t("br"),t("br"),n._v("Vừa rồi liên tục bị Yêu Thần thú bạo kích, lại vượt qua năng lực bản thân sử dụng lực lượng, nội tạng sai chỗ, thực lực đại tổn, hắn ở độ tuổi này, lại thêm tự tin bị đả kích, muốn chữa khỏi, chỉ sợ rất khó."),t("br"),t("br"),n._v("Trước mắt vị này nói hiện tại chữa khỏi, lập tức mặt mũi tràn đầy không thể tin được."),t("br"),t("br"),n._v("“Ta nói có thể trị hết, đương nhiên có thể trị hết, không muốn trị coi như xong!” Trương Huyền vẫy tay."),t("br"),t("br"),n._v("“Đương nhiên muốn trị...” Nhớ tới đối phương y sư, danh sư thân phận, Hàn đường chủ liền vội vàng gật đầu."),t("br"),t("br"),n._v("Đổi lại trước đó, nói như vậy, hắn khẳng định không tin, dễ thân mắt thấy đến thuần Yêu Thần thú ngoan ngoãn nghe lời, đã đối trước mắt vị này, mang theo tôn kính cùng tín nhiệm."),t("br"),t("br"),n._v("“Trương sư, cần gì dược liệu ư? Ta chỗ này có không ít...”"),t("br"),t("br"),n._v("Khẳng định muốn trị, Hàn đường chủ nhịn không được nhìn qua."),t("br"),t("br"),n._v("Mặc dù không phải y sư, nhưng làm cường giả, đối một chút thương thế vẫn là biết một chút, hắn cái này tổn thương, không có mấy trăm năm dược vật phối hợp, chỉ sợ rất khó có hiệu quả."),t("br"),t("br"),n._v("Mà những dược vật này, hắn ngược lại là có chút."),t("br"),t("br"),n._v("Đang muốn đem đồ vật lấy ra, lời còn chưa dứt, liền nghe đến đối diện thanh niên mở lời."),t("br"),t("br"),n._v("“Dược vật? Không cần... Cái kia, cái kia Yêu Thần thú, ngươi qua đây, đem cái tên này cho ta đánh ngất!”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-507"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-509"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);