(window.webpackJsonp=window.webpackJsonp||[]).push([[1756],{2113:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-502-thien-cong-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-502-thien-cong-su"}},[n._v("#")]),n._v(" Chương 502: Thiên Công sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 502: Thiên Công sư"),t("br"),t("br"),n._v("Hàn đàm âm lãnh, tiến vào bên trong, mọi người nhất thời cảm thấy lạnh lẽo thấu xương."),t("br"),t("br"),n._v("Đi theo Lạc Trúc phía sau, trực tiếp hướng hạ du đi."),t("br"),t("br"),n._v("Mọi người thực lực kém nhất đều đạt đến Chí Tôn, loại này cấp bậc, ở trong nước bế khí, hai, ba mươi phút cũng không thành vấn đề."),t("br"),t("br"),n._v("Triệu Phi Vũ thực lực mặc dù không tốt, có Kim Tòng Hải ở một bên không ngừng truyền vào chân khí, hình thành chân khí che đậy bảo hộ, cũng là cảm giác không đến buồn bực."),t("br"),t("br"),n._v("Một đường hướng phía dưới, vừa mới bắt đầu mượn nhờ trên mặt nước bắn ra mà đến chói lọi, còn có thể nhìn thấy tình huống trước mắt, nương theo vào sâu, đưa tay không thấy được năm ngón, đen kịt một màu."),t("br"),t("br"),n._v("Lạc Trúc đã sớm chuẩn bị, lấy ra mấy cái Dạ Minh Châu, mỗi người cho một cái, ôn nhuận ánh sáng chiếu xuống, dưới nước thế giới rõ ràng xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Cái này đầm nước, bởi vì quá mức lạnh lẽo, đồng thời không có cái gì sinh mệnh, một mực hạ du, đi tới đại khái bảy, tám mươi mét sâu địa phương, rốt cục nhìn thấy khu vực, mọc đầy đủ loại nhan sắc cực sâu cây rong."),t("br"),t("br"),n._v("Dưới nước không có cách nào nói chuyện, Lạc Trúc đối mọi người vẫy vẫy tay, xe nhẹ đường quen, dọc theo tràn đầy cây rong khu vực, chậm rãi hướng về phía trước, chỉ chốc lát đi tới vách đá trước mặt."),t("br"),t("br"),n._v("Là cái bóng loáng vách đá, thoạt nhìn không có bất luận cái gì đặc thù, nếu như không biết Vu hồn mộ, đi tới trước mặt, đều không phát hiện được cái gì."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, lấy ra một cái bóng loáng bàn đá, Lạc Trúc ở trên vách tường lục lọi một hồi, tìm đúng vị trí, đem đội lên phía trên."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),t("br"),n._v("Phảng phất kích hoạt lên cái nào đó cơ quan, bức tường từ từ mở ra, cái này thoạt nhìn hoàn hảo không chút tổn hại vách đá, lại là sống."),t("br"),t("br"),n._v("“Thật tinh xảo công nghệ!”"),t("br"),t("br"),n._v("Trương Huyền ánh mắt sáng lên."),t("br"),t("br"),n._v("≪ truyen cua tui ʘʘ vn ]"),t("br"),n._v("Dùng tảng đá điêu khắc cơ quan, ở bề ngoài mảy may nhìn không ra khe hở, công nghệ mạnh, mặc dù so ra kém trước đó Thiên Công Khéo Léo hộp, nhưng cũng vượt xa trước kia đã gặp không ít bảo vật."),t("br"),t("br"),n._v("Xem ra xây dựng cái này Vu hồn mộ thợ thủ công, cấp bậc không thấp, kém cỏi nhất cũng muốn đạt đến cửu mạt cấp bậc."),t("br"),t("br"),n._v("Truyền thuyết, cửu mạt thợ thủ công, chế tạo ra cơ quan khôi lỗi, Hóa Phàm cảnh cường giả, cũng khó khăn phân biệt thật giả, hiện tại xem ra, quả thật như thế."),t("br"),t("br"),n._v("Khó trách Lạc Trúc tự tin trừ hắn ra, không có người tìm tới nơi này, như thế cơ mật địa phương, muốn tìm được, quả thực rất khó."),t("br"),t("br"),n._v("Vách đá mở ra, một cánh cửa xuất hiện ở trước mắt, Lạc Trúc đi vào trước."),t("br"),t("br"),n._v("Trong cửa bố trí có đặc thù chống nước cơ quan, sinh mệnh có thể đi vào, dòng nước vào không được, mọi người một bước vào trong đó, quả nhiên cảm thấy trong đó không khí lưu thông, không chút nào cảm thấy bị đè nén."),t("br"),t("br"),n._v("“Nơi này chính là Vu hồn mộ thông đạo, đi đến cuối cùng, chính là mộ huyệt!”"),t("br"),t("br"),n._v("Thấy mọi người đều sau khi đi vào, vách đá tự động đóng, Lạc Trúc nói."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật gật đầu."),t("br"),t("br"),n._v("Mọi người nhìn về phía trong đó tất cả đều tràn ngập tò mò."),t("br"),t("br"),n._v("Là cái hẹp dài hành lang, hai bên điêu khắc đủ loại bích hoạ, rất nhiều đều là Vu hồn tế tự nghi thức, Dạ Minh Châu chiếu xuống, có vẻ hết sức quỷ dị."),t("br"),t("br"),n._v("“Trước không vội đi, cái này hành lang bên trong, có phi thường lợi hại cơ quan, cần từng bước một thăm dò mới có thể đi vào!”"),t("br"),t("br"),n._v("Tựa hồ đối với cổ mộ loại hình môi trường hết sức quen thuộc, Lạc Trúc lăng không một trảo, trước mắt xuất hiện hai cái hài đồng lớn nhỏ khôi lỗi."),t("br"),t("br"),n._v("Hẳn là hắn những ngày này chuyên môn chuẩn bị."),t("br"),t("br"),n._v("Lấy ra hai cái linh thạch để vào trong đó, khôi lỗi một trước một sau, dọc theo thông đạo đi thẳng về phía trước."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Con rối thứ nhất đi không đến ba bước, một tiếng gió thổi vang lên, ngay sau đó giòn vang một tiếng, nổ vỡ nát."),t("br"),t("br"),n._v("Mọi người con ngươi co rụt lại."),t("br"),t("br"),n._v("Lạc Trúc mới vừa lấy ra cái này khôi lỗi, dùng đặc thù chất liệu luyện chế, cực kỳ cứng rắn, không nói cái khác, coi như Kim Tòng Hải loại thực lực này, muốn một chút đánh nát, đều mười phần phiền phức."),t("br"),t("br"),n._v("Mà trước mắt chẳng biết lâm vào cái gì cơ quan, trực tiếp nổ tung, nếu không phải vừa rồi hắn cảnh giác, ngăn lại mọi người, một khi tiến lên, còn không đã sớm chết rồi?"),t("br"),t("br"),n._v("Trong nháy mắt, mồ hôi lạnh chảy ròng."),t("br"),t("br"),n._v("Không hổ là quỷ dị nhất chức nghiệp, cái này Vu hồn mộ, quả thực không giống bình thường."),t("br"),t("br"),n._v("Nếu không phải Trương sư giải quyết đoạt xá linh hồn sự tình, tùy ý đối phương dẫn bọn hắn tới, chỉ sợ còn không có tiến vào bên trong, liền bị đoàn diệt."),t("br"),t("br"),n._v("Kẽo kẹt! Kẽo kẹt!"),t("br"),t("br"),n._v("Mọi người trong lúc khiếp sợ, cái thứ hai khôi lỗi cũng đi chưa được mấy bước, gặp phải công kích, cùng cái thứ nhất đồng dạng, nổ vỡ nát."),t("br"),t("br"),n._v('"Năm bước bên trong, có hai cái đưa người vào chỗ chết cơ quan,'),t("br"),t("br"),n._v('Ta hiện tại phía trước dẫn đường, thuận tiện ghi chú ra bàn chân điểm rơi vị trí, các ngươi nhất định muốn theo sát lấy, một chút không thể phạm sai lầm, nếu không... Ta không cách nào cam đoan an toàn!"'),t("br"),t("br"),n._v("Lạc Trúc khuôn mặt ngưng trọng bàn giao."),t("br"),t("br"),n._v("“Tốt!” Mọi người biết những này, đồng thời nhẹ gật đầu."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Lạc Trúc thận trọng tiến về phía trước một bước, bàn chân mới vừa bước vào, liền nghe đến tiếng gió rít gào, một đạo khí mang hắn cách đó không xa xẹt qua, xem ra một khi bị đánh trúng, không chết cũng muốn trọng thương."),t("br"),t("br"),n._v("Bất quá, có vừa rồi khôi lỗi dò đường, sớm có phòng bị, cũng không làm bị thương."),t("br"),t("br"),n._v("“Đây là... Trận pháp?” Nhìn thấy thông đạo nguy cơ trùng trùng, Trại các chủ có chút không biết rõ, nhìn lại."),t("br"),n._v(" Khắp nơi nguy hiểm, có thể hay không nơi này bố trí một cái lợi hại sát trận?"),t("br"),t("br"),n._v("Nếu thật là như vậy, nhờ vào Trương sư đối với trận pháp lý giải, nghĩ biện pháp đem nó phá vỡ, có phải hay không liền sẽ đơn giản rất nhiều?"),t("br"),t("br"),n._v("“Không phải!” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Nếu như là trận pháp, hắn tuyệt đối có thể cảm nhận được linh khí trong đó ba động, thuận tiện tìm tới hạch tâm, một cước đạp ngừng, đáng tiếc... Cái này rất rõ ràng không phải."),t("br"),t("br"),n._v("“Vu hồn mộ, nhất phòng bị, không phải nhân loại, mà là phệ hồn kiến, trận pháp đối phệ hồn kiến vô dụng... Cho nên, nơi này sẽ không có trận pháp, nếu như ta không nhìn lầm, hẳn là một chút đặc thù cơ quan!”"),t("br"),t("br"),n._v("Chần chờ một chút, Trương Huyền phân tích."),t("br"),t("br"),n._v("Vu hồn thiên địch là phệ hồn kiến, xây dựng mộ huyệt, tự nhiên muốn phòng bị loại vật này. Phệ hồn kiến có thể thôn phệ trong trận pháp linh khí, để trận pháp trực tiếp dừng lại, nếu quả thật bố trí trận pháp lời nói, ngược lại không có hiệu quả, không bằng cơ quan hữu hiệu."),t("br"),t("br"),n._v("Mọi người gật đầu."),t("br"),t("br"),n._v("“Nếu như là cơ quan lời nói, liền phiền toái, chúng ta bên trong không có người quen thuộc nhanh nhẹn linh hoạt, nếu có một vị Thiên Công sư tại cái này, liền sẽ đơn giản không ít!” Trại các chủ mày nhăn lại."),t("br"),t("br"),n._v("“Thiên Công sư?” Tôn Cường đám người nhìn tới."),t("br"),t("br"),n._v("Trương Huyền chưa nói qua cái nghề nghiệp này, bọn hắn cũng không hiểu rõ lắm."),t("br"),t("br"),n._v("“Đúng vậy a, Thiên Công sư, lại gọi Cơ Xảo sư, là hạ cửu lưu một cái chức nghiệp, am hiểu thiết kế những này nhanh nhẹn linh hoạt đồ vật, thoạt nhìn thường thường không có gì lạ vật phẩm, bọn hắn có thể ở trong đó chứa đựng kiếm khí, võ kỹ, một khi mở ra, uy lực vô cùng! Rất nhiều cường giả, đều không thể chống lại.”"),t("br"),t("br"),n._v("Trại các chủ giải thích nói: “Loại nghề nghiệp này, cũng có chuyên môn tổ chức, gọi là Thiên Công viện. Chuyên môn thiết kế thiên hạ kỳ diệu nhất vũ khí cùng cơ quan. Bất quá... Loại nghề nghiệp này, ta cũng là tại thư tịch bên trên nhìn qua, chúng ta vạn quốc liên minh đồng thời không có, nghe nói Huyễn Vũ đế quốc, mới có tư cách kiến tạo một cái phân bộ.”"),t("br"),t("br"),n._v("“Loại nghề nghiệp này, thiết kế đồ vật, phi thường tinh vi, không biết nguyên lý, quả thực rất khó thông qua!”"),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, mày nhăn lại."),t("br"),t("br"),n._v("Thiên Công Khéo Léo hộp trình độ phức tạp, hắn nhưng là thấm sâu trong người, thấu hiểu rất rõ, nếu như không có Thiên Đạo thư viện, biết thiếu hụt, muốn mở ra, tuyệt đối không thể."),t("br"),t("br"),n._v("Một cái nho nhỏ hộp, liền thiết kế như thế tinh diệu, cái này hành lang thật muốn có loại người này nhúng tay, nguy hiểm bao lớn, vượt qua tưởng tượng ra."),t("br"),t("br"),n._v("Phốc!"),t("br"),t("br"),n._v("Ngay tại suy tư, đột nhiên nghe được một cái lưỡi đao vào thịt thanh âm, ngay sau đó liền thấy Lạc Trúc hơi đỏ mặt, lui trở về, cánh tay bị thứ gì vạch phá, cuồn cuộn đổ máu."),t("br"),t("br"),n._v("“Không có sao chứ?”"),t("br"),t("br"),n._v("Kim Tòng Hải đám người đi tới trước mặt, giúp hắn ngừng vết thương."),t("br"),t("br"),n._v("“Không có việc gì!” Lạc Trúc lắc đầu: “Cái lối đi này cơ quan, thật giống so trước kia càng khó đi hơn...”"),t("br"),t("br"),n._v("“Càng khó đi hơn? Cái lối đi này tổng cộng bao dài? Ngươi đi đến cuối cùng hao tốn bao lâu?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Tổng cộng có chừng ba trăm thước, ta lần trước tới, mượn nhờ khôi lỗi một chút thí nghiệm, đại khái đi bốn ngày, mới đi đến cuối cùng!”"),t("br"),t("br"),n._v("Lạc Trúc mày nhăn lại: “Bất quá, loại này cơ quan, cùng trận pháp đồng dạng, mỗi một lần đi vào, đều là không giống, kinh nghiệm lần trước, lần này hoàn toàn vô dụng, nếu không, ta cũng sẽ không bị thương...”"),t("br"),t("br"),n._v("“Đi bốn ngày?” Trương Huyền im lặng."),t("br"),t("br"),n._v("Bốn ngày mới đi chừng ba trăm thước, quả thực quá khó khăn."),t("br"),t("br"),n._v("“Đúng vậy a, nhưng không chậm rãi đi, thực sự hết cách rồi, thông đạo trong cơ quan ẩn chứa kiếm khí, ít nhất đều đạt đến Hóa Phàm tứ trọng đỉnh phong, thậm chí còn có đạt tới ngũ trọng, nếu như không thể sớm né tránh, một khi đụng tới, liền sẽ bị tại chỗ cắt thành hai đoạn, chết cũng không biết chết như thế nào!”"),t("br"),t("br"),n._v("Lạc Trúc lắc đầu."),t("br"),t("br"),n._v("“Hóa Phàm tứ trọng đỉnh phong kiếm khí?” Mọi người líu lưỡi."),t("br"),t("br"),n._v("Vừa rồi nhìn thấy khôi lỗi bị trực tiếp giết chết, liền biết chung quanh bắn ra kiếm khí rất đáng sợ, không nghĩ tới lợi hại như vậy."),t("br"),t("br"),n._v("Như thế lối đi hẹp, không cách nào chống lại, cũng không có cách nào phòng ngự, thật muốn bị cường đại như vậy kiếm khí đâm trúng, chỉ sợ Hóa Phàm tứ trọng cường giả đỉnh phong, chỉ sợ đều khó mà sống."),t("br"),t("br"),n._v("“Chỉ có thể từng bước một tìm tòi, không có những biện pháp khác?”"),t("br"),t("br"),n._v("Triệu Phi Vũ cũng mày nhăn lại."),t("br"),t("br"),n._v("“Không có! Thiên Công sư lưu lại cơ quan, vô cùng phức tạp, hơn nữa không ngừng biến hóa, nếu như không biết biến hóa quy luật, rất khó thông qua.” Lạc Trúc lắc đầu."),t("br"),t("br"),n._v("“Nghĩ biện pháp đem cái lối đi này cơ quan phá đi cũng không được?” Trương Huyền nhướng mày."),t("br"),t("br"),n._v("Trận pháp đều có thể đình chỉ, nếu như có thể tìm tới cùng cái này cơ quan chốt mở, hoặc là chỗ mấu chốt, hẳn là có thể đem nó phá hư."),t("br"),t("br"),n._v("“Phá hư?”"),t("br"),t("br"),n._v("Lạc Trúc cười khổ: “Nào có dễ dàng như vậy, Thiên Công sư vật lưu lại, cùng guồng nước đồng dạng, là đang không ngừng quay, coi như ngươi gặp qua bản vẽ, không biết nguyên lý, cũng không có bất kỳ chỗ dùng nào. Dù sao cũng là một cái rất lợi hại chức nghiệp, cùng trận pháp đồng dạng, dễ dàng như vậy liền phá vỡ, cũng không trở thành để cho người ta kiêng kị!”"),t("br"),t("br"),n._v("Cơ quan cùng trận pháp đồng dạng, coi như nhìn qua bản vẽ, không biết vận chuyển nguyên lý, đồng dạng không dùng."),t("br"),t("br"),n._v("Bởi vì cơ quan là không ngừng quay, đối cái nghề nghiệp này một khiếu không hiểu lời nói, đừng nói phá hư, có thể hay không còn sống cũng còn khó nói."),t("br"),t("br"),n._v("“Đúng vậy a, Thiên Công sư, đáng sợ liền đáng sợ ở đây, bọn hắn có thể lợi dụng máy móc, tổ hợp thành một chút đem người chém giết vật phẩm, hơn nữa loại vật phẩm này, không cần linh thạch thôi động... Nếu như ta không nhìn lầm, cái lối đi này tất cả cơ quan sinh ra động lực, đều có phía trên thác nước cung cấp, không biết nguyên lý, cho dù có la bàn, cũng tìm không thấy vị trí!”"),t("br"),t("br"),n._v("Trại các chủ gật đầu."),t("br"),t("br"),n._v("La bàn, có thể định vị linh khí đi về phía, điều tra trận pháp mạnh yếu, cùng nghĩ biện pháp tìm tới nhược điểm, có thể... Cơ quan dùng không phải linh thạch, mà là vật lý lực lượng, tỷ như: Mượn nhờ phía trên thác nước cung cấp động lực..."),t("br"),t("br"),n._v("Làm sao có thể phá?"),t("br"),t("br"),n._v("Gặp nghe được hai người lắc đầu, phân tích, tất cả mọi người biểu lộ ảm đạm, không còn trước đó vừa tới đến hưng phấn, gặp sĩ khí sa sút, Trương Huyền lắc mở miệng nói."),t("br"),t("br"),n._v("“La bàn tìm không thấy, không có nghĩa là thủ đoạn khác cũng tìm không thấy, đừng có gấp, ta thử một chút!”"),t("br"),t("br"),n._v("Nói xong, mọi người liền thấy hắn cười nhạt một tiếng, bàn tay đặt ở thông đạo trên mặt đất, nhẹ nhàng sờ soạng một chút."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-510"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-512"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);