(window.webpackJsonp=window.webpackJsonp||[]).push([[1762],{2119:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-508-ta-co-the-day-nguoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-508-ta-co-the-day-nguoi"}},[n._v("#")]),n._v(" Chương 508: Ta có thể dạy ngươi")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 508: Ta có thể dạy ngươi"),t("br"),t("br"),n._v("Mọi người cũng không biết Trương lão sư tại sao lại nói như vậy, từng cái tràn đầy kỳ quái."),t("br"),t("br"),n._v("“Nói càn?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Ngươi xem một đoạn này ‘Đem trong cơ thể toàn bộ chân khí, phát ra chư huyệt, phối hợp linh hồn, ngưng tụ thành hình’, bình thường tu luyện, đều là đem trong huyệt đạo chân khí, hội tụ đến đan điền, tích cát thành tháp, góp gió thành bão, sau đó tiến hành lợi dụng. Mà cái này, để cho người ta chân khí tán dật, lực lượng tan rã, cùng tán công không khác... Thật muốn làm như thế, còn như thế nào tiến bộ? Coi như vốn là có thực lực, dựa theo phương pháp này tu luyện, cũng sẽ trở nên càng ngày càng nhỏ yếu đi!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Mọi người sửng sốt."),t("br"),t("br"),n._v("Chân khí chảy xuôi toàn thân, tại chư huyệt cùng trong đan điền lưu chuyển, là chuyện thường xảy ra, chỉ có như vậy, mới có thể để cho người tu luyện thể phách cường kiện, bách bệnh không sinh."),t("br"),t("br"),n._v("Làm sao đến trong miệng hắn, biến thành tán công?"),t("br"),t("br"),n._v("“Vừa nói như vậy... Thật đúng là cùng tán công có chút tương tự!” Trại các chủ ngẩn người."),t("br"),t("br"),n._v("Chân khí lưu chuyển chư huyệt không có vấn đề, nhưng từ huyệt đạo phát ra, tốc độ nhanh lời nói, chính là tán công phương pháp."),t("br"),t("br"),n._v("“Đúng không!” Gặp có người nghênh hợp, Trương Huyền hài lòng gật đầu: “Ngươi đây là để chúng ta chậm rãi tán công, nước ấm nấu ếch xanh, đợi thực lực yếu đi, lại một mẻ hốt gọn!”"),t("br"),t("br"),n._v("Thiên Đạo thư viện đã đem quyển bí tịch này thiếu hụt, toàn bộ viết đi ra, tùy tiện tìm mấy cái, đưa ra chút ý kiến, đơn giản đến cực điểm."),t("br"),t("br"),n._v("“Ăn nói bừa bãi, nói bậy nói bạ! Chân khí phát ra chư huyệt, mới có thể cùng hồn phách giao hòa, để linh hồn ly thể mà tồn tại. Nếu không, các ngươi chưa đạt tới Hợp Linh cảnh, hồn lực yếu kém, không có chân khí bảo hộ, làm sao có thể độc lập đi lại?”"),t("br"),t("br"),n._v("Thanh âm khẽ nói."),t("br"),t("br"),n._v("Tu vi đạt tới Hóa Phàm ngũ trọng Hợp Linh cảnh, lực lượng linh hồn bạo tăng, dù là không phải vu hồn sư, hồn lực cũng có thể ly thể tồn tại, không bị quấy nhiễu. Thực lực không bằng, chỉ có thể dùng chân khí bảo hộ, làm sao đến đối phương trong miệng biến thành tán công?"),t("br"),t("br"),n._v("“Chân khí bảo hộ linh hồn? Nói cho dễ nghe, vậy ta hỏi ngươi, chân khí theo hợp thành Thần huyệt tuôn ra, là có ý gì? Hợp thành Thần huyệt, người chi tinh thần hội tụ tồn tại, chân khí từ nơi này tuôn ra, nhẹ thì mê muội, nặng thì tử vong. Nếu như ta không nhìn lầm, thật muốn dựa theo phương pháp này tu luyện, coi như linh hồn ly thể, thân thể cũng sẽ trọng thương, hoặc là tử vong đi!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Thanh âm dừng lại."),t("br"),t("br"),n._v("Dẫn hồn xuất thể, điểm trọng yếu nhất chính là thân thể tử vong, chỉ có như vậy, linh hồn mới có thể thoát ly trói buộc, chạy nhanh ngao du."),t("br"),t("br"),n._v("Đương nhiên, chỉ cần linh hồn trở lại thân thể, vẫn như cũ có thể khôi phục nhịp tim, tương đương với lần nữa “Phục sinh”."),t("br"),t("br"),n._v("“Lấy ta tu vi làm thí dụ, ta là Hóa Phàm nhất trọng Tăng Thọ cảnh, loại tu vi này, coi như may mắn dẫn hồn xuất thể thành công, linh nhục tách rời, thịt hồn lại không thống nhất, muốn duy trì người bình thường diện mạo, không bị ngoại nhân phát hiện, không đoán sai, hẳn là muốn mỗi ngày đều muốn ăn đan dược duy trì đi!”"),t("br"),t("br"),n._v("Không để hắn tiếp tục nói chuyện, Trương Huyền hừ một tiếng, mở miệng nói."),t("br"),t("br"),n._v("Coi như bản thân là linh hồn của ngươi, linh nhục tách rời, thân thể sau khi giả chết, cũng sẽ trở nên lạ lẫm, không uống thuốc duy trì, sớm muộn gì đều sẽ sinh cơ tiêu tan, thân thể mọc đầy thi ban, bị người phát hiện."),t("br"),t("br"),n._v("Cái này rất giống kiếp trước thay thận, liền xem như ngươi chính mình, theo thân thể xuất ra đi, trả lại trở về, đồng dạng sẽ xuất hiện bài xích, không thuốc duy trì, sớm muộn gì đều sẽ hoại tử."),t("br"),t("br"),n._v("“Ngươi... Làm sao biết?” Thanh âm sửng sốt."),t("br"),t("br"),n._v("Đây là vu hồn sư bí mật, chưa hề nói ra miệng, một ngoại nhân như thế nào biết được?"),t("br"),t("br"),n._v("“Không cần phải để ý đến ta làm thế nào biết. Dựa theo cái này Dẫn Hồn quyết, dẫn xuất hồn phách, tu vi đạt tới Hợp Linh cảnh, là có thể làm dịu không ít, nhưng... Coi như thực lực đạt tới ngươi như vậy cảnh giới, có dám tại ngày mưa dông xuất hành? Không sợ nổi trận lôi đình?” Trương Huyền nói tiếp."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Thanh âm tựa hồ có chút run rẩy."),t("br"),t("br"),n._v("Hồn phách thuần âm, nổi trận lôi đình thuần dương, dù là vu hồn sư, mượn nhờ thân thể của mình xuất hành, gặp được nổi trận lôi đình, cũng muốn né tránh, không thể thường nhân đồng dạng."),t("br"),t("br"),n._v("Cái này cùng vừa rồi bài xích đồng dạng, thuộc về vu hồn sư cấm kỵ... Cái tên này chỉ nhìn một chút Dẫn Hồn quyết liền suy tính ra những thứ này... Thật hay giả?"),t("br"),t("br"),n._v("“Không nói nổi trận lôi đình, liền nói ban đêm, mỗi đến giờ Tý, thiên địa âm khí nặng nhất thời điểm, âm khí thấu thể mà vào, có phải hay không cũng sẽ cảm thấy toàn thân ngứa lạ khó nhịn, chỉ có hồn phách ly thể, mới có thể giải trừ khốn khó?”"),t("br"),t("br"),n._v("Biết Thiên Đạo thư viện ghi chép thiếu hụt không có sai, Trương Huyền nhẹ nhàng cười một tiếng, hai tay chắp sau lưng, đứng tại bia đá trước mặt, chậm rãi nói."),t("br"),t("br"),n._v("“Vu hồn sư, dựa vào không phải thân thể, thân thể khó chịu lại như thế nào? Chỉ cần hồn phách không ngại, cũng không có cái gì!”"),t("br"),t("br"),n._v("Dường như lần nữa bị nói trúng, thanh âm khẽ nói."),t("br"),t("br"),n._v("“Ngươi nói không sai, vu hồn sư mạnh nhất là linh hồn, coi như thân thể hủy đi, cũng không tính là gì, hoàn toàn có thể một lần nữa đoạt xá, vĩnh viễn sống sót... Thế nhưng là, không có thân thể che chở, ngươi thật có thể vượt qua... Hồn lực ngũ suy?”"),t("br"),t("br"),n._v("Trương Huyền bẻ ngón tay: “Đoạt xá suy, lưu quang suy, chiến đấu suy, Dương Chước suy cùng đáng sợ nhất ngũ giác suy!”"),t("br"),t("br"),n._v("“Ngươi... Biết hồn lực ngũ suy?”"),t("br"),t("br"),n._v("Thanh âm lại không trước đó trấn định."),t("br"),t("br"),n._v("“Đoạt xá suy, là chỉ đoạt xá thời điểm, lực lượng tự nhiên suy yếu, ít nhất hạ thấp hai cái đại cấp bậc, cho nên, không phải vạn bất đắc dĩ, các ngươi sẽ không đoạt xá.”"),t("br"),t("br"),n._v("“Lưu quang suy, là chỉ không cách nào theo căn bản giải quyết linh nhục ở giữa mâu thuẫn, linh hồn sẽ nương theo thời gian trôi qua, mà tự động suy giảm, vì không đổi nhỏ yếu, chỉ có không ngừng tu luyện, liều mạng tu luyện!”"),t("br"),t("br"),n._v("“Chiến đấu suy, là chỉ dùng hồn lực chiến đấu, tu vi sẽ tự động yếu bớt, bởi vậy, các ngươi am hiểu nhất là hồn lực câu dẫn, mê hoặc, bố trí để cho người ta hoảng sợ huyễn cảnh, trên thực tế cũng rất ít chiến đấu ra tay.”"),t("br"),t("br"),n._v("“Dương Chước suy, vừa rồi đã nói qua, nổi trận lôi đình, dương khí đại thịnh đồ vật, các ngươi cũng không dám tiếp xúc, một khi tới gần, sẽ dùng hồn phách tự động trở nên suy yếu.”"),t("br"),t("br"),n._v("“Đương nhiên, trước bốn loại suy yếu, mặc dù nghe đáng sợ, vẫn là có thể tránh khỏi, đáng sợ nhất là cuối cùng một loại... Ngũ giác suy!”"),t("br"),t("br"),n._v("“Người có ngũ giác, lục thức, biết liêm sỉ, rõ là không phải, hiểu vinh nhục, biện thiện ác. Có thể linh hồn, không thể có những này, yêu rất đau lòng, tình nhiều tổn thương hồn. Một khi có tình cảm, linh hồn liền sẽ kịch liệt ba động, sẽ để cho lực lượng tự động suy giảm...”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng, mí mắt nâng lên: “Ta nói nhưng có sai lầm?”"),t("br"),n._v("“Ngươi, ngươi...”"),t("br"),t("br"),n._v("Thanh âm hoảng sợ."),t("br"),t("br"),n._v("Thiên Đạo công bằng, mặc dù vu hồn sư coi như tu vi không đủ, cũng có thể có thường nhân khó có thể tưởng tượng tuổi thọ, nhưng cũng có thường nhân khó có thể lý giải được thống khổ."),t("br"),t("br"),n._v("Không thể yêu, không thể hận, rất nhiều giới hạn, sống ở trong âm u, sống không bằng chết."),t("br"),t("br"),n._v("Chỉ là... Hồn lực ngũ suy, càng là vu hồn sư không thể nói bằng lời thiếu hụt, coi như năm đó Danh Sư đường, đều không thể lý giải, cái tên này làm thế nào biết?"),t("br"),t("br"),n._v("Chỉ vì nhìn một lần Dẫn Hồn quyết?"),t("br"),t("br"),n._v("Nếu thực như thế, đối công pháp lý giải, cũng quá sâu đi!"),t("br"),t("br"),n._v("“Ngươi... Nói những thứ vô dụng này, mặc kệ võ giả, vẫn là vu hồn sư, chỉ cần tu luyện, chính là cùng trời chống lại, làm sao có thể thuận buồm xuôi gió? Hồn lực có ngũ suy, tu luyện cũng có ngũ kiếp, thiên, địa, nhân, tài, pháp! Đã đều là như thế, còn không bằng tu luyện Vu hồn, tuổi thọ vô hạn!”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, thanh âm khôi phục lại, khẽ nói."),t("br"),t("br"),n._v("Chỉ cần là tu luyện, liền không thể cam đoan an toàn, kiểu gì cũng sẽ gặp được đủ loại chiến đấu, thương vong khó tránh khỏi."),t("br"),t("br"),n._v("Có người chuyên môn làm qua tính toán, một ngàn võ giả, muốn đạt tới Hóa Phàm cảnh đỉnh phong, ít nhất phải chết đi chín trăm chín mươi chín vị, chân chính ngàn dặm chọn một."),t("br"),t("br"),n._v("Mặc dù có danh sư đường trấn áp, vô cớ ẩu đả ít đi rất nhiều, có thể thế lực ở giữa chiến đấu, minh tranh ám đoạt, bảo vật ở giữa tranh đoạt, tu luyện tẩu hỏa nhập ma... Đều là võ giả kiếp nạn, để vô số người chết oan chết uổng."),t("br"),t("br"),n._v("“Tuổi thọ vô hạn? Vu hồn sư tuổi thọ thật sự vô hạn?”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu: “Coi như không ngừng tu luyện, có thể chống cự lưu quang suy, có thể loại này cô tịch, thật là ngươi có thể chịu được? Nếu như ta không nhìn lầm... Nhục thể của ngươi sớm đã hư thối hầu như không còn đi! Không có thân thể tẩm bổ, coi như sống sót, cũng ngày ngày dày vò, thống khổ chịu không nổi!”"),t("br"),t("br"),n._v("“Không nói cái khác, chỉ nói mỗi ngày buổi trưa, dương khí thịnh nhất lúc, tựa như như mũi kim đâm nhói, còn trôi qua quen thuộc? Nếu không phải cái này Địa Cung giấu ở đầm, nhất âm u chỗ, ngươi cảm thấy chỉ bằng vào một đoàn hồn phách, có thể sống đến hiện tại?”"),t("br"),t("br"),n._v("“Ngươi, ngươi...”"),t("br"),t("br"),n._v("Nghe nói như thế, thanh âm rốt cuộc áp chế không nổi hoảng sợ, run rẩy càng ngày càng kịch liệt."),t("br"),t("br"),n._v("Đối phương nói không sai."),t("br"),t("br"),n._v("Hồn phách có thể không hạn chế sống sót, có thể... Thân thể không được."),t("br"),t("br"),n._v("Thoát ly thân thể hồn phách, coi như trốn ở nhất âm u chỗ, mỗi đến buổi trưa, cũng sẽ ngày ngày dày vò, sống không bằng chết."),t("br"),t("br"),n._v("Thậm chí... Hắn đã từng nhiều lần nghĩ tới tử vong, đáng tiếc, linh hồn trạng thái, vật lý công kích vô dụng, cũng không cách nào đem mình giết chết, chỉ có thể kéo dài hơi tàn."),t("br"),t("br"),n._v("“Môn công pháp này có phải hay không có vấn đề? Có phải hay không không đáng đi học?”"),t("br"),t("br"),n._v("Thấy hắn thanh âm thất thố, Trương Huyền tiếp tục nói."),t("br"),t("br"),n._v("“Là có vấn đề, có thể... Bất kỳ cái gì công pháp, đều khó có khả năng không có thiếu hụt!” Thanh âm gào thét."),t("br"),t("br"),n._v("“Ngươi sai! Ngươi xem Dẫn Hồn quyết câu nói này, ‘Vô niệm vô tưởng, hồn từ phía trên trụ huyệt lưu chuyển, thông qua hợp thành Thần huyệt mà ra...’ Nếu như đổi thành ‘Ngưng hồn tụ phách, phá hợp thành Thần huyệt, mà từ phía trên trụ huyệt ra!’ Lại là hiệu quả gì?”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Thiên trụ huyệt thuần dương, hợp thành Thần huyệt thuần âm, theo dương thuộc tính huyệt vị mà ra, linh hồn tự nhiên mang theo dương tính, sẽ không hoàn toàn chịu âm khí chỗ chi phối, Dương Chước suy, liền có thể yếu bớt đến nhỏ nhất, thậm chí... Không còn tồn tại?”"),t("br"),t("br"),n._v("Thanh âm chủ nhân là vu hồn sư cường giả, nghiên cứu hồn phách chẳng biết bao nhiêu năm, nghe được khẩu quyết cải biến, không ngừng nhắc tới, đột nhiên cứ thế tại nguyên chỗ."),t("br"),t("br"),n._v("Đối phương mặc dù chỉ sửa lại mấy chữ, lại làm cho công pháp biến đổi, dùng bản thân dương khí, tẩm bổ bản thân linh hồn, cứ như vậy, Dương Chước suy chẳng khác nào yếu bớt, thậm chí biến mất."),t("br"),t("br"),n._v("Hai cái huyệt đạo thay đổi trình tự, lại làm cho dẫn hồn sau khi thành công Vu hồn trở nên càng thêm cường đại."),t("br"),t("br"),n._v("“Nhưng... Như vậy đổi, đối chân khí yêu cầu cực cao, nhất định phải chân khí mười phần tinh thuần mới được, nếu không, hợp thành Thần huyệt chật hẹp, mang theo chân khí hồn phách, khẳng định sẽ không có cách nào thông qua...”"),t("br"),t("br"),n._v("Cứ thế qua về sau, lại cảm thấy không thể nào làm được."),t("br"),t("br"),n._v("Hồn sư linh hồn, mang theo chân khí, nếu quả thật khí không đủ tinh thuần, tràn đầy tạp chất, trước qua hợp thành Thần huyệt, là không thể nào thành công."),t("br"),t("br"),n._v("“Triệu Nhã, biểu diễn một điểm chân khí cho hắn nhìn xem!”"),t("br"),t("br"),n._v("Không trả lời hắn, Trương Huyền quay đầu phân phó."),t("br"),t("br"),n._v("“Vâng, lão sư!” Triệu Nhã tiến về phía trước một bước, ngón tay búng một cái, cùng nhau chân khí bắn đi ra."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Cảm nhận được cỗ này chân khí độ tinh thuần, thanh âm run lên."),t("br"),t("br"),n._v("“Khả năng xuyên thấu qua hợp thành Thần huyệt?” Trương Huyền cười nói."),t("br"),t("br"),n._v("“Có thể là có thể... Có thể cũng không phải là bất luận người nào chân khí đều tinh thuần như vậy...” Thanh âm hơi có vẻ trầm thấp."),t("br"),t("br"),n._v("“Không sao, muốn học không? Bái ta làm thầy!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng."),t("br"),t("br"),n._v("“Ta có thể dạy ngươi!”"),t("br"),t("br"),n._v("(Thi đại học kết thúc, lão nhai cũng rốt cục có thể an tâm gõ chữ, mọi người cũng có thể an tâm nhìn, nguyệt phiếu cũng có, hắc hắc)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-516"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-518"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);