(window.webpackJsonp=window.webpackJsonp||[]).push([[1766],{2121:function(n,h,t){"use strict";t.r(h);var c=t(44),i=Object(c.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-511-chuyen-tu-đau-đen-cuoi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-511-chuyen-tu-đau-đen-cuoi"}},[n._v("#")]),n._v(" Chương 511: Chuyện từ đầu đến cuối")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 511: Chuyện từ đầu đến cuối"),t("br"),t("br"),n._v("“Cửu Tâm liên?”"),t("br"),t("br"),n._v("Lần này, không riêng Trương Huyền mày nhăn lại, không rõ ràng cho lắm, liền ngay cả một bên Lạc Trúc cũng tất cả đều là cổ quái, tựa hồ chưa từng nghe qua cái tên này."),t("br"),t("br"),n._v("“Vâng! Bảy tâm liên danh xưng thánh vật, luyện hóa sau có thể biến thành nhục thân, nhưng... Cửu Tâm liên, là danh sư đại lục, chưa hề xuất hiện qua đồ vật, nếu như nhất định phải cùng phân cấp bậc, tuyệt đối có thể được xưng là thần vật!” Mộ huyệt chủ nhân nói."),t("br"),t("br"),n._v("“Thần vật?” Đám người đồng thời líu lưỡi, sững sờ tại nguyên chỗ."),t("br"),t("br"),n._v("Bảo vật cùng công pháp đồng dạng, đồng dạng chia làm thần, thánh, linh, quỷ, phàm năm cái cấp bậc, thánh vật chính là tìm khắp toàn bộ vạn liên minh quốc tế minh, cũng không tìm tới bảo bối, cao hơn cấp bậc thần vật, chỉ ở trong truyền thuyết, toàn bộ danh sư đại lục có thể hay không tìm tới một cái, cũng chưa biết chừng."),t("br"),t("br"),n._v("Mà cái này vật đen như mực, lại là loại này cấp bậc đồ vật?"),t("br"),t("br"),n._v("Đây cũng quá kinh khủng đi!"),t("br"),t("br"),n._v("Thần vật, thế nhưng là lúc trước ngay cả lỗ sư, đều không thể đạt được bảo bối."),t("br"),t("br"),n._v("“Ta cưỡng ép luyện hóa thần vật, tự nhiên không có khả năng thành công, không riêng tất cả thân gia đều bị thôn phệ sạch sẽ, linh hồn cũng bị cầm tù trong đó lại không cách nào rời đi, cái này một cầm tù chính là vài vạn năm...”"),t("br"),t("br"),n._v("Nói đến đây, mộ huyệt chủ nhân lộ ra mười phần phiền muộn."),t("br"),t("br"),n._v("Cũng khó trách, vốn nghĩ luyện hóa hoa sen, ngưng tụ nhục thân, từ đây bay lượn thiên địa, nhất phi trùng thiên. Nằm mơ đều không nghĩ tới, không những không thành công, còn bị cầm tù không cách nào thoát thân, muốn chết đều làm không được... Không có buồn bực điên mất cũng rất không tệ."),t("br"),t("br"),n._v("Cái này rất giống bị người bắt vào ngục giam, một quan không biết bao nhiêu năm, không cách nào rời đi, có thể dày vò đến bây giờ, gia hỏa này ý chí lực đã phi thường kiên cường."),t("br"),t("br"),n._v("“Cho nên, ngươi nghĩ thay mận đổi đào, để chúng ta một cái trong đó, thay thế ngươi chịu tội, mình tốt khôi phục tự do?”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Đúng!” Mộ huyệt chủ nhân lên tiếng: “Bị nhốt vài vạn năm, ta mỗi giờ mỗi khắc không nghĩ như thế nào đào thoát gia hỏa này ma chưởng, không biết qua bao lâu, rốt cục để cho ta nghĩ đến một cái!”"),t("br"),t("br"),n._v("“Cửu Tâm liên giam giữ ta, đã không có luyện hóa, cũng không có thôn phệ, đã nói lên nó không có giết năng lực của ta, nếu như lúc này có thể tìm tới một cái khác mới mẻ linh hồn thay thế, có lẽ liền có thể thoát đi ra ngoài!”"),t("br"),t("br"),n._v("Thật giống như trong sông chết đuối hơn người, cách mỗi mấy năm, liền sẽ tìm kẻ chết thay tới."),t("br"),t("br"),n._v("Cửu Tâm liên muốn chỉ là cái linh hồn mà thôi, chỉ cần tìm tới thay thế, liền có thể thoát ly khổ hải, không đến mức mỗi ngày canh giữ ở cái này đen nhánh trong huyệt mộ."),t("br"),t("br"),n._v("“Nghĩ thông suốt những này, ta liền bắt đầu cố gắng tách rời linh hồn, dùng không biết bao lâu, rốt cục để cho ta tách ra một đạo. Bất quá... Thực lực cũng là tại quá kém, chỉ có Hóa Phàm tam trọng tả hữu. Vốn cho rằng thấp như vậy thực lực, sống không được bao lâu, liền sẽ bị lưu quang suy thôn phệ sạch sẽ, tự động tử vong, ai ngờ... Đúng vào lúc này, cái này trộm mộ gia hỏa đến rồi!”"),t("br"),t("br"),n._v("Đám người sững sờ, lập tức tất cả đều nhìn về phía Lạc Trúc."),t("br"),t("br"),n._v("Cái sau hơi đỏ mặt."),t("br"),t("br"),n._v("Bị trước mặt mọi người vạch trần thân phận, cảm thấy có chút mất mặt."),t("br"),t("br"),n._v("Dù sao, cái nghề nghiệp này, không tính là hào quang, ngược lại cùng trộm vặt móc túi đồng dạng, không coi là gì."),t("br"),t("br"),n._v("“Thế là, trong lòng ta có cái to gan ý nghĩ! Cố ý đem tiến đến cơ quan, làm tương đối đơn giản một chút, để thuận lợi tiến vào, sau đó... Tiến lên đoạt xá!”"),t("br"),t("br"),n._v("Mộ huyệt chủ nhân nói tiếp: “Kết quả, quả nhiên thành công, gia hỏa này bởi vì e ngại, vội vàng rời đi nơi đây. Lúc đầu ta ý nghĩ rất đơn giản, đem nó đoạt xá sau khi thành công, mượn nhờ không hồn kim nhân, hấp dẫn càng nhiều người đến đây, sau đó chăm chú chọn lựa, tìm một cái thích hợp nhất, để linh hồn thay thế ta. Ai ngờ gia hỏa này ý niệm kiên định, lại thêm đoạt xá về sau, thực lực giảm lớn, một mực không thành công, thậm chí đến cuối cùng còn bị một cái thực lực thấp mập mạp diệt sát...”"),t("br"),t("br"),n._v("Nói đến đây, hoa sen không ngừng run rẩy, mang theo nồng đậm oán khí."),t("br"),t("br"),n._v("“Khụ khụ!” Bị nói thành thực lực thấp mập mạp, tôn mạnh có chút lúng túng vò đầu."),t("br"),t("br"),n._v("Lúc trước cái kia linh hồn, muốn đoạt xá hắn, kết quả bị thiếu gia lưu tại thể nội thiên đạo chân khí thôn phệ, cẩn thận nói đến, thực lực của hắn, cùng đối phương so, hoàn toàn chính xác kém không đáng giá nhắc tới."),t("br"),t("br"),n._v("Nghe được cái này, đám người cũng tất cả đều hiểu được."),t("br"),t("br"),n._v("Khó trách nơi này có như thế lợi hại gia hỏa, lần đầu tiên tới, Lạc Trúc làm sao có thể thuận lợi đào tẩu, nguyên lai là cố ý để rời đi, mục đích là vì hấp dẫn càng nhiều người đến đây."),t("br"),t("br"),n._v("Kế hoạch tuy tốt, khả năng nằm mơ đều không nghĩ tới, gặp Trương Huyền tên biến thái này, không biết tốn hao bao nhiêu năm tâm huyết ngưng tụ ra hồn phách, bị tại chỗ diệt sát sạch sẽ."),t("br"),t("br"),n._v("“Chờ một chút...”"),t("br"),t("br"),n._v("Trương Huyền khoát tay áo, nhướng mày nhìn lại: “Đã ngươi có thể tách ra linh hồn rời đi hoa sen, vì sao còn muốn đoạt xá những người khác?”"),t("br"),t("br"),n._v("Đã gia hỏa này mục đích lớn nhất là thoát ly hoa sen, đều có thể tách ra một bộ phận hồn phách rời đi, lại vì sao còn muốn dẫn dụ những người khác tới, nghĩ biện pháp đoạt xá?"),t("br"),t("br"),n._v("Dù sao, tách rời linh hồn cũng thuộc về hắn một bộ phận, mang theo suy nghĩ của hắn cùng tư tưởng, đã tương đương với thoát ly gông cùm xiềng xích."),t("br"),t("br"),n._v("“Ta... Là chia lìa linh hồn, nhưng... Cái này linh hồn, rời đi hoa sen thời điểm, nhận lấy trọng thương, sống không được bao lâu! Coi như đoạt xá thành công, cũng chỉ là trị ngọn không trị gốc, muốn chân chính thoát ly thứ này, nhất định phải có người dùng mới linh hồn thay thế...”"),t("br"),t("br"),n._v("Mộ huyệt chủ nhân nói."),t("br"),t("br"),n._v("“Kia... Ngươi vì sao không trực tiếp mê hoặc Lạc Trúc, để hắn thay thế ngươi? Vạn nhất sau khi hắn rời đi lại không nghĩ trở về, há không lại không có cách nào?” Trương Huyền tiếp tục hỏi."),t("br"),t("br"),n._v("Bị nhốt mộ huyệt không biết nhiều ít vạn năm, mới tới một cái Lạc Trúc, hắn làm sao dám xác nhận, đối phương nhất định sẽ dẫn người tới, còn không bằng nghĩ biện pháp mê hoặc, trước hết để cho thay thế mình lại nói."),t("br"),t("br"),n._v("“Kẻ trộm mộ, thiên tính thuần âm, loại người này không cách nào tu luyện Dẫn Hồn Quyết!”"),t("br"),t("br"),n._v("Mộ huyệt chủ nhân nói: “Mà lại, nhìn hắn niên kỷ, sống không sai biệt lắm, lại thêm thực lực thấp như vậy, đoạt xá có làm được cái gì? Lại muốn đứng trước tử vong sao?”"),t("br"),t("br"),n._v("Lạc Trúc hơi đỏ mặt."),t("br"),n._v("Không nghĩ tới, tôn mạnh vừa bị khinh bỉ xong, hắn cũng bị khinh bỉ..."),t("br"),t("br"),n._v("Nghe được giải thích, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Dạng này liền nói đến thông."),t("br"),t("br"),n._v("Vu hồn sư đoạt xá, có đoạt xá suy, mỗi đoạt xá một lần, tự thân lực lượng liền suy giảm một lần, đã như vậy, muốn động thủ, liền làm cái tốt, chấp nhận chịu đựng... Còn không bằng không lãng phí cơ hội."),t("br"),t("br"),n._v("“Về sau, ta phân ra cái kia đạo linh hồn bị diệt, vốn cho rằng lại không có hi vọng, không nghĩ tới các ngươi đã tới... Sự tình phía sau liền đơn giản!” Mộ huyệt chủ nhân nói."),t("br"),t("br"),n._v("“Sau đó ngươi liền hấp dẫn chúng ta tu luyện Dẫn Hồn Quyết, một khi có thể làm được hồn phách xuất thể, ngươi sẽ lấy cửa ải danh nghĩa, để hồn phách tiếp xúc hoa sen... Như vậy trải qua, hồn phách sẽ tự động bị hấp dẫn, từ đó... Làm ngươi kẻ chết thay!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Đúng!” Mộ huyệt chủ nhân không có phủ nhận."),t("br"),t("br"),n._v("Cho đến lúc này, đám người giờ mới hiểu được chuyện gì xảy ra, đồng thời phía sau toát ra mồ hôi lạnh."),t("br"),t("br"),n._v("Trước đó gia hỏa này nói thật dễ nghe, đối bọn hắn tiến hành khảo hạch, hiện tại xem ra, thật muốn tu luyện Dẫn Hồn Quyết, đồng thời thành công, chỉ sợ linh hồn đã bị cái này gốc quỷ dị đóa hoa thôn phệ, mà nhục thân bị triệt để đoạt xá."),t("br"),t("br"),n._v("May mắn trương sư phát hiện ra sớm, không phải, hậu quả khó mà lường được."),t("br"),t("br"),n._v("“Không đúng!” Triệu không phải võ mày nhăn lại: “Thực lực ngươi mạnh như vậy, hẳn là có thể trực tiếp đem chúng ta linh hồn làm thoát ly nhục thân, bị hoa sen thôn phệ đi!”"),t("br"),t("br"),n._v("Đã gia hỏa này là sáu Tinh Hồn Sư, ngay cả thận thần Huyễn Hải đều có thể bố trí ra, đem bọn hắn những này thực lực, mạnh nhất bất quá Hóa Phàm tứ trọng linh hồn làm ra nhục thân, không khó lắm đi!"),t("br"),t("br"),n._v("Vì sao còn muốn tu luyện Dẫn Hồn Quyết, phiền toái như vậy."),t("br"),t("br"),n._v("“Cái này vẫn là ta để giải thích một cái đi!”"),t("br"),t("br"),n._v("Trương Huyền thần sắc lạnh nhạt nhìn qua: “Hắn không phải là không muốn làm như thế, mà là... Làm không được!”"),t("br"),t("br"),n._v("“Làm không được?” Đám người nghi hoặc."),t("br"),t("br"),n._v("“Có hai nguyên nhân. Thứ nhất, vu hồn sư có chiến đấu suy, cưỡng ép vận dụng hồn lực, rút ra người khác linh hồn, đối với hắn tự thân sẽ có rất đại thương hại! Thứ hai, hắn bị nhốt hoa sen, có thể khống chế cơ quan, đánh lén ta, đã dùng hết toàn lực, muốn cùng chúng ta nhiều người như vậy chiến đấu, thậm chí diệt sát, căn bản làm không được! Không phải, khẳng định đã sớm động thủ, không đến mức một mực dùng cơ quan tới đối phó chúng ta, thậm chí ngay cả tự thân đều che giấu, không bị phát hiện!”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Đối phương thi triển võ kỹ công kích, thư viện tạo ra thư tịch, để hắn nhìn ra chuyện ẩn ở bên trong."),t("br"),t("br"),n._v("Không phải, cũng không có khả năng như thế dũng mãnh phi thường, vọt thẳng quá khứ, đem hoa sen móc ra."),t("br"),t("br"),n._v("Bị thần vật vây khốn, có thể đem linh hồn cùng khí thế lan tràn ra, khống chế cơ quan, liền rất lợi hại, nếu có thể đem mọi người tuỳ tiện xoá bỏ, cũng sẽ không cần như thế đại phí trắc trở."),t("br"),t("br"),n._v("Đám người mới chợt hiểu ra."),t("br"),t("br"),n._v("Khó trách mạnh như thế người đối mặt bọn hắn bọn này yếu gà, còn muốn bó tay bó chân, giấu đầu lộ đuôi, nguyên lai... Gia hỏa này cũng là chỉ có bề ngoài, nhiều nhất phát ra cái khí thế, dùng chút cơ quan, mà trên thực tế không làm gì được bọn họ."),t("br"),t("br"),n._v("Mộ huyệt chủ nhân hiển nhiên không nghĩ tới mình không cách nào phát huy thực lực sự tình, cũng bị trước mắt người thanh niên này nhìn ra, tràn đầy hoảng sợ, tràn đầy cảm giác bị thất bại."),t("br"),t("br"),n._v("Vốn còn nghĩ, nếu như đối phương không biết, còn có thể lấy khí thế áp bách, uy hiếp đối phương, hiện tại xem ra, một tia hi vọng cuối cùng cũng không có."),t("br"),t("br"),n._v("Nhất là gia hỏa này ngân châm, cắm ở hoa sen ý niệm có thể tản ra địa phương, để hắn khống chế cơ quan năng lực đều làm không được, sống không bằng chết, lại nghĩ phản kháng, khẳng định không làm được."),t("br"),t("br"),n._v("“Tốt, nói một chút Đoạn Long tỏa đi!”"),t("br"),t("br"),n._v("Tất cả mọi chuyện đều giải khai, đầu này mạnh nhất lục tinh vu hồn sư cũng bị chế phục, đám người nhẹ nhàng thở ra, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Hiện tại thông đạo bị phong bế, muốn rời khỏi, chỉ có thể mở ra Đoạn Long tỏa, gia hỏa này đã đem thứ này khóa lại, liền khẳng định có rời đi phương pháp."),t("br"),t("br"),n._v("“Cái này... Ta đích xác không có cách nào mở ra!” Mộ huyệt chủ nhân hơi có vẻ lúng túng thanh âm vang lên."),t("br"),t("br"),n._v("“Không có cách nào mở? Vậy ngươi đoạt xá thành công, như thế nào rời đi nơi này?”"),t("br"),t("br"),n._v("Trương Huyền nhịn không được nói."),t("br"),t("br"),n._v("Gia hỏa này đã muốn chạy trốn hoa sen, liền khẳng định nghĩ đến như thế nào đi, luôn không khả năng đoạt xá thành công, bởi vì Đoạn Long tỏa, đem mình lần nữa vây chết ở chỗ này đi!"),t("br"),t("br"),n._v("“Ta có thể dùng... Vu hồn sư thủ đoạn rời đi!” Mộ huyệt chủ nhân nói."),t("br"),t("br"),n._v("“Vu hồn sư thủ đoạn?” Đám người không hiểu."),t("br"),t("br"),n._v("“Các ngươi nhìn lên trên, mộ huyệt phía trên, có cái cơ quan, chỉ cần mở ra, có thể nối thẳng phía trên đầm nước... Vu hồn sư, linh hồn nhẹ nhàng, có thể tự do phi hành, cho dù đoạt xá thành công, chỉ cần ta nguyện ý trả giá đắt, đồng dạng có thể từ nơi này bay ra ngoài... Mà bình thường Hóa Phàm cảnh người tu luyện, là không thể phi hành, cho nên... Coi như nói với các ngươi, cũng vô dụng!”"),t("br"),t("br"),n._v("Mộ huyệt chủ nhân nói."),t("br"),t("br"),n._v("“Phi hành?”"),t("br"),t("br"),n._v("Đám người đồng loạt nhìn lên, quả nhiên thấy đại điện trên cùng, một cái nham thạch nhìn có chút cổ quái, mà khoảng cách cao như vậy, đừng nói Trương Huyền bọn người, coi như Hóa Phàm tứ trọng thi đấu Các chủ, cũng vô pháp đi vào trước mặt."),t("br"),t("br"),n._v("Chớ nói chi là mượn nhờ trốn."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-519"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-521"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);