(window.webpackJsonp=window.webpackJsonp||[]).push([[1768],{2123:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-513-cuu-song-lo-xung-phuong-phap"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-513-cuu-song-lo-xung-phuong-phap"}},[n._v("#")]),n._v(" Chương 513: Cứu sống Lộ Xung phương pháp")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 513: Cứu sống Lộ Xung phương pháp"),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Không để ý tới mọi người khiếp sợ, Trương Huyền đi tới nóc phòng, đưa tay tại trên cơ quan sờ một cái."),t("br"),t("br"),n._v("Ngay sau đó, ngón tay như điện, liên tục tại nhô ra nham thạch bên trên chụp mấy lần."),t("br"),t("br"),n._v("Nắm giữ Thiên Đạo thư viện, có thể tuỳ tiện phát hiện trong cơ quan lỗ thủng, tựa như trước đó xích kim quan tài, mệnh danh không cách nào phá mở, trên thực tế tìm được thiếu hụt, tiến hành lợi dụng, phá vỡ liền sẽ vô cùng đơn giản."),t("br"),t("br"),n._v("Trước mắt cơ quan này cũng giống như vậy."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Liên tục chụp mấy lần, một tiếng vang giòn, nham thạch từ từ mở ra, Trương Huyền thân thể khẽ động, chui vào."),t("br"),t("br"),n._v("Là cái thông đạo, ngẩng đầu nhìn lại, một mảnh đen kịt, phía trên nên còn có một chỗ cơ quan, đem đầm nước triệt để chặn lại, không đến mức tiết lộ xuống."),t("br"),t("br"),n._v("Thân thể nhảy lên, đi mấy chục mét, quả nhiên thấy được một cái nham thạch to lớn, bắt chước làm theo, đem nó mở ra, đầm nước lập tức xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Cùng tới thời điểm đồng dạng, nơi này có đặc thù cơ quan, người có thể thông qua, đầm nước nhưng chảy xuôi không xuống."),t("br"),t("br"),n._v("“Lợi hại!”"),t("br"),t("br"),n._v("Nhìn lướt qua, phát hiện cái này cơ quan so với trận pháp đều không hề yếu, Trương Huyền nhịn không được tán thưởng một tiếng."),t("br"),t("br"),n._v("Không hổ là Thiên Công sư, những này cơ quan kỹ xảo, quả thực đủ không ít người học tập cả một đời, đều không thể hiểu."),t("br"),t("br"),n._v("Tại chỗ điều chỉnh một hồi, cảm giác Hồng Trần Đạp Thiên bộ tiêu hao không sai biệt lắm khôi phục, Trương Huyền lúc này mới một lần nữa trở lại mọi người trước mặt, theo Lạc Trúc nơi đó tìm được một sợi dây thừng, thắt ở trong thông đạo."),t("br"),t("br"),n._v("Có dẫn dắt, mọi người cũng không chậm trễ, nhao nhao ngược lên."),t("br"),t("br"),n._v("Bọn hắn đều là người tu luyện, không biết phi hành, bò dây thừng lại là mười phần đơn giản."),t("br"),t("br"),n._v("Theo đầm nước hướng thượng du đi, rất mau trở lại đến bên bờ, lúc này mới cảm giác dường như đã có mấy đời, tựa như một giấc chiêm bao."),t("br"),t("br"),n._v("Mặc dù đi vào Vu hồn mộ thời gian không dài, nhưng đã trải qua sinh tử, tuyệt vọng, thế nào đều không có nghĩ đến, cuối cùng vẫn còn sống đi ra."),t("br"),t("br"),n._v("“Đa tạ Trương sư!”"),t("br"),t("br"),n._v("Đem quần áo dùng chân khí sấy khô, Trại các chủ đám người đi tới trước mặt, vui lòng phục tùng."),t("br"),t("br"),n._v("Không hổ là trời nhận danh sư, gặp được nguy hiểm quả nhiên cùng bọn hắn không giống, dùng trí tuệ, cứ thế mà thay đổi cục diện, thậm chí đem một cái Hóa Phàm cửu trọng lục tinh vu hồn sư, thu làm tôi tớ."),t("br"),t("br"),n._v("Không phải tận mắt nhìn thấy, tuyệt không dám tin tưởng."),t("br"),t("br"),n._v("“Khách khí, mọi người cũng rất vất vả, dù sao cũng không vội vã rời khỏi, không bằng ngay ở chỗ này nghỉ ngơi một đêm, chúng ta ngày mai lại rời đi!”"),t("br"),t("br"),n._v("Trải qua một phen chơi đùa, trời đã tối xuống, sao dày đặc treo tinh hà, sáng sủa loá mắt, một vầng loan nguyệt, treo chếch đỉnh núi, cho người ta yên tĩnh tường hòa cảm giác."),t("br"),t("br"),n._v("Thu ý lờ mờ, trên cây lá cây cũng không trước đó như vậy xanh biếc, bắt đầu chậm rãi biến vàng, gió núi phất qua mang theo một chút nhàn nhạt ý lạnh."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Nghe được không vội vã rời khỏi, mọi người nhất thời nhẹ nhàng thở ra."),t("br"),t("br"),n._v("Tại Vu hồn mộ thời gian không dài, nhưng tâm thần tiều tụy, so đã trải qua một hồi đại chiến còn muốn vất vả."),t("br"),t("br"),n._v("Nơi này là Yêu Thần thú hang ổ, cũng không có cái gì mắt không mở linh thú dám tới làm loạn, mọi người dâng lên lửa trại, đem chuẩn bị xong ăn uống lấy ra, thời gian không dài, mùi thịt bốn phía."),t("br"),t("br"),n._v("“Lão sư, ngươi trên cổ làm sao vậy?”"),t("br"),t("br"),n._v("Trương Huyền cũng đói lắm rồi, mới vừa ăn một hồi đồ vật, chỉ thấy Vương Dĩnh đi tới, mặt mũi tràn đầy nghi ngờ nhìn qua."),t("br"),t("br"),n._v("“Cổ?”"),t("br"),t("br"),n._v("Sửng sốt một chút, chỉ thấy nàng đưa qua một cái gương đồng, nhìn sang, chỉ thấy cái cằm chỗ, có một chỗ hơi có vẻ xám xanh."),t("br"),t("br"),n._v("“A, là bị Tiểu Mặc Vu hồn chi khí lây nhiễm, yên tâm đi, nên rất nhanh liền có thể lui xuống đi!”"),t("br"),t("br"),n._v("Tại Vu hồn mộ thời điểm, vì để cho Mặc Hồn Sinh mắc câu, sử dụng Long lân hộ thân phù đem nó đả thương, cam tâm tình nguyện bị đối phương bắt lấy, hẳn là khi đó, lưu lại dấu vết, chẳng qua không nghiêm trọng, nếu không nhìn kỹ, căn bản nhìn không ra."),t("br"),t("br"),n._v("Cũng là Vương Dĩnh tâm tư tỉ mỉ, mới ý phát hiện."),t("br"),t("br"),n._v("Vu hồn sư, cùng nhân loại thuộc về hai loại hình thức sinh mệnh, bị mạnh như thế người tiếp xúc, lưu lại dấu ấn mười phần bình thường, nên không bao lâu, liền sẽ dần dần biến mất."),t("br"),t("br"),n._v("“Vậy là tốt rồi!” Gặp không phải cái vấn đề lớn gì, Vương Dĩnh lúc này mới thở phào nhẹ nhõm."),t("br"),t("br"),n._v("//ngantruyen.com/"),t("br"),n._v("“Ngươi cùng Triệu Nhã bọn hắn đem Lộ Xung nhận lấy, ta đi nghiên cứu một ít chuyện!”"),t("br"),t("br"),n._v("Trương Huyền bàn giao."),t("br"),t("br"),n._v("Lộ Xung không cùng mọi người cùng một chỗ, mà là đặt ở Triệu Phi Vũ đầu kia linh thú trên lưng, hiện tại Yêu Thần thú bị thuần phục, đã không có uy hiếp, cũng nên đem nó mang tới."),t("br"),t("br"),n._v("“Vâng!” Biết lão sư muốn cứu Lộ Xung tâm ý, Vương Dĩnh gật gật đầu."),t("br"),t("br"),n._v("An bài xong việc tình, Trương Huyền thì nhấc chân đi vào Yêu Thần thú sơn động."),t("br"),t("br"),n._v("Sơn động rộng lớn, sạch sẽ, tìm một chỗ an tĩnh, tinh thần khẽ động, đem gốc kia Cửu Tâm liên lấy ra ngoài."),t("br"),t("br"),n._v("Vừa rồi tại lòng đất không cẩn thận quan sát, hiện tại vừa vặn nghiên cứu một chút, tiện thể ép hỏi một ít chuyện, xem có hay không vừa Lộ Xung cứu sống phương pháp."),t("br"),t("br"),n._v("“Chủ nhân!”"),t("br"),t("br"),n._v("Rời khỏi trữ vật giới chỉ, Mặc Hồn Sinh lập tức phát hiện đã rời đi lòng đất, trong giọng nói tràn ngập hưng phấn."),t("br"),t("br"),n._v("Bị nhốt vài vạn năm, rốt cục lại có thể nhìn thấy thế giới bên ngoài, kích động trong lòng có thể nghĩ."),t("br"),t("br"),n._v("Bởi vậy, một tiếng này “Chủ nhân” kêu cam tâm tình nguyện, không có nửa điểm thẹn thùng."),t("br"),t("br"),n._v("“Ta muốn biết, ngươi có phải có biện pháp cứu tỉnh linh hồn ngủ say người!” Trương Huyền sắc mặt nghiêm túc nhìn qua."),t("br"),t("br"),n._v("“Linh hồn rơi vào trạng thái ngủ say người?”"),t("br"),t("br"),n._v("Mặc Hồn Sinh sửng sốt một chút, nói: “Linh hồn rơi vào trạng thái ngủ say, chỉ cần hồn phách đi vào trong cơ thể của hắn, đem nó đánh thức là được, không tính là phức tạp!”"),t("br"),t("br"),n._v("Trương Huyền sửng sốt, trước đó hắn liền nghe nói qua phương pháp này, chỉ là hắn là võ giả, linh hồn không cách nào ly thể, chỉ có thể như vậy coi như không có gì, nhưng... Lúc này không giống."),t("br"),t("br"),n._v("Tu luyện Thiên Đạo Dẫn Hồn quyết, đã có thể để cho hồn phách ly thể, hoàn toàn có thể đi vào đối phương trong cơ thể, đem nó đánh thức."),t("br"),n._v(" “Vâng, có điều, cái này cùng đoạt xá có chút tương tự, hơi không cẩn thận, rất dễ dàng xuất hiện đoạt xá suy.” Có linh hồn khế ước, Mặc Hồn Sinh cũng không dám giấu diếm."),t("br"),t("br"),n._v("Hồn phách đi vào trong cơ thể người khác, đã cùng đoạt xá có chút giống, có thể đem đối phương đánh thức còn tốt, không gọi tỉnh, rất dễ dàng xuất hiện đoạt xá suy, để cho mình thực lực cũng sụt giảm."),t("br"),t("br"),n._v("“Mấu chốt nhất là... Một khi không cách nào đánh thức, linh hồn của ngươi đi vào đối phương trong cơ thể, làm không cẩn thận liền sẽ để thân thể của hắn nhận chủ, nếu thực như thế, còn muốn thanh tỉnh liền không khả năng!”"),t("br"),t("br"),n._v("Mặc Hồn Sinh chần chờ một chút nói: “Dù sao, có thể đi vào người khác hồn phách, tất nhiên mạnh mẽ vô song, đối phương rơi vào trạng thái ngủ say, khẳng định rất nhỏ yếu, thân thể hồi lâu không có linh hồn thoải mái, rất dễ dàng nhận sai chủ nhân, một khi như thế, không những không cách nào cứu người, ngược lại sẽ đem nó hại chết!”"),t("br"),t("br"),n._v("Linh hồn bị thương nặng, mới có thể rơi vào trạng thái ngủ say."),t("br"),t("br"),n._v("Đây là hồn phách lực lượng là mười phần nhỏ yếu, một cái cường đại linh hồn đi vào trong cơ thể triệu hoán, có thể đánh thức tốt nhất, không gọi tỉnh, rất dễ dàng tu hú chiếm tổ chim khách, đối phương còn muốn phục sinh, cơ bản vô vọng."),t("br"),t("br"),n._v("“Nhưng có phương pháp gì tránh khỏi?”"),t("br"),t("br"),n._v("Sầm mặt lại, Trương Huyền nắm đấm xiết chặt."),t("br"),t("br"),n._v("Đã động thủ, tự nhiên muốn trăm phần trăm có thể bảo chứng đối phương sống tới, nếu là không chữa khỏi, chữa chết, về sau lương tâm bên trên làm sao có thể sao?"),t("br"),t("br"),n._v("Lộ Xung dù sao cũng là vì hắn mà chết."),t("br"),t("br"),n._v("Không cách nào chữa khỏi, thẹn là thầy người!"),t("br"),t("br"),n._v("“Tránh khỏi? Trừ phi đối phương linh hồn, trở nên cứng cỏi, mạnh mẽ, coi như triệu hoán không được, cũng có thể khống chế thân thể, không đến mức bị đoạt xá!”"),t("br"),t("br"),n._v("Mặc Hồn Sinh nói: “Biện pháp tốt nhất, chính là lợi dụng Tỉnh Hồn thảo, luyện chế thành Tỉnh Hồn hương, sau đó ở bên cạnh nhen lửa, để hắn hấp thu, lúc này tiến hành triệu hoán thành công khả năng sẽ lớn hơn rất nhiều!”"),t("br"),t("br"),n._v("“Tỉnh Hồn thảo?” Trương Huyền nhướng mày."),t("br"),t("br"),n._v("Làm luyện đan sư, từng nhìn qua không ít liên quan tới dược liệu thư tịch, có thể loại thuốc này vật, chưa từng nghe qua."),t("br"),t("br"),n._v("“Tỉnh Hồn thảo cũng không phải là thực vật, mà là vu hồn sư dùng linh hồn tẩm bổ mới có thể đản sinh đặc thù vật phẩm, có thể trồng trọt!”"),t("br"),t("br"),n._v("Mặc Hồn Sinh nói."),t("br"),t("br"),n._v("“Loại một gốc đại khái bao lâu?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Nếu như thời gian quá dài, chỉ sợ cần muốn những biện pháp khác."),t("br"),t("br"),n._v("“Không dài, một tháng đầy đủ! Chỉ là... Ta bây giờ bị vây ở chỗ này, linh hồn không cách nào rời khỏi, căn bản không có cách nào trồng trọt...” Mặc Hồn Sinh cười khổ nói."),t("br"),t("br"),n._v("Hắn bị vây ở Cửu Tâm liên bên trong, trốn không thoát đến, coi như lòng có hơn cũng lực không đủ."),t("br"),t("br"),n._v("“Chỉ cần là vu hồn sư liền có thể trồng trọt?”"),t("br"),t("br"),n._v("Không trả lời hắn, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Vâng, chỉ cần dẫn hồn thành công vu hồn sư, đều có thể trồng trọt, phương pháp rất đơn giản, ta có thể hiện tại liền cùng ngươi nói!” Mặc Hồn Sinh nói."),t("br"),t("br"),n._v("“Ừm, đem phương pháp nói một chút đi!”"),t("br"),t("br"),n._v("Trương Huyền khoát khoát tay, thần sắc lạnh nhạt nói."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Mặc Hồn Sinh lên tiếng, lập tức mở lời."),t("br"),t("br"),n._v("Rất mau đem Tỉnh Hồn thảo bồi dưỡng phương pháp nói một lần."),t("br"),t("br"),n._v("Cùng hắn nói đồng dạng, Tỉnh Hồn thảo, cũng không phải là một loại riêng biệt thực vật, mà là cần linh hồn bồi dưỡng vật phẩm, chỉ có như vậy mới có thể để cho linh hồn gây nên cộng minh, đạt tới tỉnh hồn mục đích."),t("br"),t("br"),n._v("“Ngươi không phải là muốn... Tự mình tu luyện Dẫn Hồn quyết, trở thành vu hồn sư bồi dưỡng chứ?”"),t("br"),t("br"),n._v("Gặp trước mắt chủ nhân, đem bồi dưỡng phương pháp nhớ kỹ, Mặc Hồn Sinh kịp phản ứng, tràn đầy không thể tin được."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Đã thứ này đối Lộ Xung hữu dụng, thật sự là hắn muốn tự mình đi trồng thực."),t("br"),t("br"),n._v("“Có thể ngươi là trời nhận danh sư...” Mặc Hồn Sinh giật nảy mình."),t("br"),t("br"),n._v("Vị chủ nhân này thế nhưng là trời nhận danh sư, thiên phú như vậy, một khi tu luyện vu hồn sư, chẳng khác nào từ bỏ thân phận... Có phải hay không quá lỗ mãng?"),t("br"),t("br"),n._v("“Yên tâm đi, ta đã đem Dẫn Hồn quyết sửa đổi một lần, coi như dẫn hồn thành công, cũng sẽ không đối thân thể sinh ra ảnh hưởng, có thể làm được linh nhục hợp nhất!”"),t("br"),t("br"),n._v("Trương Huyền thản nhiên nói."),t("br"),t("br"),n._v("“Sửa chữa? Linh nhục hợp nhất?” Hoa sen bên trong Mặc Hồn Sinh con mắt trợn tròn, bờ môi run rẩy, tràn đầy không thể tin được."),t("br"),t("br"),n._v("Dẫn Hồn quyết, thế nhưng là bọn hắn vu hồn sư tu luyện chẳng biết bao lâu công pháp, đã trải qua chẳng biết bao nhiêu cường giả cải tiến, lịch đại sửa chữa đều làm không được, cái tên này chỉ nhìn mấy hơi thở liền có thể sửa chữa, còn làm đến linh nhục hợp nhất?"),t("br"),t("br"),n._v("Làm sao có thể!"),t("br"),t("br"),n._v("Ngay tại nghi hoặc, cảm thấy không thể nào thời điểm, chỉ thấy trước mắt chủ nhân khoanh chân ngồi xuống, thân thể nhoáng một cái, một cái bóng ảo xuất hiện ở phía trên."),t("br"),t("br"),n._v("“Dẫn hồn xuất thể?”"),t("br"),t("br"),n._v("Thanh âm phát run."),t("br"),t("br"),n._v("Làm lục tinh vu hồn sư, hắn có thể rõ ràng nhìn ra, tự mình vị chủ nhân này hồn phách đã thoát ly thân thể."),t("br"),t("br"),n._v("“Hiện tại nhìn thấy ta?” Ngay tại khiếp sợ, liền nghe đến đối phương linh hồn thanh âm vang lên, Trương sư an tĩnh đứng tại hoa sen trước mặt."),t("br"),t("br"),n._v("“Thấy được...” Mặc Hồn Sinh run rẩy."),t("br"),t("br"),n._v("Trương Huyền hài lòng gật đầu."),t("br"),t("br"),n._v("Xem ra dùng Thiên Đạo Dẫn Hồn quyết ly thể hồn phách, cùng Thiên Đạo chân khí đồng dạng, chỉ cần mình nguyện ý, coi như vu hồn sư thực lực cao hơn chính mình, cũng ngươi là không nhìn ra."),t("br"),t("br"),n._v("Chỉ có muốn cho hắn trông thấy, mới có thể nhìn ra."),t("br"),t("br"),n._v("“Hô!”"),t("br"),t("br"),n._v("Xác nhận hết, Trương Huyền tinh thần khẽ động, lần nữa trở lại thân thể."),t("br"),t("br"),n._v("“Hồn phách quy vị, viên mãn không tiết, huyệt đạo cộng minh, linh nhục hợp nhất... Ngươi, ngươi thật cải tiến Dẫn Hồn quyết?”"),t("br"),t("br"),n._v("Nhìn thấy Trương Huyền hồn phách trở lại thân thể, không xuất hiện bất kỳ biến hóa, ngược lại rất nhiều huyệt đạo, tựa như long ngâm khẽ hát, Mặc Hồn Sinh rít lên một tiếng, dường như sắp dọa ngất đi qua."),t("br"),t("br"),n._v("(Chúc mừng thư hữu  trở thành quyển sách thứ 28 vị minh chủ, đặc biệt tăng thêm một chương!)"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-521"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-523"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);