(window.webpackJsonp=window.webpackJsonp||[]).push([[1769],{2124:function(n,h,t){"use strict";t.r(h);var i=t(44),c=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-514-bon-han-đeu-la-nam-nhan-ah"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-514-bon-han-đeu-la-nam-nhan-ah"}},[n._v("#")]),n._v(" Chương 514: Bọn hắn đều là nam nhân ah!")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 514: Bọn hắn đều là nam nhân ah!"),t("br"),t("br"),n._v("Bình thường Dẫn Hồn quyết, hồn phách ly thể, thân thể sinh cơ sẽ nhanh chóng suy bại, trở nên chán chường chịu không nổi."),t("br"),t("br"),n._v("Mà trước mắt vị này, toàn thân cao thấp sinh cơ bừng bừng, toả ra ánh sáng chói lọi, chư huyệt hợp xướng, linh nhục hợp nhất. Hồn phách quay về, không những nhìn không ra cùng phổ thông Hóa Phàm cảnh võ giả, có cái gì không giống, ngược lại khí chất bên trên càng hơn một bậc, cho người ta trên linh hồn mang đến nồng đậm áp bách!"),t("br"),t("br"),n._v("Chỉ nhìn thấy một chút, Mặc Hồn Sinh liền biết, đối phương tu luyện Dẫn Hồn quyết tuyệt đối cùng hắn tu luyện không giống, đã triệt để bị cải tiến, tránh né hồn lực ngũ suy, đạt đến một cái khác phương diện."),t("br"),t("br"),n._v("Truyền, Vu hồn cái nghề nghiệp này là khai phái tổ sư Lưu Mặc Tử, trộm lấy Thiên Đạo bên trong liên quan tới linh hồn nội dung sáng tạo, bởi vì Thiên Đạo có thiếu, trộm được nội dung không được đầy đủ, mới đưa đến xuất hiện ngũ suy, âm hồn cùng thân thể không cách nào triệt để hợp nhất các loại thiếu hụt."),t("br"),t("br"),n._v("Hậu bối vu hồn sư, chẳng biết dùng bao nhiêu phương pháp, đều không thể cải tiến mảy may, trước mắt vị này chỉ nhìn một chút, không chỉ đổi xong, còn thành công..."),t("br"),t("br"),n._v("Làm sao không để hắn khiếp sợ?"),t("br"),t("br"),n._v("“Đến cùng làm sao làm được?”"),t("br"),t("br"),n._v("Sau khi hết khiếp sợ, ngay sau đó là nồng đậm hưng phấn: “Trời nhận danh sư, có thể cải tiến Dẫn Hồn quyết... Phần này năng lực cùng hiểu biết, chỉ sợ thật có thể dẫn ta đi rất xa...”"),t("br"),t("br"),n._v("Muốn trước đó bị ép nhận chủ, thậm chí ký kết linh hồn khế ước, là bị bức ép bất đắc dĩ, hiện tại tận mắt thấy đối phương năng lực cùng thủ đoạn, đột nhiên ý thức được... Cái chủ nhân này, có lẽ thật nhận đúng rồi!"),t("br"),t("br"),n._v("Thiên tài như thế nhân vật tuyệt thế, về sau theo ở phía sau, tận tâm tận trách nhiệm, chỉ cần lấy được cẩn thận chỉ điểm, thoát ly ràng buộc, siêu việt lúc trước, khẳng định là chắc chắn!"),t("br"),t("br"),n._v("“Đây là ta học qua vu hồn sư thủ đoạn, cùng những năm này nghiên cứu ra được bí tịch phương pháp, có thể sẽ đối chủ nhân hữu dụng!”"),t("br"),t("br"),n._v("Nghĩ tới những thứ này, Mặc Hồn Sinh lại không chần chờ, tinh thần khẽ động, từng đoạn tin tức, liền theo linh hồn truyền lại mà tới."),t("br"),t("br"),n._v("Vu hồn sư, có thể trực tiếp tiến hành linh hồn truyền lại, loại phương pháp này so giải thích, đọc sách đều muốn dễ dàng hơn."),t("br"),t("br"),n._v("Bởi vì là khế ước quan hệ, loại này truyền lại tốc độ càng nhanh, Trương Huyền chỉ cảm thấy thấy hoa mắt, trong đầu lập tức nhiều hơn một đại đoàn kiến thức mới, tất cả đều là vu hồn sư."),t("br"),t("br"),n._v("Mặc Hồn Sinh là Thượng Cổ sống sót lục tinh vu hồn sư, ngay lúc đó truyền thừa còn không có đoạn tuyệt, cơ hồ hàm cái toàn bộ nghề nghiệp tất cả tri thức."),t("br"),t("br"),n._v("“Không hổ là Thượng Cổ người người kiêng kỵ chức nghiệp, quả nhiên bác đại tinh thâm...”"),t("br"),t("br"),n._v("Đại khái nhìn một lần, Trương Huyền miệng há mở, tràn đầy hoảng sợ."),t("br"),t("br"),n._v("Khó trách cái nghề nghiệp này, có thể truyền thừa chẳng biết bao nhiêu năm, bị nhiều người như vậy kiêng kị, thậm chí so độc sư đều kinh khủng, quả thực không cho dò xét."),t("br"),t("br"),n._v("Trước đó Dẫn Hồn quyết, đối với những này chỉ là đến, chỉ là cơ sở nhất nhập môn."),t("br"),t("br"),n._v("“Cái này phân liệt linh hồn pháp, là ngươi sáng tạo?”"),t("br"),t("br"),n._v("Đột nhiên, nhìn thấy một cái nội dung, Trương Huyền nhìn về phía trước mắt hoa sen."),t("br"),t("br"),n._v("Cái tên này bị nhốt Cửu Tâm liên bên trong vài vạn năm, chuyên tâm nghĩ ra được, mới đưa linh hồn chia ra một bộ phận, có thể thành công, môn công pháp này, chỉ sợ đúng là hắn nghiên cứu nhiều năm đoạt được."),t("br"),t("br"),n._v("“Là ta lục lọi ra tới...” Mặc Hồn Sinh gật đầu: “Nhờ vào phương pháp này, có thể chia ra một cái khác bản thân, về sau bản thể linh hồn coi như bị diệt sát, cũng có thể dựa vào cái này phân thân sống sót!”"),t("br"),t("br"),n._v("“Phân thân?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Vâng, loại này linh hồn phân liệt, có thể hoàn chỉnh phục chế bản thân, bao quát ký ức! Tương đương một lần nữa ngưng luyện một cái linh hồn, thật nếu gặp phải nguy hiểm, dùng để bảo mệnh, cũng không tệ lắm!”"),t("br"),t("br"),n._v("Mặc Hồn Sinh nói."),t("br"),t("br"),n._v("Trương Huyền gật gật đầu, rất mau đem cái này linh hồn phân liệt pháp nhìn một lần."),t("br"),t("br"),n._v("Không thể không, bộ này phương pháp quả thực rất lợi hại, tuyệt đối là đối linh hồn nắm giữ đến cực hạn mới có thể hoàn thành."),t("br"),t("br"),n._v("Xem ra, nếu không phải cái tên này bị nhốt Cửu Tâm liên, không cách nào hấp thu linh khí tu luyện, nhờ vào vài vạn năm tích lũy, cùng đối Vu hồn lý giải, xung kích bát tinh, thậm chí cửu tinh, cũng không thành vấn đề."),t("br"),t("br"),n._v("Cũng đúng, công việc lâu như vậy, cả ngày bị nhốt, mỗi ngày đều đang nghiên cứu như thế nào thoát khốn, đối hồn phách lý giải, lại không mạnh mẽ, còn không bằng đập đầu chết quên đi."),t("br"),t("br"),n._v("“Nhìn xem có bao nhiêu thiếu hụt!”"),t("br"),t("br"),n._v("Ý  thức đắm chìm thức hải, đi tới Thiên Đạo thư viện, tinh thần khẽ động, đem đối phương truyền tới vô số tri thức toàn bộ sao chép tại trống không thư tịch bên trên."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Tiện tay đem liên quan tới linh hồn phân liệt thư tịch cầm tới, trực tiếp lật ra."),t("br"),t("br"),n._v("“Linh hồn phân liệt, Mặc Hồn Sinh sáng tạo, tu luyện về sau, linh hồn có thể một phân thành hai... Thiếu hụt:”"),t("br"),t("br"),n._v("Lít nha lít nhít viết đầy nội dung."),t("br"),t("br"),n._v("“Chỉ có một trăm hai mươi chỗ thiếu hụt, xem như rất ít đi!”"),t("br"),t("br"),n._v("Đem nội dung nhìn xong, Trương Huyền nhịn không được tán thưởng."),t("br"),t("br"),n._v("Chỉ bằng vào lực lượng cá nhân, sáng chế một cái chỉ có chừng một trăm chỗ thiếu hụt công pháp, trước mắt cái này vu hồn sư trải qua mặc dù không thế nào đáng tin cậy, thực lực cùng thiên phú, lại là thực sự."),t("br"),t("br"),n._v("Cũng đúng, không có thiên phú, lại thế nào khả năng một đời không qua hết, liền thành liền Hóa Phàm đỉnh phong, lục tinh vu hồn sư."),t("br"),t("br"),n._v("“Chính xác!”"),t("br"),t("br"),n._v("Tinh thần khẽ động, rất nhiều vu hồn sư thư tịch dung hợp lại cùng nhau, một lần nữa sinh thành linh hồn phân liệt bí tịch."),t("br"),t("br"),n._v("Lần nữa lật xem."),t("br"),t("br"),n._v("Lúc này bên trong thiếu hụt cũng chỉ có hơn mười chỗ."),t("br"),t("br"),n._v("“Xem ra liên quan tới linh hồn thư tịch vẫn là sưu tập ít, đợi đi đến Vạn Quốc thành, tìm thêm người mua sắm một chút!”"),t("br"),t("br"),n._v("Đối với phân liệt linh hồn có thể bảo mệnh công pháp, hắn vẫn là mười phần ý động."),t("br"),t("br"),n._v("Người tu luyện, ai cũng không dám cam đoan gặp được dạng gì nguy hiểm, môn công pháp này thật có thể tu luyện thành công, đối với hắn đến, mức độ nguy hiểm tuyệt đối sẽ giảm mạnh."),t("br"),t("br"),n._v("Bất quá, môn công pháp này thiếu hụt quá nhiều, đối với hắn đến, tạm thời còn không có hứng thú quá lớn, coi như thật muốn tu luyện, cũng muốn sưu tập càng nhiều thư tịch, triệt để bổ túc lại."),t("br"),t("br"),n._v("“Nhìn xem Cửu Tâm liên!”"),t("br"),t("br"),n._v("Lại nghiên cứu một hồi, đối phương truyền tới tri thức, biết một ngày hai ngày không cách nào học được, Trương Huyền cũng không vội vã, mở to mắt, rơi vào trước mắt màu đen hoa sen bên trên."),t("br"),t("br"),n._v("Thứ này mệnh danh thần vật, hắn cũng muốn biết, đến cùng có gì công hiệu."),t("br"),t("br"),n._v("Ngón tay tới tiếp xúc, trong đầu thở nhẹ “Thiếu hụt”."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Một quyển sách xuất hiện ở trước mắt."),t("br"),t("br"),n._v("“Cửu Thiên Liên Thai, thiên ngoại rơi xuống, Mặc Hồn Sinh đoạt được, cần tinh thuần nhất lực lượng tẩm bổ, hoàn toàn chín muồi về sau, có thể luyện chế hoàn mỹ thân thể. Khuyết điểm: Một, còn chưa đạt tới hoàn thiện trạng thái, hoàn toàn chín muồi, toàn thân trắng bệch...”"),t("br"),t("br"),n._v("“Nguyên lai không gọi Cửu Tâm liên, mà là Cửu Thiên Liên Thai! Luyện chế thân thể ngược lại là chính xác!”"),t("br"),n._v("Nhìn tờ thứ nhất, Trương Huyền gật đầu."),t("br"),t("br"),n._v("Vốn là hắn thật đúng là cho rằng gọi Cửu Tâm liên, hiện tại xem ra, mặc dù bị nhốt vài vạn năm, Mặc Hồn Sinh cái tên này, vẫn là không nhận ra được."),t("br"),t("br"),n._v("Cửu Thiên Liên Thai... Mặc dù thư tịch bên trên không ghi chú cụ thể cấp bậc, nhưng xem ra cũng không thấp."),t("br"),t("br"),n._v("“Thế mà chỉ có năm nơi thiếu hụt!”"),t("br"),t("br"),n._v("Lật ra thứ hai vậy. Đang muốn tiếp tục xem tiếp, Trương Huyền đột nhiên sững sờ."),t("br"),t("br"),n._v("Thiếu hụt ghi chép, thế mà chỉ có hai trang, năm cái."),t("br"),t("br"),n._v("Ít như vậy thiếu hụt bảo bối, từ khi lấy được thư viện, tuyệt đối là lần thứ nhất nhìn thấy."),t("br"),t("br"),n._v("“Không hổ là thần vật...”"),t("br"),t("br"),n._v("Con mắt quyết liệt, đột nhiên một cái ý nghĩ xông ra: “Nếu như học xong linh hồn phân liệt, chia ra một cái phân thân, đem thứ này luyện hóa làm, chẳng phải tương đương thêm ra một cái mạng?”"),t("br"),t("br"),n._v("Càng nghĩ càng kích động."),t("br"),t("br"),n._v("Đây chính là chỉ có năm nơi thiếu hụt thần vật, đem luyện hóa thành phần thân, có thể tưởng tượng tuyệt đối sẽ trở nên phi thường cường đại."),t("br"),t("br"),n._v("“Chỉ tiếc... Ta chỉ sợ không sống tới cái đồ chơi này hoàn thiện...”"),t("br"),t("br"),n._v("Thư tịch ghi lại cái thứ nhất thiếu hụt, chính là cái này gốc Cửu Thiên Liên Thai, còn chưa hoàn thiện."),t("br"),t("br"),n._v("Hoàn toàn chín muồi Cửu Thiên Liên Thai toàn thân trắng bệch, mà không phải trước mắt cái này đen như mực bộ dáng."),t("br"),t("br"),n._v("Thôn phệ hết Mặc Hồn Sinh toàn bộ gia sản, lại còn sống vài vạn năm, còn bộ dáng này, dùng hắn mấy trăm năm tuổi thọ, khẳng định là sống không đến thành thục."),t("br"),t("br"),n._v("Không thành thục, liền không cách nào luyện hóa, cưỡng ép đi làm, làm không cẩn thận liền sẽ cùng cái này Mặc Hồn Sinh đồng dạng, bị nhốt trong đó, muốn chết cũng không xong."),t("br"),t("br"),n._v("“Ừm? Thứ này cần tinh thuần nhất lực lượng tẩm bổ, mới có thể hoàn thiện, ta Thiên Đạo chân khí, tinh thuần không gì sánh được, có thể hay không xem như chất dinh dưỡng?”"),t("br"),t("br"),n._v("Trong lòng hơi động, một cái ý nghĩ xông ra."),t("br"),t("br"),n._v("Liên quan tới cái này gốc Cửu Thiên Liên Thai miêu tả, đoạn thứ nhất chính là cần tinh thuần nhất lực lượng tẩm bổ, hắn hiện tại tìm không thấy đầy đủ linh thạch, nhưng Thiên Đạo chân khí, lại là tinh thuần không gì sánh được, có lẽ có thể thử một chút."),t("br"),t("br"),n._v("Nắm liên thai rễ cây, một đạo chân khí rót vào đi vào."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Chân khí mới đi vào, liền nghe đến một hồi như giết heo kêu thảm."),t("br"),t("br"),n._v("“Đừng có giết ta...”"),t("br"),t("br"),n._v("Mặc Hồn Sinh sắp điên rồi, vừa đem liên quan tới vu hồn sư chỉ là truyền lại đi qua, đối phương liền truyền vào chân khí đi vào, đổi lại ai, chỉ sợ đều sẽ cho rằng đối phương muốn đem hắn chém giết."),t("br"),t("br"),n._v("“Ây...”"),t("br"),t("br"),n._v("Trương Huyền vội vàng ngừng lại."),t("br"),t("br"),n._v("Chỉ mới nghĩ lấy chân khí tẩm bổ, không nghĩ tới cái tên này còn tại bên trong."),t("br"),t("br"),n._v("Vu hồn đối thiên đạo chân khí có tự nhiên e ngại, lại thêm cái tên này bị Long lân hộ thân phù đánh chỉ còn lại có nửa cái mạng, như thế nào chống đỡ được chân khí công kích."),t("br"),t("br"),n._v("Không có bị trực tiếp gạt bỏ, đã coi như là không tệ."),t("br"),t("br"),n._v("Đình chỉ chân khí truyền vào, Trương Huyền đang định đem cái này gốc hoa sen, bỏ vào trữ vật giới chỉ, về sau lại nghiên cứu, đột nhiên nhìn thấy vừa mới truyền vào chân khí rễ cây, thế mà đã trắng ra."),t("br"),t("br"),n._v("“Trắng ra? Chẳng lẽ Thiên Đạo chân khí thật có hiệu quả?”"),t("br"),t("br"),n._v("Ánh mắt sáng lên, không thể tin được phía dưới, lần nữa cầm lấy từng cây thân, lại một đạo chân khí truyền vào đi vào."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Tiếp theo hoa sen bên trong lại truyền tới cực kỳ bi thảm la lên."),t("br"),t("br"),n._v("Một lát sau, cầm lấy hoa sen."),t("br"),t("br"),n._v("“Quả nhiên lại trắng, Thiên Đạo chân khí quả nhiên có thể để cho thứ này mau chóng hoàn thiện...”"),t("br"),t("br"),n._v("Nhìn thấy vừa liếc từng cây thân, Trương Huyền tràn đầy hưng phấn, nhịn không được an ủi một câu: “Ta không phải muốn giết ngươi, chỉ là thử một chút cùng cái này gốc Cửu Tâm liên, chớ khẩn trương, ta thử lại một chút, liền một chút...”"),t("br"),t("br"),n._v("“Không muốn, ah...” Mặc Hồn Sinh khóc."),t("br"),t("br"),n._v("Ta về sau ngoan ngoãn nghe lời còn không được ư? Ngươi thử thêm vài lần, chỉ sợ cũng không cần đem ta làm đi ra, ta liền sớm treo..."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bên ngoài sơn động."),t("br"),t("br"),n._v("“Đã xảy ra chuyện gì? Chúng ta có nên đi vào hay không nhìn xem?”"),t("br"),t("br"),n._v("“Hình như là Mặc Hồn Sinh kêu thảm, Trương sư không có sao chứ?”"),t("br"),t("br"),n._v("Nghe được kêu thảm, đang dùng cơm mọi người, tất cả đều đứng dậy."),t("br"),t("br"),n._v("“Trương sư vừa rồi bàn giao, có chuyện muốn đi nghiên cứu, chúng ta tốt nhất đừng quấy rầy, ta xem không bằng... Trước nghe một chút đến cùng chuyện gì xảy ra, mới quyết định đi!”"),t("br"),t("br"),n._v("Triệu Phi Vũ hơi ổn trọng chút, khuyên nhủ mọi người."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Mọi người gật gật đầu, lỗ tai dựng thẳng lên, hướng trong sơn động nghe đi qua, ngay sau đó liền nghe đến có âm thanh loáng thoáng truyền đến."),t("br"),t("br"),n._v("“... Chớ khẩn trương, ta thử lại một chút, liền một chút...”"),t("br"),t("br"),n._v("“Không muốn, ah...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("“Thử lại một chút? Liền một chút? Còn chớ khẩn trương?”"),t("br"),t("br"),n._v("Mọi người hai mặt nhìn nhau, từng cái con mắt trợn tròn."),t("br"),t("br"),n._v("Triệu Phi Vũ càng là vẻ mặt đỏ lên, nhẫn nhịn nửa ngày, mới ra lời nói tới."),t("br"),t("br"),n._v("“Hai người bọn họ... Đều là nam nhân ah...”"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-522"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-524"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);