(window.webpackJsonp=window.webpackJsonp||[]).push([[1772],{2127:function(n,h,i){"use strict";i.r(h);var t=i(44),c=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-517-cung-ran-cuop"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-517-cung-ran-cuop"}},[n._v("#")]),n._v(" Chương 517: Cứng rắn cướp")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("“Chỉ là... Những địa phương này dược vật, cơ bản đều là hong khô hoặc là đã phơi nắng tốt, mới mẻ còn có thể tiếp tục sống sót, cũng không có bao nhiêu, chớ nói chi là Trầm Tình hoa loại này kỳ quái dược vật!” Triệu minh chủ nói tiếp."),i("br"),i("br"),n._v("Cái này ba chỗ dược liệu nhiều nhất địa phương, hong khô dược vật có đồng dạng hiệu quả, hơn nữa tốt hơn mang theo, bởi vậy, cơ bản đều là phơi nắng tốt, không có khả năng có mới mẻ hàng tồn."),i("br"),i("br"),n._v("“Dược Vương dược liệu lai lịch, có mấy cái, thường thấy nhất chính là dược nông bán, nhưng Trầm Tình hoa loại này hiếm thấy dược liệu, bình thường dược nông khẳng định là sẽ không trồng trọt, hơn nữa cũng không cái năng lực kia. Ta duy nhất biết có loại năng lực này, là nằm ở Vạn Quốc thành thành tây một cái dược viên. Nơi này trúng vô số cổ quái kỳ lạ dược vật, rất nhiều trên thị trường khó mà nhìn thấy trân quý dược liệu đều có thể tìm tới, chỉ là...”"),i("br"),i("br"),n._v("Triệu minh chủ cười khổ: “Dược viên chủ nhân, tính cách cổ quái, có chút bất cận nhân tình, nàng dược, chưa từng bán ra, dù là cho nhiều tiền hơn nữa đều không được. Liền xem như ta, lúc trước Liên Nhi bệnh nặng, muốn mua mấy bụi đặc thù dược vật, đều không có thành công!”"),i("br"),i("br"),n._v("“Chưa từng bán ra?” Trương Huyền tràn đầy kỳ quái: “Cái kia nàng loại nhiều như vậy dược liệu làm gì?”"),i("br"),i("br"),n._v("Loại dược liệu hoặc là bản thân dùng, hoặc là bán ra, trồng rất nhiều, rõ ràng là bản thân sử dụng... Có thể lại không bán ra, giữ lại làm gì?"),i("br"),i("br"),n._v("Dược vật cùng phổ thông thực vật đồng dạng, cũng có sinh mệnh hưng suy, không ít dược liệu, chỉ có ngắn ngủi mấy năm tuổi thọ, có thể đạt tới mấy trăm năm, dù sao chỉ là số ít."),i("br"),i("br"),n._v("Hơn nữa mấu chốt nhất là, trồng trọt dược vật, cần tốn hao cực lớn đại giới, linh thạch, đặc thù dịch dinh dưỡng, rất Chí Nhân tay, thiếu một thứ cũng không được, một mực không bán ra, tương đương với một mực vào bên trong đập tiền, lợi hại hơn nữa thân gia, cũng không đủ chơi đùa ah!"),i("br"),i("br"),n._v("“Ta cũng không biết, Vạn Quốc thành không ít người cũng kỳ quái, đáng tiếc, lại không người biết!”"),i("br"),i("br"),n._v("Triệu minh chủ lắc đầu."),i("br"),i("br"),n._v("“Cái kia... Chẳng lẽ không có người đi đoạt?” Trương Huyền nghi hoặc."),i("br"),i("br"),n._v("Đã rất nhiều người đều muốn dược liệu, lại mua không được, chắc chắn sẽ có người bí quá hoá liều đi!"),i("br"),i("br"),n._v("“Đương nhiên là có, nhưng đều thất bại! Dược viên chủ nhân Tiết Nhất Dao, năm mươi năm trước liền đạt đến Hóa Phàm tứ trọng đỉnh phong, danh chấn Vạn Quốc thành. Hiện tại đạt đến cái tình trạng gì, ai cũng chẳng biết, e là cho dù là ta, đều chưa hẳn có thể chiến thắng!” Triệu minh chủ nói."),i("br"),i("br"),n._v("Năm mươi năm trước Hóa Phàm tứ trọng đỉnh phong, coi như không cách nào đột phá, chỉ sợ cũng xếp tại Vạn Quốc thành vô số cường giả phía trước nhất."),i("br"),i("br"),n._v("“Càng quan trọng hơn là, dược viên còn có cực mạnh trận pháp, ít nhất đạt đến tứ tinh đỉnh phong, do Hồng hải trận pháp công hội Trịnh hội trưởng, tự tay bố trí, không ít mưu toan trộm dược người, còn không có tiến vào bên trong, liền bị giết chết hoặc là vây khốn... Dần dà, người người đều biết cái chỗ kia nguy hiểm, cũng là lại không người dám đi!”"),i("br"),i("br"),n._v("Triệu minh chủ nói."),i("br"),i("br"),n._v("Nuôi vô số trân quý dược liệu, nhưng không bán ra, dám tới thuốc xổ tài chủ ý, đều bị chém giết hoặc là vây khốn... Cái tên này quả thực rất khó đối phó."),i("br"),i("br"),n._v("“Địa phương khác nhưng còn có hai loại dược liệu?”"),i("br"),i("br"),n._v("“Không có, loại trừ dươc viên này, thật không biết địa phương khác chỗ nào có thể tìm tới, trừ phi là đã sấy chế xong!”"),i("br"),i("br"),n._v("“Sấy chế xong đối ta vô dụng!” Trương Huyền lắc đầu."),i("br"),i("br"),n._v("Bồi dưỡng Tỉnh Hồn thảo, chỉ có thể lợi dụng mới mẻ còn tại sinh trưởng dược vật, một khi phơi khô, sấy chế thành có thể phục dụng dược liệu, như thế nào lại dung hợp linh hồn, nắm giữ tỉnh hồn hiệu quả?"),i("br"),i("br"),n._v("“Ngươi nói dươc viên này ở nơi nào, ta muốn đi qua nhìn một chút!”"),i("br"),i("br"),n._v("Tỉnh Hồn thảo nhất định phải có, như vậy mới có thể cứu tỉnh Lộ Xung, liền Vu hồn mộ chỗ nguy hiểm như vậy đều đi, coi như dược viên chủ nhân lại khó quấn, chắc hẳn cũng hầu như có thể tìm tới biện pháp."),i("br"),i("br"),n._v("“Cái này...” Nghe hắn muốn đi qua, Triệu minh chủ chần chờ một chút: “Vậy được rồi, ta để Kim Tòng Hải cùng, nếu như muốn ra tay với ngươi, cũng có thể chống lại một chút!”"),i("br"),i("br"),n._v("“Tốt!” Trương Huyền gật đầu."),i("br"),i("br"),n._v("Cái kia dược viên vị trí, hắn cũng không biết, cần phải có người dẫn đường."),i("br"),i("br"),n._v("“Phụ vương, ta cũng đi qua nhìn một chút!” Triệu Phi Vũ cười một tiếng, vội nói."),i("br"),i("br"),n._v("“Ngươi đi qua làm gì?” Triệu minh chủ lông mày nhíu một cái."),i("br"),i("br"),n._v("“Dươc viên này chủ nhân liền lời của ngươi đều không nghe, ta cũng muốn đi xem xem, đến cùng có bao nhiêu lợi hại!” Triệu Phi Vũ nhẹ nhàng cười một tiếng."),i("br"),i("br"),n._v("Một đường theo sau lưng, biết vị này Trương Huyền, là cái không đạt tới mục đích không dừng tay chủ, mà đối phương nghe càng thêm khó chơi, nàng thật muốn nhìn xem hai bên giao phong, lại là cái gì kết quả."),i("br"),i("br"),n._v("“Yên tâm đi, có Kim lão tại, lại thêm thân phận của ta, vị này dược viên chủ nhân coi như lại vô lễ, cũng sẽ không động thủ!” Gặp cha còn tại chần chờ, Triệu Phi Vũ vội nói."),i("br"),i("br"),n._v("“Được rồi, các ngươi hai cái đều muốn cẩn thận, nhất là Trương sư, nếu như đối phương không nguyện ý, tuyệt đối không nên cưỡng cầu, thực sự không được, ta tự mình đi qua một chuyến, có lẽ có thể bán ta một bộ mặt.” Chần chờ một chút, Triệu minh chủ nói."),i("br"),i("br"),n._v("Lúc trước cho Triệu Phi Vũ xin thuốc, gặp phải cự tuyệt về sau, bản thân muốn đi, về sau Y Sư công hội tại địa phương tìm được dược vật thay thế, cũng là coi như không có gì."),i("br"),i("br"),n._v("Trương sư là nữ nhi ân nhân cứu mạng, lại thêm thiên phú như vậy, về sau thành tựu vô hạn, không thể có sự tình, thực sự không được, cũng chỉ có thể tự thân xuất mã."),i("br"),i("br"),n._v("Hắn đường đường vạn quốc liên minh minh chủ, Hóa Phàm tứ trọng đỉnh phong nhất nhân vật, thật muốn mua sắm dược vật, muốn đến đối phương coi như tính khí lại trách, cũng sẽ cho hơn mấy phần chút tình mọn."),i("br"),i("br"),n._v("Lại kỹ càng hỏi thăm vài câu, Trương Huyền lập tức cùng Triệu minh chủ cáo từ, mang theo Triệu Phi Vũ cùng Kim Tòng Hải, cưỡi phi hành man thú, trực tiếp hướng thành tây bay đi."),i("br"),i("br"),n._v("Vạn Quốc thành cực lớn, hoành chiều rộng khoảng cách ít nhất mấy trăm cây số, chỉ bằng vào đi qua, không có mấy canh giờ không thể nào làm được, ba người lần này không cưỡi Triệu Phi Vũ tọa kỵ, mà là ngồi ở Yêu Thần thú trên lưng."),i("br"),i("br"),n._v("Hóa Phàm tứ trọng đỉnh phong yêu thú, hai cánh mở ra, tựa như sấm sét, mấy chục phút về sau, ba người liền thấy một cái cực lớn trang viên xuất hiện ở trước mắt."),i("br"),i("br"),n._v("Trang viên chiếm diện tích mấy trăm mẫu, bị bao phủ tại nhàn nhạt trong sương mù, vừa nhìn liền biết, có đại trận thủ hộ."),i("br"),i("br"),n._v("“Ngay ở chỗ này xuống đi!”"),i("br"),i("br"),n._v("Biết không có khả năng bay thẳng đi vào, Trương Huyền gật đầu, Yêu Thần thú một tiếng kêu khẽ, tại cửa chính chỗ không xa, rơi xuống."),i("br"),i("br"),n._v("Trước mắt trang viên, đại môn đóng chặt, ngoài cửa một bóng người đều không có, xem ra tất cả mọi người cũng đều biết nơi này dược liệu không cách nào mua sắm, không có hứng thú."),i("br"),i("br"),n._v("Nếu không, hàng năm cần dược liệu người, không phải số ít, không có khả năng trước cửa có thể giăng lưới bắt chim, liền cái bóng người đều nhìn không thấy."),i("br"),i("br"),n._v("Kim Tòng Hải đi lên phía trước, đang muốn gõ cửa, liền nghe đến “Kẹt kẹt!” Một tiếng, cửa sân từ bên trong mở ra, một cái thị nữ bộ dáng người tiến lên đón."),i("br"),i("br"),n._v("“Chúng ta dược viên không đối ngoại bán ra dược vật, mấy vị mời trở về đi!”"),i("br"),i("br"),n._v("Xem ra nàng đã thành thói quen đến đây xin thuốc người, chỉ cần nhìn thấy, liền sẽ trực tiếp mở lời."),i("br"),i("br"),n._v("Không nghĩ tới, lời nói đều không có nói, liền gặp phải cự tuyệt, Trương Huyền có chút bất đắc dĩ."),i("br"),n._v(" Cửa còn không thể nào vào được, chính chủ gặp không đến, còn như thế nào mua thuốc?"),i("br"),i("br"),n._v("Đang muốn nên nói cái gì, làm cho đối phương cho đi, chí ít nhìn thấy dược viên chủ nhân, mới có thể lừa dối, liền nghe đến một hồi tiếng vó ngựa truyền đến."),i("br"),i("br"),n._v("Ngay sau đó, liền thấy một chiếc xe ngựa dừng ở cách đó không xa, một người trung niên đi xuống."),i("br"),i("br"),n._v("“Mộc viên chủ mời vào!”"),i("br"),i("br"),n._v("Nhìn người nọ, thị nữ nhận ra được, vẻ mặt vui mừng, vội vàng tránh ra thông đạo, để hắn đi vào."),i("br"),i("br"),n._v("“Ngươi không nói không bán ra dược vật ư? Hắn vì cái gì có thể đi vào?” Kim Tòng Hải nhướng mày."),i("br"),i("br"),n._v("Đây cũng quá rõ ràng đi, nhóm người mình còn chưa lên tiếng liền bị cự tuyệt ở ngoài cửa, cái tên này nhưng nghênh ngang mà vào."),i("br"),i("br"),n._v("Chẳng lẽ cũng phải nhìn người?"),i("br"),i("br"),n._v("“Thực sự thật không tiện, hắn là nhà chúng ta chủ nhân chuyên môn mời tới, có thư mời!”"),i("br"),i("br"),n._v("Thị nữ mặt mũi tràn đầy áy náy."),i("br"),i("br"),n._v("“Có thư mời mới có thể đi vào?” Trương Huyền nghi hoặc."),i("br"),i("br"),n._v("“Đương nhiên là có thư mời mới có thể đi vào!” Thị nữ vẫn chưa trả lời, một âm thanh lãnh ngạo liền vang lên, nói chuyện chính là cái dáng người yêu diễm nữ tử, chừng ba mươi tuổi bộ dáng, hóa thành nùng trang, còn chưa tới đến trước mặt, liền cho người ta một cỗ nồng đậm son phấn vị."),i("br"),i("br"),n._v("“Hôm nay Tiết viên chủ cử hành tiệc tối, chỉ có cái khác dược viên viên chủ mới có thể tiến nhập, không phải cái gì a miêu a cẩu đều có thể tùy ý tới, muốn mua dược liệu liền trực tiếp đi thôi, nơi này không chào đón!”"),i("br"),i("br"),n._v("Cổ giương lên, yêu diễm nữ tử tràn đầy cao ngạo, nhìn qua ánh mắt mang theo xem thường lạnh lùng."),i("br"),i("br"),n._v("“Tiệc tối? Dược viên viên chủ mới có thể tiến nhập? Nói như vậy lên, ngươi cũng là viên chủ?” Triệu Phi Vũ nói."),i("br"),i("br"),n._v("“Cái kia là đương nhiên! Ta là Thanh Yên dược viên viên chủ, la khói xanh!”"),i("br"),i("br"),n._v("Vẻ mặt tự ngạo, yêu diễm nữ tử cổ tay khẽ đảo, lấy ra một vật, chính là đi vào dược viên thư mời."),i("br"),i("br"),n._v("“Thấy không, đây là thư mời, không có thứ này liền lập tức rời đi, không muốn làm loạn!”"),i("br"),i("br"),n._v("Yêu diễm nữ tử lạnh lùng nhìn qua, một tiếng cười nhạo: “Xem ngươi niên kỷ, hai mươi cũng chưa tới đi, như ngươi loại này tuổi tác, liền dược liệu là cái gì đều không phân biệt được, loại này cấp bậc cao yến hội, liền nhìn cũng không có tư cách nhìn một chút, vẫn là trở về đi! Về sau học tập cho giỏi, có lẽ ba, năm mươi năm về sau, có tư cách đi vào một lần... Lạc lạc lạc lạc!”"),i("br"),i("br"),n._v("Che miệng cười khẽ, yêu diễm nữ tử, thân thể một cái, sung mãn song bóng, dường như muốn thoát ly quần áo trói buộc, bất cứ lúc nào cũng sẽ tránh thoát ra ngoài."),i("br"),i("br"),n._v("“Ba, năm mươi năm?” Trương Huyền lắc đầu, nhìn về phía cách đó không xa giữ cửa thị nữ: “Có phải hay không có thư mời liền có thể đi vào?”"),i("br"),i("br"),n._v("“Ây... Vâng!”"),i("br"),i("br"),n._v("Thị nữ do dự một chút, nhớ tới trước đó bàn giao, liền vội vàng gật đầu."),i("br"),i("br"),n._v("“Vậy là tốt rồi!”"),i("br"),i("br"),n._v("Nghe được đối phương chính xác trả lời, Trương Huyền nhìn về phía yêu diễm nữ tử, gãi đầu một cái, có chút xấu hổ: “Thương lượng với ngươi chút chuyện, ngươi thư mời, có thể hay không ta mượn dùng một chút?”"),i("br"),i("br"),n._v("“Mượn ngươi? Ha ha!”"),i("br"),i("br"),n._v("Giống như là nghe được êm tai nhất truyện cười, yêu diễm nữ tử nhìn về phía Trương Huyền, trong mắt tràn đầy chán ghét: “Dựa vào cái gì cho ngươi mượn? Đây là Tiết viên chủ cho ta, ngươi thì tính là cái gì?”"),i("br"),i("br"),n._v("“Không mượn?”"),i("br"),i("br"),n._v("Trương Huyền nhìn qua."),i("br"),i("br"),n._v("“Thừa dịp ta chưa nổi giận trước, cút ngay, nếu không, tin hay không để ngươi nằm trở về...”"),i("br"),i("br"),n._v("Một tiếng quát lớn, yêu diễm trên người nữ tử khí tức không tự chủ được chảy ra tới."),i("br"),i("br"),n._v("Chân khí thanh trọc rõ ràng, lực lượng kinh người."),i("br"),i("br"),n._v("Lại là vị Hóa Phàm tam trọng cường giả."),i("br"),i("br"),n._v("“Giết ta?” Lắc đầu, Trương Huyền nhìn về phía nơi xa, bàn giao một tiếng: “Yêu Thần, ngươi cùng với nàng thương nghị một chút, có thể hay không đem thư mời cho ta...”"),i("br"),i("br"),n._v("“Rống!”"),i("br"),i("br"),n._v("Nghe được mệnh lệnh, cách đó không xa Yêu Thần thú, một tiếng thấp giọng hô, “Sưu!” Bay tới."),i("br"),i("br"),n._v("“Ngươi muốn làm gì?”"),i("br"),i("br"),n._v("Yêu diễm nữ tử sững sờ, mắt tối sầm lại, chỉ thấy một cái cực lớn cái mông, đổ ập xuống đập xuống."),i("br"),i("br"),n._v("Phù phù!"),i("br"),i("br"),n._v("Chưa kịp phản ứng, bị Yêu Thần thú trực tiếp ngồi vào nơi đó, chỉ còn lại có hai tay hai chân cùng đầu bên ngoài, từng ngụm từng ngụm thổ huyết."),i("br"),i("br"),n._v("Đinh!"),i("br"),i("br"),n._v("Đem nó ngồi nằm xuống, Yêu Thần thú miệng vặn một cái, đem trong tay nàng huy chương ngậm chặt ném tới."),i("br"),i("br"),n._v("“...”"),i("br"),i("br"),n._v("Kim Tòng Hải, Triệu Phi Vũ đồng thời một cái lảo đảo, tròng mắt sắp rơi trên mặt đất."),i("br"),i("br"),n._v("Không phải nói muốn thương nghị sao? Thế nào... Dùng cái mông ngồi người..."),i("br"),i("br"),n._v("(Thiên Đạo thư viện thư hữu 1 quần đã đủ, muốn đến thảo luận, không muốn tiến hành lúc trước cái quần, xin thêm 2 nhóm: 434165507, chính bản đặt mua bằng hữu, xin thêm V quần tiếp đãi nhóm: 299735404, thông qua nghiệm chứng, là được đi vào Vip quần.)"),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-525"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-527"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=c.exports}}]);