(window.webpackJsonp=window.webpackJsonp||[]).push([[1774],{2129:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-519-duong-duoc-the-gia"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-519-duong-duoc-the-gia"}},[n._v("#")]),n._v(" Chương 519: Dưỡng dược thế gia")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 519: Dưỡng dược thế gia"),t("br"),t("br"),n._v("Tới vị thanh niên này lại là một vị... Tứ tinh danh sư!"),t("br"),t("br"),n._v("“La Thanh Yên, gặp qua Tống sư!”"),t("br"),t("br"),n._v("Nhìn thấy người thanh niên này, La Thanh Yên vội vàng xoay người ôm quyền, thái độ cung kính."),t("br"),t("br"),n._v("Nếu như Trương Huyền tại cái này, khẳng định có thể nhận ra, đang là lúc trước tại Hồng Hải thành, hướng hắn mua sắm Vô Hồn Kim Nhân, bị gài bẫy một số lớn linh thạch gia hỏa."),t("br"),t("br"),n._v("“Thanh Yên viên chủ đây là...” Nhìn xem vị này cao ngạo xinh đẹp viên chủ, toàn thân bùn đất, vẻ mặt chật vật, Tống Siêu tràn đầy nghi hoặc."),t("br"),t("br"),n._v("Lấy được Vô Hồn Kim Nhân, hắn liền lần Vạn Quốc thành, nhớ tới vị kia Trương sư chỉ điểm, không chút ngừng, liền đến các nơi dược viên mua sắm dược vật, bởi vậy, cùng vị này viên chủ từng có vài lần duyên phận."),t("br"),t("br"),n._v("Mỹ lệ làm rung động lòng người, câu nhân hồn phách, có thể nói vưu vật, trêu đến tâm hắn động không ngừng, thế nào bây giờ nhìn lại chật vật như thế, cùng mới vừa bị người cuồng ẩu đồng dạng?"),t("br"),t("br"),n._v("“Mong rằng Tống sư thay ta cầu xin!”"),t("br"),t("br"),n._v("Chớp mắt, La Thanh Yên mắt sáng lên, ngay sau đó giả ra nước mắt ướt át bộ dáng, liền muốn hạ bái."),t("br"),t("br"),n._v("“Cầu xin? Chuyện gì xảy ra?” Đưa tay ngăn lại, Tống Siêu kỳ quái nhìn qua."),t("br"),t("br"),n._v("“Vừa rồi có người muốn đi vào dược viên, ta nghĩ lấy Tiết viên chủ không thích người lạ bàn giao, ngăn trở một chút, ai ngờ... Liền bị hắn chỉ thị thú sủng đánh một trận!”"),t("br"),t("br"),n._v("La Thanh Yên tràn đầy mảnh mai cùng đáng thương: “Tống sư, ta thật không biết hắn là danh sư, sớm biết, khẳng định không dám ngăn trở, cũng không dám nhiều nói nửa câu nhiều lời...”"),t("br"),t("br"),n._v("“Cũng bởi vì ngăn cản một chút, liền để thú sủng đánh ngươi một chầu?” Tống Siêu lông mày nhăn lại."),t("br"),t("br"),n._v("“Kỳ thật không trách vị danh sư kia, hắn mặc dù không có mặc danh sư phục sức, cũng không cho thấy thân phận, nhưng ta hẳn là sớm hỏi một chút, không hỏi một tiếng liền để hắn chờ ở bên ngoài lấy... Mong rằng Tống sư hỗ trợ cầu xin, Thanh Yên lần sau cũng không dám nữa...”"),t("br"),t("br"),n._v("La Thanh Yên vội nói."),t("br"),t("br"),n._v("Trong miệng nàng nói là xin lỗi, trên thực tế nhưng cố ý đem bản thân vô lễ làm nhạt, tăng thêm đối phương bá đạo."),t("br"),t("br"),n._v("Cứ như vậy, cho người khác hình ảnh chính là cố tình gây sự, không sáng sáng thân phận ngược lại cũng thôi, còn cố ý đem nghiêm khắc cương vị công tác quy củ người, đánh đấm một trận túi bụi."),t("br"),t("br"),n._v("“Làm càn! Đường đường danh sư, không chú trọng dáng vẻ, thế mà tại loại trường hợp này, động thủ đánh người... Còn thể thống gì?”"),t("br"),t("br"),n._v("Quả nhiên, nghe được nàng chỉ tốt ở bề ngoài lời nói, Tống Siêu sầm mặt lại."),t("br"),t("br"),n._v("Danh sư, đại biểu cho Danh Sư đường, đi ra ngoài bên ngoài, muốn thường xuyên duy trì thân phận, như vậy làm xằng làm bậy, cùng ác bá khác nhau ở chỗ nào?"),t("br"),t("br"),n._v("“Yên tâm đi, chuyện này không trách ngươi, đợi chút nữa ta gặp được tên kia, nhất định thật tốt giáo huấn một lần, cho hắn biết như thế nào làm gương sáng cho người khác, như thế nào sư đức phong phạm! Để hắn xin lỗi ngươi!”"),t("br"),t("br"),n._v("Tống Siêu vung tay lên."),t("br"),t("br"),n._v("Đường đường danh sư, không có thư mời ngược lại cũng thôi, còn đánh người, không khỏi quá càn rỡ đi!"),t("br"),t("br"),n._v("“Đa tạ Tống sư!” Thấy đối phương tin tưởng lời của mình, còn muốn giúp nàng hả giận, La Thanh Yên hưng phấn con mắt tỏa ánh sáng, lần nữa nhìn về phía thanh niên, tràn đầy sùng bái."),t("br"),t("br"),n._v("Thấy được không, đây chính là tứ tinh danh sư Haki!"),t("br"),t("br"),n._v("Cũng chỉ có loại cấp bậc này danh sư, mới dám tại đối mặt cái khác danh sư thời điểm, trực tiếp mở lời nói muốn giáo huấn!"),t("br"),t("br"),n._v("Hừ, để ngươi vừa rồi phách lối, để thú sủng dùng cái mông ngồi ta, chờ một gặp được Tống sư, xem ngươi còn thế nào phách lối!"),t("br"),t("br"),n._v("Dưới cái nhìn của nàng, đối phương tuổi tác liền hai mươi cũng chưa tới, hơn nữa thực lực rõ ràng không bằng bản thân, liền xem như danh sư, nhị tinh cao nữa là, gặp được tứ tinh còn không lập tức cúi đầu khom lưng? Ngoan ngoãn nghe lời?"),t("br"),t("br"),n._v("“Ta vậy thì mang Tống sư đi tìm hắn!”"),t("br"),t("br"),n._v("Càng nghĩ càng hưng phấn, vội vàng phía trước dẫn đường."),t("br"),t("br"),n._v("“Ừm!” Tống Siêu gật gật đầu, hai tay chắp sau lưng, mang theo thay mỹ nhân trút giận tâm tư, theo sau."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Tại thị nữ dẫn đầu bên dưới, Trương Huyền đám người đi qua thật dài hành lang, đi tới một cái đại sảnh."),t("br"),t("br"),n._v("Giờ phút này trong phòng đã tới không ít người, lít nha lít nhít chừng hơn trăm cái nhiều."),t("br"),t("br"),n._v("Cái gọi là yến hội, còn chưa bắt đầu, những người này đều sáu, bảy cái vây tại một chỗ, dường như đang nói cái gì."),t("br"),t("br"),n._v("“Trương sư, đây chính là yến hội chỗ trên mặt đất, viên chủ một lát nữa sẽ đích thân tới chiêu đãi, ta liền đi xuống trước!”"),t("br"),t("br"),n._v("Thị nữ gật gật đầu, lui xuống."),t("br"),t("br"),n._v("Nếu như nói vừa rồi đối với người này ấn tượng là lỗ mãng, phách lối, không biết trời cao đất rộng,"),t("br"),t("br"),n._v("Nhưng giúp nàng chỉ điểm chứng bệnh, đồng thời cho ra phương pháp giải quyết về sau, đã triệt để bội phục, trở thành thần tượng."),t("br"),t("br"),n._v("“Ừm!” Biết nàng còn phải bảo vệ vườn cửa, Trương Huyền cũng không nhiều lời, khoát tay áo, hướng trong sảnh đi đến."),t("br"),t("br"),n._v("“Tiết viên chủ bình thường liền dược viên đều không cho chúng ta vào, thế nào lần này đột nhiên mời chúng ta tới? Có phải là có chuyện gì hay không, không có khả năng chỉ là yến sẽ đơn giản như vậy đi!”"),t("br"),t("br"),n._v("“Ngươi không biết?”"),t("br"),t("br"),n._v("“Ta trong mấy ngày qua một mực tại ở bên ngoài, hôm nay vừa trở về liền nhận được thư mời, làm sao có thể biết!”"),t("br"),t("br"),n._v("“Nghe nói là dược viên trấn vườn chi bảo được bệnh nặng, bất cứ lúc nào cũng sẽ tử vong, yến hội là giả, ý tứ chân chính, là muốn chúng ta những này viên chủ, liên hợp lại cùng nhau nghĩ biện pháp!”"),t("br"),t("br"),n._v("“Trấn vườn chi bảo? Ngươi nói chính là...”"),t("br"),n._v("“Xuỵt, nhỏ giọng một chút! Ta cũng là nghe nội bộ chảy ra tin tức, cụ thể chuyện gì xảy ra, cũng không rõ lắm!”"),t("br"),t("br"),n._v("“Đã như vậy, trực tiếp tìm người đến quản lý không phải tốt, vì sao phải khiến cho như thế thần thần bí bí, còn nói cái gì yến sẽ...”"),t("br"),t("br"),n._v("“Ta đây cũng không biết, Tiết viên chủ tính khí quái dị ngươi cũng không phải không biết, liên minh chủ phủ đều không để ý, nói như vậy, khẳng định có ý nghĩ của nàng...”"),t("br"),t("br"),n._v("“Như thế!”"),t("br"),t("br"),n._v("Đi trong đám người, ba người trong tai truyền đến đè thấp tiếng nói đối thoại."),t("br"),t("br"),n._v("“Trấn vườn chi bảo? Chẳng lẽ là...” Một bên Triệu Phi Vũ lông mày nhăn lại."),t("br"),t("br"),n._v("“Ngươi biết?” Gặp nàng nhớ tới cái gì, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Ta trước kia xem qua liên quan tới dược viên thư tịch, biết một chút!” Triệu Phi Vũ gật đầu, thấp giọng: “Hẳn là !”"),t("br"),t("br"),n._v("“Địa Mạch Linh Đằng?” Trương Huyền nghi hoặc."),t("br"),t("br"),n._v("Hắn mặc dù nhìn qua không ít sách, có thể liên quan tới dược viên, cũng không tiếp xúc, cái tên này, cũng là lần đầu tiên nghe nói."),t("br"),t("br"),n._v("“Ừm!” Triệu Phi Vũ gật gật đầu: “Đây cũng không phải là bí mật gì, chỉ cần có dược viên, cơ bản đều có thứ này. Địa Mạch Linh Đằng là một loại kỳ lạ thực vật, có thể phụ thuộc địa mạch sinh trưởng, đem hấp nhận được linh khí, khoáng vật chất, nguồn nước... Tụ lại, thoải mái thổ địa, hình thành thích hợp sinh trưởng các loại dược vật tuyệt thế ruộng tốt.”"),t("br"),t("br"),n._v("Bầy cừu một mực tại cùng một nơi nuôi thả, lại nồng đậm cỏ xanh cũng sẽ bị ăn sạch, dược liệu cũng là đồng lý."),t("br"),t("br"),n._v("Một chỗ sinh trưởng một gốc dược liệu còn tốt, sinh trưởng nhiều hơn, sẽ xuất hiện cạnh tranh, linh khí, nước, đặc thù khoáng vật chất, đều là tranh đoạt nội dung... Cạnh tranh nhiều hơn, liền sẽ cùng bị ăn sạch thảo nguyên đồng dạng, không có dinh dưỡng cung cấp, lợi hại hơn nữa dược liệu cũng sẽ trở nên khô héo."),t("br"),t("br"),n._v("Dược viên sở dĩ có thể liên tục không ngừng bán ra dược vật, kéo dài không suy, để tất cả dược liệu khỏe mạnh phát triển, chính là bởi vì thứ này tồn tại."),t("br"),t("br"),n._v("Một khi suy kiệt, tử vong, toàn bộ dược viên cũng chẳng khác nào hết rồi, ruộng tốt bên trên tất cả dược vật, đều sẽ hủy hoại chỉ trong chốc lát."),t("br"),t("br"),n._v("“Có Địa Mạch Linh Đằng tồn tại, ruộng tốt bên trong sinh trưởng dược liệu, chẳng những nhan sắc, kích cỡ, đều so phổ thông địa phương tốt hơn nhiều, càng quan trọng hơn là, sinh trưởng tốc độ cũng muốn mau hơn không ít, một chút trong truyền thuyết năm trăm năm dược liệu, ở đây, hai trăm năm liền có thể cung cấp. Nếu không, ai có thể sống năm trăm năm, chỉ vì trồng trọt một gốc dược liệu?”"),t("br"),t("br"),n._v("Triệu Phi Vũ nói."),t("br"),t("br"),n._v("“Thì ra là thế!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Trước đó cũng có chút hiếu kỳ, linh dược không phải cây lúa mạch, một loại một nhóm lớn đều vô sự, thứ này đối với linh khí cùng các loại dinh dưỡng nhu cầu cực lớn, coi như dược viên thổ chất lại phì nhiêu, chỉ sợ cũng rất khó cung cấp cho nhiều như vậy không đồng loại loại."),t("br"),t("br"),n._v("Nguyên lai còn có loại vật này tồn tại."),t("br"),t("br"),n._v("“Nếu như bọn hắn nói là thứ này, khó trách Tiết viên chủ sốt ruột, không có Địa Mạch Linh Đằng trung hoà điều tiết, tập trung trồng trọt dược vật, sẽ rất nhanh khô héo...” Triệu Phi Vũ tiếp tục nói."),t("br"),t("br"),n._v("Cái này rất giống điều tiết linh dược sinh trưởng trận pháp, một khi hủy đi, các loại dược vật nhu cầu, sẽ xuất hiện hỗn loạn, không có đầy đủ chất dinh dưỡng cung cấp, dược liệu không khô héo mới là lạ."),t("br"),t("br"),n._v("“Ừm!” Trương Huyền gật gật đầu, đang muốn nói chuyện, liền nghe đến một cái thanh âm nhiệt tình tại cách đó không xa vang lên."),t("br"),t("br"),n._v("“Huynh đệ, ngươi cũng là đi theo trong nhà trưởng bối tới tham gia yến hội?”"),t("br"),t("br"),n._v("Nói chuyện chính là cái khuôn mặt hòa ái thanh niên, một cười lên trên mặt hai cái lúm đồng tiền, có vẻ hơi đáng yêu."),t("br"),t("br"),n._v("“Trưởng bối?” Trương Huyền sững sờ."),t("br"),t("br"),n._v("“Đúng a, có thể nhận được thư mời, loại trừ dược viên viên chủ, có trách nhiệm bán mua thuốc vật Dược Vương, chính là dưỡng dược thế gia người, Vạn Quốc thành bát đại viên chủ, thất Đại Dược Vương, ta toàn bộ gặp qua, nhưng chưa thấy qua ngươi, tự nhiên là cùng trưởng bối cùng đi đến!”"),t("br"),t("br"),n._v("Thanh niên cười khanh khách nói."),t("br"),t("br"),n._v("“Dưỡng dược thế gia?” Trương Huyền có chút không rõ."),t("br"),t("br"),n._v("Dược viên viên chủ hắn biết, bán ra dược liệu Dược Vương cũng rõ ràng, dưỡng dược thế gia là cái gì?"),t("br"),t("br"),n._v("“Ngươi không biết dưỡng dược thế gia?” Thấy hắn vẻ mặt mê hoặc, thanh niên trừng to mắt, tràn đầy không thể tưởng tượng nổi."),t("br"),t("br"),n._v("Đây là dược viên cơ sở nhất đồ vật, ngay cả điều này cũng không biết, thế nào trà trộn vào tới?"),t("br"),t("br"),n._v("“Dược liệu bồi dưỡng, cần chuyên gia chăm sóc, mà chúng ta dưỡng dược thế gia, chính là hướng dược viên phát ra loại này dưỡng dược người gia tộc!” Thanh niên giải thích một câu."),t("br"),t("br"),n._v("Có hoa màu, liền có nông dân. Có bầy cừu, liền có người chăn cừu... Đồng lý, có dược liệu, tự nhiên cũng là có dưỡng dược người."),t("br"),t("br"),n._v("Dược liệu cấp bậc càng cao, càng khó bồi dưỡng, cùng thuần thú, luyện đan đồng dạng, là một loại mười phần rườm rà phức tạp học vấn, muốn bồi dưỡng một gốc lợi hại dược vật, không chỉ cần phải hao phí thời gian, càng quan trọng hơn là nhân lực vật lực, còn có đủ loại kinh nghiệm."),t("br"),t("br"),n._v("Bồi dưỡng quá trình bên trong, sai bên trên một bước, dược liệu không cách nào đạt tới hoàn mỹ cấp bậc là chuyện nhỏ, làm không cẩn thận sẽ còn khô héo, tử vong!"),t("br"),t("br"),n._v("Dùng Hóa Phàm dược vật  làm thí dụ, loại dược liệu này, cần tấm thân xử nữ nữ tử, đổ vào mới có thể sống sót, một khi nữ tử này cùng cái khác nam tử có quan hệ, tưới nước nhỏ lên, cả cây dược liệu sẽ lập tức hóa thành một đoàn khói đặc, triệt để tiêu tán."),t("br"),t("br"),n._v("Khanh Thủ thảo, cùng khanh tướng thủ, chính là bởi vì loại này đặc tính, thứ này cũng bị dùng để kiểm nghiệm nữ tử phải chăng hoàn bích."),t("br"),t("br"),n._v("Khúc duyệt hoa, nở hoa phía sau trạng thái như mắt người, muốn sống sót, cần phải có người thời khắc ở bên cạnh khiêu vũ ca hát, dùng khúc vì duyệt, nếu không, chẳng mấy chốc sẽ tử vong."),t("br"),t("br"),n._v("Thiên hạ còn có vô số cổ quái kỳ lạ dược liệu, không có hệ thống truyền thừa tri thức, liền muốn bồi dưỡng, khó như lên trời."),t("br"),t("br"),n._v("Chính là bởi vì có truyền thừa, mới có chuyên môn bồi dưỡng dưỡng dược người dưỡng dược thế gia."),t("br"),t("br"),n._v("Năm đó, Danh Sư đường xác lập chức nghiệp, từng đề cập qua dưỡng dược người, đáng tiếc, cái nghề nghiệp này, truyền thừa hỗn loạn, không có thống nhất lưu phái. Mấu chốt nhất là, đối tu vi không có quá đại yếu cầu, chỉ cần tri thức uyên bác là đủ."),t("br"),t("br"),n._v("Bởi vậy, cuối cùng không trở thành đặc thù chức nghiệp, nếu không, hạ cửu lưu rất nhiều ngành nghề bên trong, tất nhiên có hắn vị trí."),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-527"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-529"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);