(window.webpackJsonp=window.webpackJsonp||[]).push([[1775],{2130:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-520-kho-long-phong-bi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-520-kho-long-phong-bi"}},[n._v("#")]),n._v(" Chương 520: Khó lòng phòng bị")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Chương 520: Khó lòng phòng bị"),t("br"),t("br"),n._v("“Ta tùy tiện tới đi dạo, cũng không phải là đi theo người nào!”"),t("br"),t("br"),n._v("Rõ ràng chuyện gì xảy ra, Trương Huyền lắc đầu."),t("br"),t("br"),n._v("“Tùy tiện đi dạo?” Thanh niên sửng sốt."),t("br"),t("br"),n._v("Đây là nơi nào?"),t("br"),t("br"),n._v("Tiết viên chủ dược viên, coi như vạn quốc liên minh sứ giả, muốn đi vào, đều rất khó làm đến, tùy tiện liền có thể tới đi dạo... Nói đùa cái gì!"),t("br"),t("br"),n._v("Vừa rồi cái tên này vừa tiến đến, hắn có thể liền thấy, thủ vệ thị nữ, tự mình đưa vào, thái độ cung kính... Đây chính là liền gia gia hắn đều không có đãi ngộ!"),t("br"),t("br"),n._v("Chính vì vậy, mới tới kết giao một chút, nếu không, đường đường dưỡng dược thế gia người thừa kế, cũng không phải rảnh rỗi, trông thấy người liền chạy tới."),t("br"),t("br"),n._v("Biết đối phương có thể là không muốn nói, thanh niên cũng thức thời không tiếp tục truy vấn, cười nói: “Tại hạ Lư Trạm, Vạn Quốc thành Lư gia người!”"),t("br"),t("br"),n._v("“Trương Huyền!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Nguyên lai là Trương huynh, hạnh ngộ hạnh ngộ!” Đăng báo ra Lư gia, đối phương mặt không đổi sắc, không nhúc nhích chút nào, Lư Trạm vẻ mặt nghiêm túc, vội vàng ôm quyền."),t("br"),t("br"),n._v("Làm dưỡng dược thế gia, Vạn Quốc thành một trong tứ đại gia tộc, Lư gia quyền thế địa vị, tại toàn bộ liên minh đều là nổi danh."),t("br"),t("br"),n._v("Coi như bình thường vương công đại thần, nghe được hắn cái thân phận này, đều muốn cung kính xưng hô một tiếng Lô công tử, cái tên này nhưng mặt không đổi sắc, có thể thấy được lai lịch không đơn giản."),t("br"),t("br"),n._v("Muốn đến nơi này, thái độ càng thêm cung kính."),t("br"),t("br"),n._v("Bất quá, nếu là cho hắn biết, trước mắt vị này, liền Lư gia nghe đều chưa từng nghe qua, không thông báo sẽ không tại chỗ thổ huyết hôn mê."),t("br"),t("br"),n._v("Hai người lại hàn huyên một hồi, Lư Trạm phát hiện trước mắt vị này đối dưỡng dược quả thực một chữ cũng không biết, càng ngày càng mê hoặc."),t("br"),t("br"),n._v("Những người khác còn đang suy đoán Tiết viên chủ mời mọi người tới là vì cái gì, nhưng xem như dưỡng dược thế gia truyền nhân, đã sớm biết tin tức xác thật."),t("br"),t("br"),n._v("Dươc viên này thế nhưng là có không ít dưỡng dược thị nữ đều ra đương nhiên gia tộc bọn họ, nghe ngóng một chút tin tức, vẫn là rất đơn giản."),t("br"),t("br"),n._v("Cùng mọi người đoán đồng dạng, đang là Địa Mạch Linh Đằng xuất hiện vấn đề!"),t("br"),t("br"),n._v("Bởi vậy, hôm nay có thể nhận được mời, không có chỗ nào mà không phải là đối dưỡng dược cực tinh người, cái tên này thế mà cái gì cũng không biết, thậm chí liền dưỡng dược người đều chưa từng nghe qua... Đến cùng thân phận gì, có thể để cho Tiết viên chủ mời đến?"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bên này nói chuyện phiếm, La Thanh Yên cùng Tống Siêu, cũng đi đến."),t("br"),t("br"),n._v("“Tống sư, cái kia chính là mới vừa mới đối ta tên động thủ!” Nhìn quanh một vòng, rất mau tìm đến cái kia để nàng cắn răng nghiến lợi gia hỏa, La Thanh Yên một chỉ."),t("br"),t("br"),n._v("Theo nhìn lại, Tống Siêu lập tức thấy được một cái bóng lưng."),t("br"),t("br"),n._v("Trương Huyền cùng Lư Trạm nói chuyện phiếm, kém đối với nơi này, Tống Siêu chỉ thấy một cái phía sau lưng, mặc dù có chút nhìn quen mắt, lại không hướng về thân thể hắn muốn."),t("br"),t("br"),n._v("Cũng khó trách, vị kia Trương sư cùng Hồng Hải thành Trại các chủ quen thuộc như thế, hắn thấy, hẳn là Hồng Hải thành người, làm sao có thể chạy đến nơi đây, lại trùng hợp như vậy cũng tới đến dược viên?"),t("br"),t("br"),n._v("“Không sai, chính là hắn, dựa dẫm danh sư thân phận, đối ta xuống tay ác độc! Mong rằng Tống sư là ta làm chủ, chỉ muốn giáo huấn hắn một trận, Thanh Yên... Hết thảy đều tùy ngươi!”"),t("br"),t("br"),n._v("La Thanh Yên tròn vo to lớn bộ ngực, cọ xát tới."),t("br"),t("br"),n._v("Nàng biết nam nhân thích gì, nhờ vào vốn liếng, cũng bắt được không ít trợ giúp, nếu không, nhờ vào nàng như vậy một cái niên kỷ không lớn nữ tử, làm sao có thể tại Vạn Quốc thành đặt chân, còn mở một ngày thu đấu vàng dược viên?"),t("br"),t("br"),n._v("“Yên tâm đi, loại này bại hoại Danh Sư đường uy nghiêm, coi như ngươi không nói, ta gặp được cũng sẽ giáo huấn!”"),t("br"),t("br"),n._v("Cảm nhận được cánh tay truyền đến kinh người co dãn, Tống Siêu gật đầu đáp ứng, nghĩa chính ngôn từ."),t("br"),t("br"),n._v("Mặc dù biết đối phương là muốn lợi dụng hắn báo thù, nhưng giữ gìn Danh Sư đường uy nghiêm là mỗi cái danh sư chuyện phải làm, huống chi mỹ nhân còn thái độ như thế!"),t("br"),t("br"),n._v("Đi theo xinh đẹp thân ảnh đằng sau, rất mau tới đến Trương Huyền đám người trước mặt."),t("br"),t("br"),n._v("“Chúng ta lại gặp mặt!”"),t("br"),t("br"),n._v("Biết vị này tứ tinh cấp bậc Tống sư, khẳng định sẽ vì chính mình trút giận, La Thanh Yên không cố kỵ nữa, đi tới trước mặt, cười lạnh một tiếng."),t("br"),t("br"),n._v("Trương Huyền quay đầu, sửng sốt một chút, con mắt chớp chớp, hơi nghi hoặc một chút: “Ngươi không phải là không có thư mời ư? Thế nào cũng tiến vào!”"),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Không nghe lời này còn tốt, nghe xong La Thanh Yên kém chút không tức giận thổ huyết."),t("br"),t("br"),n._v("Còn không biết xấu hổ nói ta không có thư mời..."),t("br"),t("br"),n._v("Bị ngươi cướp đi có được hay không?"),t("br"),t("br"),n._v("“Ta đi theo Tống sư mà đến!”"),t("br"),t("br"),n._v("Cố nén lửa giận,"),t("br"),t("br"),n._v("La Thanh Yên quay đầu nhìn về phía bên cạnh thanh niên: “Chính là vị này Trương sư, không để ý danh sư tôn nghiêm, để thú sủng ra tay với ta, còn xin Tống sư vì ta chủ trì công đạo!”"),t("br"),t("br"),n._v("Nói xong, gặp thanh niên trước mặt không có động tĩnh, đang muốn tiếp tục châm ngòi thổi gió, liền nghe đến vừa rồi vị kia Trương sư tiếng âm vang lên: “Tống sư? A, nguyên lai là Tiểu Tống ah!”"),t("br"),t("br"),n._v("  “Tiểu Tống?”"),t("br"),t("br"),n._v("La Thanh Yên sững sờ, lập tức cuồng hỉ."),t("br"),t("br"),n._v("Trước mắt vị này chính là tứ tinh danh sư siêu cấp thiên tài, càng là tông môn tới cường giả!"),t("br"),t("br"),n._v("Coi như Vạn Quốc thành Danh Sư đường trưởng lão nhìn thấy, chỉ sợ đều muốn ngang hàng giao nhau, ngươi một người hai mươi tuổi cũng chưa tới gia hỏa, trực tiếp xưng hô Tiểu Tống..."),t("br"),t("br"),n._v("Vốn còn nghĩ như thế nào thêm mắm thêm muối, hiện tại xem ra không cần, thật sự là tự gây nghiệt thì không thể sống!"),t("br"),t("br"),n._v("Không riêng gì nàng, đang đang suy đoán Trương Huyền thân phận Lư Trạm cũng là run lên, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Vị này Tống sư, ngực bốn vì sao lập loè chói mắt, vừa nhìn liền biết là tứ tinh danh sư, trực tiếp xưng hô nhân gia Tiểu Tống, giống như là gọi một cái vãn bối, một tên mao đầu tiểu tử..."),t("br"),t("br"),n._v("Vội vàng hướng Tống sư nhìn lại, hi vọng hắn sẽ không trở mặt tại chỗ, chỉ thấy vị này mang theo bốn vì sao siêu cấp thiên tài, vẻ mặt vặn vẹo, cả người nhanh muốn khóc."),t("br"),t("br"),n._v("“Mạt học Tống Siêu gặp qua Trương sư!”"),t("br"),t("br"),n._v("Hắn lúc này nội tâm cũng là sụp đổ."),t("br"),t("br"),n._v("Đổi lại cái khác tứ tinh danh sư, khả năng cũng sẽ chất vấn, có thể đối mặt cái tên này... Còn thật không dám."),t("br"),t("br"),n._v("Liên tục bị sư ngôn thiên bẩm mê hoặc hai lần, tất cả gia sản đều bị lừa dối cạn... Sớm đã lĩnh giáo thủ đoạn của đối phương, một nhìn người nọ, không trực tiếp quay đầu liền chạy, cũng đã rất nể tình."),t("br"),t("br"),n._v("Còn giáo huấn... Cho hắn mười cái lá gan cũng không dám ah!"),t("br"),t("br"),n._v("“Tống sư, hắn đây là đối ngươi vô lễ, ngươi... Dát?”"),t("br"),t("br"),n._v("Đang tại ý dâm kẻ trước mắt này bị Tống sư cuồng đánh một trận, dự định giới thiệu mấy loại đánh người phương pháp, liền nghe nói như thế, La Thanh Yên một chút cứ thế tại nguyên chỗ."),t("br"),t("br"),n._v("“Mạt học” có ý tứ gì?"),t("br"),n._v("Học thức nông cạn!"),t("br"),t("br"),n._v("Bình thường đều là vãn bối nhìn thấy cường đại hơn mình người, mới có xưng hô, vị này siêu cấp thiên tài, đường đường tứ tinh danh sư, tới cái danh xưng như thế này?"),t("br"),t("br"),n._v("Chẳng lẽ... Vị này Trương sư, thoạt nhìn tuổi không lớn lắm, nhưng so vị này Tống sư còn muốn lợi hại hơn?"),t("br"),t("br"),n._v("Có lầm hay không?"),t("br"),t("br"),n._v("La Thanh Yên nhanh muốn khóc."),t("br"),t("br"),n._v("Vốn cho rằng dính vào Tống sư vị đại nhân vật này, khẳng định có thể giáo huấn cái tên này, diễu võ giương oai, rửa sạch nhục nhã, ai ngờ... Nhân gia lai lịch càng lớn!"),t("br"),t("br"),n._v("Đánh mặt không được bị đánh..."),t("br"),t("br"),n._v("Chuyện này là sao!"),t("br"),t("br"),n._v("Lư Trạm cũng miệng há mở, con mắt trừng đến so Kim Ngư còn muốn lớn."),t("br"),t("br"),n._v("Một mực suy đoán vị này là thân phận gì, thậm chí liên minh chủ phủ đều đoán qua, có thể làm giấc mơ đều không nghĩ tới, là một vị danh sư... Hơn nữa còn là một vị để tứ tinh danh sư, cam tâm tình nguyện tự xưng “Mạt học” danh sư!"),t("br"),t("br"),n._v("Quá kinh khủng đi!"),t("br"),t("br"),n._v("Khó trách có thể để cho thị nữ đưa vào, loại người này quả thực có loại này vốn liếng!"),t("br"),t("br"),n._v("“Tiểu Tống, ngươi muốn giúp nàng chủ trì công đạo?” Không để ý tới hai người khiếp sợ, Trương Huyền nhìn qua."),t("br"),t("br"),n._v("Nhìn thấy cái tên này, hắn cũng là hơi nghi hoặc một chút, chẳng qua suy nghĩ một chút cũng là giật mình."),t("br"),t("br"),n._v("Cho đối phương phương pháp trị liệu bên trong, có chút dược liệu cần tươi mới, chỉ có thể ở dược viên mua sắm, chỉ sợ lần này tới, cũng là vì một loại nào đó dược vật."),t("br"),t("br"),n._v("“Không, không... Ta chỉ là ngẫu nhiên gặp được, cùng nàng không quen...”"),t("br"),t("br"),n._v("Tống Siêu xấu hổ cười một tiếng."),t("br"),t("br"),n._v("Liên tục trúng đối phương hai lần sư ngôn thiên bẩm, biết trước mắt vị này khẳng định là cao minh hơn hắn rất nhiều danh sư, nào còn dám giả vờ giả vịt."),t("br"),t("br"),n._v("“Các ngươi đây là không quen?” Trương Huyền một chỉ."),t("br"),t("br"),n._v("Tống Siêu lúc này mới phát hiện, La Thanh Yên sung mãn mượt mà nhô lên, còn khoác lên cánh tay của hắn bên trên, bị đè ép cải biến hình dạng."),t("br"),t("br"),n._v("“Thật sự không quen...”"),t("br"),t("br"),n._v("Giật nảy mình, Tống Siêu cánh tay chấn động, đem người sau đẩy ra."),t("br"),t("br"),n._v("Vừa rồi để hắn cảm thấy đau xót thoải mái đầy đặn, giờ phút này cảm giác cây kim đồng dạng, đâm nhanh muốn khóc."),t("br"),t("br"),n._v("“Tống sư...”"),t("br"),t("br"),n._v("Bị đẩy một cái lảo đảo, La Thanh Yên thế mới biết, mới vừa rồi còn vênh váo ngút trời Tống sư, đối trước mắt vị này Trương sư, không riêng gì tôn kính, quả thực chính là sợ hãi!"),t("br"),t("br"),n._v("Có thể để cho một vị tứ tinh danh sư cảm thấy sợ hãi?"),t("br"),t("br"),n._v("Đến cùng thân phận gì?"),t("br"),t("br"),n._v("Dọa đến cổ co rụt lại, lặng lẽ lui lại, nếu như nói mới vừa rồi còn nghĩ đến làm sao báo cừu, hiện tại đánh chết cũng không dám có ý nghĩ này."),t("br"),t("br"),n._v("Nàng thế mà nhàm chán tìm loại người này phiền phức, không có bị tại chỗ đánh chết, cũng đã khoan hồng độ lượng."),t("br"),t("br"),n._v("“Không quen liền không quen, không cần thiết như vậy!”"),t("br"),t("br"),n._v("Không để ý tới chuẩn bị chạy trốn La Thanh Yên, Trương Huyền khoát tay áo, đột nhiên nhớ tới một việc, ánh mắt sáng lên, nhìn về phía trước mắt Tống Siêu: “A, đúng rồi, Vô Hồn Kim Nhân còn muốn hay không?”"),t("br"),t("br"),n._v("Trước đó tại Vu hồn mộ, thế nhưng là để Tôn Cường thu bốn chiếc tinh kim thất phẩm quan tài, bên trong một cái là trống không, còn lại ba cái đều chứa đựng lấy Vô Hồn Kim Nhân, dù sao thứ này đối với hắn mà nói cũng không có gì dùng, đã lại gặp được cái này oan đại đầu, xem có thể hay không bán ra."),t("br"),t("br"),n._v("“Vô Hồn Kim Nhân... Trương sư còn có?”"),t("br"),t("br"),n._v("Tống Siêu sững sờ."),t("br"),t("br"),n._v("“Còn có ba đầu, bên ngoài còn có tinh kim thất phẩm quan tài chứa đựng, nếu như ngươi muốn mua lời nói, cộng thêm quan tài, cho ngươi tính toán hơi rẻ, năm cái linh thạch trung phẩm một cái!” Trương Huyền nói."),t("br"),t("br"),n._v("Trước đó chỉ riêng một đầu thi thể, nhưng là bán bốn vạn hạ phẩm linh thạch, cộng thêm quan tài mới bán năm cái trung phẩm, quả thực xem như giảm giá."),t("br"),t("br"),n._v("“Năm cái?” Tống Siêu miệng co lại: “Ta không nhiều như vậy linh thạch trung phẩm...”"),t("br"),t("br"),n._v("Hắn toàn bộ tài sản, mua lấy một đầu Vô Hồn Kim Nhân, đều bị rút khô, mặc dù trở lại Vạn Quốc thành, nhìn thấy tông môn trưởng lão, đền bù tổn thất, có thể cũng đều là chút hạ phẩm linh thạch, linh thạch trung phẩm... Tìm khắp toàn thân, cũng chỉ có một cái mà thôi!"),t("br"),t("br"),n._v("Vẫn là trưởng lão bởi vì việc này ban cho ban thưởng!"),t("br"),t("br"),n._v("“Không nhiều như vậy, có bao nhiêu?” Trương Huyền nhìn qua."),t("br"),t("br"),n._v("“Chỉ có một cái...” Tống Siêu thành thật trả lời."),t("br"),t("br"),n._v("“Một cái cũng được, ta liền bán cho ngươi miệng tinh kim thất phẩm quan tài, thứ này thế nhưng là tứ tinh luyện khí sư mới có thể luyện chế đồ vật, ngủ đi vào đông ấm hè mát, vô cùng thoải mái! Trọng yếu nhất chính là, cùng trước đó bán đưa cho ngươi đầu kia Vô Hồn Kim Nhân là một bộ, lấy về nghiên cứu, nhất định có thể tại Vu hồn một đạo bên trên, có đột phá!”"),t("br"),t("br"),n._v("Trương Huyền cổ tay rung lên, đưa qua một cái trữ vật giới chỉ."),t("br"),t("br"),n._v("Thứ này đối hắn hiện tại tới nói, đã không đáng giá, trên người chứa mấy viên, mà cái này viên bên trong, trang phục chính thức lấy trước đó Lạc Trúc đánh cắp Vô Hồn Kim Nhân chiếc kia không quan tài."),t("br"),t("br"),n._v("Vu hồn sư thủ đoạn, đã thông qua Mặc Hồn Sinh lý giải rất nhiều, thứ này giữ lại tác dụng không lớn, có thể bán đi, tự nhiên tốt nhất."),t("br"),t("br"),n._v("“Tinh kim thất phẩm quan tài?” Tiếp nhận nhẫn, Tống Siêu nhận chủ, tinh thần quét vào đi, nhìn thoáng qua, ánh mắt sáng lên."),t("br"),t("br"),n._v("Phía trên xác thực điêu khắc không ít vu hồn sư mới có đặc thù ký hiệu, nếu quả thật cùng trước đó đầu kia Vô Hồn Kim Nhân liên hợp lại nghiên cứu lời nói, quả thực có thể đối Vu hồn hiểu rõ càng sâu."),t("br"),t("br"),n._v("“Tốt, ta mua, đây là linh thạch!”"),t("br"),t("br"),n._v("Nói xong lật bàn tay một cái, một cái hộp ngọc đưa tới."),t("br"),t("br"),n._v("“Ừm!” Tiếp nhận hộp ngọc, mở ra một đường nhỏ, lập tức cảm thấy linh khí nồng nặc đập vào mặt, biết bên trong chính là một quả linh thạch trung phẩm, Trương Huyền lúc này mới hài lòng gật đầu, đem nó thu vào nhẫn."),t("br"),t("br"),n._v("“Ta chỗ này còn có ba đầu đồng dạng Vô Hồn Kim Nhân, cũng có cao cấp hơn xích kim quan tài, nếu như còn muốn mua lời nói, mang đủ linh thạch trung phẩm tới tìm ta là được, tùy thời hoan nghênh!”"),t("br"),t("br"),n._v("Lấy được một cái linh thạch trung phẩm, Trương Huyền hài lòng nhìn về phía trước mắt Tống Siêu, giống như là nhìn xem một đống có thể di động túi tiền."),t("br"),t("br"),n._v("Cái tên này quả thực có tiền, liên tục hai lần, đều lợi nhuận không ít."),t("br"),t("br"),n._v("Không hổ là tông môn tử đệ, người ngốc nhiều tiền."),t("br"),t("br"),n._v("“Vâng! Ta quay đầu nhất định bẩm báo tông môn, nếu như còn có cần, sẽ tới tìm ngươi!”"),t("br"),t("br"),n._v("Gật gật đầu, Tống Siêu xoay người sang chỗ khác, mới vừa đi vài bước, đột nhiên một chút cứng ngắc tại chỗ, vẻ mặt tối đen, lệ rơi đầy mặt."),t("br"),t("br"),n._v("“Con mẹ nó chứ... Lại bị mê hoặc...”"),t("br"),t("br"),n._v("Không nghĩ tới một mực cẩn thận, kết quả lại trúng chiêu..."),t("br"),t("br"),n._v("Khó lòng phòng bị ah!"),t("br"),t("br"),n._v("Convert by: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-528"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-530"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);