(window.webpackJsonp=window.webpackJsonp||[]).push([[1776],{2131:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-44-ta-chon-mot"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-44-ta-chon-mot"}},[n._v("#")]),n._v(" Chương 44: Ta chọn một")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v(" “Dát?” "),i("br"),i("br"),n._v(" “Tiểu tử này lại nói như vậy Mặc Dương đại sư?” "),i("br"),i("br"),n._v(" “Đại sư hảo tâm hảo ý giúp hắn lựa chọn bảo bối, hắn lại còn nói đại sư lừa người?” "),i("br"),i("br"),n._v(" “Không biết trời cao đất rộng gia hỏa!” "),i("br"),i("br"),n._v(" Nghe được Trương Huyền, mọi người xung quanh tất cả đều sững sờ ở tại chỗ, tiếp theo ồ lên."),i("br"),i("br"),n._v(" Tiểu tử này đầu óc không bệnh chứ?"),i("br"),i("br"),n._v(" Đại sư khổ cực như vậy giúp hắn lựa chọn bảo bối, hắn lại còn nói đối phương... Lừa người, tinh tướng?"),i("br"),i("br"),n._v(" “Ngươi nói cái gì?” Hiển nhiên cũng không nghĩ tới thiếu niên sẽ như vậy nói, “Mặc Dương đại sư” sắc mặt một hồi chìm xuống: “Tiểu hữu có phải là đối với Mặc mỗ người có hiểu lầm gì đó?” "),i("br"),i("br"),n._v(" “Hiểu lầm? Ta nói cái gì trong lòng ngươi không rõ ràng sao? Ngươi muốn lừa người, yêu làm sao lừa gạt liền làm sao lừa gạt, chuyện không liên quan đến ta, cũng lười đi quản! Ta tuyển bảo bối của ta, ngươi lừa gạt ngươi người, mọi người nước giếng không phạm nước sông, chớ ở trước mặt ta giả ra bộ kia cao cao tại thượng sắc mặt!” "),i("br"),i("br"),n._v(" Trương Huyền có chút phiền chán xua tay."),i("br"),i("br"),n._v(" Đối với tên lừa đảo hắn luôn luôn không có cảm tình gì, đối phương không chọc giận hắn, thì cũng thôi đi, lại tinh tướng chứa vào trên đầu mình, thực sự là muốn chết!"),i("br"),i("br"),n._v(" “Làm càn!” “Mặc Dương đại sư” sắc mặt tái xanh, vung tay áo một cái, toàn thân mang theo hạo nhiên chính khí: “Ta Mặc mỗ người ba mươi năm trước thu được Giám bảo sư tư cách, giúp người giám bảo, thay người giải quyết khó khăn, luôn luôn không thẹn với lòng! Còn chưa bao giờ có người nói ta lừa người, chư vị ở đây có thể làm cho ta chứng, ta chưa từng lừa dối quá các ngươi?” "),i("br"),i("br"),n._v(" “Mặc Dương đại sư nhân tâm nhân đức, cứu người nguy ách, làm sao có khả năng lừa người!” "),i("br"),i("br"),n._v(" “Ăn nói bừa bãi tiểu bối, ta nhìn ngươi là muốn chết!” "),i("br"),i("br"),n._v(" “Đại sư không nên tức giận, ta thay ngươi dạy cái tên này một trận, cho hắn biết cơm có thể ăn bậy, lời không thể nói lung tung...” "),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v(" “Mặc Dương đại sư” một câu nói đưa tới tức giận của mọi người, từng cái từng cái nhìn về phía Trương Huyền, như là gặp được cừu nhân giết cha."),i("br"),i("br"),n._v(" “Ba mươi năm trước thu được Giám bảo sư tư cách? Không thẹn với lòng?” "),i("br"),i("br"),n._v(" Trương Huyền không nói gì."),i("br"),i("br"),n._v(" Ngươi một cái nho nhỏ giám bảo học đồ, giả trang cái gì sói đuôi to?"),i("br"),i("br"),n._v(" Da mặt thật so với tưởng tượng còn dày hơn."),i("br"),i("br"),n._v(" Chẳng trách có thể trở thành là Lưu Chu vương quốc truy nã tên lừa đảo, da mặt dày không nói, ứng biến cũng đủ nhanh chóng."),i("br"),i("br"),n._v(" Những khác tên lừa đảo bị đương chúng vạch trần nhất định sẽ rối loạn trận tuyến, cái tên này ngược lại tốt, câu nói đầu tiên đem đầu mâu chỉ hướng tự mình, làm tự mình ngược lại cưỡi hổ khó xuống."),i("br"),i("br"),n._v(" Nếu như không có Thiên Đạo Thư Viện, nhìn thấy hắn đại nghĩa như vậy lăng nhiên, khẳng định cũng sẽ hoài nghi có phải là sai lầm, nhưng thư viện nơi tay, nếu mặt trên nói hắn là tên lừa đảo, vậy thì khẳng định là!"),i("br"),i("br"),n._v(" “Mọi người đừng ầm ĩ!” Thấy triệt để dẫn động mọi người sự phẫn nộ, nắm trong tay quyền chủ động, “Mặc Dương đại sư” bàn tay lớn vẫy một cái: “Ta làm người hào phóng, nếu như ngươi có thể lập tức liền lời nói mới rồi xin lỗi, ta có thể chuyện cũ sẽ bỏ qua, bằng không, ngươi tùy ý sỉ nhục ta, sẽ chịu đến dạng gì trừng phạt, trong lòng nên rõ ràng!” "),i("br"),i("br"),n._v(" “Trừng phạt?” Trương Huyền lắc đầu: “Ngươi có tên lừa đảo, ta có không có nói quàng, muốn nghiệm chứng cũng rất đơn giản, chỉ cần đem vừa nãy bọn họ mua đồ vật đánh bóng đi ra, ngay lập tức sẽ biết kết quả! Trong này nói nhảm nhiều như vậy có ích lợi gì?” "),i("br"),i("br"),n._v(" “Đánh bóng đi ra?” “Mặc Dương đại sư” tựa hồ sớm biết hắn lại như vậy nói, lạnh nhạt nói: “Ta trước đã nói, đêm nay liền muốn rời khỏi Thiên Huyền Thành, ta giúp bọn họ lựa chọn đều là lớn kiện, ít nhất cũng cần thời gian nửa ngày đánh bóng mới có thể triển lộ hình dáng, ta có việc gấp, làm lỡ không được thời gian dài như vậy! Làm sao, ngươi có muốn nói, ta không thời gian ở lại chỗ này, là bởi vì tâm hư, muốn rời khỏi, hảo mượn cơ hội vu ta thuần khiết?” "),i("br"),i("br"),n._v(" Nói đến đây, ông lão chấn động ống tay áo, lộ ra ngạo nghễ khí tức: “Ta Mặc Dương đường đường chính chính, thì sợ gì vu hại! Chỉ là, ta hảo tâm giúp ngươi lựa chọn bảo vật, lại nói ta là tên lừa đảo, không có chứng cứ, chính là phỉ báng, ngươi cũng đã biết phỉ báng cho rằng giám bảo đại sư hậu quả?” "),i("br"),i("br"),n._v(" Nghe nói như thế, Trương Huyền giờ mới hiểu được, vẫn là xem thường đối phương vô liêm sỉ trình độ."),i("br"),i("br"),n._v(" Không hổ là nghề nghiệp tên lừa đảo, liền lùi lại đường đều muốn tốt."),i("br"),i("br"),n._v(" Trước liền nói đêm nay muốn rời khỏi Thiên Huyền Vương Thành, làm tốt phục bút, sau đó tự mình còn chưa mở miệng, liền phản đánh một bừa cào, hiện tại mình coi như nói hắn không dám chờ đợi bảo vật đánh bóng, khẳng định cũng không ai tin tưởng!"),i("br"),i("br"),n._v(" Lại nói... Nói hắn là tên lừa đảo, vẫn đúng là không có chứng cứ!"),i("br"),i("br"),n._v(" Thiên Đạo Thư Viện bí mật là không thể nào công khai, hơn nữa coi như công khai cũng phải có nhân tin tưởng mới được a!"),i("br"),i("br"),n._v(" “Tiểu tử, lập tức cho đại sư quỳ xuống xin lỗi!” "),i("br"),i("br"),n._v(" “Dám nói đại sư là tên lừa đảo, có tin hay không Lão Tử vài phút giết chết ngươi?” "),i("br"),i("br"),n._v(" “Từ nơi nào bốc lên như vậy không biết trời cao đất rộng cẩu vật, vu hại đại sư thuần khiết?” "),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v(" Thấy “Đại sư” nói quang minh lẫm liệt, không ít người lập tức lựa chọn tin tưởng, đối với Trương Huyền lớn tiếng quát lớn."),i("br"),i("br"),n._v(" Những người này đều phẫn nộ."),i("br"),i("br"),n._v(" Bị lừa người, cơ hồ đều lâm vào tự mình thôi miên bên trong, từ sâu trong nội tâm không nguyện ý thừa nhận mình bị lừa gạt, ai đánh quấy nhiễu giấc mộng đẹp của bọn họ, liền sẽ gặp phải mãnh liệt phản đối cùng quát lớn!"),i("br"),i("br"),n._v(" Dưới cái nhìn của bọn họ, “Mặc Dương đại sư” là cao bao nhiêu khiết người, có thể là tên lừa đảo sao?"),i("br"),i("br"),n._v(" Đùa gì thế!"),i("br"),i("br"),n._v(" ngantruyen.com "),i("br"),n._v(" “Nếu cái tên này nói đại sư là tên lừa đảo, tự mình khẳng định cũng tinh thông giám bảo thôi? Có bản lĩnh cũng chọn một cho chúng ta nhìn, nếu như lựa chọn là phế phẩm, liền đừng ở chỗ này giả vờ giả vịt, cút ngay đi ra ngoài!” "),i("br"),i("br"),n._v(" Trong đám người một cái bất âm bất dương âm thanh hừ nói."),i("br"),i("br"),n._v(" “Không sai, ngươi nếu nói ta là tên lừa đảo, không nguyện ý tiếp thu ta cho ngươi đề cử bảo bối, nói vậy cũng đối giám bảo có nhất định nghiên cứu!” "),i("br"),n._v(" Nghe nói như thế, “Mặc Dương đại sư” gật gật đầu, nhìn sang: “Như vậy, ta cho ngươi một cơ hội! Chúng ta đồng thời tuyển một kiện đơn giản bảo bối, có thể rất mau đánh mài đi ra bất cứ lúc nào giám định! Chỉ cần ngươi lựa chọn đồ vật có thể tăng gia trị, kiếm tiền, ta liền thừa nhận ngươi có chút ánh mắt, có thể không tính đến vu hại tội danh của ta! Mà nếu như chọn cái phế phẩm, thật không tiện, mua lại ta vừa nãy cho ngươi chọn bảo bối, sau đó quỳ xuống xin lỗi!” "),i("br"),i("br"),n._v(" “Ngươi muốn cùng ta so với giám bảo?” Trương Huyền đang rầu làm sao lấy ra chứng cứ, nghe nói như thế, hơi kinh ngạc, kém chút không bật cười."),i("br"),i("br"),n._v(" Cái tên này đầu óc không bệnh đi!"),i("br"),i("br"),n._v(" Tự mình nắm giữ Thiên Đạo Thư Viện, giám bảo muốn nói thiên hạ đệ nhị, tuyệt không người dám nói thiên hạ đệ nhất, cùng tự mình so với... Ngươi xác định đầu óc không có bị lừa đá?"),i("br"),i("br"),n._v(" Bất quá, suy nghĩ kỹ một chút, cũng là rõ ràng đối phương tại sao muốn làm như vậy rồi."),i("br"),i("br"),n._v(" Ở đối phương xem ra, tự mình mười tám, mười chín tuổi thiếu niên, biết cái gì giám bảo? Mà hắn mấy ngày trước nếu có thể giúp người khác kiếm tiền, khẳng định đã sớm làm xong thủ đoạn, đồng thời giám bảo, tự chọn không ra tốt, hắn có thể nhân cơ hội lớn mạnh thanh uy, làm không cẩn thận còn có thể nhân cơ hội lại hấp dẫn mấy cái kẻ ngu si, điên cuồng ném tiền."),i("br"),i("br"),n._v(" “Làm sao? Không dám đáp ứng?” "),i("br"),i("br"),n._v(" “Mặc Dương đại sư” một bộ ăn chắc dáng dấp của ngươi."),i("br"),i("br"),n._v(" “Hảo!” Nếu đối phương muốn tìm kích thích, Trương Huyền làm sao có khả năng không đáp ứng."),i("br"),i("br"),n._v(" “Vậy thì tốt, ta trước tiên lựa chọn!” “Mặc Dương đại sư” vung một cái ống tay áo, nhanh chân dọc theo trường đài cất bước, rất nhanh, ngừng lại, cầm lấy một cái to bằng bàn tay bảo vật."),i("br"),i("br"),n._v(" Vật này bị dày đặc nham thạch bao vây, liền hình thái cũng không thấy, chớ nói chi là đến cùng trân không trân quý."),i("br"),i("br"),n._v(" “Chủ sạp, cái này bao nhiêu tiền?” "),i("br"),i("br"),n._v(" Cầm lấy vật này, “Mặc Dương đại sư” mở miệng."),i("br"),i("br"),n._v(" “Này từng cái đầu nhỏ, cũng không có cái gì sóng linh khí, hai trăm kim tệ!” Chủ sạp đi tới."),i("br"),i("br"),n._v(" “Tốt, ngươi bây giờ tìm người đánh bóng đi!” "),i("br"),i("br"),n._v(" Đưa qua tiền, “Mặc Dương đại sư” lạnh nhạt nói."),i("br"),i("br"),n._v(" “Rõ!” "),i("br"),i("br"),n._v(" Chủ sạp tiếp nhận bảo vật, đưa đến hậu điện, một lúc nữa, trong tay nâng một cái màu vàng cóc đi ra, đầy mặt hưng phấn: “Chúc mừng đại sư, là Linh Tê Kim Thiềm lột xác!” "),i("br"),i("br"),n._v(" “Linh Tê Kim Thiềm là cái gì?” "),i("br"),i("br"),n._v(" “Linh Tê Kim Thiềm là một loại lục phẩm Man Thú, toàn thân nó thứ trọng yếu nhất chính là biểu bì, một đời chỉ có thể thuế một lần, vật này có thể làm thuốc, giá trị cực cao!” "),i("br"),i("br"),n._v(" “Không hổ là Mặc Dương đại sư, nhãn lực thật tốt, một hồi liền chọn trúng bảo bối!” "),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v(" Nghe được là Linh Tê Kim Thiềm lột xác, tất cả mọi người ồ lên."),i("br"),i("br"),n._v(" “Rất nhiều người không biết vật này, ngươi liền nói thẳng ra giá cả đi! Như vậy càng trực quan một ít!” “Mặc Dương đại sư” ngước đầu sọ, trên thân mỗi giờ mỗi khắc không toả ra mặc bức khí chất, lạnh nhạt nói."),i("br"),i("br"),n._v(" “Vật này giá trị... 2000 kim tệ!” Chủ sạp cất cao giọng nói."),i("br"),i("br"),n._v(" “2000 kim tệ?” "),i("br"),i("br"),n._v(" “Hai trăm kim tệ mua, vừa ra tay liền 2000? Tăng gấp mười lần?” "),i("br"),i("br"),n._v(" “Quá lợi hại!” "),i("br"),i("br"),n._v(" “Đại sư chính là đại sư, một cái tiểu vật phẩm đều có thể kiếm lời nhiều như vậy, mới vừa rồi giúp ta chọn lớn như vậy, chẳng phải kiếm lời lật ra?” "),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v(" Thấy cảnh này, mới vừa rồi còn có chút bận tâm mọi người, ánh mắt lần thứ hai cuồng nhiệt."),i("br"),i("br"),n._v(" Đại sư vẫn như cũ là đại sư, vừa ra tay quả nhiên không giống bình thường!"),i("br"),i("br"),n._v(" “Tốt, tới lượt ngươi!” "),i("br"),i("br"),n._v(" “Mặc Dương đại sư” hơi vung tay chưởng, lạnh lùng nhìn sang."),i("br"),i("br"),n._v(" “Ta...” Trương Huyền gật đầu."),i("br"),i("br"),n._v(" Biết đối phương là tên lừa đảo, khẳng định có hậu chiêu, chỉ là không nghĩ đến cái này hậu chiêu lợi hại như vậy!"),i("br"),i("br"),n._v(" Xem ra nhất định phải chọn một không sai biệt lắm mới được."),i("br"),i("br"),n._v(" “Ta có thể chọn trước bảo vật, chờ ngươi đánh bóng xong, xác định rõ giá cả lại cho tiền sao?” "),i("br"),i("br"),n._v(" Đi về phía trước một bước, Trương Huyền đột nhiên nhớ tới một chuyện, nhìn về phía chủ sạp."),i("br"),i("br"),n._v(" “Không được! Trước tiên trả thù lao mua đồ, chúng ta mới có thể cho ngươi đánh bóng! Đây là quy củ, không phải vậy, đánh bóng đi ra không đáng giá, ngươi từ bỏ, hoặc là quá đáng giá, chúng ta không muốn bán, đều không thể giải thích!” "),i("br"),i("br"),n._v(" Chủ sạp lạnh lùng nói."),i("br"),i("br"),n._v(" Đánh cược bảo, vì phòng ngừa cãi cọ, chỉ cần chọn lựa đồ vật, liền muốn trả tiền, như vậy bằng ký kết khế ước, ai cũng không có cách nào quỵt nợ."),i("br"),i("br"),n._v(" “Vậy cũng tốt...” "),i("br"),i("br"),n._v(" Biết quy củ này, Trương Huyền không thể làm gì khác hơn là gật gù, ngẩng đầu nhìn qua, một mặt đại khí: “Đến, chỉ cho ta một hồi, này một đống bảo vật, cái nào là tám viên kim tệ trở xuống! Ta tới chọn một cái...” "),i("br"),i("br"),n._v("Convert by: Hoang Chau"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-52"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-54"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);